  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'n.', 'A structure, usually of wood, stone, brick, or iron,\n   erected over a river or other water course, or over a chasm, railroad,\n   etc., to make a passageway from one bank to the other.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'n.', 'Anything supported at the ends, which serves to keep some\n   other thing from resting upon the object spanned, as in engraving,\n   watchmaking, etc., or which forms a platform or staging over which\n   something passes or is conveyed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'n.', 'The small arch or bar at right angles to the strings of a\n   violin, guitar, etc., serving of raise them and transmit their\n   vibrations to the body of the instrument.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'n.', 'A device to measure the resistance of a wire or other\n   conductor forming part of an electric circuit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'n.', 'A low wall or vertical partition in the fire chamber of a\n   furnace, for deflecting flame, etc.; -- usually called a bridge wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridged', 'imp. & p. p.', 'of Bridge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridging', 'p. pr. & vb. n.', 'of Bridge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'v. t.', 'To build a bridge or bridges on or over; as, to bridge a\n   river.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'v. t.', 'To open or make a passage, as by a bridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge', 'v. t.', 'To find a way of getting over, as a difficulty; --\n   generally with over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgeboard', 'n.', 'A notched board to which the treads and risers of the\n   steps of wooden stairs are fastened.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgeboard', 'n.', 'A board or plank used as a bridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgehead', 'n.', 'A fortification commanding the extremity of a bridge\n   nearest the enemy, to insure the preservation and usefulness of the\n   bridge, and prevent the enemy from crossing; a tete-de-pont.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgeless', 'a.', 'Having no bridge; not bridged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgepot', 'n.', 'The adjustable socket, or step, of a millstone spindle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgetree', 'n.', 'The beam which supports the spindle socket of the\n   runner in a grinding mill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge-ward', 'n.', 'A bridge keeper; a warden or a guard for a bridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridge-ward', 'n.', 'The principal ward of a key.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgeing', 'n.', 'The system of bracing used between floor or other\n   timbers to distribute the weight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridgey', 'a.', 'Full of bridges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'n.', 'The head gear with which a horse is governed and\n   restrained, consisting of a headstall, a bit, and reins, with other\n   appendages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'n.', 'A restraint; a curb; a check.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'n.', 'The piece in the interior of a gun lock, which holds in\n   place the tumbler, sear, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'n.', 'A span of rope, line, or chain made fast as both ends, so\n   that another rope, line, or chain may be attached to its middle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'n.', 'A mooring hawser.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridled', 'imp. & p. p.', 'of Bridle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridling', 'p. pr. & vb. n.', 'of Bridle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'v. t.', 'To put a bridle upon; to equip with a bridle; as, to\n   bridle a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'v. t.', 'To restrain, guide, or govern, with, or as with, a\n   bridle; to check, curb, or control; as, to bridle the passions; to\n   bridle a muse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle', 'v. i.', 'To hold up the head, and draw in the chin, as an\n   expression of pride, scorn, or resentment; to assume a lofty manner; --\n   usually with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridle iron', '', 'A strong flat bar of iron, so bent as to support, as in\n   a stirrup, one end of a floor timber, etc., where no sufficient bearing\n   can be had; -- called also stirrup and hanger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridler', 'n.', 'One who bridles; one who restrains and governs, as with a\n   bridle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bridoon', 'n.', 'The snaffle and rein of a military bridle, which acts\n   independently of the bit, at the pleasure of the rider. It is used in\n   connection with a curb bit, which has its own rein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'Short in duration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'Concise; terse; succinct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'Rife; common; prevalent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'adv.', 'Briefly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'adv.', 'Soon; quickly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'A short concise writing or letter; a statement in few words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'An epitome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'An abridgment or concise statement of a clients case, made\n   out for the instruction of counsel in a trial at law. This word is\n   applied also to a statement of the heads or points of a law argument.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'a.', 'A writ; a breve. See Breve, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'n.', 'A writ issuing from the chancery, directed to any judge\n   ordinary, commanding and authorizing that judge to call a jury to\n   inquire into the case, and upon their verdict to pronounce sentence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'n.', 'A letter patent, from proper authority, authorizing a\n   collection or charitable contribution of money in churches, for any\n   public or private purpose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brief', 'v. t.', 'To make an abstract or abridgment of; to shorten; as, to\n   brief pleadings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briefless', 'a.', 'Having no brief; without clients; as, a briefless\n   barrister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briefly', 'adv.', 'Concisely; in few words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briefman', 'n.', 'One who makes a brief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briefman', 'n.', 'A copier of a manuscript.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briefness', 'n.', 'The quality of being brief; brevity; conciseness in\n   discourse or writing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brier', 'n.', 'Alt. of Briar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briar', 'n.', 'A plant with a slender woody stem bearing stout prickles;\n   especially, species of Rosa, Rubus, and Smilax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briar', 'n.', 'Fig.: Anything sharp or unpleasant to the feelings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briered', 'a.', 'Set with briers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briery', 'a.', 'Full of briers; thorny.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briery', 'n.', 'A place where briers grow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brig', 'n.', 'A bridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brig', 'n.', 'A two-masted, square-rigged vessel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigade', 'n.', 'A body of troops, whether cavalry, artillery, infantry, or\n   mixed, consisting of two or more regiments, under the command of a\n   brigadier general.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigade', 'n.', 'Any body of persons organized for acting or marching\n   together under authority; as, a fire brigade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigaded', 'imp. & p. p.', 'of Brigade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigading', 'p. pr. & vb. n.', 'of Brigade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigade', 'v. t.', 'To form into a brigade, or into brigades.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigadier general', '', 'An officer in rank next above a colonel, and below\n   a major general. He commands a brigade, and is sometimes called, by a\n   shortening of his title, simple a brigadier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigand', 'n.', 'A light-armed, irregular foot soldier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigand', 'n.', 'A lawless fellow who lives by plunder; one of a band of\n   robbers; especially, one of a gang living in mountain retreats; a\n   highwayman; a freebooter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigandage', 'n.', 'Life and practice of brigands; highway robbery;\n   plunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigandine', 'n.', 'A coast of armor for the body, consisting of scales or\n   plates, sometimes overlapping each other, generally of metal, and sewed\n   to linen or other material. It was worn in the Middle Ages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigandish', 'a.', 'Like a brigand or freebooter; robberlike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigandism', 'n.', 'Brigandage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigantine', 'n.', 'A practical vessel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigantine', 'n.', 'A two-masted, square-rigged vessel, differing from a\n   brig in that she does not carry a square mainsail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigantine', 'n.', 'See Brigandine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigge', 'n.', 'A bridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'v. i.', 'See Brite, v. i.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Radiating or reflecting light; shedding or having much\n   light; shining; luminous; not dark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Transmitting light; clear; transparent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Having qualities that render conspicuous or attractive, or\n   that affect the mind as light does the eye; resplendent with charms;\n   as, bright beauty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Having a clear, quick intellect; intelligent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Sparkling with wit; lively; vivacious; shedding\n   cheerfulness and joy around; cheerful; cheery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Illustrious; glorious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Manifest to the mind, as light is to the eyes; clear;\n   evident; plain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'a.', 'Of brilliant color; of lively hue or appearance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'n.', 'Splendor; brightness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'adv.', 'Brightly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightened', 'imp. & p. p.', 'of Brighten');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightening', 'p. pr. & vb. n.', 'of Brighten');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brighten', 'a.', 'To make bright or brighter; to make to shine; to increase\n   the luster of; to give a brighter hue to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brighten', 'a.', 'To make illustrious, or more distinguished; to add luster\n   or splendor to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brighten', 'a.', 'To improve or relieve by dispelling gloom or removing\n   that which obscures and darkens; to shed light upon; to make cheerful;\n   as, to brighten ones prospects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brighten', 'a.', 'To make acute or witty; to enliven.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brighten', 'v. i.', 'To grow bright, or more bright; to become less dark or\n   gloomy; to clear up; to become bright or cheerful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright-harnessed', 'a.', 'Having glittering armor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightly', 'adv.', 'Brilliantly; splendidly; with luster; as, brightly\n   shining armor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightly', 'adv.', 'With lively intelligence; intelligently.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightness', 'n.', 'The quality or state of being bright; splendor; luster;\n   brilliancy; clearness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightness', 'n.', 'Acuteness (of the faculties); sharpness 9wit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brights disease', '', 'An affection of the kidneys, usually inflammatory\n   in character, and distinguished by the occurrence of albumin and renal\n   casts in the urine. Several varieties of Brights disease are now\n   recognized, differing in the part of the kidney involved, and in the\n   intensity and course of the morbid process.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brightsome', 'a.', 'Bright; clear; luminous; brilliant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigose', 'n.', 'Contentious; quarrelsome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigue', 'n.', 'A cabal, intrigue, faction, contention, strife, or quarrel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brigue', 'n.', 'To contend for; to canvass; to solicit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brike', 'n.', 'A breach; ruin; downfall; peril.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brill', 'n.', 'A fish allied to the turbot (Rhombus levis); much esteemed\n   in England for food; -- called also bret, pearl, prill. See Bret.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brillante', 'a.', 'In a gay, showy, and sparkling style.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brillance', 'n.', 'Brilliancy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brillancy', 'n.', 'The quality of being brilliant; splendor; glitter; great\n   brightness, whether in a literal or figurative sense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliant', 'p. pr.', 'Sparkling with luster; glittering; very bright; as,\n   a brilliant star.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliant', 'p. pr.', 'Distinguished by qualities which excite admiration;\n   splendid; shining; as, brilliant talents.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliant', 'a.', 'A diamond or other gem of the finest cut, formed into\n   faces and facets, so as to reflect and refract the light, by which it\n   is rendered more brilliant. It has at the middle, or top, a principal\n   face, called the table, which is surrounded by a number of sloping\n   facets forming a bizet; below, it has a small face or collet, parallel\n   to the table, connected with the girdle by a pavilion of elongated\n   facets. It is thus distinguished from the rose diamond, which is\n   entirely covered with facets on the surface, and is flat below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliant', 'a.', 'The smallest size of type used in England printing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliant', 'a.', 'A kind of cotton goods, figured on the weaving.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliantly', 'adv.', 'In a brilliant manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brilliantness', 'n.', 'Brilliancy; splendor; glitter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brills', 'n. pl.', 'The hair on the eyelids of a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brim', 'n.', 'The rim, border, or upper edge of a cup, dish, or any hollow\n   vessel used for holding anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brim', 'n.', 'The edge or margin, as of a fountain, or of the water\n   contained in it; the brink; border.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brim', 'n.', 'The rim of a hat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimmed', 'imp. & p. p.', 'of Brim');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimming', 'p. pr. & vb. n.', 'of Brim');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brim', 'v. i.', 'To be full to the brim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brim', 'v. t.', 'To fill to the brim, upper edge, or top.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brim', 'a.', 'Fierce; sharp; cold. See Breme.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimful', 'a.', 'Full to the brim; completely full; ready to overflow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimless', 'a.', 'Having no brim; as, brimless caps.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimmed', 'a.', 'Having a brim; -- usually in composition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimmed', 'a.', 'Full to, or level with, the brim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimmer', 'n.', 'A brimful bowl; a bumper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimming', 'a.', 'Full to the brim; overflowing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimstone', 'v. t.', 'Sulphur; See Sulphur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimstone', 'a.', 'Made of, or pertaining to, brimstone; as, brimstone\n   matches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brimstony', 'a.', 'Containing or resembling brimstone; sulphurous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brin', 'n.', 'One of the radiating sticks of a fan. The outermost are\n   larger and longer, and are called panaches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brinded', 'a.', 'Of a gray or tawny color with streaks of darker hue;\n   streaked; brindled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brindle', 'n.', 'The state of being brindled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brindle', 'n.', 'A brindled color; also, that which is brindled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brindle', 'a.', 'Brindled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brindled', 'a.', 'Having dark streaks or spots on a gray or tawny ground;\n   brinded.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brine', 'n.', 'Water saturated or strongly impregnated with salt; pickle;\n   hence, any strong saline solution; also, the saline residue or strong\n   mother liquor resulting from the evaporation of natural or artificial\n   waters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brine', 'n.', 'The ocean; the water of an ocean, sea, or salt lake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brine', 'n.', 'Tears; -- so called from their saltness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brine', 'v. t.', 'To steep or saturate in brine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brine', 'v. t.', 'To sprinkle with salt or brine; as, to brine hay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brought', 'imp. & p. p.', 'of Bring');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bringing', 'p. pr. & vb. n.', 'of Bring');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bring', 'v. t.', 'To convey to the place where the speaker is or is to be;\n   to bear from a more distant to a nearer place; to fetch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bring', 'v. t.', 'To cause the accession or obtaining of; to procure; to\n   make to come; to produce; to draw to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bring', 'v. t.', 'To convey; to move; to carry or conduct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bring', 'v. t.', 'To persuade; to induce; to draw; to lead; to guide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bring', 'v. t.', 'To produce in exchange; to sell for; to fetch; as, what\n   does coal bring per ton?');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bringer', 'n.', 'One who brings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brininess', 'n.', 'The state or quality of being briny; saltness;\n   brinishness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brinish', 'a.', 'Like brine; somewhat salt; saltish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brinishness', 'n.', 'State or quality of being brinish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brinjaree', 'n.', 'A rough-haired East Indian variety of the greyhound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brink', 'n.', 'The edge, margin, or border of a steep place, as of a\n   precipice; a bank or edge, as of a river or pit; a verge; a border; as,\n   the brink of a chasm. Also Fig.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briny', 'a.', 'Of or pertaining to brine, or to the sea; partaking of the\n   nature of brine; salt; as, a briny taste; the briny flood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briony', 'n.', 'See Bryony.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brisk', 'a.', 'Full of liveliness and activity; characterized by quickness\n   of motion or action; lively; spirited; quick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brisk', 'a.', 'Full of spirit of life; effervesc/ng, as liquors; sparkling;\n   as, brick cider.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bricked', 'imp. & p. p.', 'of Brisk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bricking', 'p. pr. & vb. n.', 'of Brisk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brisk', 'v. t. & i.', 'To make or become lively; to enliven; to animate; to\n   take, or cause to take, an erect or bold attitude; -- usually with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brisket', 'n.', 'That part of the breast of an animal which extends from\n   the fore legs back beneath the ribs; also applied to the fore part of a\n   horse, from the shoulders to the bottom of the chest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briskly', 'adv.', 'In a brisk manner; nimbly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briskness', 'n.', 'Liveliness; vigor in action; quickness; gayety;\n   vivacity; effervescence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'n.', 'A short, stiff, coarse hair, as on the back of swine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'n.', 'A stiff, sharp, roundish hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristled', 'imp. & p. p.', 'of Bristle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristling', 'p. pr. & vb. n.', 'of Bristle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'v. t.', 'To erect the bristles of; to cause to stand up, as the\n   bristles of an angry hog; -- sometimes with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'v. t.', 'To fix a bristle to; as, to bristle a thread.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'v. i.', 'To rise or stand erect, like bristles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'v. i.', 'To appear as if covered with bristles; to have\n   standing, thick and erect, like bristles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle', 'v. i.', 'To show defiance or indignation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle-pointed', 'a.', 'Terminating in a very fine, sharp point, as some\n   leaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristle-shaped', 'a.', 'Resembling a bristle in form; as, a bristle-shaped\n   leaf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristletail', 'n.', 'An insect of the genera Lepisma, Campodea, etc.,\n   belonging to the Thysanura.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristliness', 'n.', 'The quality or state of having bristles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristly', 'a.', 'Thick set with bristles, or with hairs resembling\n   bristles; rough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bristol', 'n.', 'A seaport city in the west of England.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brisure', 'n.', 'Any part of a rampart or parapet which deviates from the\n   general direction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brisure', 'n.', 'A mark of cadency or difference.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brit', 'n.', 'Alt. of Britt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Britt', 'n.', 'The young of the common herring; also, a small species of\n   herring; the sprat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Britt', 'n.', 'The minute marine animals (chiefly Entomostraca) upon which\n   the right whales feed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Britannia', 'n.', 'A white-metal alloy of tin, antimony, bismuth, copper,\n   etc. It somewhat resembles silver, and is used for table ware. Called\n   also Britannia metal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Britannic', 'a.', 'Of or pertaining to Great Britain; British; as, her\n   Britannic Majesty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brite', 'v. t.', 'Alt. of Bright');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bright', 'v. t.', 'To be or become overripe, as wheat, barley, or hops.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briticism', 'n.', 'A word, phrase, or idiom peculiar to Great Britain; any\n   manner of using a word or words that is peculiar to Great Britain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'British', 'a.', 'Of or pertaining to Great Britain or to its inhabitants;\n   -- sometimes restricted to the original inhabitants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'British', 'n. pl.', 'People of Great Britain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Britisher', 'n.', 'An Englishman; a subject or inhabitant of Great Britain,\n   esp. one in the British military or naval service.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briton', 'a.', 'British.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Briton', 'n.', 'A native of Great Britain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brittle', 'a.', 'Easily broken; apt to break; fragile; not tough or\n   tenacious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brittlely', 'adv.', 'In a brittle manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brittleness', 'n.', 'Aptness to break; fragility.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brittle star', '', 'Any species of ophiuran starfishes. See Ophiuroidea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Britzska', 'n.', 'A long carriage, with a calash top, so constructed as to\n   give space for reclining at night, when used on a journey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brize', 'n.', 'The breeze fly. See Breeze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A spit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'An awl; a bodkin; also, a wooden rod or pin, sharpened at\n   each end, used by thatchers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A tool of steel, generally tapering, and of a polygonal\n   form, with from four to eight cutting edges, for smoothing or enlarging\n   holes in metal; sometimes made smooth or without edges, as for\n   burnishing pivot holes in watches; a reamer. The broach for gun barrels\n   is commonly square and without taper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A straight tool with file teeth, made of steel, to be\n   pressed through irregular holes in metal that cannot be dressed by\n   revolving tools; a drift.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A broad chisel for stonecutting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A spire rising from a tower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A clasp for fastening a garment. See Brooch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'A spitlike start, on the head of a young stag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'The stick from which candle wicks are suspended for\n   dipping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'The pin in a lock which enters the barrel of the key.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broached', 'imp. & p. p.', 'of Broach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broaching', 'p. pr. & vb. n.', 'of Broach');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To spit; to pierce as with a spit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To tap; to pierce, as a cask, in order to draw the liquor.\n   Hence: To let out; to shed, as blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To open for the first time, as stores.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To make public; to utter; to publish first; to put forth;\n   to introduce as a topic of conversation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To cause to begin or break out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To shape roughly, as a block of stone, by chiseling with a\n   coarse tool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broach', 'n.', 'To enlarge or dress (a hole); by using a broach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broacher', 'n.', 'A spit; a broach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broacher', 'n.', 'One who broaches, opens, or utters; a first publisher or\n   promoter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Wide; extend in breadth, or from side to side; --\n   opposed to narrow; as, a broad street, a broad table; an inch broad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Extending far and wide; extensive; vast; as, the broad\n   expanse of ocean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Extended, in the sense of diffused; open; clear; full.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Fig.: Having a large measure of any thing or quality;\n   not limited; not restrained; -- applied to any subject, and retaining\n   the literal idea more or less clearly, the precise meaning depending\n   largely on the substantive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Comprehensive; liberal; enlarged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Plain; evident; as, a broad hint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Free; unrestrained; unconfined.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Characterized by breadth. See Breadth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Cross; coarse; indelicate; as, a broad compliment; a\n   broad joke; broad humor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'superl.', 'Strongly marked; as, a broad Scotch accent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'n.', 'The broad part of anything; as, the broad of an oar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'n.', 'The spread of a river into a sheet of water; a flooded fen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad', 'n.', 'A lathe tool for turning down the insides and bottoms of\n   cylinders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadax Broadaxe', 'n.', 'An ancient military weapon; a battle-ax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadax Broadaxe', 'n.', 'An ax with a broad edge, for hewing timber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadbill', 'n.', 'A wild duck (Aythya, / Fuligula, marila); which appears\n   in large numbers on the eastern coast of the United States, in autumn;\n   -- called also bluebill, blackhead, raft duck, and scaup duck. See\n   Scaup duck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadbill', 'n.', 'The shoveler. See Shoveler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadbrim', 'n.', 'A hat with a very broad brim, like those worn by men of\n   the society of Friends.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadbrim', 'n.', 'A member of the society of Friends; a Quaker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad-brimmed', 'a.', 'Having a broad brim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadcast', 'n.', 'A casting or throwing seed in all directions, as from\n   the hand in sowing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadcast', 'a.', 'Cast or dispersed in all directions, as seed from the\n   hand in sowing; widely diffused.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadcast', 'a.', 'Scattering in all directions (as a method of sowing); --\n   opposed to planting in hills, or rows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadcast', 'adv.', 'So as to scatter or be scattered in all directions; so\n   as to spread widely, as seed from the hand in sowing, or news from the\n   press.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad Church', '', 'A portion of the Church of England, consisting of\n   persons who claim to hold a position, in respect to doctrine and\n   fellowship, intermediate between the High Church party and the Low\n   Church, or evangelical, party. The term has been applied to other\n   bodies of men holding liberal or comprehensive views of Christian\n   doctrine and fellowship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadcloth', 'n.', 'A fine smooth-faced woolen cloth for mens garments,\n   usually of double width (i.e., a yard and a half); -- so called in\n   distinction from woolens three quarters of a yard wide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadened', 'imp. & p. p.', 'of Broaden');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadening', 'p. pr. & vb. n.', 'of Broaden');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broaden', 'a.', 'To grow broad; to become broader or wider.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broaden', 'v. t.', 'To make broad or broader; to render more broad or\n   comprehensive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad gauge', '', 'A wider distance between the rails than the \"standard\"\n   gauge of four feet eight inches and a half. See Gauge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad-horned', 'a.', 'Having horns spreading widely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadish', 'a.', 'Rather broad; moderately broad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadleaf', 'n.', 'A tree (Terminalia latifolia) of Jamaica, the wood of\n   which is used for boards, scantling, shingles, etc; -- sometimes called\n   the almond tree, from the shape of its fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad-leaved', 'a.', 'Alt. of Broad-leafed');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad-leafed', 'a.', 'Having broad, or relatively broad, leaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadly', 'adv.', 'In a broad manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadmouth', 'n.', 'One of the Eurylaimidae, a family of East Indian\n   passerine birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadness', 'n.', 'The condition or quality of being broad; breadth;\n   coarseness; grossness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadpiece', 'n.', 'An old English gold coin, broader than a guinea, as a\n   Carolus or Jacobus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broad seal', '', 'The great seal of England; the public seal of a country\n   or state.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadseal', 'v. t.', 'To stamp with the broad seal; to make sure; to\n   guarantee or warrant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadside', 'n.', 'The side of a ship above the water line, from the bow to\n   the quarter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadside', 'n.', 'A discharge of or from all the guns on one side of a\n   ship, at the same time.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadside', 'n.', 'A volley of abuse or denunciation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadside', 'n.', 'A sheet of paper containing one large page, or printed\n   on one side only; -- called also broadsheet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadspread', 'a.', 'Widespread.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadspreading', 'a.', 'Spreading widely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadsword', 'n.', 'A sword with a broad blade and a cutting edge; a\n   claymore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broadwise', 'adv.', 'Breadthwise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brob', 'n.', 'A peculiar brad-shaped spike, to be driven alongside the end\n   of an abutting timber to prevent its slipping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brobdingnagian', 'a.', 'Colossal; of extraordinary height; gigantic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brobdingnagian', 'n.', 'A giant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocade', 'n.', 'Silk stuff, woven with gold and silver threads, or\n   ornamented with raised flowers, foliage, etc.; -- also applied to other\n   stuffs thus wrought and enriched.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocaded', 'a.', 'Woven or worked, as brocade, with gold and silver, or\n   with raised flowers, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocaded', 'a.', 'Dressed in brocade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocage', 'n.', 'See Brokkerage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocard', 'n.', 'An elementary principle or maximum; a short, proverbial\n   rule, in law, ethics, or metaphysics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocatel', 'n.', 'A kind of coarse brocade, or figured fabric, used chiefly\n   for tapestry, linings for carriages, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocatel', 'n.', 'A marble, clouded and veined with white, gray, yellow,\n   and red, in which the yellow usually prevails. It is also called Siena\n   marble, from its locality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocatello', 'n.', 'Same as Brocatel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broccoli', 'n.', 'A plant of the Cabbage species (Brassica oleracea) of\n   many varieties, resembling the cauliflower. The \"curd,\" or flowering\n   head, is the part used for food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brochantite', 'n.', 'A basic sulphate of copper, occurring in emerald-green\n   crystals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broche', 'a.', 'Woven with a figure; as, broche goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broche', 'n.', 'See Broach, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brochure', 'v. t.', 'A printed and stitched book containing only a few\n   leaves; a pamphlet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brock', 'n.', 'A badger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brock', 'n.', 'A brocket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocket', 'n.', 'A male red deer two years old; -- sometimes called brock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brocket', 'n.', 'A small South American deer, of several species (Coassus\n   superciliaris, C. rufus, and C. auritus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brockish', 'a.', 'Beastly; brutal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brodekin', 'n.', 'A buskin or half-boot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brog', 'n.', 'A pointed instrument, as a joiners awl, a brad awl, a\n   needle, or a small sharp stick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brog', 'v. t.', 'To prod with a pointed instrument, as a lance; also, to\n   broggle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brogan', 'n.', 'A stout, coarse shoe; a brogue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broggle', 'n.', 'To sniggle, or fish with a brog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brogue', 'n.', 'A stout, coarse shoe; a brogan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brogue', 'v. t.', 'A dialectic pronunciation; esp. the Irish manner of\n   pronouncing English.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brogues', 'n. pl.', 'Breeches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broid', 'v. t.', 'To braid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broidered', 'imp. & p. p.', 'of Broider');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broider', 'v. t.', 'To embroider.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiderer', 'n.', 'One who embroiders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broidery', 'n.', 'Embroidery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broil', 'n.', 'A tumult; a noisy quarrel; a disturbance; a brawl;\n   contention; discord, either between individuals or in the state.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiled', 'imp. & p. p.', 'of Broil');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiling', 'p. pr. & vb. n.', 'of Broil');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broil', 'v. t.', 'To cook by direct exposure to heat over a fire, esp. upon\n   a gridiron over coals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broil', 'v. t.', 'To subject to great (commonly direct) heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broil', 'v. i.', 'To be subjected to the action of heat, as meat over the\n   fire; to be greatly heated, or to be made uncomfortable with heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiler', 'n.', 'One who excites broils; one who engages in or promotes\n   noisy quarrels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiler', 'n.', 'One who broils, or cooks by broiling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiler', 'n.', 'A gridiron or other utensil used in broiling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiler', 'n.', 'A chicken or other bird fit for broiling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiling', 'a.', 'Excessively hot; as, a broiling sun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broiling', 'n.', 'The act of causing anything to broil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokage', 'n.', 'See Brokerage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broke', 'v. i.', 'To transact business for another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broke', 'v. i.', 'To act as procurer in love matters; to pimp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broke', '', 'imp. & p. p. of Break.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Separated into parts or pieces by violence; divided into\n   fragments; as, a broken chain or rope; a broken dish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Disconnected; not continuous; also, rough; uneven; as, a\n   broken surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Fractured; cracked; disunited; sundered; strained;\n   apart; as, a broken reed; broken friendship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Made infirm or weak, by disease, age, or hardships.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Subdued; humbled; contrite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Subjugated; trained for use, as a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Crushed and ruined as by something that destroys hope;\n   blighted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Not carried into effect; not adhered to; violated; as, a\n   broken promise, vow, or contract; a broken law.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Ruined financially; incapable of redeeming promises\n   made, or of paying debts incurred; as, a broken bank; a broken\n   tradesman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken', 'v. t.', 'Imperfectly spoken, as by a foreigner; as, broken\n   English; imperfectly spoken on account of emotion; as, to say a few\n   broken words at parting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken-backed', 'a.', 'Having a broken back; as, a broken-backed chair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken-backed', 'a.', 'Hogged; so weakened in the frame as to droop at each\n   end; -- said of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken-bellied', 'a.', 'Having a ruptured belly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken-hearted', 'a.', 'Having the spirits depressed or crushed by grief or\n   despair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokenly', 'adv.', 'In a broken, interrupted manner; in a broken state; in\n   broken language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokenness', 'n.', 'The state or quality of being broken; unevenness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokenness', 'n.', 'Contrition; as, brokenness of heart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken wind', '', 'The heaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broken-winded', 'a.', 'Having short breath or disordered respiration, as a\n   horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broker', 'v. t.', 'One who transacts business for another; an agent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broker', 'v. t.', 'An agent employed to effect bargains and contracts, as a\n   middleman or negotiator, between other persons, for a compensation\n   commonly called brokerage. He takes no possession, as broker, of the\n   subject matter of the negotiation. He generally contracts in the names\n   of those who employ him, and not in his own.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broker', 'v. t.', 'A dealer in money, notes, bills of exchange, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broker', 'v. t.', 'A dealer in secondhand goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broker', 'v. t.', 'A pimp or procurer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokerage', 'n.', 'The business or employment of a broker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokerage', 'n.', 'The fee, reward, or commission, given or changed for\n   transacting business as a broker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokerly', 'a.', 'Mean; servile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brokery', 'n.', 'The business of a broker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broking', 'a.', 'Of or pertaining to a broker or brokers, or to brokerage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broma', 'n.', 'Aliment; food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broma', 'n.', 'A light form of prepared cocoa (or cacao); or the drink made\n   from it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromal', 'n.', 'An oily, colorless fluid, CBr3.COH, related to bromoform,\n   as chloral is to chloroform, and obtained by the action of bromine on\n   alcohol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromate', 'n.', 'A salt of bromic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromate', 'v. t.', 'To combine or impregnate with bromine; as, bromated\n   camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromatologist', 'n.', 'One versed in the science of foods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromatology', 'n.', 'The science of aliments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brome', 'n.', 'See Bromine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brome grass', '', 'A genus (Bromus) of grasses, one species of which is the\n   chess or cheat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromeliaceous', 'a.', 'Pertaining to, or resembling, a family of endogenous\n   and mostly epiphytic or saxicolous plants of which the genera\n   Tillandsia and Billbergia are examples. The pineapple, though\n   terrestrial, is also of this family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromic', 'a.', 'Of, pertaining to, or containing, bromine; -- said of those\n   compounds of bromine in which this element has a valence of five, or\n   the next to its highest; as, bromic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromide', 'n.', 'A compound of bromine with a positive radical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brominate', 'v. t.', 'See Bromate, v. t.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromine', 'n.', 'One of the elements, related in its chemical qualities to\n   chlorine and iodine. Atomic weight 79.8. Symbol Br. It is a deep\n   reddish brown liquid of a very disagreeable odor, emitting a brownish\n   vapor at the ordinary temperature. In combination it is found in minute\n   quantities in sea water, and in many saline springs. It occurs also in\n   the mineral bromyrite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromism', 'n.', 'A diseased condition produced by the excessive use of\n   bromine or one of its compounds. It is characterized by mental dullness\n   and muscular weakness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromize', 'v. t.', 'To prepare or treat with bromine; as, to bromize a\n   silvered plate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromlife', 'n.', 'A carbonate of baryta and lime, intermediate between\n   witherite and strontianite; -- called also alstonite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromoform', 'n.', 'A colorless liquid, CHBr3, having an agreeable odor and\n   sweetish taste. It is produced by the simultaneous action of bromine\n   and caustic potash upon wood spirit, alcohol, or acetone, as also by\n   certain other reactions. In composition it is the same as chloroform,\n   with the substitution of bromine for chlorine. It is somewhat similar\n   to chloroform in its effects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brompicrin', 'n.', 'A pungent colorless explosive liquid, CNO2Br3,\n   analogous to and resembling chlorpicrin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromuret', 'n.', 'See Bromide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bromyrite', 'n.', 'Silver bromide, a rare mineral; -- called also\n   bromargyrite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchi', 'n. pl.', 'See Bronchus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchia', 'n. pl.', 'The bronchial tubes which arise from the branching of\n   the trachea, esp. the subdivision of the bronchi.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchial', 'a.', 'Belonging to the bronchi and their ramifications in the\n   lungs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchic', 'a.', 'Bronchial.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchiole', 'n.', 'A minute bronchial tube.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchitic', 'a.', 'Of or pertaining to bronchitis; as, bronchitic\n   inflammation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchitis', 'n.', 'Inflammation, acute or chronic, of the bronchial tubes\n   or any part of them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broncho', 'n.', 'A native or a Mexican horse of small size.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchocele', 'n.', 'See Goiter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchophony', 'n.', 'A modification of the voice sounds, by which they are\n   intensified and heightened in pitch; -- observed in auscultation of the\n   chest in certain cases of intro-thoracic disease.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broncho-pneumonia', 'n.', 'Inflammation of the bronchi and lungs; catarrhal\n   pneumonia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchotome', 'n.', 'An instrument for cutting into the bronchial tubes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchotomy', 'n.', 'An incision into the windpipe or larynx, including the\n   operations of tracheotomy and laryngotomy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchi', 'pl. ', 'of Bronchus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronchus', 'n.', 'One of the subdivisions of the trachea or windpipe; esp.\n   one of the two primary divisions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronco', 'n.', 'Same as Broncho.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brond', 'n.', 'A sword.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brontolite', 'n.', 'Alt. of Brontolith');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brontolith', 'n.', 'An aerolite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brontology', 'n.', 'A treatise upon thunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brontosaurus', 'n.', 'A genus of American jurassic dinosaurs. A length of\n   sixty feet is believed to have been attained by these reptiles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brontotherium', 'n.', 'A genus of large extinct mammals from the miocene\n   strata of western North America. They were allied to the rhinoceros,\n   but the skull bears a pair of powerful horn cores in front of the\n   orbits, and the fore feet were four-toed. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brontozoum', 'n.', 'An extinct animal of large size, known from its\n   three-toed footprints in Mesozoic sandstone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronze', 'a.', 'An alloy of copper and tin, to which small proportions of\n   other metals, especially zinc, are sometimes added. It is hard and\n   sonorous, and is used for statues, bells, cannon, etc., the proportions\n   of the ingredients being varied to suit the particular purposes. The\n   varieties containing the higher proportions of tin are brittle, as in\n   bell metal and speculum metal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronze', 'a.', 'A statue, bust, etc., cast in bronze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronze', 'a.', 'A yellowish or reddish brown, the color of bronze; also, a\n   pigment or powder for imitating bronze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronze', 'a.', 'Boldness; impudence; \"brass.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzed', 'imp. & p. p.', 'of Bronze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzing', 'p. pr. & vb. n.', 'of Bronze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronze', 'n.', 'To give an appearance of bronze to, by a coating of bronze\n   powder, or by other means; to make of the color of bronze; as, to\n   bronze plaster casts; to bronze coins or medals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronze', 'n.', 'To make hard or unfeeling; to brazen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzewing', 'n.', 'An Australian pigeon of the genus Phaps, of several\n   species; -- so called from its bronze plumage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzine', 'n.', 'A metal so prepared as to have the appearance of bronze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzine', 'a.', 'Made of bronzine; resembling bronze; bronzelike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzing', 'n.', 'The act or art of communicating to articles in metal,\n   wood, clay, plaster, etc., the appearance of bronze by means of bronze\n   powders, or imitative painting, or by chemical processes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzing', 'n.', 'A material for bronzing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzist', 'n.', 'One who makes, imitates, collects, or deals in, bronzes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzite', 'n.', 'A variety of enstatite, often having a bronzelike luster.\n   It is a silicate of magnesia and iron, of the pyroxene family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bronzy', 'a.', 'Like bronze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooch', 'n.', 'An ornament, in various forms, with a tongue, pin, or loop\n   for attaching it to a garment; now worn at the breast by women; a\n   breastpin. Formerly worn by men on the hat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooch', 'n.', 'A painting all of one color, as a sepia painting, or an\n   India painting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooch', 'imp. & p. p.', 'To adorn as with a brooch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'The young birds hatched at one time; a hatch; as, a brood\n   of chickens.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'The young from the same dam, whether produced at the same\n   time or not; young children of the same mother, especially if nearly of\n   the same age; offspring; progeny; as, a woman with a brood of children.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'That which is bred or produced; breed; species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'Heavy waste in tin and copper ores.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'a.', 'Sitting or inclined to sit on eggs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'a.', 'Kept for breeding from; as, a brood mare; brood stock;\n   having young; as, a brood sow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooded', 'imp. & p. p.', 'of Brood');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooding', 'p. pr. & vb. n.', 'of Brood');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. i.', 'To sit on and cover eggs, as a fowl, for the purpose of\n   warming them and hatching the young; or to sit over and cover young, as\n   a hen her chickens, in order to warm and protect them; hence, to sit\n   quietly, as if brooding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. i.', 'To have the mind dwell continuously or moodily on a\n   subject; to think long and anxiously; to be in a state of gloomy,\n   serious thought; -- usually followed by over or on; as, to brood over\n   misfortunes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'To sit over, cover, and cherish; as, a hen broods her\n   chickens.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'To cherish with care.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brood', 'v. t.', 'To think anxiously or moodily upon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broody', 'a.', 'Inclined to brood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brook', 'v. t.', 'A natural stream of water smaller than a river or creek.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooked', 'imp. & p. p.', 'of Brook');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooking', 'p. pr. & vb. n.', 'of Brook');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brook', 'v. t.', 'To use; to enjoy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brook', 'v. t.', 'To bear; to endure; to put up with; to tolerate; as,\n   young men can not brook restraint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brook', 'v. t.', 'To deserve; to earn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brookite', 'n.', 'A mineral consisting of titanic oxide, and hence\n   identical with rutile and octahedrite in composition, but crystallizing\n   in the orthorhombic system.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooklet', 'n.', 'A small brook.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brooklime', 'n.', 'A plant (Veronica Beccabunga); with flowers, usually\n   blue, in axillary racemes. The American species is V. Americana.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brook mint', '', 'See Water mint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brookside', 'n.', 'The bank of a brook.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brookweed', 'n.', 'A small white-flowered herb (Samolus Valerandi) found\n   usually in wet places; water pimpernel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broom', 'n.', 'A plant having twigs suitable for making brooms to sweep\n   with when bound together; esp., the Cytisus scoparius of Western\n   Europe, which is a low shrub with long, straight, green, angular\n   branches, minute leaves, and large yellow flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broom', 'n.', 'An implement for sweeping floors, etc., commonly made of the\n   panicles or tops of broom corn, bound together or attached to a long\n   wooden handle; -- so called because originally made of the twigs of the\n   broom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broom', 'v. t.', 'See Bream.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broom corn', '', 'A variety of Sorghum vulgare, having a joined stem, like\n   maize, rising to the height of eight or ten feet, and bearing its seeds\n   on a panicle with long branches, of which brooms are made.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broom rape', '', 'A genus (Orobanche) of parasitic plants of Europe and\n   Asia. They are destitute of chlorophyll, have scales instead of leaves,\n   and spiked flowers, and grow attached to the roots of other plants, as\n   furze, clover, flax, wild carrot, etc. The name is sometimes applied to\n   other plants related to this genus, as Aphyllon uniflorumand A.\n   Ludovicianum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broomstaff', 'n.', 'A broomstick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broomstick', 'n.', 'A stick used as a handle of a broom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broomy', 'a.', 'Of or pertaining to broom; overgrowing with broom;\n   resembling broom or a broom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brose', 'n.', 'Pottage made by pouring some boiling liquid on meal (esp.\n   oatmeal); and stirring it. It is called beef brose, water brose, etc.,\n   according to the name of the liquid (beef broth, hot water, etc.) used.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotel', 'a.', 'Brittle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotelness', 'n.', 'Brittleness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Broth', 'n.', 'Liquid in which flesh (and sometimes other substances, as\n   barley or rice) has been boiled; thin or simple soup.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brothel', 'n.', 'A house of lewdness or ill fame; a house frequented by\n   prostitutes; a bawdyhouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotheler', 'n.', 'One who frequents brothels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brothelry', 'n.', 'Lewdness; obscenity; a brothel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brothers', 'pl. ', 'of Brother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brethren', 'pl. ', 'of Brother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brothers', 'pl. ', 'of Brother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brethren', 'pl. ', 'of Brother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brother', 'n.', 'A male person who has the same father and mother with\n   another person, or who has one of them only. In the latter case he is\n   more definitely called a half brother, or brother of the half blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brother', 'n.', 'One related or closely united to another by some common\n   tie or interest, as of rank, profession, membership in a society, toil,\n   suffering, etc.; -- used among judges, clergymen, monks, physicians,\n   lawyers, professors of religion, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brother', 'n.', 'One who, or that which, resembles another in distinctive\n   qualities or traits of character.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brothered', 'imp. & p. p.', 'of Brother');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brother', 'v. t.', 'To make a brother of; to call or treat as a brother; to\n   admit to a brotherhood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brother german', '', 'A brother by both the fathers and mothers side, in\n   contradistinction to a uterine brother, one by the mother only.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherhood', 'n.', 'The state of being brothers or a brother.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherhood', 'n.', 'An association for any purpose, as a society of monks;\n   a fraternity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherhood', 'n.', 'The whole body of persons engaged in the same\n   business, -- especially those of the same profession; as, the legal or\n   medical brotherhood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherhood', 'n.', 'Persons, and, poetically, things, of a like kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brothers-in-law', 'pl. ', 'of Brother-in-law');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brother-in-law', 'n.', 'The brother of ones husband or wife; also, the\n   husband of ones sister; sometimes, the husband of ones wifes sister.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherliness', 'n.', 'The state or quality of being brotherly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherly', 'a.', 'Of or pertaining to brothers; such as is natural for\n   brothers; becoming to brothers; kind; affectionate; as, brotherly love.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brotherly', 'adv.', 'Like a brother; affectionately; kindly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brouded', 'p.a.', 'Braided; broidered.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brougham', 'n.', 'A light, close carriage, with seats inside for two or\n   four, and the fore wheels so arranged as to turn short.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brow', 'n.', 'The prominent ridge over the eye, with the hair that covers\n   it, forming an arch above the orbit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brow', 'n.', 'The hair that covers the brow (ridge over the eyes); the\n   eyebrow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brow', 'n.', 'The forehead; as, a feverish brow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brow', 'n.', 'The general air of the countenance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brow', 'n.', 'The edge or projecting upper part of a steep place; as, the\n   brow of a precipice; the brow of a hill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brow', 'v. t.', 'To bound to limit; to be at, or form, the edge of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browbeat', 'imp.', 'of Browbeat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browbeaten', 'p. p.', 'of Browbeat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browbeating', 'p. pr. & vb. n.', 'of Browbeat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browbeat', 'v. t.', 'To depress or bear down with haughty, stern looks, or\n   with arrogant speech and dogmatic assertions; to abash or disconcert by\n   impudent or abusive words or looks; to bully; as, to browbeat\n   witnesses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browbeating', 'n.', 'The act of bearing down, abashing, or disconcerting,\n   with stern looks, supercilious manners, or confident assertions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browbound', 'a.', 'Crowned; having the head encircled as with a diadem.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browdyng', 'n.', 'Embroidery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browed', 'a.', 'Having (such) a brow; -- used in composition; as,\n   dark-browed, stern-browed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browless', 'a.', 'Without shame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown', 'superl.', 'Of a dark color, of various shades between black and\n   red or yellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown', 'n.', 'A dark color inclining to red or yellow, resulting from the\n   mixture of red and black, or of red, black, and yellow; a tawny, dusky\n   hue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browned', 'imp. & p. p.', 'of Brown');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browning', 'p. pr. & vb. n.', 'of Brown');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown', 'v. t.', 'To make brown or dusky.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown', 'v. t.', 'To make brown by scorching slightly; as, to brown meat or\n   flour.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown', 'v. t.', 'To give a bright brown color to, as to gun barrels, by\n   forming a thin coat of oxide on their surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown', 'v. i.', 'To become brown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownback', 'n.', 'The dowitcher or red-breasted snipe. See Dowitcher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown bill', '', 'A bill or halberd of the 16th and 17th centuries. See 4th\n   Bill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownian', 'a.', 'Pertaining to Dr. Robert Brown, who first demonstrated\n   (about 1827) the commonness of the motion described below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownie', 'n.', 'An imaginary good-natured spirit, who was supposed often\n   to perform important services around the house by night, such as\n   thrashing, churning, sweeping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browning', 'n.', 'The act or operation of giving a brown color, as to gun\n   barrels, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browning', 'n.', 'A smooth coat of brown mortar, usually the second coat,\n   and the preparation for the finishing coat of plaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownish', 'a.', 'Somewhat brown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownism', 'n.', 'The views or teachings of Robert Brown of the Brownists.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownism', 'n.', 'The doctrines of the Brunonian system of medicine. See\n   Brunonian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownist', 'n.', 'A follower of Robert Brown, of England, in the 16th\n   century, who taught that every church is complete and independent in\n   itself when organized, and consists of members meeting in one place,\n   having full power to elect and depose its officers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownist', 'n.', 'One who advocates the Brunonian system of medicine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownness', 'n.', 'The quality or state of being brown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownstone', 'n.', 'A dark variety of sandstone, much used for building\n   purposes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brown thrush', '', 'A common American singing bird (Harporhynchus rufus);\n   allied to the mocking bird; -- also called brown thrasher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brownwort', 'n.', 'A species of figwort or Scrophularia (S. vernalis); and\n   other species of the same genus, mostly perennials with inconspicuous\n   coarse flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browny', 'a.', 'Brown or, somewhat brown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browpost', 'n.', 'A beam that goes across a building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browse', 'n.', 'The tender branches or twigs of trees and shrubs, fit for\n   the food of cattle and other animals; green food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browsed', 'imp. & p. p.', 'of Browse');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browsing', 'p. pr. & vb. n.', 'of Browse');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browse', 'n.', 'To eat or nibble off, as the tender branches of trees,\n   shrubs, etc.; -- said of cattle, sheep, deer, and some other animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browse', 'n.', 'To feed on, as pasture; to pasture on; to graze.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browse', 'v. i.', 'To feed on the tender branches or shoots of shrubs or\n   trees, as do cattle, sheep, and deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browse', 'v. i.', 'To pasture; to feed; to nibble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browser', 'n.', 'An animal that browses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browsewood', 'n.', 'Shrubs and bushes upon which animals browse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browsing', 'n.', 'Browse; also, a place abounding with shrubs where animals\n   may browse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Browspot', 'n.', 'A rounded organ between the eyes of the frog; the\n   interocular gland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruang', 'n.', 'The Malayan sun bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brucine', 'n.', 'A powerful vegetable alkaloid, found, associated with\n   strychnine, in the seeds of different species of Strychnos, especially\n   in the Nux vomica. It is less powerful than strychnine. Called also\n   brucia and brucina.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brucite', 'n.', 'A white, pearly mineral, occurring thin and foliated, like\n   talc, and also fibrous; a native magnesium hydrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brucite', 'n.', 'The mineral chondrodite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruckeled', 'a.', 'Wet and dirty; begrimed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruh', 'n.', 'The rhesus monkey. See Rhesus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruin', 'a.', 'A bear; -- so called in popular tales and fables.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruised', 'imp. & p. p.', 'of Bruise');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruising', 'p. pr. & vb. n.', 'of Bruise');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruise', 'v. t.', 'To injure, as by a blow or collision, without\n   laceration; to contuse; as, to bruise ones finger with a hammer; to\n   bruise the bark of a tree with a stone; to bruise an apple by letting\n   it fall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruise', 'v. t.', 'To break; as in a mortar; to bray, as minerals, roots,\n   etc.; to crush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruise', 'v. i.', 'To fight with the fists; to box.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruise', 'n.', 'An injury to the flesh of animals, or to plants, fruit,\n   etc., with a blunt or heavy instrument, or by collision with some other\n   body; a contusion; as, a bruise on the head; bruises on fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruiser', 'n.', 'One who, or that which, bruises.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruiser', 'n.', 'A boxer; a pugilist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruiser', 'n.', 'A concave tool used in grinding lenses or the speculums of\n   telescopes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruisewort', 'n.', 'A plant supposed to heal bruises, as the true daisy,\n   the soapwort, and the comfrey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruit', 'n.', 'Report; rumor; fame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruit', 'n.', 'An abnormal sound of several kinds, heard on auscultation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruited', 'imp. & p. p.', 'of Bruit');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruiting', 'p. pr. & vb. n.', 'of Bruit');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruit', 'v. t.', 'To report; to noise abroad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brumaire', 'n.', 'The second month of the calendar adopted by the first\n   French republic. It began thirty days after the autumnal equinox. See\n   Vendemiaire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brumal', 'a.', 'Of or pertaining to winter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brume', 'n.', 'Mist; fog; vapors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brummagem', 'a.', 'Counterfeit; gaudy but worthless; sham.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brumous', 'a.', 'Foggy; misty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brun', 'n.', 'Same as Brun, a brook.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunette', 'a.', 'A girl or woman with a somewhat brown or dark complexion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunette', 'a.', 'Having a dark tint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunion', 'n.', 'A nectarine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunonian', 'a.', 'Pertaining to, or invented by, Brown; -- a term applied\n   to a system of medicine promulgated in the 18th century by John Brown,\n   of Scotland, the fundamental doctrine of which was, that life is a\n   state of excitation produced by the normal action of external agents\n   upon the body, and that disease consists in excess or deficiency of\n   excitation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunswick black', '', 'See Japan black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunswick green', '', 'An oxychloride of copper, used as a green pigment;\n   also, a carbonate of copper similarly employed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunt', 'v. t.', 'The heat, or utmost violence, of an onset; the strength\n   or greatest fury of any contention; as, the brunt of a battle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brunt', 'v. t.', 'The force of a blow; shock; collision.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'An instrument composed of bristles, or other like material,\n   set in a suitable back or handle, as of wood, bone, or ivory, and used\n   for various purposes, as in removing dust from clothes, laying on\n   colors, etc. Brushes have different shapes and names according to their\n   use; as, clothes brush, paint brush, tooth brush, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'The bushy tail of a fox.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'A tuft of hair on the mandibles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'Branches of trees lopped off; brushwood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'A thicket of shrubs or small trees; the shrubs and small\n   trees in a wood; underbrush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'A bundle of flexible wires or thin plates of metal, used to\n   conduct an electrical current to or from the commutator of a dynamo,\n   electric motor, or similar apparatus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'The act of brushing; as, to give ones clothes a brush; a\n   rubbing or grazing with a quick motion; a light touch; as, we got a\n   brush from the wheel as it passed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'A skirmish; a slight encounter; a shock or collision; as, to\n   have a brush with an enemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'A short contest, or trial, of speed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushed', 'imp. & p. p.', 'of Brush');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushing', 'p. pr. & vb. n.', 'of Brush');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'To apply a brush to, according to its particular use; to\n   rub, smooth, clean, paint, etc., with a brush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'To touch in passing, or to pass lightly over, as with a\n   brush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'n.', 'To remove or gather by brushing, or by an act like that of\n   brushing, or by passing lightly over, as wind; -- commonly with off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush', 'v. i.', 'To move nimbly in haste; to move so lightly as scarcely\n   to be perceived; as, to brush by.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brusher', 'n.', 'One who, or that which, brushes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushiness', 'n.', 'The quality of resembling a brush; brushlike condition;\n   shagginess.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushing', 'a.', 'Constructed or used to brush with; as a brushing machine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushing', 'a.', 'Brisk; light; as, a brushing gallop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushite', 'n.', 'A white or gray crystalline mineral consisting of the\n   acid phosphate of calcium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush turkey', '', 'A large, edible, gregarious bird of Australia\n   (Talegalla Lathami) of the family Megapodidae. Also applied to several\n   allied species of New Guinea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush wheel', '', 'A wheel without teeth, used to turn a similar one by the\n   friction of bristles or something brushlike or soft attached to the\n   circumference.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brush wheel', '', 'A circular revolving brush used by turners, lapidaries,\n   silversmiths, etc., for polishing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushwood', 'n.', 'Brush; a thicket or coppice of small trees and shrubs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushwood', 'n.', 'Small branches of trees cut off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brushy', 'a.', 'Resembling a brush; shaggy; rough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brusk', 'a.', 'Same as Brusque.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brusque', 'a.', 'Rough and prompt in manner; blunt; abrupt; bluff; as, a\n   brusque man; a brusque style.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brusqueness', 'n.', 'Quality of being brusque; roughness joined with\n   promptness; bluntness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brussels', 'n.', 'A city of Belgium, giving its name to a kind of carpet, a\n   kind of lace, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brustled', 'imp. & p. p.', 'of Brustle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brustling', 'p. pr. & vb. n.', 'of Brustle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brustle', 'v. i.', 'To crackle; to rustle, as a silk garment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brustle', 'v. i.', 'To make a show of fierceness or defiance; to bristle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brustle', 'n.', 'A bristle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brut', 'n.', 'To browse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brut', 'n.', 'See Birt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruta', 'n.', 'See Edentata.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutal', 'a.', 'Of or pertaining to a brute; as, brutal nature.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutal', 'a.', 'Like a brute; savage; cruel; inhuman; brutish; unfeeling;\n   merciless; gross; as, brutal manners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalism', 'n.', 'Brutish quality; brutality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalities', 'pl. ', 'of Brutality');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutality', 'n.', 'The quality of being brutal; inhumanity; savageness;\n   pitilessness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutality', 'n.', 'An inhuman act.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalization', 'n.', 'The act or process of making brutal; state of being\n   brutalized.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalized', 'imp. & p. p.', 'of Brutalize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalizing', 'p. pr. & vb. n.', 'of Brutalize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalize', 'v. t.', 'To make brutal; beasty; unfeeling; or inhuman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutalize', 'v. i.', 'To become brutal, inhuman, barbarous, or coarse and\n   beasty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutally', 'adv.', 'In a brutal manner; cruelly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'a.', 'Not having sensation; senseless; inanimate; unconscious;\n   without intelligence or volition; as, the brute earth; the brute powers\n   of nature.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'a.', 'Not possessing reason, irrational; unthinking; as, a brute\n   beast; the brute creation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'a.', 'Of, pertaining to, or characteristic of, a brute beast.\n   Hence: Brutal; cruel; fierce; ferocious; savage; pitiless; as, brute\n   violence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'a.', 'Having the physical powers predominating over the mental;\n   coarse; unpolished; unintelligent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'a.', 'Rough; uncivilized; unfeeling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'n.', 'An animal destitute of human reason; any animal not human;\n   esp. a quadruped; a beast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'n.', 'A brutal person; a savage in heart or manners; as unfeeling\n   or coarse person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brute', 'v. t.', 'To report; to bruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutely', 'adv.', 'In a rude or violent manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruteness', 'n.', 'Brutality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruteness', 'n.', 'Insensibility.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutified', 'imp. & p. p.', 'of Brutify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutifying', 'p. pr. & vb. n.', 'of Brutify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutify', 'v. t.', 'To make like a brute; to make senseless, stupid, or\n   unfeeling; to brutalize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutish', 'a.', 'Pertaining to, or resembling, a brute or brutes; of a\n   cruel, gross, and stupid nature; coarse; unfeeling; unintelligent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Brutism', 'n.', 'The nature or characteristic qualities or actions of a\n   brute; extreme stupidity, or beastly vulgarity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bruting', 'n.', 'Browsing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryological', 'a.', 'Relating to bryology; as, bryological studies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryologist', 'n.', 'One versed in bryology.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryology', 'n.', 'That part of botany which relates to mosses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryonin', 'n.', 'A bitter principle obtained from the root of the bryony\n   (Bryonia alba and B. dioica). It is a white, or slightly colored,\n   substance, and is emetic and cathartic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryony', 'n.', 'The common name of several cucurbitaceous plants of the\n   genus Bryonia. The root of B. alba (rough or white bryony) and of B.\n   dioica is a strong, irritating cathartic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryophyta', 'n. pl.', 'See Cryptogamia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryozoa', 'n. pl.', 'A class of Molluscoidea, including minute animals\n   which by budding form compound colonies; -- called also Polyzoa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryozoan', 'a.', 'Of or pertaining to the Bryozoa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryozoan', 'n.', 'One of the Bryozoa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bryozoum', 'n.', 'An individual zooid of a bryozoan coralline, of which\n   there may be two or more kinds in a single colony. The zooecia usually\n   have a wreath of tentacles around the mouth, and a well developed\n   stomach and intestinal canal; but these parts are lacking in the other\n   zooids (Avicularia, Ooecia, etc.).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buansuah', 'n.', 'The wild dog of northern India (Cuon primaevus); supposed\n   by some to be an ancestral species of the domestic dog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buat', 'n.', 'A lantern; also, the moon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bub', 'n.', 'Strong malt liquor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bub', 'n.', 'A young brother; a little boy; -- a familiar term of address\n   of a small boy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bub', 'v. t.', 'To throw out in bubbles; to bubble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubale', 'n.', 'A large antelope (Alcelaphus bubalis) of Egypt and the\n   Desert of Sahara, supposed by some to be the fallow deer of the Bible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubaline', 'a.', 'Resembling a buffalo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'A thin film of liquid inflated with air or gas; as, a soap\n   bubble; bubbles on the surface of a river.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'A small quantity of air or gas within a liquid body; as,\n   bubbles rising in champagne or aerated waters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'A globule of air, or globular vacuum, in a transparent\n   solid; as, bubbles in window glass, or in a lens.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'A small, hollow, floating bead or globe, formerly used for\n   testing the strength of spirits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'The globule of air in the spirit tube of a level.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'Anything that wants firmness or solidity; that which is\n   more specious than real; a false show; a cheat or fraud; a delusive\n   scheme; an empty project; a dishonest speculation; as, the South Sea\n   bubble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'A person deceived by an empty project; a gull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbled', 'imp. & p. p.', 'of Bubble');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbling', 'p. pr. & vb. n.', 'of Bubble');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'To rise in bubbles, as liquids when boiling or agitated; to\n   contain bubbles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'To run with a gurgling noise, as if forming bubbles; as, a\n   bubbling stream.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble', 'n.', 'To sing with a gurgling or warbling sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbler', 'v. t.', 'To cheat; to deceive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbler', 'n.', 'One who cheats.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbler', 'n.', 'A fish of the Ohio river; -- so called from the noise it\n   makes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubble shell', '', 'A marine univalve shell of the genus Bulla and allied\n   genera, belonging to the Tectibranchiata.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbling Jock', '', 'The male wild turkey, the gobbler; -- so called in\n   allusion to its notes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubbly', 'a.', 'Abounding in bubbles; bubbling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubby', 'n.', 'A womans breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubby', 'n.', 'Bub; -- a term of familiar or affectionate address to a\n   small boy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buboes', 'pl. ', 'of Bubo');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubo', 'n.', 'An inflammation, with enlargement, of a lymphatic gland, esp.\n   in the groin, as in syphilis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubonic', 'a.', 'Of or pertaining to a bubo or buboes; characterized by\n   buboes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubonocele', 'n.', 'An inguinal hernia; esp. that incomplete variety in\n   which the hernial pouch descends only as far as the groin, forming a\n   swelling there like a bubo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bubukle', 'n.', 'A red pimple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccal', 'a.', 'Of or pertaining to the mouth or cheeks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccaneer', 'n.', 'A robber upon the sea; a pirate; -- a term applied\n   especially to the piratical adventurers who made depredations on the\n   Spaniards in America in the 17th and 18th centuries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccaneer', 'v. i.', 'To act the part of a buccaneer; to live as a\n   piratical adventurer or sea robber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccaneerish', 'a.', 'Like a buccaneer; piratical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccinal', 'a.', 'Shaped or sounding like a trumpet; trumpetlike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccinator', 'n.', 'A muscle of the cheek; -- so called from its use in\n   blowing wind instruments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccinoid', 'a.', 'Resembling the genus Buccinum, or pertaining to the\n   Buccinidae, a family of marine univalve shells. See Whelk, and\n   Prosobranchiata.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buccinum', 'n.', 'A genus of large univalve mollusks abundant in the arctic\n   seas. It includes the common whelk (B. undatum).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucentaur', 'n.', 'A fabulous monster, half ox, half man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucentaur', 'n.', 'The state barge of Venice, used by the doge in the\n   ceremony of espousing the Adriatic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buceros', 'n.', 'A genus of large perching birds; the hornbills.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucholzite', 'n.', 'Same as Fibrolite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buchu', 'n.', 'A South African shrub (Barosma) with small leaves that are\n   dotted with oil glands; also, the leaves themselves, which are used in\n   medicine for diseases of the urinary organs, etc. Several species\n   furnish the leaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'Lye or suds in which cloth is soaked in the operation of\n   bleaching, or in which clothes are washed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'The cloth or clothes soaked or washed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucked', 'imp. & p. p.', 'of Buck');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucking', 'p. pr. & vb. n.', 'of Buck');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. t.', 'To soak, steep, or boil, in lye or suds; -- a process in\n   bleaching.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. t.', 'To wash (clothes) in lye or suds, or, in later usage, by\n   beating them on stones in running water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. t.', 'To break up or pulverize, as ores.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'The male of deer, especially fallow deer and antelopes, or of\n   goats, sheep, hares, and rabbits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'A gay, dashing young fellow; a fop; a dandy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'A male Indian or negro.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. i.', 'To copulate, as bucks and does.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. i.', 'To spring with quick plunging leaps, descending with the\n   fore legs rigid and the head held as low down as possible; -- said of a\n   vicious horse or mule.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. t.', 'To subject to a mode of punishment which consists in tying\n   the wrists together, passing the arms over the bent knees, and putting\n   a stick across the arms and in the angle formed by the knees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'v. t.', 'To throw by bucking. See Buck, v. i., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'A frame on which firewood is sawed; a sawhorse; a sawbuck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck', 'n.', 'The beech tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck-basket', 'n.', 'A basket in which clothes are carried to the wash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck bean', '', 'A plant (Menyanthes trifoliata) which grows in moist and\n   boggy places, having racemes of white or reddish flowers and intensely\n   bitter leaves, sometimes used in medicine; marsh trefoil; -- called\n   also bog bean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckboard', 'n.', 'A four-wheeled vehicle, having a long elastic board or\n   frame resting on the bolsters or axletrees, and a seat or seats placed\n   transversely upon it; -- called also buck wagon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucker', 'n.', 'One who bucks ore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucker', 'n.', 'A broad-headed hammer used in bucking ore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucker', 'n.', 'A horse or mule that bucks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucket', 'n.', 'A vessel for drawing up water from a well, or for catching,\n   holding, or carrying water, sap, or other liquids.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucket', 'n.', 'A vessel (as a tub or scoop) for hoisting and conveying\n   coal, ore, grain, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucket', 'n.', 'One of the receptacles on the rim of a water wheel into\n   which the water rushes, causing the wheel to revolve; also, a float of\n   a paddle wheel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucket', 'n.', 'The valved piston of a lifting pump.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucket shop', '', 'An office or a place where facilities are given for\n   betting small sums on current prices of stocks, petroleum, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckety', 'n.', 'Paste used by weavers to dress their webs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckeye', 'n.', 'A name given to several American trees and shrubs of the\n   same genus (Aesculus) as the horse chestnut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckeye', 'n.', 'A cant name for a native in Ohio.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buck-eyed', 'a.', 'Having bad or speckled eyes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckhound', 'n.', 'A hound for hunting deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckie', 'n.', 'A large spiral marine shell, esp. the common whelk. See\n   Buccinum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucking', 'n.', 'The act or process of soaking or boiling cloth in an\n   alkaline liquid in the operation of bleaching; also, the liquid used.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucking', 'n.', 'A washing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucking', 'n.', 'The process of breaking up or pulverizing ores.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckish', 'a.', 'Dandified; foppish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'A device, usually of metal, consisting of a frame with one\n   more movable tongues or catches, used for fastening things together, as\n   parts of dress or harness, by means of a strap passing through the\n   frame and pierced by the tongue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'A distortion bulge, bend, or kink, as in a saw blade or a\n   plate of sheet metal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'A curl of hair, esp. a kind of crisp curl formerly worn;\n   also, the state of being curled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'A contorted expression, as of the face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckled', 'imp. & p. p.', 'of Buckle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckling', 'p. pr. & vb. n.', 'of Buckle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'To fasten or confine with a buckle or buckles; as, to\n   buckle a harness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'To bend; to cause to kink, or to become distorted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'To prepare for action; to apply with vigor and earnestness;\n   -- generally used reflexively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'n.', 'To join in marriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'v. i.', 'To bend permanently; to become distorted; to bow; to\n   curl; to kink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'v. i.', 'To bend out of a true vertical plane, as a wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'v. i.', 'To yield; to give way; to cease opposing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckle', 'v. i.', 'To enter upon some labor or contest; to join in close\n   fight; to struggle; to contend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckler', 'n.', 'A kind of shield, of various shapes and sizes, worn on one\n   of the arms (usually the left) for protecting the front of the body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckler', 'n.', 'One of the large, bony, external plates found on many\n   ganoid fishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckler', 'n.', 'The anterior segment of the shell of trilobites.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckler', 'n.', 'A block of wood or plate of iron made to fit a hawse hole,\n   or the circular opening in a half-port, to prevent water from entering\n   when the vessel pitches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckler', 'v. t.', 'To shield; to defend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckler-headed', 'a.', 'Having a head like a buckler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckling', 'a.', 'Wavy; curling, as hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckra', 'n.', 'A white man; -- a term used by negroes of the African\n   coast, West Indies, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckra', 'a.', 'White; white mans; strong; good; as, buckra yam, a white\n   yam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckram', 'n.', 'A coarse cloth of linen or hemp, stiffened with size or\n   glue, used in garments to keep them in the form intended, and for\n   wrappers to cover merchandise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckram', 'n.', 'A plant. See Ramson.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckram', 'a.', 'Made of buckram; as, a buckram suit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckram', 'a.', 'Stiff; precise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckram', 'v. t.', 'To strengthen with buckram; to make stiff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucks-horn', 'n.', 'A plant with leaves branched somewhat like a bucks\n   horn (Plantago Coronopus); also, Lobelia coronopifolia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckshot', 'n.', 'A coarse leaden shot, larger than swan shot, used in\n   hunting deer and large game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckskin', 'n.', 'The skin of a buck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckskin', 'n.', 'A soft strong leather, usually yellowish or grayish in\n   color, made of deerskin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckskin', 'n.', 'A person clothed in buckskin, particularly an American\n   soldier of the Revolutionary war.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckskin', 'n.', 'Breeches made of buckskin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckstall', 'n.', 'A toil or net to take deer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckthorn', 'n.', 'A genus (Rhamnus) of shrubs or trees. The shorter\n   branches of some species terminate in long spines or thorns. See\n   Rhamnus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucktooth', 'n.', 'Any tooth that juts out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckwheat', 'n.', 'A plant (Fagopyrum esculentum) of the Polygonum family,\n   the seed of which is used for food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buckwheat', 'n.', 'The triangular seed used, when ground, for griddle\n   cakes, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucolic', 'a.', 'Of or pertaining to the life and occupation of a shepherd;\n   pastoral; rustic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucolic', 'n.', 'A pastoral poem, representing rural affairs, and the life,\n   manners, and occupation of shepherds; as, the Bucolics of Theocritus\n   and Virgil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucolical', 'a.', 'Bucolic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucrania', 'pl. ', 'of Bucranium');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bucranium', 'n.', 'A sculptured ornament, representing an ox skull adorned\n   with wreaths, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bud', 'n.', 'A small protuberance on the stem or branches of a plant,\n   containing the rudiments of future leaves, flowers, or stems; an\n   undeveloped branch or flower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bud', 'n.', 'A small protuberance on certain low forms of animals and\n   vegetables which develops into a new organism, either free or attached.\n   See Hydra.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budded', 'imp. & p. p.', 'of Bud');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budding', 'p. pr. & vb. n.', 'of Bud');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bud', 'v. i.', 'To put forth or produce buds, as a plant; to grow, as a bud\n   does, into a flower or shoot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bud', 'v. i.', 'To begin to grow, or to issue from a stock in the manner of\n   a bud, as a horn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bud', 'v. i.', 'To be like a bud in respect to youth and freshness, or\n   growth and promise; as, a budding virgin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bud', 'v. t.', 'To graft, as a plant with another or into another, by\n   inserting a bud from the one into an opening in the bark of the other,\n   in order to raise, upon the budded stock, fruit different from that\n   which it would naturally bear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddha', 'n.', 'The title of an incarnation of self-abnegation, virtue, and\n   wisdom, or a deified religious teacher of the Buddhists, esp. Gautama\n   Siddartha or Sakya Sinha (or Muni); the founder of Buddhism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddhism', 'n.', 'The religion based upon the doctrine originally taught by\n   the Hindoo sage Gautama Siddartha, surnamed Buddha, \"the awakened or\n   enlightened,\" in the sixth century b. c., and adopted as a religion by\n   the greater part of the inhabitants of Central and Eastern Asia and the\n   Indian Islands. Buddhas teaching is believed to have been atheistic;\n   yet it was characterized by elevated humanity and morality. It presents\n   release from existence (a beatific enfranchisement, Nirvana) as the\n   greatest good. Buddhists believe in transmigration of souls through all\n   phases and forms of life. Their number was estimated in 1881 at\n   470,000,000.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddhist', 'n.', 'One who accepts the teachings of Buddhism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddhist', 'a.', 'Of or pertaining to Buddha, Buddhism, or the Buddhists.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddhistic', 'a.', 'Same as Buddhist, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budding', 'n.', 'The act or process of producing buds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budding', 'n.', 'A process of asexual reproduction, in which a new organism\n   or cell is formed by a protrusion of a portion of the animal or\n   vegetable organism, the bud thus formed sometimes remaining attached to\n   the parent stalk or cell, at other times becoming free; gemmation. See\n   Hydroidea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budding', 'n.', 'The act or process of ingrafting one kind of plant upon\n   another stock by inserting a bud under the bark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddle', 'n.', 'An apparatus, especially an inclined trough or vat, in\n   which stamped ore is concentrated by subjecting it to the action of\n   running water so as to wash out the lighter and less valuable portions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buddle', 'v. i.', 'To wash ore in a buddle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bude burner', '', 'A burner consisting of two or more concentric Argand\n   burners (the inner rising above the outer) and a central tube by which\n   oxygen gas or common air is supplied.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bude light', '', 'A light in which high illuminating power is obtained by\n   introducing a jet of oxygen gas or of common air into the center of a\n   flame fed with coal gas or with oil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budged', 'imp. & p. p.', 'of Budge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budging', 'p. pr. & vb. n.', 'of Budge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budge', 'v. i.', 'To move off; to stir; to walk away.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budge', 'v.', 'Brisk; stirring; jocund.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budge', 'n.', 'A kind of fur prepared from lambskin dressed with the wool\n   on; -- used formerly as an edging and ornament, esp. of scholastic\n   habits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budge', 'a.', 'Lined with budge; hence, scholastic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budge', 'a.', 'Austere or stiff, like scholastics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budgeness', 'n.', 'Sternness; severity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budger', 'n.', 'One who budges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'budgerow', 'n.', 'A large and commodious, but generally cumbrous and\n   sluggish boat, used for journeys on the Ganges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budget', 'n.', 'A bag or sack with its contents; hence, a stock or store;\n   an accumulation; as, a budget of inventions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budget', 'n.', 'The annual financial statement which the British chancellor\n   of the exchequer makes in the House of Commons. It comprehends a\n   general view of the finances of the country, with the proposed plan of\n   taxation for the ensuing year. The term is sometimes applied to a\n   similar statement in other countries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budgy', 'n.', 'Consisting of fur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Budlet', 'n.', 'A little bud springing from a parent bud.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'n.', 'A sort of leather, prepared from the skin of the buffalo,\n   dressed with oil, like chamois; also, the skins of oxen, elks, and\n   other animals, dressed in like manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'n.', 'The color of buff; a light yellow, shading toward pink, gray,\n   or brown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'n.', 'A military coat, made of buff leather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'n.', 'The grayish viscid substance constituting the buffy coat. See\n   Buffy coat, under Buffy, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'a.', 'A wheel covered with buff leather, and used in polishing\n   cutlery, spoons, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'a.', 'The bare skin; as, to strip to the buff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'a.', 'Made of buff leather.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'a.', 'Of the color of buff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'v. t.', 'To polish with a buff. See Buff, n., 5.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'v. t.', 'To strike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'n.', 'A buffet; a blow; -- obsolete except in the phrase\n   \"Blindmans buff.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buff', 'a.', 'Firm; sturdy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffa', 'n. fem.', 'The comic actress in an opera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffa', 'a.', 'Comic, farcical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffaloes', 'pl. ', 'of Buffalo');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffalo', 'n.', 'A species of the genus Bos or Bubalus (B. bubalus);\n   originally from India, but now found in most of the warmer countries of\n   the eastern continent. It is larger and less docile than the common ox,\n   and is fond of marshy places and rivers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffalo', 'n.', 'A very large and savage species of the same genus (B.\n   Caffer) found in South Africa; -- called also Cape buffalo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffalo', 'n.', 'Any species of wild ox.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffalo', 'n.', 'The bison of North America.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffalo', 'n.', 'A buffalo robe. See Buffalo robe, below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffalo', 'n.', 'The buffalo fish. See Buffalo fish, below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffel duck', '', 'A small duck (Charitonetta albeola); the spirit duck, or\n   butterball. The head of the male is covered with numerous elongated\n   feathers, and thus appears large. Called also bufflehead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffer', 'n.', 'An elastic apparatus or fender, for deadening the jar\n   caused by the collision of bodies; as, a buffer at the end of a\n   railroad car.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffer', 'n.', 'A pad or cushion forming the end of a fender, which\n   receives the blow; -- sometimes called buffing apparatus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffer', 'n.', 'One who polishes with a buff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffer', 'n.', 'A wheel for buffing; a buff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffer', 'n.', 'A good-humored, slow-witted fellow; -- usually said of an\n   elderly man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bufferhead', 'n.', 'The head of a buffer, which recieves the concussion, in\n   railroad carriages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'n.', 'A cupboard or set of shelves, either movable or fixed at\n   one side of a room, for the display of plate, china, etc., a sideboard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'n.', 'A counter for refreshments; a restaurant at a railroad\n   station, or place of public gathering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. i.', 'A blow with the hand; a slap on the face; a cuff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. i.', 'A blow from any source, or that which affects like a\n   blow, as the violence of winds or waves; a stroke; an adverse action;\n   an affliction; a trial; adversity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. i.', 'A small stool; a stool for a buffet or counter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffeted', 'imp. & p. p.', 'of Buffet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffeting', 'p. pr. & vb. n.', 'of Buffet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. t.', 'To strike with the hand or fist; to box; to beat; to\n   cuff; to slap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. t.', 'To affect as with blows; to strike repeatedly; to strive\n   with or contend against; as, to buffet the billows.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. t.', 'To deaden the sound of (bells) by muffling the clapper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. i.', 'To exercise or play at boxing; to strike; to smite; to\n   strive; to contend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffet', 'v. i.', 'To make ones way by blows or struggling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffeter', 'n.', 'One who buffets; a boxer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffeting', 'n.', 'A striking with the hand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffeting', 'n.', 'A succession of blows; continued violence, as of winds\n   or waves; afflictions; adversity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffin', 'n.', 'A sort of coarse stuff; as, buffin gowns.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffing apparatus', '', 'See Buffer, 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffle', 'n.', 'The buffalo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffle', 'v. i.', 'To puzzle; to be at a loss.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bufflehead', 'n.', 'One who has a large head; a heavy, stupid fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bufflehead', 'n.', 'The buffel duck. See Buffel duck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffle-headed', 'a.', 'Having a large head, like a buffalo; dull; stupid;\n   blundering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffo', 'n.masc.', 'The comic actor in an opera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoon', 'n.', 'A man who makes a practice of amusing others by low\n   tricks, antic gestures, etc.; a droll; a mimic; a harlequin; a clown; a\n   merry-andrew.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoon', 'a.', 'Characteristic of, or like, a buffoon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoon', 'v. i.', 'To act the part of a buffoon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoon', 'v. t.', 'To treat with buffoonery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffooneries', 'pl. ', 'of Buffoonery');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoonery', 'n.', 'The arts and practices of a buffoon, as low jests,\n   ridiculous pranks, vulgar tricks and postures.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoonish', 'a.', 'Like a buffoon; consisting in low jests or gestures.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoonism', 'n.', 'The practices of a buffoon; buffoonery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffoonly', 'a.', 'Low; vulgar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buffy', 'a.', 'Resembling, or characterized by, buff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bufo', 'n.', 'A genus of Amphibia including various species of toads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bufonite', 'n.', 'An old name for a fossil consisting of the petrified\n   teeth and palatal bones of fishes belonging to the family of Pycnodonts\n   (thick teeth); whose remains occur in the oolite and chalk formations;\n   toadstone; -- so named from a notion that it was originally formed in\n   the head of a toad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bug', 'n.', 'A bugbear; anything which terrifies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bug', 'n.', 'A general name applied to various insects belonging to the\n   Hemiptera; as, the squash bug; the chinch bug, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bug', 'n.', 'An insect of the genus Cimex, especially the bedbug (C.\n   lectularius). See Bedbug.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bug', 'n.', 'One of various species of Coleoptera; as, the ladybug; potato\n   bug, etc.; loosely, any beetle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bug', 'n.', 'One of certain kinds of Crustacea; as, the sow bug; pill bug;\n   bait bug; salve bug, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugaboo', 'n.', 'Alt. of Bugbear');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugbear', 'n.', 'Something frightful, as a specter; anything imaginary that\n   causes needless fright; something used to excite needless fear; also,\n   something really dangerous, used to frighten children, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugbane', 'n.', 'A perennial white-flowered herb of the order Ranunculaceae\n   and genus Cimiciguga; bugwort. There are several species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugbear', 'n.', 'Same as Bugaboo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugbear', 'a.', 'Causing needless fright.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugbear', 'v. t.', 'To alarm with idle phantoms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugfish', 'n.', 'The menhaden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugger', 'n.', 'One guilty of buggery or unnatural vice; a sodomite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugger', 'n.', 'A wretch; -- sometimes used humorously or in playful\n   disparagement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buggery', 'n.', 'Unnatural sexual intercourse; sodomy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugginess', 'a.', 'The state of being infested with bugs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buggy', 'a.', 'Infested or abounding with bugs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buggies', 'pl. ', 'of Buggy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buggy', 'n.', 'A light one horse two-wheeled vehicle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buggy', 'n.', 'A light, four-wheeled vehicle, usually with one seat, and\n   with or without a calash top.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle', 'n.', 'A sort of wild ox; a buffalo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle', 'n.', 'A horn used by hunters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle', 'n.', 'A copper instrument of the horn quality of tone, shorter and\n   more conical that the trumpet, sometimes keyed; formerly much used in\n   military bands, very rarely in the orchestra; now superseded by the\n   cornet; -- called also the Kent bugle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle', 'n.', 'An elongated glass bead, of various colors, though commonly\n   black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle', 'a.', 'Jet black.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle', 'n.', 'A plant of the genus Ajuga of the Mint family, a native of\n   the Old World.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugled', 'a.', 'Ornamented with bugles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle horn', '', 'A bugle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugle horn', '', 'A drinking vessel made of horn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugler', 'n.', 'One who plays on a bugle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugleweed', 'n.', 'A plant of the Mint family and genus Lycopus; esp. L.\n   Virginicus, which has mild narcotic and astringent properties, and is\n   sometimes used as a remedy for hemorrhage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buglosses', 'pl. ', 'of Bugloss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugloss', 'n.', 'A plant of the genus Anchusa, and especially the A.\n   officinalis, sometimes called alkanet; oxtongue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bugwort', 'n.', 'Bugbane.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buhl', 'n.', 'Alt. of Buhlwork');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buhlwork', 'n.', 'Decorative woodwork in which tortoise shell, yellow\n   metal, white metal, etc., are inlaid, forming scrolls, cartouches, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buhlbuhl', 'n.', 'See Bulbul.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buhrstone', 'n.', 'A cellular, flinty rock, used for mill stones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Built', 'imp. & p. p.', 'of Build');
  INSERT INTO words (word, word_type, description)VALUES  (    'Building', 'p. pr. & vb. n.', 'of Build');
  INSERT INTO words (word, word_type, description)VALUES  (    'Builded', 'imp. & p. p.', 'of Build');
  INSERT INTO words (word, word_type, description)VALUES  (    'Build', 'v. t.', 'To erect or construct, as an edifice or fabric of any\n   kind; to form by uniting materials into a regular structure; to\n   fabricate; to make; to raise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Build', 'v. t.', 'To raise or place on a foundation; to form, establish, or\n   produce by using appropriate means.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Build', 'v. t.', 'To increase and strengthen; to increase the power and\n   stability of; to settle, or establish, and preserve; -- frequently with\n   up; as, to build up ones constitution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Build', 'v. i.', 'To exercise the art, or practice the business, of\n   building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Build', 'v. i.', 'To rest or depend, as on a foundation; to ground ones\n   self or ones hopes or opinions upon something deemed reliable; to\n   rely; as, to build on the opinions or advice of others.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Build', 'n.', 'Form or mode of construction; general figure; make; as, the\n   build of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Builder', 'n.', 'One who builds; one whose occupation is to build, as a\n   carpenter, a shipwright, or a mason.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Building', 'n.', 'The act of constructing, erecting, or establishing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Building', 'n.', 'The art of constructing edifices, or the practice of\n   civil architecture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Building', 'n.', 'That which is built; a fabric or edifice constructed, as\n   a house, a church, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Built', 'n.', 'Shape; build; form of structure; as, the built of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Built', 'a.', 'Formed; shaped; constructed; made; -- often used in\n   composition and preceded by the word denoting the form; as,\n   frigate-built, clipper-built, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buke muslin', '', 'See Book muslin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bukshish', 'n.', 'See Backsheesh.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulau', 'n.', 'An East Indian insectivorous mammal (Gymnura Rafflesii);\n   somewhat like a rat in appearance, but allied to the hedgehog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulb', 'n.', 'A spheroidal body growing from a plant either above or below\n   the ground (usually below); which is strictly a bud, consisting of a\n   cluster of partially developed leaves, and producing, as it grows, a\n   stem above, and roots below, as in the onion, tulip, etc. It differs\n   from a corm in not being solid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulb', 'n.', 'A name given to some parts that resemble in shape certain\n   bulbous roots; as, the bulb of the aorta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulb', 'n.', 'An expansion or protuberance on a stem or tube, as the bulb\n   of a thermometer, which may be of any form, as spherical, cylindrical,\n   curved, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulb', 'v. i.', 'To take the shape of a bulb; to swell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbaceous', 'n.', 'Bulbous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbar', 'a.', 'Of or pertaining to bulb; especially, in medicine,\n   pertaining to the bulb of the spinal cord, or medulla oblongata; as,\n   bulbar paralysis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbed', 'a.', 'Having a bulb; round-headed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbel', 'n.', 'A separable bulb formed on some flowering plants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbiferous', 'n.', 'Producing bulbs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulblet', 'n.', 'A small bulb, either produced on a larger bulb, or on some\n   aerial part of a plant, as in the axils of leaves in the tiger lily, or\n   replacing the flowers in some kinds of onion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbose', 'a.', 'Bulbous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbo-tuber', 'n.', 'A corm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbous', 'n.', 'Having or containing bulbs, or a bulb; growing from bulbs;\n   bulblike in shape or structure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbul', 'n.', 'The Persian nightingale (Pycnonotus jocosus). The name is\n   also applied to several other Asiatic singing birds, of the family\n   Timaliidae. The green bulbuls belong to the Chloropsis and allied\n   genera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulbule', 'n.', 'A small bulb; a bulblet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulchin', 'n.', 'A little bull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulge', 'n.', 'The bilge or protuberant part of a cask.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulge', 'n.', 'A swelling, protuberant part; a bending outward, esp. when\n   caused by pressure; as, a bulge in a wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulge', 'n.', 'The bilge of a vessel. See Bilge, 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulged', 'imp. & p. p.', 'of Bulge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulging', 'p. pr. & vb. n.', 'of Bulge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulge', 'v. i.', 'To swell or jut out; to bend outward, as a wall when it\n   yields to pressure; to be protuberant; as, the wall bulges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulge', 'v. i.', 'To bilge, as a ship; to founder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulgy', 'a.', 'Bulged; bulging; bending, or tending to bend, outward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulimia', 'n.', 'Alt. of Bulimy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulimy', 'n.', 'A disease in which there is a perpetual and insatiable\n   appetite for food; a diseased and voracious appetite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulimus', 'n.', 'A genus of land snails having an elongated spiral shell,\n   often of large size. The species are numerous and abundant in tropical\n   America.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulk', 'n.', 'Magnitude of material substance; dimensions; mass; size; as,\n   an ox or ship of great bulk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulk', 'n.', 'The main mass or body; the largest or principal portion; the\n   majority; as, the bulk of a debt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulk', 'n.', 'The cargo of a vessel when stowed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulk', 'n.', 'The body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulked', 'imp. & p. p.', 'of Bulk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulking', 'p. pr. & vb. n.', 'of Bulk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulk', 'v. i.', 'To appear or seem to be, as to bulk or extent; to swell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulk', 'v.', 'A projecting part of a building.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulker', 'n.', 'A person employed to ascertain the bulk or size of goods,\n   in order to fix the amount of freight or dues payable on them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulkhead', 'n.', 'A partition in a vessel, to separate apartments on the\n   same deck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulkhead', 'n.', 'A structure of wood or stone, to resist the pressure of\n   earth or water; a partition wall or structure, as in a mine; the\n   limiting wall along a water front.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulkiness', 'n.', 'Greatness in bulk; size.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulky', 'a.', 'Of great bulk or dimensions; of great size; large; thick;\n   massive; as, bulky volumes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'n.', 'The male of any species of cattle (Bovidae); hence, the male\n   of any large quadruped, as the elephant; also, the male of the whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'n.', 'One who, or that which, resembles a bull in character or\n   action.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'n.', 'Taurus, the second of the twelve signs of the zodiac.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'n.', 'A constellation of the zodiac between Aries and Gemini. It\n   contains the Pleiades.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'n.', 'One who operates in expectation of a rise in the price of\n   stocks, or in order to effect such a rise. See 4th Bear, n., 5.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'a.', 'Of or pertaining to a bull; resembling a bull; male; large;\n   fierce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'v. i.', 'To be in heat; to manifest sexual desire as cows do.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'v. t.', 'To endeavor to raise the market price of; as, to bull\n   railroad bonds; to bull stocks; to bull Lake Shore; to endeavor to\n   raise prices in; as, to bull the market. See 1st Bull, n., 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'v. i.', 'A seal. See Bulla.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'v. i.', 'A letter, edict, or respect, of the pope, written in\n   Gothic characters on rough parchment, sealed with a bulla, and dated \"a\n   die Incarnationis,\" i. e., \"from the day of the Incarnation.\" See\n   Apostolical brief, under Brief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull', 'v. i.', 'A grotesque blunder in language; an apparent congruity,\n   but real incongruity, of ideas, contained in a form of expression; so\n   called, perhaps, from the apparent incongruity between the dictatorial\n   nature of the popes bulls and his professions of humility.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullae', 'pl. ', 'of Bulla');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulla', 'n.', 'A bleb; a vesicle, or an elevation of the cuticle,\n   containing a transparent watery fluid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulla', 'n.', 'The ovoid prominence below the opening of the ear in the\n   skulls of many animals; as, the tympanic or auditory bulla.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulla', 'n.', 'A leaden seal for a document; esp. the round leaden seal\n   attached to the papal bulls, which has on one side a representation of\n   St. Peter and St. Paul, and on the other the name of the pope who uses\n   it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulla', 'n.', 'A genus of marine shells. See Bubble shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullace', 'n.', 'A small European plum (Prunus communis, var. insitita).\n   See Plum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullace', 'n.', 'The bully tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullantic', 'a.', 'Pertaining to, or used in, papal bulls.');
