  INSERT INTO words (word, word_type, description)VALUES  (    'Bull brier', '', 'A species of Smilax (S. Pseudo-China) growing from New\n   Jersey to the Gulf of Mexico, which has very large tuberous and\n   farinaceous rootstocks, formerly used by the Indians for a sort of\n   bread, and by the negroes as an ingredient in making beer; -- called\n   also bamboo brier and China brier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullcomber', 'n.', 'A scaraboid beetle; esp. the Typhaeus vulgaris of\n   Europe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldog', 'n.', 'A variety of dog, of remarkable ferocity, courage, and\n   tenacity of grip; -- so named, probably, from being formerly employed\n   in baiting bulls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldog', 'n.', 'A refractory material used as a furnace lining, obtained\n   by calcining the cinder or slag from the puddling furnace of a rolling\n   mill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldog', 'a.', 'Characteristic of, or like, a bulldog; stubborn; as,\n   bulldog courage; bulldog tenacity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldozed', 'imp. & p. p.', 'of Bulldoze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldozing', 'p. pr. & vb. n.', 'of Bulldoze');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldoze', 'v. t.', 'To intimidate; to restrain or coerce by intimidation\n   or violence; -- used originally of the intimidation of negro voters, in\n   Louisiana.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulldozer', 'n.', 'One who bulldozes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulled', 'a.', 'Swollen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullen-bullen', 'n.', 'The lyre bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullen-nail', 'n.', 'A nail with a round head and short shank, tinned and\n   lacquered.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullet', 'n.', 'A small ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullet', 'n.', 'A missile, usually of lead, and round or elongated in form,\n   to be discharged from a rifle, musket, pistol, or other small firearm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullet', 'n.', 'A cannon ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullet', 'n.', 'The fetlock of a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullet-proof', 'a.', 'Capable of resisting the force of a bullet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulletin', 'n.', 'A brief statement of facts respecting some passing event,\n   as military operations or the health of some distinguished personage,\n   issued by authority for the information of the public.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulletin', 'n.', 'Any public notice or announcement, especially of news\n   recently received.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulletin', 'n.', 'A periodical publication, especially one containing the\n   proceeding of a society.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfaced', 'a.', 'Having a large face.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfeast', 'n.', 'See Bullfight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfight', 'n.', 'Alt. of Bullfighting');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfighting', 'n.', 'A barbarous sport, of great antiquity, in which men\n   torment, and fight with, a bull or bulls in an arena, for public\n   amusement, -- still popular in Spain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfinch', 'n.', 'A bird of the genus Pyrrhula and other related genera,\n   especially the P. vulgaris / rubicilla, a bird of Europe allied to the\n   grosbeak, having the breast, cheeks, and neck, red.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfist', 'n.', 'Alt. of Bullfice');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfice', 'n.', 'A kind of fungus. See Puffball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull fly', 'n.', 'Alt. of Bullfly');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfly', 'n.', 'Any large fly troublesome to cattle, as the gadflies and\n   breeze flies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullfrog', 'n.', 'A very large species of frog (Rana Catesbiana); found in\n   North America; -- so named from its loud bellowing in spring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'A fresh-water fish of many species, of the genus\n   Uranidea, esp. U. gobio of Europe, and U. Richardsoni of the United\n   States; -- called also millers thumb.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'In America, several species of Amiurus; -- called also\n   catfish, horned pout, and bullpout.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'A marine fish of the genus Cottus; the sculpin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'The black-bellied plover (Squatarola helvetica); --\n   called also beetlehead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'The golden plover.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'A stupid fellow; a lubber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullhead', 'n.', 'A small black water insect.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullheaded', 'a.', 'Having a head like that of a bull. Fig.: Headstrong;\n   obstinate; dogged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullion', 'n.', 'Uncoined gold or silver in the mass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullion', 'n.', 'Base or uncurrent coin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullion', 'n.', 'Showy metallic ornament, as of gold, silver, or copper, on\n   bridles, saddles, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullion', 'n.', 'Heavy twisted fringe, made of fine gold or silver wire and\n   used for epaulets; also, any heavy twisted fringe whose cords are\n   prominent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullionist', 'n.', 'An advocate for a metallic currency, or a paper\n   currency always convertible into gold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullirag', 'n.', 'To intimidate by bullying; to rally contemptuously; to\n   badger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullish', 'a.', 'Partaking of the nature of a bull, or a blunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullist', 'n.', 'A writer or drawer up of papal bulls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullition', 'v. i.', 'The action of boiling; boiling. [Obs.] See\n   Ebullition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull-necked', 'a.', 'Having a short and thick neck like that of a bull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullock', 'n.', 'A young bull, or any male of the ox kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullock', 'n.', 'An ox, steer, or stag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullock', 'v. t.', 'To bully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullocks-eye', 'n.', 'See Bulls-eye, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullon', 'n.', 'A West Indian fish (Scarus Croicensis).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullpout', 'n.', 'See Bullhead, 1 (b).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A small circular or oval wooden block without sheaves,\n   having a groove around it and a hole through it, used for connecting\n   rigging.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A small round cloud, with a ruddy center, supposed by\n   sailors to portend a storm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A small thick disk of glass inserted in a deck, roof,\n   floor, ships side, etc., to let in light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A circular or oval opening for air or light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A lantern, with a thick glass lens on one side for\n   concentrating the light on any object; also, the lens itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'Aldebaran, a bright star in the eye of Taurus or the\n   Bull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'The center of a target.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A thick knob or protuberance left on glass by the end\n   of the pipe through which it was blown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-eye', 'n.', 'A small and thick old-fashioned watch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulls-nose', 'n.', 'An external angle when obtuse or rounded.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull terrier', '', 'A breed of dogs obtained by crossing the bulldog and\n   the terrier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull trout', '', 'In England, a large salmon trout of several species, as\n   Salmo trutta and S. Cambricus, which ascend rivers; -- called also sea\n   trout.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull trout', '', 'Salvelinus malma of California and Oregon; -- called also\n   Dolly Varden trout and red-spotted trout.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bull trout', '', 'The huso or salmon of the Danube.');
