  INSERT INTO words (word, word_type, description)VALUES  (    'Bullwort', 'n.', 'See Bishops-weed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullies', 'pl. ', 'of Bully');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bully', 'n.', 'A noisy, blustering fellow, more insolent than courageous;\n   one who is threatening and quarrelsome; an insolent, tyrannical fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bully', 'n.', 'A brisk, dashing fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bully', 'a.', 'Jovial and blustering; dashing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bully', 'a.', 'Fine; excellent; as, a bully horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullied', 'imp. & p. p.', 'of Bully');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bullying', 'p. pr. & vb. n.', 'of Bully');
