  INSERT INTO words (word, word_type, description)VALUES  (    'Bullyrock', 'n.', 'A bully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bully tree', '', 'The name of several West Indian trees of the order\n   Sapotaceae, as Dipholis nigra and species of Sapota and Mimusops. Most\n   of them yield a substance closely resembling gutta-percha.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulrush', 'n.', 'A kind of large rush, growing in wet land or in water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulse', 'n.', 'A purse or bag in which to carry or measure diamonds, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bultel', 'n.', 'A bolter or bolting cloth; also, bran.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulti', 'n.', 'Same as Bolty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bultow', 'n.', 'A trawl; a boulter; the mode of fishing with a boulter or\n   spiller.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulwark', 'n.', 'A rampart; a fortification; a bastion or outwork.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulwark', 'n.', 'That which secures against an enemy, or defends from\n   attack; any means of defense or protection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulwark', 'n.', 'The sides of a ship above the upper deck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulwarked', 'imp. & p. p.', 'of Bulwark');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulwarking', 'n.', 'of Bulwark');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bulwark', 'v. t.', 'To fortify with, or as with, a rampart or wall; to\n   secure by fortification; to protect.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bum', 'n.', 'The buttock.');
