  INSERT INTO words (word, word_type, description)VALUES  (    'Bumming', 'n.', 'of Bum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bum', 'v. i.,', 'To make murmuring or humming sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bum', 'n.', 'A humming noise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumbailiff', 'n.', 'See Bound bailiff, under Bound, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumbard', '', 'See Bombard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumbarge', 'n.', 'See Bumboat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumbast', '', 'See Bombast.');
