  INSERT INTO words (word, word_type, description)VALUES  (    'Bumblebee', 'n.', 'A large bee of the genus Bombus, sometimes called\n   humblebee; -- so named from its sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumboat', 'n.', 'A clumsy boat, used for conveying provisions, fruit, etc.,\n   for sale, to vessels lying in port or off shore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumkin', 'n.', 'A projecting beam or boom; as: (a) One projecting from each\n   bow of a vessel, to haul the fore tack to, called a tack bumpkin. (b)\n   One from each quarter, for the main-brace blocks, and called brace\n   bumpkin. (c) A small outrigger over the stern of a boat, to extend the\n   mizzen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bummalo', 'n.', 'A small marine Asiatic fish (Saurus ophidon) used in India\n   as a relish; -- called also Bombay duck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bummer', 'n.', 'An idle, worthless fellow, who is without any visible means\n   of support; a dissipated sponger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bummery', 'n.', 'See Bottomery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumped', 'imp. & p. p.', 'of Bump');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumping', 'p. pr. & vb. n.', 'of Bump');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'v. t.', 'To strike, as with or against anything large or solid; to\n   thump; as, to bump the head against a wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'v. i.', 'To come in violent contact with something; to thump.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'n.', 'A thump; a heavy blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'n.', 'A swelling or prominence, resulting from a bump or blow; a\n   protuberance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'n.', 'One of the protuberances on the cranium which are associated\n   with distinct faculties or affections of the mind; as, the bump of\n   \"veneration;\" the bump of \"acquisitiveness.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'n.', 'The act of striking the stern of the boat in advance with the\n   prow of the boat following.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'v. i.', 'To make a loud, heavy, or hollow noise, as the bittern; to\n   boom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bump', 'n.', 'The noise made by the bittern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumper', 'n.', 'A cup or glass filled to the brim, or till the liquor runs\n   over, particularly in drinking a health or toast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumper', 'n.', 'A covered house at a theater, etc., in honor of some\n   favorite performer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumper', 'n.', 'That which bumps or causes a bump.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumper', 'n.', 'Anything which resists or deadens a bump or shock; a\n   buffer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumpkin', 'n.', 'An awkward, heavy country fellow; a clown; a country lout.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumptious', 'a.', 'Self-conceited; forward; pushing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bumptiousness', 'n.', 'Conceitedness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bun', 'n.', 'Alt. of Bunn');
