  INSERT INTO words (word, word_type, description)VALUES  (    'Bunch', 'n.', 'A protuberance; a hunch; a knob or lump; a hump.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunch', 'n.', 'A collection, cluster, or tuft, properly of things of the\n   same kind, growing or fastened together; as, a bunch of grapes; a bunch\n   of keys.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunch', 'n.', 'A small isolated mass of ore, as distinguished from a\n   continuous vein.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunched', 'imp. & p. p.', 'of Bunch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunching', 'p. pr. & vb. n.', 'of Bunch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunch', 'v. i.', 'To swell out into a bunch or protuberance; to be\n   protuberant or round.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunch', 'v. t.', 'To form into a bunch or bunches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunch-backed', 'a.', 'Having a bunch on the back; crooked.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunchberry', 'n.', 'The dwarf cornel (Cornus Canadensis); which bears a\n   dense cluster of bright red, edible berries.');
