  INSERT INTO words (word, word_type, description)VALUES  (    'Bunchiness', 'n.', 'The quality or condition of being bunchy; knobbiness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunchy', 'a.', 'Swelling out in bunches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunchy', 'a.', 'Growing in bunches, or resembling a bunch; having tufts;\n   as, the birds bunchy tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunchy', 'a.', 'Yielding irregularly; sometimes rich, sometimes poor; as, a\n   bunchy mine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buncombe', 'n.', 'Alt. of Bunkum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunkum', 'n.', 'Speech-making for the gratification of constituents, or to\n   gain public applause; flattering talk for a selfish purpose; anything\n   said for mere show.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bund', 'n.', 'League; confederacy; esp. the confederation of German states.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bund', 'n.', 'An embankment against inundation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunder', 'n.', 'A boat or raft used in the East Indies in the landing of\n   passengers and goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bundesrath', 'n.', 'The federal council of the German Empire. In the\n   Bundesrath and the Reichstag are vested the legislative functions. The\n   federal council of Switzerland is also so called.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bundle', 'n.', 'A number of things bound together, as by a cord or\n   envelope, into a mass or package convenient for handling or conveyance;\n   a loose package; a roll; as, a bundle of straw or of paper; a bundle of\n   old clothes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bundled', 'imp. & p. p.', 'of Bundle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bundling', 'p. pr. & vb. n.', 'of Bundle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bundle', 'v. t.', 'To tie or bind in a bundle or roll.');
