  INSERT INTO words (word, word_type, description)VALUES  (    'Bundle', 'v. i.', 'To sleep on the same bed without undressing; -- applied\n   to the custom of a man and woman, especially lovers, thus sleeping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bung', 'n.', 'The large stopper of the orifice in the bilge of a cask.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bung', 'n.', 'The orifice in the bilge of a cask through which it is\n   filled; bunghole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bung', 'n.', 'A sharper or pickpocket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunged', 'imp. & p. p.', 'of Bung');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunging', 'p. pr. & vb. n.', 'of Bung');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bung', 'v. t.', 'To stop, as the orifice in the bilge of a cask, with a\n   bung; to close; -- with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungalow', 'n.', 'A thatched or tiled house or cottage, of a single story,\n   usually surrounded by a veranda.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungarum', 'n.', 'A venomous snake of India, of the genus Bungarus, allied\n   to the cobras, but without a hood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunghole', 'n.', 'See Bung, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungled', 'imp. & p. p.', 'of Bungle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungling', 'p. pr. & vb. n.', 'of Bungle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungle', 'v. i.', 'To act or work in a clumsy, awkward manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungle', 'v. t.', 'To make or mend clumsily; to manage awkwardly; to botch;\n   -- sometimes with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungle', 'n.', 'A clumsy or awkward performance; a botch; a gross blunder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungler', 'n.', 'A clumsy, awkward workman; one who bungles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungling', 'a.', 'Unskillful; awkward; clumsy; as, a bungling workman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunglingly', 'adv.', 'Clumsily; awkwardly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bungo', 'n.', 'A kind of canoe used in Central and South America; also, a\n   kind of boat used in the Southern United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunion', 'n.', 'Same as Bunyon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunk', 'n.', 'A wooden case or box, which serves for a seat in the daytime\n   and for a bed at night.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunk', 'n.', 'One of a series of berths or bed places in tiers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunk', 'n.', 'A piece of wood placed on a lumbermans sled to sustain the\n   end of heavy timbers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunked', 'imp. & p. p.', 'of Bunk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunking', 'p. pr. & vb. n.', 'of Bunk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunk', 'v. i.', 'To go to bed in a bunk; -- sometimes with in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunker', 'n.', 'A sort of chest or box, as in a window, the lid of which\n   serves for a seat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunker', 'n.', 'A large bin or similar receptacle; as, a coal bunker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunko', 'n.', 'A kind of swindling game or scheme, by means of cards or by\n   a sham lottery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunkum', 'n.', 'See Buncombe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunn', 'n.', 'See Bun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunnian', 'n.', 'See Bunyon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunny', 'n.', 'A great collection of ore without any vein coming into it or\n   going out from it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunny', 'n.', 'A pet name for a rabbit or a squirrel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunodonta', 'n. pl.', 'Alt. of Bunodonts');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunodonts', 'n. pl.', 'A division of the herbivorous mammals including the\n   hogs and hippopotami; -- so called because the teeth are tuberculated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunsens battery', '', 'Alt. of Bunsens burner');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunsens burner', '', 'See under Battery, and Burner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunt', 'n.', 'A fungus (Ustilago foetida) which affects the ear of cereals,\n   filling the grains with a fetid dust; -- also called pepperbrand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunt', 'n.', 'The middle part, cavity, or belly of a sail; the part of a\n   furled sail which is at the center of the yard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunt', 'v. i.', 'To swell out; as, the sail bunts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunt', 'v. t. & i.', 'To strike or push with the horns or head; to butt;\n   as, the ram bunted the boy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunter', 'n.', 'A woman who picks up rags in the streets; hence, a low,\n   vulgar woman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunting', 'n.', 'A bird of the genus Emberiza, or of an allied genus,\n   related to the finches and sparrows (family Fringillidae).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunting', 'n.', 'Alt. of Buntine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buntine', 'n.', 'A thin woolen stuff, used chiefly for flags, colors, and\n   ships signals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buntline', 'n.', 'One of the ropes toggled to the footrope of a sail, used\n   to haul up to the yard the body of the sail when taking it in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunyon', 'n.', 'Alt. of Bunion');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bunion', 'n.', 'An enlargement and inflammation of a small membranous sac\n   (one of the bursae muscosae); usually occurring on the first joint of\n   the great toe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoy', 'n.', 'A float; esp. a floating object moored to the bottom, to mark\n   a channel or to point out the position of something beneath the water,\n   as an anchor, shoal, rock, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyed', 'imp. & p. p.', 'of Buoy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoying', 'p. pr. & vb. n.', 'of Buoy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoy', 'v. t.', 'To keep from sinking in a fluid, as in water or air; to\n   keep afloat; -- with up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoy', 'v. t.', 'To support or sustain; to preserve from sinking into ruin\n   or despondency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoy', 'v. t.', 'To fix buoys to; to mark by a buoy or by buoys; as, to\n   buoy an anchor; to buoy or buoy off a channel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoy', 'v. i.', 'To float; to rise like a buoy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyage', 'n.', 'Buoys, taken collectively; a series of buoys, as for the\n   guidance of vessels into or out of port; the providing of buoys.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyance', 'n.', 'Buoyancy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyancies', 'pl. ', 'of Buoyancy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyancy', 'n.', 'The property of floating on the surface of a liquid, or\n   in a fluid, as in the atmosphere; specific lightness, which is\n   inversely as the weight compared with that of an equal volume of water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyancy', 'n.', 'The upward pressure exerted upon a floating body by a\n   fluid, which is equal to the weight of the body; hence, also, the\n   weight of a floating body, as measured by the volume of fluid\n   displaced.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyancy', 'n.', 'Cheerfulness; vivacity; liveliness; sprightliness; -- the\n   opposite of heaviness; as, buoyancy of spirits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyant', 'v. t. & i.', 'Having the quality of rising or floating in a\n   fluid; tending to rise or float; as, iron is buoyant in mercury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyant', 'v. t. & i.', 'Bearing up, as a fluid; sustaining another body by\n   being specifically heavier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buoyant', 'v. t. & i.', 'Light-hearted; vivacious; cheerful; as, a buoyant\n   disposition; buoyant spirits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buprestidan', 'n.', 'One of a tribe of beetles, of the genus Buprestis and\n   allied genera, usually with brilliant metallic colors. The larvae are\n   usually borers in timber, or beneath bark, and are often very\n   destructive to trees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bur', 'n.', 'Alt. of Burr');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'Any rough or prickly envelope of the seeds of plants, whether\n   a pericarp, a persistent calyx, or an involucre, as of the chestnut and\n   burdock. Also, any weed which bears burs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The thin ridge left by a tool in cutting or shaping metal.\n   See Burr, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A ring of iron on a lance or spear. See Burr, n., 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The lobe of the ear. See Burr, n., 5.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The sweetbread.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A clinker; a partially vitrified brick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A small circular saw.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A triangular chisel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A drill with a serrated head larger than the shank; -- used\n   by dentists.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The round knob of an antler next to a deers head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burbolt', 'n.', 'A birdbolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burbot', 'n.', 'A fresh-water fish of the genus Lota, having on the nose\n   two very small barbels, and a larger one on the chin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdelais', 'n.', 'A sort of grape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'That which is borne or carried; a load.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'That which is borne with labor or difficulty; that which is\n   grievous, wearisome, or oppressive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'The capacity of a vessel, or the weight of cargo that she\n   will carry; as, a ship of a hundred tons burden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'The tops or heads of stream-work which lie over the stream\n   of tin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'The proportion of ore and flux to fuel, in the charge of a\n   blast furnace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'A fixed quantity of certain commodities; as, a burden of\n   gad steel, 120 pounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'A birth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdened', 'imp. & p. p.', 'of Burden');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdening', 'p. pr. & vb. n.', 'of Burden');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'v. t.', 'To encumber with weight (literal or figurative); to lay\n   a heavy load upon; to load.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'v. t.', 'To oppress with anything grievous or trying; to\n   overload; as, to burden a nation with taxes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'v. t.', 'To impose, as a load or burden; to lay or place as a\n   burden (something heavy or objectionable).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'The verse repeated in a song, or the return of the theme at\n   the end of each stanza; the chorus; refrain. Hence: That which is often\n   repeated or which is dwelt upon; the main topic; as, the burden of a\n   prayer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'The drone of a bagpipe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burden', 'n.', 'A club.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdener', 'n.', 'One who loads; an oppressor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdenous', 'a.', 'Burdensome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdensome', 'a.', 'Grievous to be borne; causing uneasiness or fatigue;\n   oppressive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdock', 'n.', 'A genus of coarse biennial herbs (Lappa); bearing small\n   burs which adhere tenaciously to clothes, or to the fur or wool of\n   animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burdon', 'n.', 'A pilgrims staff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaus', 'pl. ', 'of Bureau');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaux', 'pl. ', 'of Bureau');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureau', 'n.', 'Originally, a desk or writing table with drawers for\n   papers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureau', 'n.', 'The place where such a bureau is used; an office where\n   business requiring writing is transacted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureau', 'n.', 'Hence: A department of public business requiring a force of\n   clerks; the body of officials in a department who labor under the\n   direction of a chief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureau', 'n.', 'A chest of drawers for clothes, especially when made as an\n   ornamental piece of furniture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaucracy', 'n.', 'A system of carrying on the business of government by\n   means of departments or bureaus, each under the control of a chief, in\n   contradiction to a system in which the officers of government have an\n   associated authority and responsibility; also, government conducted on\n   this system.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaucracy', 'n.', 'Government officials, collectively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaucrat', 'n.', 'An official of a bureau; esp. an official confirmed in\n   a narrow and arbitrary routine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaucratic', 'a.', 'Alt. of Bureaucratical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaucratical', 'a.', 'Of, relating to, or resembling, a bureaucracy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bureaucratist', 'n.', 'An advocate for , or supporter of, bureaucracy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burel', 'n. & a.', 'Same as Borrel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burette', 'n.', 'An apparatus for delivering measured quantities of liquid\n   or for measuring the quantity of liquid or gas received or discharged.\n   It consists essentially of a graduated glass tube, usually furnished\n   with a small aperture and stopcock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bur fish', '', 'A spinose, plectognath fish of the Allantic coast of the\n   United States (esp. Chilo mycterus geometricus) having the power of\n   distending its body with water or air, so as to resemble a chestnut\n   bur; -- called also ball fish, balloon fish, and swellfish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burg', 'n.', 'A fortified town.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burg', 'n.', 'A borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgage', 'n.', 'A tenure by which houses or lands are held of the king or\n   other lord of a borough or city; at a certain yearly rent, or by\n   services relating to trade or handicraft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgall', 'n.', 'A small marine fish; -- also called cunner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgamot', 'n.', 'See Bergamot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burganet', 'n.', 'See Burgonet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgee', 'n.', 'A kind of small coat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgee', 'n.', 'A swallow-tailed flag; a distinguishing pennant, used by\n   cutters, yachts, and merchant vessels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgeois', 'n.', 'See 1st Bourgeois.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgeois', 'n.', 'A burgess; a citizen. See 2d Bourgeois.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgeon', 'v. i.', 'To bud. See Bourgeon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgess', 'n.', 'An inhabitant of a borough or walled town, or one who\n   possesses a tenement therein; a citizen or freeman of a borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgess', 'n.', 'One who represents a borough in Parliament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgess', 'n.', 'A magistrate of a borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgess', 'n.', 'An inhabitant of a Scotch burgh qualified to vote for\n   municipal officers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgess-ship', 'n.', 'The state of privilege of a burgess.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burggrave', 'n.', 'Originally, one appointed to the command of a burg\n   (fortress or castle); but the title afterward became hereditary, with a\n   domain attached.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgh', 'n.', 'A borough or incorporated town, especially, one in Scotland.\n   See Borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghal', 'a.', 'Belonging to a burgh.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghbote', 'n.', 'A contribution toward the building or repairing of\n   castles or walls for the defense of a city or town.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghbrech', 'n.', 'The offense of violating the pledge given by every\n   inhabitant of a tithing to keep the peace; breach of the peace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgher', 'n.', 'A freeman of a burgh or borough, entitled to enjoy the\n   privileges of the place; any inhabitant of a borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgher', 'n.', 'A member of that party, among the Scotch seceders, which\n   asserted the lawfulness of the burgess oath (in which burgesses profess\n   \"the true religion professed within the realm\"); the opposite party\n   being called antiburghers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghermaster', 'n.', 'See Burgomaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghership', 'n.', 'The state or privileges of a burgher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghmaster', 'n.', 'A burgomaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghmaster', 'n.', 'An officer who directs and lays out the meres or\n   boundaries for the workmen; -- called also bailiff, and barmaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burghmote', 'n.', 'A court or meeting of a burgh or borough; a borough\n   court held three times yearly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burglar', 'n.', 'One guilty of the crime of burglary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burglarer', 'n.', 'A burglar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burglarious', 'a.', 'Pertaining to burglary; constituting the crime of\n   burglary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burglariously', 'adv.', 'With an intent to commit burglary; in the manner\n   of a burglar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burglaries', 'pl. ', 'of Burglary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burglary', 'n.', 'Breaking and entering the dwelling house of another, in\n   the nighttime, with intent to commit a felony therein, whether the\n   felonious purpose be accomplished or not.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgomaster', 'n.', 'A chief magistrate of a municipal town in Holland,\n   Flanders, and Germany, corresponding to mayor in England and the United\n   States; a burghmaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgomaster', 'n.', 'An aquatic bird, the glaucous gull (Larus glaucus);\n   common in arctic regions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgonet', 'n.', 'A kind of helmet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgoo', 'n.', 'A kind of oatmeal pudding, or thick gruel, used by seamen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgrass', 'n.', 'Grass of the genus Cenchrus, growing in sand, and having\n   burs for fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgrave', 'n.', 'See Burggrave.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgundy', 'n.', 'An old province of France (in the eastern central part).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burgundy', 'n.', 'A richly flavored wine, mostly red, made in Burgundy,\n   France.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burh', 'n.', 'See Burg.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burhel', 'n.', 'Alt. of Burrhel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrhel', 'n.', 'The wild Himalayan, or blue, sheep (Ovis burrhel).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burial', 'n.', 'A grave; a tomb; a place of sepulture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burial', 'n.', 'The act of burying; depositing a dead body in the earth, in\n   a tomb or vault, or in the water, usually with attendant ceremonies;\n   sepulture; interment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burier', 'n.', 'One who, or that which, buries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burin', 'n.', 'The cutting tool of an engraver on metal, used in line\n   engraving. It is made of tempered steel, one end being ground off\n   obliquely so as to produce a sharp point, and the other end inserted in\n   a handle; a graver; also, the similarly shaped tool used by workers in\n   marble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burin', 'n.', 'The manner or style of execution of an engraver; as, a soft\n   burin; a brilliant burin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burinist', 'n.', 'One who works with the burin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burion', 'n.', 'The red-breasted house sparrow of California (Carpodacus\n   frontalis); -- called also crimson-fronted bullfinch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burked', 'imp. & p. p.', 'of Burke');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burking', 'p. pr. & vb. n.', 'of Burke');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burke', 'v. t.', 'To murder by suffocation, or so as to produce few marks\n   of violence, for the purpose of obtaining a body to be sold for\n   dissection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burke', 'v. t.', 'To dispose of quietly or indirectly; to suppress; to\n   smother; to shelve; as, to burke a parliamentary question.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burkism', 'n.', 'The practice of killing persons for the purpose of selling\n   their bodies for dissection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burled', 'imp. & p. p.', 'of Burl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burling', 'p. pr. & vb. n.', 'of Burl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burl', 'v. t.', 'To dress or finish up (cloth); to pick knots, burs, loose\n   threads, etc., from, as in finishing cloth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burl', 'n.', 'A knot or lump in thread or cloth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burl', 'n.', 'An overgrown knot, or an excrescence, on a tree; also, veneer\n   made from such excrescences.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlap', 'n.', 'A coarse fabric, made of jute or hemp, used for bagging;\n   also, a finer variety of similar material, used for curtains, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burler', 'n.', 'One who burls or dresses cloth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesque', 'a.', 'Tending to excite laughter or contempt by extravagant\n   images, or by a contrast between the subject and the manner of treating\n   it, as when a trifling subject is treated with mock gravity; jocular;\n   ironical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesque', 'n.', 'Ludicrous representation; exaggerated parody; grotesque\n   satire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesque', 'n.', 'An ironical or satirical composition intended to excite\n   laughter, or to ridicule anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesque', 'n.', 'A ludicrous imitation; a caricature; a travesty; a gross\n   perversion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesqued', 'imp. & p. p.', 'of Burlesque');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesquing', 'p. pr. & vb. n.', 'of Burlesque');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesque', 'v. t.', 'To ridicule, or to make ludicrous by grotesque\n   representation in action or in language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesque', 'v. i.', 'To employ burlesque.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burlesquer', 'n.', 'One who burlesques.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burletta', 'a.', 'A comic operetta; a music farce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burliness', 'n.', 'Quality of being burly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burly', 'a.', 'Having a large, strong, or gross body; stout; lusty; -- now\n   used chiefly of human beings, but formerly of animals, in the sense of\n   stately or beautiful, and of inanimate things that were huge and bulky.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burly', 'a.', 'Coarse and rough; boisterous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burmans', 'pl. ', 'of Burman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burman', 'n.', 'A member of the Burman family, one of the four great\n   families Burmah; also, sometimes, any inhabitant of Burmah; a Burmese.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burman', 'a.', 'Of or pertaining to the Burmans or to Burmah.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bur marigold', '', 'See Beggars ticks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burmese', 'a.', 'Of or pertaining to Burmah, or its inhabitants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burmese', 'n. sing. & pl.', 'A native or the natives of Burmah. Also\n   (sing.); the language of the Burmans.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burned', 'imp. & p. p.', 'of Burn');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnt', '', 'of Burn');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burning', 'p. pr. & vb. n.', 'of Burn');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To consume with fire; to reduce to ashes by the action of\n   heat or fire; -- frequently intensified by up: as, to burn up wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To injure by fire or heat; to change destructively some\n   property or properties of, by undue exposure to fire or heat; to\n   scorch; to scald; to blister; to singe; to char; to sear; as, to burn\n   steel in forging; to burn ones face in the sun; the sun burns the\n   grass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To perfect or improve by fire or heat; to submit to the\n   action of fire or heat for some economic purpose; to destroy or change\n   some property or properties of, by exposure to fire or heat in due\n   degree for obtaining a desired residuum, product, or effect; to bake;\n   as, to burn clay in making bricks or pottery; to burn wood so as to\n   produce charcoal; to burn limestone for the lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To make or produce, as an effect or result, by the\n   application of fire or heat; as, to burn a hole; to burn charcoal; to\n   burn letters into a block.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To consume, injure, or change the condition of, as if by\n   action of fire or heat; to affect as fire or heat does; as, to burn the\n   mouth with pepper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To apply a cautery to; to cauterize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. t.', 'To cause to combine with oxygen or other active agent,\n   with evolution of heat; to consume; to oxidize; as, a man burns a\n   certain amount of carbon at each respiration; to burn iron in oxygen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. i.', 'To be of fire; to flame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. i.', 'To suffer from, or be scorched by, an excess of heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. i.', 'To have a condition, quality, appearance, sensation, or\n   emotion, as if on fire or excessively heated; to act or rage with\n   destructive violence; to be in a state of lively emotion or strong\n   desire; as, the face burns; to burn with fever.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. i.', 'To combine energetically, with evolution of heat; as,\n   copper burns in chlorine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'v. i.', 'In certain games, to approach near to a concealed object\n   which is sought.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'n.', 'A hurt, injury, or effect caused by fire or excessive or\n   intense heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'n.', 'The operation or result of burning or baking, as in\n   brickmaking; as, they have a good burn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'n.', 'A disease in vegetables. See Brand, n., 6.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burn', 'n.', 'A small stream.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnable', 'a.', 'Combustible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burned', 'p. p. & a.', 'See Burnt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burned', 'p. p.', 'Burnished.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burner', 'n.', 'One who, or that which, burns or sets fire to anything.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burner', 'n.', 'The part of a lamp, gas fixture, etc., where the flame is\n   produced.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnet', 'n.', 'A genus of perennial herbs (Poterium); especially,\n   P.Sanguisorba, the common, or garden, burnet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnettized', 'imp. & p. p.', 'of Burnettize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnettizing', 'p. pr. & vb. n.', 'of Burnettize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnettize', 'v. t.', 'To subject (wood, fabrics, etc.) to a process of\n   saturation in a solution of chloride of zinc, to prevent decay; -- a\n   process invented by Sir William Burnett.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnie', 'n.', 'A small brook.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burniebee', 'n.', 'The ladybird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burning', 'a.', 'That burns; being on fire; excessively hot; fiery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burning', 'a.', 'Consuming; intense; inflaming; exciting; vehement;\n   powerful; as, burning zeal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burning', 'n.', 'The act of consuming by fire or heat, or of subjecting to\n   the effect of fire or heat; the state of being on fire or excessively\n   heated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnished', 'imp. & p. p.', 'of Burnish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnishing', 'p. pr. & vb. n.', 'of Burnish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnish', 'a.', 'To cause to shine; to make smooth and bright; to polish;\n   specifically, to polish by rubbing with something hard and smooth; as,\n   to burnish brass or paper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnish', 'v. i.', 'To shine forth; to brighten; to become smooth and\n   glossy, as from swelling or filling out; hence, to grow large.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnish', 'n.', 'The effect of burnishing; gloss; brightness; luster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnisher', 'n.', 'One who burnishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnisher', 'n.', 'A tool with a hard, smooth, rounded end or surface, as\n   of steel, ivory, or agate, used in smoothing or polishing by rubbing.\n   It has a variety of forms adapted to special uses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnoose', 'n.', 'Alt. of Burnous');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnous', 'n.', 'A cloaklike garment and hood woven in one piece, worn by\n   Arabs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnous', 'n.', 'A combination cloak and hood worn by women.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnstickle', 'n.', 'A stickleback (Gasterosteus aculeatus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burnt', 'p. p. & a.', 'Consumed with, or as with, fire; scorched or dried,\n   as with fire or heat; baked or hardened in the fire or the sun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A prickly seed vessel. See Bur, 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The thin edge or ridge left by a tool in cutting or shaping\n   metal, as in turning, engraving, pressing, etc.; also, the rough neck\n   left on a bullet in casting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A thin flat piece of metal, formed from a sheet by punching;\n   a small washer put on the end of a rivet before it is swaged down.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A broad iron ring on a tilting lance just below the gripe, to\n   prevent the hand from slipping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The lobe or lap of the ear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'A guttural pronounciation of the letter r, produced by\n   trilling the extremity of the soft palate against the back part of the\n   tongue; rotacism; -- often called the Newcastle, Northumberland, or\n   Tweedside, burr.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'n.', 'The knot at the bottom of an antler. See Bur, n., 8.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burred', 'imp. & p. p.', 'of Burr');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burring', 'p. pr. & vb. n.', 'of Burr');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr', 'v. i.', 'To speak with burr; to make a hoarse or guttural murmur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrel', 'n.', 'A sort of pear, called also the red butter pear, from its\n   smooth, delicious, soft pulp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrel', 'n.', 'Same as Borrel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrel fly', '', 'The botfly or gadfly of cattle (Hypoderma bovis). See\n   Gadfly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrel shot', '', 'A mixture of shot, nails, stones, pieces of old iron,\n   etc., fired from a cannon at short range, in an emergency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burring machine', '', 'A machine for cleansing wool of burs, seeds, and\n   other substances.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burr millstone', '', 'See Buhrstone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burro', 'n.', 'A donkey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrock', 'n.', 'A small weir or dam in a river to direct the stream to\n   gaps where fish traps are placed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrow', 'n.', 'An incorporated town. See 1st Borough.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrow', 'n.', 'A shelter; esp. a hole in the ground made by certain\n   animals, as rabbits, for shelter and habitation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrow', 'n.', 'A heap or heaps of rubbish or refuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrow', 'n.', 'A mound. See 3d Barrow, and Camp, n., 5.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrowed', 'imp. & p. p.', 'of Burrow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrowing', 'p. pr. & vb. n.', 'of Burrow');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrow', 'v. i.', 'To excavate a hole to lodge in, as in the earth; to\n   lodge in a hole excavated in the earth, as conies or rabbits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrow', 'v. i.', 'To lodge, or take refuge, in any deep or concealed\n   place; to hide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrower', 'n.', 'One who, or that which, burrows; an animal that makes a\n   hole under ground and lives in it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burrstone', 'n.', 'See Buhrstone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burry', 'a.', 'Abounding in burs, or containing burs; resembling burs; as,\n   burry wool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursae', 'pl. ', 'of Bursa');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursa', 'n.', 'Any sac or saclike cavity; especially, one of the synovial\n   sacs, or small spaces, often lined with synovial membrane, interposed\n   between tendons and bony prominences.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursal', 'a.', 'Of or pertaining to a bursa or to bursae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursar', 'n.', 'A treasurer, or cash keeper; a purser; as, the bursar of a\n   college, or of a monastery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursar', 'n.', 'A student to whom a stipend or bursary is paid for his\n   complete or partial support.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursarship', 'n.', 'The office of a bursar.');
  INSERT INTO words (word, word_type, description)VALUES  (    '-ries', 'pl. ', 'of Bursary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursary', 'n.', 'The treasury of a college or monastery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursary', 'n.', 'A scholarship or charitable foundation in a university, as\n   in Scotland; a sum given to enable a student to pursue his studies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burschen', 'pl. ', 'of Bursch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursch', 'n.', 'A youth; especially, a student in a german university.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burse', 'n.', 'A purse; also, a vesicle; a pod; a hull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burse', 'n.', 'A fund or foundation for the maintenance of needy scholars\n   in their studies; also, the sum given to the beneficiaries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burse', 'n.', 'An ornamental case of hold the corporal when not in use.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burse', 'n.', 'An exchange, for merchants and bankers, in the cities of\n   continental Europe. Same as Bourse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burse', 'n.', 'A kind of bazaar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursiculate', 'a.', 'Bursiform.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursiform', 'a.', 'Shaped like a purse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursitis', 'n.', 'Inflammation of a bursa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'imp. & p. p.', 'of Burst');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursting', 'p. pr. & vb. n.', 'of Burst');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'v. i.', 'To fly apart or in pieces; of break open; to yield to\n   force or pressure, especially to a sudden and violent exertion of\n   force, or to pressure from within; to explode; as, the boiler had\n   burst; the buds will burst in spring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'v. i.', 'To exert force or pressure by which something is made\n   suddenly to give way; to break through obstacles or limitations; hence,\n   to appear suddenly and unexpectedly or unaccountably, or to depart in\n   such manner; -- usually with some qualifying adverb or preposition, as\n   forth, out, away, into, upon, through, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'v. t.', 'To break or rend by violence, as by an overcharge or by\n   strain or pressure, esp. from within; to force open suddenly; as, to\n   burst a cannon; to burst a blood vessel; to burst open the doors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'v. t.', 'To break.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'v. t.', 'To produce as an effect of bursting; as, to burst a hole\n   through the wall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'n.', 'A sudden breaking forth; a violent rending; an explosion;\n   as, a burst of thunder; a burst of applause; a burst of passion; a\n   burst of inspiration.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'n.', 'Any brief, violent exertion or effort; a spurt; as, a burst\n   of speed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'n.', 'A sudden opening, as of landscape; a stretch; an expanse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burst', 'n.', 'A rupture or hernia; a breach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bursten', '', 'p. p. of Burst, v. i.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burster', 'n.', 'One that bursts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burstwort', 'n.', 'A plant (Herniaria glabra) supposed to be valuable for\n   the cure of hernia or rupture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burt', 'n.', 'See Birt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burthen', 'n. & v. t.', 'See Burden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burton', 'n.', 'A peculiar tackle, formed of two or more blocks, or\n   pulleys, the weight being suspended to a hook block in the bight of the\n   running part.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bury', 'n.', 'A borough; a manor; as, the Bury of St. Edmonds');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bury', 'n.', 'A manor house; a castle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buried', 'imp. & p. p.', 'of Bury');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burying', 'p. pr. & vb. n.', 'of Bury');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bury', 'v. t.', 'To cover out of sight, either by heaping something over,\n   or by placing within something, as earth, etc.; to conceal by covering;\n   to hide; as, to bury coals in ashes; to bury the face in the hands.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bury', 'v. t.', 'Specifically: To cover out of sight, as the body of a\n   deceased person, in a grave, a tomb, or the ocean; to deposit (a\n   corpse) in its resting place, with funeral ceremonies; to inter; to\n   inhume.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bury', 'v. t.', 'To hide in oblivion; to put away finally; to abandon; as,\n   to bury strife.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burying ground', '', 'Alt. of Burying place');
  INSERT INTO words (word, word_type, description)VALUES  (    'Burying place', '', 'The ground or place for burying the dead; burial\n   place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bus', 'n.', 'An omnibus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busbies', 'pl. ', 'of Busby');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busby', 'n.', 'A military headdress or cap, used in the British army. It is\n   of fur, with a bag, of the same color as the facings of the regiment,\n   hanging from the top over the right shoulder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buscon', 'n.', 'One who searches for ores; a prospector.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'A thicket, or place abounding in trees or shrubs; a wild\n   forest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'A shrub; esp., a shrub with branches rising from or near the\n   root; a thick shrub or a cluster of shrubs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'A shrub cut off, or a shrublike branch of a tree; as, bushes\n   to support pea vines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'A shrub or branch, properly, a branch of ivy (as sacred to\n   Bacchus); hung out at vintners doors, or as a tavern sign; hence, a\n   tavern sign, and symbolically, the tavern itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'The tail, or brush, of a fox.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'v. i.', 'To branch thickly in the manner of a bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushed', 'imp. & p. p.', 'of Bush');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushing', 'p. pr. & vb. n.', 'of Bush');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'v. t.', 'To set bushes for; to support with bushes; as, to bush\n   peas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'v. t.', 'To use a bush harrow on (land); for covering seeds sown;\n   to harrow with a bush; as, to bush a piece of land; to bush seeds into\n   the ground.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'A lining for a hole to make it smaller; a thimble or ring of\n   metal or wood inserted in a plate or other part of machinery to receive\n   the wear of a pivot or arbor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'n.', 'A piece of copper, screwed into a gun, through which the\n   venthole is bored.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bush', 'v. t.', 'To furnish with a bush, or lining; as, to bush a pivot\n   hole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushboy', 'n.', 'See Bushman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushel', 'n.', 'A dry measure, containing four pecks, eight gallons, or\n   thirty-two quarts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushel', 'n.', 'A vessel of the capacity of a bushel, used in measuring; a\n   bushel measure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushel', 'n.', 'A quantity that fills a bushel measure; as, a heap\n   containing ten bushels of apples.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushel', 'n.', 'A large indefinite quantity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushel', 'n.', 'The iron lining in the nave of a wheel. [Eng.] In the\n   United States it is called a box. See 4th Bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushelage', 'n.', 'A duty payable on commodities by the bushel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushelman', 'n.', 'A tailors assistant for repairing garments; -- called\n   also busheler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushet', 'n.', 'A small bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushfighter', 'n.', 'One accustomed to bushfighting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushfighting', 'n.', 'Fighting in the bush, or from behind bushes, trees,\n   or thickets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushhammer', 'n.', 'A hammer with a head formed of a bundle of square bars,\n   with pyramidal points, arranged in rows, or a solid head with a face\n   cut into a number of rows of such points; -- used for dressing stone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushhammer', 'v. t.', 'To dress with bushhammer; as, to bushhammer a block\n   of granite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushiness', 'n.', 'The condition or quality of being bushy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushing', 'n.', 'The operation of fitting bushes, or linings, into holes or\n   places where wear is to be received, or friction diminished, as pivot\n   holes, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushing', 'n.', 'A bush or lining; -- sometimes called a thimble. See 4th\n   Bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushless', 'a.', 'Free from bushes; bare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushmen', 'pl. ', 'of Bushman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushman', 'n.', 'A woodsman; a settler in the bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushman', 'n.', 'One of a race of South African nomads, living principally\n   in the deserts, and not classified as allied in race or language to any\n   other people.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushment', 'n.', 'A thicket; a cluster of bushes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushment', 'n.', 'An ambuscade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushranger', 'n.', 'One who roams, or hides, among the bushes; especially,\n   in Australia, an escaped criminal living in the bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushwhacker', 'n.', 'One accustomed to beat about, or travel through,\n   bushes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushwhacker', 'n.', 'A guerrilla; a marauding assassin; one who pretends to\n   be a peaceful citizen, but secretly harasses a hostile force or its\n   sympathizers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushwhacking', 'n.', 'Traveling, or working a way, through bushes; pulling\n   by the bushes, as in hauling a boat along the bushy margin of a stream.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushwhacking', 'n.', 'The crimes or warfare of bushwhackers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushy', 'a.', 'Thick and spreading, like a bush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bushy', 'a.', 'Full of bushes; overgrowing with shrubs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busily', 'adv.', 'In a busy manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Businesses', 'pl. ', 'of Business');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'That which busies one, or that which engages the time,\n   attention, or labor of any one, as his principal concern or interest,\n   whether for a longer or shorter time; constant employment; regular\n   occupation; as, the business of life; business before pleasure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'Any particular occupation or employment engaged in for\n   livelihood or gain, as agriculture, trade, art, or a profession.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'Financial dealings; buying and selling; traffic in\n   general; mercantile transactions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'That which one has to do or should do; special service,\n   duty, or mission.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'Affair; concern; matter; -- used in an indefinite sense,\n   and modified by the connected words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'The position, distribution, and order of persons and\n   properties on the stage of a theater, as determined by the stage\n   manager in rehearsal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Business', 'n.', 'Care; anxiety; diligence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Businesslike', 'a.', 'In the manner of one transacting business wisely and\n   by right methods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busk', 'n.', 'A thin, elastic strip of metal, whalebone, wood, or other\n   material, worn in the front of a corset.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busked', 'imp. & p. p.', 'of Busk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busk', 'v. t. & i.', 'To prepare; to make ready; to array; to dress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busk', 'v. t. & i.', 'To go; to direct ones course.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busked', 'a.', 'Wearing a busk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busket', 'n.', 'A small bush; also, a sprig or bouquet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busket', 'n.', 'A part of a garden devoted to shrubs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buskin', 'n.', 'A strong, protecting covering for the foot, coming some\n   distance up the leg.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buskin', 'n.', 'A similar covering for the foot and leg, made with very\n   thick soles, to give an appearance of elevation to the stature; -- worn\n   by tragic actors in ancient Greece and Rome. Used as a symbol of\n   tragedy, or the tragic drama, as distinguished from comedy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buskined', 'a.', 'Wearing buskins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buskined', 'a.', 'Trodden by buskins; pertaining to tragedy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busky', 'a.', 'See Bosky, and 1st Bush, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buss', 'n.', 'A kiss; a rude or playful kiss; a smack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bussed', 'imp. & p. p.', 'of Buss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bussing', 'p. pr. & vb. n.', 'of Buss');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buss', 'v. t.', 'To kiss; esp. to kiss with a smack, or rudely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buss', 'n.', 'A small strong vessel with two masts and two cabins; -- used\n   in the herring fishery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bust', 'n.', 'A piece of sculpture representing the upper part of the human\n   figure, including the head, shoulders, and breast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bust', 'n.', 'The portion of the human figure included between the head and\n   waist, whether in statuary or in the person; the chest or thorax; the\n   upper part of the trunk of the body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustard', 'n.', 'A bird of the genus Otis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buster', 'n.', 'Something huge; a roistering blade; also, a spree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustled', 'imp. & p. p.', 'of Bustle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustling', 'n.', 'of Bustle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustle', 'v. i.', 'To move noisily; to be rudely active; to move in a way\n   to cause agitation or disturbance; as, to bustle through a crowd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustle', 'n.', 'Great stir; agitation; tumult from stirring or excitement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustle', 'n.', 'A kind of pad or cushion worn on the back below the waist,\n   by women, to give fullness to the skirts; -- called also bishop, and\n   tournure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustler', 'n.', 'An active, stirring person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustling', 'a.', 'Agitated; noisy; tumultuous; characterized by confused\n   activity; as, a bustling crowd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bustoes', 'pl. ', 'of Busto');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busto', 'n.', 'A bust; a statue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busy', 'a.', 'Engaged in some business; hard at work (either habitually or\n   only for the time being); occupied with serious affairs; not idle nor\n   at leisure; as, a busy merchant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busy', 'a.', 'Constantly at work; diligent; active.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busy', 'a.', 'Crowded with business or activities; -- said of places and\n   times; as, a busy street.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busy', 'a.', 'Officious; meddling; foolish active.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busy', 'a.', 'Careful; anxious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busied', 'imp. & p. p.', 'of Busy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busying', 'p. pr. & vb. n.', 'of Busy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busy', 'v. t.', 'To make or keep busy; to employ; to engage or keep\n   engaged; to occupy; as, to busy ones self with books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busybodies', 'pl. ', 'of Busybody');
  INSERT INTO words (word, word_type, description)VALUES  (    'Busybody', 'n.', 'One who officiously concerns himself with the affairs of\n   others; a meddling person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'adv. & conj.', 'Except with; unless with; without.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'adv. & conj.', 'Except; besides; save.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'adv. & conj.', 'Excepting or excluding the fact that; save that;\n   were it not that; unless; -- elliptical, for but that.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'adv. & conj.', 'Otherwise than that; that not; -- commonly, after a\n   negative, with that.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'adv. & conj.', 'Only; solely; merely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'adv. & conj.', 'On the contrary; on the other hand; only; yet;\n   still; however; nevertheless; more; further; -- as connective of\n   sentences or clauses of a sentence, in a sense more or less exceptive\n   or adversative; as, the House of Representatives passed the bill, but\n   the Senate dissented; our wants are many, but quite of another kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'prep., adv. & conj.', 'The outer apartment or kitchen of a\n   two-roomed house; -- opposed to ben, the inner room.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'n.', 'A limit; a boundary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'n.', 'The end; esp. the larger or thicker end, or the blunt, in\n   distinction from the sharp, end. See 1st Butt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butted', 'imp. & p. p.', 'of But');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butting', 'p. pr. & vb. n.', 'of But');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. i.', 'See Butt, v., and Abut, v.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butane', 'n.', 'An inflammable gaseous hydrocarbon, C4H10, of the marsh\n   gas, or paraffin, series.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butcher', 'n.', 'One who slaughters animals, or dresses their flesh for\n   market; one whose occupation it is to kill animals for food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butcher', 'n.', 'A slaughterer; one who kills in large numbers, or with\n   unusual cruelty; one who causes needless loss of life, as in battle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchered', 'imp. & p. p.', 'of Butcher');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchering', 'p. pr. & vb. n.', 'of Butcher');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butcher', 'v. t.', 'To kill or slaughter (animals) for food, or for market;\n   as, to butcher hogs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butcher', 'v. t.', 'To murder, or kill, especially in an unusually bloody\n   or barbarous manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchering', 'n.', 'The business of a butcher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchering', 'n.', 'The act of slaughtering; the act of killing cruelly and\n   needlessly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butcherliness', 'n.', 'Butchery quality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butcherly', 'a.', 'Like a butcher; without compunction; savage; bloody;\n   inhuman; fell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchers broom', '', 'A genus of plants (Ruscus); esp. R. aculeatus, which\n   has large red berries and leaflike branches. See Cladophyll.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchery', 'n.', 'The business of a butcher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchery', 'n.', 'Murder or manslaughter, esp. when committed with unusual\n   barbarity; great or cruel slaughter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butchery', 'n.', 'A slaughterhouse; the shambles; a place where blood is\n   shed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butler', 'n.', 'An officer in a kings or a noblemans household, whose\n   principal business it is to take charge of the liquors, plate, etc.;\n   the head servant in a large house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butlerage', 'n.', 'A duty of two shillings on every tun of wine imported\n   into England by merchant strangers; -- so called because paid to the\n   kings butler for the king.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butlership', 'n.', 'The office of a butler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butment', 'n.', 'A buttress of an arch; the supporter, or that part which\n   joins it to the upright pier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butment', 'n.', 'The mass of stone or solid work at the end of a bridge, by\n   which the extreme arches are sustained, or by which the end of a bridge\n   without arches is supported.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt', 'v. t.', 'Alt. of But');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A limit; a bound; a goal; the extreme bound; the end.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'The thicker end of anything. See But.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A mark to be shot at; a target.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A person at whom ridicule, jest, or contempt is directed;\n   as, the butt of the company.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A push, thrust, or sudden blow, given by the head of an\n   animal; as, the butt of a ram.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A thrust in fencing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A piece of land left unplowed at the end of a field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A joint where the ends of two objects come squarely\n   together without scarfing or chamfering; -- also called butt joint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'The end of a connecting rod or other like piece, to which\n   the boxing is attached by the strap, cotter, and gib.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'The portion of a half-coupling fastened to the end of a\n   hose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'The joint where two planks in a strake meet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'A kind of hinge used in hanging doors, etc.; -- so named\n   because fastened on the edge of the door, which butts against the\n   casing, instead of on its face, like the strap hinge; also called butt\n   hinge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'The thickest and stoutest part of tanned oxhides, used for\n   soles of boots, harness, trunks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But', 'v. t.', 'The hut or shelter of the person who attends to the targets\n   in rifle practice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butted', 'imp. & p. p.', 'of Butt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butting', 'p. pr. & vb. n.', 'of Butt');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt', 'v. i.', 'To join at the butt, end, or outward extremity; to\n   terminate; to be bounded; to abut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt', 'v. i.', 'To thrust the head forward; to strike by thrusting the\n   head forward, as an ox or a ram. [See Butt, n.]');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt', 'v. t.', 'To strike by thrusting the head against; to strike with\n   the head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt', 'n.', 'A large cask or vessel for wine or beer. It contains two\n   hogsheads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt', 'n.', 'The common English flounder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butte', 'n.', 'A detached low mountain, or high rising abruptly from the\n   general level of the surrounding plain; -- applied to peculiar\n   elevations in the Rocky Mountain region.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter', 'n.', 'An oily, unctuous substance obtained from cream or milk by\n   churning.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter', 'n.', 'Any substance resembling butter in degree of consistence,\n   or other qualities, especially, in old chemistry, the chlorides, as\n   butter of antimony, sesquichloride of antimony; also, certain concrete\n   fat oils remaining nearly solid at ordinary temperatures, as butter of\n   cacao, vegetable butter, shea butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttered', 'imp. & p. p.', 'of Butter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttering', 'p. pr. & vb. n.', 'of Butter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter', 'v. t.', 'To cover or spread with butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter', 'v. t.', 'To increase, as stakes, at every throw or every game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter', 'n.', 'One who, or that which, butts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterball', 'n.', 'The buffel duck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterbird', 'n.', 'The rice bunting or bobolink; -- so called in the\n   island of Jamaica.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterbump', 'n.', 'The European bittern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterbur', 'n.', 'A broad-leaved plant (Petasites vulgaris) of the\n   Composite family, said to have been used in England for wrapping up\n   pats of butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttercup', 'n.', 'A plant of the genus Ranunculus, or crowfoot,\n   particularly R. bulbosus, with bright yellow flowers; -- called also\n   butterflower, golden cup, and kingcup. It is the cuckoobud of\n   Shakespeare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter-fingered', 'a.', 'Apt to let things fall, or to let them slip away;\n   slippery; careless.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterfish', 'n.', 'A name given to several different fishes, in allusion\n   to their slippery coating of mucus, as the Stromateus triacanthus of\n   the Atlantic coast, the Epinephelus punctatus of the southern coast,\n   the rock eel, and the kelpfish of New Zealand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterflies', 'pl. ', 'of Butterfly');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterfly', 'n.', 'A general name for the numerous species of diurnal\n   Lepidoptera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterine', 'n.', 'A substance prepared from animal fat with some other\n   ingredients intermixed, as an imitation of butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butteris', 'n.', 'A steel cutting instrument, with a long bent shank set in\n   a handle which rests against the shoulder of the operator. It is\n   operated by a thrust movement, and used in paring the hoofs of horses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttermen', 'pl. ', 'of Butterman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterman', 'n.', 'A man who makes or sells butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttermilk', 'n.', 'The milk that remains after the butter is separated\n   from the cream.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butternut', 'n.', 'An American tree (Juglans cinerea) of the Walnut family,\n   and its edible fruit; -- so called from the oil contained in the\n   latter. Sometimes called oil nut and white walnut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butternut', 'n.', 'The nut of the Caryocar butyrosum and C. nuciferum, of\n   S. America; -- called also Souari nut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butter-scotch', 'n.', 'A kind of candy, mainly composed of sugar and\n   butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterweed', 'n.', 'An annual composite plant of the Mississippi valley\n   (Senecio lobatus).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterweight', 'n.', 'Over weight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butterwort', 'n.', 'A genus of low herbs (Pinguicula) having simple leaves\n   which secrete from their glandular upper surface a viscid fluid, to\n   which insects adhere, after which the margin infolds and the insects\n   are digested by the plant. The species are found mostly in the North\n   Temperate zone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttery', 'a.', 'Having the qualities, consistence, or appearance, of\n   butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butteries', 'pl. ', 'of Buttery');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttery', 'n.', 'An apartment in a house where butter, milk and other\n   provisions are kept.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttery', 'n.', 'A room in some English colleges where liquors, fruit, and\n   refreshments are kept for sale to the students.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttery', 'n.', 'A cellar in which butts of wine are kept.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt hinge', '', 'See 1st Butt, 10.');
  INSERT INTO words (word, word_type, description)VALUES  (    'But-thorn', 'n.', 'The common European starfish (Asterias rubens).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butting', 'n.', 'An abuttal; a boundary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butting joint', '', 'A joint between two pieces of timber or wood, at the\n   end of one or both, and either at right angles or oblique to the grain,\n   as the joints which the struts and braces form with the truss posts; --\n   sometimes called abutting joint.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt joint', '', 'A joint in which the edges or ends of the pieces united\n   come squarely together instead of overlapping. See 1st Butt, 8.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttock', 'n.', 'The part at the back of the hip, which, in man, forms one\n   of the rounded protuberances on which he sits; the rump.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttock', 'n.', 'The convexity of a ship behind, under the stern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'A knob; a small ball; a small, roundish mass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'A catch, of various forms and materials, used to fasten\n   together the different parts of dress, by being attached to one part,\n   and passing through a slit, called a buttonhole, in the other; -- used\n   also for ornament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'A bud; a germ of a plant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'A piece of wood or metal, usually flat and elongated,\n   turning on a nail or screw, to fasten something, as a door.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'A globule of metal remaining on an assay cupel or in a\n   crucible, after fusion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttoned', 'imp. & p. p.', 'of Button');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttoning', 'p. pr. & vb. n.', 'of Button');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'To fasten with a button or buttons; to inclose or make\n   secure with buttons; -- often followed by up.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'n.', 'To dress or clothe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Button', 'v. i.', 'To be fastened by a button or buttons; as, the coat will\n   not button.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonball', 'n.', 'See Buttonwood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonbush', 'n.', 'A shrub (Cephalanthus occidentalis) growing by the\n   waterside; -- so called from its globular head of flowers. See\n   Capitulum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonhole', 'n.', 'The hole or loop in which a button is caught.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonhole', 'v. t.', 'To hold at the button or buttonhole; to detain in\n   conversation to weariness; to bore; as, he buttonholed me a quarter of\n   an hour.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonmold', 'n.', 'A disk of bone, wood, or other material, which is made\n   into a button by covering it with cloth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttons', 'n.', 'A boy servant, or page, -- in allusion to the buttons on\n   his livery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonweed', 'n.', 'The name of several plants of the genera Spermacoce and\n   Diodia, of the Madder family.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttonwood', 'n.', 'The Platanus occidentalis, or American plane tree, a\n   large tree, producing rough balls, from which it is named; -- called\n   also buttonball tree, and, in some parts of the United States,\n   sycamore. The California buttonwood is P. racemosa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttony', 'a.', 'Ornamented with a large number of buttons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttress', 'n.', 'A projecting mass of masonry, used for resisting the\n   thrust of an arch, or for ornament and symmetry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttress', 'n.', 'Anything which supports or strengthens.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttressed', 'imp. & p. p.', 'of Buttress');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttressing', 'p. pr. & vb. n.', 'of Buttress');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttress', 'v. t.', 'To support with a buttress; to prop; to brace firmly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt shaft', '', 'An arrow without a barb, for shooting at butts; an arrow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butt weld', '', 'See Butt weld, under Butt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buttweld', 'v. t.', 'To unite by a butt weld.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butty', 'n.', 'One who mines by contract, at so much per ton of coal or\n   ore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyl', 'n.', 'A compound radical, regarded as butane, less one atom of\n   hydrogen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butylene', 'n.', 'Any one of three metameric hydrocarbons, C4H8, of the\n   ethylene series. They are gaseous or easily liquefiable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyraceous', 'a.', 'Having the qualities of butter; resembling butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyrate', 'n.', 'A salt of butyric acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyric', 'a.', 'Pertaining to, or derived from, butter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyrin', 'n.', 'A butyrate of glycerin; a fat contained in small quantity\n   in milk, which helps to give to butter its peculiar flavor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyrometer', 'n.', 'An instrument for determining the amount of fatty\n   matter or butter contained in a sample of milk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyrone', 'n.', 'A liquid ketone obtained by heating calcium butyrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Butyrous', 'a.', 'Butyraceous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buxeous', 'a.', 'Belonging to the box tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buxine', 'n.', 'An alkaloid obtained from the Buxus sempervirens, or common\n   box tree. It is identical with bebeerine; -- called also buxina.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buxom', 'a.', 'Yielding; pliable or compliant; ready to obey; obedient;\n   tractable; docile; meek; humble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buxom', 'a.', 'Having the characteristics of health, vigor, and comeliness,\n   combined with a gay, lively manner; stout and rosy; jolly; frolicsome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bought', 'imp. & p. p.', 'of Buy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buying', 'p. pr. & vb. n.', 'of Buy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buy', 'v. t.', 'To acquire the ownership of (property) by giving an\n   accepted price or consideration therefor, or by agreeing to do so; to\n   acquire by the payment of a price or value; to purchase; -- opposed to\n   sell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buy', 'v. t.', 'To acquire or procure by something given or done in\n   exchange, literally or figuratively; to get, at a cost or sacrifice; to\n   buy pleasure with pain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buy', 'v. i.', 'To negotiate or treat about a purchase.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buyer', 'n.', 'One who buys; a purchaser.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buz', 'v. & n.', 'See Buzz.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzed', 'imp. & p. p.', 'of Buzz');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzing', 'p. pr. & vb. n.', 'of Buzz');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'v. i.', 'To make a low, continuous, humming or sibilant sound, like\n   that made by bees with their wings. Hence: To utter a murmuring sound;\n   to speak with a low, humming voice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'v. t.', 'To sound forth by buzzing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'v. t.', 'To whisper; to communicate, as tales, in an under tone; to\n   spread, as report, by whispers, or secretly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'v. t.', 'To talk to incessantly or confidentially in a low humming\n   voice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'v. t.', 'To sound with a \"buzz\".');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'n.', 'A continuous, humming noise, as of bees; a confused murmur,\n   as of general conversation in low tones, or of a general expression of\n   surprise or approbation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'n.', 'A whisper; a report spread secretly or cautiously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzz', 'n.', 'The audible friction of voice consonants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzard', 'n.', 'A bird of prey of the Hawk family, belonging to the genus\n   Buteo and related genera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzard', 'n.', 'A blockhead; a dunce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzard', 'a.', 'Senseless; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzardet', 'n.', 'A hawk resembling the buzzard, but with legs relatively\n   longer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzer', 'n.', 'One who, or that which, buzzes; a whisperer; a talebearer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzingly', 'adv.', 'In a buzzing manner; with a buzzing sound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Buzzsaw', '', 'A circular saw; -- so called from the buzzing it makes when\n   running at full speed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'pref.', 'In the neighborhood of; near or next to; not far from; close\n   to; along with; as, come and sit by me.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'pref.', 'On; along; in traversing. Compare 5.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'pref.', 'Near to, while passing; hence, from one to the other side\n   of; past; as, to go by a church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'pref.', 'Used in specifying adjacent dimensions; as, a cabin twenty\n   feet by forty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'pref.', 'Against.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'pref.', 'With, as means, way, process, etc.; through means of; with\n   aid of; through; through the act or agency of; as, a city is destroyed\n   by fire; profit is made by commerce; to take by force.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'adv.', 'Near; in the neighborhood; present; as, there was no person\n   by at the time.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'adv.', 'Passing near; going past; past; beyond; as, the procession\n   has gone by; a bird flew by.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'adv.', 'Aside; as, to lay by; to put by.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By', 'a.', 'Out of the common path; aside; -- used in composition, giving\n   the meaning of something aside, secondary, or incidental, or collateral\n   matter, a thing private or avoiding notice; as, by-line, by-place,\n   by-play, by-street. It was formerly more freely used in composition\n   than it is now; as, by-business, by-concernment, by-design,\n   by-interest, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byard', 'n.', 'A piece of leather crossing the breast, used by the men who\n   drag sledges in coal mines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-bidder', 'n.', 'One who bids at an auction in behalf of the auctioneer\n   or owner, for the purpose of running up the price of articles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-blow', 'n.', 'A side or incidental blow; an accidental blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-blow', 'n.', 'An illegitimate child; a bastard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-corner', 'n.', 'A private corner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-dependence', 'n.', 'An appendage; that which depends on something else,\n   or is distinct from the main dependence; an accessory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-drinking', 'n.', 'A drinking between meals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bye', 'n.', 'A thing not directly aimed at; something which is a secondary\n   object of regard; an object by the way, etc.; as in on or upon the bye,\n   i. e., in passing; indirectly; by implication.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bye', 'n.', 'A run made upon a missed ball; as, to steal a bye.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bye', 'n.', 'A dwelling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bye', 'n.', 'In certain games, a station or place of an individual player.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-election', 'n.', 'An election held by itself, not at the time of a\n   general election.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-end', 'n.', 'Private end or interest; secret purpose; selfish advantage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bygone', 'a.', 'Past; gone by.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bygone', 'n.', 'Something gone by or past; a past event.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-interest', 'n.', 'Self-interest; private advantage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byland', 'n.', 'A peninsula.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bylander', 'n.', 'See Bilander.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-lane', 'n.', 'A private lane, or one opening out of the usual road.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-law', 'n.', 'A local or subordinate law; a private law or regulation\n   made by a corporation for its own government.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-law', 'n.', 'A law that is less important than a general law or\n   constitutional provision, and subsidiary to it; a rule relating to a\n   matter of detail; as, civic societies often adopt a constitution and\n   by-laws for the government of their members. In this sense the word has\n   probably been influenced by by, meaning secondary or aside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-name', 'n.', 'A nickname.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byname', 'v. t.', 'To give a nickname to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-pass', 'n.', 'A by-passage, for a pipe, or other channel, to divert\n   circulation from the usual course.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-passage', 'n.', 'A passage different from the usual one; a byway.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-past', 'a.', 'Past; gone by.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bypaths', 'pl. ', 'of Bypath');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bypath', 'n.', 'A private path; an obscure way; indirect means.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-place', 'n.', 'A retired or private place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byplay', 'n.', 'Action carried on aside, and commonly in dumb show, while\n   the main action proceeds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-product', 'n.', 'A secondary or additional product; something produced,\n   as in the course of a manufacture, in addition to the principal\n   product.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byre', 'n.', 'A cow house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-respect', 'n.', 'Private end or view; by-interest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byroad', 'n.', 'A private or obscure road.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byronic', 'a.', 'Pertaining to, or in the style of, Lord Byron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-room', 'n.', 'A private room or apartment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bysmottered', 'p.a.', 'Bespotted with mud or dirt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-speech', 'n.', 'An incidental or casual speech, not directly relating to\n   the point.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-spell', 'n.', 'A proverb.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byss', 'n.', 'See Byssus, n., 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssaceous', 'a.', 'Byssuslike; consisting of fine fibers or threads, as\n   some very delicate filamentous algae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssiferous', 'a.', 'Bearing a byssus or tuft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssin', 'n.', 'See Byssus, n., 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssine', 'a.', 'Made of silk; having a silky or flaxlike appearance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssoid', 'a.', 'Byssaceous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssolite', 'n.', 'An olive-green fibrous variety of hornblende.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssuses', 'pl. ', 'of Byssus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssi', 'pl. ', 'of Byssus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssus', 'n.', 'A cloth of exceedingly fine texture, used by the ancients.\n   It is disputed whether it was of cotton, linen, or silk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssus', 'n.', 'A tuft of long, tough filaments which are formed in a\n   groove of the foot, and issue from between the valves of certain\n   bivalve mollusks, as the Pinna and Mytilus, by which they attach\n   themselves to rocks, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssus', 'n.', 'An obsolete name for certain fungi composed of slender\n   threads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byssus', 'n.', 'Asbestus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bystander', 'n.', 'One who stands near; a spectator; one who has no concern\n   with the business transacting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-street', 'n.', 'A separate, private, or obscure street; an out of the\n   way or cross street.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-stroke', 'n.', 'An accidental or a slyly given stroke.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-turning', 'n.', 'An obscure road; a way turning from the main road.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-view', 'n.', 'A private or selfish view; self-interested aim or purpose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-walk', 'n.', 'A secluded or private walk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-wash', 'n.', 'The outlet from a dam or reservoir; also, a cut to divert\n   the flow of water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byway', 'n.', 'A secluded, private, or obscure way; a path or road aside\n   from the main one.');
  INSERT INTO words (word, word_type, description)VALUES  (    'By-wipe', 'n.', 'A secret or side stroke, as of raillery or sarcasm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byword', 'n.', 'A common saying; a proverb; a saying that has a general\n   currency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byword', 'n.', 'The object of a contemptuous saying.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bywork', 'n.', 'Work aside from regular work; subordinate or secondary\n   business.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byzant', 'n.', 'Alt. of Byzantine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byzantine', 'n.', 'A gold coin, so called from being coined at Byzantium.\n   See Bezant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byzantian', 'a. & n.', 'See Byzantine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byzantine', 'a.', 'Of or pertaining to Byzantium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Byzantine', 'n.', 'A native or inhabitant of Byzantium, now Constantinople;\n   sometimes, applied to an inhabitant of the modern city of\n   Constantinople.\n   C () C is the third letter of the English alphabet. It is from the\n   Latin letter C, which in old Latin represented the sounds of k, and g\n   (in go); its original value being the latter. In Anglo-Saxon words, or\n   Old English before the Norman Conquest, it always has the sound of k.\n   The Latin C was the same letter as the Greek /, /, and came from the\n   Greek alphabet. The Greeks got it from the Ph/nicians. The English name\n   of C is from the Latin name ce, and was derived, probably, through the\n   French. Etymologically C is related to g, h, k, q, s (and other\n   sibilant sounds). Examples of these relations are in L. acutus, E.\n   acute, ague; E. acrid, eager, vinegar; L. cornu, E. horn; E. cat,\n   kitten; E. coy, quiet; L. circare, OF. cerchier, E. search.');
  INSERT INTO words (word, word_type, description)VALUES  (    'C', '', 'The keynote of the normal or \"natural\" scale, which has neither\n   flats nor sharps in its signature; also, the third note of the relative\n   minor scale of the same.');
  INSERT INTO words (word, word_type, description)VALUES  (    'C', '', 'C after the clef is the mark of common time, in which each measure\n   is a semibreve (four fourths or crotchets); for alla breve time it is\n   written /.');
  INSERT INTO words (word, word_type, description)VALUES  (    'C', '', 'The \"C clef,\" a modification of the letter C, placed on any line\n   of the staff, shows that line to be middle C.');
  INSERT INTO words (word, word_type, description)VALUES  (    'C', '', 'As a numeral, C stands for Latin centum or 100, CC for 200, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caaba', 'n.', 'The small and nearly cubical stone building, toward which\n   all Mohammedans must pray.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caas', 'n. sing. & pl.', 'Case.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cab', 'n.', 'A kind of close carriage with two or four wheels, usually a\n   public vehicle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cab', 'n.', 'The covered part of a locomotive, in which the engineer has\n   his station.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cab', 'n.', 'A Hebrew dry measure, containing a little over two (2.37)\n   pints.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabal', 'n.', 'Tradition; occult doctrine. See Cabala');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabal', 'n.', 'A secret.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabal', 'n.', 'A number of persons united in some close design, usually to\n   promote their private views and interests in church or state by\n   intrigue; a secret association composed of a few designing persons; a\n   junto.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabal', 'n.', 'The secret artifices or machinations of a few persons united\n   in a close design; intrigue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caballed', 'imp. & p. p.', 'of Cabal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caballing', 'p. pr. & vb. n.', 'of Cabal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabal', 'v. i.', 'To unite in a small party to promote private views and\n   interests by intrigue; to intrigue; to plot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabala', 'n.', 'A kind of occult theosophy or traditional interpretation of\n   the Scriptures among Jewish rabbis and certain mediaeval Christians,\n   which treats of the nature of god and the mystery of human existence.\n   It assumes that every letter, word, number, and accent of Scripture\n   contains a hidden sense; and it teaches the methods of interpretation\n   for ascertaining these occult meanings. The cabalists pretend even to\n   foretell events by this means.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabala', 'n.', 'Secret science in general; mystic art; mystery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalism', 'n.', 'The secret science of the cabalists.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalism', 'n.', 'A superstitious devotion to the mysteries of the religion\n   which one professes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalist', 'n.', 'One versed in the cabala, or the mysteries of Jewish\n   traditions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalistic', 'a.', 'Alt. of Cabalistical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalistical', 'a.', 'Of or pertaining to the cabala; containing or\n   conveying an occult meaning; mystic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalistically', 'adv.', 'In a cabalistic manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabalize', 'v. i.', 'To use cabalistic language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caballer', 'n.', 'One who cabals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caballine', 'a.', 'Of or pertaining to a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caballine', 'n.', 'Caballine aloes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabaret', 'n.', 'A tavern; a house where liquors are retailed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabaret', 'n.', 'a type of restaurant where liquor and dinner is served,\n   and entertainment is provided, as by musicians, dancers, or comedians,\n   and providing space for dancing by the patrons; -- similar to a\n   nightclub. The term cabaret is often used in the names of such an\n   establishment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabaret', 'n.', 'the type of entertainment provided in a cabaret{2}.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabas', 'n.', 'A flat basket or frail for figs, etc.; hence, a ladys flat\n   workbasket, reticule, or hand bag; -- often written caba.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabassou', 'n.', 'A species of armadillo of the genus Xenurus (X.\n   unicinctus and X. hispidus); the tatouay.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbage', 'n.', 'An esculent vegetable of many varieties, derived from the\n   wild Brassica oleracea of Europe. The common cabbage has a compact head\n   of leaves. The cauliflower, Brussels sprouts, etc., are sometimes\n   classed as cabbages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbage', 'n.', 'The terminal bud of certain palm trees, used, like,\n   cabbage, for food. See Cabbage tree, below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbage', 'n.', 'The cabbage palmetto. See below.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbage', 'v. i.', 'To form a head like that the cabbage; as, to make\n   lettuce cabbage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbaged', 'imp. & p. p', 'of Cabbage');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbaging', 'p. pr. & vb. n.', 'of Cabbage');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbage', 'v. i.', 'To purloin or embezzle, as the pieces of cloth\n   remaining after cutting out a garment; to pilfer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbage', 'n.', 'Cloth or clippings cabbaged or purloined by one who cuts\n   out garments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbler', 'n.', 'One who works at cabbling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbling', 'n.', 'The process of breaking up the flat masses into which\n   wrought iron is first hammered, in order that the pieces may be\n   reheated and wrought into bar iron.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabeca', 'n.', 'Alt. of Cabesse');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabesse', 'n.', 'The finest kind of silk received from India.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caber', 'n.', 'A pole or beam used in Scottish games for tossing as a trial\n   of strength.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabezon', 'n.', 'A California fish (Hemilepidotus spinosus); allied to the\n   sculpin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabiai', 'n.', 'The capybara. See Capybara.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabin', 'n.', 'A cottage or small house; a hut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabin', 'n.', 'A small room; an inclosed place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabin', 'n.', 'A room in ship for officers or passengers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabined', 'imp. & p. p.', 'of Cabin');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabining', 'p. pr. & vb. n.', 'of Cabin');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabin', 'v. i.', 'To live in, or as in, a cabin; to lodge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabin', 'v. t.', 'To confine in, or as in, a cabin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'A hut; a cottage; a small house.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'A small room, or retired apartment; a closet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'A private room in which consultations are held.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'The advisory council of the chief executive officer of a\n   nation; a cabinet council.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'A set of drawers or a cupboard intended to contain\n   articles of value. Hence:');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'A decorative piece of furniture, whether open like an\n   etagere or closed with doors. See Etagere.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'n.', 'Any building or room set apart for the safe keeping and\n   exhibition of works of art, etc.; also, the collection itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'a.', 'Suitable for a cabinet; small.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabineting', 'p. pr. & vb. n.', 'of Cabinet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinet', 'v. i.', 'To inclose');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinetmaker', 'n.', 'One whose occupation is to make cabinets or other\n   choice articles of household furniture, as tables, bedsteads, bureaus,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinetmaking', 'n.', 'The art or occupation of making the finer articles\n   of household furniture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabinetwork', 'n.', 'The art or occupation of working upon wooden furniture\n   requiring nice workmanship; also, such furniture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabirean', 'n.', 'One of the Cabiri.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabbiri', 'n. pl.', 'Certain deities originally worshiped with mystical\n   rites by the Pelasgians in Lemnos and Samothrace and afterwards\n   throughout Greece; -- also called sons of Hephaestus (or Vulcan); as\n   being masters of the art of working metals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabirian', 'a.', 'Same as Cabiric.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabiric', 'a.', 'Of or pertaining to the Cabiri, or to their mystical\n   worship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cable', 'n.', 'A large, strong rope or chain, of considerable length, used\n   to retain a vessel at anchor, and for other purposes. It is made of\n   hemp, of steel wire, or of iron links.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cable', 'n.', 'A rope of steel wire, or copper wire, usually covered with\n   some protecting or insulating substance; as, the cable of a suspension\n   bridge; a telegraphic cable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cable', 'n.', 'A molding, shaft of a column, or any other member of convex,\n   rounded section, made to resemble the spiral twist of a rope; -- called\n   also cable molding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cable', 'v. t.', 'To fasten with a cable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cable', 'v. t.', 'To ornament with cabling. See Cabling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabled', 'imp. & p. p.', 'of Cable');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabling', 'p. pr. & vb. n.', 'of Cable');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cable', 'v. t. & i.', 'To telegraph by a submarine cable');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabled', 'a.', 'Fastened with, or attached to, a cable or rope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabled', 'a.', 'Adorned with cabling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cablegram', 'n.', 'A message sent by a submarine telegraphic cable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cablelaid', 'a.', 'Composed of three three-stranded ropes, or hawsers,\n   twisted together to form a cable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cablelaid', 'a.', 'Twisted after the manner of a cable; as, a cable-laid\n   gold chain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cablet', 'n.', 'A little cable less than ten inches in circumference.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabling', 'n.', 'The decoration of a fluted shaft of a column or of a\n   pilaster with reeds, or rounded moldings, which seem to be laid in the\n   hollows of the fluting. These are limited in length to about one third\n   of the height of the shaft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabmen', 'pl. ', 'of Cabman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabman', 'n.', 'The driver of a cab.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabob', 'n.', 'A small piece of mutton or other meat roasted on a skewer;\n   -- so called in Turkey and Persia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabob', 'n.', 'A leg of mutton roasted, stuffed with white herrings and\n   sweet herbs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabob', 'v. t.', 'To roast, as a cabob.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caboched', 'a.', 'Showing the full face, but nothing of the neck; -- said\n   of the head of a beast in armorial bearing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caboodle', 'n.', 'The whole collection; the entire quantity or number; --\n   usually in the phrase the whole caboodle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caboose', 'n.', 'A house on deck, where the cooking is done; -- commonly\n   called the galley.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caboose', 'n.', 'A car used on freight or construction trains for brakemen,\n   workmen, etc.; a tool car.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabotage', 'n.', 'Navigation along the coast; the details of coast\n   pilotage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabree', 'n.', 'The pronghorn antelope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabrerite', 'n.', 'An apple-green mineral, a hydrous arseniate of nickel,\n   cobalt, and magnesia; -- so named from the Sierra Cabrera, Spain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabrilla', 'n.', 'A name applied to various species of edible fishes of the\n   genus Serranus, and related genera, inhabiting the Meditarranean, the\n   coast of California, etc. In California, some of them are also called\n   rock bass and kelp salmon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabriole', 'n.', 'A curvet; a leap. See Capriole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabriolet', 'n.', 'A one-horse carriage with two seats and a calash top.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cabrit', 'n.', 'Same as Cabree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caburn', 'n.', 'A small line made of spun yarn, to bind or worm cables,\n   seize tackles, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacaemia', 'n.', 'Alt. of Cachaemia');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachaemia', 'n.', 'A degenerated or poisoned condition of the blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacaine', 'n.', 'The essential principle of cacao; -- now called\n   theobromine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacajao', 'n.', 'A South American short-tailed monkey (Pithecia (/\n   Brachyurus) melanocephala).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacao', 'n.', 'A small evergreen tree (Theobroma Cacao) of South America\n   and the West Indies. Its fruit contains an edible pulp, inclosing seeds\n   about the size of an almond, from which cocoa, chocolate, and broma are\n   prepared.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachalot', 'n.', 'The sperm whale (Physeter macrocephalus). It has in the\n   top of its head a large cavity, containing an oily fluid, which, after\n   death, concretes into a whitish crystalline substance called\n   spermaceti. See Sperm whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cache', 'n.', 'A hole in the ground, or hiding place, for concealing and\n   preserving provisions which it is inconvenient to carry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachectic', 'a.', 'Alt. of Cachectical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachectical', 'a.', 'Having, or pertaining to, cachexia; as, cachectic\n   remedies; cachectical blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachepot', 'n.', 'An ornamental casing for a flowerpot, of porcelain,\n   metal, paper, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachet', 'n.', 'A seal, as of a letter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachexia', 'n.', 'Alt. of Cachexy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachexy', 'n.', 'A condition of ill health and impairment of nutrition due\n   to impoverishment of the blood, esp. when caused by a specific morbid\n   process (as cancer or tubercle).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachinnation', 'n.', 'Loud or immoderate laughter; -- often a symptom of\n   hysterical or maniacal affections.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachinnatory', 'a.', 'Consisting of, or accompanied by, immoderate\n   laughter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachiri', 'n.', 'A fermented liquor made in Cayenne from the grated root of\n   the manioc, and resembling perry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacholong', 'n.', 'An opaque or milk-white chalcedony, a variety of quartz;\n   also, a similar variety of opal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachou', 'n.', 'A silvered aromatic pill, used to correct the odor of the\n   breath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachucha', 'n.', 'An Andalusian dance in three-four time, resembling the\n   bolero.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cachunde', 'n.', 'A pastil or troche, composed of various aromatic and\n   other ingredients, highly celebrated in India as an antidote, and as a\n   stomachic and antispasmodic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacique', 'n.', 'See Cazique.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cack', 'v. i.', 'To ease the body by stool; to go to stool.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackerel', 'n.', 'The mendole; a small worthless Mediterranean fish\n   considered poisonous by the ancients. See Mendole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackled', 'imp. & p. p.', 'of Cackle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackling', 'p. pr. & vb. n.', 'of Cackle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackle', 'v. i.', 'To make a sharp, broken noise or cry, as a hen or goose\n   does.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackle', 'v. i.', 'To laugh with a broken noise, like the cackling of a hen\n   or a goose; to giggle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackle', 'v. i.', 'To talk in a silly manner; to prattle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackle', 'n.', 'The sharp broken noise made by a goose or by a hen that has\n   laid an egg.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackle', 'n.', 'Idle talk; silly prattle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackler', 'n.', 'A fowl that cackles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackler', 'n.', 'One who prattles, or tells tales; a tattler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cackling', 'n.', 'The broken noise of a goose or a hen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacochymia', 'n.', 'Alt. of Cacochymy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacochymy', 'n.', 'A vitiated state of the humors, or fluids, of the body,\n   especially of the blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacochymic', 'a.', 'Alt. of Cacochymical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacochymical', 'a.', 'Having the fluids of the body vitiated, especially\n   the blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacodemon', 'n.', 'An evil spirit; a devil or demon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacodemon', 'n.', 'The nightmare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacodoxical', 'a.', 'Heretical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacodoxy', 'n.', 'Erroneous doctrine; heresy; heterodoxy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacodyl', 'n.', 'Alkarsin; a colorless, poisonous, arsenical liquid,\n   As2(CH3)4, spontaneously inflammable and possessing an intensely\n   disagreeable odor. It is the type of a series of compounds analogous to\n   the nitrogen compounds called hydrazines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacodylic', 'a.', 'Of, pertaining to, or derived from, cacodyl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacoethes', 'n.', 'A bad custom or habit; an insatiable desire; as,\n   cacoethes scribendi, \"The itch for writing\".');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacoethes', 'n.', 'A bad quality or disposition in a disease; an incurable\n   ulcer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacogastric', 'a.', 'Troubled with bad digestion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacographic', 'a.', 'Pertaining to, or characterized by, cacography; badly\n   written or spelled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacography', 'n.', 'Incorrect or bad writing or spelling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacolet', 'n.', 'A chair, litter, or other contrivance fitted to the back\n   or pack saddle of a mule for carrying travelers in mountainous\n   districts, or for the transportation of the sick and wounded of an\n   army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacology', 'n.', 'Bad speaking; bad choice or use of words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacomixle', 'n.', 'Alt. of Cacomixl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacomixtle', 'n.', 'Alt. of Cacomixl');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacomixl', 'n.', 'A North American carnivore (Bassaris astuta); about the\n   size of a cat, related to the raccoons. It inhabits Mexico, Texas, and\n   California.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacoon', 'n.', 'One of the seeds or large beans of a tropical vine (Entada\n   scandens) used for making purses, scent bottles, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophonic', 'a.', 'Alt. of Cacophonious');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophonical', 'a.', 'Alt. of Cacophonious');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophonous', 'a.', 'Alt. of Cacophonious');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophonious', 'a.', 'Harsh-sounding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophonies', 'pl. ', 'of Cacophony');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophony', 'n.', 'An uncouth or disagreable sound of words, owing to the\n   concurrence of harsh letters or syllables.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophony', 'n.', 'A combination of discordant sounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacophony', 'n.', 'An unhealthy state of the voice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacotechny', 'n.', 'A corruption or corrupt state of art.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacoxene', 'n.', 'Alt. of Cacoxenite');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacoxenite', 'n.', 'A hydrous phosphate of iron occurring in yellow\n   radiated tufts. The phosphorus seriously injures it as an iron ore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cactaceous', 'a.', 'Belonging to, or like, the family of plants of which\n   the prickly pear is a common example.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cactuses', 'pl. ', 'of Cactus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacti', 'pl. ', 'of Cactus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cactus', 'n.', 'Any plant of the order Cactacae, as the prickly pear and\n   the night-blooming cereus. See Cereus. They usually have leafless stems\n   and branches, often beset with clustered thorns, and are mostly natives\n   of the warmer parts of America.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacuminal', 'a.', 'Pertaining to the top of the palate; cerebral; --\n   applied to certain consonants; as, cacuminal (or cerebral) letters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cacuminate', 'v. i.', 'To make sharp or pointed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cad', 'n.', 'A person who stands at the door of an omnibus to open and shut\n   it, and to receive fares; an idle hanger-on about innyards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cad', 'n.', 'A lowbred, presuming person; a mean, vulgar fellow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadastral', 'a.', 'Of or pertaining to landed property.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadastre', 'n.', 'Alt. of Cadaster');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadaster', 'n.', 'An official statement of the quantity and value of real\n   estate for the purpose of apportioning the taxes payable on such\n   property.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadaver', 'n.', 'A dead human body; a corpse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadaveric', 'a.', 'Of, pertaining to, or resembling, a corpse, or the\n   changes produced by death; cadaverous; as, cadaveric rigidity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadaverous', 'a.', 'Having the appearance or color of a dead human body;\n   pale; ghastly; as, a cadaverous look.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadaverous', 'a.', 'Of or pertaining to, or having the qualities of, a dead\n   body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadbait', 'n.', 'See Caddice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddice', 'n.', 'Alt. of Caddis');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddis', 'n.', 'The larva of a caddice fly. These larvae generally live in\n   cylindrical cases, open at each end, and covered externally with pieces\n   of broken shells, gravel, bits of wood, etc. They are a favorite bait\n   with anglers. Called also caddice worm, or caddis worm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddis', 'n.', 'A kind of worsted lace or ribbon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddish', 'a.', 'Like a cad; lowbred and presuming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddow', 'n.', 'A jackdaw.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddies', 'pl. ', 'of Caddy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddy', 'n.', 'A small box, can, or chest to keep tea in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cade', 'a.', 'Bred by hand; domesticated; petted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cade', 'v. t.', 'To bring up or nourish by hand, or with tenderness; to\n   coddle; to tame.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cade', 'n.', 'A barrel or cask, as of fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cade', 'n.', 'A species of juniper (Juniperus Oxycedrus) of Mediterranean\n   countries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'The act or state of declining or sinking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'A fall of the voice in reading or speaking, especially at\n   the end of a sentence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'A rhythmical modulation of the voice or of any sound; as,\n   music of bells in cadence sweet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'Rhythmical flow of language, in prose or verse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'See Cadency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'Harmony and proportion in motions, as of a well-managed\n   horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'A uniform time and place in marching.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'The close or fall of a strain; the point of rest, commonly\n   reached by the immediate succession of the tonic to the dominant chord.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'n.', 'A cadenza, or closing embellishment; a pause before the\n   end of a strain, which the performer may fill with a flight of fancy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadence', 'v. t.', 'To regulate by musical measure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadency', 'n.', 'Descent of related families; distinction between the\n   members of a family according to their ages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadene', 'n.', 'A species of inferior carpet imported from the Levant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadent', 'a.', 'Falling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadenza', 'n.', 'A parenthetic flourish or flight of ornament in the course\n   of a piece, commonly just before the final cadence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cader', 'n.', 'See Cadre.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadet', 'n.', 'The younger of two brothers; a younger brother or son; the\n   youngest son.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadet', 'n.', 'A gentleman who carries arms in a regiment, as a volunteer,\n   with a view of acquiring military skill and obtaining a commission.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadet', 'n.', 'A young man in training for military or naval service; esp.\n   a pupil in a military or naval school, as at West Point, Annapolis, or\n   Woolwich.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadetship', 'n.', 'The position, rank, or commission of a cadet; as, to get\n   a cadetship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadew', 'n.', 'Alt. of Cadeworm');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadeworm', 'n.', 'A caddice. See Caddice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadged', 'imp. & p. p.', 'of Cadge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadging', 'p. pr. & vb. n.', 'of Cadge');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadge', 'v. t. & i.', 'To carry, as a burden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadge', 'v. t. & i.', 'To hawk or peddle, as fish, poultry, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadge', 'v. t. & i.', 'To intrude or live on another meanly; to beg.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadge', 'n.', 'A circular frame on which cadgers carry hawks for sale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadger', 'v. t.', 'A packman or itinerant huckster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadger', 'v. t.', 'One who gets his living by trickery or begging.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadger', 'n.', 'One who carries hawks on a cadge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadgy', 'a.', 'Cheerful or mirthful, as after good eating or drinking;\n   also, wanton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadi', 'n.', 'An inferior magistrate or judge among the Mohammedans,\n   usually the judge of a town or village.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadie', 'n.', 'Alt. of Caddie');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caddie', 'n.', 'A Scotch errand boy, porter, or messenger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadilesker', 'n.', 'A chief judge in the Turkish empire, so named\n   originally because his jurisdiction extended to the cases of soldiers,\n   who are now tried only by their own officers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadillac', 'n.', 'A large pear, shaped like a flattened top, used chiefly\n   for cooking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadis', 'n.', 'A kind of coarse serge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadmean', 'a.', 'Of or pertaining to Cadmus, a fabulous prince of Thebes,\n   who was said to have introduced into Greece the sixteen simple letters\n   of the alphabet -- /, /, /, /, /, /, /, /, /, /, /, /, /, /, /, /.\n   These are called Cadmean letters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadmia', 'n.', 'An oxide of zinc which collects on the sides of furnaces\n   where zinc is sublimed. Formerly applied to the mineral calamine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadmian', 'a.', 'See Cadmean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadmic', 'a.', 'Pertaining to, derived from, or containing, cadmium; as,\n   cadmic sulphide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadmium', 'n.', 'A comparatively rare element related to zinc, and\n   occurring in some zinc ores. It is a white metal, both ductile and\n   malleable. Symbol Cd. Atomic weight 111.8. It was discovered by\n   Stromeyer in 1817, who named it from its association with zinc or zinc\n   ore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadrans', 'n.', 'An instrument with a graduated disk by means of which the\n   angles of gems are measured in the process of cutting and polishing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cadre', 'n.', 'The framework or skeleton upon which a regiment is to be\n   formed; the officers of a regiment forming the staff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caducary', 'a.', 'Relating to escheat, forfeiture, or confiscation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caducean', 'a.', 'Of or belonging to Mercurys caduceus, or wand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caduceus', 'n.', 'The official staff or wand of Hermes or Mercury, the\n   messenger of the gods. It was originally said to be a heralds staff of\n   olive wood, but was afterwards fabled to have two serpents coiled about\n   it, and two wings at the top.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caducibranchiate', 'a.', 'With temporary gills: -- applied to those\n   Amphibia in which the gills do not remain in adult life.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caducity', 'n.', 'Tendency to fall; the feebleness of old age; senility.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caducous', '', 'Dropping off or disappearing early, as the calyx of a\n   poppy, or the gills of a tadpole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caduke', 'a.', 'Perishable; frail; transitory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cady', 'n.', 'See Cadie.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caeca', 'n. pl.', 'See Caecum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecal', 'a.', 'Of or pertaining to the caecum, or blind gut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecal', 'a.', 'Having the form of a caecum, or bag with one opening;\n   baglike; as, the caecal extremity of a duct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecias', 'n.', 'A wind from the northeast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecilian', 'n.', 'A limbless amphibian belonging to the order Caeciliae or\n   Ophimorpha. See Ophiomorpha.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecums', 'pl. ', 'of Caecum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caeca', 'pl. ', 'of Caecum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecum', 'n.', 'A cavity open at one end, as the blind end of a canal or\n   duct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caecum', 'n.', 'The blind part of the large intestine beyond the entrance\n   of the small intestine; -- called also the blind gut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caenozoic', 'a.', 'See Cenozoic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caen stone', '', 'A cream-colored limestone for building, found near Caen,\n   France.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesar', 'n.', 'A Roman emperor, as being the successor of Augustus Caesar.\n   Hence, a kaiser, or emperor of Germany, or any emperor or powerful\n   ruler. See Kaiser, Kesar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesarean', 'a.', 'Alt. of Caesarian');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesarian', 'a.', 'Of or pertaining to Caesar or the Caesars; imperial.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesarism', 'n.', 'A system of government in which unrestricted power is\n   exercised by a single person, to whom, as Caesar or emperor, it has\n   been committed by the popular will; imperialism; also, advocacy or\n   support of such a system of government.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesious', 'a.', 'Of the color of lavender; pale blue with a slight mixture\n   of gray.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesium', 'n.', 'A rare alkaline metal found in mineral water; -- so called\n   from the two characteristic blue lines in its spectrum. It was the\n   first element discovered by spectrum analysis, and is the most strongly\n   basic and electro-positive substance known. Symbol Cs. Atomic weight\n   132.6.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caespitose', 'a.', 'Same as Cespitose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesuras', 'pl. ', 'of Caesura');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesurae', 'pl. ', 'of Caesura');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesura', 'n.', 'A metrical break in a verse, occurring in the middle of a\n   foot and commonly near the middle of the verse; a sense pause in the\n   middle of a foot. Also, a long syllable on which the caesural accent\n   rests, or which is used as a foot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caesural', 'a.', 'Of or pertaining to a caesura.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cafe', 'n.', 'A coffeehouse; a restaurant; also, a room in a hotel or\n   restaurant where coffee and liquors are served.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cafenet', 'n.', 'Alt. of Cafeneh');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cafeneh', 'n.', 'A humble inn or house of rest for travelers, where coffee\n   is sold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caffeic', 'a.', 'Pertaining to, or obtained from, coffee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caffeine', 'n.', 'A white, bitter, crystallizable substance, obtained from\n   coffee. It is identical with the alkaloid theine from tea leaves, and\n   with guaranine from guarana.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caffetannic', 'a.', 'Pertaining to, or derived from, the tannin of coffee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caffila', 'n.', 'See Cafila.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caffre', 'n.', 'See Kaffir.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cafila', 'n.', 'Alt. of Cafileh');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cafileh', 'n.', 'A caravan of travelers; a military supply train or\n   government caravan; a string of pack horses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caftan', 'n.', 'A garment worn throughout the Levant, consisting of a long\n   gown with sleeves reaching below the hands. It is generally fastened by\n   a belt or sash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caftan', 'v. t.', 'To clothe with a caftan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cag', 'n.', 'See Keg.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'A box or inclosure, wholly or partly of openwork, in wood or\n   metal, used for confining birds or other animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'A place of confinement for malefactors');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'An outer framework of timber, inclosing something within it;\n   as, the cage of a staircase.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'A skeleton frame to limit the motion of a loose piece, as a\n   ball valve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'A wirework strainer, used in connection with pumps and pipes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'The box, bucket, or inclosed platform of a lift or elevator;\n   a cagelike structure moving in a shaft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'The drum on which the rope is wound in a hoisting whim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'n.', 'The catchers wire mask.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caged', 'imp. & p. p.', 'of Cage');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caging', 'p. pr. & vb. n.', 'of Cage');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cage', 'v. i.', 'To confine in, or as in, a cage; to shut up or confine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caged', 'a.', 'Confined in, or as in, a cage; like a cage or prison.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cageling', 'n.', 'A bird confined in a cage; esp. a young bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cagit', 'n.', 'A kind of parrot, of a beautiful green color, found in the\n   Philippine Islands.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cagmag', 'n.', 'A tough old goose; hence, coarse, bad food of any kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cagot', 'n.', 'One of a race inhabiting the valleys of the Pyrenees, who\n   until 1793 were political and social outcasts (Christian Pariahs). They\n   are supposed to be a remnant of the Visigoths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cahier', 'n.', 'A number of sheets of paper put loosely together; esp. one\n   of the successive portions of a work printed in numbers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cahier', 'n.', 'A memorial of a body; a report of legislative proceedings,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cahincic', 'a.', 'Pertaining to, or derived from, cahinca, the native name\n   of a species of Brazilian Chiococca, perhaps C. racemosa; as, cahincic\n   acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cahoot', 'n.', 'Partnership; as, to go in cahoot with a person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caimacam', 'n.', 'The governor of a sanjak or district in Turkey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caiman', 'n.', 'See Cayman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cainozoic', 'a.', 'See Cenozic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caique', 'n.', 'A light skiff or rowboat used on the Bosporus; also, a\n   Levantine vessel of larger size.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ca ira', '', 'The refrain of a famous song of the French Revolution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caird', 'n.', 'A traveling tinker; also a tramp or sturdy beggar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cairn', 'n.', 'A rounded or conical heap of stones erected by early\n   inhabitants of the British Isles, apparently as a sepulchral monument.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cairn', 'n.', 'A pile of stones heaped up as a landmark, or to arrest\n   attention, as in surveying, or in leaving traces of an exploring party,\n   etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cairngormstone', '', 'A yellow or smoky brown variety of rock crystal, or\n   crystallized quartz, found esp, in the mountain of Cairngorm, in\n   Scotland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A chest to hold ammunition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A four-wheeled carriage for conveying ammunition,\n   consisting of two parts, a body and a limber. In light field batteries\n   there is one caisson to each piece, having two ammunition boxes on the\n   body, and one on the limber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A chest filled with explosive materials, to be laid in the\n   way of an enemy and exploded on his approach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A water-tight box, of timber or iron within which work is\n   carried on in building foundations or structures below the water level.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A hollow floating box, usually of iron, which serves to\n   close the entrances of docks and basins.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A structure, usually with an air chamber, placed beneath a\n   vessel to lift or float it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caisson', 'n.', 'A sunk panel of ceilings or soffits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caitiff', 'a.', 'Captive; wretched; unfortunate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caitiff', 'a.', 'Base; wicked and mean; cowardly; despicable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caitiff', 'n.', 'A captive; a prisoner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caitiff', 'n.', 'A wretched or unfortunate man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caitiff', 'n.', 'A mean, despicable person; one whose character meanness\n   and wickedness meet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajeput', 'n.', 'See Cajuput.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajoled', 'imp. & p. p.', 'of Cajole');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajoling', 'p. pr. & vb. n.', 'of Cajole');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajole', 'v. i.', 'To deceive with flattery or fair words; to wheedle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajolement', 'n.', 'The act of cajoling; the state of being cajoled;\n   cajolery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajoler', 'n.', 'A flatterer; a wheedler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajoleries', 'pl. ', 'of Cajolery');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajolery', 'n.', 'A wheedling to delude; words used in cajoling; flattery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajuput', 'n.', 'A highly stimulating volatile inflammable oil, distilled\n   from the leaves of an East Indian tree (Melaleuca cajuputi, etc.) It is\n   greenish in color and has a camphoraceous odor and pungent taste.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cajuputene', 'n.', 'A colorless or greenish oil extracted from cajuput.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'n.', 'A small mass of dough baked; especially, a thin loaf from\n   unleavened dough; as, an oatmeal cake; johnnycake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'n.', 'A sweetened composition of flour and other ingredients,\n   leavened or unleavened, baked in a loaf or mass of any size or shape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'n.', 'A thin wafer-shaped mass of fried batter; a griddlecake or\n   pancake; as buckwheat cakes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'n.', 'A mass of matter concreted, congealed, or molded into a solid\n   mass of any form, esp. into a form rather flat than high; as, a cake of\n   soap; an ague cake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'v. i.', 'To form into a cake, or mass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caked', 'imp. & p. p.', 'of Cake');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caking', 'p. pr. & vb. n.', 'of Cake');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'v. i.', 'To concrete or consolidate into a hard mass, as dough in\n   an oven; to coagulate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cake', 'v. i.', 'To cackle as a goose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caking coal', '', 'See Coal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cal', 'n.', 'Wolfram, an ore of tungsten.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calabar', 'n.', 'A district on the west coast of Africa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calabarine', 'n.', 'An alkaloid resembling physostigmine and occurring with\n   it in the calabar bean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calabash', 'n.', 'The common gourd (plant or fruit).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calabash', 'n.', 'The fruit of the calabash tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calabash', 'n.', 'A water dipper, bottle, bascket, or other utensil, made\n   from the dry shell of a calabash or gourd.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calaboose', 'n.', 'A prison; a jail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calade', 'n.', 'A slope or declivity in a manege ground down which a horse\n   is made to gallop, to give suppleness to his haunches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caladium', 'n.', 'A genus of aroideous plants, of which some species are\n   cultivated for their immense leaves (which are often curiously blotched\n   with white and red); and others (in Polynesia) for food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calaite', 'n.', 'A mineral. See Turquoise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamanco', 'n.', 'A glossy woolen stuff, plain, striped, or checked.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamander wood', '', 'A valuable furniture wood from India and Ceylon, of\n   a hazel-brown color, with black stripes, very hard in texture. It is a\n   species of ebony, and is obtained from the Diospyros quaesita. Called\n   also Coromandel wood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamar', 'n.', 'Alt. of Calamary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamary', 'n.', 'A cephalopod, belonging to the genus Loligo and related\n   genera. There are many species. They have a sack of inklike fluid which\n   they discharge from the siphon tube, when pursued or alarmed, in order\n   to confuse their enemies. Their shell is a thin horny plate, within the\n   flesh of the back, shaped very much like a quill pen. In America they\n   are called squids. See Squid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calambac', 'n.', 'A fragrant wood; agalloch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calambour', 'n.', 'A species of agalloch, or aloes wood, of a dusky or\n   mottled color, of a light, friable texture, and less fragrant than\n   calambac; -- used by cabinetmakers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamiferous', 'a.', 'Producing reeds; reedy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamine', 'n.', 'A mineral, the hydrous silicate of zinc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamint', 'n.', 'A genus of perennial plants (Calamintha) of the Mint\n   family, esp. the C. Nepeta and C. Acinos, which are called also basil\n   thyme.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamist', 'n.', 'One who plays upon a reed or pipe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamistrate', 'v. i.', 'To curl or friz, as the hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamistration', 'n.', 'The act or process of curling the hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamistrum', 'n.', 'A comblike structure on the metatarsus of the hind\n   legs of certain spiders (Ciniflonidae); used to curl certain fibers in\n   the construction of their webs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamite', 'n.', 'A fossil plant of the coal formation, having the general\n   form of plants of the modern Equiseta (the Horsetail or Scouring Rush\n   family) but sometimes attaining the height of trees, and having the\n   stem more or less woody within. See Acrogen, and Asterophyllite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamitous', 'a.', 'Suffering calamity; wretched; miserable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamitous', 'a.', 'Producing, or attended with distress and misery; making\n   wretched; wretched; unhappy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamities', 'pl. ', 'of Calamity');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamity', 'n.', 'Any great misfortune or cause of misery; -- generally\n   applied to events or disasters which produce extensive evil, either to\n   communities or individuals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamity', 'n.', 'A state or time of distress or misfortune; misery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calami', 'pl. ', 'of Calamus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamus', 'n.', 'The indian cane, a plant of the Palm family. It furnishes\n   the common rattan. See Rattan, and Dragons blood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamus', 'n.', 'A species of Acorus (A. calamus); commonly called calamus,\n   or sweet flag. The root has a pungent, aromatic taste, and is used in\n   medicine as a stomachic; the leaves have an aromatic odor, and were\n   formerly used instead of rushes to strew on floors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calamus', 'n.', 'The horny basal portion of a feather; the barrel or quill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calando', 'a.', 'Gradually diminishing in rapidity and loudness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calash', 'n.', 'A light carriage with low wheels, having a top or hood that\n   can be raised or lowered, seats for inside, a separate seat for the\n   driver, and often a movable front, so that it can be used as either an\n   open or a close carriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calash', 'n.', 'In Canada, a two-wheeled, one-seated vehicle, with a calash\n   top, and the drivers seat elevated in front.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calash', 'n.', 'A hood or top of a carriage which can be thrown back at\n   pleasure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calash', 'n.', 'A hood, formerly worn by ladies, which could be drawn\n   forward or thrown back like the top of a carriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calaverite', 'n.', 'A bronze-yellow massive mineral with metallic luster; a\n   telluride of gold; -- first found in Calaveras County California.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcaneal', 'a.', 'Pertaining to the calcaneum; as, calcaneal arteries.');
  INSERT INTO words (word, word_type, description)VALUES  (    '-neums', 'pl. ', 'of Calcaneum');
  INSERT INTO words (word, word_type, description)VALUES  (    '-nea', 'pl. ', 'of Calcaneum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcaneum', 'n.', 'One of the bones of the tarsus which in man, forms the\n   great bone of the heel; -- called also fibulare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcar', 'n.', 'A kind of oven, or reverberatory furnace, used for the\n   calcination of sand and potash, and converting them into frit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcaria', 'pl. ', 'of Calcar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcar', 'n.', 'A hollow tube or spur at the base of a petal or corolla.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcar', 'n.', 'A slender bony process from the ankle joint of bats, which\n   helps to support the posterior part of the web, in flight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcar', 'n.', 'A spur, or spurlike prominence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcar', 'n.', 'A curved ridge in the floor of the leteral ventricle of the\n   brain; the calcar avis, hippocampus minor, or ergot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcarate', 'a.', 'Alt. of Calcarated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcarated', 'a.', 'Having a spur, as the flower of the toadflax and\n   larkspur; spurred.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcarated', 'a.', 'Armed with a spur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcareo-argillaceous', 'a.', 'consisting of, or containing, calcareous and\n   argillaceous earths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcareo-bituminous', 'a.', 'Consisting of, or containing, lime and\n   bitumen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcareo-siliceous', 'a.', 'Consisting of, or containing calcareous and\n   siliceous earths.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcareous', 'a.', 'Partaking of the nature of calcite or calcium\n   carbonate; consisting of, or containing, calcium carbonate or carbonate\n   of lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcareousness', 'n.', 'Quality of being calcareous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcariferous', 'a.', 'Lime-yielding; calciferous');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcarine', 'a.', 'Pertaining to, or situated near, the calcar of the\n   brain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcavella', 'n.', 'A sweet wine from Portugal; -- so called from the\n   district of Carcavelhos.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calceated', 'a.', 'Fitted with, or wearing, shoes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calced', 'a.', 'Wearing shoes; calceated; -- in distintion from discalced\n   or barefooted; as the calced Carmelites.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcedon', 'n.', 'A foul vein, like chalcedony, in some precious stones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcedonic', 'a.', 'Alt. of Calcedonian');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcedonian', 'a.', 'See Chalcedonic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calceiform', 'a.', 'Shaped like a slipper, as one petal of the\n   ladys-slipper; calceolate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calceolaria', 'n.', 'A genus of showy herbaceous or shrubby plants, brought\n   from South America; slipperwort. It has a yellow or purple flower,\n   often spotted or striped, the shape of which suggests its name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calceolate', 'a.', 'Slipper-ahaped. See Calceiform.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calces', 'n. pl.', 'See Calx.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcic', 'a.', 'Pertaining to, derived from, or containing, calcium or\n   lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calciferous', 'a.', 'Bearing, producing, or containing calcite, or\n   carbonate of lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcific', 'a.', 'Calciferous. Specifically: (Zool.) of or pertaining to\n   the portion of the oviduct which forms the eggshell in birds and\n   reptiles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcification', 'n.', 'The process of change into a stony or calcareous\n   substance by the deposition of lime salt; -- normally, as in the\n   formation of bone and of teeth; abnormally, as in calcareous\n   degeneration of tissue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcified', 'a.', 'Consisting of, or containing, calcareous matter or lime\n   salts; calcareous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calciform', 'a.', 'In the form of chalk or lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcified', 'imp. & p. p.', 'of Calcify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcifying', 'p. pr. & vb. n.', 'of Calcify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcify', 'v. t.', 'To make stony or calcareous by the deposit or secretion\n   of salts of lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcify', 'v. i.', 'To become changed into a stony or calcareous condition,\n   in which lime is a principal ingredient, as in the formation of teeth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcigenous', 'a.', 'Tending to form, or to become, a calx or earthlike\n   substance on being oxidized or burnt; as magnesium, calcium. etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcigerous', 'a.', 'Holding lime or other earthy salts; as, the\n   calcigerous cells of the teeth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcimine', 'n.', 'A white or colored wash for the ceiling or other\n   plastering of a room, consisting of a mixture of clear glue, Paris\n   white or zinc white, and water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcimined', 'imp. &p. p.', 'of Calcimine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcimining', 'p. pr. & vb. n.', 'of Calcimine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcimine', 'v. t.', 'To wash or cover with calcimine; as, to calcimine\n   walls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calciminer', 'n.', 'One who calcimines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcinable', 'a.', 'That may be calcined; as, a calcinable fossil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcinate', 'v. i.', 'To calcine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcination', 'n.', 'The act or process of disintegrating a substance, or\n   rendering it friable by the action of heat, esp. by the expulsion of\n   some volatile matter, as when carbonic and acid is expelled from\n   carbonate of calcium in the burning of limestone in order to make lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcination', 'n.', 'The act or process of reducing a metal to an oxide or\n   metallic calx; oxidation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcinatory', 'n.', 'A vessel used in calcination.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calciden', 'imp. & p. p.', 'of Calcine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcining', 'p. pr. & vb. n.', 'of Calcine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcine', 'v. i.', 'To reduce to a powder, or to a friable state, by the\n   action of heat; to expel volatile matter from by means of heat, as\n   carbonic acid from limestone, and thus (usually) to produce\n   disintegration; as to, calcine bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcine', 'v. i.', 'To oxidize, as a metal by the action of heat; to reduce\n   to a metallic calx.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcine', 'v. i.', 'To be converted into a powder or friable substance, or\n   into a calx, by the action of heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calciner', 'n.', 'One who, or that which, calcines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcispongiae', 'n. pl.', 'An order of marine sponges, containing\n   calcareous spicules. See Porifera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcite', 'n.', 'Calcium carbonate, or carbonate of lime. It is\n   rhombohedral in its crystallization, and thus distinguished from\n   aragonite. It includes common limestone, chalk, and marble. Called also\n   calc-spar and calcareous spar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcitrant', 'a.', 'Kicking. Hence: Stubborn; refractory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcitrate', 'v. i. & i.', 'To kick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcitration', 'n.', 'Act of kicking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcium', 'n.', 'An elementary substance; a metal which combined with\n   oxygen forms lime. It is of a pale yellow color, tenacious, and\n   malleable. It is a member of the alkaline earth group of elements.\n   Atomic weight 40. Symbol Ca.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcivorous', 'a.', 'Eroding, or eating into, limestone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcographer', 'n.', 'One who practices calcography.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcographic', 'a.', 'Alt. of Calcographical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcographical', 'a.', 'Relating to, or in the style of, calcography.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcography', 'n.', 'The art of drawing with chalk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calc-sinter', 'n.', 'See under Calcite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calc-spar', 'n.', 'Same as Calcite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calc-tufa', 'n.', 'See under Calcite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculable', 'a.', 'That may be calculated or ascertained by calculation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculary', 'a.', 'Of or pertaining to calculi.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculary', 'n.', 'A congeries of little stony knots found in the pulp of\n   the pear and other fruits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculater', 'imp. & p. p.', 'of Calculate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculating', 'p. pr. & vb. n.', 'of Calculate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculate', 'v. i.', 'To ascertain or determine by mathematical processes,\n   usually by the ordinary rules of arithmetic; to reckon up; to estimate;\n   to compute.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculate', 'v. i.', 'To ascertain or predict by mathematical or\n   astrological computations the time, circumstances, or other conditions\n   of; to forecast or compute the character or consequences of; as, to\n   calculate or cast ones nativity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculate', 'v. i.', 'To adjust for purpose; to adapt by forethought or\n   calculation; to fit or prepare by the adaptation of means to an end;\n   as, to calculate a system of laws for the government and protection of\n   a free people.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculate', 'v. i.', 'To plan; to expect; to think.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculate', 'v. i.', 'To make a calculation; to forecast consequences; to\n   estimate; to compute.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculated', 'p. p. & a.', 'Worked out by calculation; as calculated tables\n   for computing interest; ascertained or conjectured as a result of\n   calculation; as, the calculated place of a planet; the calculated\n   velocity of a cannon ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculated', 'p. p. & a.', 'Adapted by calculation, contrivance. or\n   forethought to accomplish a purpose; as, to use arts calculated to\n   deceive the people.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculated', 'p. p. & a.', 'Likely to produce a certain effect, whether\n   intended or not; fitted; adapted; suited.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculating', 'a.', 'Of or pertaining to mathematical calculations;\n   performing or able to perform mathematical calculations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculating', 'a.', 'Given to contrivance or forethought; forecasting;\n   scheming; as, a cool calculating disposition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculating', 'n.', 'The act or process of making mathematical computations\n   or of estimating results.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculation', 'n.', 'The act or process, or the result, of calculating;\n   computation; reckoning, estimate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculation', 'n.', 'An expectation based on circumstances.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculative', 'a.', 'Of or pertaining to calculation; involving\n   calculation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculator', 'n.', 'One who computes or reckons: one who estimates or\n   considers the force and effect of causes, with a view to form a correct\n   estimate of the effects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculatory', 'a.', 'Belonging to calculation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcule', 'n.', 'Reckoning; computation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calcule', 'v. i.', 'To calculate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculi', 'n. pl.', 'See Calculus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculous', 'a.', 'Of the nature of a calculus; like stone; gritty; as, a\n   calculous concretion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculous', 'a.', 'Caused, or characterized, by the presence of a calculus\n   or calculi; a, a calculous disorder; affected with gravel or stone; as,\n   a calculous person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculi', 'pl. ', 'of Calculus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculus', 'n.', 'Any solid concretion, formed in any part of the body, but\n   most frequent in the organs that act as reservoirs, and in the passages\n   connected with them; as, biliary calculi; urinary calculi, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calculus', 'n.', 'A method of computation; any process of reasoning by the\n   use of symbols; any branch of mathematics that may involve calculation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caldron', 'n.', 'A large kettle or boiler of copper, brass, or iron.\n   [Written also cauldron.]');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caleche', 'n.', 'See Calash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caledonia', 'n.', 'The ancient Latin name of Scotland; -- still used in\n   poetry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caledonian', 'a.', 'Of or pertaining to Caledonia or Scotland; Scottish;\n   Scotch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caledonian', 'n.', 'A native or inhabitant of Caledonia or Scotland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caledonite', 'n.', 'A hydrous sulphate of copper and lead, found in some\n   parts of Caledonia or Scotland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefacient', 'a.', 'Making warm; heating.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefacient', 'n.', 'A substance that excites warmth in the parts to which\n   it is applied, as mustard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefaction', 'n.', 'The act of warming or heating; the production of heat\n   in a body by the action of fire, or by communication of heat from other\n   bodies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefaction', 'n.', 'The state of being heated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefactive', 'a.', 'See Calefactory.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefactor', 'n.', 'A heater; one who, or that which, makes hot, as a\n   stove, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefactory', 'a.', 'Making hot; producing or communicating heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefactory', 'n.', 'An apartment in a monastery, warmed and used as a\n   sitting room.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefactory', 'n.', 'A hollow sphere of metal, filled with hot water, or a\n   chafing dish, placed on the altar in cold weather for the priest to\n   warm his hands with.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefied', 'imp. & p. p.', 'of Calefy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefying', 'p. pr. & vb. n.', 'of Calefy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefy', 'v. i.', 'To make warm or hot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calefy', 'v. i.', 'To grow hot or warm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calembour', 'n.', 'A pun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendar', 'n.', 'An orderly arrangement of the division of time, adapted\n   to the purposes of civil life, as years, months, weeks, and days; also,\n   a register of the year with its divisions; an almanac.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendar', 'n.', 'A tabular statement of the dates of feasts, offices,\n   saints days, etc., esp. of those which are liable to change yearly\n   according to the varying date of Easter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendar', 'n.', 'An orderly list or enumeration of persons, things, or\n   events; a schedule; as, a calendar of state papers; a calendar of bills\n   presented in a legislative assembly; a calendar of causes arranged for\n   trial in court; a calendar of a college or an academy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendared', 'imp. & p. p.', 'of Calendar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendaring', 'p. pr. & vb. n.', 'of Calendar');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendar', 'v. t.', 'To enter or write in a calendar; to register.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendarial', 'a.', 'Of or pertaining to the calendar or a calendar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendary', 'a.', 'Calendarial.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calender', 'n.', 'A machine, used for the purpose of giving cloth, paper,\n   etc., a smooth, even, and glossy or glazed surface, by cold or hot\n   pressure, or for watering them and giving them a wavy appearance. It\n   consists of two or more cylinders revolving nearly in contact, with the\n   necessary apparatus for moving and regulating.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calender', 'n.', 'One who pursues the business of calendering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendered', 'imp. & p. p.', 'of Calender');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendering', 'p. pr. & vb. n.', 'of Calender');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calender', 'n.', 'To press between rollers for the purpose of making smooth\n   and glossy, or wavy, as woolen and silk stuffs, linens, paper, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calender', 'n.', 'One of a sect or order of fantastically dressed or\n   painted dervishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendographer', 'n.', 'One who makes calendars.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendrer', 'n.', 'A person who calenders cloth; a calender.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendric', 'a.', 'Alt. of Calendrical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendrical', 'a.', 'Of or pertaining to a calendar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calends', 'n. pl.', 'The first day of each month in the ancient Roman\n   calendar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendula', 'n.', 'A genus of composite herbaceous plants. One species,\n   Calendula officinalis, is the common marigold, and was supposed to\n   blossom on the calends of every month, whence the name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calendulin', 'n.', 'A gummy or mucilaginous tasteless substance obtained\n   from the marigold or calendula, and analogous to bassorin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calenture', 'n.', 'A name formerly given to various fevers occuring in\n   tropics; esp. to a form of furious delirium accompanied by fever, among\n   sailors, which sometimes led the affected person to imagine the sea to\n   be a green field, and to throw himself into it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calenture', 'v. i.', 'To see as in the delirium of one affected with\n   calenture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calescence', 'n.', 'Growing warmth; increasing heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calves', 'pl. ', 'of Calf');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calf', 'n.', 'The young of the cow, or of the Bovine family of quadrupeds.\n   Also, the young of some other mammals, as of the elephant, rhinoceros,\n   hippopotamus, and whale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calf', 'n.', 'Leather made of the skin of the calf; especially, a fine,\n   light-colored leather used in bookbinding; as, to bind books in calf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calf', 'n.', 'An awkward or silly boy or young man; any silly person; a\n   dolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calf', 'n.', 'A small island near a larger; as, the Calf of Man.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calf', 'n.', 'A small mass of ice set free from the submerged part of a\n   glacier or berg, and rising to the surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calf', 'n.', 'The fleshy hinder part of the leg below the knee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calfskin', 'n.', 'The hide or skin of a calf; or leather made of the skin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cali', 'n.', 'The tenth avatar or incarnation of the god Vishnu.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliber', 'n.', 'Alt. of Calibre');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calibre', 'n.', 'The diameter of the bore, as a cannon or other firearm, or\n   of any tube; or the weight or size of the projectile which a firearm\n   will carry; as, an 8 inch gun, a 12-pounder, a 44 caliber.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calibre', 'n.', 'The diameter of round or cylindrical body, as of a bullet\n   or column.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calibre', 'n.', 'Fig.: Capacity or compass of mind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calibrate', 'v. i.', 'To ascertain the caliber of, as of a thermometer\n   tube; also, more generally, to determine or rectify the graduation of,\n   as of the various standards or graduated instruments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calibration', 'n.', 'The process of estimating the caliber a tube, as of a\n   thermometer tube, in order to graduate it to a scale of degrees; also,\n   more generally, the determination of the true value of the spaces in\n   any graduated instrument.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calice', 'n.', 'See Chalice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calicle', 'n.', 'One of the small cuplike cavities, often with elevated\n   borders, covering the surface of most corals. Each is formed by a\n   polyp. (b) One of the cuplike structures inclosing the zooids of\n   certain hydroids. See Campanularian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calicoes', 'pl. ', 'of Calico');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calico', 'n.', 'Plain white cloth made from cotton, but which receives\n   distinctive names according to quality and use, as, super calicoes,\n   shirting calicoes, unbleached calicoes, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calico', 'n.', 'Cotton cloth printed with a figured pattern.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calico', 'a.', 'Made of, or having the appearance of, calico; -- often\n   applied to an animal, as a horse or cat, on whose body are large\n   patches of a color strikingly different from its main color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calicoback', 'n.', 'The calico bass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calicoback', 'n.', 'An hemipterous insect (Murgantia histrionica) which\n   injures the cabbage and other garden plants; -- called also calico bug\n   and harlequin cabbage bug.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calicular', 'a.', 'Alt. of Caliculate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliculate', 'a.', 'Relating to, or resembling, a cup; also improperly used\n   for calycular, calyculate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calid', 'a.', 'Hot; burning; ardent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calidity', 'n.', 'Heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliduct', 'n.', 'A pipe or duct used to convey hot air or steam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calif', 'n.', 'Alt. of Califate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Califate', 'n.', 'Same as Caliph, Caliphate, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Californian', 'a.', 'Of or pertaining to California.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Californian', 'n.', 'A native or inhabitant of California.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caligation', 'n.', 'Dimness; cloudiness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliginosity', 'n.', 'Darkness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliginous', 'a.', 'Affected with darkness or dimness; dark; obscure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caligo', 'n.', 'Dimness or obscurity of sight, dependent upon a speck on\n   the cornea; also, the speck itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caligraphic', 'a.', 'See Calligraphic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caligraphy', 'n.', 'See Caligraphy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calin', 'n.', 'An alloy of lead and tin, of which the Chinese make tea\n   canisters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calipash', 'n.', 'A part of a turtle which is next to the upper shell. It\n   contains a fatty and gelatinous substance of a dull greenish tinge,\n   much esteemed as a delicacy in preparations of turtle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calipee', 'n.', 'A part of a turtle which is attached to the lower shell.\n   It contains a fatty and gelatinous substance of a light yellowish\n   color, much esteemed as a delicacy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calipers', 'n. pl.', 'An instrument, usually resembling a pair of dividers\n   or compasses with curved legs, for measuring the diameter or thickness\n   of bodies, as of work shaped in a lathe or planer, timber, masts, shot,\n   etc.; or the bore of firearms, tubes, etc.; -- called also caliper\n   compasses, or caliber compasses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliph', 'n.', 'Successor or vicar; -- a title of the successors of\n   Mohammed both as temporal and spiritual rulers, now used by the sultans\n   of Turkey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliphate', 'n.', 'The office, dignity, or government of a caliph or of the\n   caliphs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calippic', 'a.', 'Of or pertaining to Calippus, an Athenian astronomer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calisaya bark', '', 'A valuable kind of Peruvian bark obtained from the\n   Cinchona Calisaya, and other closely related species.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calistheneum', 'n.', 'A gymnasium; esp. one for light physical exercise by\n   women and children.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calisthenis', 'a.', 'Of or pertaining to calisthenics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calisthenics', 'n.', 'The science, art, or practice of healthful exercise\n   of the body and limbs, to promote strength and gracefulness; light\n   gymnastics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caliver', 'n.', 'An early form of hand gun, variety of the arquebus;\n   originally a gun having a regular size of bore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calix', 'n.', 'A cup. See Calyx.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calked', 'imp. &p. p.', 'of Calk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calking', 'p. pr. & vb. n.', 'of Calk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'v. t.', 'To drive tarred oakum into the seams between the planks of\n   (a ship, boat, etc.); to prevent leaking. The calking is completed by\n   smearing the seams with melted pitch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'v. t.', 'To make an indentation in the edge of a metal plate, as\n   along a seam in a steam boiler or an iron ship, to force the edge of\n   the upper plate hard against the lower and so fill the crevice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'v. t.', 'To copy, as a drawing, by rubbing the back of it with red\n   or black chalk, and then passing a blunt style or needle over the\n   lines, so as to leave a tracing on the paper or other thing against\n   which it is laid or held.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'n.', 'A sharp-pointed piece of iron or steel projecting downward on\n   the shoe of a horse or an ox, to prevent the animal from slipping; --\n   called also calker, calkin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'n.', 'An instrument with sharp points, worn on the sole of a shoe\n   or boot, to prevent slipping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'v. i.', 'To furnish with calks, to prevent slipping on ice; as, to\n   calk the shoes of a horse or an ox.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calk', 'v. i.', 'To wound with a calk; as when a horse injures a leg or a\n   foot with a calk on one of the other feet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calker', 'n.', 'One who calks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calker', 'n.', 'A calk on a shoe. See Calk, n., 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calkin', 'n.', 'A calk on a shoe. See Calk, n., 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calking', 'n.', 'The act or process of making seems tight, as in ships, or\n   of furnishing with calks, as a shoe, or copying, as a drawing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Called', 'imp. & p. p.', 'of Call');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'p. pr. & vb. n.', 'of Call');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To command or request to come or be present; to summon;\n   as, to call a servant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To summon to the discharge of a particular duty; to\n   designate for an office, or employment, especially of a religious\n   character; -- often used of a divine summons; as, to be called to the\n   ministry; sometimes, to invite; as, to call a minister to be the pastor\n   of a church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To invite or command to meet; to convoke; -- often with\n   together; as, the President called Congress together; to appoint and\n   summon; as, to call a meeting of the Board of Aldermen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To give name to; to name; to address, or speak of, by a\n   specifed name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To regard or characterize as of a certain kind; to\n   denominate; to designate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To state, or estimate, approximately or loosely; to\n   characterize without strict regard to fact; as, they call the distance\n   ten miles; he called it a full days work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To show or disclose the class, character, or nationality\n   of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To utter in a loud or distinct voice; -- often with off;\n   as, to call, or call off, the items of an account; to call the roll of\n   a military company.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To invoke; to appeal to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. t.', 'To rouse from sleep; to awaken.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. i.', 'To speak in loud voice; to cry out; to address by name; --\n   sometimes with to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. i.', 'To make a demand, requirement, or request.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'v. i.', 'To make a brief visit; also, to stop at some place\n   designated, as for orders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'The act of calling; -- usually with the voice, but often\n   otherwise, as by signs, the sound of some instrument, or by writing; a\n   summons; an entreaty; an invitation; as, a call for help; the bugles\n   call.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A signal, as on a drum, bugle, trumpet, or pipe, to summon\n   soldiers or sailors to duty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'An invitation to take charge of or serve a church as its\n   pastor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A requirement or appeal arising from the circumstances of the\n   case; a moral requirement or appeal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A divine vocation or summons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'Vocation; employment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A short visit; as, to make a call on a neighbor; also, the\n   daily coming of a tradesman to solicit orders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A note blown on the horn to encourage the hounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A whistle or pipe, used by the boatswain and his mate, to\n   summon the sailors to duty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'The cry of a bird; also a noise or cry in imitation of a\n   bird; or a pipe to call birds by imitating their note or cry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'A reference to, or statement of, an object, course, distance,\n   or other matter of description in a survey or grant requiring or\n   calling for a corresponding object, etc., on the land.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'The privilege to demand the delivery of stock, grain, or any\n   commodity, at a fixed, price, at or within a certain time agreed on.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Call', 'n.', 'See Assessment, 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calla', 'n.', 'A genus of plants, of the order Araceae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callat', 'n.', 'Same as Callet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calle', 'n.', 'A kind of head covering; a caul.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caller', 'n.', 'One who calls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caller', 'a.', 'Cool; refreshing; fresh; as, a caller day; the caller air.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caller', 'a.', 'Fresh; in good condition; as, caller berrings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callet', 'n.', 'A trull or prostitute; a scold or gossip.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callet', 'v. i.', 'To rail or scold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callid', 'a.', 'Characterized by cunning or shrewdness; crafty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callidity', 'n.', 'Acuteness of discernment; cunningness; shrewdness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calligrapher', 'n.', 'One skilled in calligraphy; a good penman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calligraphic', 'a.', 'Alt. of Calligraphical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calligraphical', 'a.', 'Of or pertaining to calligraphy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calligraphist', 'n.', 'A calligrapher');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calligraphy', 'n.', 'Fair or elegant penmanship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'The act of one who calls; a crying aloud, esp. in order to\n   summon, or to attact the attention of, some one.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'A summoning or convocation, as of Parliament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'A divine summons or invitation; also, the state of being\n   divinely called.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'A naming, or inviting; a reading over or reciting in\n   order, or a call of names with a view to obtaining an answer, as in\n   legislative bodies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'Ones usual occupation, or employment; vocation; business;\n   trade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'The persons, collectively, engaged in any particular\n   professions or employment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calling', 'n.', 'Title; appellation; name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calliope', 'n.', 'The Muse that presides over eloquence and heroic poetry;\n   mother of Orpheus, and chief of the nine Muses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calliope', 'n.', 'One of the asteroids. See Solar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calliope', 'n.', 'A musical instrument consisting of a series of steam\n   whistles, toned to the notes of the scale, and played by keys arranged\n   like those of an organ. It is sometimes attached to steamboat boilers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calliope', 'n.', 'A beautiful species of humming bird (Stellula Calliope)\n   of California and adjacent regions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calliopsis', 'n.', 'A popular name given to a few species of the genus\n   Coreopsis, especially to C. tinctoria of Arkansas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callipash', 'n.', 'See Calipash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callipee', 'n.', 'See Calipee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callipers', 'n. pl.', 'See Calipers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callisection', 'n.', 'Painless vivisection; -- opposed to sentisection.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callisthenic', 'n.', 'Alt. of Callisthenics');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callisthenics', 'n.', 'See Calisthenic, Calisthenics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callithump', 'n.', 'A somewhat riotous parade, accompanied with the blowing\n   of tin horns, and other discordant noises; also, a burlesque serenade;\n   a charivari.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callithumpian', 'a.', 'Of, pertaining to, or resembling, a callithump.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callosan', 'a.', 'Of the callosum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callose', 'a.', 'Furnished with protuberant or hardened spots.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callosities', 'pl. ', 'of Callosity');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callosity', 'n.', 'A hard or thickened spot or protuberance; a hardening\n   and thickening of the skin or bark of a part, eps. as a result of\n   continued pressure or friction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callosum', 'n.', 'The great band commissural fibers which unites the two\n   cerebral hemispheres. See corpus callosum, under Carpus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callot', 'n.', 'A plant coif or skullcap. Same as Calotte.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callous', 'a.', 'Hardened; indurated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callous', 'a.', 'Hardened in mind; insensible; unfeeling; unsusceptible.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callow', 'a.', 'Destitute of feathers; naked; unfledged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callow', 'a.', 'Immature; boyish; \"green\"; as, a callow youth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callow', 'n.', 'A kind of duck. See Old squaw.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callus', 'n.', 'Same as Callosity');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callus', 'n.', 'The material of repair in fractures of bone; a substance\n   exuded at the site of fracture, which is at first soft or cartilaginous\n   in consistence, but is ultimately converted into true bone and unites\n   the fragments into a single piece.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callus', 'n.', 'The new formation over the end of a cutting, before it puts\n   out rootlets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calm', 'n.', 'Freedom from motion, agitation, or disturbance; a cessation\n   or absence of that which causes motion or disturbance, as of winds or\n   waves; tranquility; stillness; quiet; serenity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calmed', 'imp. & p. p.', 'of Calm');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calming', 'p. pr. & vb. n.', 'of Calm');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calm', 'n.', 'To make calm; to render still or quiet, as elements; as, to\n   calm the winds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calm', 'n.', 'To deliver from agitation or excitement; to still or soothe,\n   as the mind or passions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calm', 'super.', 'Not stormy; without motion, as of winds or waves; still;\n   quiet; serene; undisturbed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calm', 'super.', 'Undisturbed by passion or emotion; not agitated or\n   excited; tranquil; quiet in act or speech.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calmer', 'n.', 'One who, or that which, makes calm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calmly', 'adv.', 'In a calm manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calmness', 'n.', 'The state of quality of being calm; quietness;\n   tranquillity; self-repose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calmucks', 'n. pl.', 'A branch of the Mongolian race inhabiting parts of\n   the Russian and Chinese empires; also (sing.); the language of the\n   Calmucks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calmy', 'n.', 'Tranquil; peaceful; calm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calomel', 'n.', 'Mild chloride of mercury, Hg2Cl2, a heavy, white or\n   yellowish white substance, insoluble and tasteless, much used in\n   medicine as a mercurial and purgative; mercurous chloride. It occurs\n   native as the mineral horn quicksilver.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorescence', 'n.', 'The conversion of obscure radiant heat into light;\n   the transmutation of rays of heat into others of higher refrangibility.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caloric', 'n.', 'The principle of heat, or the agent to which the phenomena\n   of heat and combustion were formerly ascribed; -- not now used in\n   scientific nomenclature, but sometimes used as a general term for heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caloric', 'a.', 'Of or pertaining to caloric.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caloricity', 'n.', 'A faculty in animals of developing and preserving the\n   heat necessary to life, that is, the animal heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caloriduct', 'n.', 'A tube or duct for conducting heat; a caliduct.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorie', 'n.', 'The unit of heat according to the French standard; the\n   amount of heat required to raise the temperature of one kilogram\n   (sometimes, one gram) of water one degree centigrade, or from 0¡ to 1¡.\n   Compare the English standard unit, Foot pound.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorifacient', 'a.', 'See Calorificient.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorifere', 'n.', 'An apparatus for conveying and distributing heat,\n   especially by means of hot water circulating in tubes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorifiant', 'a.', 'See Calorificient.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorific', 'a.', 'Possessing the quality of producing heat; heating.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorification', 'n.', 'Production of heat, esp. animal heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorificient', 'a.', 'Having, or relating to the power of producing heat;\n   -- applied to foods which, being rich in carbon, as the fats, are\n   supposed to give rise to heat in the animal body by oxidation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorimeter', 'n.', 'An apparatus for measuring the amount of heat\n   contained in bodies or developed by some mechanical or chemical\n   process, as friction, chemical combination, combustion, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorimeter', 'n.', 'An apparatus for measuring the proportion of\n   unevaporated water contained in steam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorimetric', 'a.', 'Of or pertaining to the process of using the\n   calorimeter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorimetry', 'n.', 'Measurement of the quantities of heat in bodies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calorimotor', 'n.', 'A voltaic battery, having a large surface of plate,\n   and producing powerful heating effects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calotte', 'n.', 'Alt. of Callot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callot', 'n.', 'A close cap without visor or brim.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callot', 'n.', 'Such a cap, worn by English serjeants at law.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callot', 'n.', 'Such a cap, worn by the French cavalry under their helmets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callot', 'n.', 'Such a cap, worn by the clergy of the Roman Catholic\n   Church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calotype', 'n.', 'A method of taking photographic pictures, on paper\n   sensitized with iodide of silver; -- also called Talbotype, from the\n   inventor, Mr. Fox. Talbot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caloyer', 'n.', 'A monk of the Greek Church; a cenobite, anchoret, or\n   recluse of the rule of St. Basil, especially, one on or near Mt. Athos.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calque', 'v. t.', 'See 2d Calk, v. t.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caltrop', 'n.', 'Alt. of Caltrap');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caltrap', 'n.', 'A genus of herbaceous plants (Tribulus) of the order\n   Zygophylleae, having a hard several-celled fruit, armed with stout\n   spines, and resembling the military instrument of the same name. The\n   species grow in warm countries, and are often very annoying to cattle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caltrap', 'n.', 'An instrument with four iron points, so disposed that, any\n   three of them being on the ground, the other projects upward. They are\n   scattered on the ground where an enemys cavalry are to pass, to impede\n   their progress by endangering the horses feet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumba', 'n.', 'The root of a plant (Jateorrhiza Calumba, and probably\n   Cocculus palmatus); indigenous in Mozambique. It has an unpleasantly\n   bitter taste, and is used as a tonic and antiseptic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumbin', 'n.', 'A bitter principle extracted as a white crystalline\n   substance from the calumba root.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumet', 'n.', 'A kind of pipe, used by the North American Indians for\n   smoking tobacco. The bowl is usually made of soft red stone, and the\n   tube is a long reed often ornamented with feathers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniated', 'imp. & p. p.', 'of Calumniate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniating', 'p. pr. & vb. n.', 'of Calumniate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniate', 'v. t.', 'To accuse falsely and maliciously of a crime or\n   offense, or of something disreputable; to slander; to libel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniate', 'v. i.', 'To propagate evil reports with a design to injure\n   the reputation of another; to make purposely false charges of some\n   offense or crime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniation', 'n.', 'False accusation of crime or offense, or a malicious\n   and false representation of the words or actions of another, with a\n   view to injure his good name.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniator', 'n.', 'One who calumniates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumniatory', 'a.', 'Containing calumny; slanderous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumnious', 'a.', 'Containing or implying calumny; false, malicious, and\n   injurious to reputation; slanderous; as, calumnious reports.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumnies', 'pl. ', 'of Calumny');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calumny', 'n.', 'False accusation of a crime or offense, maliciously made\n   or reported, to the injury of another; malicious misrepresentation;\n   slander; detraction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvaria', 'n.', 'The bones of the cranium; more especially, the bones of\n   the domelike upper portion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvary', 'n.', 'The place where Christ was crucified, on a small hill\n   outside of Jerusalem.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvary', 'n.', 'A representation of the crucifixion, consisting of three\n   crosses with the figures of Christ and the thieves, often as large as\n   life, and sometimes surrounded by figures of other personages who were\n   present at the crucifixion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvary', 'n.', 'A cross, set upon three steps; -- more properly called\n   cross calvary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calved', 'imp. & p. p.', 'of Calve');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calving', 'p. pr. & vb. n.', 'of Calve');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calve', 'v. i.', 'To bring forth a calf.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calve', 'v. i.', 'To bring forth young; to produce offspring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calver', 'v. i.', 'To cut in slices and pickle, as salmon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calver', 'v. i.', 'To crimp; as, calvered salmon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calver', 'v. i.', 'To bear, or be susceptible of, being calvered; as,\n   graylings flesh will calver.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvessnout', 'n.', 'Snapdragon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvinism', 'n.', 'The theological tenets or doctrines of John Calvin (a\n   French theologian and reformer of the 16th century) and his followers,\n   or of the so-called calvinistic churches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvinist', 'n.', 'A follower of Calvin; a believer in Calvinism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvinistic', 'a.', 'Alt. of Calvinistical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvinistical', 'a.', 'Of or pertaining to Calvin, or Calvinism; following\n   Calvin; accepting or Teaching Calvinism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvinize', 'v. t.', 'To convert to Calvinism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calvish', 'a.', 'Like a calf; stupid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calxes', 'pl. ', 'of Calx');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calces', 'pl. ', 'of Calx');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calx', 'n.', 'Quicklime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calx', 'n.', 'The substance which remains when a metal or mineral has been\n   subjected to calcination or combustion by heat, and which is, or may\n   be, reduced to a fine powder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calx', 'n.', 'Broken and refuse glass, returned to the post.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycifloral', 'a.', 'Alt. of callyciflorous');
  INSERT INTO words (word, word_type, description)VALUES  (    'Callyciflorous', 'a.', 'Having the petals and stamens adnate to the calyx;\n   -- applied to a subclass of dicotyledonous plants in the system of the\n   French botanist Candolle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyciform', 'a.', 'Having the form or appearance of a calyx.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycinal', 'a.', 'Alt. of Calycine');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycine', 'a.', 'Pertaining to a calyx; having the nature of a calyx.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycle', 'n.', 'A row of small bracts, at the base of the calyx, on the\n   outside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycled', 'a.', 'Calyculate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycozoa', 'n. pl.', 'A group of acalephs of which Lucernaria is the type.\n   The body is cup-shaped with eight marginal lobes bearing clavate\n   tentacles. An aboral sucker serves for attachment. The interior is\n   divided into four large compartments. See Lucernarida.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calycular', 'a.', 'Pertaining to, or resembling, the bracts of a calycle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyculate', 'a.', 'Alt. of Calyculated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyculated', 'a.', 'Having a set of bracts resembling a calyx.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calymene', 'n.', 'A genus of trilobites characteristic of the Silurian age.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyon', 'n.', 'Flint or pebble stone, used in building walls, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calypso', 'n.', 'A small and beautiful species of orchid, having a flower\n   variegated with purple, pink, and yellow. It grows in cold and wet\n   localities in the northern part of the United States. The Calypso\n   borealis is the only orchid which reaches 68¡ N.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyptra', 'n.', 'A little hood or veil, resembling an extinguisher in form\n   and position, covering each of the small flasklike capsules which\n   contain the spores of mosses; also, any similar covering body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyptriform', 'a.', 'Having the form a calyptra, or extinguisher.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyxes', 'pl. ', 'of Calyx');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyces', 'pl. ', 'of Calyx');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyx', 'n.', 'The covering of a flower. See Flower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calyx', 'n.', 'A cuplike division of the pelvis of the kidney, which\n   surrounds one or more of the renal papillae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Calzoons', 'n. pl.', 'Drawers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cam', 'n.', 'A turning or sliding piece which, by the shape of its\n   periphery or face, or a groove in its surface, imparts variable or\n   intermittent motion to, or receives such motion from, a rod, lever, or\n   block brought into sliding or rolling contact with it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cam', 'n.', 'A curved wedge, movable about an axis, used for forcing or\n   clamping two pieces together.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cam', 'n.', 'A projecting part of a wheel or other moving piece so shaped\n   as to give alternate or variable motion to another piece against which\n   it acts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cam', 'n.', 'A ridge or mound of earth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cam', 'a.', 'Crooked.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camaieu', 'n.', 'A cameo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camaieu', 'n.', 'Painting in shades of one color; monochrome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camail', 'n.', 'A neck guard of chain mall, hanging from the bascinet or\n   other headpiece.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camail', 'n.', 'A hood of other material than mail;');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camail', 'n.', 'a hood worn in church services, -- the amice, or the like.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camarasaurus', 'n.', 'A genus of gigantic American Jurassic dinosaurs,\n   having large cavities in the bodies of the dorsal vertebrae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camarilla', 'n.', 'The private audience chamber of a king.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camarilla', 'n.', 'A company of secret and irresponsible advisers, as of a\n   king; a cabal or clique.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camass', 'n.', 'A blue-flowered liliaceous plant (Camassia esculenta) of\n   northwestern America, the bulbs of which are collected for food by the\n   Indians.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camber', 'n.', 'An upward convexity of a deck or other surface; as, she has\n   a high camber (said of a vessel having an unusual convexity of deck).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camber', 'n.', 'An upward concavity in the under side of a beam, girder, or\n   lintel; also, a slight upward concavity in a straight arch. See\n   Hogback.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambered', 'imp. & p. p.', 'of Camber');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambering', 'p. pr. & vb. n.', 'of Camber');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camber', 'v. t.', 'To cut bend to an upward curve; to construct, as a deck,\n   with an upward curve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camber', 'v. i.', 'To curve upward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camberkeeled', 'a.', 'Having the keel arched upwards, but not actually\n   hogged; -- said of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambial', 'a.', 'Belonging to exchanges in commerce; of exchange.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambist', 'n.', 'A banker; a money changer or broker; one who deals in\n   bills of exchange, or who is skilled in the science of exchange.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambistry', 'n.', 'The science of exchange, weight, measures, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambium', 'n.', 'A series of formative cells lying outside of the wood\n   proper and inside of the inner bark. The growth of new wood takes place\n   in the cambium, which is very soft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambium', 'n.', 'A fancied nutritive juice, formerly supposed to originate\n   in the blood, to repair losses of the system, and to promote its\n   increase.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camblet', 'n.', 'See Camlet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camboge', 'n.', 'See Gamboge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camboose', 'n.', 'See Caboose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambrasine', 'n.', 'A kind of linen cloth made in Egypt, and so named from\n   its resemblance to cambric.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambrel', 'n.', 'See Gambrel, n., 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambria', 'n.', 'The ancient Latin name of Wales. It is used by modern\n   poets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambrian', 'a.', 'Of or pertaining to Cambria or Wales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambrian', 'a.', 'Of or pertaining to the lowest subdivision of the rocks\n   of the Silurian or Molluscan age; -- sometimes described as inferior to\n   the Silurian. It is named from its development in Cambria or Wales. See\n   the Diagram under Geology.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambrian', 'n.', 'A native of Cambria or Wales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambrian', 'n.', 'The Cambrian formation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambric', 'n.', 'A fine, thin, and white fabric made of flax or linen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambric', 'n.', 'A fabric made, in imitation of linen cambric, of fine,\n   hardspun cotton, often with figures of various colors; -- also called\n   cotton cambric, and cambric muslin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cambro-Briton', 'n.', 'A Welshman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Came', '', 'imp. of Come.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Came', 'n.', 'A slender rod of cast lead, with or without grooves, used, in\n   casements and stained-glass windows, to hold together the panes or\n   pieces of glass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camel', 'n.', 'A large ruminant used in Asia and Africa for carrying\n   burdens and for riding. The camel is remarkable for its ability to go a\n   long time without drinking. Its hoofs are small, and situated at the\n   extremities of the toes, and the weight of the animal rests on the\n   callous. The dromedary (Camelus dromedarius) has one bunch on the back,\n   while the Bactrian camel (C. Bactrianus) has two. The llama, alpaca,\n   and vicua, of South America, belong to a related genus (Auchenia).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camel', 'n.', 'A water-tight structure (as a large box or boxes) used to\n   assist a vessel in passing over a shoal or bar or in navigating shallow\n   water. By admitting water, the camel or camels may be sunk and attached\n   beneath or at the sides of a vessel, and when the water is pumped out\n   the vessel is lifted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camel-backed', 'a.', 'Having a back like a camel; humpbacked.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameleon', 'n.', 'See Chaceleon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camellia', 'n.', 'An Asiatic genus of small shrubs, often with shining\n   leaves and showy flowers. Camellia Japonica is much cultivated for\n   ornament, and C. Sassanqua and C. oleifera are grown in China for the\n   oil which is pressed from their seeds. The tea plant is now referred to\n   this genus under the name of Camellia Thea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camelopard', 'n.', 'An African ruminant; the giraffe. See Giraffe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camelot', 'n.', 'See Camelet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camelshair', 'a.', 'Of camels hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameos', 'pl. ', 'of Cameo');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameo', 'n.', 'A carving in relief, esp. one on a small scale used as a\n   jewel for personal adornment, or like.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameras', 'pl. ', 'of Camera');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerae', 'pl. ', 'of Camera');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camera', 'n.', 'A chamber, or instrument having a chamber. Specifically:\n   The camera obscura when used in photography. See Camera, and Camera\n   obscura.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerade', 'n.', 'See Comrade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameralistic', 'a.', 'Of or pertaining to finance and public revenue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameralistics', 'n.', 'The science of finance or public revenue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camera lucida', '', 'An instrument which by means of a prism of a peculiar\n   form, or an arrangement of mirrors, causes an apparent image of an\n   external object or objects to appear as if projected upon a plane\n   surface, as of paper or canvas, so that the outlines may conveniently\n   traced. It is generally used with the microscope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camera obscura', '', 'An apparatus in which the images of external objects,\n   formed by a convex lens or a concave mirror, are thrown on a paper or\n   other white surface placed in the focus of the lens or mirror within a\n   darkened chamber, or box, so that the outlines may be traced.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camera obscura', '', 'An apparatus in which the image of an external object\n   or objects is, by means of lenses, thrown upon a sensitized plate or\n   surface placed at the back of an extensible darkened box or chamber\n   variously modified; -- commonly called simply the camera.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerated', 'imp. & p. p.', 'of Camerate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerzting', 'p. pr. & vb. n.', 'of Camerate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerate', 'v. i.', 'To build in the form of a vault; to arch over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerate', 'v. i.', 'To divide into chambers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameration', 'n.', 'A vaulting or arching over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camerlingo', 'n.', 'The papal chamberlain; the cardinal who presides over\n   the popes household. He has at times possessed great power.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cameronian', 'n.', 'A follower of the Rev. Richard Cameron, a Scotch\n   Covenanter of the time of Charles II.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camis', 'n.', 'A light, loose dress or robe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisade', 'n.', 'Alt. of Camisado');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisado', 'n.', 'A shirt worn by soldiers over their uniform, in order to\n   be able to recognize one another in a night attack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisado', 'n.', 'An attack by surprise by soldiers wearing the camisado.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisard', 'n.', 'One of the French Protestant insurgents who rebelled\n   against Louis XIV, after the revocation of the edict of Nates; -- so\n   called from the peasants smock (camise) which they wore.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisated', 'a.', 'Dressed with a shirt over the other garments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisole', 'n.', 'A short dressing jacket for women.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camisole', 'n.', 'A kind of straitjacket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camlet', 'n.', 'A woven fabric originally made of camels hair, now chiefly\n   of goats hair and silk, or of wool and cotton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camleted', 'a.', 'Wavy or undulating like camlet; veined.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cammas', 'n.', 'See Camass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cammock', 'n.', 'A plant having long hard, crooked roots, the Ononis\n   spinosa; -- called also rest-harrow. The Scandix Pecten-Veneris is also\n   called cammock.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camomile', 'n.', 'Alt. of Chamomile');
  INSERT INTO words (word, word_type, description)VALUES  (    'Chamomile', 'n.', 'A genus of herbs (Anthemis) of the Composite family. The\n   common camomile, A. nobilis, is used as a popular remedy. Its flowers\n   have a strong and fragrant and a bitter, aromatic taste. They are\n   tonic, febrifugal, and in large doses emetic, and the volatile oil is\n   carminative.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camonflet', 'n.', 'A small mine, sometimes formed in the wall or side of an\n   enemys gallery, to blow in the earth and cut off the retreat of the\n   miners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camous', 'a.', 'Alt. of Camoys');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camoys', 'a.', 'Flat; depressed; crooked; -- said only of the nose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camoused', 'a.', 'Depressed; flattened.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camously', 'adv.', 'Awry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'The ground or spot on which tents, huts, etc., are erected\n   for shelter, as for an army or for lumbermen, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'A collection of tents, huts, etc., for shelter, commonly\n   arranged in an orderly manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'A single hut or shelter; as, a hunters camp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'The company or body of persons encamped, as of soldiers, of\n   surveyors, of lumbermen, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'A mound of earth in which potatoes and other vegetables are\n   stored for protection against frost; -- called also burrow and pie.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'An ancient game of football, played in some parts of England.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camped', 'imp. & p. p.', 'of Camp');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camping', 'p. pr. & vb n.', 'of Camp');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'v. t.', 'To afford rest or lodging for, as an army or travelers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'v. i.', 'To pitch or prepare a camp; to encamp; to lodge in a camp;\n   -- often with out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camp', 'n.', 'To play the game called camp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campagna', 'n.', 'An open level tract of country; especially \"Campagna di\n   Roma.\" The extensive undulating plain which surrounds Rome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campagnol', 'n.', 'A mouse (Arvicala agrestis); called also meadow mouse,\n   which often does great damage in fields and gardens, by feeding on\n   roots and seeds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaign', 'n.', 'An open field; a large, open plain without considerable\n   hills. SeeChampaign.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaign', 'n.', 'A connected series of military operations forming a\n   distinct stage in a war; the time during which an army keeps the field.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaign', 'n.', 'Political operations preceding an election; a canvass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaign', 'n.', 'The period during which a blast furnace is continuously\n   in operation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaign', 'v. i.', 'To serve in a campaign.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaigner', 'n.', 'One who has served in an army in several campaigns; an\n   old soldier; a veteran.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campana', 'n.', 'A church bell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campana', 'n.', 'The pasque flower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campana', 'n.', 'Same as Gutta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaned', 'a.', 'Furnished with, or bearing, campanes, or bells.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanero', 'n.', 'The bellbird of South America. See Bellbird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanes', 'n. pl.', 'Bells.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campania', 'n.', 'Open country.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaniform', 'a.', 'Bell-shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanile', 'n.', 'A bell tower, esp. one built separate from a church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campaniliform', 'a.', 'Bell-shaped; campanulate; campaniform.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanologist', 'n.', 'One skilled in campanology; a bell ringer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanology', 'n.', 'The art of ringing bells, or a treatise on the art.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanula', 'n.', 'A large genus of plants bearing bell-shaped flowers,\n   often of great beauty; -- also called bellflower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanulaceous', 'a.', 'Of pertaining to, or resembling, the family of\n   plants (Camponulaceae) of which Campanula is the type, and which\n   includes the Canterbury bell, the harebell, and the Venuss\n   looking-glass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanularian', 'n.', 'A hydroid of the family ampanularidae, characterized\n   by having the polyps or zooids inclosed in bell-shaped calicles or\n   hydrothecae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campanulate', 'a.', 'Bell-shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campbellite', 'n.', 'A member of the denomination called Christians or\n   Disciples of Christ. They themselves repudiate the term Campbellite as\n   a nickname. See Christian, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campeachy Wood', '', 'Logwood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camper', 'n.', 'One who lodges temporarily in a hut or camp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campestral', 'a.', 'Alt. of Campestrian');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campestrian', 'a.', 'Relating to an open fields; drowing in a field;\n   growing in a field, or open ground.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camptight', 'n.', 'A duel; the decision of a case by a duel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphene', 'n.', 'One of a series of substances C10H16, resembling camphor,\n   regarded as modified terpenes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphine', 'n.', 'Rectified oil of turpentine, used for burning in lamps,\n   and as a common solvent in varnishes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphire', 'n.', 'An old spelling of Camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphogen', 'n.', 'See Cymene.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphol', 'n.', 'See Borneol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphor', 'n.', 'A tough, white, aromatic resin, or gum, obtained from\n   different species of the Laurus family, esp. from Cinnamomum camphara\n   (the Laurus camphara of Linnaeus.). Camphor, C10H16O, is volatile and\n   fragrant, and is used in medicine as a diaphoretic, a stimulant, or\n   sedative.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphor', 'n.', 'A gum resembling ordinary camphor, obtained from a tree\n   (Dryobalanops camphora) growing in Sumatra and Borneo; -- called also\n   Malay camphor, camphor of Borneo, or borneol. See Borneol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphor', 'v. t.', 'To impregnate or wash with camphor; to camphorate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphoraceous', 'a.', 'Of the nature of camphor; containing camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphorate', 'v. t.', 'To impregnate or treat with camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphorate', 'n.', 'A salt of camphoric acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphorate', '', 'Alt. of Camporated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camporated', '', 'Combined or impregnated with camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphoric', 'a.', 'Of, pertaining to, or derived from, camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camphretic', 'a.', 'Pertaining to, or derived from camphor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camping', 'n.', 'Lodging in a camp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camping', 'n.', 'A game of football.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campion', 'n.', 'A plant of the Pink family (Cucubalus bacciferus); bearing\n   berries regarded as poisonous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campus', 'n.', 'The principal grounds of a college or school, between the\n   buildings or within the main inclosure; as, the college campus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campylospermous', 'a.', 'Having seeds grooved lengthwise on the inner face,\n   as in sweet cicely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Campylotropous', 'a.', 'Having the ovules and seeds so curved, or bent down\n   upon themselves, that the ends of the embryo are brought close\n   together.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camus', 'n.', 'See Camis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Camwood', 'n.', 'See Barwood.');
