  INSERT INTO words (word, word_type, description)VALUES  (    'Can', 'v. t. & i.', 'To be able to do; to have power or influence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Can', 'v. t. & i.', 'To be able; -- followed by an infinitive without to;\n   as, I can go, but do not wish to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaanite', 'n.', 'A descendant of Canaan, the son of Ham, and grandson of\n   Noah.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaanite', 'n.', 'A Native or inhabitant of the land of Canaan, esp. a\n   member of any of the tribes who inhabited Canaan at the time of the\n   exodus of the Israelites from Egypt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaanite', 'n.', 'A zealot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaanitish', 'a.', 'Of or pertaining to Canaan or the Canaanites.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caada', 'n.', 'A small caon; a narrow valley or glen; also, but less\n   frequently, an open valley.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canada', 'n.', 'A British province in North America, giving its name to\n   various plants and animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canadian', 'a.', 'Of or pertaining to Canada.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canadian', 'n.', 'A native or inhabitant of Canada.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaille', 'n.', 'The lowest class of people; the rabble; the vulgar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaille', 'n.', 'Shorts or inferior flour.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canakin', 'n.', 'A little can or cup.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canal', 'n.', 'An artificial channel filled with water and designed for\n   navigation, or for irrigating land, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canal', 'n.', 'A tube or duct; as, the alimentary canal; the semicircular\n   canals of the ear.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canal coal', '', 'See Cannel coal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaliculate', 'a.', 'Alt. of Canaliculated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaliculated', 'a.', 'Having a channel or groove, as in the leafstalks of\n   most palms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaliculi', 'pl. ', 'of Canaliculus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaliculus', 'n.', 'A minute canal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canalization', 'n.', 'Construction of, or furnishing with, a canal or\n   canals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canard', 'n.', 'An extravagant or absurd report or story; a fabricated\n   sensational report or statement; esp. one set afloat in the newspapers\n   to hoax the public.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canarese', 'a.', 'Pertaining to Canara, a district of British India.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'a.', 'Of or pertaining to the Canary Islands; as, canary wine;\n   canary birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'a.', 'Of a pale yellowish color; as, Canary stone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaries', 'pl. ', 'of Canary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'n.', 'Wine made in the Canary Islands; sack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'n.', 'A canary bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'n.', 'A pale yellow color, like that of a canary bird.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'n.', 'A quick and lively dance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary', 'v. i.', 'To perform the canary dance; to move nimbly; to caper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canary bird', '', 'A small singing bird of the Finch family (Serinus\n   Canarius); a native of the Canary Islands. It was brought to Europe in\n   the 16th century, and made a household pet. It generally has a\n   yellowish body with the wings and tail greenish, but in its wild state\n   it is more frequently of gray or brown color. It is sometimes called\n   canary finch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canaster', 'n.', 'A kind of tobacco for smoking, made of the dried leaves,\n   coarsely broken; -- so called from the rush baskets in which it is\n   packed in South America.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Can buoy', '', 'See under Buoy, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancan', 'n.', 'A rollicking French dance, accompanied by indecorous or\n   extravagant postures and gestures.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canceled', 'imp. & p. p.', 'of Cancel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancelled', '', 'of Cancel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canceling', 'p. pr. & vb. n.', 'of Cancel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancelling', '', 'of Cancel');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'To inclose or surround, as with a railing, or with\n   latticework.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'To shut out, as with a railing or with latticework; to\n   exclude.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'To cross and deface, as the lines of a writing, or as a\n   word or figure; to mark out by a cross line; to blot out or obliterate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'To annul or destroy; to revoke or recall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'To suppress or omit; to strike out, as matter in type.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'An inclosure; a boundary; a limit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'The suppression or striking out of matter in type, or of\n   a printed page or pages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancel', 'v. i.', 'The part thus suppressed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancelier', 'v. i.', 'To turn in flight; -- said of a hawk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancelier', 'n.', 'Alt. of Canceleer');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canceleer', 'n.', 'The turn of a hawk upon the wing to recover herself,\n   when she misses her aim in the stoop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellarean', 'a.', 'Cancellarean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellate', 'v. t.', 'Consisting of a network of veins, without\n   intermediate parenchyma, as the leaves of certain plants; latticelike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellate', 'v. t.', 'Having the surface coveres with raised lines,\n   crossing at right angles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellated', 'a.', 'Crossbarred; marked with cross lines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellated', 'a.', 'Open or spongy, as some porous bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellation', 'n.', 'The act, process, or result of canceling; as, the\n   cansellation of certain words in a contract, or of the contract itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellation', 'n.', 'The operation of striking out common factors, in both\n   the dividend and divisor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancelli', 'v. t.', 'An interwoven or latticed wall or inclosure;\n   latticework, rails, or crossbars, as around the bar of a court of\n   justice, between the chancel and the nave of a church, or in a window.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancelli', 'v. t.', 'The interlacing osseous plates constituting the\n   elastic porous tissue of certain parts of the bones, esp. in their\n   articular extremities.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancellous', 'a.', 'Having a spongy or porous structure; made up of\n   cancelli; cancellated; as, the cancellous texture of parts of many\n   bones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancer', 'n.', 'A genus of decapod Crustacea, including some of the most\n   common shore crabs of Europe and North America, as the rock crab, Jonah\n   crab, etc. See Crab.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancer', 'n.', 'The fourth of the twelve signs of the zodiac. The first\n   point is the northern limit of the suns course in summer; hence, the\n   sign of the summer solstice. See Tropic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancer', 'n.', 'A northern constellation between Gemini and Leo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancer', 'n.', 'Formerly, any malignant growth, esp. one attended with\n   great pain and ulceration, with cachexia and progressive emaciation. It\n   was so called, perhaps, from the great veins which surround it,\n   compared by the ancients to the claws of a crab. The term is now\n   restricted to such a growth made up of aggregations of epithelial\n   cells, either without support or embedded in the meshes of a trabecular\n   framework.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancerated', 'imp. & p. p.', 'of Cancerate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancerate', 'v. i.', 'To grow into a canser; to become cancerous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canceration', 'n.', 'The act or state of becoming cancerous or growing into\n   a cancer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancerite', 'n.', 'Like a cancer; having the qualities or virulence of a\n   cancer; affected with cancer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancriform', 'a.', 'Having the form of, or resembling, a crab; crab-shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancriform', 'a.', 'Like a cancer; cancerous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancrine', 'a.', 'Having the qualities of a crab; crablike.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancrinite', 'n.', 'A mineral occurring in hexagonal crystals, also\n   massive, generally of a yellow color, containing silica, alumina, lime,\n   soda, and carbon dioxide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancroid', 'a.', 'Resembling a crab; pertaining to the Cancroidea, one of\n   the families of crabs, including the genus Cancer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cancroid', 'a.', 'Like a cancer; as, a cancroid tumor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cand', 'n.', 'Fluor spar. See Kand.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candelabra', 'pl. ', 'of Candelabrum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candelabrums', 'pl. ', 'of Candelabrum');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candelabrum', 'n.', 'A lamp stand of any sort.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candelabrum', 'n.', 'A highly ornamented stand of marble or other ponderous\n   material, usually having three feet, -- frequently a votive offering to\n   a temple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candelabrum', 'n.', 'A large candlestick, having several branches.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candent', 'a.', 'Heated to whiteness; glowing with heat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canderos', 'n.', 'An East Indian resin, of a pellucid white color, from\n   which small ornaments and toys are sometimes made.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candescence', 'n.', 'See Incandescence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candicant', 'a.', 'Growing white.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candid', 'a.', 'White.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candid', 'a.', 'Free from undue bias; disposed to think and judge according\n   to truth and justice, or without partiality or prejudice; fair; just;\n   impartial; as, a candid opinion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candid', 'a.', 'Open; frank; ingenuous; outspoken.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidacy', 'n.', 'The position of a candidate; state of being a candidate;\n   candidateship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidate', 'n.', 'One who offers himself, or is put forward by others, as\n   a suitable person or an aspirant or contestant for an office,\n   privilege, or honor; as, a candidate for the office of governor; a\n   candidate for holy orders; a candidate for scholastic honors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidateship', 'n.', 'Candidacy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidating', 'n.', 'The taking of the position of a candidate;\n   specifically, the preaching of a clergyman with a view to settlement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidature', 'n.', 'Candidacy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidly', 'adv.', 'In a candid manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candidness', 'n.', 'The quality of being candid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candied', 'a.', 'Preserved in or with sugar; incrusted with a candylike\n   substance; as, candied fruits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candied', 'a.', 'Converted wholly or partially into sugar or candy; as\n   candied sirup.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candied', 'a.', 'Conted or more or less with sugar; as, candidied raisins');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candied', 'a.', 'Figuratively; Honeyed; sweet; flattering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candied', 'a.', 'Covered or incrusted with that which resembles sugar or\n   candy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candify', 'v. t. / v. i.', 'To make or become white, or candied.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candiot', 'a.', 'Of or pertaining to Candia; Cretary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candite', 'n.', 'A variety of spinel, of a dark color, found at Candy, in\n   Ceylon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candle', 'n.', 'A slender, cylindrical body of tallow, containing a wick\n   composed of loosely twisted linen of cotton threads, and used to\n   furnish light.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candle', 'n.', 'That which gives light; a luminary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candleberry tree', '', 'A shrub (the Myrica cerifera, or wax-bearing\n   myrtle); common in North America, the little nuts of which are covered\n   with a greenish white wax, which was formerly, used for hardening\n   candles; -- also called bayberry tree, bayberry, or candleberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlebomb', 'n.', 'A small glass bubble, filled with water, which, if\n   placed in the flame of a candle, bursts by expansion of steam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlebomb', 'n.', 'A pasteboard shell used in signaling. It is filled with\n   a composition which makes a brilliant light when it explodes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candle coal', '', 'See Cannel coal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlefish', 'n.', 'A marine fish (Thaleichthys Pacificus); allied to the\n   smelt, found on the north Pacific coast; -- called also eulachon. It is\n   so oily that, when dried, it may be used as a candle, by drawing a wick\n   through it');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlefish', 'n.', 'The beshow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candleholder', 'n.', 'One who, or that which, holds a candle; also, one who\n   assists another, but is otherwise not of importance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlelight', 'n.', 'The light of a candle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlemas', 'n.', 'The second day of February, on which is celebrated the\n   feast of the Purification of the Virgin Mary; -- so called because the\n   candles for the altar or other sacred uses are blessed on that day.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlestick', 'n.', 'An instrument or utensil for supporting a candle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candlewaster', 'n.', 'One who consumes candles by being up late for study\n   or dissipation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candock', 'n.', 'A plant or weed that grows in rivers; a species of\n   Equisetum; also, the yellow frog lily (Nuphar luteum).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candor', 'n.', 'Whiteness; brightness; (as applied to moral conditions)\n   usullied purity; innocence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candor', 'n.', 'A disposition to treat subjects with fairness; freedom from\n   prejudice or disguise; frankness; sincerity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candroy', 'n.', 'A machine for spreading out cotton cloths to prepare them\n   for printing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candied', 'imp. & p. p.', 'of Candy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candying', 'p. pr & vb. n.', 'of Candy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'v. t.', 'To conserve or boil in sugar; as, to candy fruits; to\n   candy ginger.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'v. t.', 'To make sugar crystals of or in; to form into a mass\n   resembling candy; as, to candy sirup.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'v. t.', 'To incrust with sugar or with candy, or with that which\n   resembles sugar or candy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'v. i.', 'To have sugar crystals form in or on; as, fruits\n   preserved in sugar candy after a time.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'v. i.', 'To be formed into candy; to solidify in a candylike form\n   or mass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'v. t.', 'A more or less solid article of confectionery made by\n   boiling sugar or molasses to the desired consistency, and than\n   crystallizing, molding, or working in the required shape. It is often\n   flavored or colored, and sometimes contains fruit, nuts, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candy', 'n.', 'A weight, at Madras 500 pounds, at Bombay 560 pounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Candytuft', 'n.', 'An annual plant of the genus Iberis, cultivated in\n   gardens. The name was originally given to the I. umbellata, first,\n   discovered in the island of Candia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'n.', 'A name given to several peculiar palms, species of Calamus\n   and Daemanorops, having very long, smooth flexible stems, commonly\n   called rattans.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'n.', 'Any plant with long, hard, elastic stems, as reeds and\n   bamboos of many kinds; also, the sugar cane.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'n.', 'Stems of other plants are sometimes called canes; as, the\n   canes of a raspberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'n.', 'A walking stick; a staff; -- so called because originally\n   made of one the species of cane.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'n.', 'A lance or dart made of cane.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'n.', 'A local European measure of length. See Canna.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caned', 'imp. & p. p.', 'of Cane');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caning', 'p. pr. & vb. n.', 'of Cane');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'v. t.', 'To beat with a cane.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cane', 'v. t.', 'To make or furnish with cane or rattan; as, to cane\n   chairs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canebrake', 'n.', 'A thicket of canes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caned', 'a.', 'Filled with white flakes; mothery; -- said vinegar when\n   containing mother.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canella', 'n.', 'A genus of trees of the order Canellaceae, growing in the\n   West Indies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canescent', 'a.', 'Growing white, or assuming a color approaching to white.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Can hook', '', 'A device consisting of a short rope with flat hooks at each\n   end, for hoisting casks or barrels by the ends of the staves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannicula', 'n.', 'The Dog Star; Sirius.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canicular', 'a.', 'Pertaining to, or measured, by the rising of the Dog\n   Star.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canicule', 'n.', 'Canicula.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caninal', 'a.', 'See Canine, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canine', 'a.', 'Of or pertaining to the family Canidae, or dogs and wolves;\n   having the nature or qualities of a dog; like that or those of a dog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canine', 'a.', 'Of or pertaining to the pointed tooth on each side the\n   incisors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canine', 'n.', 'A canine tooth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canes', 'pl. ', 'of Canis');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canis', 'n.', 'A genus of carnivorous mammals, of the family Canidae,\n   including the dogs and wolves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canister', 'n.', 'A small basket of rushes, reeds, or willow twigs, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canister', 'n.', 'A small box or case for holding tea, coffee, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canister', 'n.', 'A kind of case shot for cannon, in which a number of lead\n   or iron balls in layers are inclosed in a case fitting the gun; --\n   called also canister shot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'n.', 'A corroding or sloughing ulcer; esp. a spreading gangrenous\n   ulcer or collection of ulcers in or about the mouth; -- called also\n   water canker, canker of the mouth, and noma.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'n.', 'Anything which corrodes, corrupts, or destroy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'n.', 'A disease incident to trees, causing the bark to rot and\n   fall off.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'n.', 'An obstinate and often incurable disease of a horses foot,\n   characterized by separation of the horny portion and the development of\n   fungoid growths; -- usually resulting from neglected thrush.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'n.', 'A kind of wild, worthless rose; the dog-rose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankered', 'imp. & p. p.', 'of Canker');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankering', 'p. pr. & vb. n.', 'of Canker');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'v. t.', 'To affect as a canker; to eat away; to corrode; to\n   consume.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'v. t.', 'To infect or pollute; to corrupt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'v. i.', 'To waste away, grow rusty, or be oxidized, as a mineral.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker', 'v. i.', 'To be or become diseased, or as if diseased, with\n   canker; to grow corrupt; to become venomous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker-bit', 'a.', 'Eaten out by canker, or as by canker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker bloom', '', 'The bloom or blossom of the wild rose or dog-rose.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker blossom', '', 'That which blasts a blossom as a canker does.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankered', 'a.', 'Affected with canker; as, a cankered mouth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankered', 'a.', 'Affected mentally or morally as with canker; sore,\n   envenomed; malignant; fretful; ill-natured.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankeredly', 'adv.', 'Fretfully; spitefully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker fly', '', 'A fly that preys on fruit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankerous', 'a.', 'Affecting like a canker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canker rash', '', 'A form of scarlet fever characterized by ulcerated or\n   putrid sore throat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankerworm', 'n.', 'The larva of two species of geometrid moths which are\n   very injurious to fruit and shade trees by eating, and often entirely\n   destroying, the foliage. Other similar larvae are also called\n   cankerworms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankery', 'a.', 'Like a canker; full of canker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cankery', 'a.', 'Surly; sore; malignant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canna', 'n.', 'A measure of length in Italy, varying from six to seven\n   feet. See Cane, 4.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canna', 'n.', 'A genus of tropical plants, with large leaves and often with\n   showy flowers. The Indian shot (C. Indica) is found in gardens of the\n   northern United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannabene', 'n.', 'A colorless oil obtained from hemp by distillation, and\n   possessing its intoxicating properties.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannabin', 'n.', 'A poisonous resin extracted from hemp (Cannabis sativa,\n   variety Indica). The narcotic effects of hasheesh are due to this\n   resin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannabine', 'a.', 'Pertaining to hemp; hempen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannabis', 'n.', 'A genus of a single species belonging to the order\n   Uricaceae; hemp.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannel coal', '', 'A kind of mineral coal of a black color, sufficiently\n   hard and solid to be cut and polished. It burns readily, with a clear,\n   yellow flame, and on this account has been used as a substitute for\n   candles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannery', 'n.', 'A place where the business of canning fruit, meat, etc.,\n   is carried on.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannibal', 'n.', 'A human being that eats human flesh; hence, any that\n   devours its own kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannibal', 'a.', 'Relating to cannibals or cannibalism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannibalism', 'n.', 'The act or practice of eating human flesh by mankind.\n   Hence; Murderous cruelty; barbarity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannibally', 'adv.', 'In the manner of cannibal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannikin', 'n.', 'A small can or drinking vessel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannily', 'adv.', 'In a canny manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canniness', 'n.', 'Caution; crafty management.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannons', 'pl. ', 'of Cannon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannon', 'pl. ', 'of Cannon');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannon', 'n.', 'A great gun; a piece of ordnance or artillery; a firearm\n   for discharging heavy shot with great force.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannon', 'n.', 'A hollow cylindrical piece carried by a revolving shaft, on\n   which it may, however, revolve independently.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannon', 'n.', 'A kind of type. See Canon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannon', 'n. & v.', 'See Carom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonade', 'n.', 'The act of discharging cannon and throwing ball, shell,\n   etc., for the purpose of destroying an army, or battering a town, ship,\n   or fort; -- usually, an attack of some continuance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonade', 'n.', 'Fig.; A loud noise like a cannonade; a booming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonade', 'imp. & p. p.', 'of Cannonade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonading', 'p. pr. & vb. n.', 'of Cannonade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonade', 'v. t.', 'To attack with heavy artillery; to batter with cannon\n   shot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonade', 'v. i.', 'To discharge cannon; as, the army cannonaded all day.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannon bone', '', 'See Canon Bone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannoned', 'a.', 'Furnished with cannon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannoneer', 'n.', 'Alt. of Cannonier');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonier', 'n.', 'A man who manages, or fires, cannon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonering', 'n.', 'The use of cannon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonry', 'n.', 'Cannon, collectively; artillery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannot', '', 'Am, is, or are, not able; -- written either as one word or\n   two.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannula', 'n.', 'A small tube of metal, wood, or India rubber, used for\n   various purposes, esp. for injecting or withdrawing fluids. It is\n   usually associated with a trocar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannular', 'a.', 'Having the form of a tube; tubular.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannulated', 'a.', 'Hollow; affording a passage through its interior length\n   for wire, thread, etc.; as, a cannulated (suture) needle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canny', 'a.', 'Alt. of Cannei');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannei', 'a.', 'Artful; cunning; shrewd; wary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannei', 'a.', 'Skillful; knowing; capable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannei', 'a.', 'Cautious; prudent; safe..');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannei', 'a.', 'Having pleasing or useful qualities; gentle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannei', 'a.', 'Reputed to have magical powers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoes', 'pl. ', 'of Canoe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoe', 'n.', 'A boat used by rude nations, formed of trunk of a tree,\n   excavated, by cutting of burning, into a suitable shape. It is\n   propelled by a paddle or paddles, or sometimes by sail, and has no\n   rudder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoe', 'n.', 'A boat made of bark or skins, used by savages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoe', 'n.', 'A light pleasure boat, especially designed for use by one\n   who goes alone upon long excursions, including portage. It it propelled\n   by a paddle, or by a small sail attached to a temporary mast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoed', 'imp. & p. p.', 'of Canoe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoeing', 'p. pr. & vb. n.', 'of Canoe');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoe', 'v. i.', 'To manage a canoe, or voyage in a canoe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoeing', 'n.', 'The act or art of using a canoe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoeist', 'n.', 'A canoeman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoemen', 'pl. ', 'of Canoeman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoeman', 'n.', 'One who uses a canoe; one who travels in a canoe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'A law or rule.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'A law, or rule of doctrine or discipline, enacted by a\n   council and confirmed by the pope or the sovereign; a decision,\n   regulation, code, or constitution made by ecclesiastical authority.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'The collection of books received as genuine Holy Scriptures,\n   called the sacred canon, or general rule of moral and religious duty,\n   given by inspiration; the Bible; also, any one of the canonical\n   Scriptures. See Canonical books, under Canonical, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'In monasteries, a book containing the rules of a religious\n   order.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'A catalogue of saints acknowledged and canonized in the\n   Roman Catholic Church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'A member of a cathedral chapter; a person who possesses a\n   prebend in a cathedral or collegiate church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'A musical composition in which the voices begin one after\n   another, at regular intervals, successively taking up the same subject.\n   It either winds up with a coda (tailpiece); or, as each voice finishes,\n   commences anew, thus forming a perpetual fugue or round. It is the\n   strictest form of imitation. See Imitation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'The largest size of type having a specific name; -- so\n   called from having been used for printing the canons of the church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'The part of a bell by which it is suspended; -- called also\n   ear and shank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon', 'n.', 'See Carom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caon', 'n.', 'A deep gorge, ravine, or gulch, between high and steep banks,\n   worn by water courses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon bit', '', 'That part of a bit which is put in a horses mouth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canon bone', '', 'The shank bone, or great bone above the fetlock, in the\n   fore and hind legs of the horse and allied animals, corresponding to\n   the middle metacarpal or metatarsal bone of most mammals. See Horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canoness', 'n.', 'A woman who holds a canonry in a conventual chapter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonic', 'a.', 'Alt. of Cannonical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cannonical', 'a.', 'Of or pertaining to a canon; established by, or\n   according to a , canon or canons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonically', 'adv.', 'In a canonical manner; according to the canons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonicalness', 'n.', 'The quality of being canonical; canonicity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonicals', 'n. pl.', 'The dress prescribed by canon to be worn by a\n   clergyman when officiating. Sometimes, any distinctive professional\n   dress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonicate', 'n.', 'The office of a canon; a canonry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonicity', 'n.', 'The state or quality of being canonical; agreement with\n   the canon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonist', 'n.', 'A professor of canon law; one skilled in the knowledge\n   and practice of ecclesiastical law.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonistic', 'a.', 'Of or pertaining to a canonist.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonization', 'n.', 'The final process or decree (following beatifacation)\n   by which the name of a deceased person is placed in the catalogue\n   (canon) of saints and commended to perpetual veneration and invocation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonization', 'n.', 'The state of being canonized or sainted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonized', 'imp. & p. p.', 'of Canonize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonizing', 'p. pr. & vb. n.', 'of Canonize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonize', 'v. t.', 'To declare (a deceased person) a saint; to put in the\n   catalogue of saints; as, Thomas a Becket was canonized.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonize', 'v. t.', 'To glorify; to exalt to the highest honor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonize', 'v. t.', 'To rate as inspired; to include in the canon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonry', 'n. pl.', 'A benefice or prebend in a cathedral or collegiate\n   church; a right to a place in chapter and to a portion of its revenues;\n   the dignity or emoluments of a canon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canonship', 'a.', 'Of or pertaining to Canopus in Egypt; as, the Canopic\n   vases, used in embalming.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopus', 'n.', 'A star of the first magnitude in the southern\n   constellation Argo.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopies', 'pl. ', 'of Canopy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopy', 'n.', 'A covering fixed over a bed, dais, or the like, or carried\n   on poles over an exalted personage or a sacred object, etc. chiefly as\n   a mark of honor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopy', 'n.', 'An ornamental projection, over a door, window, niche, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopy', 'n.', 'Also, a rooflike covering, supported on pillars over an\n   altar, a statue, a fountain, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopes', 'imp. & p. p.', 'of Canopy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopying', 'p. pr. & vb. n.', 'of Canopy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canopy', 'v. t.', 'To cover with, or as with, a canopy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canorous', 'a.', 'Melodious; musical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canorousness', 'n.', 'The quality of being musical.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canstick', 'n.', 'Candlestick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'A corner; angle; niche.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'An outer or external angle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'An inclination from a horizontal or vertical line; a slope or\n   bevel; a titl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'A sudden thrust, push, kick, or other impulse, producing a\n   bias or change of direction; also, the bias or turn so give; as, to\n   give a ball a cant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'A segment forming a side piece in the head of a cask.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'A segment of he rim of a wooden cogwheel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'A piece of wood laid upon the deck of a vessel to support the\n   bulkheads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canted', 'imp. & p. p.', 'of Cant');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canting', 'p. pr. & vb. n.', 'of Cant');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. t.', 'To incline; to set at an angle; to tilt over; to tip upon\n   the edge; as, to cant a cask; to cant a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. t.', 'To give a sudden turn or new direction to; as, to cant\n   round a stick of timber; to cant a football.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. t.', 'To cut off an angle from, as from a square piece of\n   timber, or from the head of a bolt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'An affected, singsong mode of speaking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'The idioms and peculiarities of speech in any sect, class, or\n   occupation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'The use of religious phraseology without understanding or\n   sincerity; empty, solemn speech, implying what is not felt; hypocrisy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'Vulgar jargon; slang; the secret language spoker by gipsies,\n   thieves, tramps, or beggars.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'a.', 'Of the nature of cant; affected; vulgar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. i.', 'To speak in a whining voice, or an affected, singsong\n   tone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. i.', 'To make whining pretensions to goodness; to talk with an\n   affectation of religion, philanthropy, etc.; to practice hypocrisy; as,\n   a canting fanatic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. i.', 'To use pretentious language, barbarous jargon, or\n   technical terms; to talk with an affectation of learning.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'n.', 'A call for bidders at a public sale; an auction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', 'v. t.', 'to sell by auction, or bid a price at a sale by auction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant', '', 'A colloquial contraction for can not.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantab', 'n.', 'A Cantabrigian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantabile', 'a.', 'In a melodious, flowing style; in a singing style, as\n   opposed to bravura, recitativo, or parlando.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantabile', 'n.', 'A piece or passage, whether vocal or instrumental,\n   peculiarly adapted to singing; -- sometimes called cantilena.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantabrian', 'a.', 'Of or pertaining to Cantabria on the Bay of Biscay in\n   Spain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantabrigian', 'n.', 'A native or resident of Cambridge; esp. a student or\n   graduate of the university of Cambridge, England.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantalever', 'n.', 'A bracket to support a balcony, a cornice, or the like.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantalever', 'n.', 'A projecting beam, truss, or bridge unsupported at the\n   outer end; one which overhangs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantaloupe', 'n.', 'A muskmelon of several varieties, having when mature, a\n   yellowish skin, and flesh of a reddish orange color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantankerous', 'a.', 'Perverse; contentious; ugly; malicious.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantar', 'n.', 'Alt. of Cantarro');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantarro', 'n.', 'A weight used in southern Europe and East for heavy\n   articles. It varies in different localities; thus, at Rome it is nearly\n   75 pounds, in Sardinia nearly 94 pounds, in Cairo it is 95 pounds, in\n   Syria about 503 pounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantarro', 'n.', 'A liquid measure in Spain, ranging from two and a half to\n   four gallons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantata', 'n.', 'A poem set to music; a musical composition comprising\n   choruses, solos, interludes, etc., arranged in a somewhat dramatic\n   manner; originally, a composition for a single noise, consisting of\n   both recitative and melody.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantation', 'n.', 'A singing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantatory', 'a.', 'Containing cant or affectation; whining; singing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantatrice', 'n.', 'A female professional singer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canted', 'a.', 'Having angles; as, a six canted bolt head; a canted window.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canted', 'a.', 'Inclined at an angle to something else; tipped; sloping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canteen', 'n.', 'A vessel used by soldiers for carrying water, liquor, or\n   other drink.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canteen', 'n.', 'The sutlers shop in a garrison; also, a chest containing\n   culinary and other vessels for officers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantel', 'n.', 'See Cantle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canter', 'n.', 'A moderate and easy gallop adapted to pleasure riding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canter', 'n.', 'A rapid or easy passing over.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantered', 'imp. & p. p.', 'of Canter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantering', 'p. pr. & vb. n.', 'of Canter');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canter', 'v. i.', 'To move in a canter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canter', 'v. t.', 'To cause, as a horse, to go at a canter; to ride (a\n   horse) at a canter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canter', 'n.', 'One who cants or whines; a beggar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canter', 'n.', 'One who makes hypocritical pretensions to goodness; one who\n   uses canting language.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canterbury', 'n.', 'A city in England, giving its name various articles. It\n   is the seat of the Archbishop of Canterbury (primate of all England);\n   and contains the shrine of Thomas a Becket, to which pilgrimages were\n   formerly made.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canterbury', 'n.', 'A stand with divisions in it for holding music, loose\n   papers, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantharidal', 'a.', 'Of or pertaining to cantharides or made of\n   cantharides; as, cantharidal plaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantharides', 'n. pl.', 'See Cantharis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantharidin', 'n.', 'The active principle of the cantharis, or Spanish fly,\n   a volatile, acrid, bitter solid, crystallizing in four-sided prisms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantharides', 'pl. ', 'of Cantharis');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantharis', 'n.', 'A beetle (Lytta, / Cantharis, vesicatoria); havin1g an\n   elongated cylindrical body of a brilliant green color, and a nauseous\n   odor; the blister fly or blister beetle, of the apothecary; -- also\n   called Spanish fly. Many other species of Lytta, used for the same\n   purpose, take the same name. See Blister beetle, under Blister. The\n   plural form in usually applied to the dried insects used in medicine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cant hook', '', 'A wooden lever with a movable iron hook. hear the end; --\n   used for canting or turning over heavy logs, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canthoplasty', 'n.', 'The operation of forming a new canthus, when one has\n   been destroyed by injury or disease.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canthi', 'pl. ', 'of Canthus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canthus', 'n.', 'The corner where the upper and under eyelids meet on each\n   side of the eye.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canticles', 'pl. ', 'of Canticle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canticle', 'n.', 'A song; esp. a little song or hymn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canticle', 'n.', 'The Song of Songs or Song of Solomon, one of the books of\n   the Old Testament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canticle', 'n.', 'A canto or division of a poem');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canticle', 'n.', 'A psalm, hymn, or passage from the Bible, arranged for\n   chanting in church service.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canticoy', 'n.', 'A social gathering; usually, one for dancing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantile', 'v. i.', 'Same as Cantle, v. t.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantilena', 'n.', 'See Cantabile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantilever', 'n.', 'Same as Cantalever.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantillate', 'v. i.', 'To chant; to recite with musical tones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantillation', 'n.', 'A chanting; recitation or reading with musical\n   modulations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantine', 'n.', 'See Canteen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canting', 'a.', 'Speaking in a whining tone of voice; using technical or\n   religious terms affectedly; affectedly pious; as, a canting rogue; a\n   canting tone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canting', 'n.', 'The use of cant; hypocrisy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantiniere', 'n.', 'A woman who carries a canteen for soldiers; a\n   vivandiere.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantion', 'n.', 'A song or verses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantle', 'n.', 'A corner or edge of anything; a piece; a fragment; a part.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantle', 'n.', 'The upwardly projecting rear part of saddle, opposite to\n   the pommel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantle', 'v. t.', 'To cut in pieces; to cut out from.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantlet', 'n.', 'A piece; a fragment; a corner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantos', 'pl. ', 'of Canto');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canto', 'n.', 'One of the chief divisions of a long poem; a book.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canto', 'n.', 'The highest vocal part; the air or melody in choral music;\n   anciently the tenor, now the soprano.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'n.', 'A song or canto');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'n.', 'A small portion; a division; a compartment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'n.', 'A small community or clan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'n.', 'A small territorial district; esp. one of the twenty-two\n   independent states which form the Swiss federal republic; in France, a\n   subdivision of an arrondissement. See Arrondissement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'n.', 'A division of a shield occupying one third part of the\n   chief, usually on the dexter side, formed by a perpendicular line from\n   the top of the shield, meeting a horizontal line from the side.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoned', 'imp. & p. p.', 'of Canton');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoning', 'p. pr. & vb. n.', 'of Canton');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'v. i.', 'To divide into small parts or districts; to mark off or\n   separate, as a distinct portion or division.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton', 'v. i.', 'To allot separate quarters to, as to different parts or\n   divisions of an army or body of troops.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantonal', 'a.', 'Of or pertaining to a canton or cantons; of the nature of\n   a canton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton crape', '', 'A soft, white or colored silk fabric, of a gauzy\n   texture and wavy appearance, used for ladies scarfs, shawls, bonnet\n   trimmings, etc.; -- called also Oriental crape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoned', 'a.', 'Having a charge in each of the four corners; -- said of a\n   cross on a shield, and also of the shield itself.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoned', 'a.', 'Having the angles marked by, or decorated with,\n   projecting moldings or small columns; as, a cantoned pier or pilaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canton flannel', '', 'See Cotton flannel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantonize', 'v. i.', 'To divide into cantons or small districts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantonment', 'n.', 'A town or village, or part of a town or village,\n   assigned to a body of troops for quarters; temporary shelter or place\n   of rest for an army; quarters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoon', 'n.', 'A cotton stuff showing a fine cord on one side and a\n   satiny surface on the other.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantor', 'n.', 'A singer; esp. the leader of a church choir; a precentor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoral', 'a.', 'Of or belonging to a cantor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantoris', 'a.', 'Of or pertaining to a cantor; as, the cantoris side of a\n   choir; a cantoris stall.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantrap', 'n.', 'Alt. of Cantrip');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantrip', 'n.', 'A charm; an incantation; a shell; a trick; adroit\n   mischief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantred', 'n.', 'Alt. of Cantref');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cantref', 'n.', 'A district comprising a hundred villages, as in Wales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canty', 'a.', 'Cheerful; sprightly; lively; merry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canuck', 'n.', 'A Canadian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canuck', 'n.', 'A small or medium-sized hardy horse, common in Canada.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canula', 'a.', 'Alt. of Canulated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canular', 'a.', 'Alt. of Canulated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canulated', 'a.', 'See Cannula, Cannular, and Cannulated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvas', 'n.', 'A strong cloth made of hemp, flax, or cotton; -- used for\n   tents, sails, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvas', 'n.', 'A coarse cloth so woven as to form regular meshes for\n   working with the needle, as in tapestry, or worsted work.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvas', 'n.', 'A piece of strong cloth of which the surface has been\n   prepared to receive painting, commonly painting in oil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvas', 'n.', 'Something for which canvas is used: (a) A sail, or a\n   collection of sails. (b) A tent, or a collection of tents. (c) A\n   painting, or a picture on canvas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvas', 'n.', 'A rough draft or model of a song, air, or other literary or\n   musical composition; esp. one to show a poet the measure of the verses\n   he is to make.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvas', 'a.', 'Made of, pertaining to, or resembling, canvas or coarse\n   cloth; as, a canvas tent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvasback', 'n.', 'A Species of duck (Aythya vallisneria); esteemed for\n   the delicacy of its flesh. It visits the United States in autumn;\n   particularly Chesapeake Bay and adjoining waters; -- so named from the\n   markings of the plumage on its back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvassed', 'imp. & p. p.', 'of Canvass');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvassing', 'p. pr. & vb. n.', 'of Canvass');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'n.', 'To sift; to strain; to examine thoroughly; to scrutinize;\n   as, to canvass the votes cast at an election; to canvass a district\n   with reference to its probable vote.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'n.', 'To examine by discussion; to debate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'n.', 'To go trough, with personal solicitation or public\n   addresses; as, to canvass a district for votes; to canvass a city for\n   subscriptions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'v. i.', 'To search thoroughly; to engage in solicitation by\n   traversing a district; as, to canvass for subscriptions or for votes;\n   to canvass for a book, a publisher, or in behalf of a charity; --\n   commonly followed by for.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'n.', 'Close inspection; careful review for verification; as, a\n   canvass of votes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'n.', 'Examination in the way of discussion or debate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvass', 'n.', 'Search; exploration; solicitation; systematic effort to\n   obtain votes, subscribers, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canvasser', 'n.', 'One who canvasses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cany', 'a.', 'Of or pertaining to cane or canes; abounding with canes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canyon', 'n.', 'The English form of the Spanish word Caon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canzone', 'n.', 'A song or air for one or more voices, of Provencal origin,\n   resembling, though not strictly, the madrigal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canzone', 'n.', 'An instrumental piece in the madrigal style.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Canzonet', 'n.', 'A short song, in one or more parts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caoutchin', 'n.', 'An inflammable, volatile, oily, liquid hydrocarbon,\n   obtained by the destructive distillation of caoutchouc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caoutchouc', 'n.', 'A tenacious, elastic, gummy substance obtained from the\n   milky sap of several plants of tropical South America (esp. the\n   euphorbiaceous tree Siphonia elastica or Hevea caoutchouc); Asia, and\n   Africa. Being impermeable to liquids and gases, and not readly affected\n   by exposure to air, acids, and alkalies, it is used, especially when\n   vulcanized, for many purposes in the arts and in manufactures. Also\n   called India rubber (because it was first brought from India, and was\n   formerly used chiefly for erasing pencil marks) and gum elastic. See\n   Vulcanization.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caoutchoucin', 'n.', 'See Caoutchin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'A covering for the head');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'One usually with a visor but without a brim, for men and boys');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'One of lace, muslin, etc., for women, or infants');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'One used as the mark or ensign of some rank, office, or\n   dignity, as that of a cardinal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'The top, or uppermost part; the chief.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'A respectful uncovering of the head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'The whole top of the head of a bird from the base of the bill\n   to the nape of the neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'Anything resembling a cap in form, position, or use');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'The uppermost of any assemblage of parts; as, the cap of\n   column, door, etc.; a capital, coping, cornice, lintel, or plate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'Something covering the top or end of a thing for protection or\n   ornament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'A collar of iron or wood used in joining spars, as the mast\n   and the topmast, the bowsprit and the jib boom; also, a covering of\n   tarred canvas at the end of a rope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'A percussion cap. See under Percussion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'The removable cover of a journal box.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'A portion of a spherical or other convex surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'n.', 'A large size of writing paper; as, flat cap; foolscap; legal\n   cap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capped', 'imp. & p. p.', 'of Cap');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capping', 'p. pr. & vb. n.', 'of Cap');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'v. t.', 'To cover with a cap, or as with a cap; to provide with a\n   cap or cover; to cover the top or end of; to place a cap upon the\n   proper part of; as, to cap a post; to cap a gun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'v. t.', 'To deprive of cap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'v. t.', 'To complete; to crown; to bring to the highest point or\n   consummation; as, to cap the climax of absurdity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'v. t.', 'To salute by removing the cap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'v. t.', 'To match; to mate in contest; to furnish a complement to;\n   as, to cap text; to cap proverbs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cap', 'v. i.', 'To uncover the head respectfully.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capabilities', 'pl. ', 'of Capability');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capability', 'n.', 'The quality of being capable; capacity; capableness;\n   esp. intellectual power or ability.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capability', 'n.', 'Capacity of being used or improved.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capable', 'a.', 'Possessing ability, qualification, or susceptibility;\n   having capacity; of sufficient size or strength; as, a room capable of\n   holding a large number; a castle capable of resisting a long assault.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capable', 'a.', 'Possessing adequate power; qualified; able; fully\n   competent; as, a capable instructor; a capable judge; a mind capable of\n   nice investigations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capable', 'a.', 'Possessing legal power or capacity; as, a man capable of\n   making a contract, or a will.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capable', 'a.', 'Capacious; large; comprehensive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capableness', 'n.', 'The quality or state of being capable; capability;\n   adequateness; competency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacified', 'imp. & p. p.', 'of Capacify');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacify', 'v. t.', 'To quality.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacious', 'a.', 'Having capacity; able to contain much; large; roomy;\n   spacious; extended; broad; as, a capacious vessel, room, bay, or\n   harbor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacious', 'a.', 'Able or qualified to make large views of things, as in\n   obtaining knowledge or forming designs; comprehensive; liberal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capaciosly', 'adv.', 'In a capacious manner or degree; comprehensively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capaciousness', 'n.', 'The quality of being capacious, as of a vessel, a\n   reservoir a bay, the mind, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacitated', 'imp. & p. p.', 'of Capacitate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacitating', 'p. pr. & vb. n.', 'of Capacitate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacitate', 'v. t.', 'To render capable; to enable; to qualify.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacities', 'pl. ', 'of Capacity');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacity', 'n.', 'The power of receiving or containing; extent of room or\n   space; passive power; -- used in reference to physical things.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacity', 'n.', 'The power of receiving and holding ideas, knowledge,\n   etc.; the comprehensiveness of the mind; the receptive faculty;\n   capability of undestanding or feeling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacity', 'n.', 'Ability; power pertaining to, or resulting from, the\n   possession of strength, wealth, or talent; possibility of being or of\n   doing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacity', 'n.', 'Outward condition or circumstances; occupation;\n   profession; character; position; as, to work in the capacity of a mason\n   or a carpenter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capacity', 'n.', 'Legal or noral qualification, as of age, residence,\n   character, etc., necessary for certain purposes, as for holding office,\n   for marrying, for making contracts, will, etc.; legal power or right;\n   competency.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capape', 'adv.', 'See Cap-a-pie.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capapie', 'adv.', 'From head to foot; at all points.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparison', 'n.', 'An ornamental covering or housing for a horse; the\n   harness or trappings of a horse, taken collectively, esp. when\n   decorative.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparison', 'n.', 'Gay or rich clothing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparisoned', 'imp. & p. p.', 'of Caparison');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparisoning', 'p. pr. & vb. n.', 'of Caparison');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparison', 'v. t.', 'To cover with housings, as a horse; to harness or fit\n   out with decorative trappings, as a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparison', 'v. t.', 'To aborn with rich dress; to dress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caparro', 'n.', 'A large South American monkey (Lagothrix Humboldtii); with\n   prehensile tail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capcase', 'n.', 'A small traveling case or bandbox; formerly, a chest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cape', 'n.', 'A piece or point of land, extending beyond the adjacent coast\n   into the sea or a lake; a promontory; a headland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cape', 'v. i.', 'To head or point; to keep a course; as, the ship capes\n   southwest by south.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cape', 'n.', 'A sleeveless garment or part of a garment, hanging from the\n   neck over the back, arms, and shoulders, but not reaching below the\n   hips. See Cloak.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cape', 'v. i.', 'To gape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capel', 'n.', 'Alt. of Caple');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caple', 'n.', 'A horse; a nag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capel', 'n.', 'A composite stone (quartz, schorl, and hornblende) in the\n   walls of tin and copper lodes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capelan', 'n.', 'See Capelin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capelin', 'n.', 'A small marine fish (Mallotus villosus) of the family\n   Salmonidae, very abundant on the coasts of Greenland, Iceland,\n   Newfoundland, and Alaska. It is used as a bait for the cod.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cappeline', 'n.', 'A hood-shaped bandage for the head, the shoulder, or the\n   stump of an amputated limb.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capella', 'n.', 'A brilliant star in the constellation Auriga.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capellane', 'n.', 'The curate of a chapel; a chaplain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capelle', 'n.', 'The private orchestra or band of a prince or of a church.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capellet', 'n.', 'A swelling, like a wen, on the point of the elbow (or the\n   heel of the hock) of a horse, caused probably by bruises in lying down.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capellmeister', 'n.', 'The musical director in royal or ducal chapel; a\n   choir-master.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capered', 'imp. & p. p.', 'of Caper');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capering', 'p. pr. & vb. n.', 'of Caper');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper', 'v. i.', 'To leap or jump about in a sprightly manner; to cut\n   capers; to skip; to spring; to prance; to dance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper', 'n.', 'A frolicsome leap or spring; a skip; a jump, as in mirth or\n   dancing; a prank.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper', 'n.', 'A vessel formerly used by the Dutch, privateer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper', 'n.', 'The pungent grayish green flower bud of the European and\n   Oriental caper (Capparis spinosa); much used for pickles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper', 'n.', 'A plant of the genus Capparis; -- called also caper bush,\n   caper tree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caperberry', 'n.', 'The small olive-shaped berry of the European and\n   Oriental caper, said to be used in pickles and as a condiment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caperberry', 'n.', 'The currantlike fruit of the African and Arabian caper\n   (Capparis sodado).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper bush', '', 'Alt. of Caper tree');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caper tree', '', 'See Capper, a plant, 2.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capercailzie', 'n.', 'Alt. of Capercally');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capercally', 'n.', 'A species of grouse (Tetrao uragallus) of large size\n   and fine flavor, found in northern Europe and formerly in Scotland; --\n   called also cock of the woods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caperclaw', 'v. t.', 'To treat with cruel playfulness, as a cat treats a\n   mouse; to abuse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caperer', 'n.', 'One who capers, leaps, and skips about, or dances.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capful', 'n.', 'As much as will fill a cap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capias', 'n.', 'A writ or process commanding the officer to take the body\n   of the person named in it, that is, to arrest him; -- also called writ\n   of capias.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capibara', 'n.', 'See Capybara.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillaceous', 'a.', 'Having long filaments; resembling a hair; slender.\n   See Capillary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillaire', 'n.', 'A sirup prepared from the maiden-hair, formerly\n   supposed to have medicinal properties.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillaire', 'n.', 'Any simple sirup flavored with orange flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillament', 'n.', 'A filament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillament', 'n.', 'Any villous or hairy covering; a fine fiber or\n   filament, as of the nerves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillariness', 'n.', 'The quality of being capillary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillarity', 'n.', 'The quality or condition of being capillary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillarity', 'n.', 'The peculiar action by which the surface of a liquid,\n   where it is in contact with a solid (as in a capillary tube); is\n   elevated or depressed; capillary attraction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillary', 'a.', 'Resembling a hair; fine; minute; very slender; having\n   minute tubes or interspaces; having very small bore; as, the capillary\n   vessels of animals and plants.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillary', 'a.', 'Pertaining to capillary tubes or vessels; as, capillary\n   action.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillary', 'n.', 'A tube or vessel, extremely fine or minute.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillary', 'n.', 'A minute, thin-walled vessel; particularly one of the\n   smallest blood vessels connecting arteries and veins, but used also for\n   the smallest lymphatic and biliary vessels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillation', 'n.', 'A capillary blood vessel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillature', 'n.', 'A bush of hair; frizzing of the hair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capilliform', 'a.', 'In the shape or form of, a hair, or of hairs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capillose', 'a.', 'Having much hair; hairy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capistrate', 'a.', 'Hooded; cowled.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'Of or pertaining to the head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'Having reference to, or involving, the forfeiture of the\n   head or life; affecting life; punishable with death; as, capital\n   trials; capital punishment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'First in importance; chief; principal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'Chief, in a political sense, as being the seat of the\n   general government of a state or nation; as, Washington and Paris are\n   capital cities.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'Of first rate quality; excellent; as, a capital speech or\n   song.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'The head or uppermost member of a column, pilaster, etc.\n   It consists generally of three parts, abacus, bell (or vase); and\n   necking. See these terms, and Column.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'The seat of government; the chief city or town in a\n   country; a metropolis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'n.', 'Money, property, or stock employed in trade, manufactures,\n   etc.; the sum invested or lent, as distinguished from the income or\n   interest. See Capital stock, under Capital, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'a.', 'That portion of the produce of industry, which may be\n   directly employed either to support human beings or to assist in\n   production.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'a.', 'Anything which can be used to increase ones power or\n   influence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'a.', 'An imaginary line dividing a bastion, ravelin, or other\n   work, into two equal parts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'a.', 'A chapter, or section, of a book.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capital', 'a.', 'See Capital letter, under Capital, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalist', 'n.', 'One who has capital; one who has money for investment,\n   or money invested; esp. a person of large property, which is employed\n   in business.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalization', 'n.', 'The act or process of capitalizing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalized', 'imp. & p. p.', 'of Capitalize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalizing', 'p. pr. & vb. n.', 'of Capitalize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalize', 'v. t.', 'To convert into capital, or to use as capital.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalize', 'v. t.', 'To compute, appraise, or assess the capital value of\n   (a patent right, an annuity, etc.)');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalize', 'v. t.', 'To print in capital letters, or with an initial\n   capital.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitally', 'adv.', 'In a way involving the forfeiture of the head or life;\n   as, to punish capitally.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitally', 'adv.', 'In a capital manner; excellently.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitalness', 'n.', 'The quality of being capital; preeminence.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitan Pasha', '', 'Alt. of Pacha');
  INSERT INTO words (word, word_type, description)VALUES  (    'Pacha', '', 'The chief admiral of the Turkish fleet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitate', 'a.', 'Headlike in form; also, having the distal end enlarged\n   and rounded, as the stigmas of certain flowers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitate', 'a.', 'Having the flowers gathered into a head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitatim', 'a.', 'Of so much per head; as, a capitatim tax; a capitatim\n   grant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitation', 'n.', 'A numbering of heads or individuals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitation', 'n.', 'A tax upon each head or person, without reference to\n   property; a poll tax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capite', 'n.', 'See under Tenant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitellate', 'a.', 'Having a very small knoblike termination, or collected\n   into minute capitula.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitibranchiata', 'n. pl.', 'A division of annelids in which the gills\n   arise from or near the head. See Tubicola.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitol', '', 'The temple of Jupiter, at Rome, on the Mona Capitolinus,\n   where the Senate met.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitol', '', 'The edifice at Washington occupied by the Congress of the\n   United States; also, the building in which the legislature of State\n   holds its sessions; a statehouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitolian', 'a.', 'Alt. of Capitoline');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitoline', 'a.', 'Of or pertaining to the Capitol in Rome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitula', 'n. pl.', 'See Capitulum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitular', 'n.', 'An act passed in a chapter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitular', 'n.', 'A member of a chapter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitular', 'n.', 'The head or prominent part.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitular', 'a.', 'Of or pertaining to a chapter; capitulary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitular', 'a.', 'Growing in, or pertaining to, a capitulum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitular', 'a.', 'Pertaining to a capitulum; as, the capitular process of\n   a vertebra, the process which articulates with the capitulum of a rib.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitularly', 'adv.', 'In the manner or form of an ecclesiastical chapter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitularies', 'pl. ', 'of Capitulary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulary', 'n.', 'A capitular.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulary', 'n.', 'The body of laws or statutes of a chapter, or of an\n   ecclesiastical council.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulary', 'n.', 'A collection of laws or statutes, civil and\n   ecclesiastical, esp. of the Frankish kings, in chapters or sections.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulary', 'a.', 'Relating to the chapter of a cathedral; capitular.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulated', 'imp. & p. p.', 'of Capitulate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulating', 'p. pr. & vb. n.', 'of Capitulate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulate', 'n.', 'To settle or draw up the heads or terms of an\n   agreement, as in chapters or articles; to agree.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulate', 'n.', 'To surrender on terms agreed upon (usually, drawn up\n   under several heads); as, an army or a garrison capitulates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulate', 'v. t.', 'To surrender or transfer, as an army or a fortress,\n   on certain conditions.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulation', 'n.', 'A reducing to heads or articles; a formal agreement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulation', 'n.', 'The act of capitulating or surrendering to an emeny\n   upon stipulated terms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulation', 'n.', 'The instrument containing the terms of an agreement\n   or surrender.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulator', 'n.', 'One who capitulates.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitule', 'n.', 'A summary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulum', 'n.', 'A thick head of flowers on a very short axis, as a\n   clover top, or a dandelion; a composite flower. A capitulum may be\n   either globular or flat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capitulum', 'n.', 'A knoblike protuberance of any part, esp. at the end of\n   a bone or cartilage. [See Illust. of Artiodactyla.]');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capivi', 'n.', 'A balsam of the Spanish West Indies. See Copaiba.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caple', 'n.', 'See Capel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caplin', 'n.', 'See Capelin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caplin', 'n.', 'Alt. of Capling');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capling', 'n.', 'The cap or coupling of a flail, through which the thongs\n   pass which connect the handle and swingel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capnomancy', 'n.', 'Divination by means of the ascent or motion of smoke.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capnomor', 'n.', 'A limpid, colorless oil with a peculiar odor, obtained\n   from beech tar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capoc', 'n.', 'A sort of cotton so short and fine that it can not be spun,\n   used in the East Indies to line palanquins, to make mattresses, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capoches', 'pl. ', 'of Capoch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capoch', 'n.', 'A hood; especially, the hood attached to the gown of a\n   monk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capoched', 'imp. & p. p.', 'of Capoch');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capoch', 'v. t.', 'To cover with, or as with, a hood; hence, to hoodwink or\n   blind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capon', 'n.', 'A castrated cock, esp. when fattened; a male chicken gelded\n   to improve his flesh for the table.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capon', 'v. t.', 'To castrate; to make a capon of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caponet', 'n.', 'A young capon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caponiere', 'n.', 'A work made across or in the ditch, to protect it from\n   the enemy, or to serve as a covered passageway.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caponize', 'v. t.', 'To castrate, as a fowl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capot', 'n.', 'A winning of all the tricks at the game of piquet. It counts\n   for forty points.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capotted', 'imp. & p. p.', 'of Capot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capot', 'v. t.', 'To win all the tricks from, in playing at piquet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capote', 'n.', 'A long cloak or overcoat, especially one with a hood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capouch', 'n. & v. t.', 'Same as Capoch.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cappadine', 'n.', 'A floss or waste obtained from the cocoon after the silk\n   has been reeled off, used for shag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cappaper', '', 'See cap, n., also Paper, n.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cappeak', 'n.', 'The front piece of a cap; -- now more commonly called\n   visor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cappella', 'n.', 'See A cappella.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capper', 'n.', 'One whose business is to make or sell caps.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capper', 'n.', 'A by-bidder; a decoy for gamblers [Slang, U. S.].');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capper', 'n.', 'An instrument for applying a percussion cap to a gun or\n   cartridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capping plane', '', 'A plane used for working the upper surface of\n   staircase rails.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capra', 'n.', 'A genus of ruminants, including the common goat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprate', 'n.', 'A salt of capric acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capreolate', 'a.', 'Having a tendril or tendrils.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capreoline', 'a.', 'Of or pertaining to the roebuck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capric', 'a.', 'Of or pertaining to capric acid or its derivatives.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cariccio', 'n.', 'A piece in a free form, with frequent digressions from\n   the theme; a fantasia; -- often called caprice.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cariccio', 'n.', 'A caprice; a freak; a fancy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capricioso', 'a.', 'In a free, fantastic style.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprice', 'v. i.', 'An abrupt change in feeling, opinion, or action,\n   proceeding from some whim or fancy; a freak; a notion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprice', 'v. i.', 'See Capriccio.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capricious', 'a.', 'Governed or characterized by caprice; apt to change\n   suddenly; freakish; whimsical; changeable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capricorn', 'n.', 'The tenth sign of zodiac, into which the sun enters at\n   the winter solstice, about December 21. See Tropic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capricorn', 'n.', 'A southern constellation, represented on ancient\n   monuments by the figure of a goat, or a figure with its fore part like\n   a fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprid', 'a.', 'Of or pertaining to the tribe of ruminants of which the\n   goat, or genus Capra, is the type.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprification', 'n.', 'The practice of hanging, upon the cultivated fig\n   tree, branches of the wild fig infested with minute hymenopterous\n   insects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprifole', 'n.', 'The woodbine or honeysuckle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprifoliaceous', 'a.', 'Of, pertaining to, or resembling, the Honeysuckle\n   family of plants (Caprifoliacae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capriform', 'a.', 'Having the form of a goat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprigenous', 'a.', 'Of the goat kind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprine', 'a.', 'Of or pertaining to a goat; as, caprine gambols.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capriole', 'v. i.', 'A leap that a horse makes with all fours, upwards\n   only, without advancing, but with a kick or jerk of the hind legs when\n   at the height of the leap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capriole', 'v. i.', 'A leap or caper, as in dancing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capriole', 'v. i.', 'To perform a capriole.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capriped', 'a.', 'Having feet like those of a goat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caproate', 'n.', 'A salt of caproic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caproic', 'a.', 'See under Capric.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprylate', 'n.', 'A salt of caprylic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caprylic', 'a.', 'See under Capric.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsaicin', 'n.', 'A colorless crystalline substance extracted from the\n   Capsicum annuum, and giving off vapors of intense acridity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsheaf', 'n.', 'The top sheaf of a stack of grain: (fig.) the crowning or\n   finishing part of a thing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsicin', 'n.', 'A red liquid or soft resin extracted from various species\n   of capsicum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsicine', 'n.', 'A volatile alkaloid extracted from Capsicum annuum or\n   from capsicin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsicum', 'n.', 'A genus of plants of many species, producing capsules or\n   dry berries of various forms, which have an exceedingly pungent, biting\n   taste, and when ground form the red or Cayenne pepper of commerce.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsized', 'imp. & p. p.', 'of Capsize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsizing', 'p. pr. & vb. n.', 'of Capsize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsize', 'v. t. & i.', 'To upset or overturn, as a vessel or other body.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsize', 'n.', 'An upset or overturn.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsquare', 'n.', 'A metal covering plate which passes over the trunnions\n   of a cannon, and holds it in place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capstan', 'n.', 'A vertical cleated drum or cylinder, revolving on an\n   upright spindle, and surmounted by a drumhead with sockets for bars or\n   levers. It is much used, especially on shipboard, for moving or raising\n   heavy weights or exerting great power by traction upon a rope or cable,\n   passing around the drum. It is operated either by steam power or by a\n   number of men walking around the capstan, each pushing on the end of a\n   lever fixed in its socket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capstone', 'n.', 'A fossil echinus of the genus Cannulus; -- so called from\n   its supposed resemblance to a cap.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsular', 'a.', 'Alt. of Capsulary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsulary', 'a.', 'Of or pertaining to a capsule; having the nature of a\n   capsule; hollow and fibrous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsulate', 'a.', 'Alt. of Capsulated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsulated', 'a.', 'Inclosed in a capsule, or as in a chest or box.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'a dry fruit or pod which is made up of several parts or\n   carpels, and opens to discharge the seeds, as, the capsule of the\n   poppy, the flax, the lily, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'A small saucer of clay for roasting or melting samples of\n   ores, etc.; a scorifier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'a small, shallow, evaporating dish, usually of porcelain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'A small cylindrical or spherical gelatinous envelope in\n   which nauseous or acrid doses are inclosed to be swallowed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'A membranous sac containing fluid, or investing an organ\n   or joint; as, the capsule of the lens of the eye. Also, a capsulelike\n   organ.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'A metallic seal or cover for closing a bottle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capsule', 'n.', 'A small cup or shell, as of metal, for a percussion cap,\n   cartridge, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'A head, or chief officer');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'The military officer who commands a company, troop, or\n   battery, or who has the rank entitling him to do so though he may be\n   employed on other service.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'An officer in the United States navy, next above a\n   commander and below a commodore, and ranking with a colonel in the\n   army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'By courtesy, an officer actually commanding a vessel,\n   although not having the rank of captain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'The master or commanding officer of a merchant vessel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'One in charge of a portion of a ships company; as, a\n   captain of a top, captain of a gun, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'The foreman of a body of workmen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'A person having authority over others acting in concert;\n   as, the captain of a boats crew; the captain of a football team.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'n.', 'A military leader; a warrior.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'v. t.', 'To act as captain of; to lead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captain', 'a.', 'Chief; superior.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captaincy', 'n.', 'The rank, post, or commission of a captain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captainry', 'n.', 'Power, or command, over a certain district;\n   chieftainship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captainship', 'n.', 'The condition, rank, post, or authority of a captain\n   or chief commander.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captainship', 'n.', 'Military skill; as, to show good captainship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captation', 'n.', 'A courting of favor or applause, by flattery or address;\n   a captivating quality; an attraction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caption', 'n.', 'A caviling; a sophism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caption', 'n.', 'The act of taking or arresting a person by judicial\n   process.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caption', 'n.', 'That part of a legal instrument, as a commission,\n   indictment, etc., which shows where, when, and by what authority, it\n   was taken, found, or executed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caption', 'n.', 'The heading of a chapter, section, or page.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captious', 'a.', 'Apt to catch at faults; disposed to find fault or to\n   cavil; eager to object; difficult to please.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captious', 'a.', 'Fitted to harass, perplex, or insnare; insidious;\n   troublesome.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captiously', 'adv.', 'In a captious manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captiousness', 'n.', 'Captious disposition or manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivated', 'imp. & p. p.', 'of Captivate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivating', 'p. pr. & vb. n.', 'of Captivate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivate', 'v. t.', 'To take prisoner; to capture; to subdue.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivate', 'v. t.', 'To acquire ascendancy over by reason of some art or\n   attraction; to fascinate; to charm; as, Cleopatra captivated Antony;\n   the orator captivated all hearts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivate', 'p. a.', 'Taken prisoner; made captive; insnared; charmed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivating', 'a.', 'Having power to captivate or charm; fascinating; as,\n   captivating smiles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivation', 'n.', 'The act of captivating.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captive', 'n.', 'A prisoner taken by force or stratagem, esp., by an enemy,\n   in war; one kept in bondage or in the power of another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captive', 'n.', 'One charmed or subdued by beaty, excellence, or affection;\n   one who is captivated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captive', 'a.', 'Made prisoner, especially in war; held in bondage or in\n   confinement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captive', 'a.', 'Subdued by love; charmed; captivated.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captive', 'a.', 'Of or pertaining to bondage or confinement; serving to\n   confine; as, captive chains; captive hours.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captived', 'imp. & p. p.', 'of Captive');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captiving', 'p. pr. & vb. n.', 'of Captive');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captive', 'v. t.', 'To take prisoner; to capture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivity', 'n.', 'The state of being a captive or a prisoner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captivity', 'n.', 'A state of being under control; subjection of the will\n   or affections; bondage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captor', 'n.', 'One who captures any person or thing, as a prisoner or a\n   prize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capture', 'n.', 'The act of seizing by force, or getting possession of by\n   superior power or by stratagem; as, the capture of an enemy, a vessel,\n   or a criminal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capture', 'n.', 'The securing of an object of strife or desire, as by the\n   power of some attraction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capture', 'n.', 'The thing taken by force, surprise, or stratagem; a prize;\n   prey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Captured', 'imp. & p. p.', 'of Capture');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capturing', 'p. pr. & vb. n.', 'of Capture');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capture', 'v. t.', 'To seize or take possession of by force, surprise, or\n   stratagem; to overcome and hold; to secure by effort.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuccio', 'n.', 'A capoch or hood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuched', 'a.', 'Cover with, or as with, a hood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuchin', 'n.', 'A Franciscan monk of the austere branch established in\n   1526 by Matteo di Baschi, distinguished by wearing the long pointed\n   cowl or capoch of St. Francis.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuchin', 'n.', 'A garment for women, consisting of a cloak and hood,\n   resembling, or supposed to resemble, that of capuchin monks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuchin', 'n.', 'A long-tailed South American monkey (Cabus capucinus);\n   having the forehead naked and wrinkled, with the hair on the crown\n   reflexed and resembling a monks cowl, the rest being of a grayish\n   white; -- called also capucine monkey, weeper, sajou, sapajou, and sai.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuchin', 'n.', 'Other species of Cabus, as C. fatuellus (the brown or\n   horned capucine.); C. albifrons (the cararara); and C. apella.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capuchin', 'n.', 'A variety of the domestic pigeon having a hoodlike tuft\n   of feathers on the head and sides of the neck.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capucine', 'n.', 'See Capuchin, 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capulet', 'n.', 'Same as Capellet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capulin', 'n.', 'The Mexican cherry (Prunus Capollin).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capita', 'pl. ', 'of Caput');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caput', 'n.', 'The head; also, a knoblike protuberance or capitulum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caput', 'n.', 'The top or superior part of a thing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caput', 'n.', 'The council or ruling body of the University of Cambridge\n   prior to the constitution of 1856.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Capybara', 'n.', 'A large South American rodent (Hydrochaerus capybara)\n   Living on the margins of lakes and rivers. It is the largest extant\n   rodent, being about three feet long, and half that in height. It\n   somewhat resembles the Guinea pig, to which it is related; -- called\n   also cabiai and water hog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'A small vehicle moved on wheels; usually, one having but two\n   wheels and drawn by one horse; a cart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'A vehicle adapted to the rails of a railroad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'A chariot of war or of triumph; a vehicle of splendor,\n   dignity, or solemnity.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'The stars also called Charless Wain, the Great Bear, or the\n   Dipper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'The cage of a lift or elevator.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'The basket, box, or cage suspended from a balloon to contain\n   passengers, ballast, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Car', 'n.', 'A floating perforated box for living fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carabid', 'a.', 'Of, pertaining to, or resembling, the genus Carbus or\n   family Carabidae.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carabid', 'n.', 'One of the Carabidae, a family of active insectivorous\n   beetles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carabine', 'n.', 'A carbine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carabineer', 'n.', 'A carbineer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caraboid', 'a.', 'Like, or pertaining to the genus Carabus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carabus', 'n.', 'A genus of ground beetles, including numerous species.\n   They devour many injurious insects.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carac', 'n.', 'See Carack.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracal', 'n.', 'A lynx (Felis, or Lynx, caracal.) It is a native of Africa\n   and Asia. Its ears are black externally, and tipped with long black\n   hairs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracara', 'n.', 'A south American bird of several species and genera,\n   resembling both the eagles and the vultures. The caracaras act as\n   scavengers, and are also called carrion buzzards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carack', 'n.', 'A kind of large ship formerly used by the Spaniards and\n   Portuguese in the East India trade; a galleon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracole', 'n.', 'A half turn which a horseman makes, either to the right\n   or the left.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracole', 'n.', 'A staircase in a spiral form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracoled', 'imp. & p. p.', 'of Caracole');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracole', 'v. i.', 'To move in a caracole, or in caracoles; to wheel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracoly', 'n.', 'An alloy of gold, silver, and copper, of which an\n   inferior quality of jewelry is made.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracore', 'n.', 'Alt. of Caracora');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caracora', 'n.', 'A light vessel or proa used by the people of Borneo,\n   etc., and by the Dutch in the East Indies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carafe', 'n.', 'A glass water bottle for the table or toilet; -- called\n   also croft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carageen', 'n.', 'Alt. of Caragheen');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caragheen', 'n.', 'See Carrageen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carambola', 'n.', 'An East Indian tree (Averrhoa Carambola); and its acid,\n   juicy fruit; called also Coromandel gooseberry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caramel', 'n.', 'Burnt sugar; a brown or black porous substance obtained by\n   heating sugar. It is soluble in water, and is used for coloring\n   spirits, gravies, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caramel', 'n.', 'A kind of confectionery, usually a small cube or square of\n   tenacious paste, or candy, of varying composition and flavor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carangoid', 'a.', 'Belonging to the Carangidae, a family of fishes allied\n   to the mackerels, and including the caranx, American bluefish, and the\n   pilot fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caranx', 'n.', 'A genus of fishes, common on the Atlantic coast, including\n   the yellow or golden mackerel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carapace', 'n.', 'The thick shell or shield which covers the back of the\n   tortoise, or turtle, the crab, and other crustaceous animals.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carapato', 'n.', 'A south American tick of the genus Amblyomma. There are\n   several species, very troublesome to man and beast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carapax', 'n.', 'See Carapace.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carat', 'n.', 'The weight by which precious stones and pearls are weighed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carat', 'n.', 'A twenty-fourth part; -- a term used in estimating the\n   proportionate fineness of gold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravan', 'n.', 'A company of travelers, pilgrims, or merchants, organized\n   and equipped for a long journey, or marching or traveling together,\n   esp. through deserts and countries infested by robbers or hostile\n   tribes, as in Asia or Africa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravan', 'n.', 'A large, covered wagon, or a train of such wagons, for\n   conveying wild beasts, etc., for exhibition; an itinerant show, as of\n   wild beasts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravan', 'n.', 'A covered vehicle for carrying passengers or for moving\n   furniture, etc.; -- sometimes shorted into van.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravaneer', 'n.', 'The leader or driver of the camels in caravan.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravansaries', 'pl. ', 'of Caravansary');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravansary', 'n.', 'A kind of inn, in the East, where caravans rest at\n   night, being a large, rude, unfurnished building, surrounding a court.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravel', 'n.', 'A name given to several kinds of vessels.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravel', 'n.', 'The caravel of the 16th century was a small vessel with\n   broad bows, high, narrow poop, four masts, and lateen sails. Columbus\n   commanded three caravels on his great voyage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravel', 'n.', 'A Portuguese vessel of 100 or 150 tons burden.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravel', 'n.', 'A small fishing boat used on the French coast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caravel', 'n.', 'A Turkish man-of-war.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caraway', 'n.', 'A biennial plant of the Parsley family (Carum Carui). The\n   seeds have an aromatic smell, and a warm, pungent taste. They are used\n   in cookery and confectionery, and also in medicine as a carminative.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Caraway', 'n.', 'A cake or sweetmeat containing caraway seeds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbamic', 'a.', 'Pertaining to an acid so called.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbamide', 'n.', 'The technical name for urea.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbamine', 'n.', 'An isocyanide of a hydrocarbon radical. The carbamines\n   are liquids, usually colorless, and of unendurable odor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbanil', 'n.', 'A mobile liquid, CO.N.C6H5, of pungent odor. It is the\n   phenyl salt of isocyanic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbazol', 'n.', 'A white crystallized substance, C12H8NH, derived from\n   aniline and other amines.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbazotate', 'n.', 'A salt of carbazotic or picric acid; a picrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbazotic', 'a.', 'Containing, or derived from, carbon and nitrogen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbide', 'n.', 'A binary compound of carbon with some other element or\n   radical, in which the carbon plays the part of a negative; -- formerly\n   termed carburet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbimide', 'n.', 'The technical name for isocyanic acid. See under\n   Isocyanic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbine', 'n.', 'A short, light musket or rifle, esp. one used by mounted\n   soldiers or cavalry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbineer', 'n.', 'A soldier armed with a carbine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbinol', 'n.', 'Methyl alcohol, CH3OH; -- also, by extension, any one in\n   the homologous series of paraffine alcohols of which methyl alcohol is\n   the type.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbohydrate', 'n.', 'One of a group of compounds including the sugars,\n   starches, and gums, which contain six (or some multiple of six) carbon\n   atoms, united with a variable number of hydrogen and oxygen atoms, but\n   with the two latter always in proportion as to form water; as dextrose,\n   C6H12O6.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbohydride', 'n.', 'A hydrocarbon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbolic', 'a.', 'Pertaining to, or designating, an acid derived from coal\n   tar and other sources; as, carbolic acid (called also phenic acid, and\n   phenol). See Phenol.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbolize', 'v. t.', 'To apply carbolic acid to; to wash or treat with\n   carbolic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbon', 'n.', 'An elementary substance, not metallic in its nature, which\n   is present in all organic compounds. Atomic weight 11.97. Symbol C. it\n   is combustible, and forms the base of lampblack and charcoal, and\n   enters largely into mineral coals. In its pure crystallized state it\n   constitutes the diamond, the hardest of known substances, occuring in\n   monometric crystals like the octahedron, etc. Another modification is\n   graphite, or blacklead, and in this it is soft, and occurs in hexagonal\n   prisms or tables. When united with oxygen it forms carbon dioxide,\n   commonly called carbonic acid, or carbonic oxide, according to the\n   proportions of the oxygen; when united with hydrogen, it forms various\n   compounds called hydrocarbons. Compare Diamond, and Graphite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonaceous', 'a.', 'Pertaining to, containing, or composed of, carbon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonade', 'n.', 'Alt. of Carbonado');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonado', 'n.', 'Flesh, fowl, etc., cut across, seasoned, and broiled on\n   coals; a chop.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonadoed', 'imp. & p. p.', 'of Carbonade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonadoing', 'p. pr. & vb. n.', 'of Carbonade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonado', 'v. t.', 'Alt. of Carbonade');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonade', 'v. t.', 'To cut (meat) across for frying or broiling; to cut\n   or slice and broil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonade', 'v. t.', 'To cut or hack, as in fighting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonadoes', 'pl. ', 'of Carbonado');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonado', 'n.', 'A black variety of diamond, found in Brazil, and used\n   for diamond drills. It occurs in irregular or rounded fragments, rarely\n   distinctly crystallized, with a texture varying from compact to porous.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonarism', 'n.', 'The principles, practices, or organization of the\n   Carbonari.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonari', 'pl. ', 'of Carbonaro');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonaro', 'n.', 'A member of a secret political association in Italy,\n   organized in the early part of the nineteenth centry for the purpose of\n   changing the government into a republic.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonatation', 'n.', 'The saturation of defecated beet juice with carbonic\n   acid gas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonate', 'n.', 'A salt or carbonic acid, as in limestone, some forms of\n   lead ore, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonated', 'a.', 'Combined or impregnated with carbonic acid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbone', 'v. t.', 'To broil. [Obs.] \"We had a calfs head carboned\".');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonic', 'a.', 'Of, pertaining to, or obtained from, carbon; as, carbonic\n   oxide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonide', 'n.', 'A carbide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carboniferous', 'a.', 'Producing or containing carbon or coal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonization', 'n.', 'The act or process of carbonizing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonized', 'imp. & p. p.', 'of Carbonize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonizing', 'p. pr. & vb. n.', 'of Carbonize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonize', 'v. t.', 'To convert (an animal or vegetable substance) into a\n   residue of carbon by the action of fire or some corrosive agent; to\n   char.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonize', 'v. t.', 'To impregnate or combine with carbon, as in making\n   steel by cementation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonometer', 'n.', 'An instrument for detecting and measuring the amount\n   of carbon which is present, or more esp. the amount of carbon dioxide,\n   by its action on limewater or by other means.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbonyl', 'n.', 'The radical (CO); occuring, always combined, in many\n   compounds, as the aldehydes, the ketones, urea, carbonyl chloride, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbostyril', 'n.', 'A white crystalline substance, C9H6N.OH, of acid\n   properties derived from one of the amido cinnamic acids.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carboxide', 'n.', 'A compound of carbon and oxygen, as carbonyl, with some\n   element or radical; as, potassium carboxide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carboxyl', 'n.', 'The complex radical, CO.OH, regarded as the essential and\n   characteristic constituent which all oxygen acids of carbon (as formic,\n   acetic, benzoic acids, etc.) have in common; -- called also oxatyl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carboy', 'n.', 'A large, globular glass bottle, esp. one of green glass,\n   inclosed in basket work or in a box, for protection; -- used commonly\n   for carrying corrosive liquids; as sulphuric acid, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbuncle', 'n.', 'A beautiful gem of a deep red color (with a mixture of\n   scarlet) called by the Greeks anthrax; found in the East Indies. When\n   held up to the sun, it loses its deep tinge, and becomes of the color\n   of burning coal. The name belongs for the most part to ruby sapphire,\n   though it has been also given to red spinel and garnet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbuncle', 'n.', 'A very painful acute local inflammation of the\n   subcutaneous tissue, esp. of the trunk or back of the neck,\n   characterized by brawny hardness of the affected parts, sloughing of\n   the skin and deeper tissues, and marked constitutional depression. It\n   differs from a boil in size, tendency to spread, and the absence of a\n   central core, and is frequently fatal. It is also called anthrax.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbuncle', 'n.', 'A charge or bearing supposed to represent the precious\n   stone. It has eight scepters or staves radiating from a common center.\n   Called also escarbuncle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbuncled', 'a.', 'Set with carbuncles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbuncled', 'a.', 'Affected with a carbuncle or carbuncles; marked with\n   red sores; pimpled and blotched.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbuncular', 'a.', 'Belonging to a carbuncle; resembling a carbuncle; red;\n   inflamed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbunculation', 'n.', 'The blasting of the young buds of trees or plants,\n   by excessive heat or cold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburet', 'n.', 'A carbide. See Carbide');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbureted', 'imp. & p. p.', 'of Carburet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburetted', '', 'of Carburet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbureting', 'p. pr. & vb. n.', 'of Carburet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburetting', '', 'of Carburet');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburet', 'v. t.', 'To combine or to impregnate with carbon, as by passing\n   through or over a liquid hydrocarbon; to carbonize or carburize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburetant', 'n.', 'Any volatile liquid used in charging illuminating\n   gases.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbureted', 'a.', 'Combined with carbon in the manner of a carburet or\n   carbide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carbureted', 'a.', 'Saturated or impregnated with some volatile carbon\n   compound; as, water gas is carbureted to increase its illuminating\n   power.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburetor', 'n.', 'An apparatus in which coal gas, hydrogen, or air is\n   passed through or over a volatile hydrocarbon, in order to confer or\n   increase illuminating power.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburization', 'n.', 'The act, process, or result of carburizing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburized', 'imp. & p. p.', 'of Carburize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburizing', 'p. pr. & vb. n.', 'of Carburize');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carburize', 'v. t.', 'To combine with carbon or a carbon compound; -- said\n   esp. of a process for conferring a higher degree of illuminating power\n   on combustible gases by mingling them with a vapor of volatile\n   hydrocarbons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcajou', 'n.', 'The wolverene; -- also applied, but erroneously, to the\n   Canada lynx, and sometimes to the American badger. See Wolverene.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcanet', 'n.', 'A jeweled chain, necklace, or collar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcase', 'n.', 'See Carcass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcasses', 'pl. ', 'of Carcass');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcass', 'n.', 'A dead body, whether of man or beast; a corpse; now\n   commonly the dead body of a beast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcass', 'n.', 'The living body; -- now commonly used in contempt or\n   ridicule.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcass', 'n.', 'The abandoned and decaying remains of some bulky and once\n   comely thing, as a ship; the skeleton, or the uncovered or unfinished\n   frame, of a thing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcass', 'n.', 'A hollow case or shell, filled with combustibles, to be\n   thrown from a mortar or howitzer, to set fire to buldings, ships, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcavelhos', 'n.', 'A sweet wine. See Calcavella.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcelage', 'n.', 'Prison fees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcel lamp', '', 'A French mechanical lamp, for lighthouses, in which a\n   superabundance of oil is pumped to the wick tube by clockwork.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carceral', 'a.', 'Belonging to a prison.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcinological', 'a.', 'Of or pertaining to carcinology.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcinology', 'n.', 'The department of zoology which treats of the\n   Crustacea (lobsters, crabs, etc.); -- called also malacostracology and\n   crustaceology.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcinoma', 'n.', 'A cancer. By some medical writers, the term is applied\n   to an indolent tumor. See Cancer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcinomatous', 'a.', 'Of or pertaining to carcinoma.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carcinosys', 'n.', 'The affection of the system with cancer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'A piece of pasteboard, or thick paper, blank or prepared for\n   various uses; as, a playing card; a visiting card; a card of\n   invitation; pl. a game played with cards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'A published note, containing a brief statement, explanation,\n   request, expression of thanks, or the like; as, to put a card in the\n   newspapers. Also, a printed programme, and (fig.); an attraction or\n   inducement; as, this will be a good card for the last day of the fair.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'A paper on which the points of the compass are marked; the\n   dial or face of the mariners compass.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'A perforated pasteboard or sheet-metal plate for warp\n   threads, making part of the Jacquard apparatus of a loom. See Jacquard.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'An indicator card. See under Indicator.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carded', 'imp. & p. p.', 'of Card');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carding', 'p. pr. & vb. n.', 'of Card');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'v. i.', 'To play at cards; to game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'An instrument for disentangling and arranging the fibers of\n   cotton, wool, flax, etc.; or for cleaning and smoothing the hair of\n   animals; -- usually consisting of bent wire teeth set closely in rows\n   in a thick piece of leather fastened to a back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'n.', 'A roll or sliver of fiber (as of wool) delivered from a\n   carding machine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'v. t.', 'To comb with a card; to cleanse or disentangle by carding;\n   as, to card wool; to card a horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'v. t.', 'To clean or clear, as if by using a card.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Card', 'v. t.', 'To mix or mingle, as with an inferior or weaker article.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardamine', 'n.', 'A genus of cruciferous plants, containing the\n   ladys-smock, cuckooflower, bitter cress, meadow cress, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardamom', 'n.', 'The aromatic fruit, or capsule with its seeds, of several\n   plants of the Ginger family growing in the East Indies and elsewhere,\n   and much used as a condiment, and in medicine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardamom', 'n.', 'A plant which produces cardamoms, esp. Elettaria\n   Cardamomum and several species of Amomum.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardboard', 'n.', 'A stiff compact pasteboard of various qualities, for\n   making cards, etc., often having a polished surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardcase', 'n.', 'A case for visiting cards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardecu', 'n.', 'A quarter of a crown.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carder', 'n.', 'One who, or that which cards wool flax, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardia', 'n.', 'The heart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardia', 'n.', 'The anterior or cardiac orifice of the stomach, where the\n   esophagus enters it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiac', 'a.', 'Pertaining to, resembling, or hear the heart; as, the\n   cardiac arteries; the cardiac, or left, end of the stomach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiac', 'a.', 'Exciting action in the heart, through the medium of the\n   stomach; cordial; stimulant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiac', 'n.', 'A medicine which excites action in the stomach; a cardial.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiacal', 'a.', 'Cardiac.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiacle', 'n.', 'A pain about the heart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiagraph', 'n.', 'See Cardiograph.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardialgla', 'n.', 'Alt. of Cardialgy');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardialgy', 'n.', 'A burning or gnawing pain, or feeling of distress,\n   referred to the region of the heart, accompanied with cardiac\n   palpitation; heartburn. It is usually a symptom of indigestion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardigan jacket', '', 'A warm jacket of knit worsted with or without\n   sleeves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinal', 'a.', 'Of fundamental importance; preeminent; superior; chief;\n   principal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinal', 'a.', 'One of the ecclesiastical princes who constitute the\n   popes council, or the sacred college.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinal', 'a.', 'A womans short cloak with a hood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinal', 'a.', 'Mulled red wine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinalate', 'n.', 'The office, rank, or dignity of a cardinal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinalize', 'v. t.', 'To exalt to the office of a cardinal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardinalship', 'n.', 'The condition, dignity, of office of a cardinal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carding', 'a.', 'The act or process of preparing staple for spinning, etc.,\n   by carding it. See the Note under Card, v. t.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Carding', 'v. t.', 'A roll of wool or other fiber as it comes from the\n   carding machine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiograph', 'n.', 'An instrument which, when placed in contact with the\n   chest, will register graphically the comparative duration and intensity\n   of the hearts movements.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardiographic', 'a.', 'Of or pertaining to, or produced by, a cardiograph.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardioid', 'n.', 'An algebraic curve, so called from its resemblance to a\n   heart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Cardioinhibitory', 'a.', 'Checking or arresting the hearts action.');
INSERT INTO words (word, word_type, description)VALUES  ('Cardiology',    'n.',    'The science which treats of the heart and its functions.');
INSERT INTO words (word, word_type, description)VALUES  ('Cardiometry',    'n.',    'Measurement of the heart, as by percussion or\n   auscultation.');
INSERT INTO words (word, word_type, description)VALUES  ('Cardiosphygmograph',    'n.',    'A combination of cardiograph and sphygmograph.');
