INSERT INTO words (word, word_type, description) VALUES  (    'Carditis',    'n.',    'Inflammation of the fleshy or muscular substance of the\n   heart. See Endocarditis and Pericarditis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cardines', 'pl. ', 'of Cardo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cardo', 'n.', 'The basal joint of the maxilla in insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cardo', 'n.', 'The hinge of a bivalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cardol', 'n.', 'A yellow oily liquid, extracted from the shell of the\n   cashew nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cardoon', 'n.', 'A large herbaceous plant (Cynara Cardunculus) related to\n   the artichoke; -- used in cookery and as a salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Care', 'n.', 'A burdensome sense of responsibility; trouble caused by\n   onerous duties; anxiety; concern; solicitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Care', 'n.', 'Charge, oversight, or management, implying responsibility for\n   safety and prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Care', 'n.', 'Attention or heed; caution; regard; heedfulness;\n   watchfulness; as, take care; have a care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Care', 'n.', 'The object of watchful attention or anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cared', 'imp. & p. p.', 'of Care');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caring', 'p. pr. & vb. n.', 'of Care');
  INSERT INTO words (word, word_type, description) VALUES  (    'Care', 'n.', 'To be anxious or solicitous; to be concerned; to have regard\n   or interest; -- sometimes followed by an objective of measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careened', 'imp. & p. p.', 'of Careen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careening', 'p. pr. & vb. n.', 'of Careen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careen', 'v. t.', 'To cause (a vessel) to lean over so that she floats on\n   one side, leaving the other side out of water and accessible for\n   repairs below the water line; to case to be off the keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careen', 'v. i.', 'To incline to one side, or lie over, as a ship when\n   sailing on a wind; to be off the keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careenage', 'n.', 'Expense of careening ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careenage', 'n.', 'A place for careening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Career', 'n.', 'A race course: the ground run over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Career', 'n.', 'A running; full speed; a rapid course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Career', 'n.', 'General course of action or conduct in life, or in a\n   particular part or calling in life, or in some special undertaking;\n   usually applied to course or conduct which is of a public character;\n   as, Washingtons career as a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Career', 'n.', 'The flight of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careered', 'imp. & p. p.', 'of Career');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careering', 'p. pr. & vb. n.', 'of Career');
  INSERT INTO words (word, word_type, description) VALUES  (    'Career', 'v. i.', 'To move or run rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careful', 'a.', 'Full of care; anxious; solicitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careful', 'a.', 'Filling with care or solicitude; exposing to concern,\n   anxiety, or trouble; painful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careful', 'a.', 'Taking care; giving good heed; watchful; cautious;\n   provident; not indifferent, heedless, or reckless; -- often followed by\n   of, for, or the infinitive; as, careful of money; careful to do right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carefully', 'adv.', 'In a careful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carefulness', 'n.', 'Quality or state of being careful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careless', 'a.', 'Free from care or anxiety. hence, cheerful;\n   light-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careless', 'a.', 'Having no care; not taking ordinary or proper care;\n   negligent; unconcerned; heedless; inattentive; unmindful; regardless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careless', 'a.', 'Without thought or purpose; without due care; without\n   attention to rule or system; unstudied; inconsiderate; spontaneous;\n   rash; as, a careless throw; a careless expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careless', 'a.', 'Not receiving care; uncared for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carelessly', 'adv.', 'In a careless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carelessness', 'n.', 'The quality or state of being careless; heedlessness;\n   negligenece; inattention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carene', 'n.', 'A fast of forty days on bread and water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caress', 'n.', 'An act of endearment; any act or expression of affection;\n   an embracing, or touching, with tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caressed', 'imp. & p. p.', 'of Caress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caressing', 'p. pr. & vb. n.', 'of Caress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caress', 'n.', 'To treat with tokens of fondness, affection, or kindness;\n   to touch or speak to in a loving or endearing manner; to fondle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caressingly', 'ad.', 'In caressing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caret', 'n.', 'A mark [^] used by writers and proof readers to indicate\n   that something is interlined above, or inserted in the margin, which\n   belongs in the place marked by the caret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caret', 'n.', 'The hawkbill turtle. See Hawkbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caretuned', 'a.', 'Weary; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Careworn', 'a.', 'Worn or burdened with care; as, careworn look or face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carex', 'n.', 'A numerous and widely distributed genus of perennial\n   herbaceous plants of the order Cypreaceae; the sedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carf', '', 'pret. of Carve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cargason', 'n.', 'A cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cargoes', 'pl. ', 'of Cargo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cargo', 'n.', 'The lading or freight of a ship or other vessel; the goods,\n   merchandise, or whatever is conveyed in a vessel or boat; load;\n   freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cargoose', 'n.', 'A species of grebe (Podiceps crisratus); the crested\n   grebe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cariama', 'n.', 'A large, long-legged South American bird (Dicholophus\n   cristatus) which preys upon snakes, etc. See Seriema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caries', 'pl. ', 'of Carib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carib', 'n.', 'A native of the Caribbee islands or the coasts of the\n   Caribbean sea; esp., one of a tribe of Indians inhabiting a region of\n   South America, north of the Amazon, and formerly most of the West India\n   islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caribbean', 'a.', 'Alt. of Caribbee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caribbee', 'a.', 'Of or pertaining to the Caribs, to their islands (the\n   eastern and southern West Indies); or to the sea (called the Caribbean\n   sea) lying between those islands and Central America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caribbee', 'n.', 'A Carib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caribe', 'n.', 'A south American fresh water fish of the genus Serrasalmo\n   of many species, remarkable for its voracity. When numerous they attack\n   man or beast, often with fatal results.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caribou', 'n.', 'The American reindeer, especially the common or woodland\n   species (Rangifer Caribou).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricature', 'v. t.', 'An exaggeration, or distortion by exaggeration, of\n   parts or characteristics, as in a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricature', 'v. t.', 'A picture or other figure or description in which\n   the peculiarities of a person or thing are so exaggerated as to appear\n   ridiculous; a burlesque; a parody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricatured', 'imp. & p. p.', 'of Caricature');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricaturing', 'p. pr. & vb. n.', 'of Caricature');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricature', 'v. t.', 'To make or draw a caricature of; to represent with\n   ridiculous exaggeration; to burlesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricaturist', 'n.', 'One who caricatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caricous', 'a.', 'Of the shape of a fig; as, a caricous tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caries', 'n.', 'Ulceration of bone; a process in which bone disintegrates\n   and is carried away piecemeal, as distinguished from necrosis, in which\n   it dies in masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carillon', 'n.', 'A chime of bells diatonically tuned, played by clockwork\n   or by finger keys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carillon', 'n.', 'A tune adapted to be played by musical bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carina', 'n.', 'A keel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carina', 'n.', 'That part of a papilionaceous flower, consisting of two\n   petals, commonly united, which incloses the organs of fructification');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carina', 'n.', 'A longitudinal ridge or projection like the keel of a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carina', 'n.', 'The keel of the breastbone of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carinaria', 'n.', 'A genus of oceanic heteropod Mollusca, having a thin,\n   glassy, bonnet-shaped shell, which covers only the nucleus and gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carinatae', 'n. pl.', 'A grand division of birds, including all existing\n   flying birds; -- So called from the carina or keel on the breastbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carinate', 'a.', 'Alt. of Carinated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carinated', 'a.', 'Shaped like the keel or prow of a ship; having a carina\n   or keel; as, a carinate calyx or leaf; a carinate sternum (of a bird).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cariole', 'n.', 'A small, light, open one-horse carriage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cariole', 'n.', 'A covered cart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cariole', 'n.', 'A kind of calash. See Carryall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cariopsis', 'n.', 'See Caryopsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cariosity', 'n.', 'Caries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carious', 'a.', 'Affected with caries; decaying; as, a carious tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cark', 'n.', 'A noxious or corroding care; solicitude; worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cark', 'v. i.', 'To be careful, anxious, solicitous, or troubles in mind;\n   to worry or grieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cark', 'v. t.', 'To vex; to worry; to make by anxious care or worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carkanet', 'n.', 'A carcanet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carking', 'a.', 'Distressing; worrying; perplexing; corroding; as, carking\n   cares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carl', 'n.', 'A rude, rustic man; a churl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carl', 'n.', 'Large stalks of hemp which bear the seed; -- called also carl\n   hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carl', 'n.', 'A kind of food. See citation, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carlin', 'n.', 'An old woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carline', 'n.', 'Alt. of Caroline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroline', 'n.', 'A silver coin once current in some parts of Italy, worth\n   about seven cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carline', 'n.', 'Alt. of Carling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carling', 'n.', 'A short timber running lengthwise of a ship, from one\n   transverse desk beam to another; also, one of the cross timbers that\n   strengthen a hath; -- usually in pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carline thistle', '', 'A prickly plant of the genus Carlina (C. vulgaris);\n   found in Europe and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carlings', 'n. pl.', 'Same as Carl, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carlist', 'n.', 'A partisan of Charles X. of France, or of Don Carlos of\n   Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carlock', 'n.', 'A sort of Russian isinglass, made from the air bladder of\n   the sturgeon, and used in clarifying wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carlot', 'n.', 'A churl; a boor; a peasant or countryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carlovingian', 'a.', 'Pertaining to, founded by, of descended from,\n   Charlemagne; as, the Carlovingian race of kings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmagnole', 'n.', 'A popular or Red Rebublican song and dance, of the time\n   of the first French Revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmagnole', 'n.', 'A bombastic report from the French armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carman', 'n.', 'A man whose employment is to drive, or to convey goods in,\n   a car or car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmelite', 'a.', 'Alt. of Carmelin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmelin', 'a.', 'Of or pertaining to the order of Carmelites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmelite', 'n.', 'A friar of a mendicant order (the Order of Our Lady of\n   Mount Carmel) established on Mount Carmel, in Syria, in the twelfth\n   century; a White Friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmelite', 'n.', 'A nun of the Order of Our lady of Mount Carmel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carminated', 'a.', 'Of, relating to, or mixed with, carmine; as, carminated\n   lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carminative', 'a.', 'Expelling wind from the body; warming; antispasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carminative', 'n.', 'A substance, esp. an aromatic, which tends to expel\n   wind from the alimentary canal, or to relieve colic, griping, or\n   flatulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmine', 'n.', 'A rich red or crimson color with a shade of purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmine', 'n.', 'A beautiful pigment, or a lake, of this color, prepared\n   from cochineal, and used in miniature painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmine', 'n.', 'The essential coloring principle of cochineal, extracted\n   as a purple-red amorphous mass. It is a glucoside and possesses acid\n   properties; -- hence called also carminic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carminic', 'a.', 'Of or pertaining to, or derived from, carmine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carmot', 'n.', 'The matter of which the philosophers stone was believed to\n   be composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnage', 'n.', 'Flesh of slain animals or men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnage', 'n.', 'Great destruction of life, as in battle; bloodshed;\n   slaughter; massacre; murder; havoc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnal', 'a.', 'Of or pertaining to the body or its appetites; animal;\n   fleshly; sensual; given to sensual indulgence; lustful; human or\n   worldly as opposed to spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnal', 'a.', 'Flesh-devouring; cruel; ravenous; bloody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnalism', 'n.', 'The state of being carnal; carnality; sensualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnalist', 'n.', 'A sensualist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnality', 'n.', 'The state of being carnal; fleshly lust, or the\n   indulgence of lust; grossness of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnalized', 'imp. & p. p.', 'of Carnalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnalizing', 'p. pr. & vb. n.', 'of Carnalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnalize', 'v. t.', 'To make carnal; to debase to carnality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnallite', 'n.', 'A hydrous chloride of potassium and magnesium,\n   sometimes found associated with deposits of rock salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnally', 'adv.', 'According to the flesh, to the world, or to human\n   nature; in a manner to gratify animal appetites and lusts; sensually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnal-minded', 'a.', 'Worldly-minded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnal-mindedness', 'n.', 'Grossness of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnary', 'n.', 'A vault or crypt in connection with a church, used as a\n   repository for human bones disintered from their original burial\n   places; a charnel house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnassial', 'a.', 'Adapted to eating flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnassial', 'n.', 'A carnassial tooth; especially, the last premolar in\n   many carnivores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnate', 'a.', 'Invested with, or embodied in, flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnation', 'n.', 'The natural color of flesh; rosy pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnation', 'n.', 'Those parts of a picture in which the human body or any\n   part of it is represented in full color; the flesh tints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnation', 'n.', 'A species of Dianthus (D. Caryophyllus) or pink, having\n   very beautiful flowers of various colors, esp. white and usually a\n   rich, spicy scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnationed', 'a.', 'Having a flesh color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnauba', 'n.', 'The Brazilian wax palm. See Wax palm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnelian', 'n.', 'A variety of chalcedony, of a clear, deep red, flesh\n   red, or reddish white color. It is moderately hard, capable of a good\n   polish, and often used for seals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carneous', 'a.', 'Consisting of, or like, flesh; carnous; fleshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carney', 'n.', 'A disease of horses, in which the mouth is so furred that\n   the afflicted animal can not eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnifex', 'n.', 'The public executioner at Rome, who executed persons of\n   the lowest rank; hence, an executioner or hangman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnification', 'n.', 'The act or process of turning to flesh, or to a\n   substance resembling flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnify', 'v. i.', 'To form flesh; to become like flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnin', 'n.', 'A white crystalline nitrogenous substance, found in extract\n   of meat, and related to xanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnival', 'n.', 'A festival celebrated with merriment and revelry in Roman\n   Gatholic countries during the week before Lent, esp. at Rome and\n   Naples, during a few days (three to ten) before Lent, ending with\n   Shrove Tuesday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnival', 'n.', 'Any merrymaking, feasting, or masquerading, especially\n   when overstepping the bounds of decorum; a time of riotous excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnivora', 'n. pl.', 'An order of Mammallia including the lion, tiger,\n   wolf bear, seal, etc. They are adapted by their structure to feed upon\n   flesh, though some of them, as the bears, also eat vegetable food. The\n   teeth are large and sharp, suitable for cutting flesh, and the jaws\n   powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnivoracity', 'n.', 'Greediness of appetite for flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnivore', 'n.', 'One of the Carnivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnivorous', 'a.', 'Eating or feeding on flesh. The term is applied: (a)\n   to animals which naturally seek flesh for food, as the tiger, dog,\n   etc.; (b) to plants which are supposed to absorb animal food; (c) to\n   substances which destroy animal tissue, as caustics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnose', 'a.', 'Alt. of Carnous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnous', 'a.', 'Of or pertaining to flesh; fleshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnous', 'a.', 'Of a fleshy consistence; -- applied to succulent leaves,\n   stems, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnosity', 'n.', 'A fleshy excrescence; esp. a small excrescence or\n   fungous growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carnosity', 'n.', 'Fleshy substance or quality; fleshy covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carob', 'n.', 'An evergreen leguminous tree (Ceratania Siliqua) found in\n   the countries bordering the Mediterranean; the St. Johns bread; --\n   called also carob tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carob', 'n.', 'One of the long, sweet, succulent, pods of the carob tree,\n   which are used as food for animals and sometimes eaten by man; --\n   called also St. Johns bread, carob bean, and algaroba bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroche', 'n.', 'A kind of pleasure carriage; a coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroched', 'a.', 'Placed in a caroche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroigne', 'n.', 'Dead body; carrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'n.', 'A round dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'n.', 'A song of joy, exultation, or mirth; a lay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'n.', 'A song of praise of devotion; as, a Christmas or Easter\n   carol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'n.', 'Joyful music, as of a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroled', 'imp. & p. p.', 'of Carol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolled', '', 'of Carol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroling', 'p. pr. & vb. n.', 'of Carol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolling', '', 'of Carol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'v. t.', 'To praise or celebrate in song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'v. t.', 'To sing, especially with joyful notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'v. i.', 'To sing; esp. to sing joyfully; to warble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carol', 'n.', 'Alt. of Carrol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrol', 'n.', 'A small closet or inclosure built against a window on the\n   inner side, to sit in for study. The word was used as late as the 16th\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolin', 'n.', 'A former gold coin of Germany worth nearly five dollars;\n   also, a gold coin of Sweden worth nearly five dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolina pink', '', 'See Pinkboot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroline', 'n.', 'A coin. See Carline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroling', 'n.', 'A song of joy or devotion; a singing, as of carols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolinian', 'n.', 'A native or inhabitant of north or South Carolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolitic', 'a.', 'Adorned with sculptured leaves and branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroluses', 'pl. ', 'of Carolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroli', 'pl. ', 'of Carolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carolus', 'n.', 'An English gold coin of the value of twenty or\n   twenty-three shillings. It was first struck in the reign of Charles I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carom', 'n.', 'A shot in which the ball struck with the cue comes in\n   contact with two or more balls on the table; a hitting of two or more\n   balls with the players ball. In England it is called cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carom', 'v. i.', 'To make a carom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caromel', 'n.', 'See Caramel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroteel', 'n.', 'A tierce or cask for dried fruits, etc., usually about\n   700 lbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carotic', 'a.', 'Of or pertaining to stupor; as, a carotic state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carotic', 'a.', 'Carotid; as, the carotic arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carotid', 'n.', 'One of the two main arteries of the neck, by which blood\n   is conveyed from the aorta to the head. [See Illust. of Aorta.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carotid', 'a.', 'Alt. of Carotidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carotidal', 'a.', 'Pertaining to, or near, the carotids or one of them; as,\n   the carotid gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carotin', 'n.', 'A red crystallizable tasteless substance, extracted from\n   the carrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carousal', 'n.', 'A jovial feast or festival; a drunken revel; a carouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carouse', 'n.', 'A large draught of liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carouse', 'n.', 'A drinking match; a carousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caroused', 'imp. & p. p.', 'of Carouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carousing', 'p. pr. & vb. n.', 'of Carouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carouse', 'v. i.', 'To drink deeply or freely in compliment; to take part\n   in a carousal; to engage in drunken revels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carouse', 'v. t.', 'To drink up; to drain; to drink freely or jovially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carouser', 'n.', 'One who carouses; a reveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carousing', 'a.', 'That carouses; relating to a carouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carousingly', 'adv.', 'In the manner of a carouser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carped', 'imp. & p. p.', 'of Carp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carping', 'p. pr. & vb. n.', 'of Carp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carp', 'v. i.', 'To talk; to speak; to prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carp', 'v. i.', 'To find fault; to cavil; to censure words or actions\n   without reason or ill-naturedly; -- usually followed by at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carp', 'v. t.', 'To say; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carp', 'v. t.', 'To find fault with; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carp', 'pl. ', 'of Carp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carps', 'pl. ', 'of Carp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carp', 'n.', 'A fresh-water herbivorous fish (Cyprinus carpio.). Several\n   other species of Cyprinus, Catla, and Carassius are called carp. See\n   Cruclan carp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpal', 'a.', 'Of or pertaining to the carpus, or wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpal', 'n.', 'One of the bones or cartilages of the carpus; a carpale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpalia', 'pl. ', 'of Carpale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpale', 'n.', 'One of the bones or cartilages of the carpus; esp. one of\n   the series articulating with the metacarpals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpathian', 'a.', 'Of or pertaining to a range of mountains in\n   Austro-Hungary, called the Carpathians, which partially inclose Hungary\n   on the north, east, and south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpel', 'n.', 'Alt. of Carpellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpellum', 'n.', 'A simple pistil or single-celled ovary or seed vessel,\n   or one of the parts of a compound pistil, ovary, or seed vessel. See\n   Illust of Carpaphore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpellary', 'a.', 'Belonging to, forming, or containing carpels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpenter', 'n.', 'An artificer who works in timber; a framer and builder\n   of houses, ships, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpentering', 'n.', 'The occupation or work of a carpenter; the act of\n   working in timber; carpentry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpentry', 'n.', 'The art of cutting, framing, and joining timber, as in\n   the construction of buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpentry', 'n.', 'An assemblage of pieces of timber connected by being\n   framed together, as the pieces of a roof, floor, etc.; work done by a\n   carpenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carper', 'n.', 'One who carps; a caviler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpet', 'n.', 'A heavy woven or felted fabric, usually of wool, but also\n   of cotton, hemp, straw, etc.; esp. a floor covering made in breadths to\n   be sewed together and nailed to the floor, as distinguished from a rug\n   or mat; originally, also, a wrought cover for tables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpet', 'n.', 'A smooth soft covering resembling or suggesting a carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpeted', 'imp. & p. p.', 'of Carpet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpeting', 'p. pr. & vb. n.', 'of Carpet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpet', 'v. t.', 'To cover with, or as with, a carpet; to spread with\n   carpets; to furnish with a carpet or carpets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpetbag', 'n.', 'A portable bag for travelers; -- so called because\n   originally made of carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpetbagger', 'n.', 'An adventurer; -- a term of contempt for a Northern\n   man seeking private gain or political advancement in the southern part\n   of the United States after the Civil War (1865).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpeting', 'n.', 'The act of covering with carpets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpeting', 'n.', 'Cloth or materials for carpets; carpets, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpetless', 'a.', 'Without a carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpetmonger', 'n.', 'One who deals in carpets; a buyer and seller of\n   carpets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpetmonger', 'n.', 'One fond of pleasure; a gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpetway', 'n.', 'A border of greensward left round the margin of a plowed\n   field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carphology', 'n.', 'See Floccillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carping', 'a.', 'Fault-finding; censorious caviling. See Captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpintero', 'n.', 'A california woodpecker (Melanerpes formicivorus);\n   noted for its habit of inserting acorns in holes which it drills in\n   trees. The acorns become infested by insect larvae, which, when grown,\n   are extracted for food by the bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpogenic', 'a.', 'Productive of fruit, or causing fruit to be developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpolite', 'n.', 'A general term for a fossil fruit, nut, or seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpological', 'a.', 'Of or pertaining to carpology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpologist', 'n.', 'One who describes fruits; one versed in carpology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpology', 'n.', 'That branch of botany which relates to the structure of\n   seeds and fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpophagous', 'a.', 'Living on fruits; fruit-consuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpophore', 'n.', 'A slender prolongation of the receptacle as an axis\n   between the carpels, as in Geranium and many umbelliferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpophyll', 'n.', 'A leaf converted into a fruit or a constituent portion\n   of a fruit; a carpel. [See Illust. of Gymnospermous.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpophyte', 'n.', 'A flowerless plant which forms a true fruit as the\n   result of fertilization, as the red seaweeds, the Ascomycetes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpospore', 'n.', 'A kind of spore formed in the conceptacles of red\n   algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpi', 'pl. ', 'of Carpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carpus', 'n.', 'The wrist; the bones or cartilages between the forearm, or\n   antibrachium, and the hand or forefoot; in man, consisting of eight\n   short bones disposed in two rows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrack', 'n.', 'See Carack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrageen', 'n.', 'Alt. of Carrigeen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrigeen', 'n.', 'A small, purplish, branching, cartilaginous seaweed\n   (Chondrus crispus); which, when bleached, is the Irish moss of\n   commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrancha', 'n.', 'The Brazilian kite (Polyborus Brasiliensis); -- so\n   called in imitation of its notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carraway', 'n.', 'See Caraway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrel', 'n.', 'See Quarrel, an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrel', 'n.', 'Same as 4th Carol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriable', 'a.', 'Capable of being carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'That which is carried; burden; baggage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'The act of carrying, transporting, or conveying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'The price or expense of carrying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'That which carries of conveys,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'A wheeled vehicle for persons, esp. one designed for\n   elegance and comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'A wheeled vehicle carrying a fixed burden, as a gun\n   carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'A part of a machine which moves and carries of supports\n   some other moving object or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'A frame or cage in which something is carried or\n   supported; as, a bell carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'The manner of carrying ones self; behavior; bearing;\n   deportment; personal manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriage', 'n.', 'The act or manner of conducting measures or projects;\n   management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriageable', 'a.', 'Passable by carriages; that can be conveyed in\n   carriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carriboo', 'n.', 'See Caribou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrick', 'n.', 'A carack. See Carack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrier', 'n.', 'One who, or that which, carries or conveys; a messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrier', 'n.', 'One who is employed, or makes it his business, to carry\n   goods for others for hire; a porter; a teamster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrier', 'n.', 'That which drives or carries; as: (a) A piece which\n   communicates to an object in a lathe the motion of the face plate; a\n   lathe dog. (b) A spool holder or bobbin holder in a braiding machine.\n   (c) A movable piece in magazine guns which transfers the cartridge to a\n   position from which it can be thrust into the barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrion', 'n.', 'The dead and putrefying body or flesh of an animal; flesh\n   so corrupted as to be unfit for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrion', 'n.', 'A contemptible or worthless person; -- a term of reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrion', 'a.', 'Of or pertaining to dead and putrefying carcasses; feeding\n   on carrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrol', 'n.', 'See 4th Carol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrom', 'n.', 'See Carom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carronade', 'n.', 'A kind of short cannon, formerly in use, designed to\n   throw a large projectile with small velocity, used for the purpose of\n   breaking or smashing in, rather than piercing, the object aimed at, as\n   the side of a ship. It has no trunnions, but is supported on its\n   carriage by a bolt passing through a loop on its under side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carron oil', '', 'A lotion of linseed oil and lime water, used as an\n   application to burns and scalds; -- first used at the Carron iron works\n   in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrot', 'n.', 'An umbelliferous biennial plant (Daucus Carota); of many\n   varieties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrot', 'n.', 'The esculent root of cultivated varieties of the plant,\n   usually spindle-shaped, and of a reddish yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carroty', 'a.', 'Like a carrot in color or in taste; -- an epithet given to\n   reddish yellow hair, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrow', 'n.', 'A strolling gamester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carried', 'imp. & p. p.', 'of Carry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrying', 'p. pr. & vb. n.', 'of Carry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To convey or transport in any manner from one place to\n   another; to bear; -- often with away or off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To have or hold as a burden, while moving from place to\n   place; to have upon or about ones person; to bear; as, to carry a\n   wound; to carry an unborn child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To move; to convey by force; to impel; to conduct; to\n   lead or guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To transfer from one place (as a country, book, or\n   column) to another; as, to carry the war from Greece into Asia; to\n   carry an account to the ledger; to carry a number in adding figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To convey by extension or continuance; to extend; as, to\n   carry the chimney through the roof; to carry a road ten miles farther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To bear or uphold successfully through conflict, as a\n   leader or principle; hence, to succeed in, as in a contest; to bring to\n   a successful issue; to win; as, to carry an election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To get possession of by force; to capture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To contain; to comprise; to bear the aspect of ; to show\n   or exhibit; to imply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To bear (ones self); to behave, to conduct or demean; --\n   with the reflexive pronouns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. t.', 'To bear the charges or burden of holding or having, as\n   stocks, merchandise, etc., from one time to another; as, a merchant is\n   carrying a large stock; a farm carries a mortgage; a broker carries\n   stock for a customer; to carry a life insurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. i.', 'To act as a bearer; to convey anything; as, to fetch and\n   carry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. i.', 'To have propulsive power; to propel; as, a gun or mortar\n   carries well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. i.', 'To hold the head; -- said of a horse; as, to carry well\n   i. e., to hold the head high, with arching neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'v. i.', 'To have earth or frost stick to the feet when running, as\n   a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carries', 'pl. ', 'of Carry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carry', 'n.', 'A tract of land, over which boats or goods are carried\n   between two bodies of navigable water; a carrying place; a portage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carryall', 'n.', 'A light covered carriage, having four wheels and seats\n   for four or more persons, usually drawn by one horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrying', 'n.', 'The act or business of transporting from one place to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carryk', 'n.', 'A carack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carrytale', 'n.', 'A talebearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carse', 'n.', 'Low, fertile land; a river valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'n.', 'A common name for various kinds of vehicles, as a Scythian\n   dwelling on wheels, or a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'n.', 'A two-wheeled vehicle for the ordinary purposes of husbandry,\n   or for transporting bulky and heavy articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'n.', 'A light business wagon used by bakers, grocerymen, butchers,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'n.', 'An open two-wheeled pleasure carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carted', 'imp. & p. p.', 'of Cart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carting', 'p. pr. & vb. n.', 'of Cart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'v. t.', 'To carry or convey in a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'v. t.', 'To expose in a cart by way of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cart', 'v. i.', 'To carry burdens in a cart; to follow the business of a\n   carter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartage', 'n.', 'The act of carrying in a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartage', 'n.', 'The price paid for carting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartbote', 'n.', 'Wood to which a tenant is entitled for making and\n   repairing carts and other instruments of husbandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carte', 'n.', 'Bill of fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carte', 'n.', 'Short for Carte de visite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carte', 'n.', 'Alt. of Quarte');
  INSERT INTO words (word, word_type, description) VALUES  (    'Quarte', 'n.', 'A position in thrusting or parrying, with the inside of the\n   hand turned upward and the point of the weapon toward the adversarys\n   right breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carte blanche', '', 'A blank paper, with a persons signature, etc., at the\n   bottom, given to another person, with permission to superscribe what\n   conditions he pleases. Hence: Unconditional terms; unlimited authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartes de visite', 'pl. ', 'of Carte de visite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carte de visite', '', 'A visiting card.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carte de visite', '', 'A photographic picture of the size formerly in use\n   for a visiting card.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartel', 'n.', 'An agreement between belligerents for the exchange of\n   prisoners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartel', 'n.', 'A letter of defiance or challenge; a challenge to single\n   combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartel', 'v. t.', 'To defy or challenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carter', 'n.', 'A charioteer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carter', 'n.', 'A man who drives a cart; a teamster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carter', 'n.', 'Any species of Phalangium; -- also called harvestman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carter', 'n.', 'A British fish; the whiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartesian', 'a.', 'Of or pertaining to the French philosopher Rene\n   Descartes, or his philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartesian', 'n.', 'An adherent of Descartes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartesianism', 'n.', 'The philosophy of Descartes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carthaginian', 'a.', 'Of a pertaining to ancient Carthage, a city of\n   northern Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carthaginian', 'n.', 'A native or inhabitant of Carthage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carthamin', 'n.', 'A red coloring matter obtained from the safflower, or\n   Carthamus tinctorius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carthusian', 'n.', 'A member of an exceeding austere religious order,\n   founded at Chartreuse in France by St. Bruno, in the year 1086.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carthusian', 'a.', 'Pertaining to the Carthusian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartilage', 'n.', 'A translucent, elastic tissue; gristle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartilagineous', 'a.', 'See Cartilaginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartilaginification', 'n.', 'The act or process of forming cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartilaginous', 'a.', 'Of or pertaining to cartilage; gristly; firm and\n   tough like cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartilaginous', 'a.', 'Having the skeleton in the state of cartilage, the\n   bones containing little or no calcareous matter; said of certain\n   fishes, as the sturgeon and the sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartman', 'n.', 'One who drives or uses a cart; a teamster; a carter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartographer', 'n.', 'One who makes charts or maps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartographic', 'a.', 'Alt. of Cartographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartographical', 'a.', 'Of or pertaining to cartography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartographically', 'adv.', 'By cartography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartography', 'n.', 'The art or business of forming charts or maps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartomancy', 'n.', 'The art of telling fortunes with cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carton', 'n.', 'Pasteboard for paper boxes; also, a pasteboard box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartoon', 'n.', 'A design or study drawn of the full size, to serve as a\n   model for transferring or copying; -- used in the making of mosaics,\n   tapestries, fresco pantings and the like; as, the cartoons of Raphael.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartoon', 'n.', 'A large pictorial sketch, as in a journal or magazine;\n   esp. a pictorial caricature; as, the cartoons of \"Puck.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartoonist', 'n.', 'One skilled in drawing cartoons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouches', 'pl. ', 'of Cartouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A roll or case of paper, etc., holding a charge for a\n   firearm; a cartridge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A cartridge box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A wooden case filled with balls, to be shot from a\n   cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A gunners bag for ammunition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A military pass for a soldier on furlough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A cantalever, console, corbel, or modillion, which has\n   the form of a scroll of paper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'A tablet for ornament, or for receiving an inscription,\n   formed like a sheet of paper with the edges rolled up; hence, any\n   tablet of ornamental form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartouch', 'n.', 'An oval figure on monuments, and in papyri, containing\n   the name of a sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartridge', 'n.', 'A complete charge for a firearm, contained in, or held\n   together by, a case, capsule, or shell of metal, pasteboard, or other\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartularies', 'pl. ', 'of Cartulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartulary', 'n.', 'A register, or record, as of a monastery or church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartulary', 'n.', 'An ecclesiastical officer who had charge of records or\n   other public papers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartway', 'n.', 'A way or road for carts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cartwright', 'n.', 'An artificer who makes carts; a cart maker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carucage', 'n.', 'A tax on every plow or plowland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carucage', 'n.', 'The act of plowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carucate', 'n.', 'A plowland; as much land as one team can plow in a year\n   and a day; -- by some said to be about 100 acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caruncle', 'n.', 'Alt. of Caruncula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caruncula', 'n.', 'A small fleshy prominence or excrescence; especially the\n   small, reddish body, the caruncula lacrymalis, in the inner angle of\n   the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caruncula', 'n.', 'An excrescence or appendage surrounding or near the\n   hilum of a seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caruncula', 'n.', 'A naked, flesh appendage, on the head of a bird, as the\n   wattles of a turkey, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caruncular', 'a.', 'Alt. of Carunculous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carunculous', 'a.', 'Of, pertaining to, or like, a caruncle; furnished with\n   caruncles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carunculate', 'a.', 'Alt. of Carunculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carunculated', 'a.', 'Having a caruncle or caruncles; caruncular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carus', 'n.', 'Coma with complete insensibility; deep lethargy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvacrol', 'n.', 'A thick oily liquid, C10H13.OH, of a strong taste and\n   disagreeable odor, obtained from oil of caraway (Carum carui).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carved', 'imp. & p. p.', 'of Carve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carving', 'p. pr. & vb. n.', 'of Carve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To cut, as wood, stone, or other material, in an artistic\n   or decorative manner; to sculpture; to engrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To make or shape by cutting, sculpturing, or engraving;\n   to form; as, to carve a name on a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To cut into small pieces or slices, as meat at table; to\n   divide for distribution or apportionment; to apportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To cut: to hew; to mark as if by cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To take or make, as by cutting; to provide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. t.', 'To lay out; to contrive; to design; to plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. i.', 'To exercise the trade of a sculptor or carver; to engrave\n   or cut figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'v. i.', 'To cut up meat; as, to carve for all the guests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carve', 'n.', 'A carucate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvel', 'n.', 'Same as Caravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvel', 'n.', 'A species of jellyfish; sea blubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvelbuilt', 'a.', 'Having the planks meet flush at the seams, instead of\n   lapping as in a clinker-built vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carven', 'a.', 'Wrought by carving; ornamented by carvings; carved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvene', 'n.', 'An oily substance, C10H16, extracted from oil caraway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carver', 'n.', 'One who carves; one who shapes or fashions by carving, or\n   as by carving; esp. one who carves decorative forms, architectural\n   adornments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carver', 'n.', 'One who carves or divides meat at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carver', 'n.', 'A large knife for carving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carving', 'n.', 'The act or art of one who carves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carving', 'n.', 'A piece of decorative work cut in stone, wood, or other\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carving', 'n.', 'The whole body of decorative sculpture of any kind or\n   epoch, or in any material; as, the Italian carving of the 15th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvist', 'n.', 'A hawk which is of proper age and training to be carried\n   on the hand; a hawk in its first year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Carvol', 'n.', 'One of a species of aromatic oils, resembling carvacrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Car wheel', '', 'A flanged wheel of a railway car or truck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryatic', 'a.', 'Alt. of Caryatid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryatid', 'a.', 'Of or pertaining to a caryatid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryatids', 'pl. ', 'of Caryatid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryatid', 'n.', 'A draped female figure supporting an entablature, in the\n   place of a column or pilaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryatides', 'n. pl.', 'Caryatids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryophyllaceous', 'a.', 'Having corollas of five petals with long claws\n   inclosed in a tubular, calyx, as the pink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryophyllaceous', 'a.', 'Belonging to the family of which the pink and the\n   carnation are the types.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryophyllin', 'n.', 'A tasteless and odorless crystalline substance,\n   extracted from cloves, polymeric with common camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryophyllous', 'a.', 'Caryophyllaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryopses', 'pl. ', 'of Caryopsis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caryopsis', 'n.', 'A one-celled, dry, indehiscent fruit, with a thin\n   membranous pericarp, adhering closely to the seed, so that fruit and\n   seed are incorporated in one body, forming a single grain, as of wheat,\n   barley, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casal', 'a.', 'Of or pertaining to case; as, a casal ending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascabel', 'n.', 'The projection in rear of the breech of a cannon, usually\n   a knob or breeching loop connected with the gun by a neck. In old\n   writers it included all in rear of the base ring. [See Illust. of\n   Cannon.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascade', 'n.', 'A fall of water over a precipice, as in a river or brook;\n   a waterfall less than a cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascade', 'v. i.', 'To fall in a cascade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascade', 'v. i.', 'To vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascalho', 'n.', 'A deposit of pebbles, gravel, and ferruginous sand, in\n   which the Brazilian diamond is usually found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascara sagrada', '', 'Holy bark; the bark of the California buckthorn\n   (Rhamnus Purshianus); used as a mild cathartic or laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascarilla', 'n.', 'A euphorbiaceous West Indian shrub (Croton Eleutheria);\n   also, its aromatic bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cascarillin', 'n.', 'A white, crystallizable, bitter substance extracted\n   from oil of cascarilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'A box, sheath, or covering; as, a case for holding goods; a\n   case for spectacles; the case of a watch; the case (capsule) of a\n   cartridge; a case (cover) for a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'A box and its contents; the quantity contained in a box; as,\n   a case of goods; a case of instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'A shallow tray divided into compartments or \"boxes\" for\n   holding type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'An inclosing frame; a casing; as, a door case; a window case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'A small fissure which admits water to the workings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cased', 'imp. & p. p.', 'of Case');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casing', 'p. pr. & vb. n.', 'of Case');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'v. t.', 'To cover or protect with, or as with, a case; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'v. t.', 'To strip the skin from; as, to case a box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'Chance; accident; hap; opportunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'That which befalls, comes, or happens; an event; an instance;\n   a circumstance, or all the circumstances; condition; state of things;\n   affair; as, a strange case; a case of injustice; the case of the Indian\n   tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'A patient under treatment; an instance of sickness or injury;\n   as, ten cases of fever; also, the history of a disease or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'The matters of fact or conditions involved in a suit, as\n   distinguished from the questions of law; a suit or action at law; a\n   cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'n.', 'One of the forms, or the inflections or changes of form, of a\n   noun, pronoun, or adjective, which indicate its relation to other\n   words, and in the aggregate constitute its declension; the relation\n   which a noun or pronoun sustains to some other word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case', 'v. i.', 'To propose hypothetical cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseation', 'n.', 'A degeneration of animal tissue into a cheesy or curdy\n   mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case-bay', 'n.', 'The space between two principals or girders');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case-bay', 'n.', 'One of the joists framed between a pair of girders in\n   naked flooring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseharden', 'v. t.', 'To subject to a process which converts the surface\n   of iron into steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseharden', 'v. t.', 'To render insensible to good influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casehardened', 'a.', 'Having the surface hardened, as iron tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casehardened', 'a.', 'Hardened against, or insusceptible to, good\n   influences; rendered callous by persistence in wrongdoing or resistance\n   of good influences; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casehardening', 'n.', 'The act or process of converting the surface of iron\n   into steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseic', 'a.', 'Of or pertaining to cheese; as, caseic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casein', 'n.', 'A proteid substance present in both the animal and the\n   vegetable kingdom. In the animal kingdom it is chiefly found in milk,\n   and constitutes the main part of the curd separated by rennet; in the\n   vegetable kingdom it is found more or less abundantly in the seeds of\n   leguminous plants. Its reactions resemble those of alkali albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case knife', '', 'A knife carried in a sheath or case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case knife', '', 'A large table knife; -- so called from being formerly\n   kept in a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casemate', 'n.', 'A bombproof chamber, usually of masonry, in which cannon\n   may be placed, to be fired through embrasures; or one capable of being\n   used as a magazine, or for quartering troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casemate', 'n.', 'A hollow molding, chiefly in cornices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casemated', 'a.', 'Furnished with, protected by, or built like, a casemate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casement', 'n.', 'A window sash opening on hinges affixed to the upright\n   side of the frame into which it is fitted. (Poetically) A window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casemented', 'a.', 'Having a casement or casements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseous', 'a.', 'Of, pertaining to, or resembling, cheese; having the\n   qualities of cheese; cheesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casern', 'n.', 'A lodging for soldiers in garrison towns, usually near the\n   rampart; barracks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Case shot', '', 'A collection of small projectiles, inclosed in a case or\n   canister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseum', 'n.', 'Same as Casein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caseworm', 'n.', 'A worm or grub that makes for itself a case. See Caddice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cash', 'n.', 'A place where money is kept, or where it is deposited and\n   paid out; a money box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cash', 'n.', 'Ready money; especially, coin or specie; but also applied to\n   bank notes, drafts, bonds, or any paper easily convertible into money');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cash', 'n.', 'Immediate or prompt payment in current funds; as, to sell\n   goods for cash; to make a reduction in price for cash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashed', 'imp. & p. p.', 'of Cash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casing', 'p. pr. & vb. n.', 'of Cash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cash', 'v. t.', 'To pay, or to receive, cash for; to exchange for money;\n   as, cash a note or an order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cash', 'v. t.', 'To disband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cash', 'n.sing & pl.', 'A Chinese coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashbook', 'n.', 'A book in which is kept a register of money received or\n   paid out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashew', 'n.', 'A tree (Anacardium occidentale) of the same family which\n   the sumac. It is native in tropical America, but is now naturalized in\n   all tropical countries. Its fruit, a kidney-shaped nut, grows at the\n   extremity of an edible, pear-shaped hypocarp, about three inches long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashier', 'n.', 'One who has charge of money; a cash keeper; the officer\n   who has charge of the payments and receipts (moneys, checks, notes); of\n   a bank or a mercantile company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cahiered', 'imp. & p. p.', 'of Cashier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashiering', 'p. pr. &vb. n.', 'of Cashier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashier', 'v. t.', 'To dismiss or discard; to discharge; to dismiss with\n   ignominy from military service or from an office or place of trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashier', 'v. t.', 'To put away or reject; to disregard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashierer', 'n.', 'One who rejects, discards, or dismisses; as, a cashierer\n   of monarchs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashmere', 'n.', 'A rich stuff for shawls, scarfs, etc., originally made in\n   Cashmere from the soft wool found beneath the hair of the goats of\n   Cashmere, Thibet, and the Himalayas. Some cashmere, of fine quality, is\n   richly embroidered for sale to Europeans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashmere', 'n.', 'A dress fabric made of fine wool, or of fine wool and\n   cotton, in imitation of the original cashmere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashmerette', 'n.', 'A kind of dress goods, made with a soft and glossy\n   surface like cashmere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cashoo', 'n.', 'See Catechu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casing', 'n.', 'The act or process of inclosing in, or covering with, a\n   case or thin substance, as plaster, boards, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casing', 'n.', 'An outside covering, for protection or ornament, or to\n   precent the radiation of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casing', 'n.', 'An inclosing frame; esp. the framework around a door or a\n   window. See Case, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casings', 'n. pl.', 'Dried dung of cattle used as fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casinos', 'pl. ', 'of Casino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casini', 'pl. ', 'of Casino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casino', 'n.', 'A small country house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casino', 'n.', 'A building or room used for meetings, or public amusements,\n   for dancing, gaming, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casino', 'n.', 'A game at cards. See Cassino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cask', 'n.', 'Same as Casque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cask', 'n.', 'A barrel-shaped vessel made of staves headings, and hoops,\n   usually fitted together so as to hold liquids. It may be larger or\n   smaller than a barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cask', 'n.', 'The quantity contained in a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cask', 'n.', 'A casket; a small box for jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cask', 'v. t.', 'To put into a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'A small chest or box, esp. of rich material or ornamental\n   character, as for jewels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'A kind of burial case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'Anything containing or intended to contain something highly\n   esteemed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'The body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'The tomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'A book of selections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'n.', 'A gasket. See Gasket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casket', 'v. t.', 'To put into, or preserve in, a casket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casque', 'n.', 'A piece of defensive or ornamental armor (with or without a\n   vizor) for the head and neck; a helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cass', 'v. t.', 'To render useless or void; to annul; to reject; to send\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassada', 'n.', 'See Cassava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassareep', 'n.', 'A condiment made from the sap of the bitter cassava\n   (Manihot utilissima) deprived of its poisonous qualities, concentrated\n   by boiling, and flavored with aromatics. See Pepper pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassate', 'v. t.', 'To render void or useless; to vacate or annul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassation', 'n.', 'The act of annulling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassava', 'n.', 'A shrubby euphorbiaceous plant of the genus Manihot, with\n   fleshy rootstocks yielding an edible starch; -- called also manioc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassava', 'n.', 'A nutritious starch obtained from the rootstocks of the\n   cassava plant, used as food and in making tapioca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casse Paper', '', 'Broken paper; the outside quires of a ream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casserole', 'n.', 'A small round dish with a handle, usually of porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casserole', 'n.', 'A mold (in the shape of a hollow vessel or incasement)\n   of boiled rice, mashed potato or paste, baked, and afterwards filled\n   with vegetables or meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassia', 'n.', 'A genus of leguminous plants (herbs, shrubs, or trees) of\n   many species, most of which have purgative qualities. The leaves of\n   several species furnish the senna used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassia', 'n.', 'The bark of several species of Cinnamomum grown in China,\n   etc.; Chinese cinnamon. It is imported as cassia, but commonly sold as\n   cinnamon, from which it differs more or less in strength and flavor,\n   and the amount of outer bark attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassican', 'n.', 'An American bird of the genus Cassicus, allied to the\n   starlings and orioles, remarkable for its skillfully constructed and\n   suspended nest; the crested oriole. The name is also sometimes given to\n   the piping crow, an Australian bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassideous', 'a.', 'Helmet-shaped; -- applied to a corolla having a broad,\n   helmet-shaped upper petal, as in aconite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassidony', 'n.', 'The French lavender (Lavandula Stoechas)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassidony', 'n.', 'The goldilocks (Chrysocoma Linosyris) and perhaps other\n   plants related to the genus Gnaphalium or cudweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassimere', 'n.', 'A thin, twilled, woolen cloth, used for mens garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassinette', 'n.', 'A cloth with a cotton warp, and a woof of very fine\n   wool, or wool and silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassinian ovals', '', 'See under Oval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassino', 'n.', 'A game at cards, played by two or more persons, usually\n   for twenty-one points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassioberry', 'n.', 'The fruit of the Viburnum obovatum, a shrub which\n   grows from Virginia to Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassiopeia', 'n.', 'A constellation of the northern hemisphere, situated\n   between Cepheus and Perseus; -- so called in honor of the wife of\n   Cepheus, a fabulous king of Ethiopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassiterite', 'n.', 'Native tin dioxide; tin stone; a mineral occurring in\n   tetragonal crystals of reddish brown color, and brilliant adamantine\n   luster; also massive, sometimes in compact forms with concentric\n   fibrous structure resembling wood (wood tin); also in rolled fragments\n   or pebbly (Stream tin). It is the chief source of metallic tin. See\n   Black tin, under Black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassius', 'n.', 'A brownish purple pigment, obtained by the action of some\n   compounds of tin upon certain salts of gold. It is used in painting and\n   staining porcelain and glass to give a beautiful purple color. Commonly\n   called Purple of Cassius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassock', 'n.', 'A long outer garment formerly worn by men and women, as\n   well as by soldiers as part of their uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassock', 'n.', 'A garment resembling a long frock coat worn by the clergy\n   of certain churches when officiating, and by others as the usually\n   outer garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassocked', 'a.', 'Clothed with a cassock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassolette', 'n.', 'a box, or vase, with a perforated cover to emit\n   perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassonade', 'n.', 'Raw sugar; sugar not refined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassowaries', 'pl. ', 'of Cassowary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassowary', 'n.', 'A large bird, of the genus Casuarius, found in the east\n   Indies. It is smaller and stouter than the ostrich. Its head is armed\n   with a kind of helmet of horny substance, consisting of plates\n   overlapping each other, and it has a group of long sharp spines on each\n   wing which are used as defensive organs. It is a shy bird, and runs\n   with great rapidity. Other species inhabit New Guinea, Australia, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassumunar', 'n.', 'Alt. of Cassumuniar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cassumuniar', 'n.', 'A pungent, bitter, aromatic, gingerlike root, obtained\n   from the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'imp. & p. p.', 'of Cast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casting', 'p. pr. & vb. n.', 'of Cast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To send or drive by force; to throw; to fling; to hurl; to\n   impel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To direct or turn, as the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To drop; to deposit; as, to cast a ballot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To throw down, as in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To throw up, as a mound, or rampart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To throw off; to eject; to shed; to lose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To bring forth prematurely; to slink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To throw out or emit; to exhale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To cause to fall; to shed; to reflect; to throw; as, to\n   cast a ray upon a screen; to cast light upon a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To impose; to bestow; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To dismiss; to discard; to cashier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To compute; to reckon; to calculate; as, to cast a\n   horoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To contrive; to plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To defeat in a lawsuit; to decide against; to convict; as,\n   to be cast in damages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To turn (the balance or scale); to overbalance; hence, to\n   make preponderate; to decide; as, a casting voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To form into a particular shape, by pouring liquid metal\n   or other material into a mold; to fashion; to found; as, to cast bells,\n   stoves, bullets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To stereotype or electrotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. t.', 'To fix, distribute, or allot, as the parts of a play among\n   actors; also to assign (an actor) for a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To throw, as a line in angling, esp, with a fly hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To turn the head of a vessel around from the wind in\n   getting under weigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To consider; to turn or revolve in the mind; to plan; as,\n   to cast about for reasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To calculate; to compute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To receive form or shape in a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To warp; to become twisted out of shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'v. i.', 'To vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', '', '3d pres. of Cast, for Casteth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'The act of casting or throwing; a throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'The thing thrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'The distance to which a thing is or can be thrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A throw of dice; hence, a chance or venture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'That which is throw out or off, shed, or ejected; as, the\n   skin of an insect, the refuse from a hawks stomach, the excrement of a\n   earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'The act of casting in a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'An impression or mold, taken from a thing or person; amold; a\n   pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'That which is formed in a mild; esp. a reproduction or copy,\n   as of a work of art, in bronze or plaster, etc.; a casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'Form; appearence; mien; air; style; as, a peculiar cast of\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A tendency to any color; a tinge; a shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A chance, opportunity, privilege, or advantage; specifically,\n   an opportunity of riding; a lift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'The assignment of parts in a play to the actors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A flight or a couple or set of hawks let go at one time from\n   the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A stoke, touch, or trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A motion or turn, as of the eye; direction; look; glance;\n   squint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'A tube or funnel for conveying metal into a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'Four; that is, as many as are thrown into a vessel at once in\n   counting herrings, etc; a warp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast', 'n.', 'Contrivance; plot, design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castalian', 'a.', 'Of or pertaining to Castalia, a mythical fountain of\n   inspiration on Mt. Parnassus sacred to the Muses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castanea', 'n.', 'A genus of nut-bearing trees or shrubs including the\n   chestnut and chinquapin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castanet', 'n.', 'See Castanets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castanets', 'n. pl.', 'Two small, concave shells of ivory or hard wood,\n   shaped like spoons, fastened to the thumb, and beaten together with the\n   middle finger; -- used by the Spaniards and Moors as an accompaniment\n   to their dance and guitars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castaway', 'n.', 'One who, or that which, is cast away or shipwrecked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castaway', 'n.', 'One who is ruined; one who has made moral shipwreck; a\n   reprobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castaway', 'a.', 'Of no value; rejected; useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caste', 'n.', 'One of the hereditary classes into which the Hindoos are\n   divided according to the laws of Brahmanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caste', 'n.', 'A separate and fixed order or class of persons in society\n   who chiefly hold intercourse among themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castellan', 'n.', 'A governor or warden of a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castellanies', 'pl. ', 'of Castellany');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castellany', 'n.', 'The lordship of a castle; the extent of land and\n   jurisdiction appertaining to a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castellated', 'a.', 'Inclosed within a building; as, a fountain or cistern\n   castellated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castellated', 'a.', 'Furnished with turrets and battlements, like a castle;\n   built in the style of a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castellation', 'n.', 'The act of making into a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caster', 'n.', 'One who casts; as, caster of stones, etc. ; a caster of\n   cannon; a caster of accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caster', 'n.', 'A vial, cruet, or other small vessel, used to contain\n   condiments at the table; as, a set of casters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caster', 'n.', 'A stand to hold a set of cruets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caster', 'n.', 'A small wheel on a swivel, on which furniture is supported\n   and moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigated', 'imp. & p. p.', 'of Castigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigating', 'p. pr. & vb. n.', 'of Castigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigate', 'v. t.', 'To punish by stripes; to chastise by blows; to\n   chasten; also, to chastise verbally; to reprove; to criticise severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigate', 'v. t.', 'To emend; to correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigation', 'n.', 'Corrective punishment; chastisement; reproof; pungent\n   criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigation', 'n.', 'Emendation; correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigator', 'n.', 'One who castigates or corrects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigatory', 'a.', 'Punitive in order to amendment; corrective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castigatory', 'n.', 'An instrument formerly used to punish and correct\n   arrant scolds; -- called also a ducking stool, or trebucket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castile soap', '', 'A kind of fine, hard, white or mottled soap, made with\n   olive oil and soda; also, a soap made in imitation of the\n   above-described soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castilian', 'n.', 'An inhabitant or native of Castile, in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castilian', 'n.', 'The Spanish language as spoken in Castile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castillan', 'a.', 'Of or pertaining to Castile, in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casting', 'n.', 'The act of one who casts or throws, as in fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casting', 'n.', 'The act or process of making casts or impressions, or of\n   shaping metal or plaster in a mold; the act or the process of pouring\n   molten metal into a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casting', 'n.', 'That which is cast in a mold; esp. the mass of metal so\n   cast; as, a casting in iron; bronze casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casting', 'n.', 'The warping of a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casting', 'n.', 'The act of casting off, or that which is cast off, as\n   skin, feathers, excrement, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast iron', '', 'Highly carbonized iron, the direct product of the blast\n   furnace; -- used for making castings, and for conversion into wrought\n   iron and steel. It can not be welded or forged, is brittle, and\n   sometimes very hard. Besides carbon, it contains sulphur, phosphorus,\n   silica, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast-iron', 'a.', 'Made of cast iron. Hence, Fig.: like cast iron; hardy;\n   unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle', 'n.', 'A fortified residence, especially that of a prince or\n   nobleman; a fortress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle', 'n.', 'Any strong, imposing, and stately mansion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle', 'n.', 'A small tower, as on a ship, or an elephants back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle', 'n.', 'A piece, made to represent a castle, used in the game of\n   chess; a rook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castled', 'imp. & p. p.', 'of Castle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castling', 'p. pr. & vb. n.', 'of Castle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle', 'v. i.', 'To move the castle to the square next to king, and then\n   the king around the castle to the square next beyond it, for the\n   purpose of covering the king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castlebuilder', 'n.', 'Fig.: one who builds castles in the air or forms\n   visionary schemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castled', 'a.', 'Having a castle or castles; supporting a castle; as, a\n   castled height or crag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castled', 'a.', 'Fortified; turreted; as, castled walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle-guard', 'n.', 'The guard or defense of a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle-guard', 'n.', 'A tax or imposition an a dwelling within a certain\n   distance of a castle, for the purpose of maintaining watch and ward in\n   it; castle-ward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castle-guard', 'n.', 'A feudal tenure, obliging the tenant to perform\n   service within the realm, without limitation of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castlery', 'n.', 'The government of a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castlet', 'n.', 'A small castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castleward', 'n.', 'Same as Castleguard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castling', 'n.', 'That which is cast or brought forth prematurely; an\n   abortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castling', 'n.', 'A compound move of the king and castle. See Castle, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast-off', 'a.', 'Cast or laid aside; as, cast-off clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'A genus of rodents, including the beaver. See Beaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'Castoreum. See Castoreum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'A hat, esp. one made of beaver fur; a beaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'A heavy quality of broadcloth for overcoats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'See Caster, a small wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'the northernmost of the two bright stars in the\n   constellation Gemini, the other being Pollux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor', 'n.', 'Alt. of Castorite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castorite', 'n.', 'A variety of the mineral called petalite, from Elba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor and Pollux', '', 'See Saint Elmos fire, under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor bean', '', 'The bean or seed of the castor-oil plant (Ricinus\n   communis, or Palma Christi.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castoreum', 'n.', 'A peculiar bitter orange-brown substance, with strong,\n   penetrating odor, found in two sacs between the anus and external\n   genitals of the beaver; castor; -- used in medicine as an\n   antispasmodic, and by perfumers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castorin', 'n.', 'A white crystalline substance obtained from castoreum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castor oil', '', 'A mild cathartic oil, expressed or extracted from the\n   seeds of the Ricinus communis, or Palma Christi. When fresh the oil is\n   inodorous and insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrametation', 'n.', 'The art or act of encamping; the making or laying\n   out of a camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrated', 'imp. & p. p.', 'of Castrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrating', 'p. pr. & vb. n.', 'of Castrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrate', 'v. t.', 'To deprive of the testicles; to emasculate; to geld;\n   to alter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrate', 'v. t.', 'To cut or take out; esp. to remove anything erroneous,\n   or objectionable from, as the obscene parts of a writing; to expurgate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castration', 'n.', 'The act of castrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrato', 'n.', 'A male person castrated for the purpose of improving his\n   voice for singing; an artificial, or male, soprano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrel', 'n.', 'See Kestrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrensial', 'a.', 'Belonging to a camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Castrensian', 'a.', 'Castrensial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cast steel', '', 'See Cast steel, under Steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casual', 'a.', 'Happening or coming to pass without design, and without\n   being foreseen or expected; accidental; fortuitous; coming by chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casual', 'a.', 'Coming without regularity; occasional; incidental; as,\n   casual expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casual', 'n.', 'One who receives relief for a night in a parish to which he\n   does not belong; a vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualism', 'n.', 'The doctrine that all things exist or are controlled by\n   chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualist', 'n.', 'One who believes in casualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casually', 'adv.', 'Without design; accidentally; fortuitously; by chance;\n   occasionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualness', 'n.', 'The quality of being casual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualties', 'pl. ', 'of Casualty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualty', 'n.', 'That which comes without design or without being\n   foreseen; contingency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualty', 'n.', 'Any injury of the body from accident; hence, death, or\n   other misfortune, occasioned by an accident; as, an unhappy casualty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casualty', 'n.', 'Numerical loss caused by death, wounds, discharge, or\n   desertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuarina', 'n.', 'A genus of leafless trees or shrubs, with drooping\n   branchlets of a rushlike appearance, mostly natives of Australia. Some\n   of them are large, producing hard and heavy timber of excellent\n   quality, called beefwood from its color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuist', 'n.', 'One who is skilled in, or given to, casuistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuist', 'v. i.', 'To play the casuist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuistic', 'a.', 'Alt. of Casuistieal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuistieal', 'a.', 'Of or pertaining to casuists or casuistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuistry', 'a.', 'The science or doctrine of dealing with cases of\n   conscience, of resolving questions of right or wrong in conduct, or\n   determining the lawfulness or unlawfulness of what a man may do by\n   rules and principles drawn from the Scriptures, from the laws of\n   society or the church, or from equity and natural reason; the\n   application of general moral rules to particular cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casuistry', 'a.', 'Sophistical, equivocal, or false reasoning or teaching\n   in regard to duties, obligations, and morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Casus', 'n.', 'An event; an occurrence; an occasion; a combination of\n   circumstances; a case; an act of God. See the Note under Accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'n.', 'An animal of various species of the genera Felis and Lynx. The\n   domestic cat is Felis domestica. The European wild cat (Felis catus) is\n   much larger than the domestic cat. In the United States the name wild\n   cat is commonly applied to the bay lynx (Lynx rufus) See Wild cat, and\n   Tiger cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'n.', 'A strong vessel with a narrow stern, projecting quarters, and\n   deep waist. It is employed in the coal and timber trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'n.', 'A strong tackle used to draw an anchor up to the cathead of a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'n.', 'A double tripod (for holding a plate, etc.); having six feet,\n   of which three rest on the ground, in whatever position in is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'n.', 'An old game; (a) The game of tipcat and the implement with\n   which it is played. See Tipcat. (c) A game of ball, called, according\n   to the number of batters, one old cat, two old cat, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'n.', 'A cat o nine tails. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'tted', 'imp. & p. p.', 'of Cat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catting', 'p. pr. & vb. n.', 'of Cat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat', 'v. t.', 'To bring to the cathead; as, to cat an anchor. See Anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cata', '', 'The Latin and English form of a Greek preposition, used as a\n   prefix to signify down, downward, under, against, contrary or opposed\n   to, wholly, completely; as in cataclysm, catarrh. It sometimes drops\n   the final vowel, as in catoptric; and is sometimes changed to cath, as\n   in cathartic, catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catabaptist', 'n.', 'One who opposes baptism, especially of infants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catabasion', 'n.', 'A vault under altar of a Greek church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catabiotic', 'a.', 'See under Force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catacaustic', 'a.', 'Relating to, or having the properties of, a caustic\n   curve formed by reflection. See Caustic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catacaustic', 'n.', 'A caustic curve formed by reflection of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catachresis', 'n.', 'A figure by which one word is wrongly put for another,\n   or by which a word is wrested from its true signification; as, \"To take\n   arms against a sea of troubles\". Shak. \"Her voice was but the shadow of\n   a sound.\" Young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catachrestic', 'a.', 'Alt. of Catachrestical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catachrestical', 'a.', 'Belonging to, or in the manner of, a catachresis;\n   wrested from its natural sense or form; forced; far-fetched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataclysm', 'n.', 'An extensive overflow or sweeping flood of water; a\n   deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataclysm', 'n.', 'Any violent catastrophe, involving sudden and extensive\n   changes of the earths surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataclysmal', 'a.', 'Alt. of Cataclysmic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataclysmic', 'a.', 'Of or pertaining to a cataclysm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataclysmist', 'n.', 'One who believes that the most important geological\n   phenomena have been produced by cataclysms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catacomb', 'n.', 'A cave, grotto, or subterraneous place of large extent\n   used for the burial of the dead; -- commonly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catacoustic', 'n.', 'That part of acoustics which treats of reflected\n   sounds or echoes See Acoustics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadioptric', 'a.', 'Alt. of Catadioptrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadioptrical', 'a.', 'Pertaining to, produced by, or involving, both the\n   reflection and refraction of light; as, a catadioptric light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadioptrics', 'n.', 'The science which treats of catadioptric phenomena,\n   or of the used of catadioptric instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadrome', 'n.', 'A race course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadrome', 'n.', 'A machine for raising or lowering heavy weights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadromous', 'a.', 'Having the lowest inferior segment of a pinna nearer\n   the rachis than the lowest superior one; -- said of a mode of branching\n   in ferns, and opposed to anadromous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catadromous', 'a.', 'Living in fresh water, and going to the sea to spawn;\n   -- opposed to anadromous, and said of the eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catafalco', 'n.', 'See Catafalque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catafalque', 'n.', 'A temporary structure sometimes used in the funeral\n   solemnities of eminent persons, for the public exhibition of the\n   remains, or their conveyance to the place of burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catagmatic', 'a.', 'Having the quality of consolidating broken bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataian', 'n.', 'A native of Cathay or China; a foreigner; -- formerly a\n   term of reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalan', 'a.', 'Of or pertaining to Catalonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalan', 'n.', 'A native or inhabitant of Catalonia; also, the language of\n   Catalonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalectic', 'a.', 'Wanting a syllable at the end, or terminating in an\n   imperfect foot; as, a catalectic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalectic', 'a.', 'Incomplete; partial; not affecting the whole of a\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalepsy', 'n.', 'Alt. of Catalepsis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalepsis', 'n.', 'A sudden suspension of sensation and volition, the body\n   and limbs preserving the position that may be given them, while the\n   action of the heart and lungs continues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataleptic', 'a.', 'Pertaining to, or resembling, catalepsy; affected with\n   catalepsy; as, a cataleptic fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catallacta', 'n. pl.', 'A division of Protozoa, of which Magosphaera is the\n   type. They exist both in a myxopod state, with branched pseudopodia,\n   and in the form of ciliated bodies united in free, spherical colonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catallactics', 'n.', 'The science of exchanges, a branch of political\n   economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalog', 'n. & v.', 'Catalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalogize', 'v. t.', 'To insert in a catalogue; to register; to catalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalogue', 'n.', 'A list or enumeration of names, or articles arranged\n   methodically, often in alphabetical order; as, a catalogue of the\n   students of a college, or of books, or of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalogued', 'imp. & p. p.', 'of Catalogue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataloguing', 'p. pr. & vb. n.', 'of Catalogue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalogue', 'v. t.', 'To make a list or catalogue; to insert in a\n   catalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataloguer', 'n.', 'A maker of catalogues; esp. one skilled in the making\n   of catalogues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalpa', 'n.', 'A genus of American and East Indian trees, of which the\n   best know species are the Catalpa bignonioides, a large, ornamental\n   North American tree, with spotted white flowers and long cylindrical\n   pods, and the C. speciosa, of the Mississipi valley; -- called also\n   Indian bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalyse', 'pl. ', 'of Catalysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalysis', 'n.', 'Dissolution; degeneration; decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalysis', 'n.', 'A process by which reaction occurs in the presence of\n   certain agents which were formerly believed to exert an influence by\n   mere contact. It is now believed that such reactions are attended with\n   the formation of an intermediate compound or compounds, so that by\n   alternate composition and decomposition the agent is apparenty left\n   unchanged; as, the catalysis of making ether from alcohol by means of\n   sulphuric acid; or catalysis in the action of soluble ferments (as\n   diastase, or ptyalin) on starch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalysis', 'n.', 'The catalytic force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalytic', 'a.', 'Relating to, or causing, catalysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catalytic', 'n.', 'An agent employed in catalysis, as platinum black,\n   aluminium chloride, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamaran', 'n.', 'A kind of raft or float, consisting of two or more logs\n   or pieces of wood lashed together, and moved by paddles or sail; --\n   used as a surf boat and for other purposes on the coasts of the East\n   and West Indies and South America. Modified forms are much used in the\n   lumber regions of North America, and at life-saving stations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamaran', 'n.', 'Any vessel with twin hulls, whether propelled by sails\n   or by steam; esp., one of a class of double-hulled pleasure boats\n   remarkable for speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamaran', 'n.', 'A kind of fire raft or torpedo bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamaran', 'n.', 'A quarrelsome woman; a scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamenia', 'n. pl.', 'The monthly courses of women; menstrual discharges;\n   menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamenial', 'a.', 'Pertaining to the catamenia, or menstrual discharges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamite', 'n.', 'A boy kept for unnatural purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catamount', 'n.', 'The cougar. Applied also, in some parts of the United\n   States, to the lynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catanadromous', 'a.', 'Ascending and descending fresh streams from and to\n   the sea, as the salmon; anadromous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catapasm', 'n.', 'A compound medicinal powder, used by the ancients to\n   sprinkle on ulcers, to absorb perspiration, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catapeltic', 'a.', 'Of or pertaining to a catapult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catapetalous', 'a.', 'Having the petals held together by stamens, which\n   grow to their bases, as in the mallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphonic', 'a.', 'Of or relating to cataphonics; catacoustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphonics', 'n.', 'That branch of acoustics which treats of reflected\n   sounds; catacoustics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphract', 'n.', 'Defensive armor used for the whole body and often for\n   the horse, also, esp. the linked mail or scale armor of some eastern\n   nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphract', 'n.', 'A horseman covered with a cataphract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphract', 'n.', 'The armor or plate covering some fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphracted', 'a.', 'Covered with a cataphract, or armor of plates,\n   scales, etc.; or with that which corresponds to this, as horny or bony\n   plates, hard, callous skin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphractic', 'a.', 'Of, pertaining to, or resembling, a cataphract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataphysical', 'a.', 'Unnatural; contrary to nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataplasm', 'n.', 'A soft and moist substance applied externally to some\n   part of the body; a poultice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catapuce', 'n.', 'Spurge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catapult', 'n.', 'An engine somewhat resembling a massive crossbow, used by\n   the ancient Greeks and Romans for throwing stones, arrows, spears, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catapult', 'n.', 'A forked stick with elastic band for throwing small\n   stones, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataract', 'n.', 'A great fall of water over a precipice; a large\n   waterfall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataract', 'n.', 'An opacity of the crystalline lens, or of its capsule,\n   which prevents the passage of the rays of light and impairs or destroys\n   the sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataract', 'n.', 'A kind of hydraulic brake for regulating the action of\n   pumping engines and other machines; -- sometimes called dashpot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cataractous', 'a.', 'Of the nature of a cataract in the eye; affected with\n   cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catarrh', 'n.', 'An inflammatory affection of any mucous membrane, in which\n   there are congestion, swelling, and an altertion in the quantity and\n   quality of mucus secreted; as, catarrh of the stomach; catarrh of the\n   bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catarrhal', 'a.', 'Pertaining to, produced by, or attending, catarrh; of\n   the nature of catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catarrhine', 'n.', 'One of the Catarrhina, a division of Quadrumana,\n   including the Old World monkeys and apes which have the nostrils close\n   together and turned downward. See Monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catarrhous', 'a.', 'Catarrhal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastaltic', 'a.', 'Checking evacuations through astringent or styptic\n   qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastasis', 'n.', 'That part of a speech, usually the exordium, in which\n   the orator sets forth the subject matter to be discussed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastasis', 'n.', 'The state, or condition of anything; constitution;\n   habit of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catasterism', 'n.', 'A placing among the stars; a catalogue of stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastrophe', 'n.', 'An event producing a subversion of the order or system\n   of things; a final event, usually of a calamitous or disastrous nature;\n   hence, sudden calamity; great misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastrophe', 'n.', 'The final event in a romance or a dramatic piece; a\n   denouement, as a death in a tragedy, or a marriage in a comedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastrophe', 'n.', 'A violent and widely extended change in the surface of\n   the earth, as, an elevation or subsidence of some part of it, effected\n   by internal causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastrophic', 'a.', 'Of a pertaining to a catastrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastrophism', 'n.', 'The doctrine that the geological changes in the\n   earths crust have been caused by the sudden action of violent physical\n   causes; -- opposed to the doctrine of uniformism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catastrophist', 'n.', 'One who holds the theory or catastrophism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catawba', 'n.', 'A well known light red variety of American grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catawba', 'n.', 'A light-colored, sprightly American wine from the Catawba\n   grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catawbas', 'n. pl.', 'An Appalachian tribe of Indians which originally\n   inhabited the regions near the Catawba river and the head waters of the\n   Santee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catbird', 'n.', 'An American bird (Galeoscoptes Carolinensis); allied to\n   the mocking bird, and like it capable of imitating the notes of other\n   birds, but less perfectly. Its note resembles at times the mewing of a\n   cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catboat', 'n.', 'A small sailboat, with a single mast placed as far forward\n   as possible, carring a sail extended by a gaff and long boom. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catcall', 'n.', 'A sound like the cry of a cat, such as is made in\n   playhouses to express dissatisfaction with a play; also, a small shrill\n   instrument for making such a noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caught', 'imp. & p. p.', 'of Catch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catched', '', 'of Catch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catching', 'p. pr. & vb. n.', 'of Catch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To lay hold on; to seize, especially with the hand; to\n   grasp (anything) in motion, with the effect of holding; as, to catch a\n   ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To seize after pursuing; to arrest; as, to catch a thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To take captive, as in a snare or net, or on a hook; as,\n   to catch a bird or fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'Hence: To insnare; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To seize with the senses or the mind; to apprehend; as,\n   to catch a melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To communicate to; to fasten upon; as, the fire caught\n   the adjoining building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To engage and attach; to please; to charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To get possession of; to attain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To take or receive; esp. to take by sympathy, contagion,\n   infection, or exposure; as, to catch the spirit of an occasion; to\n   catch the measles or smallpox; to catch cold; the house caught fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To come upon unexpectedly or by surprise; to find; as, to\n   catch one in the act of stealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. t.', 'To reach in time; to come up with; as, to catch a train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. i.', 'To attain possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. i.', 'To be held or impeded by entanglement or a light\n   obstruction; as, a kite catches in a tree; a door catches so as not to\n   open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. i.', 'To take hold; as, the bolt does not catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'v. i.', 'To spread by, or as by, infecting; to communicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'Act of seizing; a grasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'That by which anything is caught or temporarily fastened;\n   as, the catch of a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'The posture of seizing; a state of preparation to lay hold\n   of, or of watching he opportunity to seize; as, to lie on the catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'That which is caught or taken; profit; gain; especially, the\n   whole quantity caught or taken at one time; as, a good catch of fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'Something desirable to be caught, esp. a husband or wife in\n   matrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'Passing opportunities seized; snatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'A slight remembrance; a trace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch', 'n.', 'A humorous canon or round, so contrived that the singers\n   catch up each others words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchable', 'a.', 'Capable of being caught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch-basin', 'n.', 'A cistern or vault at the point where a street gutter\n   discharges into a sewer, to catch bulky matters which would not pass\n   readily through the sewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchdrain', 'n.', 'A ditch or drain along the side of a hill to catch the\n   surface water; also, a ditch at the side of a canal to catch the\n   surplus water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catcher', 'n.', 'One who, or that which, catches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catcher', 'n.', 'The player who stands behind the batsman to catch the\n   ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchfly', 'n.', 'A plant with the joints of the stem, and sometimes other\n   parts, covered with a viscid secretion to which small insects adhere.\n   The species of Silene are examples of the catchfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catching', 'a.', 'Infectious; contagious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catching', 'a.', 'Captivating; alluring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catching', 'n.', 'The act of seizing or taking hold of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catch-meadow', 'n.', 'A meadow irrigated by water from a spring or rivulet\n   on the side of hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchment', 'n.', 'A surface of ground on which water may be caught and\n   collected into a reservoir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchpenny', 'a.', 'Made or contrived for getting small sums of money from\n   the ignorant or unwary; as, a catchpenny book; a catchpenny show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchpenny', 'n.', 'Some worthless catchpenny thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchpoll', 'n.', 'A bailiffs assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchup', 'n.', 'Alt. of Catsup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catsup', 'n.', 'A table sauce made from mushrooms, tomatoes, walnuts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchwater', 'n.', 'A ditch or drain for catching water. See Catchdrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchweed', 'n.', 'See Cleavers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchweight', 'adv.', 'Without any additional weight; without being\n   handicapped; as, to ride catchweight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchword', 'n.', 'Among theatrical performers, the last word of the\n   preceding speaker, which reminds one that he is to speak next; cue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchword', 'n.', 'The first word of any page of a book after the first,\n   inserted at the right hand bottom corner of the preceding page for the\n   assistance of the reader. It is seldom used in modern printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchword', 'n.', 'A word or phrase caught up and repeated for effect; as,\n   the catchword of a political party, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catchwork', 'n.', 'A work or artificial water-course for throwing water on\n   lands that lie on the slopes of hills; a catchdrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cate', 'n.', 'Food. [Obs.] See Cates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechetic', 'a.', 'Alt. of Catechetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechetical', 'a.', 'Relating to or consisting in, asking questions and\n   receiving answers, according to the ancient manner of teaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechetically', 'adv.', 'In a catechetical manner; by question and answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechetics', 'n.', 'The science or practice of instructing by questions\n   and answers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechin', 'n.', 'One of the tannic acids, extracted from catechu as a\n   white, crystalline substance; -- called also catechuic acid, and\n   catechuin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechisation', 'n.', 'The act of catechising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechised', 'imp. & p. p.', 'of Catechise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechising', 'p. pr. & vb. n.', 'of Catechise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechise', 'v. t.', 'To instruct by asking questions, receiving answers,\n   and offering explanations and corrections, -- esp. in regard to points\n   of religious faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechise', 'v. t.', 'To question or interrogate; to examine or try by\n   questions; -- sometimes with a view to reproof, by eliciting from a\n   person answers which condemn his own conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechiser', 'n.', 'One who catechises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechism', 'n.', 'A form of instruction by means of questions and answers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechism', 'n.', 'A book containing a summary of principles, especially of\n   religious doctrine, reduced to the form of questions and answers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechismal', 'a.', 'Of or pertaining to a catechism, having the form of\n   questions and answers; catechetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechist', 'n.', 'One who instructs by question and answer, especially in\n   religions matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechistic', 'a.', 'Alt. of Catechistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechistical', 'a.', 'Of or pertaining to a catechist or to a catechism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechize', 'v. t.', 'See Catechise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechu', 'n.', 'A dry, brown, astringent extract, obtained by decoction\n   and evaporation from the Acacia catechu, and several other plants\n   growing in India. It contains a large portion of tannin or tannic acid,\n   and is used in medicine and in the arts. It is also known by the names\n   terra japonica, cutch, gambier, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechuic', 'a.', 'Of or pertaining to catechu or its derivatives. See\n   catechin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechumen', 'L. catechunenus, Gr.', 'One who is\n   receiving rudimentary instruction in the doctrines of Christianity; a\n   neophyte; in the primitive church, one officially recognized as a\n   Christian, and admitted to instruction preliminary to admission to full\n   membership in the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechumenate', 'n.', 'The state or condition of a catechumen or the time\n   during which one is a catechumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechumenical', 'a.', 'Of or pertaining to catechumens; as, catechumenical\n   instructions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catechumenist', 'n.', 'A catechumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categorematic', 'a.', 'Capable of being employed by itself as a term; --\n   said of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categorical', 'a.', 'Of or pertaining to a category.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categorical', 'a.', 'Not hypothetical or relative; admitting no conditions\n   or exceptions; declarative; absolute; positive; express; as, a\n   categorical proposition, or answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categorically', 'adv.', 'Absolutely; directly; expressly; positively; as,\n   to affirm categorically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categoricalness', 'n.', 'The quality of being categorical, positive, or\n   absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categorist', 'n.', 'One who inserts in a category or list; one who\n   classifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categorize', 'v. t.', 'To insert in a category or list; to class; to\n   catalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Categories', 'pl. ', 'of Category');
  INSERT INTO words (word, word_type, description) VALUES  (    'Category', 'n.', 'One of the highest classes to which the objects of\n   knowledge or thought can be reduced, and by which they can be arranged\n   in a system; an ultimate or undecomposable conception; a predicament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Category', 'n.', 'Class; also, state, condition, or predicament; as, we are\n   both in the same category.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catel', 'n.', 'Property; -- often used by Chaucer in contrast with rent, or\n   income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catelectrode', 'n.', 'The negative electrode or pole of a voltaic battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catelectrotonic', 'a.', 'Relating to, or characterized by, catelectrotonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catelectrotonus', 'n.', 'The condition of increased irritability of a nerve\n   in the region of the cathode or negative electrode, on the passage of a\n   current of electricity through it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catene', 'pl. ', 'of Catena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catena', 'n.', 'A chain or series of things connected with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenary', 'a.', 'Alt. of Catenarian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenarian', 'a.', 'Relating to a chain; like a chain; as, a catenary\n   curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenary', 'n.', 'The curve formed by a rope or chain of uniform density\n   and perfect flexibility, hanging freely between two points of\n   suspension, not in the same vertical line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenated', 'imp. & p. p.', 'of Catenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenating', 'p. pr. & vb. n.', 'of Catenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenate', 'v. t.', 'To connect, in a series of links or ties; to chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenation', 'n.', 'Connection of links or union of parts, as in a chain; a\n   regular or connected series. See Concatenation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenulate', 'a.', 'Consisting of little links or chains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catenulate', 'a.', 'Chainlike; -- said both or color marks and of\n   indentations when arranged like the links of a chain, as on shells,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater', 'n.', 'A provider; a purveyor; a caterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catered', 'imp. & p. p.', 'of Cater');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catering', 'p. pr. & vb. n.', 'of Cater');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater', 'n.', 'To provide food; to buy, procure, or prepare provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater', 'n.', 'By extension: To supply what is needed or desired, at\n   theatrical or musical entertainments; -- followed by for or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater', 'n.', 'The four of cards or dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater', 'v. t.', 'To cut diagonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cateran', 'n.', 'A Highland robber: a kind of irregular soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater-cornered', 'a.', 'Diagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cater-cousin', 'n.', 'A remote relation. See Quater-cousin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterer', 'n.', 'One who caters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cateress', 'n.', 'A woman who caters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterpillar', 'n.', 'The larval state of a butterfly or any lepidopterous\n   insect; sometimes, but less commonly, the larval state of other\n   insects, as the sawflies, which are also called false caterpillars. The\n   true caterpillars have three pairs of true legs, and several pairs of\n   abdominal fleshy legs (prolegs) armed with hooks. Some are hairy,\n   others naked. They usually feed on leaves, fruit, and succulent\n   vegetables, being often very destructive, Many of them are popularly\n   called worms, as the cutworm, cankerworm, army worm, cotton worm,\n   silkworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterpillar', 'n.', 'A plant of the genus Scorpiurus, with pods resembling\n   caterpillars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterwauled', 'imp. & p. p.', 'of Caterwaul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterwauling', 'p. pr. & vb. n.', 'of Caterwaul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterwaul', 'v. i.', 'To cry as cats in rutting time; to make a harsh,\n   offensive noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterwaul', 'n.', 'A caterwauling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caterwauling', 'n.', 'The cry of cats; a harsh, disagreeable noise or cry\n   like the cry of cats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catery', 'n.', 'The place where provisions are deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cates', 'n.', 'Provisions; food; viands; especially, luxurious food;\n   delicacies; dainties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-eyed', 'a.', 'Having eyes like a cat; hence, able to see in the dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catfall', 'n.', 'A rope used in hoisting the anchor to the cathead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catfish', 'n.', 'A name given in the United States to various species of\n   siluroid fishes; as, the yellow cat (Amiurus natalis); the bind cat\n   (Gronias nigrilabrus); the mud cat (Pilodictic oilwaris); the stone cat\n   (Noturus flavus); the sea cat (Arius felis); etc. This name is also\n   sometimes applied to the wolf fish. See Bullhrad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catgut', 'n.', 'A cord of great toughness made from the intestines of\n   animals, esp. of sheep, used for strings of musical instruments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catgut', 'n.', 'A sort of linen or canvas, with wide interstices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catharine wheel', '', 'See catherine wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catharist', 'n.', 'One aiming at or pretending to a greater purity of like\n   than others about him; -- applied to persons of various sects. See\n   Albigenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-harpin', 'n.', 'See Cat-harping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-harping', 'n.', 'One of the short ropes or iron cramps used to brace in\n   the shrouds toward the masts so a to give freer sweep to the yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catharsis', 'n.', 'A natural or artificial purgation of any passage, as of\n   the mouth, bowels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathartic', 'a.', 'Alt. of Catharical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catharical', 'a.', 'Cleansing the bowels; promoting evacuations by stool;\n   purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catharical', 'a.', 'Of or pertaining to the purgative principle of senna,\n   as cathartic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathartic', 'n.', 'A medicine that promotes alvine discharges; a purge; a\n   purgative of moderate activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathartin', 'n.', 'The bitter, purgative principle of senna. It is a\n   glucoside with the properties of a weak acid; -- called also cathartic\n   acid, and cathartina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathay', 'n.', 'China; -- an old name for the Celestial Empire, said have\n   been introduced by Marco Polo and to be a corruption of the Tartar name\n   for North China (Khitai, the country of the Khitans.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathead', 'n.', 'A projecting piece of timber or iron near the bow of\n   vessel, to which the anchor is hoisted and secured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedra', 'n.', 'The official chair or throne of a bishop, or of any\n   person in high authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedral', 'n.', 'The principal church in a diocese, so called because in\n   it the bishop has his official chair (Cathedra) or throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedral', 'a.', 'Pertaining to the head church of a diocese; as, a\n   cathedral church; cathedral service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedral', 'a.', 'Emanating from the chair of office, as of a pope or\n   bishop; official; authoritative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedral', 'a.', 'Resembling the aisles of a cathedral; as, cathedral\n   walks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedralic', 'a.', 'Cathedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathedrated', 'a.', 'Relating to the chair or office of a teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheretic', 'n.', 'A mild kind caustic used to reduce warts and other\n   excrescences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catherine wheel', '', 'Same as Rose window and Wheel window. Called also\n   Catherine-wheel window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catherine wheel', '', 'A revolving piece of fireworks resembling in form\n   the window of the same name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheter', 'n.', 'The name of various instruments for passing along mucous\n   canals, esp. applied to a tubular instrument to be introduced into the\n   bladder through the urethra to draw off the urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheterism', 'n.', 'Alt. of Catheterization');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheterization', 'n.', 'The operation of introducing a catheter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheterized', 'imp. & p. p.', 'of Catheterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheterizing', 'p. pr. & vb. n.', 'of Catheterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheterize', 'v. t.', 'To operate on with a catheter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathetometer', 'n.', 'An instrument for the accurate measurement of small\n   differences of height; esp. of the differences in the height of the\n   upper surfaces of two columns of mercury or other fluid, or of the same\n   column at different times. It consists of a telescopic leveling\n   apparatus (d); which slides up or down a perpendicular metallic\n   standard very finely graduated (bb). The telescope is raised or\n   depressed in order to sight the objects or surfaces, and the\n   differences in vertical height are thus shown on the graduated\n   standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catheti', 'pl. ', 'of Cathetus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathetus', 'n.', 'One line or radius falling perpendicularly on another;\n   as, the catheti of a right-angled triangle, that is, the two sides that\n   include the right angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathode', 'n.', 'The part of a voltaic battery by which the electric\n   current leaves substances through which it passes, or the surface at\n   which the electric current passes out of the electrolyte; the negative\n   pole; -- opposed to anode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cathodic', 'a.', 'A term applied to the centrifugal, or efferent, course of\n   the nervous influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-hole', 'n.', 'One of two small holes astern, above the gunroom ports,\n   through which hawsers may be passed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholic', 'a.', 'Universal or general; as, the catholic faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholic', 'a.', 'Not narrow-minded, partial, or bigoted; liberal; as,\n   catholic tastes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholic', 'a.', 'Of or pertaining to, or affecting the Roman Catholics;\n   as, the Catholic emancipation act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholic', 'n.', 'A person who accepts the creeds which are received in\n   common by all parts of the orthodox Christian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholic', 'n.', 'An adherent of the Roman Catholic church; a Roman\n   Catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholical', 'a.', 'Catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicism', 'n.', 'The state or quality of being catholic or universal;\n   catholicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicism', 'n.', 'Liberality of sentiment; breadth of view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicism', 'n.', 'The faith of the whole orthodox Christian church, or\n   adherence thereto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicism', 'n.', 'The doctrines or faith of the Roman Catholic church,\n   or adherence thereto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicity', 'n.', 'The state or quality of being catholic; universality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicity', 'n.', 'Liberality of sentiments; catholicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicity', 'n.', 'Adherence or conformity to the system of doctrine held\n   by all parts of the orthodox Christian church; the doctrine so held;\n   orthodoxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicity', 'n.', 'Adherence to the doctrines of the church of Rome, or\n   the doctrines themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicize', 'v. t. & i.', 'To make or to become catholic or Roman\n   Catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicly', 'adv.', 'In a catholic manner; generally; universally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicness', 'n.', 'The quality of being catholic; universality;\n   catholicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicon', 'n.', 'A remedy for all diseases; a panacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catholicos', 'n.', 'The spiritual head of the Armenian church, who resides\n   at Etchmiadzin, Russia, and has ecclesiastical jurisdiction over, and\n   consecrates the holy oil for, the Armenians of Russia, Turkey, and\n   Persia, including the Patriarchs of Constantinople, Jerusalem, and Sis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catilinarian', 'a.', 'Pertaining to Catiline, the Roman conspirator;\n   resembling Catilines conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cation', 'n.', 'An electro-positive substance, which in\n   electro-decomposition is evolved at the cathode; -- opposed to anion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catkin', 'n.', 'An ament; a species of inflorescence, consisting of a\n   slender axis with many unisexual apetalous flowers along its sides, as\n   in the willow and poplar, and (as to the staminate flowers) in the\n   chestnut, oak, hickory, etc. -- so called from its resemblance to a\n   cats tail. See Illust. of Ament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catlike', 'a.', 'Like a cat; stealthily; noiselessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catling', 'n.', 'A little cat; a kitten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catling', 'n.', 'Catgut; a catgut string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catling', 'n.', 'A double-edged, sharp-pointed dismembering knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catlinite', 'n.', 'A red clay from the Upper Missouri region, used by the\n   Indians for their pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catnip', 'n.', 'Alt. of Catmint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catmint', 'n.', 'A well-know plant of the genus Nepeta (N. Cataria);\n   somewhat like mint, having a string scent, and sometimes used in\n   medicine. It is so called because cats have a peculiar fondness for it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cato-cathartic', 'n.', 'A remedy that purges by alvine discharges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catonian', 'a.', 'Of, pertaining to, or resembling, the stern old Roman,\n   Cato the Censor; severe; inflexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat o nine tails', '', 'See under Cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catopter', 'n.', 'Alt. of Catoptron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catoptron', 'n.', 'A reflecting optical glass or instrument; a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catoptric', 'a.', 'Alt. of Catoptrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catoptrical', 'a.', 'Of or pertaining to catoptrics; produced by\n   reflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catoptrics', 'n.', 'That part of optics which explains the properties and\n   phenomena of reflected light, and particularly that which is reflected\n   from mirrors or polished bodies; -- formerly called anacamptics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catoptromancy', 'n.', 'A species of divination, which was performed by\n   letting down a mirror into water, for a sick person to look at his face\n   in it. If his countenance appeared distorted and ghastly, it was an ill\n   omen; if fresh and healthy, it was favorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catopron', 'n.', 'See Catopter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catpipe', 'n.', 'See Catcall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-rigged', 'a.', 'Rigged like a catboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-salt', 'n.', 'A sort of salt, finely granulated, formed out of the\n   bittern or leach brine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cats-eye', 'n.', 'A variety of quartz or chalcedony, exhibiting opalescent\n   reflections from within, like the eye of a cat. The name is given to\n   other gems affording like effects, esp. the chrysoberyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cats-foot', 'n.', 'A plant (Nepeta Glechoma) of the same genus with\n   catnip; ground ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-silver', 'n.', 'Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catskill period', '', 'The closing subdivision of the Devonian age in\n   America. The rocks of this period are well developed in the Catskill\n   mountains, and extend south and west under the Carboniferous formation.\n   See the Diagram under Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catsos', 'pl. ', 'of Catso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catso', 'n.', 'A base fellow; a rogue; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cats-paw', 'n.', 'A light transitory air which ruffles the surface of the\n   water during a calm, or the ripples made by such a puff of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cats-paw', 'n.', 'A particular hitch or turn in the bight of a rope, into\n   which a tackle may be hooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cats-paw', 'n.', 'A dupe; a tool; one who, or that which, is used by\n   another as an instrument to a accomplish his purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cats-tail', 'n.', 'See Timothy, Cat-tail, Cirrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catstick', 'n.', 'A stick or club employed in the game of ball called cat\n   or tipcat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catstitch', 'v. t.', 'To fold and sew down the edge of with a coarse zigzag\n   stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catsup', 'n.', 'Same as Catchup, and Ketchup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cat-tail', 'n.', 'A tall rush or flag (Typha latifolia) growing in marshes,\n   with long, flat leaves, and having its flowers in a close cylindrical\n   spike at the top of the stem. The leaves are frequently used for\n   seating chairs, making mats, etc. See Catkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cattish', 'a.', 'Catlike; feline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cattle', 'n. pl.', 'Quadrupeds of the Bovine family; sometimes, also,\n   including all domestic quadrupeds, as sheep, goats, horses, mules,\n   asses, and swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Catty', 'n.', 'An East Indian Weight of 1 1/3 pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucasian', 'a.', 'Of or pertaining to the Caucasus, a mountainous region\n   between the Black and Caspian seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucasian', 'a.', 'Of or pertaining to the white races of mankind, of whom\n   the people about Mount Caucasus were formerly taken as the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucasian', 'n.', 'A native or inhabitant of the Caucasus, esp. a\n   Circassian or Georgian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucasian', 'n.', 'A member of any of the white races of mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucus', 'n.', 'A meeting, especially a preliminary meeting, of persons\n   belonging to a party, to nominate candidates for public office, or to\n   select delegates to a nominating convention, or to confer regarding\n   measures of party policy; a political primary meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucused', 'imp. & p. p.', 'of Caucus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucusing', 'p. pr. & vb. n.', 'of Caucus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caucus', 'v. i.', 'To hold, or meet in, a caucus or caucuses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudad', 'adv.', 'Backwards; toward the tail or posterior part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauda galli', '', 'A plume-shaped fossil, supposed to be a seaweed,\n   characteristic of the lower Devonian rocks; as, the cauda galli grit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudal', 'a.', 'Of the nature of, or pertaining to, a tail; having a\n   tail-like appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudata', 'n. pl.', 'See Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudate', 'a.', 'Alt. of Caudated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudated', 'a.', 'Having a tail; having a termination like a tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudices', 'pl. ', 'of Caudex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudexes', 'pl. ', 'of Caudex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudex', 'n.', 'The stem of a tree., esp. a stem without a branch, as of a\n   palm or a tree fern; also, the perennial rootstock of an herbaceous\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudicle', 'n.', 'Alt. of Caudicula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudicula', 'n.', 'A slender, elastic process, to which the masses of\n   pollen in orchidaceous plants are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudle', 'n.', 'A kind of warm drink for sick persons, being a mixture of\n   wine with eggs, bread, sugar, and spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudled', 'imp. & p. p.', 'of Caudle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudling', 'p. pr. & vb. n.', 'of Caudle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudle', 'v. t.', 'To make into caudle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caudle', 'v. t.', 'Too serve as a caudle to; to refresh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauf', 'n.', 'A chest with holes for keeping fish alive in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caufle', 'n.', 'A gang of slaves. Same as Coffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caught', '', 'imp. & p. p. of Catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauk', 'n.', 'Alt. of Cauker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauker', 'n.', 'See Cawk, Calker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caul', 'n.', 'A covering of network for the head, worn by women; also, a\n   net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caul', 'n.', 'The fold of membrane loaded with fat, which covers more or\n   less of the intestines in mammals; the great omentum. See Omentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caul', 'n.', 'A part of the amnion, one of the membranes enveloping the\n   fetus, which sometimes is round the head of a child at its birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caulescent', 'a.', 'Having a leafy stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caulicle', 'n.', 'A short caulis or stem, esp. the rudimentary stem seen in\n   the embryo of seed; -- otherwise called a radicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauliculi', 'pl. ', 'of Cauliculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauliculus', 'n.', 'In the Corinthian capital, one of the eight stalks\n   rising out of the lower leafage and terminating in leaves which seem to\n   support the volutes. See Illust. of Corinthian order, under Corinthian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauliflower', 'n.', 'An annual variety of Brassica oleracea, or cabbage, of\n   which the cluster of young flower stalks and buds is eaten as a\n   vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauliflower', 'n.', 'The edible head or \"curd\" of a cauliflower plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauliform', 'a.', 'Having the form of a caulis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauline', 'a.', 'Growing immediately on a caulis; of or pertaining to a\n   caulis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caules', 'pl. ', 'of Caulis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caulis', 'n.', 'An herbaceous or woody stem which bears leaves, and may\n   bear flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caulk', 'v. t. & n.', 'See Calk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caulocarpous', 'a.', 'Having stems which bear flowers and fruit year after\n   year, as most trees and shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauma', 'n.', 'Great heat, as of the body in fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauponize', 'v. i.', 'To sell wine or victuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causable', 'a.', 'Capable of being caused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causal', 'a.', 'Relating to a cause or causes; inplying or containing a\n   cause or causes; expressing a cause; causative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causal', 'n.', 'A causal word or form of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causality', 'n.', 'The agency of a cause; the action or power of a cause,\n   in producing its effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causality', 'n.', 'The faculty of tracing effects to their causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causally', 'adv.', 'According to the order or series of causes; by tracing\n   effects to causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causally', 'n.', 'The lighter, earthy parts of ore, carried off washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causation', 'n.', 'The act of causing; also the act or agency by which an\n   effect is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causationist', 'n.', 'One who believes in the law of universal causation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causative', 'a.', 'Effective, as a cause or agent; causing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causative', 'a.', 'Expressing a cause or reason; causal; as, the ablative\n   is a causative case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causative', 'n.', 'A word which expresses or suggests a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causatively', 'adv.', 'In a causative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causator', 'n.', 'One who causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v.', 'That which produces or effects a result; that from which\n   anything proceeds, and without which it would not exist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v.', 'That which is the occasion of an action or state; ground;\n   reason; motive; as, cause for rejoicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v.', 'Sake; interest; advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v.', 'A suit or action in court; any legal process by which a\n   party endeavors to obtain his claim, or what he regards as his right;\n   case; ground of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v.', 'Any subject of discussion or debate; matter; question;\n   affair in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v.', 'The side of a question, which is espoused, advocated, and\n   upheld by a person or party; a principle which is advocated; that which\n   a person or party seeks to attain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caused', 'imp. & p. p.', 'of Cause');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causing', 'p. pr. & v. n.', 'of Cause');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'n.', 'To effect as an agent; to produce; to be the occasion of; to\n   bring about; to bring into existence; to make; -- usually followed by\n   an infinitive, sometimes by that with a finite verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'v. i.', 'To assign or show cause; to give a reason; to make\n   excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cause', 'conj.', 'Abbreviation of Because.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeful', 'n.', 'Having a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeless', 'a.', '1. Self-originating; uncreated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeless', 'a.', 'Without just or sufficient reason; groundless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeless', 'adv.', 'Without cause or reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causelessness', 'n.', 'The state of being causeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causer', 'n.', 'One who or that which causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeuse', 'n.', 'A kind of sofa for two persons. A tete-/-tete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeway', 'n.', 'Alt. of Causey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causey', 'n.', 'A way or road raised above the natural level of the ground,\n   serving as a dry passage over wet or marshy ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causewayed', 'a.', 'Alt. of Causeyed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causeyed', 'a.', 'Having a raised way (causeway or causey); paved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causidical', 'a.', 'Pertaining to an advocate, or to the maintenance and\n   defense of suits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caustic', 'a.', 'Alt. of Caustical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caustical', 'a.', 'Capable of destroying the texture of anything or eating\n   away its substance by chemical action; burning; corrosive; searing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caustical', 'a.', 'Severe; satirical; sharp; as, a caustic remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caustic', 'a.', 'Any substance or means which, applied to animal or other\n   organic tissue, burns, corrodes, or destroys it by chemical action; an\n   escharotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caustic', 'a.', 'A caustic curve or caustic surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caustically', 'adv.', 'In a caustic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causticily', 'n.', 'The quality of being caustic; corrosiveness; as, the\n   causticity of potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causticily', 'n.', 'Severity of language; sarcasm; as, the causticity of a\n   reply or remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Causticness', 'n.', 'The quality of being caustic; causticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautel', 'n.', 'Caution; prudence; wariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautel', 'n.', 'Craft; deceit; falseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautelous', 'a.', 'Caution; prudent; wary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautelous', 'a.', 'Crafty; deceitful; false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauter', 'n.', 'A hot iron for searing or cauterizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterant', 'n.', 'A cauterizing substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterism', 'n.', 'The use or application of a caustic; cautery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterization', 'n.', 'The act of searing some morbid part by the\n   application of a cautery or caustic; also, the effect of such\n   application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterized', 'imp. & p. p.', 'of Cauterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterizing', 'p. pr. & vb. n.', 'of Cauterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterize', 'v. t.', 'To burn or sear with a cautery or caustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauterize', 'v. t.', 'To sear, as the conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cauteries', 'pl. ', 'of Cautery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautery', 'n.', 'A burning or searing, as of morbid flesh, with a hot iron,\n   or by application of a caustic that will burn, corrode, or destroy\n   animal tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautery', 'n.', 'The iron of other agent in cauterizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caution', 'n.', 'A careful attention to the probable effects of an act, in\n   order that failure or harm may be avoided; prudence in regard to\n   danger; provident care; wariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caution', 'n.', 'Security; guaranty; bail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caution', 'n.', 'Precept or warning against evil of any kind; exhortation\n   to wariness; advice; injunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautioned', 'imp. & p. p.', 'of Caution');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautioning', 'p. pr. & vb. n.', 'of Caution');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caution', 'v. t.', 'To give notice of danger to; to warn; to exhort [one]\n   to take heed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautionary', 'a.', 'Conveying a caution, or warning to avoid danger; as,\n   cautionary signals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautionary', 'a.', 'Given as a pledge or as security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautionary', 'a.', 'Wary; cautious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautioner', 'n.', 'One who cautions or advises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautioner', 'n.', 'A surety or sponsor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautionry', 'n.', 'Suretyship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautious', 'a.', 'Attentive to examine probable effects and consequences of\n   acts with a view to avoid danger or misfortune; prudent; circumspect;\n   wary; watchful; as, a cautious general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautiously', 'adv.', 'In a cautious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cautiousness', 'n.', 'The quality of being cautious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalcade', 'n.', 'A procession of persons on horseback; a formal, pompous\n   march of horsemen by way of parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalero', 'n.', 'Alt. of Cavaliero');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavaliero', 'n.', 'A cavalier; a gallant; a libertine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'n.', 'A military man serving on horseback; a knight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'n.', 'A gay, sprightly, military man; hence, a gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'n.', 'One of the court party in the time of king Charles I. as\n   contrasted with a Roundhead or an adherent of Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'n.', 'A work of more than ordinary height, rising from the\n   level ground of a bastion, etc., and overlooking surrounding parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'a.', 'Gay; easy; offhand; frank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'a.', 'High-spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'a.', 'Supercilious; haughty; disdainful; curt; brusque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalier', 'a.', 'Of or pertaining to the party of King Charles I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalierish', 'a.', 'Somewhat like a cavalier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalierism', 'n.', 'The practice or principles of cavaliers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalierly', 'adv.', 'In a supercilious, disdainful, or haughty manner;\n   arrogantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalierness', 'n.', 'A disdainful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavally', 'n.', 'A carangoid fish of the Atlantic coast (Caranx hippos): --\n   called also horse crevalle. [See Illust. under Carangoid.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalry', 'n.', 'That part of military force which serves on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavalryman', 'n.', 'One of a body of cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavatina', 'n.', 'Originally, a melody of simpler form than the aria; a\n   song without a second part and a da capo; -- a term now variously and\n   vaguely used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cave', 'n.', 'A hollow place in the earth, either natural or artificial; a\n   subterraneous cavity; a cavern; a den.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cave', 'n.', 'Any hollow place, or part; a cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caved', 'imp. & p. p.', 'of Cave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caving', 'p. pr. & vb. n.', 'of Cave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cave', 'n.', 'To make hollow; to scoop out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cave', 'v. i.', 'To dwell in a cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cave', 'v. i.', 'To fall in or down; as, the sand bank caved. Hence\n   (Slang); to retreat from a position; to give way; to yield in a\n   disputed matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caveat', 'n.', 'A notice given by an interested party to some officer not\n   to do a certain act until the party is heard in opposition; as, a\n   caveat entered in a probate court to stop the proving of a will or the\n   taking out of letters of administration, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caveat', 'n.', 'A description of some invention, designed to be patented,\n   lodged in the patent office before the patent right is applied for, and\n   operating as a bar to the issue of letters patent to any other person,\n   respecting the same invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caveat', 'n.', 'Intimation of caution; warning; protest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caveating', 'n.', 'Shifting the sword from one side of an adversarys sword\n   to the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caveator', 'n.', 'One who enters a caveat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavendish', 'n.', 'Leaf tobacco softened, sweetened, and pressed into plugs\n   or cakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavern', 'n.', 'A large, deep, hollow place in the earth; a large cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caverned', 'a.', 'Containing caverns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caverned', 'a.', 'Living in a cavern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavernous', 'a.', 'Full of caverns; resembling a cavern or large cavity;\n   hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavernous', 'a.', 'Filled with small cavities or cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavernous', 'a.', 'Having a sound caused by a cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavernulous', 'a.', 'Full of little cavities; as, cavernulous metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavesson', 'n.', 'Alt. of Cavezon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavezon', 'n.', 'A kind of noseband used in breaking and training horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavetto', 'n.', 'A concave molding; -- used chiefly in classical\n   architecture. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviare', 'n.', 'Alt. of Caviar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviar', 'n.', 'The roes of the sturgeon, prepared and salted; -- used as a\n   relish, esp. in Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavicorn', 'a.', 'Having hollow horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavicornia', 'n. pl.', 'A group of ruminants whose horns are hollow, and\n   planted on a bony process of the front, as the ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviled', 'imp. & p. p.', 'of Cavil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavilled', '', 'of Cavil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviling', 'p. pr. & vb. n.', 'of Cavil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavilling', '', 'of Cavil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavil', 'v. i.', 'To raise captious and frivolous objections; to find fault\n   without good reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavil', 'v. t.', 'To cavil at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavil', 'n.', 'A captious or frivolous objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviler', 'n.', 'Alt. of Caviller');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviller', 'n.', 'One who cavils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caviling', 'a.', 'Disposed to cavil; finding fault without good reason. See\n   Captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavilingly', 'adv.', 'In a caviling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavillation', 'n.', 'Frivolous or sophistical objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavilous', 'a.', 'Alt. of Cavillous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavillous', 'a.', 'Characterized by caviling, or disposed to cavil;\n   quibbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavin', 'n.', 'A hollow way, adapted to cover troops, and facilitate their\n   aproach to a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavitary', 'a.', 'Containing a body cavity; as, the cavitary or nematoid\n   worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavities', 'pl. ', 'of Cavity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavity', 'n.', 'Hollowness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavity', 'n.', 'A hollow place; a hollow; as, the abdominal cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavo-relievo', 'n.', 'Cavo-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavo-rilievo', 'n.', 'Hollow relief; sculpture in relief within a sinking\n   made for the purpose, so no part of it projects beyond the plain\n   surface around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavorted', 'imp. & p. p.', 'of Cavort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavorting', 'p. pr. & vb. n.', 'of Cavort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavort', 'v. i.', 'To prance ostentatiously; -- said of a horse or his\n   rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavies', 'pl. ', 'of Cavy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cavy', 'n.', 'A rodent of the genera Cavia and Dolichotis, as the guinea\n   pig (Cavia cobaya). Cavies are natives of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cawed', 'imp. & p. p.', 'of Caw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cawing', 'p. pr. & vb. n.', 'of Caw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caw', 'v. i.', 'To cry like a crow, rook, or raven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caw', 'n.', 'The cry made by the crow, rook, or raven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cawk', 'n.', 'An opaque, compact variety of barite, or heavy spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cawker', 'n.', 'See Calker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cawky', 'a.', 'Of or pertaining to cawk; like cawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caxon', 'n.', 'A kind of wig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Caxton', 'n.', 'Any book printed by William Caxton, the first English\n   printer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cay', 'n.', 'See Key, a ledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cayenne', 'n.', 'Cayenne pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cayman', 'n.', 'The south America alligator. See Alligator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cayugas', 'n. pl.', 'A tribe of Indians formerly inhabiting western\n   New-York, forming part of the confederacy called the Five Nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cayuse', 'n.', 'An Indian pony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cazique', 'n.', 'Alt. of Cazic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cazic', 'n.', 'A chief or petty king among some tribes of Indians in\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceased', 'imp. & p. p.', 'of Cease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceasing', 'p. pr. & vb. n.', 'of Cease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cease', 'v. i.', 'To come to an end; to stop; to leave off or give over; to\n   desist; as, the noise ceased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cease', 'v. i.', 'To be wanting; to fail; to pass away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cease', 'v. t.', 'To put a stop to; to bring to an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cease', 'n.', 'Extinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceaseless', 'a.', 'Without pause or end; incessant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceaseless', 'adv.', 'Without intermission or end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cecidomyia', 'n.', 'A genus of small dipterous files, including several\n   very injurious species, as the Hessian fly. See Hessian fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cecity', 'n.', 'Blindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cecutiency', 'n.', 'Partial blindness, or a tendency to blindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedar', 'n.', 'The name of several evergreen trees. The wood is remarkable\n   for its durability and fragrant odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedar', 'a.', 'Of or pertaining to cedar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedared', 'a.', 'Covered, or furnished with, cedars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedarn', 'a.', 'Of or pertaining to the cedar or its wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceded', 'imp. & p. p.', 'of Cede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceding', 'p. pr. & vb. n.', 'of Cede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cede', 'v. t.', 'To yield or surrender; to give up; to resign; as, to cede\n   a fortress, a province, or country, to another nation, by treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedilla', 'n.', 'A mark placed under the letter c [thus, c], to show that\n   it is to be sounded like s, as in facade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedrat', 'n.', 'Properly the citron, a variety of Citrus medica, with large\n   fruits, not acid, and having a high perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedrene', 'n.', 'A rich aromatic oil, C15H24, extracted from oil of red\n   cedar, and regarded as a polymeric terpene; also any one of a class of\n   similar substances, as the essential oils of cloves, cubebs, juniper,\n   etc., of which cedrene proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedrine', 'a.', 'Of or pertaining to cedar or the cedar tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedriret', 'n.', 'Same as Coerulignone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedry', 'a.', 'Of the nature of cedar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cedule', 'n.', 'A scroll; a writing; a schedule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceduous', 'a.', 'Fit to be felled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceiled', 'imp. & p. p.', 'of Ceil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceiling', 'p. pr. & vb. n.', 'of Ceil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceil', 'v. t.', 'To overlay or cover the inner side of the roof of; to\n   furnish with a ceiling; as, to ceil a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceil', 'v. t.', 'To line or finish a surface, as of a wall, with plaster,\n   stucco, thin boards, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceiling', 'v. t.', 'The inside lining of a room overhead; the under side of\n   the floor above; the upper surface opposite to the floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceiling', 'v. t.', 'The lining or finishing of any wall or other surface,\n   with plaster, thin boards, etc.; also, the work when done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceiling', 'v. t.', 'The inner planking of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceint', 'n.', 'A girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celadon', 'n.', 'A pale sea-green color; also, porcelain or fine pottery of\n   this tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celandine', 'n.', 'A perennial herbaceous plant (Chelidonium majus) of the\n   poppy family, with yellow flowers. It is used as a medicine in\n   jaundice, etc., and its acrid saffron-colored juice is used to cure\n   warts and the itch; -- called also greater celandine and swallowwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celature', 'n.', 'The act or art of engraving or embossing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celature', 'n.', 'That which is engraved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrant', 'n.', 'One who performs a public religious rite; -- applied\n   particularly to an officiating priest in the Roman Catholic Church, as\n   distinguished from his assistants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrated', 'imp. & p. p.', 'of Celebrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrating', 'p. pr. & vb. n.', 'of Celebrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrate', 'v. t.', 'To extol or honor in a solemn manner; as, to\n   celebrate the name of the Most High.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrate', 'v. t.', 'To honor by solemn rites, by ceremonies of joy and\n   respect, or by refraining from ordinary business; to observe duly; to\n   keep; as, to celebrate a birthday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrate', 'v. t.', 'To perform or participate in, as a sacrament or\n   solemn rite; to solemnize; to perform with appropriate rites; as, to\n   celebrate a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrated', 'a.', 'Having celebrity; distinguished; renowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebration', 'n.', 'The act, process, or time of celebrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrator', 'n.', 'One who celebrates; a praiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrious', 'a.', 'Famous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrities', 'pl. ', 'of Celebrity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrity', 'n.', 'Celebration; solemnization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrity', 'n.', 'The state or condition of being celebrated; fame;\n   renown; as, the celebrity of Washington.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celebrity', 'n.', 'A person of distinction or renown; -- usually in the\n   plural; as, he is one of the celebrities of the place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celeriac', 'n.', 'Turnip-rooted celery, a from of celery with a large\n   globular root, which is used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celerity', 'n.', 'Rapidity of motion; quickness; swiftness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celery', 'n.', 'A plant of the Parsley family (Apium graveolens); of which\n   the blanched leafstalks are used as a salad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestial', 'a.', 'Belonging to the aerial regions, or visible heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestial', 'a.', 'Of or pertaining to the spiritual heaven; heavenly;\n   divine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestial', 'n.', 'An inhabitant of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestial', 'n.', 'A native of China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestialize', 'v. t.', 'To make celestial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestially', 'adv.', 'In a celestial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestify', 'v. t.', 'To make like heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestine', 'n.', 'Alt. of Celestite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestite', 'n.', 'Native strontium sulphate, a mineral so named from its\n   occasional delicate blue color. It occurs crystallized, also in compact\n   massive and fibrous forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestine', 'n.', 'Alt. of Celestinian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celestinian', 'n.', 'A monk of the austere branch of the Franciscan Order\n   founded by Celestine V. in the 13th centry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celiac', 'a.', 'See Coellac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celibacy', 'n.', 'The state of being unmarried; single life, esp. that of a\n   bachelor, or of one bound by vows not to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celibate', 'n.', 'Celibate state; celibacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celibate', 'n.', 'One who is unmarried, esp. a bachelor, or one bound by\n   vows not to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celibate', 'a.', 'Unmarried; single; as, a celibate state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celibatist', 'n.', 'One who lives unmarried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celidography', 'n.', 'A description of apparent spots on the disk of the\n   sun, or on planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'A very small and close apartment, as in a prison or in a\n   monastery or convent; the hut of a hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'A small religious house attached to a monastery or convent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'Any small cavity, or hollow place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'The space between the ribs of a vaulted roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'Same as Cella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'A jar of vessel, or a division of a compound vessel, for\n   holding the exciting fluid of a battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'n.', 'One of the minute elementary structures, of which the greater\n   part of the various tissues and organs of animals and plants are\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celled', 'imp. & p. p.', 'of Cell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cell', 'v. t.', 'To place or inclose in a cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cella', 'n.', 'The part inclosed within the walls of an ancient temple, as\n   distinguished from the open porticoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellar', 'n.', 'A room or rooms under a building, and usually below the\n   surface of the ground, where provisions and other stores are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellarage', 'n.', 'The space or storerooms of a cellar; a cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellarage', 'n.', 'Chare for storage in a cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellarer', 'n.', 'A steward or butler of a monastery or chapter; one who\n   has charge of procuring and keeping the provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellaret', 'n.', 'A receptacle, as in a dining room, for a few bottles of\n   wine or liquor, made in the form of a chest or coffer, or a deep drawer\n   in a sideboard, and usually lined with metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellarist', 'n.', 'Same as Cellarer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celled', 'a.', 'Containing a cell or cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellepore', 'n.', 'A genus of delicate branching corals, made up of minute\n   cells, belonging to the Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celliferous', 'a.', 'Bearing or producing cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellos', 'pl. ', 'of Cello');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celli', 'pl. ', 'of Cello');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cello', 'n.', 'A contraction for Violoncello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellular', 'a.', 'Consisting of, or containing, cells; of or pertaining to\n   a cell or cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellulated', 'a.', 'Cellular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellule', 'n.', 'A small cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celluliferous', 'a.', 'Bearing or producing little cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellulitis', 'n.', 'An inflammantion of the cellular or areolar tissue,\n   esp. of that lying immediately beneath the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celluloid', 'n.', 'A substance composed essentially of gun cotton and\n   camphor, and when pure resembling ivory in texture and color, but\n   variously colored to imitate coral, tortoise shell, amber, malachite,\n   etc. It is used in the manufacture of jewelry and many small articles,\n   as combs, brushes, collars, and cuffs; -- originally called xylonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellulose', 'a.', 'Consisting of, or containing, cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cellulose', 'n.', 'The substance which constitutes the essential part of\n   the solid framework of plants, of ordinary wood, linen, paper, etc. It\n   is also found to a slight extent in certain animals, as the tunicates.\n   It is a carbohydrate, (C6H10O5)n, isomeric with starch, and is\n   convertible into starches and sugars by the action of heat and acids.\n   When pure, it is a white amorphous mass. See Starch, Granulose, Lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celotomy', 'n.', 'The act or operation of cutting, to relieve the structure\n   in strangulated hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celsiture', 'n.', 'Height; altitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celsius', 'n.', 'The Celsius thermometer or scale, so called from Anders\n   Celsius, a Swedish astronomer, who invented it. It is the same as the\n   centigrade thermometer or scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celt', 'n.', 'One of an ancient race of people, who formerly inhabited a\n   great part of Central and Western Europe, and whose descendants at the\n   present day occupy Ireland, Wales, the Highlands of Scotland, and the\n   northern shores of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celt', 'n.', 'A weapon or implement of stone or metal, found in the tumuli,\n   or barrows, of the early Celtic nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celtiberian', 'a.', 'Of or pertaining to the ancient Celtiberia (a district\n   in Spain lying between the Ebro and the Tagus) or its inhabitants the\n   Celtiberi (Celts of the river Iberus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celtiberian', 'n.', 'An inhabitant of Celtiberia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celtic', 'a.', 'Of or pertaining to the Celts; as, Celtic people, tribes,\n   literature, tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celtic', 'n.', 'The language of the Celts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celticism', 'n.', 'A custom of the Celts, or an idiom of their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Celticize', 'v. t.', 'To render Celtic; to assimilate to the Celts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cembalo', 'n.', 'An old name for the harpsichord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'Any substance used for making bodies adhere to each other,\n   as mortar, glue, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'A kind of calcined limestone, or a calcined mixture of clay\n   and lime, for making mortar which will harden under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'The powder used in cementation. See Cementation, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'Bond of union; that which unites firmly, as persons in\n   friendship, or men in society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'The layer of bone investing the root and neck of a tooth;\n   -- called also cementum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cemented', 'imp. & p. p.', 'of Cement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cementing', 'p. pr. & vb. n.', 'of Cement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'To unite or cause to adhere by means of a cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'To unite firmly or closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'n.', 'To overlay or coat with cement; as, to cement a cellar\n   bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cement', 'v. i.', 'To become cemented or firmly united; to cohere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cemental', 'a.', 'Of or pertaining to cement, as of a tooth; as, cemental\n   tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cementation', 'n.', 'The act or process of cementing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cementation', 'n.', 'A process which consists in surrounding a solid body\n   with the powder of other substances, and heating the whole to a degree\n   not sufficient to cause fusion, the physical properties of the body\n   being changed by chemical combination with powder; thus iron becomes\n   steel by cementation with charcoal, and green glass becomes porcelain\n   by cementation with sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cementatory', 'a.', 'Having the quality of cementing or uniting firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cementer', 'n.', 'A person or thing that cements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cementitious', 'n.', 'Of the nature of cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cemeterial', 'a.', 'Of or pertaining to a cemetery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cemeteries', 'pl. ', 'of Cemetery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cemetery', 'n.', 'A place or ground set apart for the burial of the dead; a\n   graveyard; a churchyard; a necropolis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenanthy', 'n.', 'The absence or suppression of the essential organs\n   (stamens and pistil) in a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenation', 'n.', 'Meal-taking; dining or supping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenatory', 'a.', 'Of or pertaining to dinner or supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenobite', 'n.', 'One of a religious order, dwelling in a convent, or a\n   community, in opposition to an anchoret, or hermit, who lives in\n   solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenobitic', 'a.', 'Alt. of Cenobitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenobitical', 'a.', 'Of or pertaining to a cenobite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenobitism', 'n.', 'The state of being a cenobite; the belief or practice\n   of a cenobite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenogamy', 'n.', 'The state of a community which permits promiscuous sexual\n   intercourse among its members, as in certain societies practicing\n   communism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenotaph', 'n.', 'An empty tomb or a monument erected in honor of a person\n   who is buried elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenotaphy', 'n.', 'A cenotaph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cenozoic', 'a.', 'Belonging to the most recent division of geological time,\n   including the tertiary, or Age of mammals, and the Quaternary, or Age\n   of man. [Written also caenozoic, cainozoic, kainozoic.] See Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cense', 'n.', 'A census; -- also, a public rate or tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cense', 'n.', 'Condition; rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censed', 'imp. & p. p.', 'of Cense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censing', 'p. pr. & vb. n.', 'of Cense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cense', 'v. t.', 'To perfume with odors from burning gums and spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cense', 'v. i.', 'To burn or scatter incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censer', 'n.', 'A vessel for perfumes; esp. one in which incense is burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censor', 'n.', 'One of two magistrates of Rome who took a register of the\n   number and property of citizens, and who also exercised the office of\n   inspector of morals and conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censor', 'n.', 'One who is empowered to examine manuscripts before they are\n   committed to the press, and to forbid their publication if they contain\n   anything obnoxious; -- an official in some European countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censor', 'n.', 'One given to fault-finding; a censurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censor', 'n.', 'A critic; a reviewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censorial', 'a.', 'Belonging to a censor, or to the correction of public\n   morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censorial', 'a.', 'Full of censure; censorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censorian', 'a.', 'Censorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censorious', 'a.', 'Addicted to censure; apt to blame or condemn; severe in\n   making remarks on others, or on their writings or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censorious', 'a.', 'Implying or expressing censure; as, censorious remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censorship', 'n.', 'The office or power of a censor; as, to stand for a\n   censorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censual', 'a.', 'Relating to, or containing, a census.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censurable', 'a.', 'Deserving of censure; blamable; culpable;\n   reprehensible; as, a censurable person, or censurable conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'n.', 'Judgment either favorable or unfavorable; opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'n.', 'The act of blaming or finding fault with and condemning as\n   wrong; reprehension; blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'n.', 'Judicial or ecclesiastical sentence or reprimand;\n   condemnatory judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censured', 'imp. & p. p.', 'of Censure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censuring', 'p. pr. & vb. n.', 'of Censure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'v. i.', 'To form or express a judgment in regard to; to\n   estimate; to judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'v. i.', 'To find fault with and condemn as wrong; to blame; to\n   express disapprobation of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'v. i.', 'To condemn or reprimand by a judicial or ecclesiastical\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censure', 'v. i.', 'To judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Censurer', 'n.', 'One who censures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Census', 'n.', 'A numbering of the people, and valuation of their estate,\n   for the purpose of imposing taxes, etc.; -- usually made once in five\n   years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Census', 'n.', 'An official registration of the number of the people, the\n   value of their estates, and other general statistics of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cent', 'n.', 'A hundred; as, ten per cent, the proportion of ten parts in a\n   hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cent', 'n.', 'A United States coin, the hundredth part of a dollar,\n   formerly made of copper, now of copper, tin, and zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cent', 'n.', 'An old game at cards, supposed to be like piquet; -- so\n   called because 100 points won the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centage', 'n.', 'Rate by the hundred; percentage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cental', 'n.', 'A weight of one hundred pounds avoirdupois; -- called in\n   many parts of the United States a Hundredweight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cental', 'n.', 'Relating to a hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centare', 'n.', 'A measure of area, the hundredth part of an are; one\n   square meter, or about 1/ square yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centaur', 'n.', 'A fabulous being, represented as half man and half horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centaur', 'n.', 'A constellation in the southern heavens between Hydra and\n   the Southern Cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centaurea', 'n.', 'A large genus of composite plants, related to the\n   thistles and including the cornflower or bluebottle (Centaurea Cyanus)\n   and the star thistle (C. Calcitrapa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centaury', 'n.', 'A gentianaceous plant not fully identified. The name is\n   usually given to the Erytheraea Centaurium and the Chlora perfoliata of\n   Europe, but is also extended to the whole genus Sabbatia, and even to\n   the unrelated Centaurea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenarian', 'a.', 'Of or relating to a hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenarian', 'n.', 'A person a hundred years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenary', 'a.', 'Relating to, or consisting of, a hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenary', 'a.', 'Occurring once in every hundred years; centennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenaries', 'pl. ', 'of Centenary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenary', 'n.', 'The aggregate of a hundred single things; specifically,\n   a century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centenary', 'n.', 'A commemoration or celebration of an event which\n   occurred a hundred years before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centennial', 'a.', 'Relating to, or associated with, the commemoration of\n   an event that happened a hundred years before; as, a centennial ode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centennial', 'a.', 'Happening once in a hundred years; as, centennial\n   jubilee; a centennial celebration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centennial', 'a.', 'Lasting or aged a hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centennial', 'n.', 'The celebration of the hundredth anniversary of any\n   event; a centenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centennially', 'adv.', 'Once in a hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'A point equally distant from the extremities of a line,\n   figure, or body, or from all parts of the circumference of a circle;\n   the middle point or place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'The middle or central portion of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'A principal or important point of concentration; the\n   nucleus around which things are gathered or to which they tend; an\n   object of attention, action, or force; as, a center of attaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'The earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'Those members of a legislative assembly (as in France) who\n   support the existing government. They sit in the middle of the\n   legislative chamber, opposite the presiding officer, between the\n   conservatives or monarchists, who sit on the right of the speaker, and\n   the radicals or advanced republicans who occupy the seats on his left,\n   See Right, and Left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'A temporary structure upon which the materials of a vault\n   or arch are supported in position until the work becomes\n   self-supporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'One of the two conical steel pins, in a lathe, etc., upon\n   which the work is held, and about which it revolves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'n.', 'A conical recess, or indentation, in the end of a shaft or\n   other work, to receive the point of a center, on which the work can\n   turn, as in a lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centered', 'imp. & p. p.', 'of Centre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centred', '', 'of Centre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centering', 'p. pr. & vb. n.', 'of Centre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centring', '', 'of Centre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'v. i.', 'Alt. of Centre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centre', 'v. i.', 'To be placed in a center; to be central.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centre', 'v. i.', 'To be collected to a point; to be concentrated; to rest\n   on, or gather about, as a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Center', 'v. t.', 'Alt. of Centre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centre', 'v. t.', 'To place or fix in the center or on a central point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centre', 'v. t.', 'To collect to a point; to concentrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centre', 'v. t.', 'To form a recess or indentation for the reception of a\n   center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centerbit', 'n.', 'Alt. of Centrebit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrebit', 'n.', 'An instrument turning on a center, for boring holes. See\n   Bit, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centerboard', 'n.', 'Alt. of Centreboard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centreboard', 'n.', 'A movable or sliding keel formed of a broad board or\n   slab of wood or metal which may be raised into a water-tight case\n   amidships, when in shallow water, or may be lowered to increase the\n   area of lateral resistance and prevent leeway when the vessel is\n   beating to windward. It is used in vessels of all sizes along the coast\n   of the United States');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centerfire cartridge', '', 'See under Cartridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centering', 'n.', 'Same as Center, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centerpiece', 'n.', 'Alt. of Centrepiece');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrepiece', 'n.', 'An ornament to be placed in the center, as of a table,\n   ceiling, atc.; a central article or figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centesimal', 'a.', 'Hundredth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centesimal', 'n.', 'A hundredth part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centesimation', 'n.', 'The infliction of the death penalty upon one person\n   in every hundred, as in cases of mutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centesm', 'n.', 'Hundredth.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-mi', 'pl. ', 'of Centesimo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centesimo', 'n.', 'A copper coin of Italy and Spain equivalent to a\n   centime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centiare', 'n.', 'See centare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centicipitous', 'a.', 'Hundred-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centifidous', 'a.', 'Divided into a hundred parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centifolious', 'a.', 'Having a hundred leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centigrade', 'a.', 'Consisting of a hundred degrees; graduated into a\n   hundred divisions or equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centigrade', 'a.', 'Of or pertaining to the centigrade thermometer; as, 10¡\n   centigrade (or 10¡ C.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centigram', 'n.', 'Alt. of Centigramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centigramme', 'n.', 'The hundredth part of a gram; a weight equal to .15432\n   of a grain. See Gram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centiliter', 'n.', 'Alt. of Centilitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centilitre', 'n.', 'The hundredth part of a liter; a measure of volume or\n   capacity equal to a little more than six tenths (0.6102) of a cubic\n   inch, or one third (0.338) of a fluid ounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centiloquy', 'n.', 'A work divided into a hundred parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centime', 'n.', 'The hundredth part of a franc; a small French copper coin\n   and money of account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centimeter', 'n.', 'Alt. of Centimetre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centimetre', 'n.', 'The hundredth part of a meter; a measure of length\n   equal to rather more than thirty-nine hundredths (0.3937) of an inch.\n   See Meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centinel', 'n.', 'Sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centinody', 'n.', 'A weed with a stem of many joints (Illecebrum\n   verticillatum); also, the Polygonum aviculare or knotgrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centiped', 'n.', 'A species of the Myriapoda; esp. the large, flattened,\n   venomous kinds of the order Chilopoda, found in tropical climates. they\n   are many-jointed, and have a great number of feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centistere', 'n.', 'The hundredth part of a stere, equal to .353 cubic\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centner', 'n.', 'A weight divisible first into a hundred parts, and then\n   into smaller parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centner', 'n.', 'The commercial hundredweight in several of the continental\n   countries, varying in different places from 100 to about 112 pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centos', 'pl. ', 'of Cento');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cento', 'n.', 'A literary or a musical composition formed by selections\n   from different authors disposed in a new order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centonism', 'n.', 'The composition of a cento; the act or practice of\n   composing a cento or centos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Central', 'a.', 'Relating to the center; situated in or near the center or\n   middle; containing the center; of or pertaining to the parts near the\n   center; equidistant or equally accessible from certain points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Central', 'n.', 'Alt. of Centrale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrale', 'n.', 'The central, or one of the central, bones of the carpus\n   or or tarsus. In the tarsus of man it is represented by the navicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralism', 'n.', 'The state or condition of being central; the\n   combination of several parts into one whole; centralization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralism', 'n.', 'The system by which power is centralized, as in a\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralities', 'pl. ', 'of Centrality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrality', 'n.', 'The state of being central; tendency towards a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralization', 'n.', 'The act or process of centralizing, or the state of\n   being centralized; the act or process of combining or reducing several\n   parts into a whole; as, the centralization of power in the general\n   government; the centralization of commerce in a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralized', 'imp. & p. p.', 'of Centralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralizing', 'p. pr. & vb. n.', 'of Centralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centralize', 'v. t.', 'To draw or bring to a center point; to gather into\n   or about a center; to bring into one system, or under one control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrally', 'adv.', 'In a central manner or situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centre', 'n. & v.', 'See Center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centric', 'a.', 'Alt. of Centrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrical', 'a.', 'Placed in the center or middle; central.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centricity', 'n.', 'The state or quality of being centric; centricalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrifugal', 'a.', 'Tending, or causing, to recede from the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrifugal', 'a.', 'Expanding first at the summit, and later at the base,\n   as a flower cluster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrifugal', 'a.', 'Having the radicle turned toward the sides of the\n   fruit, as some embryos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrifugal', 'n.', 'A centrifugal machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrifugence', 'n.', 'The property or quality of being centrifugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centring', 'n.', 'See Centring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centripetal', 'a.', 'Tending, or causing, to approach the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centripetal', 'a.', 'Expanding first at the base of the inflorescence, and\n   proceeding in order towards the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centripetal', 'a.', 'Having the radicle turned toward the axis of the\n   fruit, as some embryos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centripetal', 'a.', 'Progressing by changes from the exterior of a thing\n   toward its center; as, the centripetal calcification of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centripetence', 'n.', 'Centripetency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centripetency', 'n.', 'Tendency toward the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centriscoid', 'a.', 'Allied to, or resembling, the genus Centriscus, of\n   which the bellows fish is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrobaric', 'a.', 'Relating to the center of gravity, or to the process\n   of finding it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrode', 'n.', 'In two figures having relative motion, one of the two\n   curves which are the loci of the instantaneous center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centroid', 'n.', 'The center of mass, inertia, or gravity of a body or\n   system of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrolecithal', 'a.', 'Having the food yolk placed at the center of the\n   ovum, segmentation being either regular or unequal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrolinead', 'n.', 'An instrument for drawing lines through a point, or\n   lines converging to a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrolineal', 'a.', 'Converging to a center; -- applied to lines drawn so\n   as to meet in a point or center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrosome', 'n.', 'A peculiar rounded body lying near the nucleus of a\n   cell. It is regarded as the dynamic element by means of which the\n   machinery of cell division is organized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrostaltic', 'a.', 'A term applied to the action of nerve force in the\n   spinal center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrums', 'pl. ', 'of Centrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centra', 'pl. ', 'of Centrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centrum', 'n.', 'The body, or axis, of a vertebra. See Vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centry', 'n.', 'See Sentry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centumviri', 'pl. ', 'of Centumvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centumvir', 'n.', 'One of a court of about one hundred judges chosen to try\n   civil suits. Under the empire the court was increased to 180, and met\n   usually in four sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centumviral', 'a.', 'Of or pertaining to the centumviri, or to a centumvir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centumvirate', 'n.', 'The office of a centumvir, or of the centumviri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuple', 'a.', 'Hundredfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuple', 'v. t.', 'To increase a hundredfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuplicated', 'imp. & p. p.', 'of Centuplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuplicating', 'p. pr. & vb. n.', 'of Centuplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuplicate', 'a.', 'To make a hundredfold; to repeat a hundred times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centurial', 'a.', 'Of or pertaining to a century; as, a centurial sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuriate', 'a.', 'Pertaining to, or divided into, centuries or hundreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuriate', 'v. t.', 'To divide into hundreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuriator', 'n.', 'Alt. of Centurist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centurist', 'n.', 'An historian who distinguishes time by centuries, esp.\n   one of those who wrote the \"Magdeburg Centuries.\" See under Century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centurion', 'n.', 'A military officer who commanded a minor division of the\n   Roman army; a captain of a century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Centuries', 'pl. ', 'of Century');
  INSERT INTO words (word, word_type, description) VALUES  (    'Century', 'n.', 'A hundred; as, a century of sonnets; an aggregate of a\n   hundred things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Century', 'n.', 'A period of a hundred years; as, this event took place\n   over two centuries ago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Century', 'n.', 'A division of the Roman people formed according to their\n   property, for the purpose of voting for civil officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Century', 'n.', 'One of sixty companies into which a legion of the army was\n   divided. It was Commanded by a centurion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cepevorous', 'a.', 'Feeding upon onions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalad', 'adv.', 'Forwards; towards the head or anterior extremity of the\n   body; opposed to caudad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalalgia', 'n.', 'Alt. of Cephalalgy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalalgy', 'n.', 'Pain in the head; headache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalalgic', 'a.', 'Relating to, or affected with, headache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalalgic', 'n.', 'A remedy for the headache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalanthium', 'n.', 'Same as Anthodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalaspis', 'n.', 'A genus of fossil ganoid fishes found in the old red\n   sandstone or Devonian formation. The head is large, and protected by a\n   broad shield-shaped helmet prolonged behind into two lateral points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalata', 'n. pl.', 'A large division of Mollusca, including all except\n   the bivalves; -- so called because the head is distinctly developed.\n   See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalate', 'a.', 'Having a head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalic', 'a.', 'Of or pertaining to the head. See the Note under\n   Anterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalic', 'n.', 'A medicine for headache, or other disorder in the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalitis', 'n.', 'Same as Phrenitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalization', 'n.', 'Domination of the head in animal life as expressed\n   in the physical structure; localization of important organs or parts in\n   or near the head, in animal development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalo', '', 'A combining form denoting the head, of the head, connected\n   with the head; as, cephalosome, cephalopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalocercal', 'a.', 'Relating to the long axis of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephaloid', 'a.', 'Shaped like the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalology', 'n.', 'The science which treats of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalomere', 'n.', 'One of the somites (arthromeres) which make up the\n   head of arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalometer', 'n.', 'An instrument measuring the dimensions of the head of\n   a fetus during delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalon', 'n.', 'The head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalophora', 'n. pl.', 'The cephalata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalopod', 'n.', 'Alt. of Cephalopode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalopode', 'n.', 'One of the Cephalopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalopoda', 'n. pl.', 'The highest class of Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalopodic', 'a.', 'Alt. of Cephalopodous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalopodous', 'a.', 'Belonging to, or resembling, the cephalopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephaloptera', 'n.', 'One of the generic names of the gigantic ray (Manta\n   birostris); known as devilfish and sea devil. It is common on the\n   coasts of South Carolina, Florida, and farther south. Some of them grow\n   to enormous size, becoming twenty feet of more across the body, and\n   weighing more than a ton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalosome', 'n.', 'The anterior region or head of insects and other\n   arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalostyle', 'n.', 'The anterior end of the notochord and its bony sheath\n   in the base of cartilaginous crania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalothorax', 'n.', 'The anterior portion of any one of the Arachnida and\n   higher Crustacea, consisting of the united head and thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalotome', 'n.', 'An instrument for cutting into the fetal head, to\n   facilitate delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalotomy', 'n.', 'Dissection or opening of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalotomy', 'n.', 'Craniotomy; -- usually applied to bisection of the\n   fetal head with a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalotribe', 'n.', 'An obstetrical instrument for performing\n   cephalotripsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalotripsy', 'n.', 'The act or operation of crushing the head of a fetus\n   in the womb in order to effect delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalotrocha', 'n.', 'A kind of annelid larva with a circle of cilia\n   around the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cephalous', 'a.', 'Having a head; -- applied chiefly to the Cephalata, a\n   division of mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cepheus', 'n.', 'A northern constellation near the pole. Its head, which is\n   in the Milky Way, is marked by a triangle formed by three stars of the\n   fourth magnitude. See Cassiopeia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceraceous', 'a.', 'Having the texture and color of new wax; like wax; waxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerago', 'n.', 'Beebread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceramic', 'a.', 'Of or pertaining to pottery; relating to the art of making\n   earthenware; as, ceramic products; ceramic ornaments for ceilings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceramics', 'n.', 'The art of making things of baked clay; as pottery,\n   tiles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceramics', 'n.', 'Work formed of clay in whole or in part, and baked; as,\n   vases, urns, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerargyrite', 'n.', 'Native silver chloride, a mineral of a white to pale\n   yellow or gray color, darkening on exposure to the light. It may be cut\n   by a knife, like lead or horn (hence called horn silver).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerasin', 'n.', 'A white amorphous substance, the insoluble part of cherry\n   gum; -- called also meta-arabinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerasin', 'n.', 'A gummy mucilaginous substance; -- called also bassorin,\n   tragacanthin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerasinous', 'a.', 'Pertaining to, or containing, cerasin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerasinous', 'a.', 'Of a cherry color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerastes', 'n.', 'A genus of poisonous African serpents, with a horny scale\n   over each eye; the horned viper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerate', 'n.', 'An unctuous preparation for external application, of a\n   consistence intermediate between that of an ointment and a plaster, so\n   that it can be spread upon cloth without the use of heat, but does not\n   melt when applied to the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerated', 'p. a.', 'Covered with wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratine', 'a.', 'Sophistical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratobranchia', 'n. pl.', 'A group of nudibranchiate Mollusca having on\n   the back papilliform or branched organs serving as gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratobranchial', 'a.', 'Pertaining to the bone, or cartilage, below the\n   epibranchial in a branchial arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratobranchial', 'n.', 'A ceratobranchial bone, or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratodus', 'n.', 'A genus of ganoid fishes, of the order Dipnoi, first\n   known as Mesozoic fossil fishes; but recently two living species have\n   been discovered in Australian rivers. They have lungs so well developed\n   that they can leave the water and breathe in air. In Australia they are\n   called salmon and baramunda. See Dipnoi, and Archipterygium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratohyal', 'a.', 'Pertaining to the bone, or cartilage, below the epihyal\n   in the hyoid arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratohyal', 'n.', 'A ceratohyal bone, or cartilage, which, in man, forms\n   one of the small horns of the hyoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratosaurus', 'n.', 'A carnivorous American Jurassic dinosaur allied to\n   the European Megalosaurus. The animal was nearly twenty feet in length,\n   and the skull bears a bony horn core on the united nasal bones. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceratospongiae', 'n. pl.', 'An order of sponges in which the skeleton\n   consists of horny fibers. It includes all the commercial sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceraunics', 'n.', 'That branch of physics which treats of heat and\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceraunoscope', 'n.', 'An instrument or apparatus employed in the ancient\n   mysteries to imitate thunder and lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerberean', 'a.', 'Of or pertaining to, or resembling, Cerberus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerberus', 'n.', 'A monster, in the shape of a three-headed dog, guarding\n   the entrance into the infernal regions, Hence: Any vigilant custodian\n   or guardian, esp. if surly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerberus', 'n.', 'A genus of East Indian serpents, allied to the pythons;\n   the bokadam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercal', 'a.', 'Of or pertaining to the tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercarle', 'pl. ', 'of Cercaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercaria', 'n.', 'The larval form of a trematode worm having the shape of a\n   tadpole, with its body terminated by a tail-like appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercarian', 'a.', 'Of, like, or pertaining to, the Cercariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercarian', 'n.', 'One of the Cercariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercopod', 'n.', 'One of the jointed antenniform appendages of the\n   posterior somites of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerci', 'pl. ', 'of Cercus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cercus', 'n.', 'See Cercopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cere', 'n.', 'The soft naked sheath at the base of the beak of birds of\n   prey, parrots, and some other birds. See Beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cered', 'imp. & p. p.', 'of Cere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cering', 'p. pr. & vb. n.', 'of Cere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cere', 'v. t.', 'To wax; to cover or close with wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cereal', 'a.', 'Of or pertaining to the grasses which are cultivated for\n   their edible seeds (as wheat, maize, rice, etc.); or to their seeds or\n   grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cereal', 'n.', 'Any grass cultivated for its edible grain, or the grain\n   itself; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerealia', 'n. pl.', 'Public festivals in honor of Ceres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerealia', 'n. pl.', 'The cereals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerealin', 'n.', 'A nitrogenous substance closely resembling diastase,\n   obtained from bran, and possessing the power of converting starch into\n   dextrin, sugar, and lactic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebel', 'n.', 'The cerebellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebellar', 'a.', 'Alt. of Cerebellous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebellous', 'a.', 'Pertaining to the cerebellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebellums', 'pl. ', 'of Cerebellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebella', 'pl. ', 'of Cerebellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebellum', 'n.', 'The large lobe of the hind brain in front of and above\n   the medulla; the little brain. It controls combined muscular action.\n   See Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebral', 'a.', 'Of or pertaining to the cerebrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebral', 'n.', 'One of a class of lingual consonants in the East Indian\n   languages. See Lingual, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebralism', 'n.', 'The doctrine or theory that psychical phenomena are\n   functions or products of the brain only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebralist', 'n.', 'One who accepts cerebralism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrate', 'v. i.', 'To exhibit mental activity; to have the brain in\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebration', 'n.', 'Action of the brain, whether conscious or unconscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebric', 'a.', 'Of, pertaining to, or derived from, the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebricity', 'n.', 'Brain power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebriform', 'a.', 'Like the brain in form or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrifugal', 'a.', 'Applied to those nerve fibers which go from the brain\n   to the spinal cord, and so transfer cerebral impulses (centrifugal\n   impressions) outwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrin', 'n.', 'A nonphosphorized, nitrogenous substance, obtained from\n   brain and nerve tissue by extraction with boiling alcohol. It is\n   uncertain whether it exists as such in nerve tissue, or is a product of\n   the decomposition of some more complex substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebripetal', 'a.', 'Applied to those nerve fibers which go from the\n   spinal cord to the brain and so transfer sensations (centripetal\n   impressions) from the exterior inwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebritis', 'n.', 'Inflammation of the cerebrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebroid', 'a.', 'Resembling, or analogous to, the cerebrum or brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrology', 'n.', 'The science which treats of the cerebrum or brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebropathy', 'n.', 'A hypochondriacal condition verging upon insanity,\n   occurring in those whose brains have been unduly taxed; -- called also\n   brain fag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebroscopy', 'n.', 'Examination of the brain for the diagnosis of\n   disease; esp., the act or process of diagnosticating the condition of\n   the brain by examination of the interior of the eye (as with an\n   ophthalmoscope).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrose', 'n.', 'A sugarlike body obtained by the decomposition of the\n   nitrogenous non-phosphorized principles of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebro-spinal', 'a.', 'Of or pertaining to the central nervous system\n   consisting of the brain and spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrums', 'pl. ', 'of Cerebrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebra', 'pl. ', 'of Cerebrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerebrum', 'n.', 'The anterior, and in man the larger, division of the\n   brain; the seat of the reasoning faculties and the will. See Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerecloth', 'n.', 'A cloth smeared with melted wax, or with some gummy or\n   glutinous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerement', 'n.', 'A cerecloth used for the special purpose of enveloping a\n   dead body when embalmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerement', 'n.', 'Any shroud or wrapping for the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonial', 'a.', 'Relating to ceremony, or external rite; ritual;\n   according to the forms of established rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonial', 'a.', 'Observant of forms; ceremonious. [In this sense\n   ceremonious is now preferred.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonial', 'n.', 'A system of rules and ceremonies, enjoined by law, or\n   established by custom, in religious worship, social intercourse, or the\n   courts of princes; outward form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonial', 'n.', 'The order for rites and forms in the Roman Catholic\n   church, or the book containing the rules prescribed to be observed on\n   solemn occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonialism', 'n.', 'Adherence to external rites; fondness for ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonially', 'adv.', 'According to rites and ceremonies; as, a person\n   ceremonially unclean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonialness', 'n.', 'Quality of being ceremonial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonious', 'a.', 'Consisting of outward forms and rites; ceremonial. [In\n   this sense ceremonial is now preferred.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonious', 'a.', 'According to prescribed or customary rules and forms;\n   devoted to forms and ceremonies; formally respectful; punctilious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremoniously', 'adv.', 'In a ceremonious way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremoniousness', 'n.', 'The quality, or practice, of being ceremonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremonies', 'pl. ', 'of Ceremony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremony', 'n.', 'Ar act or series of acts, often of a symbolical\n   character, prescribed by law, custom, or authority, in the conduct of\n   important matters, as in the performance of religious duties, the\n   transaction of affairs of state, and the celebration of notable events;\n   as, the ceremony of crowning a sovereign; the ceremonies observed in\n   consecrating a church; marriage and baptismal ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremony', 'n.', 'Behavior regulated by strict etiquette; a formal method\n   of performing acts of civility; forms of civility prescribed by custom\n   or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremony', 'n.', 'A ceremonial symbols; an emblem, as a crown, scepter,\n   garland, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceremony', 'n.', 'A sign or prodigy; a portent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cereous', 'a.', 'Waxen; like wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceres', 'n.', 'The daughter of Saturn and Ops or Rhea, the goddess of corn\n   and tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceres', 'n.', 'The first discovered asteroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceresin', 'n.', 'A white wax, made by bleaching and purifying ozocerite,\n   and used as a substitute for beeswax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cereus', 'n.', 'A genus of plants of the Cactus family. They are natives of\n   America, from California to Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerial', 'a.', 'Same as Cerrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceriferous', 'a.', 'Producing wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerin', 'n.', 'A waxy substance extracted by alcohol or ether from cork;\n   sometimes applied also to the portion of beeswax which is soluble in\n   alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerin', 'n.', 'A variety of the mineral allanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerinthian', 'n.', 'One of an ancient religious sect, so called from\n   Cerinthus, a Jew, who attempted to unite the doctrines of Christ with\n   the opinions of the Jews and Gnostics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceriph', 'n.', 'One of the fine lines of a letter, esp. one of the fine\n   cross strokes at the top and bottom of letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerise', 'a.', 'Cherry-colored; a light bright red; -- applied to textile\n   fabrics, especially silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerite', 'n.', 'A gastropod shell belonging to the family Cerithiidae; --\n   so called from its hornlike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerite', 'n.', 'A mineral of a brownish of cherry-red color, commonly\n   massive. It is a hydrous silicate of cerium and allied metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerium', 'n.', 'A rare metallic element, occurring in the minerals cerite,\n   allanite, monazite, etc. Symbol Ce. Atomic weight 141.5. It resembles\n   iron in color and luster, but is soft, and both malleable and ductile.\n   It tarnishes readily in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cernuous', 'a.', 'Inclining or nodding downward; pendulous; drooping; --\n   said of a bud, flower, fruit, or the capsule of a moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cero', 'n.', 'A large and valuable fish of the Mackerel family, of the\n   genus Scomberomorus. Two species are found in the West Indies and less\n   commonly on the Atlantic coast of the United States, -- the common cero\n   (Scomberomorus caballa); called also kingfish, and spotted, or king,\n   cero (S. regalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerograph', 'n.', 'A writing on wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerographic', 'a.', 'Alt. of Cerographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerographical', 'a.', 'Of or pertaining to cerography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerographist', 'n.', 'One who practices cerography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerography', 'n.', 'The art of making characters or designs in, or with,\n   wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerography', 'n.', 'A method of making stereotype plates from inscribed\n   sheets of wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerolite', 'n.', 'A hydrous silicate of magnesium, allied to serpentine,\n   occurring in waxlike masses of a yellow or greenish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroma', 'n.', 'The unguent (a composition of oil and wax) with which\n   wrestlers were anointed among the ancient Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroma', 'n.', 'That part of the baths and gymnasia in which bathers and\n   wrestlers anointed themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroma', 'n.', 'The cere of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceromancy', 'n.', 'Divination by dropping melted wax in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroon', 'n.', 'A bale or package. covered with hide, or with wood bound\n   with hide; as, a ceroon of indigo, cochineal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroplastic', 'a.', 'Relating to the art of modeling in wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroplastic', 'a.', 'Modeled in wax; as, a ceroplastic figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroplastics', 'n.', 'Alt. of Ceroplasty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceroplasty', 'n.', 'The art of modeling in wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerosin', 'n.', 'A waxy substance obtained from the bark of the sugar cane,\n   and crystallizing in delicate white laminae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerote', 'n.', 'See Cerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerotene', 'n.', 'A white waxy solid obtained from Chinese wax, and by the\n   distillation of cerotin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerotic', 'a.', 'Pertaining to, or derived from, beeswax or Chinese wax;\n   as, cerotic acid or alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerotin', 'n.', 'A white crystalline substance, C27H55.OH, obtained from\n   Chinese wax, and regarded as an alcohol of the marsh gas series; --\n   called also cerotic alcohol, ceryl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerrial', 'a.', 'Of or pertaining to the cerris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerris', 'n.', 'A species of oak (Quercus cerris) native in the Orient and\n   southern Europe; -- called also bitter oak and Turkey oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Assured in mind; having no doubts; free from suspicions\n   concerning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Determined; resolved; -- used with an infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Not to be doubted or denied; established as a fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Actually existing; sure to happen; inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Unfailing; infallible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Fixed or stated; regular; determinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'a.', 'Not specifically named; indeterminate; indefinite; one or\n   some; -- sometimes used independenty as a noun, and meaning certain\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'n.', 'Certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'n.', 'A certain number or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certain', 'adv.', 'Certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certainly', 'adv.', 'Without doubt or question; unquestionably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certainness', 'n.', 'Certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certainties', 'pl. ', 'of Certainty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certainty', 'n.', 'The quality, state, or condition, of being certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certainty', 'n.', 'A fact or truth unquestionable established.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certainty', 'n.', 'Clearness; freedom from ambiguity; lucidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certes', 'adv.', 'Certainly; in truth; verily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certificate', 'n.', 'A written testimony to the truth of any fact; as,\n   certificate of good behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certificate', 'n.', 'A written declaration legally authenticated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certificated', 'imp. & p. p.', 'of Certificate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certificating', 'p. pr. & vb. n.', 'of Certificate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certificate', 'v. t.', 'To verify or vouch for by certificate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certificate', 'v. t.', 'To furnish with a certificate; as, to certificate\n   the captain of a vessel; a certificated teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certification', 'n.', 'The act of certifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certifier', 'n.', 'One who certifies or assures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certified', 'imp. & p. p.', 'of Certify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certifying', 'p. pr. & vb. n.', 'of Certify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certify', 'v. t.', 'To give cetain information to; to assure; to make\n   certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certify', 'v. t.', 'To give certain information of; to make certain, as a\n   fact; to verify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certify', 'v. t.', 'To testify to in writing; to make a declaration\n   concerning, in writing, under hand, or hand and seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certiorari', 'n.', 'A writ issuing out of chancery, or a superior court, to\n   call up the records of a inferior court, or remove a cause there\n   depending, in order that the party may have more sure and speedy\n   justice, or that errors and irregularities may be corrected. It is\n   obtained upon complaint of a party that he has not received justice, or\n   can not have an impartial trial in the inferior court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Certitude', 'n.', 'Freedom from doubt; assurance; certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerule', 'a.', 'Blue; cerulean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerulean', 'a.', 'Sky-colored; blue; azure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceruleous', 'a.', 'Cerulean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerulific', 'a.', 'Producing a blue or sky color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerumen', 'n.', 'The yellow, waxlike secretion from the glands of the\n   external ear; the earwax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceruminous', 'a.', 'Pertaining to, or secreting, cerumen; as, the\n   ceruminous glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceruse', 'n.', 'White lead, used as a pigment. See White lead, under White.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceruse', 'n.', 'A cosmetic containing white lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceruse', 'n.', 'The native carbonate of lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerused', 'a.', 'Washed with a preparation of white lead; as, cerused face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerusite', 'n.', 'Alt. of Cerussite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cerussite', 'n.', 'Native lead carbonate; a mineral occurring in colorless,\n   white, or yellowish transparent crystals, with an adamantine, also\n   massive and compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervantite', 'n.', 'See under Antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervelat', 'n.', 'An ancient wind instrument, resembling the bassoon in\n   tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervical', 'a.', 'Of or pertaining to the neck; as, the cervical vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervicide', 'n.', 'The act of killing deer; deer-slaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervine', 'a.', 'Of or pertaining to the deer, or to the family Cervidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervixes', 'pl. ', 'of Cervix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervices', 'pl. ', 'of Cervix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervix', 'n.', 'The neck; also, the necklike portion of any part, as of the\n   womb. See Illust. of Bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cervus', 'n.', 'A genus of ruminants, including the red deer and other\n   allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceryl', 'n.', 'A radical, C27H55 supposed to exist in several compounds\n   obtained from Chinese wax, beeswax, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesarean', 'a.', 'Alt. of Cesarian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesarian', 'a.', 'Same as Caesarean, Caesarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesarism', 'n.', 'See Caesarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cespitine', 'n.', 'An oil obtained by distillation of peat, and containing\n   various members of the pyridine series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cespititious', 'a.', 'Same as Cespitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cespitose', 'a.', 'Having the form a piece of turf, i. e., many stems from\n   one rootstock or from many entangled rootstocks or roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cespitous', 'a.', 'Pertaining to, consisting, of resembling, turf; turfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cess', 'n.', 'A rate or tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cess', 'n.', 'Bound; measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessed', 'imp. & p. p.', 'of Cess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessing', 'p. pr. & vb. n.', 'of Cess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cess', 'v. t.', 'To rate; to tax; to assess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cess', 'v. i.', 'To cease; to neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessant', 'a.', 'Inactive; dormant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessation', 'n.', 'A ceasing or discontinuance, as of action, whether\n   temporary or final; a stop; as, a cessation of the war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessavit', 'n.', 'A writ given by statute to recover lands when the tenant\n   has for two years failed to perform the conditions of his tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesser', 'v. i.', 'a neglect of a tenant to perform services, or make\n   payment, for two years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessible', 'a.', 'Giving way; yielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cession', 'n.', 'A yielding to physical force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cession', 'n.', 'Concession; compliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cession', 'n.', 'A yielding, or surrender, as of property or rights, to\n   another person; the act of ceding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cession', 'n.', 'The giving up or vacating a benefice by accepting another\n   without a proper dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cession', 'n.', 'The voluntary surrender of a persons effects to his\n   creditors to avoid imprisonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessionary', 'a.', 'Having surrendered the effects; as, a cessionary\n   bankrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessment', 'v. t.', 'An assessment or tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessor', 'v. i.', 'One who neglects, for two years, to perform the service\n   by which he holds lands, so that he incurs the danger of the writ of\n   cessavit. See Cessavit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cessor', 'v. t.', 'An assessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesspipe', 'n.', 'A pipe for carrying off waste water, etc., from a sink or\n   cesspool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesspool', 'n.', 'A cistern in the course, or the termination, of a drain,\n   to collect sedimentary or superfluous matter; a privy vault; any\n   receptacle of filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cest', 'n.', 'A womans girdle; a cestus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestode', 'a.', 'Of or pertaining to the Cestoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestode', 'n.', 'One of the Cestoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestoid', 'a.', 'Of or pertaining to the Cestoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestoid', 'n.', 'One of the Cestoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestoidea', 'n. pl.', 'A class of parasitic worms (Platelminthes) of which\n   the tapeworms are the most common examples. The body is flattened, and\n   usually but not always long, and composed of numerous joints or\n   segments, each of which may contain a complete set of male and female\n   reproductive organs. They have neither mouth nor intestine. See\n   Tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestoldean', 'n.', 'One of the Cestoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestraciont', 'n.', 'A shark of the genus Cestracion, and of related\n   genera. The posterior teeth form a pavement of bony plates for crushing\n   shellfish. Most of the species are extinct. The Port Jackson shark and\n   a similar one found in California are living examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestraciont', 'a.', 'Pertaining to, or characteristic of, the genus\n   Cestracion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestus', 'n.', 'A girdle; particularly that of Aphrodite (or Venus) which\n   gave the wearer the power of exciting love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestus', 'n.', 'A genus of Ctenophora. The typical species (Cestus Veneris)\n   is remarkable for its brilliant iridescent colors, and its long,\n   girdlelike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestus', 'n.', 'A covering for the hands of boxers, made of leather bands,\n   and often loaded with lead or iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestuy', 'pron.', 'Alt. of Cestui');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cestui', 'pron.', 'He; the one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesura', 'n.', 'See Caesura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cesural', 'a.', 'See Caesural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetacea', 'n. pl.', 'An order of marine mammals, including the whales. Like\n   ordinary mammals they breathe by means of lungs, and bring forth living\n   young which they suckle for some time. The anterior limbs are changed\n   to paddles; the tail flukes are horizontal. There are two living\n   suborders:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetacean', 'n.', 'One of the Cetacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetaceous', 'a.', 'Of or pertaining to the Cetacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cete', 'n.', 'One of the Cetacea, or collectively, the Cetacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetene', 'n.', 'An oily hydrocarbon, C16H32, of the ethylene series,\n   obtained from spermaceti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceterach', 'n.', 'A species of fern with fronds (Asplenium Ceterach).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetewale', 'n.', 'Same as Zedoary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetic', 'a.', 'Of or pertaining to a whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetin', 'n.', 'A white, waxy substance, forming the essential part of\n   spermaceti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetological', 'a.', 'Of or pertaining to cetology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetologist', 'a.', 'One versed in cetology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetology', 'n.', 'The description or natural history of cetaceous animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetraric', 'a.', 'Pertaining to, or derived from, the lichen, Iceland moss\n   (Cetaria Islandica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetrarin', 'n.', 'A white substance extracted from the lichen, Iceland moss\n   (Cetraria Islandica). It consists of several ingredients, among which\n   is cetraric acid, a white, crystalline, bitter substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetyl', 'n.', 'A radical, C16H33, not yet isolated, but supposed to exist\n   in a series of compounds homologous with the ethyl compounds, and\n   derived from spermaceti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cetylic', 'a.', 'Of, pertaining to, or derived from, spermaceti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceylanite', 'n.', 'A dingy blue, or grayish black, variety of spinel. It is\n   also called pleonaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceylonese', 'a.', 'Of or pertaining to Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ceylonese', 'n. sing. & pl.', 'A native or natives of Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'C. G. S.', '', 'An abbreviation for Centimeter, Gram, Second. -- applied to\n   a system of units much employed in physical science, based upon the\n   centimeter as the unit of length, the gram as the unit of weight or\n   mass, and the second as the unit of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chab', 'n.', 'The red-bellied wood pecker (Melanerpes Carolinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chabasite', 'n.', 'Alt. of Cabazite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cabazite', 'n.', 'A mineral occuring in glassy rhombohedral crystals,\n   varying, in color from white to yellow or red. It is essentially a\n   hydrous silicate of alumina and lime. Called also chabasie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chablis', 'n.', 'A white wine made near Chablis, a town in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chablis', 'n.', 'a white wine resembling Chablis{1}, but made elsewhere, as\n   in California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chabouk', 'n.', 'Alt. of Chabuk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chabuk', 'n.', 'A long whip, such as is used in the East in the infliction\n   of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chace', 'n.', 'See 3d Chase, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chace', 'v. t.', 'To pursue. See Chase v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chachalaca', 'n.', 'The Texan guan (Ortalis vetula).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chak', 'v. i.', 'To toss up the head frequently, as a horse to avoid the\n   restraint of the bridle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chacma', 'n.', 'A large species of African baboon (Cynocephalus porcarius);\n   -- called also ursine baboon. [See Illust. of Baboon.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaconne', 'n.', 'An old Spanish dance in moderate three-four measure, like\n   the Passacaglia, which is slower. Both are used by classical composers\n   as themes for variations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chad', 'n.', 'See Shad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetetes', 'n.', 'A genus of fossil corals, common in the lower Silurian\n   limestones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetiferous', 'a.', 'Bearing setae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetodont', 'n.', 'A marine fish of the family Chaetodontidae. The\n   chaetodonts have broad, compressed bodies, and usually bright colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetodont', 'a.', 'Of or pertaining to the Chaetodonts or the family\n   Chaetodontidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetognath', 'a.', 'Of or pertaining to the Chaetognatha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetognatha', 'n. pl.', 'An order of free-swimming marine worms, of which\n   the genus Sagitta is the type. They have groups of curved spines on\n   each side of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetopod', 'a.', 'Pertaining to the Chaetopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetopod', 'n.', 'One of the Chaetopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetopoda', 'n. pl.', 'A very extensive order of Annelida, characterized\n   by the presence of lateral setae, or spines, on most or all of the\n   segments. They are divided into two principal groups: Oligochaeta,\n   including the earthworms and allied forms, and Polychaeta, including\n   most of the marine species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaetotaxy', 'n.', 'The arrangement of bristles on an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafed', 'imp. & p. p.', 'of Chafe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafing', 'p pr. & vb. n.', 'of Chafe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'v. t.', 'To excite heat in by friction; to rub in order to\n   stimulate and make warm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'v. t.', 'To excite passion or anger in; to fret; to irritate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'v. t.', 'To fret and wear by rubbing; as, to chafe a cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'v. i.', 'To rub; to come together so as to wear by rubbing; to\n   wear by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'v. i.', 'To be worn by rubbing; as, a cable chafes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'v. i.', 'To have a feeling of vexation; to be vexed; to fret; to\n   be irritated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'n.', 'Heat excited by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'n.', 'Injury or wear caused by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafe', 'n.', 'Vexation; irritation of mind; rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafer', 'n.', 'One who chafes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafer', 'n.', 'A vessel for heating water; -- hence, a dish or pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafer', 'n.', 'A kind of beetle; the cockchafer. The name is also applied\n   to other species; as, the rose chafer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafery', 'v. t.', 'An open furnace or forge, in which blooms are heated\n   before being wrought into bars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafewax', 'n.', 'Alt. of Chaffwax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffwax', 'n.', 'Formerly a chancery officer who fitted wax for sealing\n   writs and other documents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafeweed', 'n.', 'The cudweed (Gnaphalium); used to prevent or cure\n   chafing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'n.', 'The glumes or husks of grains and grasses separated from the\n   seed by threshing and winnowing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'n.', 'Anything of a comparatively light and worthless character;\n   the refuse part of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'n.', 'Straw or hay cut up fine for the food of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'n.', 'Light jesting talk; banter; raillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'n.', 'The scales or bracts on the receptacle, which subtend each\n   flower in the heads of many Compositae, as the sunflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffed', 'imp. & p. p.', 'of Chaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffing', 'p. pr. & vb. n.', 'of Chaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'v. i.', 'To use light, idle language by way of fun or ridicule; to\n   banter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaff', 'v. t.', 'To make fun of; to turn into ridicule by addressing in\n   ironical or bantering language; to quiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffer', 'n.', 'One who chaffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffer', 'n.', 'Bargaining; merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffered', 'imp. & p. p.', 'of Chaffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffering', 'p. pr. & vb. n.', 'of Chaffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffer', 'n.', 'To treat or dispute about a purchase; to bargain; to\n   haggle or higgle; to negotiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffer', 'n.', 'To talk much and idly; to chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffer', 'v. t.', 'To buy or sell; to trade in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffer', 'v. t.', 'To exchange; to bandy, as words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafferer', 'n.', 'One who chaffers; a bargainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffern', 'v. t.', 'A vessel for heating water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffery', 'n.', 'Traffic; bargaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffinch', 'n.', 'A bird of Europe (Fringilla coelebs); having a variety\n   of very sweet songs, and highly valued as a cage bird; -- called also\n   copper finch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffing', 'n.', 'The use of light, frivolous language by way of fun or\n   ridicule; raillery; banter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffless', 'a.', 'Without chaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffy', 'a.', 'Abounding in, or resembling, chaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffy', 'a.', 'Light or worthless as chaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffy', 'a.', 'Resembling chaff; composed of light dry scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaffy', 'a.', 'Bearing or covered with dry scales, as the under surface of\n   certain ferns, or the disk of some composite flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chafing', 'v. t.', 'The act of rubbing, or wearing by friction; making by\n   rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chagreen', 'n.', 'See Shagreen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chagrin', 'n.', 'Vexation; mortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chagrined', 'imp. & p. p.', 'of Chagrin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargrining', 'p. pr. & vb. n.', 'of Chagrin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chagrin', 'n.', 'To excite ill-humor in; to vex; to mortify; as, he was not\n   a little chagrined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chagrin', 'v. i.', 'To be vexed or annoyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chagrin', 'a.', 'Chagrined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'n.', 'A series of links or rings, usually of metal, connected, or\n   fitted into one another, used for various purposes, as of support, of\n   restraint, of ornament, of the exertion and transmission of mechanical\n   power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'n.', 'That which confines, fetters, or secures, as a chain; a\n   bond; as, the chains of habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'n.', 'A series of things linked together; or a series of things\n   connected and following each other in succession; as, a chain of\n   mountains; a chain of events or ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'n.', 'An instrument which consists of links and is used in\n   measuring land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'n.', 'Iron links bolted to the side of a vessel to bold the\n   dead-eyes connected with the shrouds; also, the channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'n.', 'The warp threads of a web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chained', 'imp. p. p.', 'of Chain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaining', 'p. pr. & vb. n.', 'of Chain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'v. t.', 'To fasten, bind, or connect with a chain; to fasten or\n   bind securely, as with a chain; as, to chain a bulldog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'v. t.', 'To keep in slavery; to enslave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'v. t.', 'To unite closely and strongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'v. t.', 'To measure with the chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain', 'v. t.', 'To protect by drawing a chain across, as a harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chainless', 'a.', 'Having no chain; not restrained or fettered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chainlet', 'n.', 'A small chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain pump', '', 'A pump consisting of an endless chain, running over a\n   drum or wheel by which it is moved, and dipping below the water to be\n   raised. The chain has at intervals disks or lifts which fit the tube\n   through which the ascending part passes and carry the water to the\n   point of discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain stitch', '', 'An ornamental stitch like the links of a chain; -- used\n   in crocheting, sewing, and embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain stitch', '', 'A stitch in which the looping of the thread or threads\n   forms a chain on the under side of the work; the loop stitch, as\n   distinguished from the lock stitch. See Stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain wheel', '', 'A chain pulley, or sprocket wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chain wheel', '', 'An inversion of the chain pump, by which it becomes a\n   motor driven by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chainwork', 'n.', 'Work looped or linked after the manner of a chain; chain\n   stitch work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'n.', 'A movable single seat with a back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'n.', 'An official seat, as of a chief magistrate or a judge, but\n   esp. that of a professor; hence, the office itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'n.', 'The presiding officer of an assembly; a chairman; as, to\n   address the chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'n.', 'A vehicle for one person; either a sedan borne upon poles,\n   or two-wheeled carriage, drawn by one horse; a gig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'n.', 'An iron block used on railways to support the rails and\n   secure them to the sleepers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaired', 'imp. & p. pr.', 'of Chair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chairing', 'p. pr. & vb. n.', 'of Chair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'v. t.', 'To place in a chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chair', 'v. t.', 'To carry publicly in a chair in triumph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chairmen', 'pl. ', 'of Chairman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chairman', 'n.', 'The presiding officer of a committee, or of a public or\n   private meeting, or of any organized body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chairman', 'n.', 'One whose business it is to cary a chair or sedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chairmanship', 'n.', 'The office of a chairman of a meeting or organized\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaise', 'n.', 'A two-wheeled carriage for two persons, with a calash top,\n   and the body hung on leather straps, or thorough-braces. It is usually\n   drawn by one horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaise', 'n.', 'a carriage in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaja', 'n.', 'The crested screamer of Brazil (Palamedea, / Chauna,\n   chavaria); so called in imitation of its notes; -- called also chauna,\n   and faithful kamichi. It is often domesticated and is useful in\n   guarding other poultry. See Kamichi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalazas', 'pl. ', 'of Chalaza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalazae', 'pl. ', 'of Chalaza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalaza', 'n.', 'The place on an ovule, or seed, where its outer coats\n   cohere with each other and the nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalaza', 'n.', 'A spiral band of thickened albuminous substance which\n   exists in the white of the birds egg, and serves to maintain the yolk\n   in its position; the treadle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalazal', 'a.', 'Of or pertaining to the chalaza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalaze', 'n.', 'Same as Chalaza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalaziferous', 'a.', 'Having or bearing chalazas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalazion', 'n.', 'A small circumscribed tumor of the eyelid caused by\n   retention of secretion, and by inflammation of the Melbomian glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcanthite', 'n.', 'Native blue vitriol. See Blue vitriol, under Blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcedonic', 'a.', 'Of or pertaining to chalcedony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcedonies', 'pl. ', 'of Chalcedony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcedony', 'n.', 'A cryptocrystalline, translucent variety of quartz,\n   having usually a whitish color, and a luster nearly like wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalchihuitl', 'n.', 'The Mexican name for turquoise. See Turquoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcid fly', '', 'One of a numerous family of hymenopterous insects\n   (Chalcididae. Many are gallflies, others are parasitic on insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcidian', 'n.', 'One of a tropical family of snakelike lizards\n   (Chalcidae); having four small or rudimentary legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcocite', 'n.', 'Native copper sulphide, called also copper glance, and\n   vitreous copper; a mineral of a black color and metallic luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcographer', 'n.', 'Alt. of Chalcographist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcographist', 'n.', 'An engraver on copper or brass; hence, an engraver\n   of copper plates for printing upon paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcography', 'n.', 'The act or art of engraving on copper or brass,\n   especially of engraving for printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalcopyrite', 'n.', 'Copper pyrites, or yellow copper ore; a common ore of\n   copper, containing copper, iron, and sulphur. It occurs massive and in\n   tetragonal crystals of a bright brass yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldaic', 'a.', 'Of or pertaining to Chaldea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldaic', 'n.', 'The language or dialect of the Chaldeans; Chaldee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldaism', 'n.', 'An idiom or peculiarity in the Chaldee dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldean', 'a.', 'Of or pertaining to Chaldea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldean', 'n.', 'A native or inhabitant of Chaldea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldean', 'n.', 'A learned man, esp. an astrologer; -- so called among the\n   Eastern nations, because astrology and the kindred arts were much\n   cultivated by the Chaldeans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldean', 'n.', 'Nestorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldee', 'a.', 'Of or pertaining to Chaldea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldee', 'n.', 'The language or dialect of the Chaldeans; eastern Aramaic,\n   or the Aramaic used in Chaldea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldrich', 'n.', 'Alt. of Chalder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalder', 'n.', 'A kind of bird; the oyster catcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaldron', 'n.', 'An English dry measure, being, at London, 36 bushels\n   heaped up, or its equivalent weight, and more than twice as much at\n   Newcastle. Now used exclusively for coal and coke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalet', 'n.', 'A herdsmans hut in the mountains of Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalet', 'n.', 'A summer cottage or country house in the Swiss mountains;\n   any country house built in the style of the Swiss cottages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalice', 'n.', 'A cup or bowl; especially, the cup used in the sacrament\n   of the Lords Supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaliced', 'a.', 'Having a calyx or cup; cup-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalk', 'n.', 'A soft, earthy substance, of a white, grayish, or yellowish\n   white color, consisting of calcium carbonate, and having the same\n   composition as common limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalk', 'n.', 'Finely prepared chalk, used as a drawing implement; also, by\n   extension, a compound, as of clay and black lead, or the like, used in\n   the same manner. See Crayon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalked', 'imp. & p. p.', 'of Chalk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalking', 'p. pr. & vb. n.', 'of Chalk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalk', 'v. t.', 'To rub or mark with chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalk', 'v. t.', 'To manure with chalk, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalk', 'v. t.', 'To make white, as with chalk; to make pale; to bleach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalkcutter', 'n.', 'A man who digs chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalkiness', 'n.', 'The state of being chalky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalkstone', 'n.', 'A mass of chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalkstone', 'n.', 'A chalklike concretion, consisting mainly of urate of\n   sodium, found in and about the small joints, in the external ear, and\n   in other situations, in those affected with gout; a tophus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalky', 'a.', 'Consisting of, or resembling, chalk; containing chalk; as,\n   a chalky cliff; a chalky taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'An invitation to engage in a contest or controversy of\n   any kind; a defiance; specifically, a summons to fight a duel; also,\n   the letter or message conveying the summons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'The act of a sentry in halting any one who appears at\n   his post, and demanding the countersign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'A claim or demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'The opening and crying of hounds at first finding the\n   scent of their game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'An exception to a juror or to a member of a court\n   martial, coupled with a demand that he should be held incompetent to\n   act; the claim of a party that a certain person or persons shall not\n   sit in trial upon him or his cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'An exception to a person as not legally qualified to\n   vote. The challenge must be made when the ballot is offered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenged', 'imp. & p. p.', 'of Challenge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenging', 'p. pr. & vb. n.', 'of Challenge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To call to a contest of any kind; to call to answer; to\n   defy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To call, invite, or summon to answer for an offense by\n   personal combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To claim as due; to demand as a right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To censure; to blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To question or demand the countersign from (one who\n   attempts to pass the lines); as, the sentinel challenged us, with \"Who\n   comes there?\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To take exception to; question; as, to challenge the\n   accuracy of a statement or of a quotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To object to or take exception to, as to a juror, or\n   member of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'n.', 'To object to the reception of the vote of, as on the\n   ground that the person in not qualified as a voter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenge', 'v. i.', 'To assert a right; to claim a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challengeable', 'a.', 'That may be challenged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challenger', 'n.', 'One who challenges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Challis', 'n.', 'A soft and delicate woolen, or woolen and silk, fabric,\n   for ladies dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalon', 'n.', 'A bed blanket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalybean', 'a.', 'Of or pertaining to the Chalybes, an ancient people of\n   Pontus in Asia Minor, celebrated for working in iron and steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalybean', 'a.', 'Of superior quality and temper; -- applied to steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalybeate', 'a.', 'Impregnated with salts of iron; having a taste like\n   iron; as, chalybeate springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalybeate', 'n.', 'Any water, liquid, or medicine, into which iron enters\n   as an ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalybeous', 'a.', 'Steel blue; of the color of tempered steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chalybite', 'n.', 'Native iron carbonate; -- usually called siderite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cham', 'v. t.', 'To chew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cham', 'n.', 'The sovereign prince of Tartary; -- now usually written khan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamade', 'n.', 'A signal made for a parley by beat of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamal', 'n.', 'The Angora goat. See Angora goat, under Angora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A retired room, esp. an upper room used for sleeping; a\n   bedroom; as, the house had four chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'Apartments in a lodging house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A hall, as where a king gives audience, or a deliberative\n   body or assembly meets; as, presence chamber; senate chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A legislative or judicial body; an assembly; a society or\n   association; as, the Chamber of Deputies; the Chamber of Commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A compartment or cell; an inclosed space or cavity; as,\n   the chamber of a canal lock; the chamber of a furnace; the chamber of\n   the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A room or rooms where a lawyer transacts business; a room\n   or rooms where a judge transacts such official business as may be done\n   out of court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A chamber pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'That part of the bore of a piece of ordnance which holds\n   the charge, esp. when of different diameter from the rest of the bore;\n   -- formerly, in guns, made smaller than the bore, but now larger, esp.\n   in breech-loading guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A cavity in a mine, usually of a cubical form, to contain\n   the powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'n.', 'A short piece of ordnance or cannon, which stood on its\n   breech, without any carriage, formerly used chiefly for rejoicings and\n   theatrical cannonades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambered', 'imp. & p. p.', 'of Chamber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambering', 'p. pr. & vb. n.', 'of Chamber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'v. i.', 'To reside in or occupy a chamber or chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'v. i.', 'To be lascivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'v. t.', 'To shut up, as in a chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamber', 'v. t.', 'To furnish with a chamber; as, to chamber a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambered', 'a.', 'Having a chamber or chambers; as, a chambered shell; a\n   chambered gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberer', 'n.', 'One who attends in a chamber; a chambermaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberer', 'n.', 'A civilian; a carpetmonger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambering', 'n.', 'Lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberlain', 'n.', 'An officer or servant who has charge of a chamber or\n   chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberlain', 'n.', 'An upper servant of an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberlain', 'n.', 'An officer having the direction and management of the\n   private chambers of a nobleman or monarch; hence, in Europe, one of the\n   high officers of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberlain', 'n.', 'A treasurer or receiver of public money; as, the\n   chamberlain of London, of North Wales, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamberlainship', 'n.', 'Office of a chamberlain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambermaid', 'n.', 'A maidservant who has the care of chambers, making the\n   beds, sweeping, cleaning the rooms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambermaid', 'n.', 'A ladys maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambertin', 'n.', 'A red wine from Chambertin near Dijon, in Burgundy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chambrel', 'n.', 'Same as Gambrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chameck', 'n.', 'A kind of spider monkey (Ateles chameck); having the\n   thumbs rudimentary and without a nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chameleon', 'n.', 'A lizardlike reptile of the genus Chamaeleo, of several\n   species, found in Africa, Asia, and Europe. The skin is covered with\n   fine granulations; the tail is prehensile, and the body is much\n   compressed laterally, giving it a high back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chameleonize', 'v. t.', 'To change into various colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfer', 'n.', 'The surface formed by cutting away the arris, or angle,\n   formed by two faces of a piece of timber, stone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfered', 'imp. & p. p.', 'of Chamfer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfering', 'p. pr. & vb. n.', 'of Chamfer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfer', 'v. t.', 'To cut a furrow in, as in a column; to groove; to\n   channel; to flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfer', 'v. t.', 'To make a chamfer on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfret', 'n.', 'A small gutter; a furrow; a groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfret', 'n.', 'A chamfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamfron', 'n.', 'The frontlet, or head armor, of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamlet', 'n.', 'See Camlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamois', 'n.', 'A small species of antelope (Rupicapra tragus); living on\n   the loftiest mountain ridges of Europe, as the Alps, Pyrenees, etc. It\n   possesses remarkable agility, and is a favorite object of chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamois', 'n.', 'A soft leather made from the skin of the chamois, or from\n   sheepskin, etc.; -- called also chamois leather, and chammy or shammy\n   leather. See Shammy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamomile', 'n.', 'See Camomile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champed', 'imp. & p. p.', 'of Champ');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champing', 'p. pr. & vb. n.', 'of Champ');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champ', 'v. t.', 'To bite with repeated action of the teeth so as to be\n   heard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champ', 'v. t.', 'To bite into small pieces; to crunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champ', 'v. i.', 'To bite or chew impatiently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champ', 'n.', 'Alt. of Champe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champe', 'n.', 'The field or ground on which carving appears in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champagne', 'n.', 'A light wine, of several kinds, originally made in the\n   province of Champagne, in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champaign', 'n.', 'A flat, open country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champaign', 'a.', 'Flat; open; level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champer', 'n.', 'One who champs, or bites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champertor', 'n.', 'One guilty of champerty; one who purchases a suit, or\n   the right of suing, and carries it on at his own expense, in order to\n   obtain a share of the gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champerty', 'n.', 'Partnership in power; equal share of authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champerty', 'n.', 'The prosecution or defense of a suit, whether by\n   furnishing money or personal services, by one who has no legitimate\n   concern therein, in consideration of an agreement that he shall\n   receive, in the event of success, a share of the matter in suit;\n   maintenance with the addition of an agreement to divide the thing in\n   suit. See Maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champignon', 'n.', 'An edible species of mushroom (Agaricus campestris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chappion', 'n.', 'One who engages in any contest; esp. one who in ancient\n   times contended in single combat in behalf of anothers honor or\n   rights; or one who acts or speaks in behalf of a person or a cause; a\n   defender; an advocate; a hero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chappion', 'n.', 'One who by defeating all rivals, has obtained an\n   acknowledged supremacy in any branch of athetics or game of skill, and\n   is ready to contend with any rival; as, the champion of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Championed', 'imp. & p. p.', 'of Champion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Championing', 'p. pr. & vb. n.', 'of Champion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champion', 'v. t.', 'To furnish with a champion; to attend or defend as\n   champion; to support or maintain; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Championness', 'n.', 'A female champion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Championship', 'n.', 'State of being champion; leadership; supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Champlain period', '', 'A subdivision of the Quaternary age immediately\n   following the Glacial period; -- so named from beds near Lake\n   Champlain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chamsin', 'n.', 'See Kamsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'n.', 'A supposed material or psychical agent or mode of activity\n   other than a force, law, or purpose; fortune; fate; -- in this sense\n   often personified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'n.', 'The operation or activity of such agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'n.', 'The supposed effect of such an agent; something that\n   befalls, as the result of unknown or unconsidered forces; the issue of\n   uncertain conditions; an event not calculated upon; an unexpected\n   occurrence; a happening; accident; fortuity; casualty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'n.', 'A possibility; a likelihood; an opportunity; -- with\n   reference to a doubtful result; as, a chance to escape; a chance for\n   life; the chances are all against him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'n.', 'Probability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanced', 'imp. & p. p.', 'of Chance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancing', 'p. pr. & vb. n.', 'of Chance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'v. i.', 'To happen, come, or arrive, without design or\n   expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'v. t.', 'To take the chances of; to venture upon; -- usually with\n   it as object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'v. t.', 'To befall; to happen to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'a.', 'Happening by chance; casual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance', 'adv.', 'By chance; perchance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanceable', 'a.', 'Fortuitous; casual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanceably', 'adv.', 'By chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanceful', 'a.', 'Hazardous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancel', 'v. t.', 'That part of a church, reserved for the use of the\n   clergy, where the altar, or communion table, is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancel', 'v. t.', 'All that part of a cruciform church which is beyond the\n   line of the transept farthest from the main front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancellery', 'n.', 'Chancellorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancellor', 'n.', 'A judicial court of chancery, which in England and in\n   the United States is distinctively a court with equity jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancellorship', 'n.', 'The office of a chancellor; the time during which\n   one is chancellor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance-medley', 'n.', 'The killing of another in self-defense upon a sudden\n   and unpremeditated encounter. See Chaud-Medley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chance-medley', 'n.', 'Luck; chance; accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancery', 'n.', 'In England, formerly, the highest court of judicature\n   next to the Parliament, exercising jurisdiction at law, but chiefly in\n   equity; but under the jurisdiction act of 1873 it became the chancery\n   division of the High Court of Justice, and now exercises jurisdiction\n   only in equity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancery', 'n.', 'In the Unites States, a court of equity; equity;\n   proceeding in equity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancre', 'n.', 'A venereal sore or ulcer; specifically, the initial lesion\n   of true syphilis, whether forming a distinct ulcer or not; -- called\n   also hard chancre, indurated chancre, and Hunterian chancre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancroid', 'n.', 'A venereal sore, resembling a chancre in its seat and\n   some external characters, but differing from it in being the starting\n   point of a purely local process and never of a systemic disease; --\n   called also soft chancre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chancrous', 'a.', 'Of the nature of a chancre; having chancre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandelier', 'n.', 'A candlestick, lamp, stand, gas fixture, or the like,\n   having several branches; esp., one hanging from the ceiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandelier', 'n.', 'A movable parapet, serving to support fascines to cover\n   pioneers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandler', 'n.', 'A maker or seller of candles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandler', 'n.', 'A dealer in other commodities, which are indicated by a\n   word prefixed; as, ship chandler, corn chandler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandlerly', 'a.', 'Like a chandler; in a petty way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandlery', 'n.', 'Commodities sold by a chandler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandoo', 'n.', 'An extract or preparation of opium, used in China and\n   India for smoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chandry', 'n.', 'Chandlery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanfrin', 'n.', 'The fore part of a horses head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changed', 'imp. & p. p.', 'of Change');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changing', 'p. pr. & vb. n.', 'of Change');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'To alter; to make different; to cause to pass from one\n   state to another; as, to change the position, character, or appearance\n   of a thing; to change the countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'To alter by substituting something else for, or by\n   giving up for something else; as, to change the clothes; to change\n   ones occupation; to change ones intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'To give and take reciprocally; to exchange; -- followed\n   by with; as, to change place, or hats, or money, with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'Specifically: To give, or receive, smaller denominations\n   of money (technically called change) for; as, to change a gold coin or\n   a bank bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. i.', 'To be altered; to undergo variation; as, men sometimes\n   change for the better.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. i.', 'To pass from one phase to another; as, the moon changes\n   to-morrow night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'Any variation or alteration; a passing from one state or\n   form to another; as, a change of countenance; a change of habits or\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'A succesion or substitution of one thing in the place of\n   another; a difference; novelty; variety; as, a change of seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'A passing from one phase to another; as, a change of the\n   moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'Alteration in the order of a series; permutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'That which makes a variety, or may be substituted for\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'Small money; the money by means of which the larger\n   coins and bank bills are made available in small dealings; hence, the\n   balance returned when payment is tendered by a coin or note exceeding\n   the sum due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'A place where merchants and others meet to transact\n   business; a building appropriated for mercantile transactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'A public house; an alehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Change', 'v. t.', 'Any order in which a number of bells are struck, other\n   than that of the diatonic scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeability', 'n.', 'Changeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeable', 'a.', 'Capable of change; subject to alteration; mutable;\n   variable; fickle; inconstant; as, a changeable humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeable', 'a.', 'Appearing different, as in color, in different lights,\n   or under different circumstances; as, changeable silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeableness', 'n.', 'The quality of being changeable; fickleness;\n   inconstancy; mutability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeably', 'adv.', 'In a changeable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeful', 'a.', 'Full of change; mutable; inconstant; fickle; uncertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeless', 'a.', 'That can not be changed; constant; as, a changeless\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeling', 'n.', 'One who, or that which, is left or taken in the place\n   of another, as a child exchanged by fairies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeling', 'n.', 'A simpleton; an idiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeling', 'n.', 'One apt to change; a waverer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeling', 'a.', 'Taken or left in place of another; changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changeling', 'a.', 'Given to change; inconstant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changer', 'n.', 'One who changes or alters the form of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changer', 'n.', 'One who deals in or changes money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Changer', 'n.', 'One apt to change; an inconstant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chank', 'n.', 'The East Indian name for the large spiral shell of several\n   species of sea conch much used in making bangles, esp. Turbinella\n   pyrum. Called also chank chell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'n.', 'The hollow bed where a stream of water runs or may run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'n.', 'The deeper part of a river, harbor, strait, etc., where\n   the main current flows, or which affords the best and safest passage\n   for vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'n.', 'A strait, or narrow sea, between two portions of lands;\n   as, the British Channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'n.', 'That through which anything passes; means of passing,\n   conveying, or transmitting; as, the news was conveyed to us by\n   different channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'n.', 'A gutter; a groove, as in a fluted column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'n.', 'Flat ledges of heavy plank bolted edgewise to the outside\n   of a vessel, to increase the spread of the shrouds and carry them clear\n   of the bulwarks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channeled', 'imp. & p. p.', 'of Channel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channelled', '', 'of Channel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channeling', 'p. pr. & vb. n.', 'of Channel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channelling', '', 'of Channel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'v. t.', 'To form a channel in; to cut or wear a channel or\n   channels in; to groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channel', 'v. t.', 'To course through or over, as in a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channeling', 'n.', 'The act or process of forming a channel or channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Channeling', 'n.', 'A channel or a system of channels; a groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanson', 'n.', 'A song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chansonnettes', 'pl. ', 'of Chansonnette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chansonnette', 'n.', 'A little song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanted', 'imp. & p. p.', 'of Chant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanting', 'p. pr. & vb. n.', 'of Chant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'To utter with a melodious voice; to sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'To celebrate in song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'To sing or recite after the manner of a chant, or to a\n   tune called a chant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. i.', 'To make melody with the voice; to sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. i.', 'To sing, as in reciting a chant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'Song; melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'A short and simple melody, divided into two parts by\n   double bars, to which unmetrical psalms, etc., are sung or recited. It\n   is the most ancient form of choral music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'A psalm, etc., arranged for chanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chant', 'v. t.', 'Twang; manner of speaking; a canting tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chantant', 'a.', 'Composed in a melodious and singing style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanter', 'n.', 'One who chants; a singer or songster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanter', 'n.', 'The chief singer of the chantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanter', 'n.', 'The flute or finger pipe in a bagpipe. See Bagpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanter', 'n.', 'The hedge sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanterelle', 'n.', 'A name for several species of mushroom, of which one\n   (Cantharellus cibrius) is edible, the others reputed poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanticleer', 'n.', 'A cock, so called from the clearness or loudness of\n   his voice in crowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chanting', 'n.', 'Singing, esp. as a chant is sung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chantor', 'n.', 'A chanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chantress', 'n.', 'A female chanter or singer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chantries', 'pl. ', 'of Chantry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chantry', 'n.', 'An endowment or foundation for the chanting of masses and\n   offering of prayers, commonly for the founder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chantry', 'n.', 'A chapel or altar so endowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaomancy', 'n.', 'Divination by means of appearances in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaos', 'n.', 'An empty, immeasurable space; a yawning chasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaos', 'n.', 'The confused, unorganized condition or mass of matter before\n   the creation of distinct and orderly forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaos', 'n.', 'Any confused or disordered collection or state of things; a\n   confused mixture; confusion; disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaotic', 'a.', 'Resembling chaos; confused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaotically', 'adv.', 'In a chaotic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapped', 'imp. & p. p.', 'of Chap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapping', 'p. pr. & vb. n.', 'of Chap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'v. t.', 'To cause to open in slits or chinks; to split; to cause\n   the skin of to crack or become rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'v. t.', 'To strike; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'v. i.', 'To crack or open in slits; as, the earth chaps; the hands\n   chap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'v. i.', 'To strike; to knock; to rap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'A cleft, crack, or chink, as in the surface of the earth, or\n   in the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'A division; a breach, as in a party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'A blow; a rap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'One of the jaws or the fleshy covering of a jaw; -- commonly\n   in the plural, and used of animals, and colloquially of human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'One of the jaws or cheeks of a vise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'A buyer; a chapman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'n.', 'A man or boy; a youth; a fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chap', 'v. i.', 'To bargain; to buy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaparral', 'n.', 'A thicket of low evergreen oaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaparral', 'n.', 'An almost impenetrable thicket or succession of thickets\n   of thorny shrubs and brambles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapbook', 'n.', 'Any small book carried about for sale by chapmen or\n   hawkers. Hence, any small book; a toy book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chape', 'n.', 'The piece by which an object is attached to something, as\n   the frog of a scabbard or the metal loop at the back of a buckle by\n   which it is fastened to a strap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chape', 'n.', 'The transverse guard of a sword or dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chape', 'n.', 'The metal plate or tip which protects the end of a scabbard,\n   belt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapeux', 'pl. ', 'of Chapeau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapeau', 'n.', 'A hat or covering for the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapeau', 'n.', 'A cap of maintenance. See Maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaped', 'p. p. / a.', 'Furnished with a chape or chapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'A subordinate place of worship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'a small church, often a private foundation, as for a\n   memorial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'a small building attached to a church');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'a room or recess in a church, containing an altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'A place of worship not connected with a church; as, the\n   chapel of a palace, hospital, or prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'In England, a place of worship used by dissenters from the\n   Established Church; a meetinghouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'A choir of singers, or an orchestra, attached to the court\n   of a prince or nobleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'A printing office, said to be so called because printing\n   was first carried on in England in a chapel near Westminster Abbey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'n.', 'An association of workmen in a printing office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'v. t.', 'To deposit or inter in a chapel; to enshrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapel', 'v. t.', 'To cause (a ship taken aback in a light breeze) so to\n   turn or make a circuit as to recover, without bracing the yards, the\n   same tack on which she had been sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapeless', 'a.', 'Without a chape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapelet', 'n.', 'A pair of straps, with stirrups, joined at the top and\n   fastened to the pommel or the frame of the saddle, after they have been\n   adjusted to the convenience of the rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapelet', 'n.', 'A kind of chain pump, or dredging machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapellanies', 'pl. ', 'of Chapellany');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapellany', 'n.', 'A chapel within the jurisdiction of a church; a\n   subordinate ecclesiastical foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapelry', 'n.', 'The territorial district legally assigned to a chapel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperon', 'n.', 'A hood; especially, an ornamental or an official hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperon', 'n.', 'A device placed on the foreheads of horses which draw the\n   hearse in pompous funerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperon', 'n.', 'A matron who accompanies a young lady in public, for\n   propriety, or as a guide and protector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperoned', 'imp. & p. p.', 'of Chaperon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperoning', 'p. pr. & vb. n.', 'of Chaperon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperon', 'v. t.', 'To attend in public places as a guide and protector;\n   to matronize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaperonage', 'n.', 'Attendance of a chaperon on a lady in public;\n   protection afforded by a chaperon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapfallen', 'a.', 'Having the lower chap or jaw drooping, -- an indication\n   of humiliation and dejection; crestfallen; discouraged. See Chopfallen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapiter', 'n.', 'A capital [Obs.] See Chapital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapiter', 'n.', 'A summary in writing of such matters as are to be\n   inquired of or presented before justices in eyre, or justices of\n   assize, or of the peace, in their sessions; -- also called articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplain', 'n.', 'An ecclesiastic who has a chapel, or who performs\n   religious service in a chapel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplain', 'n.', 'A clergyman who is officially attached to the army or\n   navy, to some public institution, or to a family or court, for the\n   purpose of performing divine service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplain', 'n.', 'Any person (clergyman or layman) chosen to conduct\n   religious exercises for a society, etc.; as, a chaplain of a Masonic or\n   a temperance lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplaincy', 'n.', 'The office, position, or station of a chaplain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplainship', 'n.', 'The office or business of a chaplain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplainship', 'n.', 'The possession or revenue of a chapel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapless', 'a.', 'Having no lower jaw; hence, fleshless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A garland or wreath to be worn on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A string of beads, or part of a string, used by Roman\n   Catholic in praying; a third of a rosary, or fifty beads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A small molding, carved into beads, pearls, olives, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A chapelet. See Chapelet, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A bent piece of sheet iron, or a pin with thin plates on\n   its ends, for holding a core in place in the mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A tuft of feathers on a peacocks head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'n.', 'A small chapel or shrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapleted', 'imp. & p. p.', 'of Chaplet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaplet', 'v. t.', 'To adorn with a chaplet or with flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapmen', 'pl. ', 'of Chapman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapman', 'n.', 'One who buys and sells; a merchant; a buyer or a seller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapman', 'n.', 'A peddler; a hawker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chappy', '', 'Full of chaps; cleft; gaping; open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaps', 'n. pl.', 'The jaws, or the fleshy parts about them. See Chap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A division of a book or treatise; as, Genesis has fifty\n   chapters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'An assembly of monks, or of the prebends and other\n   clergymen connected with a cathedral, conventual, or collegiate church,\n   or of a diocese, usually presided over by the dean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A community of canons or canonesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A bishops council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A business meeting of any religious community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'An organized branch of some society or fraternity as of\n   the Freemasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A meeting of certain organized societies or orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A chapter house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A decretal epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'n.', 'A location or compartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'v. t.', 'To divide into chapters, as a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chapter', 'v. t.', 'To correct; to bring to book, i. e., to demand chapter\n   and verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaptrel', 'n.', 'An impost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'n.', 'Alt. of Charr');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charr', 'n.', 'One of the several species of fishes of the genus\n   Salvelinus, allied to the spotted trout and salmon, inhabiting deep\n   lakes in mountainous regions in Europe. In the United States, the brook\n   trout (Salvelinus fontinalis) is sometimes called a char.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'n.', 'A car; a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'n.', 'Work done by the day; a single job, or task; a chore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'v. t.', 'Alt. of Chare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chare', 'v. t.', 'To perform; to do; to finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chare', 'v. t.', 'To work or hew, as stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'v. i.', 'Alt. of Chare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chare', 'v. i.', 'To work by the day, without being a regularly hired\n   servant; to do small jobs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charred', 'imp. & p. p.', 'of Char');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charring', 'p. pr. & vb. n.', 'of Char');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'n.', 'To reduce to coal or carbon by exposure to heat; to reduce to\n   charcoal; to burn to a cinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char', 'n.', 'To burn slightly or partially; as, to char wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chara', 'n.', 'A genus of flowerless plants, having articulated stems and\n   whorled branches. They flourish in wet places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chars-a-banc', 'pl. ', 'of Char-a-bancs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Char-a-bancs', 'n.', 'A long, light, open vehicle, with benches or seats\n   running lengthwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charact', 'n.', 'A distinctive mark; a character; a letter or sign. [Obs.]\n   See Character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'A distinctive mark; a letter, figure, or symbol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'Style of writing or printing; handwriting; the peculiar\n   form of letters used by a particular person or people; as, an\n   inscription in the Runic character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'The peculiar quality, or the sum of qualities, by which\n   a person or a thing is distinguished from others; the stamp impressed\n   by nature, education, or habit; that which a person or thing really is;\n   nature; disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'Strength of mind; resolution; independence;\n   individuality; as, he has a great deal of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'Moral quality; the principles and motives that control\n   the life; as, a man of character; his character saves him from\n   suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'Quality, position, rank, or capacity; quality or conduct\n   with respect to a certain office or duty; as, in the miserable\n   character of a slave; in his character as a magistrate; her character\n   as a daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'The estimate, individual or general, put upon a person\n   or thing; reputation; as, a mans character for truth and veracity; to\n   give one a bad character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'A written statement as to behavior, competency, etc.,\n   given to a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'A unique or extraordinary individuality; a person\n   characterized by peculiar or notable traits; a person who illustrates\n   certain phases of character; as, Randolph was a character; Caesar is a\n   great historical character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'n.', 'One of the persons of a drama or novel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charactered', 'imp. & p. p.', 'of Character');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'v. t.', 'To engrave; to inscribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Character', 'v. t.', 'To distinguish by particular marks or traits; to\n   describe; to characterize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterism', 'n.', 'A distinction of character; a characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characteristic', 'a.', 'Pertaining to, or serving to constitute, the\n   character; showing the character, or distinctive qualities or traits,\n   of a person or thing; peculiar; distinctive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characteristic', 'n.', 'A distinguishing trait, quality, or property; an\n   element of character; that which characterized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characteristic', 'n.', 'The integral part (whether positive or negative) of\n   a logarithm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characteristical', 'a.', 'Characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characteristically', 'adv.', 'In a characteristic manner; in a way that\n   characterizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterization', 'n.', 'The act or process of characterizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterized', 'imp. & p. p.', 'of Characterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterizing', 'p. pr. & vb. n.', 'of Characterize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterize', 'v. t.', 'To make distinct and recognizable by peculiar\n   marks or traits; to make with distinctive features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterize', 'v. t.', 'To engrave or imprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterize', 'v. t.', 'To indicate the character of; to describe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterize', 'v. t.', 'To be a characteristic of; to make, or express the\n   character of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Characterless', 'a.', 'Destitute of any distinguishing quality; without\n   character or force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charactery', 'n.', 'The art or means of characterizing; a system of signs\n   or characters; symbolism; distinctive mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charactery', 'n.', 'That which is charactered; the meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charade', 'n.', 'A verbal or acted enigma based upon a word which has two\n   or more significant syllables or parts, each of which, as well as the\n   word itself, is to be guessed from the descriptions or representations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charbocle', 'n.', 'Carbuncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charbon', 'n.', 'A small black spot or mark remaining in the cavity of the\n   corner tooth of a horse after the large spot or mark has become\n   obliterated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charbon', 'n.', 'A very contagious and fatal disease of sheep, horses, and\n   cattle. See Maligmant pustule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charcoal', 'v. t.', 'Impure carbon prepared from vegetable or animal\n   substances; esp., coal made by charring wood in a kiln, retort, etc.,\n   from which air is excluded. It is used for fuel and in various\n   mechanical, artistic, and chemical processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charcoal', 'v. t.', 'Finely prepared charcoal in small sticks, used as a\n   drawing implement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chard', 'n.', 'The tender leaves or leafstalks of the artichoke, white\n   beet, etc., blanched for table use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chard', 'n.', 'A variety of the white beet, which produces large, succulent\n   leaves and leafstalks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chare', 'n.', 'A narrow street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chare', 'n. & v.', 'A chore; to chore; to do. See Char.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charged', 'imp. & p. p.', 'of Charge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charging', 'p. pr. & vb. n.', 'of Charge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To lay on or impose, as a load, tax, or burden; to load;\n   to fill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To lay on or impose, as a task, duty, or trust; to\n   command, instruct, or exhort with authority; to enjoin; to urge\n   earnestly; as, to charge a jury; to charge the clergy of a diocese; to\n   charge an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To lay on, impose, or make subject to or liable for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To fix or demand as a price; as, he charges two dollars\n   a barrel for apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To place something to the account of as a debt; to\n   debit, as, to charge one with goods. Also, to enter upon the debit side\n   of an account; as, to charge a sum to one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To impute or ascribe; to lay to ones charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To accuse; to make a charge or assertion against (a\n   person or thing); to lay the responsibility (for something said or\n   done) at the door of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To place within or upon any firearm, piece of apparatus\n   or machinery, the quantity it is intended and fitted to hold or bear;\n   to load; to fill; as, to charge a gun; to charge an electrical machine,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To ornament with or cause to bear; as, to charge an\n   architectural member with a molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To assume as a bearing; as, he charges three roses or;\n   to add to or represent on; as, he charges his shield with three roses\n   or.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To call to account; to challenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'To bear down upon; to rush upon; to attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. i.', 'To make an onset or rush; as, to charge with fixed\n   bayonets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. i.', 'To demand a price; as, to charge high for goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. i.', 'To debit on an account; as, to charge for purchases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. i.', 'To squat on its belly and be still; -- a command given\n   by a sportsman to a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'A load or burder laid upon a person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'A person or thing commited or intrusted to the care,\n   custody, or management of another; a trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'Custody or care of any person, thing, or place; office;\n   responsibility; oversight; obigation; duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'Heed; care; anxiety; trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'Harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'An order; a mandate or command; an injunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'An address (esp. an earnest or impressive address)\n   containing instruction or exhortation; as, the charge of a judge to a\n   jury; the charge of a bishop to his clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'An accusation of a wrong of offense; allegation;\n   indictment; specification of something alleged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'Whatever constitutes a burden on property, as rents,\n   taxes, lines, etc.; costs; expense incurred; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'The price demanded for a thing or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'An entry or a account of that which is due from one\n   party to another; that which is debited in a business transaction; as,\n   a charge in an account book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'That quantity, as of ammunition, electricity, ore, fuel,\n   etc., which any apparatus, as a gun, battery, furnace, machine, etc.,\n   is intended to receive and fitted to hold, or which is actually in it\n   at one time');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'The act of rushing upon, or towards, an enemy; a sudden\n   onset or attack, as of troops, esp. cavalry; hence, the signal for\n   attack; as, to sound the charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'A position (of a weapon) fitted for attack; as, to bring\n   a weapon to the charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'A soft of plaster or ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'v. t.', 'A bearing. See Bearing, n., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'n.', 'Thirty-six pigs of lead, each pig weighing about seventy\n   pounds; -- called also charre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge', 'n.', 'Weight; import; value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeable', 'a.', 'That may be charged, laid, imposed, or imputes; as, a\n   duty chargeable on iron; a fault chargeable on a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeable', 'a.', 'Subject to be charge or accused; liable or responsible;\n   as, revenues chargeable with a claim; a man chargeable with murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeable', 'a.', 'Serving to create expense; costly; burdensome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeableness', 'n.', 'The quality of being chargeable or expensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeably', 'adv.', 'At great cost; expensively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeant', 'a.', 'Burdensome; troublesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charges daffaires', 'pl. ', 'of Charge daffaires');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charge daffaires', 'n.', 'A diplomatic representative, or minister of an\n   inferior grade, accredited by the government of one state to the\n   minister of foreign affairs of another; also, a substitute, ad interim,\n   for an ambassador or minister plenipotentiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeful', 'a.', 'Costly; expensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargehouse', 'n.', 'A schoolhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeless', 'a.', 'Free from, or with little, charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeous', 'a.', 'Burdensome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charger', 'n.', 'One who, or that which charges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charger', 'n.', 'An instrument for measuring or inserting a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charger', 'n.', 'A large dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charger', 'n.', 'A horse for battle or parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chargeship', 'n.', 'The office of a charge daffaires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charily', 'adv.', 'In a chary manner; carefully; cautiously; frugally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chariness', 'n.', 'The quality of being chary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chariot', 'n.', 'A two-wheeled car or vehicle for war, racing, state\n   processions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chariot', 'n.', 'A four-wheeled pleasure or state carriage, having one\n   seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charioted', 'imp. & p. p.', 'of Chariot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charioting', 'p. pr. & vb. n.', 'of Chariot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chariot', 'v. t.', 'To convey in a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chariotee', 'n.', 'A light, covered, four-wheeled pleasure carriage with\n   two seats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charioteer', 'n.', 'One who drives a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charioteer', 'n.', 'A constellation. See Auriga, and Wagones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charism', 'n.', 'A miraculously given power, as of healing, speaking\n   foreign languages without instruction, etc., attributed to some of the\n   early Christians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charismatic', 'a.', 'Of or pertaining to a charism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitable', 'a.', 'Full of love and good will; benevolent; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitable', 'a.', 'Liberal in judging of others; disposed to look on the\n   best side, and to avoid harsh judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitable', 'a.', 'Liberal in benefactions to the poor; giving freely;\n   generous; beneficent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitable', 'a.', 'Of or pertaining to charity; springing from, or\n   intended for, charity; relating to almsgiving; eleemosynary; as, a\n   charitable institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitable', 'a.', 'Dictated by kindness; favorable; lenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitableness', 'n.', 'The quality of being charitable; the exercise of\n   charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charitably', 'adv.', 'In a charitable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charities', 'pl. ', 'of Charity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charity', 'n.', 'Love; universal benevolence; good will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charity', 'n.', 'Liberality in judging of men and their actions; a\n   disposition which inclines men to put the best construction on the\n   words and actions of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charity', 'n.', 'Liberality to the poor and the suffering, to benevolent\n   institutions, or to worthy causes; generosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charity', 'n.', 'Whatever is bestowed gratuitously on the needy or\n   suffering for their relief; alms; any act of kindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charity', 'n.', 'A charitable institution, or a gift to create and support\n   such an institution; as, Lady Margarets charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charity', 'n.', 'Eleemosynary appointments [grants or devises] including\n   relief of the poor or friendless, education, religious culture, and\n   public institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charivari', 'n.', 'A mock serenade of discordant noises, made with kettles,\n   tin horns, etc., designed to annoy and insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chark', 'n.', 'Charcoal; a cinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charked', 'imp. & p. p.', 'of Chark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chark', 'v. t.', 'To burn to a coal; to char.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlatan', 'n.', 'One who prates much in his own favor, and makes\n   unwarrantable pretensions; a quack; an impostor; an empiric; a\n   mountebank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlatanic', 'a.', 'Alt. of Charlatanical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlatanical', 'a.', 'Of or like a charlatan; making undue pretension;\n   empirical; pretentious; quackish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlatanism', 'n.', 'Charlatanry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlatanry', 'n.', 'Undue pretensions to skill; quackery; wheedling;\n   empiricism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charless Wain', '', 'The group of seven stars, commonly called the Dipper,\n   in the constellation Ursa Major, or Great Bear. See Ursa major, under\n   Ursa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlock', 'n.', 'A cruciferous plant (Brassica sinapistrum) with yellow\n   flowers; wild mustard. It is troublesome in grain fields. Called also\n   chardock, chardlock, chedlock, and kedlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charlotte', 'n.', 'A kind of pie or pudding made by lining a dish with\n   slices of bread, and filling it with bread soaked in milk, and baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'A melody; a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'A word or combination of words sung or spoken in the\n   practice of magic; a magical combination of words, characters, etc.; an\n   incantation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'That which exerts an irresistible power to please and\n   attract; that which fascinates; any alluring quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'Anything worn for its supposed efficacy to the wearer in\n   averting ill or securing good fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'Any small decorative object worn on the person, as a seal, a\n   key, a silver whistle, or the like. Bunches of charms are often worn at\n   the watch chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmed', 'imp. & p. p.', 'of Charm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charming', 'p. pr. & vb. n.', 'of Charm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'To make music upon; to tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'To subdue, control, or summon by incantation or supernatural\n   influence; to affect by magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'To subdue or overcome by some secret power, or by that which\n   gives pleasure; to allay; to soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'To attract irresistibly; to delight exceedingly; to enchant;\n   to fascinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'n.', 'To protect with, or make invulnerable by, spells, charms, or\n   supernatural influences; as, a charmed life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'v. i.', 'To use magic arts or occult power; to make use of charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'v. i.', 'To act as, or produce the effect of, a charm; to please\n   greatly; to be fascinating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charm', 'v. i.', 'To make a musical sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmel', 'n.', 'A fruitful field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmer', 'n.', 'One who charms, or has power to charm; one who uses the\n   power of enchantment; a magician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmer', 'n.', 'One who delights and attracts the affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmeress', 'n.', 'An enchantress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmful', 'a.', 'Abounding with charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charming', 'a.', 'Pleasing the mind or senses in a high degree; delighting;\n   fascinating; attractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charmless', 'a.', 'Destitute of charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charneco', 'n.', 'Alt. of Charnico');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charnico', 'n.', 'A sort of sweet wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charnel', 'a.', 'Containing the bodies of the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charnel', 'n.', 'A charnel house; a grave; a cemetery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charon', 'n.', 'The son of Erebus and Nox, whose office it was to ferry the\n   souls of the dead over the Styx, a river of the infernal regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charpie', 'n.', 'Straight threads obtained by unraveling old linen cloth;\n   -- used for surgical dressings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charqui', 'n.', 'Jerked beef; beef cut into long strips and dried in the\n   wind and sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charr', 'n.', 'See 1st Char.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charras', 'n.', 'The gum resin of the hemp plant (Cannabis sativa). Same as\n   Churrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charre', 'n.', 'See Charge, n., 17.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charry', 'a.', 'Pertaining to charcoal, or partaking of its qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chart', 'n.', 'A sheet of paper, pasteboard, or the like, on which\n   information is exhibited, esp. when the information is arranged in\n   tabular form; as, an historical chart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chart', 'n.', 'A map; esp., a hydrographic or marine map; a map on which is\n   projected a portion of water and the land which it surrounds, or by\n   which it is surrounded, intended especially for the use of seamen; as,\n   the United States Coast Survey charts; the English Admiralty charts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chart', 'n.', 'A written deed; a charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charted', 'imp. & p. p.', 'of Chart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chart', 'v. t.', 'To lay down in a chart; to map; to delineate; as, to\n   chart a coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charta', 'n.', 'Material on which instruments, books, etc., are written;\n   parchment or paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charta', 'n.', 'A charter or deed; a writing by which a grant is made. See\n   Magna Charta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartaceous', 'a.', 'Resembling paper or parchment; of paper-like texture;\n   papery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charte', 'n.', 'The constitution, or fundamental law, of the French\n   monarchy, as established on the restoration of Louis XVIII., in 1814.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'n.', 'A written evidence in due form of things done or granted,\n   contracts made, etc., between man and man; a deed, or conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'n.', 'An instrument in writing, from the sovereign power of a\n   state or country, executed in due form, bestowing rights, franchises,\n   or privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'n.', 'An act of a legislative body creating a municipal or other\n   corporation and defining its powers and privileges. Also, an instrument\n   in writing from the constituted authorities of an order or society (as\n   the Freemasons); creating a lodge and defining its powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'n.', 'A special privilege, immunity, or exemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'n.', 'The letting or hiring a vessel by special contract, or the\n   contract or instrument whereby a vessel is hired or let; as, a ship is\n   offered for sale or charter. See Charter party, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartered', 'imp. & p. p.', 'of Charter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartering', 'p. pr. & vb. n.', 'of Charter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'v. t.', 'To establish by charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charter', 'v. t.', 'To hire or let by charter, as a ship. See Charter\n   party, under Charter, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartered', 'a.', 'Granted or established by charter; having, or existing\n   under, a charter; having a privilege by charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartered', 'a.', 'Hired or let by charter, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charterer', 'n.', 'One who charters; esp. one who hires a ship for a\n   voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charterhouse', 'n.', 'A well known public school and charitable foundation\n   in the building once used as a Carthusian monastery (Chartreuse) in\n   London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charterist', 'n.', 'Same as Chartist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartism', 'n.', 'The principles of a political party in England (1838-48);\n   which contended for universal suffrage, the vote by ballot, annual\n   parliaments, equal electoral districts, and other radical reforms, as\n   set forth in a document called the Peoples Charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartist', 'n.', 'A supporter or partisan of chartism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartless', 'a.', 'Without a chart; having no guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartless', 'a.', 'Not mapped; uncharted; vague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartographer', 'n.', 'Alt. of Chartography');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartographic', 'n.', 'Alt. of Chartography');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartography', 'n.', 'Same as Cartographer, Cartographic, Cartography, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartomancy', 'n.', 'Divination by written paper or by cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartometer', 'n.', 'An instrument for measuring charts or maps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartreuse', 'n.', 'A Carthusian monastery; esp. La Grande Chartreuse,\n   mother house of the order, in the mountains near Grenoble, France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartreuse', 'n.', 'An alcoholic cordial, distilled from aromatic herbs; --\n   made at La Grande Chartreuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartreux', 'n.', 'A Carthusian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chartulary', 'n.', 'See Cartulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charwomen', 'pl. ', 'of Charwoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charwoman', 'n.', 'A woman hired for odd work or for single days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chary', 'a.', 'Careful; wary; cautious; not rash, reckless, or spendthrift;\n   saving; frugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Charybdis', 'n.', 'A dangerous whirlpool on the coast of Sicily opposite\n   Scylla on the Italian coast. It is personified as a female monster. See\n   Scylla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasable', 'a.', 'Capable of being chased; fit for hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chased', 'imp. & p. p.', 'of Chase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasing', 'p. pr. & vb. n.', 'of Chase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v. t.', 'To pursue for the purpose of killing or taking, as an\n   enemy, or game; to hunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v. t.', 'To follow as if to catch; to pursue; to compel to move\n   on; to drive by following; to cause to fly; -- often with away or off;\n   as, to chase the hens away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v. t.', 'To pursue eagerly, as hunters pursue game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v. i.', 'To give chase; to hunt; as, to chase around after a\n   doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v.', 'Vehement pursuit for the purpose of killing or capturing, as\n   of an enemy, or game; an earnest seeking after any object greatly\n   desired; the act or habit of hunting; a hunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v.', 'That which is pursued or hunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v.', 'An open hunting ground to which game resorts, and which is\n   private properly, thus differing from a forest, which is not private\n   property, and from a park, which is inclosed. Sometimes written chace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v.', 'A division of the floor of a gallery, marked by a figure or\n   otherwise; the spot where a ball falls, and between which and the\n   dedans the adversary must drive his ball in order to gain a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'n.', 'A rectangular iron frame in which pages or columns of type\n   are imposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'n.', 'The part of a cannon from the reenforce or the trunnions to\n   the swell of the muzzle. See Cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'n.', 'A groove, or channel, as in the face of a wall; a trench, as\n   for the reception of drain tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'n.', 'A kind of joint by which an overlap joint is changed to a\n   flush joint, by means of a gradually deepening rabbet, as at the ends\n   of clinker-built boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v. t.', 'To ornament (a surface of metal) by embossing, cutting\n   away parts, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chase', 'v. t.', 'To cut, so as to make a screw thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaser', 'n.', 'One who or that which chases; a pursuer; a driver; a\n   hunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaser', 'n.', 'Same as Chase gun, esp. in terms bow chaser and stern\n   chaser. See under Bow, Stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaser', 'n.', 'One who chases or engraves. See 5th Chase, and Enchase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaser', 'n.', 'A tool with several points, used for cutting or finishing\n   screw threads, either external or internal, on work revolving in a\n   lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasible', 'n.', 'See Chasuble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasing', 'n.', 'The art of ornamenting metal by means of chasing tools;\n   also, a piece of ornamental work produced in this way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasm', 'n.', 'A deep opening made by disruption, as a breach in the earth\n   or a rock; a yawning abyss; a cleft; a fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasm', 'n.', 'A void space; a gap or break, as in ranks of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasmed', 'a.', 'Having gaps or a chasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasmy', 'a.', 'Of or pertaining to a chasm; abounding in chasms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasse', 'n.', 'A movement in dancing, as across or to the right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasse', 'v. i.', 'To make the movement called chasse; as, all chasse;\n   chasse to the right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasselas', 'n.', 'A white grape, esteemed for the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chassepot', 'n.', 'A kind of breechloading, center-fire rifle, or improved\n   needle gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasseur', 'n.', 'One of a body of light troops, cavalry or infantry,\n   trained for rapid movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasseur', 'n.', 'An attendant upon persons of rank or wealth, wearing a\n   plume and sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chassis', 'n.', 'A traversing base frame, or movable railway, along which\n   the carriage of a barbette or casemate gun moves backward and forward.\n   [See Gun carriage.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chast', 'v. t.', 'to chasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaste', 'a.', 'Pure from unlawful sexual intercourse; virtuous; continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaste', 'a.', 'Pure in thought and act; innocent; free from lewdness and\n   obscenity, or indecency in act or speech; modest; as, a chaste mind;\n   chaste eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaste', 'a.', 'Pure in design and expression; correct; free from\n   barbarisms or vulgarisms; refined; simple; as, a chaste style in\n   composition or art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaste', 'a.', 'Unmarried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastely', 'adv.', 'In a chaste manner; with purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastened', 'imp. & p. p.', 'of Chasten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastening', 'p. pr. & vb. n.', 'of Chasten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasten', 'v. t.', 'To correct by punishment; to inflict pain upon the\n   purpose of reclaiming; to discipline; as, to chasten a son with a rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasten', 'v. t.', 'To purify from errors or faults; to refine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastened', 'a.', 'Corrected; disciplined; refined; purified; toned down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastener', 'n.', 'One who chastens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasteness', 'n.', 'Chastity; purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasteness', 'n.', 'Freedom from all that is meretricious, gaudy, or\n   affected; as, chasteness of design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastisable', 'a.', 'Capable or deserving of chastisement; punishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastised', 'imp. & p. p.', 'of Chastise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastising', 'p. pr. & vb. n.', 'of Chastise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastise', 'v. t.', 'To inflict pain upon, by means of stripes, or in any\n   other manner, for the purpose of punishment or reformation; to punish,\n   as with stripes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastise', 'v. t.', 'To reduce to order or obedience; to correct or purify;\n   to free from faults or excesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastisement', 'n.', 'The act of chastising; pain inflicted for punishment\n   and correction; discipline; punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastiser', 'n.', 'One who chastises; a punisher; a corrector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastity', 'n.', 'The state of being chaste; purity of body; freedom from\n   unlawful sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastity', 'n.', 'Moral purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastity', 'n.', 'The unmarried life; celibacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chastity', 'n.', 'Chasteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chasuble', 'n.', 'The outer vestment worn by the priest in saying Mass,\n   consisting, in the Roman Catholic Church, of a broad, flat, back piece,\n   and a narrower front piece, the two connected over the shoulders only.\n   The back has usually a large cross, the front an upright bar or pillar,\n   designed to be emblematical of Christs sufferings. In the Greek Church\n   the chasuble is a large round mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatted', 'imp. & p. p.', 'of Chat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatting', 'p. pr. & vb. n.', 'of Chat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chat', 'v. i.', 'To talk in a light and familiar manner; to converse\n   without form or ceremony; to gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chat', 'v. t.', 'To talk of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chat', 'n.', 'Light, familiar talk; conversation; gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chat', 'n.', 'A bird of the genus Icteria, allied to the warblers, in\n   America. The best known species are the yellow-breasted chat (I.\n   viridis); and the long-tailed chat (I. longicauda). In Europe the name\n   is given to several birds of the family Saxicolidae, as the stonechat,\n   and whinchat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chat', 'n.', 'A twig, cone, or little branch. See Chit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chat', 'n.', 'Small stones with ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chateux', 'pl. ', 'of Chateau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chateau', 'n.', 'A castle or a fortress in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chateau', 'n.', 'A manor house or residence of the lord of the manor; a\n   gentlemans country seat; also, particularly, a royal residence; as,\n   the chateau of the Louvre; the chateau of the Luxembourg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatelaine', 'n.', 'An ornamental hook, or brooch worn by a lady at her\n   waist, and having a short chain or chains attached for a watch, keys,\n   trinkets, etc. Also used adjectively; as, a chatelaine chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatelet', 'n.', 'A little castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatellany', 'n.', 'Same as Castellany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chati', 'n.', 'A small South American species of tiger cat (Felis mitis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatoyant', 'a.', 'Having a changeable, varying luster, or color, like that\n   of a changeable silk, or oa a cats eye in the dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatoyant', 'n.', 'A hard stone, as the cats-eye, which presents on a\n   polished surface, and in the interior, an undulating or wary light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatoyment', 'n.', 'Changeableness of color, as in a mineral; play of\n   colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chattel', 'n.', 'Any item of movable or immovable property except the\n   freehold, or the things which are parcel of it. It is a more extensive\n   term than goods or effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chattelism', 'n.', 'The act or condition of holding chattels; the state of\n   being a chattel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chattered', 'imp. & p. p.', 'of Chatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chattering', 'p. pr. & vb. n.', 'of Chatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatter', 'v. i.', 'To utter sounds which somewhat resemble language, but\n   are inarticulate and indistinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatter', 'v. i.', 'To talk idly, carelessly, or with undue rapidity; to\n   jabber; to prate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatter', 'v. i.', 'To make a noise by rapid collisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatter', 'v. t.', 'To utter rapidly, idly, or indistinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatter', 'n.', 'Sounds like those of a magpie or monkey; idle talk; rapid,\n   thoughtless talk; jabber; prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatter', 'n.', 'Noise made by collision of the teeth, as in shivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatteration', 'n.', 'The act or habit of chattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatterer', 'n.', 'A prater; an idle talker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatterer', 'n.', 'A bird of the family Ampelidae -- so called from its\n   monotonous note. The Bohemion chatterer (Ampelis garrulus) inhabits the\n   arctic regions of both continents. In America the cedar bird is a more\n   common species. See Bohemian chatterer, and Cedar bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chattering', 'n.', 'The act or habit of talking idly or rapidly, or of\n   making inarticulate sounds; the sounds so made; noise made by the\n   collision of the teeth; chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chattiness', 'n.', 'The quality of being chatty, or of talking easily and\n   pleasantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatty', 'a.', 'Given to light, familiar talk; talkative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatty', 'n.', 'A porous earthen pot used in India for cooling water, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chatwood', 'n.', 'Little sticks; twigs for burning; fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaud-medley', 'n.', 'The killing of a person in an affray, in the heat of\n   blood, and while under the influence of passion, thus distinguished\n   from chance-medley or killing in self-defense, or in a casual affray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaudron', 'n.', 'See Chawdron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chauffer', 'n.', 'A table stove or small furnace, usually a cylindrical box\n   of sheet iron, with a grate at the bottom, and an open top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chauldron', 'n.', 'See Chawdron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaun', 'n.', 'A gap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaun', 'v. t. & i.', 'To open; to yawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaunt', 'n. & v.', 'See Chant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaunter', 'n.', 'A street seller of ballads and other broadsides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaunter', 'n.', 'A deceitful, tricky dealer or horse jockey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaunter', 'n.', 'The flute of a bagpipe. See Chanter, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaunterie', 'n.', 'See Chantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaus', 'n.', 'a lynxlike animal of Asia and Africa (Lynx Lybicus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chausses', 'n. pl.', 'The garment for the legs and feet and for the body\n   below the waist, worn in Europe throughout the Middle Ages; applied\n   also to the armor for the same parts, when fixible, as of chain mail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaussure', 'n.', 'A foot covering of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chauvinism', 'n.', 'Blind and absurd devotion to a fallen leader or an\n   obsolete cause; hence, absurdly vainglorious or exaggerated patriotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chavender', 'n.', 'The chub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chawed', 'imp. & p. p.', 'of Chaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chawing', 'p. pr. & vb. n.', 'of Chaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaw', 'v. t.', 'To grind with the teeth; to masticate, as food in eating;\n   to chew, as the cud; to champ, as the bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaw', 'v. t.', 'To ruminate in thought; to consider; to keep the mind\n   working upon; to brood over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaw', 'v. t.', 'As much as is put in the mouth at once; a chew; a quid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chaw', 'v. t.', 'The jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chawdron', 'n.', 'Entrails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chay root', '', 'The root of the Oldenlandia umbellata, native in India,\n   which yieds a durable red dyestuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chazy epoch', '', 'An epoch at the close of the Canadian period of the\n   American Lower Silurian system; -- so named from a township in Clinton\n   Co., New York. See the Diagram under Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap', 'n.', 'A bargain; a purchase; cheapness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap', 'n.', 'Having a low price in market; of small cost or price, as\n   compared with the usual price or the real value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap', 'n.', 'Of comparatively small value; common; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap', 'adv.', 'Cheaply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap', 'v. i.', 'To buy; to bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheapened', 'imp. & p. p.', 'of Cheapen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheapening', 'p. pr. & vb. n.', 'of Cheapen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheapen', 'v. t.', 'To ask the price of; to bid, bargain, or chaffer for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheapen', 'a.', 'To beat down the price of; to lessen the value of; to\n   depreciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheapener', 'n.', 'One who cheapens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap-jack', 'n.', 'Alt. of Cheap-john');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheap-john', 'n.', 'A seller of low-priced or second goods; a hawker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheaply', 'adv.', 'At a small price; at a low value; in a common or\n   inferior manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheapness', 'n.', 'Lowness in price, considering the usual price, or real\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chear', 'n. & v.', 'See Cheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'An act of deception or fraud; that which is the means of\n   fraud or deception; a fraud; a trick; imposition; imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'One who cheats or deceives; an impostor; a deceiver; a\n   cheater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'A troublesome grass, growing as a weed in grain fields; --\n   called also chess. See Chess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'The obtaining of property from another by an intentional\n   active distortion of the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheated', 'imp. & p. p.', 'of Cheat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheating', 'p. pr. & vb. n.', 'of Cheat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'To deceive and defraud; to impose upon; to trick; to\n   swindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'To beguile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'v. i.', 'To practice fraud or trickery; as, to cheat at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheat', 'n.', 'Wheat, or bread made from wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheatable', 'a.', 'Capable of being cheated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheatableness', 'n.', 'Capability of being cheated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheater', 'n.', 'One who cheats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheater', 'n.', 'An escheator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chebacco', 'n.', 'A narrow-sterned boat formerly much used in the\n   Newfoundland fisheries; -- called also pinkstern and chebec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chebec', 'n.', 'See Chebacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chebec', 'n.', 'A small American bird (Empidonax minimus); the least\n   flycatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'A word of warning denoting that the king is in danger; such\n   a menace of a players king by an adversarys move as would, if it were\n   any other piece, expose it to immediate capture. A king so menaced is\n   said to be in check, and must be made safe at the next move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'A condition of interrupted or impeded progress; arrest;\n   stop; delay; as, to hold an enemy in check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'Whatever arrests progress, or limits action; an obstacle,\n   guard, restraint, or rebuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'A mark, certificate, or token, by which, errors may be\n   prevented, or a thing or person may be identified; as, checks placed\n   against items in an account; a check given for baggage; a return check\n   on a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'A written order directing a bank or banker to pay money as\n   therein stated. See Bank check, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'A woven or painted design in squares resembling the patten\n   of a checkerboard; one of the squares of such a design; also, cloth\n   having such a figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'The forsaking by a hawk of its proper game to follow other\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'n.', 'Small chick or crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checked', 'imp. & p. p.', 'of Check');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checking', 'p. pr. & vb. n.', 'of Check');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. t.', 'To make a move which puts an adversarys piece, esp. his\n   king, in check; to put in check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. t.', 'To put a sudden restraint upon; to stop temporarily; to\n   hinder; to repress; to curb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. t.', 'To verify, to guard, to make secure, by means of a mark,\n   token, or other check; to distinguish by a check; to put a mark against\n   (an item) after comparing with an original or a counterpart in order to\n   secure accuracy; as, to check an account; to check baggage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. t.', 'To chide, rebuke, or reprove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. t.', 'To slack or ease off, as a brace which is too stiffly\n   extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. t.', 'To make checks or chinks in; to cause to crack; as, the\n   sun checks timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. i.', 'To make a stop; to pause; -- with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. i.', 'To clash or interfere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. i.', 'To act as a curb or restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. i.', 'To crack or gape open, as wood in drying; or to crack in\n   small checks, as varnish, paint, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'v. i.', 'To turn, when in pursuit of proper game, and fly after\n   other birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Check', 'a.', 'Checkered; designed in checks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkage', 'n.', 'The act of checking; as, the checkage of a name or of an\n   item in a list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkage', 'n.', 'The items, or the amount, to which attention is called by\n   a check or checks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checker', 'v. t.', 'One who checks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkered', 'imp. & p. p.', 'of Checker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkering', 'p. pr. & vb. n.', 'of Checker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checker', 'n.', 'To mark with small squares like a checkerboard, as by\n   crossing stripes of different colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checker', 'n.', 'To variegate or diversify with different qualities,\n   colors, scenes, or events; esp., to subject to frequent alternations of\n   prosperity and adversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checker', 'v. t.', 'A piece in the game of draughts or checkers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checker', 'v. t.', 'A pattern in checks; a single check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checker', 'v. t.', 'Checkerwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkerberries', 'pl. ', 'of Checkerberry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkerberry', 'n.', 'A spicy plant and its bright red berry; the\n   wintergreen (Gaultheria procumbens). Also incorrectly applied to the\n   partridge berry (Mitchella repens).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkerboard', 'n.', 'A board with sixty-four squares of alternate color,\n   used for playing checkers or draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkered', 'a.', 'Marked with alternate squares or checks of different\n   color or material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkered', 'a.', 'Diversified or variegated in a marked manner, as in\n   appearance, character, circumstances, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkers', 'v.', 'A game, called also daughts, played on a checkerboard by\n   two persons, each having twelve men (counters or checkers) which are\n   moved diagonally. The game is ended when either of the players has lost\n   all his men, or can not move them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkerwork', 'n.', 'Work consisting of or showing checkers varied\n   alternately as to colors or materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkerwork', 'n.', 'Any aggregate of varied vicissitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checklaton', 'n.', 'Ciclatoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checklaton', 'n.', 'Gilded leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkless', 'a.', 'That can not be checked or restrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkmate', 'n.', 'The position in the game of chess when a king is in\n   check and cannot be released, -- which ends the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkmate', 'n.', 'A complete check; utter defeat or overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkmated', 'imp. & p. p.', 'of Checkmate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkmating', 'p. pr. & vb. n.', 'of Checkmate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkmate', 'v. t.', 'To check (an adversarys king) in such a manner that\n   escape in impossible; to defeat (an adversary) by putting his king in\n   check from which there is no escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkmate', 'v. t.', 'To defeat completely; to terminate; to thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkrein', 'n.', 'A short rein looped over the check hook to prevent a\n   horse from lowering his head; -- called also a bearing rein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkrein', 'n.', 'A branch rein connecting the driving rein of one horse\n   of a span or pair with the bit of the other horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkroll', 'n.', 'A list of servants in a household; -- called also\n   chequer roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkstring', 'n.', 'A cord by which a person in a carriage or horse car\n   may signal to the driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checkwork', 'n.', 'Anything made so as to form alternate squares like those\n   of a checkerboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Checky', 'a.', 'Divided into small alternating squares of two tinctures; --\n   said of the field or of an armorial bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheddar', 'a.', 'Of or pertaining to, or made at, Cheddar, in England; as,\n   Cheddar cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'n.', 'The side of the face below the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'n.', 'The cheek bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'n.', 'Those pieces of a machine, or of any timber, or stone work,\n   which form corresponding sides, or which are similar and in pair; as,\n   the cheeks (jaws) of a vise; the cheeks of a gun carriage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'n.', 'The branches of a bridle bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'n.', 'A section of a flask, so made that it can be moved\n   laterally, to permit the removal of the pattern from the mold; the\n   middle part of a flask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'n.', 'Cool confidence; assurance; impudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheek', 'v. t.', 'To be impudent or saucy to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeked', 'a.', 'Having a cheek; -- used in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeky', '', 'a Brazen-faced; impudent; bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeped', 'imp. & p. p.', 'of Cheep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheep', 'v. i.', 'To chirp, as a young bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheep', 'v. t.', 'To give expression to in a chirping tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheep', 'n.', 'A chirp, peep, or squeak, as of a young bird or mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'n.', 'The face; the countenance or its expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'n.', 'Feeling; spirit; state of mind or heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'n.', 'Gayety; mirth; cheerfulness; animation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'n.', 'That which promotes good spirits or cheerfulness; provisions\n   prepared for a feast; entertainment; as, a table loaded with good\n   cheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'n.', 'A shout, hurrah, or acclamation, expressing joy enthusiasm,\n   applause, favor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheered', 'imp. & p. p.', 'of Cheer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheering', 'p. pr. & vb. n.', 'of Cheer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'v. t.', 'To cause to rejoice; to gladden; to make cheerful; --\n   often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'v. t.', 'To infuse life, courage, animation, or hope, into; to\n   inspirit; to solace or comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'v. t.', 'To salute or applaud with cheers; to urge on by cheers;\n   as, to cheer hounds in a chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'v. i.', 'To grow cheerful; to become gladsome or joyous; --\n   usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'v. i.', 'To be in any state or temper of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheer', 'v. i.', 'To utter a shout or shouts of applause, triumph, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerer', 'n.', 'One who cheers; one who, or that which, gladdens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerful', 'a.', 'Having or showing good spirits or joy; cheering; cheery;\n   contented; happy; joyful; lively; animated; willing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerfully', 'adv.', 'In a cheerful manner, gladly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerfulness', 'n.', 'Good spirits; a state of moderate joy or gayety;\n   alacrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerily', 'adv.', 'In a cheery manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeriness', 'n.', 'The state of being cheery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeringly', 'adv.', 'In a manner to cheer or encourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerisness', 'n.', 'Cheerfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerless', 'a.', 'Without joy, gladness, or comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerly', 'a.', 'Gay; cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerly', 'adv.', 'Cheerily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheerry', 'a.', 'Cheerful; lively; gay; bright; pleasant; as, a cheery\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheese', 'n.', 'The curd of milk, coagulated usually with rennet, separated\n   from the whey, and pressed into a solid mass in a hoop or mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheese', 'n.', 'A mass of pomace, or ground apples, pressed together in the\n   form of a cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheese', 'n.', 'The flat, circular, mucilaginous fruit of the dwarf mallow\n   (Malva rotundifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheese', 'n.', 'A low courtesy; -- so called on account of the cheese form\n   assumed by a womans dress when she stoops after extending the skirts\n   by a rapid gyration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeselep', 'n.', 'A bag in which rennet is kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheesemonger', 'n.', 'One who deals in cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeseparing', 'n.', 'A thin portion of the rind of a cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheeseparing', 'a.', 'Scrimping; mean; as, cheeseparing economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheesiness', 'n.', 'The quality of being cheesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheesy', 'a.', 'Having the nature, qualities, taste, form, consistency, or\n   appearance of cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheetah', 'n.', 'A species of leopard (Cynaelurus jubatus) tamed and used\n   for hunting in India. The woolly cheetah of South Africa is C. laneus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chef', 'n.', 'A chief of head person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chef', 'n.', 'The head cook of large establishment, as a club, a family,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chef', 'n.', 'Same as Chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chefs-doeuvre', 'pl. ', 'of Chef-doeuvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chef-doeuvre', 'n.', 'A masterpiece; a capital work in art, literature,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chegoe', 'n.', 'Alt. of Chegre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chegre', 'n.', 'See Chigoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheiloplasty', 'n.', 'The process of forming an artificial tip or part of a\n   lip, by using for the purpose a piece of healthy tissue taken from some\n   neighboring part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheilopoda', 'n.', 'See Ch/lopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheirepter', 'n.', 'One of the Cheiroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheiroptera', 'n. pl.', 'An order of mammalia, including the bats, having\n   four toes of each of the anterior limbs elongated and connected by a\n   web, so that they can be used like wings in flying. See Bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheiropterous', 'a.', 'Belonging to the Cheiroptera, or Bat family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheiropterygia', 'pl. ', 'of Cheiropterygium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheiropterygium', 'n.', 'The typical pentadactyloid limb of the higher\n   vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheirosophy', 'n.', 'The art of reading character as it is delineated in\n   the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheirotherium', 'n.', 'A genus of extinct animals, so named from fossil\n   footprints rudely resembling impressions of the human hand, and\n   believed to have been made by labyrinthodont reptiles. See Illustration\n   in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chekelatoun', 'n.', 'See Ciclatoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chekmak', 'n.', 'A turkish fabric of silk and cotton, with gold thread\n   interwoven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelae', 'pl. ', 'of Chela');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chela', 'n.', 'The pincherlike claw of Crustacea and Arachnida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelate', 'a.', 'Same as Cheliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelerythrine', 'n.', 'An alkaloidal principle obtained from the celandine,\n   and named from the red color of its salts. It is a colorless\n   crystalline substance, and acts as an acrid narcotic poison. It is\n   identical with sanguinarine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelicerae', 'pl. ', 'of Chelicera');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelicera', 'n.', 'One of the anterior pair of mouth organs, terminated by\n   a pincherlike claw, in scorpions and allied Arachnida. They are\n   homologous with the falcers of spiders, and probably with the mandibles\n   of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelidon', 'n.', 'The hollow at the flexure of the arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelidonic', 'a.', 'Of, pertaining to, or derived from, the celandine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelidonius', 'n.', 'A small stone taken from the gizzard of a young\n   swallow. -- anciently worn as a medicinal charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelifer', 'n.', 'See Book scorpion, under Book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheliferous', 'a.', 'Having cheliform claws, like a crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheliform', 'a.', 'Having a movable joint or finger closing against a\n   preceding joint or a projecting part of it, so that the whole may be\n   used for grasping, as the claw of a crab; pincherlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelone', 'n.', 'A genus of hardy perennial flowering plants, of the order\n   Scrophulariaceae, natives of North America; -- called also snakehead,\n   turtlehead, shellflower, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelonia', 'n. pl.', 'An order of reptiles, including the tortoises and\n   turtles, peculiar in having a part of the vertebrae, ribs, and sternum\n   united with the dermal plates so as to form a firm shell. The jaws are\n   covered by a horny beak. See Reptilia; also, Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelonian', 'a.', 'Of or pertaining to animals of the tortoise kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelonian', 'n.', 'One of the Chelonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chelura', 'n.', 'A genus of marine amphipod crustacea, which bore into and\n   sometimes destroy timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chely', 'n.', 'A claw. See Chela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemic', 'n.', 'A chemist; an alchemist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemic', 'n.', 'A solution of chloride of lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemic', 'a.', 'Chemical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemical', 'a.', 'Pertaining to chemistry; characterized or produced by the\n   forces and operations of chemistry; employed in the processes of\n   chemistry; as, chemical changes; chemical combinations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemical', 'n.', 'A substance used for producing a chemical effect; a\n   reagent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemically', 'adv.', 'According to chemical principles; by chemical process\n   or operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemiglyphic', 'a.', 'Engraved by a voltaic battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemiloon', 'n.', 'A garment for women, consisting of chemise and drawers\n   united in one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemise', 'n.', 'A shift, or undergarment, worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemise', 'n.', 'A wall that lines the face of a bank or earthwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemisette', 'n.', 'An under-garment, worn by women, usually covering the\n   neck, shoulders, and breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemism', 'n.', 'The force exerted between the atoms of elementary\n   substance whereby they unite to form chemical compounds; chemical\n   attaction; affinity; -- sometimes used as a general expression for\n   chemical activity or relationship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemist', 'n.', 'A person versed in chemistry or given to chemical\n   investigation; an analyst; a maker or seller of chemicals or drugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemistry', 'n.', 'That branch of science which treats of the composition\n   of substances, and of the changes which they undergo in consequence of\n   alterations in the constitution of the molecules, which depend upon\n   variations of the number, kind, or mode of arrangement, of the\n   constituent atoms. These atoms are not assumed to be indivisible, but\n   merely the finest grade of subdivision hitherto attained. Chemistry\n   deals with the changes in the composition and constitution of\n   molecules. See Atom, Molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemistry', 'n.', 'An application of chemical theory and method to the\n   consideration of some particular subject; as, the chemistry of iron;\n   the chemistry of indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemistry', 'n.', 'A treatise on chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemitype', 'n.', 'One of a number of processes by which an impression from\n   an engraved plate is obtained in relief, to be used for printing on an\n   ordinary printing press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemolysis', 'n.', 'A term sometimes applied to the decomposition of\n   organic substance into more simple bodies, by the use of chemical\n   agents alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemosmosis', 'n.', 'Chemical action taking place through an intervening\n   membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemosmotic', 'a.', 'Pertaining to, or produced by, chemosmosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chemung period', '', 'A subdivision in the upper part of the Devonian\n   system in America, so named from the Chemung River, along which the\n   rocks are well developed. It includes the Portage and Chemung groups or\n   epochs. See the Diagram under Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheng', 'n.', 'A chinese reed instrument, with tubes, blown by the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chenille', 'n.', 'Tufted cord, of silk or worsted, for the trimming of\n   ladies dresses, for embroidery and fringes, and for the weft of\n   Chenille rugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chenomorphae', 'n. pl.', 'An order of birds, including the swans, ducks,\n   geese, flamingoes and screamers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chepster', 'n.', 'The European starling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheque', 'n.', 'See Check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chequer', 'n. & v.', 'Same as Checker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chequing', 'n.', 'A coin. See Sequin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chequy', 'n.', 'Same as Checky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherif', 'n.', 'See Cherif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherimoyer', 'n.', 'A small downy-leaved tree (Anona Cherimolia); with\n   fragrant flowers. It is a native of Peru.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherimoyer', 'n.', 'Its delicious fruit, which is succulent, dark purple,\n   and similar to the custard apple of the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherished', 'imp. & p. p.', 'of Cherish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherising', 'p. pr. & vb. n.', 'of Cherish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherish', 'v. t.', 'To treat with tenderness and affection; to nurture with\n   care; to protect and aid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherish', 'v. t.', 'To hold dear; to embrace with interest; to indulge; to\n   encourage; to foster; to promote; as, to cherish religious principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherisher', 'n.', 'One who cherishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherishment', 'n.', 'Encouragement; comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chermes', 'n.', 'See Kermes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherogril', 'n.', 'See Cony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherokees', 'n. pl.', 'An Appalachian tribe of Indians, formerly inhabiting\n   the region about the head waters of the Tennessee River. They are now\n   mostly settled in the Indian Territory, and have become one of the most\n   civilized of the Indian Tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheroot', 'n.', 'A kind of cigar, originally brought from Mania, in the\n   Philippine Islands; now often made of inferior or adulterated tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'n.', 'A tree or shrub of the genus Prunus (Which also includes\n   the plum) bearing a fleshy drupe with a bony stone;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'n.', 'The common garden cherry (Prunus Cerasus); of which several\n   hundred varieties are cultivated for the fruit, some of which are, the\n   begarreau, blackheart, black Tartarian, oxheart, morelle or morello,\n   May-duke (corrupted from Medoc in France).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'n.', 'The wild cherry; as, Prunus serotina (wild black cherry);\n   valued for its timber; P. Virginiana (choke cherry); an American shrub\n   which bears astringent fruit; P. avium and P. Padus, European trees\n   (bird cherry).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'n.', 'The fruit of the cherry tree, a drupe of various colors and\n   flavors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'n.', 'The timber of the cherry tree, esp. of the black cherry,\n   used in cabinetmaking, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'n.', 'A peculiar shade of red, like that of a cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherry', 'a.', 'Like a red cherry in color; ruddy; blooming; as, a cherry\n   lip; cherry cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chersonese', 'n.', 'A peninsula; a tract of land nearly surrounded by\n   water, but united to a larger tract by a neck of land or isthmus; as,\n   the Cimbric Chersonese, or Jutland; the Tauric Chersonese, or Crimea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chert', 'n.', 'An impure, massive, flintlike quartz or hornstone, of a dull\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherty', 'a.', 'Like chert; containing chert; flinty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubs', 'pl. ', 'of Cherub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubim', 'pl. ', 'of Cherub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherub', 'n.', 'A mysterious composite being, the winged footstool and\n   chariot of the Almighty, described in Ezekiel i. and x.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherub', 'n.', 'A symbolical winged figure of unknown form used in\n   connection with the mercy seat of the Jewish Ark and Temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherub', 'n.', 'One of a order of angels, variously represented in art. In\n   European painting the cherubim have been shown as blue, to denote\n   knowledge, as distinguished from the seraphim (see Seraph); and in\n   later art the childrens heads with wings are generally called cherubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherub', 'n.', 'A beautiful child; -- so called because artists have\n   represented cherubs as beautiful children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubic', 'a.', 'Alt. of Cherubical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubical', 'a.', 'Of or pertaining to cherubs; angelic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubim', 'n.', 'The Hebrew plural of Cherub.. Cf. Seraphim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubin', 'a.', 'Cherubic; angelic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherubin', 'n.', 'A cherub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherup', 'v. i.', 'To make a short, shrill, cheerful sound; to chirp. See\n   Chirrup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherup', 'v. t.', 'To excite or urge on by making a short, shrill, cheerful\n   sound; to cherup to. See Chirrup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cherup', 'n.', 'A short, sharp, cheerful noise; a chirp; a chirrup; as, the\n   cherup of a cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chervil', 'n.', 'A plant (Anthriscus cerefolium) with pinnately divided\n   aromatic leaves, of which several curled varieties are used in soups\n   and salads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ches', '', 'pret. of Chese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chese', 'v. t.', 'To choose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chesible', 'n.', 'See Chasuble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheslip', 'n.', 'The wood louse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chess', 'n.', 'A game played on a chessboard, by two persons, with two\n   differently colored sets of men, sixteen in each set. Each player has a\n   king, a queen, two bishops, two knights, two castles or rooks, and\n   eight pawns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chess', 'n.', 'A species of brome grass (Bromus secalinus) which is a\n   troublesome weed in wheat fields, and is often erroneously regarded as\n   degenerate or changed wheat; it bears a very slight resemblance to\n   oats, and if reaped and ground up with wheat, so as to be used for\n   food, is said to produce narcotic effects; -- called also cheat and\n   Willards bromus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chess-apple', 'n.', 'The wild service of Europe (Purus torminalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessboard', 'n.', 'The board used in the game of chess, having eight rows\n   of alternate light and dark squares, eight in each row. See\n   Checkerboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessel', 'n.', 'The wooden mold in which cheese is pressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chesses', 'n. pl.', 'The platforms, consisting of two or more planks\n   doweled together, for the flooring of a temporary military bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessil', 'n.', 'Gravel or pebbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessmen', 'pl. ', 'of Chessman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessman', 'n.', 'A piece used in the game of chess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessom', 'n.', 'Mellow earth; mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chesstree', 'n.', 'A piece of oak bolted perpendicularly on the side of a\n   vessel, to aid in drawing down and securing the clew of the mainsail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chessy copper', '', 'The mineral azurite, found in fine crystallization at\n   Chessy, near Lyons; called also chessylite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'n.', 'A large box of wood, or other material, having, like a\n   trunk, a lid, but no covering of skin, leather, or cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'n.', 'A coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'n.', 'The part of the body inclosed by the ribs and breastbone;\n   the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'n.', 'A case in which certain goods, as tea, opium, etc., are\n   transported; hence, the quantity which such a case contains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'n.', 'A tight receptacle or box, usually for holding gas, steam,\n   liquids, etc.; as, the steam chest of an engine; the wind chest of an\n   organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chested', 'imp. & p. p.', 'of Chest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'v. i.', 'To deposit in a chest; to hoard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'v. i.', 'To place in a coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest', 'n.', 'Strife; contention; controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chested', 'a.', 'Having (such) a chest; -- in composition; as,\n   broad-chested; narrow-chested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chesterlite', 'n.', 'A variety of feldspar found in crystals in the county\n   of Chester, Pennsylvania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chesteyn', 'n.', 'The chestnut tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chest founder', '', 'A rheumatic affection of the muscles of the breast and\n   fore legs of a horse, affecting motion and respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'n.', 'The edible nut of a forest tree (Castanea vesca) of\n   Europe and America. Commonly two or more of the nuts grow in a prickly\n   bur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'n.', 'The tree itself, or its light, coarse-grained timber,\n   used for ornamental work, furniture, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'n.', 'A bright brown color, like that of the nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'n.', 'The horse chestnut (often so used in England).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'n.', 'One of the round, or oval, horny plates on the inner\n   sides of the legs of the horse, and allied animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'n.', 'An old joke or story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chestnut', 'a.', 'Of the color of a chestnut; of a reddish brown color; as,\n   chestnut curls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chetah', 'n.', 'See Cheetah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chetvert', 'n.', 'A measure of grain equal to 0.7218 of an imperial\n   quarter, or 5.95 Winchester bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevachie', 'n.', 'See Chivachie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevage', 'n.', 'See Chiefage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevaux', 'pl. ', 'of Cheval');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheval', 'n.', 'A horse; hence, a support or frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevaux-de-frise', 'pl. ', 'of Cheval-de-frise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheval-de-frise', 'n.', 'A piece of timber or an iron barrel traversed with\n   iron-pointed spikes or spears, five or six feet long, used to defend a\n   passage, stop a breach, or impede the advance of cavalry, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevalier', 'n.', 'A horseman; a knight; a gallant young man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevalier', 'n.', 'A member of certain orders of knighthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevaux', 'n. pl.', 'See Cheval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheve', 'v. i.', 'To come to an issue; to turn out; to succeed; as, to\n   cheve well in a enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevelure', 'n.', 'A hairlike envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheven', 'n.', 'A river fish; the chub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheventein', 'n.', 'A variant of Chieftain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheveril', 'v. i.', 'Soft leather made of kid skin. Fig.: Used as a symbol\n   of flexibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheveril', 'a.', 'Made of cheveril; pliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheverliize', 'v. i.', 'To make as pliable as kid leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevet', 'n.', 'The extreme end of the chancel or choir; properly the round\n   or polygonal part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheviot', 'n.', 'A valuable breed of mountain sheep in Scotland, which\n   takes its name from the Cheviot hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheviot', 'n.', 'A woolen fabric, for mens clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevisance', 'n.', 'Achievement; deed; performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevisance', 'n.', 'A bargain; profit; gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevisance', 'n.', 'A making of contracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevisance', 'n.', 'A bargain or contract; an agreement about a matter in\n   dispute, such as a debt; a business compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevisance', 'n.', 'An unlawful agreement or contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevrette', 'n.', 'A machine for raising guns or mortar into their\n   carriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevron', 'n.', 'One of the nine honorable ordinaries, consisting of two\n   broad bands of the width of the bar, issuing, respectively from the\n   dexter and sinister bases of the field and conjoined at its center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevron', 'n.', 'A distinguishing mark, above the elbow, on the sleeve of a\n   non-commissioned officers coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevron', 'n.', 'A zigzag molding, or group of moldings, common in Norman\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevroned', 'p. a.', 'Having a chevron; decorated with an ornamental figure\n   of a zigzag from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevronel', 'n.', 'A bearing like a chevron, but of only half its width.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevronwise', 'adv.', 'In the manner of a chevron; as, the field may be\n   divided chevronwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevrotain', 'n.', 'A small ruminant of the family Tragulidae a allied to\n   the musk deer. It inhabits Africa and the East Indies. See Kanchil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chevy', 'v. t.', 'See Chivy, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chewed', 'imp. & p. p.', 'of Chew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chewing', 'p. pr. & vb. n.', 'of Chew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chew', 'v. t.', 'To bite and grind with the teeth; to masticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chew', 'v. t.', 'To ruminate mentally; to meditate on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chew', 'v. i.', 'To perform the action of biting and grinding with the\n   teeth; to ruminate; to meditate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chew', 'n.', 'That which is chewed; that which is held in the mouth at\n   once; a cud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chewer', 'n.', 'One who chews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chewet', 'n.', 'A kind of meat pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chewink', 'n.', 'An american bird (Pipilo erythrophthalmus) of the Finch\n   family, so called from its note; -- called also towhee bunting and\n   ground robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cheyennes', 'n. pl.', 'A warlike tribe of indians, related to the\n   blackfeet, formerly inhabiting the region of Wyoming, but now mostly on\n   reservations in the Indian Territory. They are noted for their\n   horsemanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chian', 'a.', 'Of or pertaining to Chios, an island in the Aegean Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiaroscurist', 'n.', 'A painter who cares for and studies light and shade\n   rather than color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiaroscuro', 'n.', 'Alt. of Chiaro-oscuro');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiaro-oscuro', 'n.', 'The arrangement of light and dark parts in a work of\n   art, such as a drawing or painting, whether in monochrome or in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiaro-oscuro', 'n.', 'The art or practice of so arranging the light and\n   dark parts as to produce a harmonious effect. Cf. Clair-obscur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiasm', 'n.', 'Alt. of Chiasma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiasma', 'n.', 'A commissure; especially, the optic commissure, or crucial\n   union of the optic nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiasmus', 'n.', 'An inversion of the order of words or phrases, when\n   repeated or subsequently referred to in a sentence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiastolite', 'n.', 'A variety of andalusite; -- called also macle. The\n   tessellated appearance of a cross section is due to the symmetrical\n   arrangement of impurities in the crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chibbal', 'n.', 'See Cibol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chibouque', 'n.', 'Alt. of Chibouk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chibouk', 'n.', 'A Turkish pipe, usually with a mouthpiece of amber, a\n   stem, four or five feet long and not pliant, of some valuable wood, and\n   a bowl of baked clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chic', 'n.', 'Good form; style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chica', 'n.', 'A red coloring matter. extracted from the Bignonia Chica,\n   used by some tribes of South American Indians to stain the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chica', 'n.', 'A fermented liquor or beer made in South American from a\n   decoction of maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chica', 'n.', 'A popular Moorish, Spanish, and South American dance, said\n   to be the original of the fandango, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicane', 'n.', 'The use of artful subterfuge, designed to draw away\n   attention from the merits of a case or question; -- specifically\n   applied to legal proceedings; trickery; chicanery; caviling; sophistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicane', 'n.', 'To use shifts, cavils, or artifices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicaner', 'n.', 'One who uses chicanery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicanery', 'n.', 'Mean or unfair artifice to perplex a cause and obscure\n   the truth; stratagem; sharp practice; sophistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiccory', 'n.', 'See Chicory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiches', 'pl. ', 'of Chich');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chich', 'n.', 'The chick-pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicha', 'n.', 'See Chica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chichevache', 'n.', 'A fabulous cow of enormous size, whose food was\n   patient wives, and which was therefore in very lean condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chichling', 'n.', 'Alt. of Chichling vetch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chichling vetch', 'n.', 'A leguminous plant (Lathyrus sativus); with broad\n   flattened seeds which are sometimes used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chick', 'v. i.', 'To sprout, as seed in the ground; to vegetate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chick', 'n.', 'A chicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chick', 'n.', 'A child or young person; -- a term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chickabiddy', 'n.', 'A chicken; a fowl; also, a trivial term of endearment\n   for a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chickadee', 'n.', 'A small bird, the blackcap titmouse (Parus\n   atricapillus); of North America; -- named from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chickaree', 'n.', 'The American red squirrel (Sciurus Hudsonius); -- so\n   called from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chickasaws', 'n. pl.', 'A tribe of North American Indians (Southern\n   Appalachian) allied to the Choctaws. They formerly occupied the\n   northern part of Alabama and Mississippi, but now live in the Indian\n   Territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicken', 'n.', 'A young bird or fowl, esp. a young barnyard fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicken', 'n.', 'A young person; a child; esp. a young woman; a maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicken-breasted', 'a.', 'Having a narrow, projecting chest, caused by\n   forward curvature of the vertebral column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicken-hearted', 'a.', 'Timid; fearful; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicken pox', '', 'A mild, eruptive disease, generally attacking children\n   only; varicella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chickling', 'n.', 'A small chick or chicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chick-pea', 'n.', 'A Small leguminous plant (Cicer arietinum) of Asia,\n   Africa, and the south of Europe; the chich; the dwarf pea; the gram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chick-pea', 'n.', 'Its nutritious seed, used in cookery, and especially,\n   when roasted (parched pulse); as food for travelers in the Eastern\n   deserts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chickweed', 'n.', 'The name of several caryophyllaceous weeds, especially\n   Stellaria media, the seeds and flower buds of which are a favorite food\n   of small birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicky', 'n.', 'A chicken; -- used as a diminutive or pet name, especially\n   in calling fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicory', 'n.', 'A branching perennial plant (Cichorium Intybus) with\n   bright blue flowers, growing wild in Europe, Asia, and America; also\n   cultivated for its roots and as a salad plant; succory; wild endive.\n   See Endive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chicory', 'n.', 'The root, which is roasted for mixing with coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chide', 'p. pr. & vb. n.', 'To rebuke; to reprove; to scold; to find fault\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chide', 'p. pr. & vb. n.', 'Fig.: To be noisy about; to chafe against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chide', 'v. i.', 'To utter words of disapprobation and displeasure; to find\n   fault; to contend angrily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chide', 'v. i.', 'To make a clamorous noise; to chafe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chide', 'n.', 'A continuous noise or murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chider', 'n.', 'One who chides or quarrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chideress', 'n.', 'She who chides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chidester', 'n.', 'A female scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chidingly', 'adv.', 'In a chiding or reproving manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief', 'n.', 'The head or leader of any body of men; a commander, as of an\n   army; a head man, as of a tribe, clan, or family; a person in authority\n   who directs the work of others; the principal actor or agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief', 'n.', 'The principal part; the most valuable portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief', 'n.', 'The upper third part of the field. It is supposed to be\n   composed of the dexter, sinister, and middle chiefs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief', 'a.', 'Highest in office or rank; principal; head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief', 'a.', 'Principal or most eminent in any quality or action; most\n   distinguished; having most influence; taking the lead; most important;\n   as, the chief topic of conversation; the chief interest of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief', 'a.', 'Very intimate, near, or close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiefage', 'n.', 'A tribute by the head; a capitation tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief baron', '', 'The presiding judge of the court of exchequer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiefest', 'a.', 'First or foremost; chief; principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief hare', '', 'A small rodent (Lagamys princeps) inhabiting the summits\n   of the Rocky Mountains; -- also called crying hare, calling hare, cony,\n   American pika, and little chief hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief justice', '', 'The presiding justice, or principal judge, of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chief-justiceship', 'n.', 'The office of chief justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiefless', 'a.', 'Without a chief or leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiefly', 'adv.', 'In the first place; principally; preeminently; above;\n   especially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiefly', 'adv.', 'For the most part; mostly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiefrie', 'n.', 'A small rent paid to the lord paramount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chieftain', 'n.', 'A captain, leader, or commander; a chief; the head of a\n   troop, army, or clan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chieftaincy', 'n.', 'Alt. of Chieftainship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chieftainship', 'n.', 'The rank, dignity, or office of a chieftain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chierte', 'n.', 'Love; tender regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chievance', 'n.', 'An unlawful bargain; traffic in which money is exported\n   as discount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chieve', 'v. i.', 'See Cheve, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiff-chaff', 'n.', 'A species of European warbler (Sylvia hippolais); --\n   called also chip-chap, and pettychaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiffonier', 'n.', 'Alt. of niere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiffo', 'n.', 'Alt. of niere');
  INSERT INTO words (word, word_type, description) VALUES  (    'niere', 'n.', 'One who gathers rags and odds and ends; a ragpicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'niere', 'n.', 'A receptacle for rags or shreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'niere', 'n.', 'A movable and ornamental closet or piece of furniture with\n   shelves or drawers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chignon', 'n.', 'A knot, boss, or mass of hair, natural or artificial, worn\n   by a woman at the back of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chigoe', 'n.', 'Alt. of Chigre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chigre', 'n.', 'A species of flea (Pulex penetrans); common in the West\n   Indies and South America, which often attacks the feet or any exposed\n   part of the human body, and burrowing beneath the skin produces great\n   irritation. When the female is allowed to remain and breed, troublesome\n   sores result, which are sometimes dangerous. See Jigger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chikara', 'n.', 'The goat antelope (Tragops Bennettii) of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chikara', 'n.', 'The Indian four-horned antelope (Tetraceros quadricornis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilblain', 'n.', 'A blain, sore, or inflammatory swelling, produced by\n   exposure of the feet or hands to cold, and attended by itching, pain,\n   and sometimes ulceration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilblain', 'v. t.', 'To produce chilblains upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Children', 'pl. ', 'of Child');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'n.', 'A son or a daughter; a male or female descendant, in the\n   first degree; the immediate progeny of human parents; -- in law,\n   legitimate offspring. Used also of animals and plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'n.', 'A descendant, however remote; -- used esp. in the plural;\n   as, the children of Israel; the children of Edom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'n.', 'One who, by character of practice, shows signs of\n   relationship to, or of the influence of, another; one closely connected\n   with a place, occupation, character, etc.; as, a child of God; a child\n   of the devil; a child of disobedience; a child of toil; a child of the\n   people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'n.', 'A noble youth. See Childe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'n.', 'A young person of either sex. esp. one between infancy and\n   youth; hence, one who exhibits the characteristics of a very young\n   person, as innocence, obedience, trustfulness, limited understanding,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'n.', 'A female infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childed', 'imp. & p. p.', 'of Child');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childing', 'p. pr. & vb. n.', 'of Child');
  INSERT INTO words (word, word_type, description) VALUES  (    'Child', 'v. i.', 'To give birth; to produce young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childbearing', 'n.', 'The act of producing or bringing forth children;\n   parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childbed', 'n.', 'The state of a woman bringing forth a child, or being in\n   labor; parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childbirth', 'n.', 'The act of bringing forth a child; travail; labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childcrowing', 'n.', 'The crowing noise made by children affected with\n   spasm of the laryngeal muscles; false croup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childe', 'n.', 'A cognomen formerly prefixed to his name by the oldest son,\n   until he succeeded to his ancestral titles, or was knighted; as, Childe\n   Roland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childed', 'a.', 'Furnished with a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childermas day', '', 'A day (December 28) observed by mass or festival in\n   commemoration of the children slain by Herod at Bethlehem; -- called\n   also Holy Innocents Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childhood', 'n.', 'The state of being a child; the time in which persons\n   are children; the condition or time from infancy to puberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childhood', 'n.', 'Children, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childhood', 'n.', 'The commencement; the first period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childing', 'v. i.', 'Bearing Children; (Fig.) productive; fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childish', 'a.', 'Of, pertaining to, befitting, or resembling, a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childish', 'a.', 'Puerile; trifling; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childishly', 'adv.', 'In the manner of a child; in a trifling way; in a\n   weak or foolish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childishness', 'n.', 'The state or quality of being childish; simplicity;\n   harmlessness; weakness of intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childlessness', 'n.', 'The state of being childless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childlike', 'a.', 'Resembling a child, or that which belongs to children;\n   becoming a child; meek; submissive; dutiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childly', 'a.', 'Having the character of a child; belonging, or\n   appropriate, to a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childly', 'adv.', 'Like a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childness', 'n.', 'The manner characteristic of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Children', 'n.', 'pl. of Child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Childship', 'n.', 'The state or relation of being a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chili', 'n.', 'A kind of red pepper. See Capsicum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliad', 'n.', 'A thousand; the aggregate of a thousand things;\n   especially, a period of a thousand years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliagon', 'n.', 'A plane figure of a thousand angles and sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliahedron', 'n.', 'A figure bounded by a thousand plane surfaces');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilian', 'a.', 'Of or pertaining to Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilian', 'n.', 'A native or citizen of Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilian', 'n.', 'Alt. of Chiliarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliarch', 'n.', 'The commander or chief of a thousand men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliarchy', 'n.', 'A body consisting of a thousand men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliasm', 'n.', 'The millennium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliasm', 'n.', 'The doctrine of the personal reign of Christ on earth\n   during the millennium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliast', 'n.', 'One who believes in the second coming of Christ to reign\n   on earth a thousand years; a milllenarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiliastic', 'a.', 'Millenarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'n.', 'A moderate but disagreeable degree of cold; a disagreeable\n   sensation of coolness, accompanied with shivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'n.', 'A sensation of cold with convulsive shaking of the body,\n   pinched face, pale skin, and blue lips, caused by undue cooling of the\n   body or by nervous excitement, or forming the precursor of some\n   constitutional disturbance, as of a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'n.', 'A check to enthusiasm or warmth of feeling; discouragement;\n   as, a chill comes over an assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'n.', 'An iron mold or portion of a mold, serving to cool rapidly,\n   and so to harden, the surface of molten iron brought in contact with\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'n.', 'The hardened part of a casting, as the tread of a car wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'a.', 'Moderately cold; tending to cause shivering; chilly; raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'a.', 'Affected by cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'a.', 'Characterized by coolness of manner, feeling, etc.; lacking\n   enthusiasm or warmth; formal; distant; as, a chill reception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'a.', 'Discouraging; depressing; dispiriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilled', 'imp. & p. p.', 'of Chill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilling', 'p. pr. & vb. n.', 'of Chill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'v. t.', 'To strike with a chill; to make chilly; to cause to\n   shiver; to affect with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'v. t.', 'To check enthusiasm or warmth of feeling of; to depress;\n   to discourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'v. t.', 'To produce, by sudden cooling, a change of\n   crystallization at or near the surface of, so as to increase the\n   hardness; said of cast iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chill', 'v. i.', 'To become surface-hardened by sudden cooling while\n   solidifying; as, some kinds of cast iron chill to a greater depth than\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilled', 'a.', 'Hardened on the surface or edge by chilling; as, chilled\n   iron; a chilled wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilled', 'a.', 'Having that cloudiness or dimness of surface that is\n   called \"blooming.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilli', 'n.', 'See Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilliness', 'n.', 'A state or sensation of being chilly; a disagreeable\n   sensation of coldness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilliness', 'n.', 'A moderate degree of coldness; disagreeable coldness or\n   rawness; as, the chilliness of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilliness', 'n.', 'Formality; lack of warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilling', 'a.', 'Making chilly or cold; depressing; discouraging; cold;\n   distant; as, a chilling breeze; a chilling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chillness', 'n.', 'Coolness; coldness; a chill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilly', 'a.', 'Moderately cold; cold and raw or damp so as to cause\n   shivering; causing or feeling a disagreeable sensation of cold, or a\n   shivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilognath', 'n.', 'A myriapod of the order Chilognatha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilognatha', 'n. pl.', 'One of the two principal orders of myriapods. They\n   have numerous segments, each bearing two pairs of small, slender legs,\n   which are attached ventrally, near together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiloma', 'n.', 'The tumid upper lip of certain mammals, as of a camel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilopod', 'n.', 'A myriapod of the order Chilopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilopoda', 'n. pl.', 'One of the orders of myriapods, including the\n   centipeds. They have a single pair of elongated legs attached laterally\n   to each segment; well developed jaws; and a pair of thoracic legs\n   converted into poison fangs. They are insectivorous, very active, and\n   some species grow to the length of a foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilostoma', 'n. pl.', 'Alt. of Chilostomata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilostomata', 'n. pl.', 'An extensive suborder of marine Bryozoa, mostly\n   with calcareous shells. They have a movable lip and a lid to close the\n   aperture of the cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chilostomatous', 'a.', 'Of or pertaining to the Chilostoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiltern Hundreds', '', 'A tract of crown land in Buckinghamshire and\n   Oxfordshire, England, to which is attached the nominal office of\n   steward. As members of Parliament cannot resign, when they wish to go\n   out they accept this stewardship, which legally vacates their seats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimaera', 'n.', 'A cartilaginous fish of several species, belonging to the\n   order Holocephali. The teeth are few and large. The head is furnished\n   with appendages, and the tail terminates in a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimaeroid', 'a.', 'Related to, or like, the chimaera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimango', '', 'A south American carrion buzzard (Milvago chimango). See\n   Caracara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimb', 'n.', 'The edge of a cask, etc; a chine. See Chine, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimb', 'v. i.', 'Chime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'See Chine, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'The harmonious sound of bells, or of musical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'A set of bells musically tuned to each other; specif., in\n   the pl., the music performed on such a set of bells by hand, or\n   produced by mechanism to accompany the striking of the hours or their\n   divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'Pleasing correspondence of proportion, relation, or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimed', 'imp. & p. p.', 'of Chime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiming', 'p. pr. & vb. n.', 'of Chime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'To sound in harmonious accord, as bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'To be in harmony; to agree; to suit; to harmonize; to\n   correspond; to fall in with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'To join in a conversation; to express assent; -- followed by\n   in or in with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'n.', 'To make a rude correspondence of sounds; to jingle, as in\n   rhyming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'v. i.', 'To cause to sound in harmony; to play a tune, as upon a\n   set of bells; to move or strike in harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chime', 'v. i.', 'To utter harmoniously; to recite rhythmically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimer', 'n.', 'One who chimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimeras', 'pl. ', 'of Chimera');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimera', 'n.', 'A monster represented as vomiting flames, and as having\n   the head of a lion, the body of a goat, and the tail of a dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimera', 'n.', 'A vain, foolish, or incongruous fancy, or creature of the\n   imagination; as, the chimera of an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimere', 'n.', 'The upper robe worn by a bishop, to which lawn sleeves are\n   usually attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimeric', 'a.', 'Chimerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimerical', 'a.', 'Merely imaginary; fanciful; fantastic; wildly or vainly\n   conceived; having, or capable of having, no existence except in\n   thought; as, chimerical projects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimerically', 'adv.', 'Wildy; vainly; fancifully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiminage', 'n.', 'A toll for passage through a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimneys', 'pl. ', 'of Chimney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimney', 'n.', 'A fireplace or hearth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimney', 'n.', 'That part of a building which contains the smoke flues;\n   esp. an upright tube or flue of brick or stone, in most cases extending\n   through or above the roof of the building. Often used instead of\n   chimney shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimney', 'n.', 'A tube usually of glass, placed around a flame, as of a\n   lamp, to create a draft, and promote combustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimney', 'n.', 'A body of ore, usually of elongated form, extending\n   downward in a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimney-breast', 'n.', 'The horizontal projection of a chimney from the\n   wall in which it is built; -- commonly applied to its projection in the\n   inside of a building only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimney-piece', 'n.', 'A decorative construction around the opening of a\n   fireplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chimpanzee', 'n.', 'An african ape (Anthropithecus troglodytes or\n   Troglodytes niger) which approaches more nearly to man, in most\n   respects, than any other ape. When full grown, it is from three to four\n   feet high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chin', 'n.', 'The lower extremity of the face below the mouth; the point of\n   the under jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chin', 'n.', 'The exterior or under surface embraced between the branches\n   of the lower jaw bone, in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'China', 'n.', 'A country in Eastern Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'China', 'n.', 'China ware, which is the modern popular term for porcelain.\n   See Porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinaldine', 'n.', 'See Quinaldine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinamen', 'pl. ', 'of Chinaman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinaman', 'n.', 'A native of China; a Chinese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chincapin', 'n.', 'See Chinquapin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinch', 'n.', 'The bedbug (Cimex lectularius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinch', 'n.', 'A bug (Blissus leucopterus); which, in the United States,\n   is very destructive to grass, wheat, and other grains; -- also called\n   chiniz, chinch bug, chink bug. It resembles the bedbug in its\n   disgusting odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chincha', 'n.', 'A south American rodent of the genus Lagotis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinche', 'a.', 'Parsimonious; niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chincherie', 'n.', 'Penuriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinchilla', 'n.', 'A small rodent (Chinchilla lanigera); of the size of a\n   large squirrel, remarkable for its fine fur, which is very soft and of\n   a pearly gray color. It is a native of Peru and Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinchilla', 'n.', 'The fur of the chinchilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinchilla', 'n.', 'A heavy, long-napped, tufted woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinchona', '', 'Alt. of Chincona');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chincona', '', 'See Cinchona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chin cough', '', 'Whooping cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chine', 'n.', 'A chink or cleft; a narrow and deep ravine; as, Shanklin\n   Chine in the Isle of Wight, a quarter of a mile long and 230 feet deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chine', 'n.', 'The backbone or spine of an animal; the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chine', 'n.', 'A piece of the backbone of an animal, with the adjoining\n   parts, cut for cooking. [See Illust. of Beef.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chine', 'n.', 'The edge or rim of a cask, etc., formed by the projecting\n   ends of the staves; the chamfered end of a stave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chined', 'imp. & p. p.', 'of Chine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chine', 'v. t.', 'To cut through the backbone of; to cut into chine pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chine', 'v. t.', 'Too chamfer the ends of a stave and form the chine..');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chined', 'a.', 'Pertaining to, or having, a chine, or backbone; -- used in\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chined', 'a.', 'Broken in the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinese', 'a.', 'Of or pertaining to China; peculiar to China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinese', 'n. sing. & pl.', 'A native or natives of China, or one of that\n   yellow race with oblique eyelids who live principally in China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinese', 'n. sing. & pl.', 'The language of China, which is monosyllabic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'n.', 'A small cleft, rent, or fissure, of greater length than\n   breadth; a gap or crack; as, the chinks of wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinked', 'imp. & p. p.', 'of Chink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinking', 'p. pr. & vb. n.', 'of Chink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'v. i.', 'To crack; to open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'v. t.', 'To cause to open in cracks or fissures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'v. t.', 'To fill up the chinks of; as, to chink a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'n.', 'A short, sharp sound, as of metal struck with a slight\n   degree of violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'n.', 'Money; cash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'v. t.', 'To cause to make a sharp metallic sound, as coins, small\n   pieces of metal, etc., by bringing them into collision with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chink', 'v. i.', 'To make a slight, sharp, metallic sound, as by the\n   collision of little pieces of money, or other small sonorous bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinky', 'a.', 'Full of chinks or fissures; gaping; opening in narrow\n   clefts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinned', 'a.', 'Having a chin; -- used chiefly in compounds; as,\n   short-chinned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinoidine', 'n.', 'See Quinodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinoline', 'n.', 'See Quinoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinone', 'n.', 'See Quinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinook', 'n.', 'One of a tribe of North American Indians now living in the\n   state of Washington, noted for the custom of flattening their skulls.\n   Chinooks also called Flathead Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinook', 'n.', 'A warm westerly wind from the country of the Chinooks,\n   sometimes experienced on the slope of the Rocky Mountains, in Montana\n   and the adjacent territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinook', 'n.', 'A jargon of words from various languages (the largest\n   proportion of which is from that of the Chinooks) generally understood\n   by all the Indian tribes of the northwestern territories of the United\n   States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinquapin', 'n.', 'A branching, nut-bearing tree or shrub (Castanea\n   pumila) of North America, from six to twenty feet high, allied to the\n   chestnut. Also, its small, sweet, edible nat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinsed', 'imp. & p. p.', 'of Chinse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinsing', 'p. pr. & vb. n.', 'of Chinse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chinse', 'v. t. & i.', 'To thrust oakum into (seams or chinks) with a\n   chisel , the point of a knife, or a chinsing iron; to calk slightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chintzes', 'pl. ', 'of Chintz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chintz', 'n.', 'Cotton cloth, printed with flowers and other devices, in a\n   number of different colors, and often glazed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chioppine', 'n.', 'Same as Chopine, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipped', 'imp. & p. p.', 'of Chip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipping', 'p. pr. & vb. n.', 'of Chip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'v. t.', 'To cut small pieces from; to diminish or reduce to shape,\n   by cutting away a little at a time; to hew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'v. t.', 'To break or crack, or crack off a portion of, as of an\n   eggshell in hatching, or a piece of crockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'v. t.', 'To bet, as with chips in the game of poker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'v. i.', 'To break or fly off in small pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'n.', 'A piece of wood, stone, or other substance, separated by an\n   ax, chisel, or cutting instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'n.', 'A fragment or piece broken off; a small piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'n.', 'Wood or Cuban palm leaf split into slips, or straw plaited in\n   a special manner, for making hats or bonnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'n.', 'Anything dried up, withered, or without flavor; -- used\n   contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'n.', 'One of the counters used in poker and other games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chip', 'n.', 'The triangular piece of wood attached to the log line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipmunk', 'n.', 'A squirrel-like animal of the genus Tamias, sometimes\n   called the striped squirrel, chipping squirrel, ground squirrel,\n   hackee. The common species of the United States is the Tamias striatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipper', 'v. i.', 'To chirp or chirrup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipper', 'a.', 'Lively; cheerful; talkative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chippeways', 'n. pl.', 'A tribe of Indians formerly inhabiting the northern\n   and western shores of Lake Superior; -- called also Objibways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipping', 'n.', 'A chip; a piece separated by a cutting or graving\n   instrument; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipping', 'n.', 'The act or process of cutting or breaking off small\n   pieces, as in dressing iron with a chisel, or reducing a timber or\n   block of stone to shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipping', 'n.', 'The breaking off in small pieces of the edges of potters\n   ware, porcelain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipping bird', '', 'The chippy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chipping squirrel', '', 'See Chipmunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chippy', 'a.', 'Abounding in, or resembling, chips; dry and tasteless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chippy', 'n.', 'A small American sparrow (Spizella socialis); very common\n   near dwelling; -- also called chipping bird and chipping sparrow, from\n   its simple note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chips', 'n.', 'A ships carpenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiragra', 'n.', 'Gout in the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiragrical', 'a.', 'Having the gout in the hand, or subject to that\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiretta', 'n.', 'A plant (Agathotes Chirayta) found in Northern India,\n   having medicinal properties to the gentian, and esteemed as a tonic and\n   febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirk', 'v. i.', 'To shriek; to gnash; to utter harsh or shrill cries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirk', 'v. i.', 'To chirp like a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirk', 'v. t.', 'To cheer; to enliven; as, to chirk one up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirk', 'v. i.', 'Lively; cheerful; in good spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirm', 'v. i.', 'To chirp or to make a mournful cry, as a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirognomy', 'n.', 'The art of judging character by the shape and\n   appearance of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirograph', 'n.', 'A writing which, requiring a counterpart, was engrossed\n   twice on the same piece of parchment, with a space between, in which\n   was written the word chirographum, through which the parchment was cut,\n   and one part given to each party. It answered to what is now called a\n   charter party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirograph', 'n.', 'The last part of a fine of land, commonly called the\n   foot of the fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirographer', 'n.', 'One who practice the art or business of writing or\n   engrossing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirographer', 'n.', 'See chirographist, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirographic', 'a.', 'Alt. of Chirographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirographical', 'a.', 'Of or pertaining to chirography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirographist', 'n.', 'A chirographer; a writer or engrosser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirographist', 'n.', 'One who tells fortunes by examining the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirography', 'n.', 'The art of writing or engrossing; handwriting; as,\n   skilled in chirography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirography', 'n.', 'The art of telling fortunes by examining the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirogymnast', 'n.', 'A mechanical contrivance for exercising the fingers\n   of a pianist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirological', 'a.', 'Relating to chirology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirologist', 'n.', 'One who communicates thoughts by signs made with the\n   hands and fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirology', 'n.', 'The art or practice of using the manual alphabet or of\n   communicating thoughts by sings made by the hands and fingers; a\n   substitute for spoken or written language in intercourse with the deaf\n   and dumb. See Dactylalogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromancer', 'n.', 'One who practices chiromancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromancy', 'n.', 'The art or practice of foretelling events, or of\n   telling the fortunes or the disposition of persons by inspecting the\n   hand; palmistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromanist', 'n.', 'Alt. of Chiromantist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromantist', 'n.', 'A chiromancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromantic', 'a.', 'Alt. of Chiromantical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromantical', 'a.', 'Of or pertaining to chiromancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiromonic', 'a.', 'Relating to chironomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chironomy', 'n.', 'The art of moving the hands in oratory or in pantomime;\n   gesture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiroplast', 'n.', 'An instrument to guid the hands and fingers of pupils\n   in playing on the piano, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiropodist', 'n.', 'One who treats diseases of the hands and feet;\n   especially, one who removes corns and bunions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiropody', 'n.', 'The art of treating diseases of the hands and feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirosophist', 'n.', 'A fortune teller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirped', 'imp. & p. p.', 'of Chirp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirping', 'p. pr. & vb. n.', 'of Chirp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirp', 'v. i.', 'To make a shop, sharp, cheerful, as of small birds or\n   crickets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirp', 'n.', 'A short, sharp note, as of a bird or insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirper', 'n.', 'One who chirps, or is cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirping', 'a.', 'Cheering; enlivening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirpingly', 'adv.', 'In a chirping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirre', 'v. i.', 'To coo, as a pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirruped', 'imp. & p. p.', 'of Chirrup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirruping', 'p. pr. & vb. n.', 'of Chirrup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirrup', 'v. t.', 'To quicken or animate by chirping; to cherup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirrup', 'v. i.', 'To chirp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirrup', 'n.', 'The act of chirping; a chirp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirrupy', 'a.', 'Cheerful; joyous; chatty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirurgeon', 'n.', 'A surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirurgeonly', 'adv.', 'Surgically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirurgery', 'n.', 'Surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirurgic', 'a.', 'Alt. of Chirurgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirurgical', 'a.', 'Surgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chisel', 'n.', 'A tool with a cutting edge on one end of a metal blade,\n   used in dressing, shaping, or working in timber, stone, metal, etc.; --\n   usually driven by a mallet or hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiseled', 'imp. & p. p.', 'of Chisel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiselled', '', 'of Chisel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiseling', 'p. pr. & vb. n.', 'of Chisel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiselling', '', 'of Chisel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chisel', 'v. t.', 'To cut, pare, gouge, or engrave with a chisel; as, to\n   chisel a block of marble into a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chisel', 'v. t.', 'To cut close, as in a bargain; to cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chisleu', 'n.', 'The ninth month of the Jewish ecclesiastical year,\n   answering to a part of November with a part of December.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chisley', 'a.', 'Having a large admixture of small pebbles or gravel; --\n   said of a soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chit', 'n.', 'The embryo or the growing bud of a plant; a shoot; a sprout;\n   as, the chits of Indian corn or of potatoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chit', 'n.', 'A child or babe; as, a forward chit; also, a young, small, or\n   insignificant person or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chit', 'n.', 'An excrescence on the body, as a wart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chit', 'n.', 'A small tool used in cleaving laths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chit', 'v. i.', 'To shoot out; to sprout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chit', '3d sing.', 'Chideth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitchat', 'n.', 'Familiar or trifling talk; prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitin', 'n.', 'A white amorphous horny substance forming the harder part\n   of the outer integument of insects, crustacea, and various other\n   invertebrates; entomolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitinization', 'n.', 'The process of becoming chitinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitinous', 'a.', 'Having the nature of chitin; consisting of, or\n   containing, chitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiton', 'n.', 'An under garment among the ancient Greeks, nearly\n   representing the modern shirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chiton', 'n.', 'One of a group of gastropod mollusks, with a shell composed\n   of eight movable dorsal plates. See Polyplacophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitter', 'v. i.', 'To chirp in a tremulous manner, as a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitter', 'v. i.', 'To shiver or chatter with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitterling', 'n.', 'The frill to the breast of a shirt, which when ironed\n   out resembled the small entrails. See Chitterlings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitterlings', 'n. pl.', 'The smaller intestines of swine, etc., fried for\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chittra', 'n.', 'The axis deer of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitty', 'a.', 'Full of chits or sprouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chitty', 'a.', 'Childish; like a babe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivachie', 'n.', 'A cavalry raid; hence, a military expedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalric', 'a.', 'Relating to chivalry; knightly; chivalrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalrous', 'a.', 'Pertaining to chivalry or knight-errantry; warlike;\n   heroic; gallant; high-spirited; high-minded; magnanimous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalrously', 'adv.', 'In a chivalrous manner; gallantly; magnanimously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalry', 'n.', 'A body or order of cavaliers or knights serving on\n   horseback; illustrious warriors, collectively; cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalry', 'n.', 'The dignity or system of knighthood; the spirit, usages,\n   or manners of knighthood; the practice of knight-errantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalry', 'n.', 'The qualifications or character of knights, as valor,\n   dexterity in arms, courtesy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalry', 'n.', 'A tenure of lands by knights service; that is, by the\n   condition of a knights performing service on horseback, or of\n   performing some noble or military service to his lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivalry', 'n.', 'Exploit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chive', 'n.', 'A filament of a stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chive', 'n.', 'A perennial plant (Allium Schoenoprasum); allied to the\n   onion. The young leaves are used in omelets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivied', 'imp. & p. p.', 'of Chivy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivying', 'p. pr. & vb. n.', 'of Chivy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chivy', 'v. t.', 'To goad, drive, hunt, throw, or pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlamydate', 'a.', 'Having a mantle; -- applied to certain gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlamyphore', 'n.', 'A small South American edentate (Chlamyphorus\n   truncatus, and C. retusus) allied to the armadillo. It is covered with\n   a leathery shell or coat of mail, like a cloak, attached along the\n   spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlamyses', 'pl. ', 'of Chlamys');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlamydes', 'pl. ', 'of Chlamys');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlamys', 'n.', 'A loose and flowing outer garment, worn by the ancient\n   Greeks; a kind of cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloasma', 'n.', 'A cutaneous affection characterized by yellow or\n   yellowish brown pigmented spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloral', 'n.', 'A colorless oily liquid, CCl3.CHO, of a pungent odor and\n   harsh taste, obtained by the action of chlorine upon ordinary or ethyl\n   alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloral', 'n.', 'Chloral hydrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloralamide', 'n.', 'A compound of chloral and formic amide used to\n   produce sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloralism', 'n.', 'A morbid condition of the system resulting from\n   excessive use of chloral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloralum', 'n.', 'An impure aqueous solution of chloride of aluminium,\n   used as an antiseptic and disinfectant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloranil', 'n.', 'A yellow crystalline substance, C6Cl4.O2, regarded as a\n   derivative of quinone, obtained by the action of chlorine on certain\n   benzene derivatives, as aniline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorate', 'n.', 'A salt of chloric acid; as, chlorate of potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloraurate', 'n.', 'See Aurochloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorhydric', 'a.', 'Same as Hydrochloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorhydrin', 'n.', 'One of a class of compounds formed from certain\n   polybasic alcohols (and especially glycerin) by the substitution of\n   chlorine for one or more hydroxyl groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloric', 'a.', 'Pertaining to, or obtained from, chlorine; -- said of\n   those compounds of chlorine in which this element has a valence of\n   five, or the next to its highest; as, chloric acid, HClO3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloridate', 'v. t.', 'To treat or prepare with a chloride, as a plate with\n   chloride of silver, for the purposes of photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloride', 'n.', 'A binary compound of chlorine with another element or\n   radical; as, chloride of sodium (common salt).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloridic', 'a.', 'Of or pertaining to a chloride; containing a chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloridize', 'v. t.', 'See Chloridate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorimetry', 'n.', 'See Chlorometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorinated', 'imp. & p. p.', 'of Chlorinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorinating', 'p. pr. & vb. n.', 'of Chlorinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorinate', 'v. t.', 'To treat, or cause to combine, with chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorination', 'n.', 'The act or process of subjecting anything to the\n   action of chlorine; especially, a process for the extraction of gold by\n   exposure of the auriferous material to chlorine gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorine', 'n.', 'One of the elementary substances, commonly isolated as a\n   greenish yellow gas, two and one half times as heavy as air, of an\n   intensely disagreeable suffocating odor, and exceedingly poisonous. It\n   is abundant in nature, the most important compound being common salt.\n   It is powerful oxidizing, bleaching, and disinfecting agent. Symbol Cl.\n   Atomic weight, 35.4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloriodic', 'a.', 'Compounded of chlorine and iodine; containing chlorine\n   and iodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloriodine', 'n.', 'A compound of chlorine and iodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorite', 'n.', 'The name of a group of minerals, usually of a green color\n   and micaceous to granular in structure. They are hydrous silicates of\n   alumina, iron, and magnesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorite', 'n.', 'Any salt of chlorous acid; as, chlorite of sodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloritic', 'a.', 'Pertaining to, or containing, chlorite; as, chloritic\n   sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlormethane', 'n.', 'A colorless gas, CH3Cl, of a sweet odor, easily\n   condensed to a liquid; -- called also methyl chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloro-', '', 'A prefix denoting that chlorine is an ingredient in the\n   substance named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorocruorin', 'n.', 'A green substance, supposed to be the cause of the\n   green color of the blood in some species of worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorodyne', 'n.', 'A patent anodyne medicine, containing opium,\n   chloroform, Indian hemp, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroform', 'n.', 'A colorless volatile liquid, CHCl3, having an ethereal\n   odor and a sweetish taste, formed by treating alcohol with chlorine and\n   an alkali. It is a powerful solvent of wax, resin, etc., and is\n   extensively used to produce anaesthesia in surgical operations; also\n   externally, to alleviate pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroformed', 'imp. & p. p.', 'of Chloroform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroforming', 'p. pr. & vb. n.', 'of Chloroform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroform', 'v. t.', 'To treat with chloroform, or to place under its\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroleucite', 'n.', 'Same as Chloroplastid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorometer', 'n.', 'An instrument to test the decoloring or bleaching\n   power of chloride of lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorometry', 'n.', 'The process of testing the bleaching power of any\n   combination of chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloropal', 'n.', 'A massive mineral, greenish in color, and opal-like in\n   appearance. It is essentially a hydrous silicate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloropeptic', 'a.', 'Of or pertaining to an acid more generally called\n   pepsin-hydrochloric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorophane', 'n.', 'A variety of fluor spar, which, when heated, gives a\n   beautiful emerald green light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorophane', 'n.', 'The yellowish green pigment in the inner segment of\n   the cones of the retina. See Chromophane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorophyll', 'n.', 'Literally, leaf green; a green granular matter formed\n   in the cells of the leaves (and other parts exposed to light) of\n   plants, to which they owe their green color, and through which all\n   ordinary assimilation of plant food takes place. Similar chlorophyll\n   granules have been found in the tissues of the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroplastid', 'n.', 'A granule of chlorophyll; -- also called\n   chloroleucite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloroplatinic', 'a.', 'See Platinichloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorosis', 'n.', 'The green sickness; an anaemic disease of young women,\n   characterized by a greenish or grayish yellow hue of the skin,\n   weakness, palpitation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorosis', 'n.', 'A disease in plants, causing the flowers to turn green\n   or the leaves to lose their normal green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorotic', 'a.', 'Pertaining to, or affected by, chlorosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorous', 'a.', 'Of, pertaining to, or derived from, chlorine; -- said of\n   those compounds of chlorine in which this element has a valence of\n   three, the next lower than in chloric compounds; as, chlorous acid,\n   HClO2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorous', 'a.', 'Pertaining to, or resembling, the electro-negative\n   character of chlorine; hence, electro-negative; -- opposed to basylous\n   or zincous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chlorpicrin', 'n.', 'A heavy, colorless liquid, CCl3.NO2, of a strong\n   pungent odor, obtained by subjecting picric acid to the action of\n   chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chloruret', 'n.', 'A chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choak', 'v. t. & i.', 'See Choke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choanoid', 'a.', 'Funnel-shaped; -- applied particularly to a hollow muscle\n   attached to the ball of the eye in many reptiles and mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chocard', 'n.', 'The chough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chocked', 'imp. & p. p.', 'of Chock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chocking', 'p. pr. & vb. n.', 'of Chock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'v. t.', 'To stop or fasten, as with a wedge, or block; to scotch;\n   as, to chock a wheel or cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'v. i.', 'To fill up, as a cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'n.', 'A wedge, or block made to fit in any space which it is\n   desired to fill, esp. something to steady a cask or other body, or\n   prevent it from moving, by fitting into the space around or beneath it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'n.', 'A heavy casting of metal, usually fixed near the gunwale. It\n   has two short horn-shaped arms curving inward, between which ropes or\n   hawsers may pass for towing, mooring, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'adv.', 'Entirely; quite; as, chock home; chock aft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'v. t.', 'To encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock', 'n.', 'An encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chockablock', 'a.', 'Hoisted as high as the tackle will admit; brought\n   close together, as the two blocks of a tackle in hoisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chock-full', 'a.', 'Quite full; choke-full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chocolate', 'n.', 'A paste or cake composed of the roasted seeds of the\n   Theobroma Cacao ground and mixed with other ingredients, usually sugar,\n   and cinnamon or vanilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chocolate', 'n.', 'The beverage made by dissolving a portion of the paste\n   or cake in boiling water or milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choctaws', 'n. pl.', 'A tribe of North American Indians (Southern\n   Appalachian); in early times noted for their pursuit of agriculture,\n   and for living at peace with the white settlers. They are now one of\n   the civilized tribes of the Indian Territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chode', '', 'the old imp. of chide. See Chide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chogset', 'n.', 'See Cunner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'n.', 'Act of choosing; the voluntary act of selecting or\n   separating from two or more things that which is preferred; the\n   determination of the mind in preferring one thing to another; election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'n.', 'The power or opportunity of choosing; option.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'n.', 'Care in selecting; judgment or skill in distinguishing what\n   is to be preferred, and in giving a preference; discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'n.', 'A sufficient number to choose among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'n.', 'The thing or person chosen; that which is approved and\n   selected in preference to others; selection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'n.', 'The best part; that which is preferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'superl.', 'Worthly of being chosen or preferred; select;\n   superior; precious; valuable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'superl.', 'Preserving or using with care, as valuable; frugal; --\n   used with of; as, to be choice of time, or of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choice', 'superl.', 'Selected with care, and due attention to preference;\n   deliberately chosen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choiceful', 'a.', 'Making choices; fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choicely', 'adv.', 'With care in choosing; with nice regard to preference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choicely', 'adv.', 'In a preferable or excellent manner; excellently;\n   eminently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choiceness', 'n.', 'The quality of being of particular value or worth;\n   nicely; excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choir', 'n.', 'A band or organized company of singers, especially in church\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choir', 'n.', 'That part of a church appropriated to the singers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choir', 'n.', 'The chancel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choked', 'imp. & p. p.', 'of Choke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choking', 'p. pr. & vb. n.', 'of Choke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. t.', 'To render unable to breathe by filling, pressing upon, or\n   squeezing the windpipe; to stifle; to suffocate; to strangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. t.', 'To obstruct by filling up or clogging any passage; to\n   block up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. t.', 'To hinder or check, as growth, expansion, progress, etc.;\n   to stifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. t.', 'To affect with a sense of strangulation by passion or\n   strong feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. t.', 'To make a choke, as in a cartridge, or in the bore of the\n   barrel of a shotgun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. i.', 'To have the windpipe stopped; to have a spasm of the\n   throat, caused by stoppage or irritation of the windpipe; to be\n   strangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'v. i.', 'To be checked, as if by choking; to stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'n.', 'A stoppage or irritation of the windpipe, producing the\n   feeling of strangulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'n.', 'The tied end of a cartridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke', 'n.', 'A constriction in the bore of a shotgun, case of a rocket,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chokeberry', 'n.', 'The small apple-shaped or pear-shaped fruit of an\n   American shrub (Pyrus arbutifolia) growing in damp thickets; also, the\n   shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chokecherry', 'n.', 'The astringent fruit of a species of wild cherry\n   (Prunus Virginiana); also, the bush or tree which bears such fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke damp', '', 'See Carbonic acid, under Carbonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chokedar', 'n.', 'A watchman; an officer of customs or police.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke-full', 'a.', 'Full to the brim; quite full; chock-full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke pear', '', 'A kind of pear that has a rough, astringent taste, and is\n   swallowed with difficulty, or which contracts the mucous membrane of\n   the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke pear', '', 'A sarcasm by which one is put to silence; anything that\n   can not be answered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choker', 'n.', 'One who, or that which, chokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choker', 'n.', 'A stiff wide cravat; a stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choke-strap', 'n.', 'A strap leading from the bellyband to the lower part\n   of the collar, to keep the collar in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choking', 'a.', 'That chokes; producing the feeling of strangulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choking', 'a.', 'Indistinct in utterance, as the voice of a person affected\n   with strong emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choky Chokey', 'a.', 'Tending to choke or suffocate, or having power to\n   suffocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choky Chokey', 'a.', 'Inclined to choke, as a person affected with strong\n   emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholaemaa', 'n.', 'A disease characterized by severe nervous symptoms,\n   dependent upon the presence of the constituents of the bile in the\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholagogue', 'a.', 'Promoting the discharge of bile from the system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholagogue', 'n.', 'An agent which promotes the discharge of bile from the\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholate', 'n.', 'A salt of cholic acid; as, sodium cholate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholecystis', 'n.', 'The gall bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholecystotomy', 'n.', 'The operation of making an opening in the gall\n   bladder, as for the removal of a gallstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choledology', 'n.', 'A treatise on the bile and bilary organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleic', 'a.', 'Pertaining to, or obtained from, bile; as, choleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choler', 'n.', 'The bile; -- formerly supposed to be the seat and cause of\n   irascibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choler', 'n.', 'Irritation of the passions; anger; wrath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholera', 'n.', 'One of several diseases affecting the digestive and\n   intestinal tract and more or less dangerous to life, esp. the one\n   commonly called Asiatic cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleraic', 'a.', 'Relating to, or resulting from, or resembling, cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleric', 'a.', 'Abounding with, or producing choler, or bile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleric', 'a.', 'Easily irritated; irascible; inclined to anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleric', 'a.', 'Angry; indicating anger; excited by anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholericly', 'adv.', 'In a choleric manner; angrily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleriform', 'a.', 'Resembling cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholerine', 'n.', 'The precursory symptoms of cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholerine', 'n.', 'The first stage of epidemic cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholerine', 'n.', 'A mild form of cholera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choleroid', 'a.', 'Choleriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholesteric', 'a.', 'Pertaining to cholesterin, or obtained from it; as,\n   cholesteric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholesterin', 'n.', 'A white, fatty, crystalline substance, tasteless and\n   odorless, found in animal and plant products and tissue, and especially\n   in nerve tissue, in the bile, and in gallstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choliamb', 'n.', 'Alt. of Choliambic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choliambic', 'n.', 'A verse having an iambus in the fifth place, and a\n   spondee in the sixth or last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholic', 'a.', 'Alt. of Cholinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholinic', 'a.', 'Pertaining to, or obtained from, the bile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choline', 'n.', 'See Neurine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholochrome', 'n.', 'See Bilirubin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cholophaein', 'n.', 'See Bilirubin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choltry', 'n.', 'A Hindoo caravansary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chomp', 'v. i.', 'To chew loudly and greedily; to champ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrification', 'n.', 'Formation of, or conversion into, cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrify', 'v. t. & i.', 'To convert, or be converted, into cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrigen', 'n.', 'The chemical basis of cartilage, converted by long\n   boiling in water into a gelatinous body called chondrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrigenous', 'a.', 'Affording chondrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrin', 'n.', 'A colorless, amorphous, nitrogenous substance, tasteless\n   and odorless, formed from cartilaginous tissue by long-continued action\n   of boiling water. It is similar to gelatin, and is a large ingredient\n   of commercial gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrite', 'n.', 'A meteoric stone characterized by the presence of\n   chondrules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondritic', 'a.', 'Granular; pertaining to, or having the granular\n   structure characteristic of, the class of meteorites called chondrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondritis', 'n.', 'An inflammation of cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondro-', '', 'A combining form meaning a grain, granular, granular\n   cartilage, cartilaginous; as, the chondrocranium, the cartilaginous\n   skull of the lower vertebrates and of embryos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrodite', 'n.', 'A fluosilicate of magnesia and iron, yellow to red in\n   color, often occurring in granular form in a crystalline limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondroganoidea', 'n.', 'An order of ganoid fishes, including the\n   sturgeons; -- so called on account of their cartilaginous skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrogen', 'n.', 'Same as Chondrigen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrogenesis', 'n.', 'The development of cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondroid', 'a.', 'Resembling cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrology', 'n.', 'The science which treats of cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondromata', 'pl. ', 'of Chondroma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondroma', 'n.', 'A cartilaginous tumor or growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrometer', 'n.', 'A steelyard for weighting grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondropterygian', 'a.', 'Having a cartilaginous skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondropterygian', 'n.', 'One of the Chondropterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondropterygii', 'n. pl.', 'A group of fishes, characterized by\n   cartilaginous fins and skeleton. It includes both ganoids (sturgeons,\n   etc.) and selachians (sharks); but is now often restricted to the\n   latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrostei', 'n. pl.', 'An order of fishes, including the sturgeons; -- so\n   named because the skeleton is cartilaginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrotomy', 'n.', 'The dissection of cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chondrule', 'n.', 'A peculiar rounded granule of some mineral, usually\n   enstatite or chrysolite, found imbedded more or less abundantly in the\n   mass of many meteoric stones, which are hence called chondrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chose', 'imp.', 'of Choose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chosen', 'p. p.', 'of Choose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chose', '', 'of Choose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choosing', 'p. pr. & vb. n.', 'of Choose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choose', 'v. t.', 'To make choice of; to select; to take by way of\n   preference from two or more objects offered; to elect; as, to choose\n   the least of two evils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choose', 'v. t.', 'To wish; to desire; to prefer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choose', 'v. i.', 'To make a selection; to decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Choose', 'v. i.', 'To do otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chooser', 'n.', 'One who chooses; one who has the power or right of\n   choosing; an elector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chopped', 'imp. & p. p.', 'of Chop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chopping', 'p. pr. & vb. n.', 'of Chop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. t.', 'To cut by striking repeatedly with a sharp instrument; to\n   cut into pieces; to mince; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. t.', 'To sever or separate by one more blows of a sharp\n   instrument; to divide; -- usually with off or down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. t.', 'To seize or devour greedily; -- with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. i.', 'To make a quick strike, or repeated strokes, with an ax or\n   other sharp instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. i.', 'To do something suddenly with an unexpected motion; to\n   catch or attempt to seize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. i.', 'To interrupt; -- with in or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. i.', 'To barter or truck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chop', 'v. i.', 'To exchange; substitute one thing for another.');
