  INSERT INTO words (word, word_type, description) VALUES  (    'Christianlike', 'a.', 'Becoming to a Christian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christianly', 'adv.', 'In a manner becoming the principles of the Christian\n   religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christianly', 'a.', 'Christianlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christianness', 'n.', 'Consonance with the doctrines of Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christless', 'a.', 'Without faith in Christ; unchristian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christlike', 'a.', 'Resembling Christ in character, actions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christly', 'a.', 'Christlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christmas', 'n.', 'An annual church festival (December 25) and in some\n   States a legal holiday, in memory of the birth of Christ, often\n   celebrated by a particular church service, and also by special gifts,\n   greetings, and hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christmastide', 'n.', 'The season of Christmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christocentric', 'a.', 'Making Christ the center, about whom all things are\n   grouped, as in religion or history; tending toward Christ, as the\n   central object of thought or emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christology', 'n.', 'A treatise on Christ; that department of theology\n   which treats of the personality, attributes, or life of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christom', 'n.', 'See Chrisom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christophany', 'n.', 'An appearance of Christ, as to his disciples after\n   the crucifixion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Christs-thorn', 'n.', 'One of several prickly or thorny shrubs found in\n   Palestine, especially the Paliurus aculeatus, Zizyphus Spina-Christi,\n   and Z. vulgaris. The last bears the fruit called jujube, and may be\n   considered to have been the most readily obtainable for the Crown of\n   Thorns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromascope', 'n.', 'An instrument for showing the optical effects of\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromate', 'n.', 'A salt of chromic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatic', 'a.', 'Relating to color, or to colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatic', 'a.', 'Proceeding by the smaller intervals (half steps or\n   semitones) of the scale, instead of the regular intervals of the\n   diatonic scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatical', 'a.', 'Chromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatically', 'adv.', 'In a chromatic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatics', 'n.', 'The science of colors; that part of optics which treats\n   of the properties of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatin', 'n.', 'Tissue which is capable of being stained by dyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatism', 'n.', 'The state of being colored, as in the case of images\n   formed by a lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatism', 'n.', 'An abnormal coloring of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatogenous', 'a.', 'Producing color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatography', 'n.', 'A treatise on colors');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatology', 'n.', 'A treatise on colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatophore', 'n.', 'A contractile cell or vesicle containing liquid\n   pigment and capable of changing its form or size, thus causing changes\n   of color in the translucent skin of such animals as possess them. They\n   are highly developed and numerous in the cephalopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatophore', 'n.', 'One of the granules of protoplasm, which in mass\n   give color to the part of the plant containing them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatoscope', 'n.', 'A reflecting telescope, part of which is made to\n   rotate eccentrically, so as to produce a ringlike image of a star,\n   instead of a point; -- used in studying the scintillation of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatosphere', 'n.', 'A chromosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatrope', 'n.', 'An instrument for exhibiting certain chromatic effects\n   of light (depending upon the persistence of vision and mixture of\n   colors) by means of rapidly rotating disks variously colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatrope', 'n.', 'A device in a magic lantern or stereopticon to produce\n   kaleidoscopic effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatype', 'n.', 'A colored photographic picture taken upon paper made\n   sensitive with potassium bichromate or some other salt of chromium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromatype', 'n.', 'The process by which such picture is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrome', 'n.', 'Same as Chromium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromic', 'a.', 'Pertaining to, or obtained from, chromium; -- said of the\n   compounds of chromium in which it has its higher valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromid', 'n.', 'One of the Chromidae, a family of fresh-water fishes\n   abundant in the tropical parts of America and Africa. Some are valuable\n   food fishes, as the bulti of the Nile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromidrosis', 'n.', 'Secretion of abnormally colored perspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromism', 'n.', 'Same as Chromatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromite', 'n.', 'A black submetallic mineral consisting of oxide of\n   chromium and iron; -- called also chromic iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromite', 'n.', 'A compound or salt of chromous hydroxide regarded as an\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromium', 'n.', 'A comparatively rare element occurring most abundantly in\n   the mineral chromite. Atomic weight 52.5. Symbol Cr. When isolated it\n   is a hard, brittle, grayish white metal, fusible with difficulty. Its\n   chief commercial importance is for its compounds, as potassium\n   chromate, lead chromate, etc., which are brilliantly colored and are\n   used dyeing and calico printing. Called also chrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromos', 'pl. ', 'of Chromo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromo', 'n.', 'A chromolithograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromoblast', 'n.', 'An embryonic cell which develops into a pigment cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromogen', '', 'Vegetable coloring matter other than green; chromule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromogen', '', 'Any colored compound, supposed to contain one or more\n   chromophores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromogenic', 'a.', 'Containing, or capable of forming, chromogen; as,\n   chromogenic bacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromograph', 'n.', 'An apparatus by which a number of copies of written\n   matter, maps, plans, etc., can be made; -- called also hectograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromoleucite', 'n.', 'A chromoplastid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromolithograph', 'n.', 'A picture printed in tints and colors by repeated\n   impressions from a series of stones prepared by the lithographic\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromolithographer', 'n.', 'One who is engaged in chromolithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromolithographic', 'a.', 'Pertaining to, or made by, chromolithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromolithography', 'n.', 'Lithography adapted to printing in inks of\n   various colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromophane', 'n.', 'A general name for the several coloring matters, red,\n   green, yellow, etc., present in the inner segments in the cones of the\n   retina, held in solution by fats, and slowly decolorized by light;\n   distinct from the photochemical pigments of the rods of the retina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromophore', 'n.', 'Any chemical group or residue (as NO2; N2; or O2)\n   which imparts some decided color to the compound of which it is an\n   ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromophotography', 'n.', 'The art of producing photographs in colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromophotolithograph', 'n.', 'A photolithograph printed in colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromoplastid', 'n.', 'A protoplasmic granule of some other color than\n   green; -- also called chromoleucite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromosome', 'n.', 'One of the minute bodies into which the chromatin of\n   the nucleus is resolved during mitotic cell division; the idant of\n   Weismann.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromosphere', 'n.', 'An atmosphere of rare matter, composed principally of\n   incandescent hydrogen gas, surrounding the sun and enveloping the\n   photosphere. Portions of the chromosphere are here and there thrown up\n   into enormous tongues of flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromospheric', 'a.', 'Of or pertaining to the chromosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromotype', 'n.', 'A sheet printed in colors by any process, as a\n   chromolithograph. See Chromolithograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromotype', 'n.', 'A photographic picture in the natural colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromous', 'a.', 'Of, pertaining to, or derived from, chromium, when this\n   element has a valence lower than that in chromic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chromule', 'n.', 'A general name for coloring matter of plants other than\n   chlorophyll, especially that of petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronic', 'a.', 'Relating to time; according to time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronic', 'a.', 'Continuing for a long time; lingering; habitual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronical', 'a.', 'Chronic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicle', 'n.', 'An historical register or account of facts or events\n   disposed in the order of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicle', 'n.', 'A narrative of events; a history; a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicle', 'n.', 'The two canonical books of the Old Testament in which\n   immediately follow 2 Kings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicled', 'imp. & p. p.', 'of Chronicle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicling', 'p. pr. & vb. n.', 'of Chronicle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicle', 'v. t.', 'To record in a history or chronicle; to record; to\n   register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronicler', 'n.', 'A writer of a chronicle; a recorder of events in the\n   order of time; an historian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronique', 'n.', 'A chronicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronogram', 'n.', 'An inscription in which certain numeral letters, made\n   to appear specially conspicuous, on being added together, express a\n   particular date or epoch, as in the motto of a medal struck by Gustavus\n   Adolphus in 1632: ChrIstVs DVX; ergo trIVMphVs.- the capitals of which\n   give, when added as numerals, the sum 1632.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronogram', 'n.', 'The record or inscription made by a chronograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronogrammatic', 'a.', 'Alt. of Chronogrammatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronogrammatical', 'a.', 'Belonging to a chronogram, or containing one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronogrammatist', 'n.', 'A writer of chronograms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronograph', 'n.', 'An instrument for measuring or recording intervals of\n   time, upon a revolving drum or strip of paper moved by clockwork. The\n   action of the stylus or pen is controlled by electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronograph', 'n.', 'Same as Chronogram, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronograph', 'n.', 'A chronoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronographer', 'n.', 'One who writes a chronography; a chronologer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronographic', 'a.', 'Of or pertaining to a chronograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronography', 'n.', 'A description or record of past time; history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronologer', 'n.', 'Same as Chronologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronologic', 'a.', 'Alt. of Chronological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronological', 'a.', 'Relating to chronology; containing an account of\n   events in the order of time; according to the order of time; as,\n   chronological tables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronologist', 'n.', 'Alt. of Chronologer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronologer', 'n.', 'A person who investigates dates of events and\n   transactions; one skilled in chronology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronologies', 'pl. ', 'of Chronology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronology', 'n.', 'The science which treats of measuring time by regular\n   divisions or periods, and which assigns to events or transactions their\n   proper dates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronometer', 'n.', 'An instrument for measuring time; a timekeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronometer', 'n.', 'A portable timekeeper, with a heavy compensation\n   balance, and usually beating half seconds; -- intended to keep time\n   with great accuracy for use an astronomical observations, in\n   determining longitude, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronometer', 'n.', 'A metronome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronometric', 'a.', 'Alt. of Chronometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronometrical', 'a.', 'Pertaining to a chronometer; measured by a\n   chronometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronometry', 'n.', 'The art of measuring time; the measuring of time by\n   periods or divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronopher', 'n.', 'An instrument signaling the correct time to distant\n   points by electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chronoscope', 'n.', 'An instrument for measuring minute intervals of time;\n   used in determining the velocity of projectiles, the duration of\n   short-lived luminous phenomena, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysalid', 'a.', 'Pertaining to a chrysalis; resembling a chrysalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysalids', 'pl. ', 'of Chrysalid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysalid', 'n.', 'See Chrysalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysalides', 'pl. ', 'of Chrysalis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysalis', 'n.', 'The pupa state of certain insects, esp. of butterflies,\n   from which the perfect insect emerges. See Pupa, and Aurelia (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysaniline', 'n.', 'A yellow substance obtained as a by-product in the\n   manufacture of rosaniline. It dyes silk a fine golden-yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysanthemum', 'n.', 'A genus of composite plants, mostly perennial, and\n   of many species including the many varieties of garden chrysanthemums\n   (annual and perennial); and also the feverfew and the oxeye daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysarobin', 'n.', 'A bitter, yellow substance forming the essential\n   constituent of Goa powder, and yielding chrysophanic acid proper; hence\n   formerly called also chrysphanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysaurin', 'n.', 'An orange-colored dyestuff, of artificial production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chryselephantine', 'a.', 'Composed of, or adorned with, gold and ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysene', 'n.', 'One of the higher aromatic hydrocarbons of coal tar,\n   allied to naphthalene and anthracene. It is a white crystalline\n   substance, C18H12, of strong blue fluorescence, but generally colored\n   yellow by impurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysoberyl', 'n.', 'A mineral, found in crystals, of a yellow to green or\n   brown color, and consisting of aluminia and glucina. It is very hard,\n   and is often used as a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysochlore', 'n.', 'A South African mole of the genus Chrysochloris; the\n   golden mole, the fur of which reflects brilliant metallic hues of green\n   and gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysocolla', 'n.', 'A hydrous silicate of copper, occurring massive, of a\n   blue or greenish blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysogen', 'n.', 'A yellow crystalline substance extracted from crude\n   anthracene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysography', 'n.', 'The art of writing in letters of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysography', 'n.', 'A writing executed in letters of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysoidine', 'n.', 'An artificial, yellow, crystalline dye,\n   C6H5N2.C6H3(NH2)2. Also, one of a group of dyestuffs resembling\n   chrysoidine proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysolite', 'n.', 'A mineral, composed of silica, magnesia, and iron, of a\n   yellow to green color. It is common in certain volcanic rocks; --\n   called also olivine and peridot. Sometimes used as a gem. The name was\n   also early used for yellow varieties of tourmaline and topaz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysology', 'n.', 'That branch of political economy which relates to the\n   production of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysopa', 'n.', 'A genus of neuropterous insects. See Lacewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysophane', 'n.', 'A glucoside extracted from rhubarb as a bitter,\n   yellow, crystalline powder, and yielding chrysophanic acid on\n   decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysophanic', 'a.', 'Pertaining to, or derived from, or resembling,\n   chrysophane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysoprase', 'n.', 'An apple-green variety of chalcedony, colored by\n   nickel. It has a dull flinty luster, and is sometimes used in jewelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysoprasus', 'n.', 'See Chrysoprase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysosperm', 'n.', 'The seed of gold; a means of creating gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysotype', 'n.', 'A photographic picture taken upon paper prepared by the\n   use of a sensitive salt of iron and developed by the application of\n   chloride of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chrysotype', 'n.', '2process, invented by Sir J.Herschel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chthonic', 'a.', 'Pertaining to the earth; earthy; as, chthonic religions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chthonophagia', 'n.', 'Alt. of Chthonophagy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chthonophagy', 'n.', 'A disease characterized by an irresistible desire to\n   eat earth, observed in some parts of the southern United States, the\n   West Indies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chub', 'n.', 'A species to fresh-water fish of the Cyprinidae or Carp\n   family. The common European species is Leuciscus cephalus; the cheven.\n   In America the name is applied to various fishes of the same family, of\n   the genera Semotilus, Squalius, Ceratichthys, etc., and locally to\n   several very different fishes, as the tautog, black bass, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chubbed', 'a.', 'Chubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chubbedness', 'n.', 'The state of being chubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chubby', 'a.', 'Like a chub; plump, short, and thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chub-faced', 'a.', 'Having a plump, short face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chucked', 'imp. & p. p.', 'of Chuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chucking', 'p. pr. & vb. n.', 'of Chuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'v. i.', 'To make a noise resembling that of a hen when she calls\n   her chickens; to cluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'v. i.', 'To chuckle; to laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'v. t.', 'To call, as a hen her chickens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'The chuck or call of a hen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A sudden, small noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A word of endearment; -- corrupted from chick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chucked', 'imp. & p. p.', 'of Chuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chucking', 'p. pr. & vb. n.', 'of Chuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'v. t.', 'To strike gently; to give a gentle blow to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'v. t.', 'To toss or throw smartly out of the hand; to pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'v. t.', 'To place in a chuck, or hold by means of a chuck, as in\n   turning; to bore or turn (a hole) in a revolving piece held in a chuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A slight blow or pat under the chin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A short throw; a toss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A contrivance or machine fixed to the mandrel of a lathe,\n   for holding a tool or the material to be operated upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A small pebble; -- called also chuckstone and chuckiestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A game played with chucks, in which one or more are tossed\n   up and caught; jackstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck', 'n.', 'A piece of the backbone of an animal, from between the neck\n   and the collar bone, with the adjoining parts, cut for cooking; as, a\n   chuck steak; a chuck roast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckled', 'imp. & p. p.', 'of Chuckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckling', 'p. pr. & vb. n.', 'of Chuckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckle', 'v. t.', 'To call, as a hen her chickens; to cluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckle', 'v. t.', 'To fondle; to cocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckle', 'n.', 'A short, suppressed laugh; the expression of satisfaction,\n   exultation, or derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckle', 'v. i.', 'To laugh in a suppressed or broken manner, as\n   expressing inward satisfaction, exultation, or derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chucklehead', 'n.', 'A person with a large head; a numskull; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuckleheaded', 'a.', 'Having a large head; thickheaded; dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuck-Wills-widow', 'n.', 'A species of goatsucker (Antrostomus\n   Carolinensis); of the southern United States; -- so called from its\n   note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chud', 'v. t.', 'To champ; to bite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuet', 'n.', 'Minced meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chufa', 'n.', 'A sedgelike plant (Cyperus esculentus) producing edible\n   tubers, native about the Mediterranean, now cultivated in many regions;\n   the earth almond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuff', 'n.', 'A coarse or stupid fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuff', 'a.', 'Stupid; churlish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuffily', 'adv.', 'Clownishly; surlily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuffiness', 'n.', 'The quality of being chuffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuffy', 'a.', 'Fat or puffed out in the cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuffy', 'a.', 'Rough; clownish; surly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chulan', 'n.', 'The fragrant flowers of the Chloranthus inconspicuus, used\n   in China for perfuming tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chum', 'n.', 'A roommate, especially in a college or university; an old and\n   intimate friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chummed', 'imp. p. p.', 'of Chum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chumming', 'p. pr. & vb. n.', 'of Chum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chum', 'v. i.', 'To occupy a chamber with another; as, to chum together at\n   college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chum', 'n.', 'Chopped pieces of fish used as bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chump', 'n.', 'A short, thick, heavy piece of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chunam', 'n.', 'Quicklime; also, plaster or mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chunk', 'n.', 'A short, thick piece of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chunky', 'a.', 'Short and thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'A building set apart for Christian worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'A Jewish or heathen temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'A formally organized body of Christian believers worshiping\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'A body of Christian believers, holding the same creed,\n   observing the same rites, and acknowledging the same ecclesiastical\n   authority; a denomination; as, the Roman Catholic church; the\n   Presbyterian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'The collective body of Christians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'Any body of worshipers; as, the Jewish church; the church\n   of Brahm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'n.', 'The aggregate of religious influences in a community;\n   ecclesiastical influence, authority, etc.; as, to array the power of\n   the church against some moral evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churched', 'imp. & p. p.', 'of Church');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churching', 'p. pr. & vb. n.', 'of Church');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church', 'v. t.', 'To bless according to a prescribed form, or to unite\n   with in publicly returning thanks in church, as after deliverance from\n   the dangers of childbirth; as, the churching of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church-ale', 'n.', 'A church or parish festival (as in commemoration of the\n   dedication of a church); at which much ale was used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church-bench', 'n.', 'A seat in the porch of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchdom', 'n.', 'The institution, government, or authority of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchgoer', 'n.', 'One who attends church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchgoing', 'a.', 'Habitually attending church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchgoing', 'a.', 'Summoning to church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church-haw', 'n.', 'Churchyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchism', 'n.', 'Strict adherence to the forms or principles of some\n   church organization; sectarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchless', 'a.', 'Without a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchlike', 'a.', 'Befitting a church or a churchman; becoming to a\n   clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchliness', 'n.', 'Regard for the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchly', 'a.', 'Pertaining to, or suitable for, the church;\n   ecclesiastical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchmen', 'pl. ', 'of Churchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchman', 'n.', 'An ecclesiastic or clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchman', 'n.', 'An Episcopalian, or a member of the Established Church\n   of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchman', 'n.', 'One was is attached to, or attends, church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchmanly', 'a.', 'Pertaining to, or becoming, a churchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchmanship', 'n.', 'The state or quality of being a churchman;\n   attachment to the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Church modes', '', 'The modes or scales used in ancient church music. See\n   Gregorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchship', 'n.', 'State of being a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchwarden', 'n.', 'One of the officers (usually two) in an Episcopal\n   church, whose duties vary in different dioceses, but always include the\n   provision of what is necessary for the communion service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchwarden', 'n.', 'A clay tobacco pipe, with a long tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchwardenship', 'n.', 'The office of a churchwarden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchy', 'a.', 'Relating to a church; unduly fond of church forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churchyard', 'n.', 'The ground adjoining a church, in which the dead are\n   buried; a cemetery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churl', 'n.', 'A rustic; a countryman or laborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churl', 'n.', 'A rough, surly, ill-bred man; a boor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churl', 'n.', 'A selfish miser; an illiberal person; a niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churl', 'a.', 'Churlish; rough; selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churlish', 'a.', 'Like a churl; rude; cross-grained; ungracious; surly;\n   illiberal; niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churlish', 'a.', 'Wanting pliancy; unmanageable; unyielding; not easily\n   wrought; as, a churlish soil; the churlish and intractable nature of\n   some minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churlishly', 'adv.', 'In a churlish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churlishness', 'n.', 'Rudeness of manners or temper; lack of kindness or\n   courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churly', 'a.', 'Rude; churlish; violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churme', 'n.', 'Alt. of Chirm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chirm', 'n.', 'Clamor, or confused noise; buzzing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churn', 'v. t.', 'A vessel in which milk or cream is stirred, beaten, or\n   otherwise agitated (as by a plunging or revolving dasher) in order to\n   separate the oily globules from the other parts, and obtain butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churned', 'imp. & p. p.', 'of Churn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churning', 'p. pr. & vb. n.', 'of Churn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churn', 'v. t.', 'To stir, beat, or agitate, as milk or cream in a churn,\n   in order to make butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churn', 'v. t.', 'To shake or agitate with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churn', 'v. i.', 'To perform the operation of churning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churning', 'n.', 'The act of one who churns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churning', 'n.', 'The quantity of butter made at one operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churrus', 'n.', 'A powerfully narcotic and intoxicating gum resin which\n   exudes from the flower heads, seeds, etc., of Indian hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Churrworm', 'n.', 'An insect that turns about nimbly; the mole cricket; --\n   called also fan cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chuse', 'v. t.', 'See Choose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chute', 'n.', 'A framework, trough, or tube, upon or through which objects\n   are made to slide from a higher to a lower level, or through which\n   water passes to a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chute', 'n.', 'See Shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chutney', 'n.', 'Alt. of Chutnee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chutnee', 'n.', 'A warm or spicy condiment or pickle made in India,\n   compounded of various vegetable substances, sweets, acids, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylaceous', 'a.', 'Possessed of the properties of chyle; consisting of\n   chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylaqueous', 'a.', 'Consisting of chyle much diluted with water; -- said\n   of a liquid which forms the circulating fluid of some inferior animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chyle', 'n.', 'A milky fluid containing the fatty matter of the food in a\n   state of emulsion, or fine mechanical division; formed from chyme by\n   the action of the intestinal juices. It is absorbed by the lacteals,\n   and conveyed into the blood by the thoracic duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylifaction', 'n.', 'The act or process by which chyle is formed from food\n   in animal bodies; chylification, -- a digestive process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylifactive', 'a.', 'Producing, or converting into, chyle; having the\n   power to form chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chyliferous', 'a.', 'Transmitting or conveying chyle; as, chyliferous\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylific', 'a.', 'Chylifactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylification', 'n.', 'The formation of chyle. See Chylifaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylificatory', 'a.', 'Chylifactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylify', 'v. t. & i.', 'To make chyle of; to be converted into chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylopoetic', 'a.', 'Concerned in the formation of chyle; as, the\n   chylopoetic organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chylous', 'a.', 'Consisting of, or similar to, chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chyluria', 'n.', 'A morbid condition in which the urine contains chyle or\n   fatty matter, giving it a milky appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chyme', 'n.', 'The pulpy mass of semi-digested food in the small intestines\n   just after its passage from the stomach. It is separated in the\n   intestines into chyle and excrement. See Chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymic', '', 'Alt. of Chymistry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymist', '', 'Alt. of Chymistry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymistry', '', 'See Chemic, Chemist, Chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymiferous', 'a.', 'Bearing or containing chyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymification', 'n.', 'The conversion of food into chyme by the digestive\n   action of gastric juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymify', 'v. t.', 'To form into chyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chymous', 'a.', 'Of or pertaining to chyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Chyometer', 'n.', 'An instrument for measuring liquids. It consists of a\n   piston moving in a tube in which is contained the liquid, the quantity\n   expelled being indicated by the graduation upon the piston rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cibarious', 'a.', 'Pertaining to food; edible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cibation', 'n.', 'The act of taking food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cibation', 'n.', 'The process or operation of feeding the contents of the\n   crucible with fresh material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cibol', 'n.', 'A perennial alliaceous plant (Allium fistulosum); sometimes\n   called Welsh onion. Its fistular leaves areused in cookery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciboria', 'pl. ', 'of Ciborium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciborium', 'n.', 'A canopy usually standing free and supported on four\n   columns, covering the high altar, or, very rarely, a secondary altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciborium', 'n.', 'The coffer or case in which the host is kept; the pyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicadas', 'pl. ', 'of Cicada');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicadae', 'pl. ', 'of Cicada');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicada', 'n.', 'Any species of the genus Cicada. They are large hemipterous\n   insects, with nearly transparent wings. The male makes a shrill sound\n   by peculiar organs in the under side of the abdomen, consisting of a\n   pair of stretched membranes, acted upon by powerful muscles. A noted\n   American species (C. septendecim) is called the seventeen year locust.\n   Another common species is the dogday cicada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicala', 'n.', 'A cicada. See Cicada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrice', 'n.', 'A cicatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatricial', 'a.', 'Relating to, or having the character of, a cicatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatricle', 'n.', 'The germinating point in the embryo of a seed; the\n   point in the yolk of an egg at which development begins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrisive', 'a.', 'Tending to promote the formation of a cicatrix; good\n   for healing of a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrices', 'pl. ', 'of Cicatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrix', 'n.', 'The pellicle which forms over a wound or breach of\n   continuity and completes the process of healing in the latter, and\n   which subsequently contracts and becomes white, forming the scar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrizant', 'n.', 'A medicine or application that promotes the healing of\n   a sore or wound, or the formation of a cicatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrization', 'n.', 'The process of forming a cicatrix, or the state of\n   being cicatrized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrized', 'imp. & p. p.', 'of Cicatrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrizing', 'p. pr. & vb. n.', 'of Cicatrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrize', 'v. t.', 'To heal or induce the formation of a cicatrix in, as\n   in wounded or ulcerated flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrize', 'v. i.', 'To heal; to have a new skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicatrose', 'a.', 'Full of scars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicely', 'n.', 'Any one of several umbelliferous plants, of the genera\n   Myrrhis, Osmorrhiza, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicero', 'n.', 'Pica type; -- so called by French printers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciceroni', 'pl. ', 'of Cicerone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicerones', 'pl. ', 'of Cicerone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicerone', 'n.', 'One who shows strangers the curiosities of a place; a\n   guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciceronian', 'a.', 'Resembling Cicero in style or action; eloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciceronianism', 'n.', 'Imitation of, or resemblance to, the style or action\n   Cicero; a Ciceronian phrase or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cichoraceous', 'a.', 'Belonging to, or resembling, a suborder of composite\n   plants of which the chicory (Cichorium) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cich-pea', 'n.', 'The chick-pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicisbeism', 'n.', 'The state or conduct of a cicisbeo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicisbei', 'pl. ', 'of Cicisbeo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicisbeo', 'n.', 'A professed admirer of a married woman; a dangler about\n   women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicisbeo', 'n.', 'A knot of silk or ribbon attached to a fan, walking\n   stick, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciclatoun', 'n.', 'A costly cloth, of uncertain material, used in the\n   Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicurate', 'v. t.', 'To tame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicuration', 'n.', 'The act of taming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicuta', 'n.', 'a genus of poisonous umbelliferous plants, of which the\n   water hemlock or cowbane is best known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cicutoxin', 'n.', 'The active principle of the water hemlock (Cicuta)\n   extracted as a poisonous gummy substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cid', 'n.', 'Chief or commander; in Spanish literature, a title of Ruy\n   Diaz, Count of Bivar, a champion of Christianity and of the old Spanish\n   royalty, in the 11th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cid', 'n.', 'An epic poem, which celebrates the exploits of the Spanish\n   national hero, Ruy Diaz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cider', 'n.', 'The expressed juice of apples. It is used as a beverage, for\n   making vinegar, and for other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciderist', 'n.', 'A maker of cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciderkin', 'n.', 'A kind of weak cider made by steeping the refuse pomace\n   in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ci-devant', 'a.', 'Former; previous; of times gone by; as, a ci-devant\n   governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cierge', 'n.', 'A wax candle used in religous rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cigar', 'n.', 'A small roll of tobacco, used for smoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cigarette', 'n.', 'A little cigar; a little fine tobacco rolled in paper\n   for smoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilia', 'n. pl.', 'The eyelashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilia', 'n. pl.', 'Small, generally microscopic, vibrating appendages\n   lining certain organs, as the air passages of the higher animals, and\n   in the lower animals often covering also the whole or a part of the\n   exterior. They are also found on some vegetable organisms. In the\n   Infusoria, and many larval forms, they are locomotive organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilia', 'n. pl.', 'Hairlike processes, commonly marginal and forming a\n   fringe like the eyelash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilia', 'n. pl.', 'Small, vibratory, swimming organs, somewhat resembling\n   true cilia, as those of Ctenophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliary', 'a.', 'Pertaining to the cilia, or eyelashes. Also applied to\n   special parts of the eye itself; as, the ciliary processes of the\n   choroid coat; the ciliary muscle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliary', 'a.', 'Pertaining to or connected with the cilia in animal or\n   vegetable organisms; as, ciliary motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliata', 'n. pl.', 'One of the orders of Infusoria, characterized by\n   having cilia. In some species the cilia cover the body generally, in\n   others they form a band around the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliate', 'a.', 'Alt. of Ciliated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliated', 'a.', 'Provided with, or surrounded by, cilia; as, a ciliate\n   leaf; endowed with vibratory motion; as, the ciliated epithelium of the\n   windpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilice', 'n.', 'A kind of haircloth undergarment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilician', 'a.', 'Of or pertaining to Cilicia in Asia Minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilician', 'n.', 'A native or inhabitant of Cilicia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilicious', 'a.', 'Made, or consisting, of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliform', 'a.', 'Alt. of Ciliiform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliiform', 'a.', 'Having the form of cilia; very fine or slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciliograde', 'a.', 'Moving by means of cilia, or cilialike organs; as, the\n   ciliograde Medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cilium', 'n.', 'See Cilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cill', 'n.', 'See Sill., n. a foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cillosis', 'n.', 'A spasmodic trembling of the upper eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cima', 'n.', 'A kind of molding. See Cyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimar', 'n.', 'See Simar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimbal', 'n.', 'A kind of confectionery or cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimbia', 'n.', 'A fillet or band placed around the shaft of a column as if\n   to strengthen it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimbrian', 'a.', 'Of or pertaining to the Cimbri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimbrian', 'n.', 'One of the Cimbri. See Cimbric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimbric', 'a.', 'Pertaining to the Cimbri, an ancient tribe inhabiting\n   Northern Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimbric', 'n.', 'The language of the Cimbri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimeliarch', 'n.', 'A superintendent or keeper of a churchs valuables; a\n   churchwarden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimeter', 'n.', 'See Scimiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimices', 'pl. ', 'of Cimex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimex', 'n.', 'A genus of hemipterous insects of which the bedbug is the\n   best known example. See Bedbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimia', 'n.', 'See Cimbia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimiss', 'n.', 'The bedbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimmerian', 'a.', 'Pertaining to the Cimmerii, a fabulous people, said to\n   have lived, in very ancient times, in profound and perpetual darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimmerian', 'a.', 'Without any light; intensely dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cimolite', 'n.', 'A soft, earthy, clayey mineral, of whitish or grayish\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinch', 'n.', 'A strong saddle girth, as of canvas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinch', 'n.', 'A tight grip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchona', 'n.', 'A genus of trees growing naturally on the Andes in Peru\n   and adjacent countries, but now cultivated in the East Indies,\n   producing a medicinal bark of great value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchona', 'n.', 'The bark of any species of Cinchona containing three per\n   cent. or more of bitter febrifuge alkaloids; Peruvian bark; Jesuits\n   bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchonaceous', 'a.', 'Allied or pertaining to cinchona, or to the plants\n   that produce it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchonic', 'a.', 'Belonging to, or obtained from, cinchona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchonidine', 'n.', 'One of the quinine group of alkaloids, found\n   especially in red cinchona bark. It is a white crystalline substance,\n   C19H22N2O, with a bitter taste and qualities similar to, but weaker\n   than, quinine; -- sometimes called also cinchonidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchonine', 'n.', 'One of the quinine group of alkaloids isomeric with and\n   resembling cinchonidine; -- called also cinchonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchonism', 'n.', 'A condition produced by the excessive or long-continued\n   use of quinine, and marked by deafness, roaring in the ears, vertigo,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinchonize', 'v. t.', 'To produce cinchonism in; to poison with quinine or\n   with cinchona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cincinnati epoch', '', 'An epoch at the close of the American lower\n   Silurian system. The rocks are well developed near Cincinnati, Ohio.\n   The group includes the Hudson River and Lorraine shales of New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cincture', 'n.', 'A belt, a girdle, or something worn round the body, -- as\n   by an ecclesiastic for confining the alb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cincture', 'n.', 'That which encompasses or incloses; an inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cincture', 'n.', 'The fillet, listel, or band next to the apophyge at the\n   extremity of the shaft of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinctured', 'n.', 'Having or wearing a cincture or girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinder', 'n.', 'Partly burned or vitrified coal, or other combustible, in\n   which fire is extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinder', 'n.', 'A hot coal without flame; an ember.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinder', 'n.', 'A scale thrown off in forging metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinder', 'n.', 'The slag of a furnace, or scoriaceous lava from a volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cindery', 'a.', 'Resembling, or composed of, cinders; full of cinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinefaction', 'n.', 'Cineration; reduction to ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinematic', 'a.', 'Alt. of Cinematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinematical', 'a.', 'See Kinematic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinematics', 'n. sing.', 'See Kinematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cineraceous', 'a.', 'Like ashes; ash-colored; cinereous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cineraria', 'n.', 'A Linnaean genus of free-flowering composite plants,\n   mostly from South Africa. Several species are cultivated for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinerary', 'a.', 'Pertaining to ashes; containing ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cineration', 'n.', 'The reducing of anything to ashes by combustion;\n   cinefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinereous', 'a.', 'Like ashes; ash-colored; grayish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinerescent', 'a.', 'Somewhat cinereous; of a color somewhat resembling\n   that of wood ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cineritious', 'a.', 'Like ashes; having the color of ashes, -- as the\n   cortical substance of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinerulent', 'a.', 'Full of ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingalese', 'n. sing. & pl.', 'A native or natives of Ceylon descended from\n   its primitive inhabitants');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingalese', 'n. sing. & pl.', 'the language of the Cingalese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingalese', 'a.', 'Of or pertaining to the Cingalese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingle', 'n.', 'A girth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingulum', 'n.', 'A distinct girdle or band of color; a raised spiral line\n   as seen on certain univalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingulum', 'n.', 'The clitellus of earthworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cingulum', 'n.', 'The base of the crown of a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnabar', 'n.', 'Red sulphide of mercury, occurring in brilliant red\n   crystals, and also in red or brown amorphous masses. It is used in\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnabar', 'n.', 'The artificial red sulphide of mercury used as a pigment;\n   vermilion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnabarine', 'a.', 'Pertaining to, or resembling, cinnabar; consisting of\n   cinnabar, or containing it; as, cinnabarine sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamene', 'n.', 'Styrene (which was formerly called cinnamene because\n   obtained from cinnamic acid). See Styrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamic', 'a.', 'Pertaining to, or obtained from, cinnamon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamomic', 'a.', 'See Cinnamic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamon', 'n.', 'The inner bark of the shoots of Cinnamomum Zeylanicum, a\n   tree growing in Ceylon. It is aromatic, of a moderately pungent taste,\n   and is one of the best cordial, carminative, and restorative spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamon', 'n.', 'Cassia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamone', 'n.', 'A yellow crystalline substance, (C6H5.C2H2)2CO, the\n   ketone of cinnamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnamyl', 'n.', 'The hypothetical radical, (C6H5.C2H2)2C, of cinnamic\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinnoline', 'n.', 'A nitrogenous organic base, C8H6N2, analogous to\n   quinoline, obtained from certain complex diazo compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinque', 'n.', 'Five; the number five in dice or cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinquecento', 'n. & a.', 'The sixteenth century, when applied to Italian\n   art or literature; as, the sculpture of the Cinquecento; Cinquecento\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinquefoil', 'n.', 'The name of several different species of the genus\n   Potentilla; -- also called five-finger, because of the resemblance of\n   its leaves to the fingers of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinquefoil', 'n.', 'An ornamental foliation having five points or cups,\n   used in windows, panels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinque-pace', 'n.', 'A lively dance (called also galliard); the steps of\n   which were regulated by the number five.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinque Ports', '', 'Five English ports, to which peculiar privileges were\n   anciently accorded; -- viz., Hastings, Romney, Hythe, Dover, and\n   Sandwich; afterwards increased by the addition of Winchelsea, Rye, and\n   some minor places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinque-spotted', 'a.', 'Five-spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinter', 'n.', 'See Center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cinura', 'n. pl.', 'The group of Thysanura which includes Lepisma and\n   allied forms; the bristletails. See Bristletail, and Lepisma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cion', 'n.', 'See Scion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'n.', 'A character [0] which, standing by itself, expresses\n   nothing, but when placed at the right hand of a whole number, increases\n   its value tenfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'n.', 'One who, or that which, has no weight or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'n.', 'A character in general, as a figure or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'n.', 'A combination or interweaving of letters, as the initials\n   of a name; a device; a monogram; as, a painters cipher, an engravers\n   cipher, etc. The cut represents the initials N. W.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'n.', 'A private alphabet, system of characters, or other mode of\n   writing, contrived for the safe transmission of secrets; also, a\n   writing in such characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'a.', 'Of the nature of a cipher; of no weight or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciphered', 'imp. & p. p.', 'of Cipher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciphering', 'p. pr. & vb. n.', 'of Cipher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'v. i.', 'To use figures in a mathematical process; to do sums in\n   arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'v. t.', 'To write in occult characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'v. t.', 'To get by ciphering; as, to cipher out the answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'v. t.', 'To decipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipher', 'v. t.', 'To designate by characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipherer', 'n.', 'One who ciphers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipherhood', 'n.', 'Nothingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cipolin', 'n.', 'A whitish marble, from Rome, containiing pale greenish\n   zones. It consists of calcium carbonate, with zones and cloudings of\n   talc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cippi', 'pl. ', 'of Cippus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cippus', 'n.', 'A small, low pillar, square or round, commonly having an\n   inscription, used by the ancients for various purposes, as for\n   indicating the distances of places, for a landmark, for sepulchral\n   inscriptions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circ', 'n.', 'An amphitheatrical circle for sports; a circus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circar', 'n.', 'A district, or part of a province. See Sircar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circassian', 'a.', 'Of or pertaining to Circassia, in Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circassian', 'n.', 'A native or inhabitant of Circassia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circean', 'a.', 'Having the characteristics of Circe, daughter of Sol and\n   Perseis, a mythological enchantress, who first charmed her victims and\n   then changed them to the forms of beasts; pleasing, but noxious; as, a\n   Circean draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circensial', 'a.', 'Alt. of Circensian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circensian', 'a.', 'Of or pertaining to, or held in, the Circus, In Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circinal', 'a.', 'Circinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circinate', 'a.', 'Rolled together downward, the tip occupying the center;\n   -- a term used in reference to foliation or leafing, as in ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circinate', 'v. t.', 'To make a circle around; to encompass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circination', 'n.', 'An orbicular motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circination', 'n.', 'A circle; a concentric layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A plane figure, bounded by a single curve line called its\n   circumference, every part of which is equally distant from a point\n   within it, called the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'The line that bounds such a figure; a circumference; a\n   ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'An instrument of observation, the graduated limb of which\n   consists of an entire circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A round body; a sphere; an orb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'Compass; circuit; inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A company assembled, or conceived to assemble, about a\n   central point of interest, or bound by a common tie; a class or\n   division of society; a coterie; a set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A circular group of persons; a ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A series ending where it begins, and repeating itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A form of argument in which two or more unproved statements\n   are used to prove each other; inconclusive reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'Indirect form of words; circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'A territorial division or district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circled', 'imp. & p. p.', 'of Circle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circling', 'p. pr. & vb. n.', 'of Circle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'To move around; to revolve around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'n.', 'To encompass, as by a circle; to surround; to inclose; to\n   encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circle', 'v. i.', 'To move circularly; to form a circle; to circulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circled', 'a.', 'Having the form of a circle; round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circler', 'n.', 'A mean or inferior poet, perhaps from his habit of\n   wandering around as a stroller; an itinerant poet. Also, a name given\n   to the cyclic poets. See under Cyclic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circlet', 'n.', 'A little circle; esp., an ornament for the person, having\n   the form of a circle; that which encircles, as a ring, a bracelet, or a\n   headband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circlet', 'n.', 'A round body; an orb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circlet', 'n.', 'A circular piece of wood put under a dish at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circocele', 'n.', 'See Cirsocele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'The act of moving or revolving around, or as in a circle\n   or orbit; a revolution; as, the periodical circuit of the earth round\n   the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'The circumference of, or distance round, any space; the\n   measure of a line round an area.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'That which encircles anything, as a ring or crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'The space inclosed within a circle, or within limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'A regular or appointed journeying from place to place in\n   the exercise of ones calling, as of a judge, or a preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'A certain division of a state or country, established by\n   law for a judge or judges to visit, for the administration of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'A district in which an itinerant preacher labors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'n.', 'Circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'v. i.', 'To move in a circle; to go round; to circulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuit', 'v. t.', 'To travel around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuiteer', 'n.', 'A circuiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuiter', 'n.', 'One who travels a circuit, as a circuit judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuition', 'n.', 'The act of going round; circumlocution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuitous', 'a.', 'Going round in a circuit; roundabout; indirect; as, a\n   circuitous road; a circuitous manner of accomplishing an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circuity', 'n.', 'A going round in a circle; a course not direct; a\n   roundabout way of proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulable', 'a.', 'That may be circulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'In the form of, or bounded by, a circle; round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'repeating itself; ending in itself; reverting to the\n   point of beginning; hence, illogical; inconclusive; as, circular\n   reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'Adhering to a fixed circle of legends; cyclic; hence,\n   mean; inferior. See Cyclic poets, under Cyclic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'Addressed to a circle, or to a number of persons having a\n   common interest; circulated, or intended for circulation; as, a\n   circular letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'Perfect; complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'A circular letter, or paper, usually printed, copies of\n   which are addressed or given to various persons; as, a business\n   circular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circular', 'a.', 'A sleeveless cloak, cut in circular form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circularity', 'n.', 'The quality or state of being circular; a circular\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circularly', 'adv.', 'In a circular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulary', 'a.', 'Circular; illogical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciorculated', 'imp. & p. p.', 'of Circulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulating', 'P. pr. & vb. n.', 'of Circulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulate', 'v. i.', 'To move in a circle or circuitously; to move round\n   and return to the same point; as, the blood circulates in the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulate', 'v. i.', 'To pass from place to place, from person to person,\n   or from hand to hand; to be diffused; as, money circulates; a story\n   circulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulate', 'v. t.', 'To cause to pass from place to place, or from person\n   to person; to spread; as, to circulate a report; to circulate bills of\n   credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulation', 'n.', 'The act of moving in a circle, or in a course which\n   brings the moving body to the place where its motion began.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulation', 'n.', 'The act of passing from place to place or person to\n   person; free diffusion; transmission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulation', 'n.', 'Currency; circulating coin; notes, bills, etc.,\n   current for coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulation', 'n.', 'The extent to which anything circulates or is\n   circulated; the measure of diffusion; as, the circulation of a\n   newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulation', 'n.', 'The movement of the blood in the blood-vascular\n   system, by which it is brought into close relations with almost every\n   living elementary constituent. Also, the movement of the sap in the\n   vessels and tissues of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulative', 'a.', 'Promoting circulation; circulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulator', 'n.', 'One who, or that which, circulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulatorious', 'a.', 'Travelling from house to house or from town to\n   town; itinerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulatory', 'a.', 'Circular; as, a circulatory letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulatory', 'a.', 'Circulating, or going round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulatory', 'a.', 'Subserving the purposes of circulation; as,\n   circulatory organs; of or pertaining to the organs of circulation; as,\n   circulatory diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Circulatory', 'n.', 'A chemical vessel consisting of two portions unequally\n   exposed to the heat of the fire, and with connecting pipes or passages,\n   through which the fluid rises from the overheated portion, and descends\n   from the relatively colder, maintaining a circulation.');
