  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrigrade', 'a.', 'Moving or moved by cirri, or hairlike appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirriped', 'n.', 'One of the Cirripedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirripedia', 'n. pl.', 'An order of Crustacea including the barnacles. When\n   adult, they have a calcareous shell composed of several pieces. From\n   the opening of the shell the animal throws out a group of curved legs,\n   looking like a delicate curl, whence the name of the group. See\n   Anatifa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrobranchiata', 'n. pl.', 'A division of Mollusca having slender,\n   cirriform appendages near the mouth; the Scaphopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirro-cumulus', 'n.', 'See under Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrose', 'a.', 'Bearing a tendril or tendrils; as, a cirrose leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrose', 'a.', 'Resembling a tendril or cirrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrostomi', 'n. pl.', 'The lowest group of vertebrates; -- so called from\n   the cirri around the mouth; the Leptocardia. See Amphioxus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirro-stratus', 'n.', 'See under Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrous', 'a.', 'Cirrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrous', 'a.', 'Tufted; -- said of certain feathers of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirri', 'pl. ', 'of Cirrus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrus', 'n.', 'A tendril or clasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrus', 'n.', 'A soft tactile appendage of the mantle of many Mollusca,\n   and of the parapodia of Annelida. Those near the head of annelids are\n   Tentacular cirri; those of the last segment are caudal cirri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrus', 'n.', 'The jointed, leglike organs of Cirripedia. See Annelida,\n   and Polychaeta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrus', 'n.', 'The external male organ of trematodes and some other worms,\n   and of certain Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirrus', 'n.', 'See under Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirsocele', 'n.', 'The varicose dilatation of the spermatic vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirsoid', 'a.', 'Varicose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cirsotomy', 'n.', 'Any operation for the removal of varices by incision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cis-', '', 'A Latin preposition, sometimes used as a prefix in English\n   words, and signifying on this side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cisalpine', 'a.', 'On the hither side of the Alps with reference to Rome,\n   that is, on the south side of the Alps; -- opposed to transalpine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cisatlantic', 'a.', 'On this side of the Atlantic Ocean; -- used of the\n   eastern or the western side, according to the standpoint of the writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cisco', 'n.', 'The Lake herring (Coregonus Artedi); valuable food fish of\n   the Great Lakes of North America. The name is also applied to C. Hoyi,\n   a related species of Lake Michigan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ciselure', 'n.', 'The process of chasing on metals; also, the work thus\n   chased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cisleithan', 'a.', 'On the Austrian side of the river Leitha; Austrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cismontane', 'a.', 'On this side of the mountains. See under Ultramontane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cispadane', 'a.', 'On the hither side of the river Po with reference to\n   Rome; that is, on the south side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cissoid', 'n.', 'A curve invented by Diocles, for the purpose of solving\n   two celebrated problems of the higher geometry; viz., to trisect a\n   plane angle, and to construct two geometrical means between two given\n   straight lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cist', 'n.', 'A box or chest. Specifically: (a) A bronze receptacle, round\n   or oval, frequently decorated with engravings on the sides and cover,\n   and with feet, handles, etc., of decorative castings. (b) A cinerary\n   urn. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cist', 'n.', 'See Cyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cisted', 'a.', 'Inclosed in a cyst. See Cysted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cistercian', 'n.', 'A monk of the prolific branch of the Benedictine Order,\n   established in 1098 at Citeaux, in France, by Robert, abbot of Molesme.\n   For two hundred years the Cistercians followed the rule of St. Benedict\n   in all its rigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cistercian', 'a.', 'Of or pertaining to the Cistercians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cistern', 'n.', 'An artificial reservoir or tank for holding water, beer,\n   or other liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cistern', 'n.', 'A natural reservoir; a hollow place containing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cistic', 'a.', 'See Cystic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cit', 'n.', 'A citizen; an inhabitant of a city; a pert townsman; -- used\n   contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citable', 'a.', 'Capable of being cited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citadel', 'n.', 'A fortress in or near a fortified city, commanding the\n   city and fortifications, and intended as a final point of defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cital', 'n.', 'Summons to appear, as before a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cital', 'n.', 'Citation; quotation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citation', 'n.', 'An official summons or notice given to a person to\n   appear; the paper containing such summons or notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citation', 'n.', 'The act of citing a passage from a book, or from another\n   person, in his own words; also, the passage or words quoted; quotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citation', 'n.', 'Enumeration; mention; as, a citation of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citation', 'n.', 'A reference to decided cases, or books of authority, to\n   prove a point in law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citator', 'n.', 'One who cites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citatory', 'a.', 'Having the power or form of a citation; as, letters\n   citatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cited', 'imp. & p. p.', 'of Cite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citing', 'p. pr. & vb. n.', 'of Cite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cite', 'v. t.', 'To call upon officially or authoritatively to appear, as\n   before a court; to summon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cite', 'v. t.', 'To urge; to enjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cite', 'v. t.', 'To quote; to repeat, as a passage from a book, or the\n   words of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cite', 'v. t.', 'To refer to or specify, as for support, proof,\n   illustration, or confirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cite', 'v. t.', 'To bespeak; to indicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cite', 'v. t.', 'To notify of a proceeding in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citer', 'n.', 'One who cites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citess', 'n.', 'A city woman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cithara', 'n.', 'An ancient instrument resembling the harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citharistic', 'a.', 'Pertaining, or adapted, to the cithara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cithern', 'n.', 'See Cittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citicism', 'n.', 'The manners of a cit or citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citied', 'a.', 'Belonging to, or resembling, a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citied', 'a.', 'Containing, or covered with, cities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citified', 'a.', 'Aping, or having, the manners of a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citigradae', 'n. pl.', 'A suborder of Arachnoidea, including the European\n   tarantula and the wolf spiders (Lycosidae) and their allies, which\n   capture their prey by rapidly running and jumping. See Wolf spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citigrade', 'a.', 'Pertaining to the Citigradae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citigrade', 'n.', 'One of the Citigradae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citiner', 'n.', 'One who is born or bred in a city; a citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizen', 'n.', 'One who enjoys the freedom and privileges of a city; a\n   freeman of a city, as distinguished from a foreigner, or one not\n   entitled to its franchises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizen', 'n.', 'An inhabitant of a city; a townsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizen', 'n.', 'A person, native or naturalized, of either sex, who owes\n   allegiance to a government, and is entitled to reciprocal protection\n   from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizen', 'n.', 'One who is domiciled in a country, and who is a citizen,\n   though neither native nor naturalized, in such a sense that he takes\n   his legal status from such country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizen', 'a.', 'Having the condition or qualities of a citizen, or of\n   citizens; as, a citizen soldiery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizen', 'a.', 'Of or pertaining to the inhabitants of a city;\n   characteristic of citizens; effeminate; luxurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizeness', 'n.', 'A female citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citizenship', 'n.', 'The state of being a citizen; the status of a citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citole', 'n.', 'A musical instrument; a kind of dulcimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citraconic', 'a.', 'Pertaining to, derived from, or having certain\n   characteristics of, citric and aconitic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citrate', 'n.', 'A salt of citric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citric', 'a.', 'Of, pertaining to, or derived from, the citron or lemon;\n   as, citric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citrination', 'n.', 'The process by which anything becomes of the color of\n   a lemon; esp., in alchemy, the state of perfection in the philosophers\n   stone indicated by its assuming a deep yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citrine', 'a.', 'Like a citron or lemon; of a lemon color; greenish yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citrine', 'n.', 'A yellow, pellucid variety of quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citron', 'n.', 'A fruit resembling a lemon, but larger, and pleasantly\n   aromatic. The thick rind, when candied, is the citron of commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citron', 'n.', 'A citron tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citron', 'n.', 'A citron melon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Citrus', 'n.', 'A genus of trees including the orange, lemon, citron, etc.,\n   originally natives of southern Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cittern', 'n.', 'An instrument shaped like a lute, but strung with wire and\n   played with a quill or plectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cittern-head', 'n.', 'Blockhead; dunce; -- so called because the handle of\n   a cittern usually ended with a carved head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cities', 'pl. ', 'of City');
  INSERT INTO words (word, word_type, description) VALUES  (    'City', 'n.', 'A large town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'City', 'n.', 'A corporate town; in the United States, a town or collective\n   body of inhabitants, incorporated and governed by a mayor and aldermen\n   or a city council consisting of a board of aldermen and a common\n   council; in Great Britain, a town corporate, which is or has been the\n   seat of a bishop, or the capital of his see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'City', 'n.', 'The collective body of citizens, or inhabitants of a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'City', 'a.', 'Of or pertaining to a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cive', 'n.', 'Same as Chive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civet', 'n.', 'A substance, of the consistence of butter or honey, taken\n   from glands in the anal pouch of the civet (Viverra civetta). It is of\n   clear yellowish or brownish color, of a strong, musky odor, offensive\n   when undiluted, but agreeable when a small portion is mixed with\n   another substance. It is used as a perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civet', 'n.', 'The animal that produces civet (Viverra civetta); -- called\n   also civet cat. It is carnivorous, from two to three feet long, and of\n   a brownish gray color, with transverse black bands and spots on the\n   body and tail. It is a native of northern Africa and of Asia. The name\n   is also applied to other species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civet', 'v. t.', 'To scent or perfume with civet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civic', 'a.', 'Relating to, or derived from, a city or citizen; relating to\n   man as a member of society, or to civil affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civicism', 'n.', 'The principle of civil government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civics', 'n.', 'The science of civil government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civil', 'a.', 'Pertaining to a city or state, or to a citizen in his\n   relations to his fellow citizens or to the state; within the city or\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civil', 'a.', 'Subject to government; reduced to order; civilized; not\n   barbarous; -- said of the community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civil', 'a.', 'Performing the duties of a citizen; obedient to government;\n   -- said of an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civil', 'a.', 'Having the manners of one dwelling in a city, as opposed to\n   those of savages or rustics; polite; courteous; complaisant; affable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civil', 'a.', 'Pertaining to civic life and affairs, in distinction from\n   military, ecclesiastical, or official state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civil', 'a.', 'Relating to rights and remedies sought by action or suit\n   distinct from criminal proceedings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilian', 'n.', 'One skilled in the civil law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilian', 'n.', 'A student of the civil law at a university or college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilian', 'n.', 'One whose pursuits are those of civil life, not military\n   or clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilist', 'n.', 'A civilian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilities', 'pl. ', 'of Civillty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civillty', 'n.', 'The state of society in which the relations and duties of\n   a citizen are recognized and obeyed; a state of civilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civillty', 'n.', 'A civil office, or a civil process');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civillty', 'n.', 'Courtesy; politeness; kind attention; good breeding; a\n   polite act or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilizable', 'a.', 'Capable of being civilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilization', 'n.', 'The act of civilizing, or the state of being\n   civilized; national culture; refinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilization', 'n.', 'Rendering a criminal process civil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilized', 'imp. & p. p.', 'of Civilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilizing', 'p. pr. & vb. n.', 'of Civilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilize', 'v. t.', 'To reclaim from a savage state; to instruct in the\n   rules and customs of civilization; to educate; to refine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilize', 'v. t.', 'To admit as suitable to a civilized state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilized', 'a.', 'Reclaimed from savage life and manners; instructed in\n   arts, learning, and civil manners; refined; cultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civilizer', 'n.', 'One who, or that which, civilizes or tends to civilize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civily', 'adv.', 'In a civil manner; as regards civil rights and\n   privileges; politely; courteously; in a well bred manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Civism', 'n.', 'State of citizenship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cizar', 'v. i.', 'To clip with scissors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cizars', 'n. pl.', 'Scissors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cize', 'n.', 'Bulk; largeness. [Obs.] See Size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clabber', 'n.', 'Milk curdled so as to become thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clabber', 'v. i.', 'To become clabber; to lopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clachan', 'n.', 'A small village containing a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clacked', 'imp. & p. p.', 'of Clack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clacking', 'p. pr. & vb. n.', 'of Clack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'n.', 'To make a sudden, sharp noise, or a succesion of such\n   noises, as by striking an object, or by collision of parts; to rattle;\n   to click.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'n.', 'To utter words rapidly and continually, or with abruptness;\n   to let the tongue run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'v. t.', 'To cause to make a sudden, sharp noise, or succession of\n   noises; to click.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'v. t.', 'To utter rapidly and inconsiderately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'v. t.', 'A sharp, abrupt noise, or succession of noises, made by\n   striking an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'v. t.', 'Anything that causes a clacking noise, as the clapper of\n   a mill, or a clack valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clack', 'v. t.', 'Continual or importunate talk; prattle; prating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clacker', 'n.', 'One who clacks; that which clacks; especially, the clapper\n   of a mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clacker', 'n.', 'A claqueur. See Claqueur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clad', 'v.t', 'To clothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clad', '', 'imp. & p. p. of Clothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cladocera', 'n. pl.', 'An order of the Entomostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cladophyll', 'n.', 'A special branch, resembling a leaf, as in the apparent\n   foliage of the broom (Ruscus) and of the common cultivated smilax\n   (Myrsiphillum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claggy', 'a.', 'Adhesive; -- said of a roof in a mine to which coal clings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claik', 'n.', 'See Clake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claimed', 'imp. & p. p.', 'of Claim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claiming', 'p. pr. & vb. n.', 'of Claim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'v./.', 'To ask for, or seek to obtain, by virtue of authority,\n   right, or supposed right; to challenge as a right; to demand as due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'v./.', 'To proclaim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'v./.', 'To call or name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'v./.', 'To assert; to maintain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'v. i.', 'To be entitled to anything; to deduce a right or title;\n   to have a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'n.', 'A demand of a right or supposed right; a calling on another\n   for something due or supposed to be due; an assertion of a right or\n   fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'n.', 'A right to claim or demand something; a title to any debt,\n   privilege, or other thing in possession of another; also, a title to\n   anything which another should give or concede to, or confer on, the\n   claimant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'n.', 'The thing claimed or demanded; that (as land) to which any\n   one intends to establish a right; as a settlers claim; a miners\n   claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claim', 'n.', 'A loud call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claimable', 'a.', 'Capable of being claimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claimant', 'n.', 'One who claims; one who asserts a right or title; a\n   claimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claimer', 'n.', 'One who claims; a claimant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claimless', 'a.', 'Having no claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clair-obscur', 'n.', 'See Chiaroscuro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clairvoyance', 'n.', 'A power, attributed to some persons while in a\n   mesmeric state, of discering objects not perceptible by the senses in\n   their normal condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clairvoyant', 'a.', 'Pertaining to clairvoyance; discerning objects while\n   in a mesmeric state which are not present to the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clairvoyant', 'n.', 'One who is able, when in a mesmeric state, to discern\n   objects not present to the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clake', 'n.', 'Alt. of Claik');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claik', 'n.', 'The bernicle goose; -- called also clack goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'v. t.', 'A bivalve mollusk of many kinds, especially those that are\n   edible; as, the long clam (Mya arenaria); the quahog or round clam\n   (Venus mercenaria); the sea clam or hen clam (Spisula solidissima); and\n   other species of the United States. The name is said to have been given\n   originally to the Tridacna gigas, a huge East Indian bivalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'v. t.', 'Strong pinchers or forceps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'v. t.', 'A kind of vise, usually of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clammed', 'imp. & p. p.', 'of Clam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamming', 'p. pr. & vb. n.', 'of Clam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'v. t.', 'To clog, as with glutinous or viscous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'v. i.', 'To be moist or glutinous; to stick; to adhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'n.', 'Claminess; moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'n.', 'A crash or clangor made by ringing all the bells of a chime\n   at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clam', 'v. t. & i.', 'To produce, in bell ringing, a clam or clangor; to\n   cause to clang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamant', 'a.', 'Crying earnestly, beseeching clamorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamation', 'n.', 'The act of crying out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamatores', 'n. pl.', 'A division of passerine birds in which the vocal\n   muscles are but little developed, so that they lack the power of\n   singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamatorial', 'a.', 'Like or pertaining to the Clamatores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clambake', 'n.', 'The backing or steaming of clams on heated stones,\n   between layers of seaweed; hence, a picnic party, gathered on such an\n   occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clambered', 'imp. & p. p.', 'of Clamber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clambering', 'p. pr. & vb. n.', 'of Clamber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamber', 'v. i.', 'To climb with difficulty, or with hands and feet; --\n   also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamber', 'n.', 'The act of clambering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamber', 'v. t.', 'To ascend by climbing with difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamjamphrie', 'n.', 'Low, worthless people; the rabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clammily', 'adv.', 'In a clammy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamminess', 'n.', 'State of being clammy or viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clammy', 'Compar.', 'Having the quality of being viscous or adhesive; soft\n   and sticky; glutinous; damp and adhesive, as if covered with a cold\n   perspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'n.', 'A great outcry or vociferation; loud and continued shouting\n   or exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'n.', 'Any loud and continued noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'n.', 'A continued expression of dissatisfaction or discontent; a\n   popular outcry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamored', 'imp. & p. p.', 'of Clamor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamoring', 'p. pr. & vb. n.', 'of Clamor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'v. t.', 'To salute loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'v. t.', 'To stun with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'v. t.', 'To utter loudly or repeatedly; to shout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamor', 'v. i.', 'To utter loud sounds or outcries; to vociferate; to\n   complain; to make importunate demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamorer', 'n.', 'One who clamors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamorous', 'a.', 'Speaking and repeating loud words; full of clamor;\n   calling or demanding loudly or urgently; vociferous; noisy; bawling;\n   loud; turbulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'Something rigid that holds fast or binds things together; a\n   piece of wood or metal, used to hold two or more pieces together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'An instrument with a screw or screws by which work is held\n   in its place or two parts are temporarily held together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'A piece of wood placed across another, or inserted into\n   another, to bind or strengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'One of a pair of movable pieces of lead, or other soft\n   material, to cover the jaws of a vise and enable it to grasp without\n   bruising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'A thick plank on the inner part of a ships side, used to\n   sustain the ends of beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'A mass of bricks heaped up to be burned; or of ore for\n   roasting, or of coal for coking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'A mollusk. See Clam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamped', 'imp. & p. p.', 'of Clamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamping', 'p. pr. & vb. n.', 'of Clamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'v. t.', 'To fasten with a clamp or clamps; to apply a clamp to; to\n   place in a clamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'v. t.', 'To cover, as vegetables, with earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'n.', 'A heavy footstep; a tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamp', 'v. i.', 'To tread heavily or clumsily; to clump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clamper', 'n.', 'An instrument of iron, with sharp prongs, attached to a\n   boot or shoe to enable the wearer to walk securely upon ice; a creeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clan', 'n.', 'A tribe or collection of families, united under a chieftain,\n   regarded as having the same common ancestor, and bearing the same\n   surname; as, the clan of Macdonald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clan', 'n.', 'A clique; a sect, society, or body of persons; esp., a body\n   of persons united by some common interest or pursuit; -- sometimes used\n   contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clancular', 'a.', 'Conducted with secrecy; clandestine; concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clancularly', 'adv.', 'privately; secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clandestine', 'a.', 'Conducted with secrecy; withdrawn from public notice,\n   usually for an evil purpose; kept secret; hidden; private; underhand;\n   as, a clandestine marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clandestinity', 'n.', 'Privacy or secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clanged', 'imp. & p. p.', 'of Clang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clanging', 'p. pr. & vb. n.', 'of Clang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clang', 'v. t.', 'To strike together so as to produce a ringing metallic\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clang', 'v. i.', 'To give out a clang; to resound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clang', 'n.', 'A loud, ringing sound, like that made by metallic substances\n   when clanged or struck together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clang', 'n.', 'Quality of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clangor', 'v. t.', 'A sharp, harsh, ringing sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clangorous', 'a.', 'Making a clangor; having a ringing, metallic sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clangous', 'a.', 'Making a clang, or a ringing metallic sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clanjamfrie', 'n.', 'Same as Clamjamphrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clank', 'n.', 'A sharp, brief, ringing sound, made by a collision of\n   metallic or other sonorous bodies; -- usually expressing a duller or\n   less resounding sound than clang, and a deeper and stronger sound than\n   clink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clanked', 'imp. & p. p.', 'of Clank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clanking', 'p. pr. & vb. n.', 'of Clank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clank', 'v. t.', 'To cause to sound with a clank; as, the prisoners clank\n   their chains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clank', 'v. i.', 'To sound with a clank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clankless', 'a.', 'Without a clank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clannish', 'a.', 'Of or pertaining to a clan; closely united, like a clan;\n   disposed to associate only with ones clan or clique; actuated by the\n   traditions, prejudices, habits, etc., of a clan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clanship', 'n.', 'A state of being united together as in a clan; an\n   association under a chieftain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clansmen', 'pl. ', 'of Clansman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clansman', 'n.', 'One belonging to the same clan with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapped', 'imp. & p. p.', 'of Clap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapping', 'p. pr. & vb. n.', 'of Clap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. t.', 'To strike; to slap; to strike, or strike together, with a\n   quick motion, so, as to make a sharp noise; as, to clap ones hands; a\n   clapping of wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. t.', 'To thrust, drive, put, or close, in a hasty or abrupt\n   manner; -- often followed by to, into, on, or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. t.', 'To manifest approbation of, by striking the hands\n   together; to applaud; as, to clap a performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. t.', 'To express contempt or derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. i.', 'To knock, as at a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. i.', 'To strike the hands together in applause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. i.', 'To come together suddenly with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. i.', 'To enter with alacrity and briskness; -- with to or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'v. i.', 'To talk noisily; to chatter loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'A loud noise made by sudden collision; a bang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'A burst of sound; a sudden explosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'A single, sudden act or motion; a stroke; a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'A striking of hands to express approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'Noisy talk; chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'The nether part of the beak of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clap', 'n.', 'Gonorrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapboard', 'n.', 'A narrow board, thicker at one edge than at the other;\n   -- used for weatherboarding the outside of houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapboard', 'n.', 'A stave for a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapboard', 'v. t.', 'To cover with clapboards; as, to clapboard the sides\n   of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapbread', 'n.', 'Alt. of Clapcake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapcake', 'n.', 'Oatmeal cake or bread clapped or beaten till it is thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clape', 'n.', 'A bird; the flicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapper', 'n.', 'A person who claps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapper', 'n.', 'That which strikes or claps, as the tongue of a bell, or\n   the piece of wood that strikes a mill hopper, etc. See Illust. of Bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapper', 'n.', 'A rabbit burrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapperclaw', 'v. t.', 'To fight and scratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clapperclaw', 'v. t.', 'To abuse with the tongue; to revile; to scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claps', 'v. t.', 'Variant of Clasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claptrap', 'n.', 'A contrivance for clapping in theaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claptrap', 'n.', 'A trick or device to gain applause; humbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claptrap', 'a.', 'Contrived for the purpose of making a show, or gaining\n   applause; deceptive; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claque', 'n.', 'A collection of persons employed to applaud at a theatrical\n   exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claqueur', 'n.', 'One of the claque employed to applaud at a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clare', 'n.', 'A nun of the order of St. Clare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarence', 'n.', 'A close four-wheeled carriage, with one seat inside, and\n   a seat for the driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarenceux', 'n.', 'Alt. of Clarencieux');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarencieux', 'n.', 'See King-at-arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarendon', 'n.', 'A style of type having a narrow and heave face. It is\n   made in all sizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clare-obscure', 'n.', 'See Chiaroscuro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claret', 'n.', 'The name first given in England to the red wines of Medoc,\n   in France, and afterwards extended to all the red Bordeaux wines. The\n   name is also given to similar wines made in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claribella', 'n.', 'A soft, sweet stop, or set of open wood pipes in an\n   organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarichord', 'n.', 'A musical instrument, formerly in use, in form of a\n   spinet; -- called also manichord and clavichord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarification', 'n.', 'The act or process of making clear or transparent,\n   by freeing visible impurities; as, the clarification of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarification', 'n.', 'The act of freeing from obscurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarifier', 'n.', 'That which clarifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarifier', 'n.', 'A vessel in which the process of clarification is\n   conducted; as, the clarifier in sugar works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarified', 'imp. & p. p.', 'of Clarify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarifying', 'p. pr. & vb. n.', 'of Clarify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarify', 'v. t.', 'To make clear or bright by freeing from feculent\n   matter; to defecate; to fine; -- said of liquids, as wine or sirup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarify', 'v. t.', 'To make clear; to free from obscurities; to brighten or\n   illuminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarify', 'v. t.', 'To glorify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarify', 'v. i.', 'To grow or become clear or transparent; to become free\n   from feculent impurities, as wine or other liquid under clarification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarify', 'v. i.', 'To grow clear or bright; to clear up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarigate', 'v. i.', 'To declare war with certain ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarinet', 'n.', 'A wind instrument, blown by a single reed, of richer and\n   fuller tone than the oboe, which has a double reed. It is the leading\n   instrument in a military band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarino', 'n.', 'A reed stop in an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarion', 'n.', 'A kind of trumpet, whose note is clear and shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarionet', 'n.', 'See Clarinet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarisonus', 'a.', 'Having a clear sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claritude', 'n.', 'Clearness; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarity', 'n.', 'Clearness; brightness; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claro-obscuro', 'n.', 'See Chiaroscuro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarre', 'n.', 'Wine with a mixture of honey and species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clart', 'v. t.', 'To daub, smear, or spread, as with mud, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clarty', 'a.', 'Sticky and foul; muddy; filthy; dirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clary', 'v. i.', 'To make a loud or shrill noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clary', 'n.', 'A plant (Salvia sclarea) of the Sage family, used in\n   flavoring soups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clashed', 'imp. & p. p.', 'of Clash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clashing', 'p. pr. & vb. n.', 'of Clash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clash', 'v. i.', 'To make a noise by striking against something; to dash\n   noisily together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clash', 'v. i.', 'To meet in opposition; to act in a contrary direction; to\n   come onto collision; to interfere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clash', 'v. t.', 'To strike noisily against or together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clash', 'n.', 'A loud noise resulting from collision; a noisy collision of\n   bodies; a collision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clash', 'n.', 'Opposition; contradiction; as between differing or\n   contending interests, views, purposes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clashingly', 'adv.', 'With clashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasped', 'imp. & p. p.', 'of Clasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasping', 'p. pr. & vb. n.', 'of Clasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasp', 'v. t.', 'To shut or fasten together with, or as with, a clasp; to\n   shut or fasten (a clasp, or that which fastens with a clasp).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasp', 'v. t.', 'To inclose and hold in the hand or with the arms; to\n   grasp; to embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasp', 'v. t.', 'To surround and cling to; to entwine about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasp', 'n.', 'An adjustable catch, bent plate, or hook, for holding\n   together two objects or the parts of anything, as the ends of a belt,\n   the covers of a book, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasp', 'n.', 'A close embrace; a throwing of the arms around; a grasping,\n   as with the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasper', 'n.', 'One who, or that which, clasps, as a tendril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasper', 'n.', 'One of a pair of organs used by the male for grasping the\n   female among many of the Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clasper', 'n.', 'One of a pair of male copulatory organs, developed on the\n   anterior side of the ventral fins of sharks and other elasmobranchs.\n   See Illust. of Chimaera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claspered', 'a.', 'Furnished with tendrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'A group of individuals ranked together as possessing common\n   characteristics; as, the different classes of society; the educated\n   class; the lower classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'A number of students in a school or college, of the same\n   standing, or pursuing the same studies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'A comprehensive division of animate or inanimate objects,\n   grouped together on account of their common characteristics, in any\n   classification in natural science, and subdivided into orders,\n   families, tribes, genera, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'A set; a kind or description, species or variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'One of the sections into which a church or congregation is\n   divided, and which is under the supervision of a class leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classed', 'imp. & p. p.', 'of Class');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classing', 'p. pr. & vb. n.', 'of Class');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'To arrange in classes; to classify or refer to some class;\n   as, to class words or passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'n.', 'To divide into classes, as students; to form into, or place\n   in, a class or classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Class', 'v. i.', 'To grouped or classed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classible', 'a.', 'Capable of being classed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classic', 'n.', 'Alt. of Classical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classical', 'n.', 'Of or relating to the first class or rank, especially in\n   literature or art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classical', 'n.', 'Of or pertaining to the ancient Greeks and Romans, esp.\n   to Greek or Roman authors of the highest rank, or of the period when\n   their best literature was produced; of or pertaining to places\n   inhabited by the ancient Greeks and Romans, or rendered famous by their\n   deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classical', 'n.', 'Conforming to the best authority in literature and art;\n   chaste; pure; refined; as, a classical style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classic', 'n.', 'A work of acknowledged excellence and authority, or its\n   author; -- originally used of Greek and Latin works or authors, but now\n   applied to authors and works of a like character in any language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classic', 'n.', 'One learned in the literature of Greece and Rome, or a\n   student of classical literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicalism', 'n.', 'A classical idiom, style, or expression; a\n   classicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicalism', 'n.', 'Adherence to what are supposed or assumed to be the\n   classical canons of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicalist', 'n.', 'One who adheres to what he thinks the classical\n   canons of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicality', 'n.', 'Alt. of Classicalness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicalness', 'n.', 'The quality of being classical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classically', 'adv.', 'In a classical manner; according to the manner of\n   classical authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classically', 'adv.', 'In the manner of classes; according to a regular\n   order of classes or sets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicism', 'n.', 'A classic idiom or expression; a classicalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classicist', 'n.', 'One learned in the classics; an advocate for the\n   classics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classifiable', 'a.', 'Capable of being classified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classific', 'a.', 'Characterizing a class or classes; relating to\n   classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classification', 'n.', 'The act of forming into a class or classes; a\n   distibution into groups, as classes, orders, families, etc., according\n   to some common relations or affinities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classificatory', 'a.', 'Pertaining to classification; admitting of\n   classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classifier', 'n.', 'One who classifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classified', 'imp. & pp.', 'of Classify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classifying', 'p. pr. & vb. n.', 'of Classify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classify', 'v. t.', 'To distribute into classes; to arrange according to a\n   system; to arrange in sets according to some method founded on common\n   properties or characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classes', 'pl. ', 'of Classis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classis', 'n.', 'A class or order; sort; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classis', 'n.', 'An ecclesiastical body or judicatory in certain churches,\n   as the Reformed Dutch. It is intermediate between the consistory and\n   the synod, and corresponds to the presbytery in the Presbyterian\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classmen', 'pl. ', 'of Classman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classman', 'n.', 'A member of a class; a classmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classman', 'n.', 'A candidate for graduation in arts who is placed in an\n   honor class, as opposed to a passman, who is not classified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Classmate', 'n.', 'One who is in the same class with another, as at school\n   or college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clastic', 'a.', 'Pertaining to what may be taken apart; as, clastic anatomy\n   (of models).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clastic', 'a.', 'Fragmental; made up of brok/ fragments; as, sandstone is a\n   clastic rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clathrate', 'a.', 'Shaped like a lattice; cancellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clathrate', 'a.', 'Having the surface marked with raised lines resembling a\n   lattice, as many shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clattered', 'imp. & p. p.', 'of Clatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clattering', 'p. pr. & vb. n.', 'of Clatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatter', 'v. i.', 'To make a rattling sound by striking hard bodies\n   together; to make a succession of abrupt, rattling sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatter', 'v. i.', 'To talk fast and noisily; to rattle with the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatter', 'v. t.', 'To make a rattling noise with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatter', 'n.', 'A rattling noise, esp. that made by the collision of hard\n   bodies; also, any loud, abrupt sound; a repetition of abrupt sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatter', 'n.', 'Commotion; disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatter', 'n.', 'Rapid, noisy talk; babble; chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatterer', 'n.', 'One who clatters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clatteringly', 'adv.', 'With clattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claude Lorraine glass', '', 'A slightly convex mirror, commonly of black\n   glass, used as a toy for viewing the reflected landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claudent', 'a.', 'Shutting; confining; drawing together; as, a claudent\n   muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claudicant', 'a.', 'Limping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claudication', 'n.', 'A halting or limping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clause', 'n.', 'A separate portion of a written paper, paragraph, or\n   sentence; an article, stipulation, or proviso, in a legal document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clause', 'n.', 'A subordinate portion or a subdivision of a sentence\n   containing a subject and its predicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clause', 'n.', 'See Letters clause / close, under Letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claustral', 'a.', 'Cloistral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claustra', 'pl. ', 'of Claustrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claustrum', 'n.', 'A thin lamina of gray matter in each cerebral hemisphere\n   of the brain of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clausular', 'n.', 'Consisting of, or having, clauses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clausure', 'n.', 'The act of shutting up or confining; confinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavate', 'a.', 'Alt. of Clavated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavated', 'a.', 'Club-shaped; having the form of a club; growing gradually\n   thicker toward the top. [See Illust. of Antennae.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clave', '', 'imp. of Cleave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavecin', 'n.', 'The harpsichord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavel', 'n.', 'See Clevis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavellate', 'a.', 'See Clavate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavellated', 'a.', 'Said of potash, probably in reference to its having\n   been obtained from billets of wood by burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claver', 'n.', 'See Clover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claver', 'n.', 'Frivolous or nonsensical talk; prattle; chattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavichord', 'n.', 'A keyed stringed instrument, now superseded by the\n   pianoforte. See Clarichord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavicle', 'n.', 'The collar bone, which is joined at one end to the\n   scapula, or shoulder blade, and at the other to the sternum, or\n   breastbone. In man each clavicle is shaped like the letter /, and is\n   situated just above the first rib on either side of the neck. In birds\n   the two clavicles are united ventrally, forming the merrythought, or\n   wishbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavicorn', 'a.', 'Having club-shaped antennae. See Antennae');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavicorn', 'n.', 'One of the Clavicornes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavicornes', 'n. pl.', 'A group of beetles having club-shaped antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavicular', 'a.', 'Of or pertaining to the clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavier', 'n.', 'The keyboard of an organ, pianoforte, or harmonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claviform', 'a.', 'Club-shaped; clavate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claviger', 'n.', 'One who carries the keys of any place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claviger', 'n.', 'One who carries a club; a club bearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavigerous', 'a.', 'Bearing a club or a key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claves', 'pl. ', 'of Clavis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavises', 'pl. ', 'of Clavis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavis', 'n.', 'A key; a glossary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavus', 'n.', 'A callous growth, esp. one the foot; a corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavies', 'pl. ', 'of Clavy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clavy', 'n.', 'A mantelpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'A sharp, hooked nail, as of a beast or bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'The whole foot of an animal armed with hooked nails; the\n   pinchers of a lobster, crab, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'Anything resembling the claw of an animal, as the curved and\n   forked end of a hammer for drawing nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'A slender appendage or process, formed like a claw, as the\n   base of petals of the pink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawed', 'imp. & p. p.', 'of Claw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawing', 'p. pr. & vb. n.', 'of Claw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'To pull, tear, or scratch with, or as with, claws or nails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'To relieve from some uneasy sensation, as by scratching; to\n   tickle; hence, to flatter; to court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'n.', 'To rail at; to scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claw', 'v. i.', 'To scrape, scratch, or dig with a claw, or with the hand\n   as a claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawback', 'n.', 'A flatterer or sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawback', 'a.', 'Flattering; sycophantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawback', 'v. t.', 'To flatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawed', 'a.', 'Furnished with claws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clawless', 'a.', 'Destitute of claws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clay', 'n.', 'A soft earth, which is plastic, or may be molded with the\n   hands, consisting of hydrous silicate of aluminium. It is the result of\n   the wearing down and decomposition, in part, of rocks containing\n   aluminous minerals, as granite. Lime, magnesia, oxide of iron, and\n   other ingredients, are often present as impurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clay', 'n.', 'Earth in general, as representing the elementary particles of\n   the human body; hence, the human body as formed from such particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clayed', 'imp. & p. p.', 'of Clay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claying', 'p. pr. & vb. n.', 'of Clay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clay', 'v. t.', 'To cover or manure with clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clay', 'v. t.', 'To clarify by filtering through clay, as sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clay-brained', 'a.', 'Stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clayes', 'n. pl.', 'Wattles, or hurdles, made with stakes interwoven with\n   osiers, to cover lodgments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clayey', 'a.', 'Consisting of clay; abounding with clay; partaking of clay;\n   like clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clayish', 'a.', 'Partaking of the nature of clay, or containing particles\n   of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claymore', 'n.', 'A large two-handed sword used formerly by the Scottish\n   Highlanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Claytonia', 'n.', 'An American genus of perennial herbs with delicate\n   blossoms; -- sometimes called spring beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleading', 'n.', 'A jacket or outer covering of wood, etc., to prevent\n   radiation of heat, as from the boiler, cylinder. etc., of a steam\n   engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleading', 'n.', 'The planking or boarding of a shaft, cofferdam, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from dirt or filth; as, clean clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from that which is useless or injurious; without\n   defects; as, clean land; clean timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from awkwardness; not bungling; adroit; dexterous;\n   as, aclean trick; a clean leap over a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from errors and vulgarisms; as, a clean style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from restraint or neglect; complete; entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from moral defilement; sinless; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from ceremonial defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Free from that which is corrupting to the morals; pure\n   in tone; healthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'superl.', 'Well-proportioned; shapely; as, clean limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'adv.', 'Without limitation or remainder; quite; perfectly; wholly;\n   entirely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'adv.', 'Without miscarriage; not bunglingly; dexterously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaned', 'imp. & p. p.', 'of Clean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaning', 'p. pr. & vb. n.', 'of Clean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean', 'a.', 'To render clean; to free from whatever is foul, offensive,\n   or extraneous; to purify; to cleanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean-cut', 'a.', 'See Clear-cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaner', 'n.', 'One who, or that which, cleans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaning', 'n.', 'The act of making clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaning', 'n.', 'The afterbirth of cows, ewes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanlily', 'adv.', 'In a cleanly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean-limbed', 'a.', 'With well-proportioned, unblemished limbs; as, a\n   clean-limbed young fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanliness', 'n.', 'State of being cleanly; neatness of person or dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanly', 'superl.', 'Habitually clean; pure; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanly', 'superl.', 'Cleansing; fitted to remove moisture; dirt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanly', 'superl.', 'Adroit; skillful; dexterous; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanly', 'adv.', 'In a clean manner; neatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanly', 'adv.', 'Innocently; without stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanly', 'adv.', 'Adroitly; dexterously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanness', 'n.', 'The state or quality of being clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanness', 'n.', 'Purity of life or language; freedom from licentious\n   courses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleansable', 'a.', 'Capable of being cleansed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleansed', 'imp. & p. p.', 'of Cleanse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleansing', 'p. pr. & vb. n.', 'of Cleanse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanse', 'v. t.', 'To render clean; to free from fith, pollution,\n   infection, guilt, etc.; to clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleanser', 'n.', 'One who, or that which, cleanses; a detergent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clean-timbered', 'a.', 'Well-proportioned; symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Free from opaqueness; transparent; bright; light;\n   luminous; unclouded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Free from ambiguity or indistinctness; lucid;\n   perspicuous; plain; evident; manifest; indubitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Able to perceive clearly; keen; acute; penetrating;\n   discriminating; as, a clear intellect; a clear head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Not clouded with passion; serene; cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Easily or distinctly heard; audible; canorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Without mixture; entirely pure; as, clear sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Without defect or blemish, such as freckles or knots;\n   as, a clear complexion; clear lumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Free from guilt or stain; unblemished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Without diminution; in full; net; as, clear profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Free from impediment or obstruction; unobstructed; as,\n   a clear view; to keep clear of debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'superl.', 'Free from embarrassment; detention, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'n.', 'Full extent; distance between extreme limits; especially;\n   the distance between the nearest surfaces of two bodies, or the space\n   between walls; as, a room ten feet square in the clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'adv.', 'In a clear manner; plainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'adv.', 'Without limitation; wholly; quite; entirely; as, to cut a\n   piece clear off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleared', 'imp. & p. p.', 'of Clear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearing', 'p. pr. & vb. n.', 'of Clear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To render bright, transparent, or undimmed; to free from\n   clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To free from impurities; to clarify; to cleanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To free from obscurity or ambiguity; to relive of\n   perplexity; to make perspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To render more quick or acute, as the understanding; to\n   make perspicacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To free from impediment or incumbrance, from defilement,\n   or from anything injurious, useless, or offensive; as, to clear land of\n   trees or brushwood, or from stones; to clear the sight or the voice; to\n   clear ones self from debt; -- often used with of, off, away, or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To free from the imputation of guilt; to justify,\n   vindicate, or acquit; -- often used with from before the thing imputed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To leap or pass by, or over, without touching or failure;\n   as, to clear a hedge; to clear a reef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. t.', 'To gain without deduction; to net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. i.', 'To become free from clouds or fog; to become fair; --\n   often followed by up, off, or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. i.', 'To disengage ones self from incumbrances, distress, or\n   entanglements; to become free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. i.', 'To make exchanges of checks and bills, and settle\n   balances, as is done in a clearing house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear', 'v. i.', 'To obtain a clearance; as, the steamer cleared for\n   Liverpool to-day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearage', 'n.', 'The act of removing anything; clearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearance', 'n.', 'The act of clearing; as, to make a thorough clearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearance', 'n.', 'A certificate that a ship or vessel has been cleared at\n   the customhouse; permission to sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearance', 'n.', 'Clear or net profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearance', 'n.', 'The distance by which one object clears another, as the\n   distance between the piston and cylinder head at the end of a stroke in\n   a steam engine, or the least distance between the point of a cogwheel\n   tooth and the bottom of a space between teeth of a wheel with which it\n   engages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-cut', 'a.', 'Having a sharp, distinct outline, like that of a cameo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-cut', 'a.', 'Concisely and distinctly expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearedness', 'n.', 'The quality of being cleared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearer', 'n.', 'One who, or that which, clears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearer', 'n.', 'A tool of which the hemp for lines and twines, used by\n   sailmakers, is finished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-headed', 'a.', 'Having a clear understanding; quick of perception;\n   intelligent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearing', 'n.', 'The act or process of making clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearing', 'n.', 'A tract of land cleared of wood for cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearing', 'n.', 'A method adopted by banks and bankers for making an\n   exchange of checks held by each against the others, and settling\n   differences of accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearing', 'n.', 'The gross amount of the balances adjusted in the clearing\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearly', 'adv.', 'In a clear manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearness', 'n.', 'The quality or state of being clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-seeing', 'a.', 'Having a clear physical or mental vision; having a\n   clear understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-shining', 'a.', 'Shining brightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-sighted', 'a.', 'Seeing with clearness; discerning; as, clear-sighted\n   reason');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clear-sightedness', 'n.', 'Acute discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearstarched', 'imp. & p. p.', 'of Clearstarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearstraching', 'p. pr. & vb. n.', 'of Clearstarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearstarch', 'v. t.', 'To stiffen with starch, and then make clear by\n   clapping with the hands; as, to clearstarch muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearstarcher', 'n.', 'One who clearstarches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearstory', 'n.', 'Alt. of Clerestory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerestory', 'n.', 'The upper story of the nave of a church, containing\n   windows, and rising above the aisle roofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clearwing', 'n.', 'A lepidopterous insect with partially transparent wings,\n   of the family Aegeriadae, of which the currant and peach-tree borers\n   are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleat', 'n.', 'A strip of wood or iron fastened on transversely to\n   something in order to give strength, prevent warping, hold position,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleat', 'n.', 'A device made of wood or metal, having two arms, around\n   which turns may be taken with a line or rope so as to hold securely and\n   yet be readily released. It is bolted by the middle to a deck or mast,\n   etc., or it may be lashed to a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleat', 'v. t.', 'To strengthen with a cleat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleavable', 'a.', 'Capable of cleaving or being divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleavage', 'n.', 'The act of cleaving or splitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleavage', 'n.', 'The quality possessed by many crystallized substances of\n   splitting readily in one or more definite directions, in which the\n   cohesive attraction is a minimum, affording more or less smooth\n   surfaces; the direction of the dividing plane; a fragment obtained by\n   cleaving, as of a diamond. See Parting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleavage', 'n.', 'Division into laminae, like slate, with the lamination\n   not necessarily parallel to the plane of deposition; -- usually\n   produced by pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaved', 'imp.', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clave', '', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaved', 'p. p.', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaving', 'p. pr. & vb. n.', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleave', 'v. i. ', 'To adhere closely; to stick; to hold fast; to cling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleave', 'v. i. ', 'To unite or be united closely in interest or affection;\n   to adhere with strong attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleave', 'v. i. ', 'To fit; to be adapted; to assimilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'imp.', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clave', '', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clove', '', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'p. p.', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaved', '', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloven', '', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaving', 'p. pr. & vb. n.', 'of Cleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleave', 'v. t.', 'To part or divide by force; to split or rive; to cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleave', 'v. t.', 'To part or open naturally; to divide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleave', 'v. i.', 'To part; to open; to crack; to separate; as parts of\n   bodies; as, the ground cleaves by frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleavelandite', 'n.', 'A variety of albite, white and lamellar in\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleaver', 'n.', 'One who cleaves, or that which cleaves; especially, a\n   butchers instrument for cutting animal bodies into joints or pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleavers', 'n.', 'A species of Galium (G. Aparine); having a fruit set with\n   hooked bristles, which adhere to whatever they come in contact with; --\n   called also, goose grass, catchweed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleche', 'a.', 'Charged with another bearing of the same figure, and of the\n   color of the field, so large that only a narrow border of the first\n   bearing remains visible; -- said of any heraldic bearing. Compare\n   Voided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clechy', 'a.', 'See Cleche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cledge', 'n.', 'The upper stratum of fullers earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cledgy', 'a.', 'Stiff, stubborn, clayey, or tenacious; as, a cledgy soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clee', 'n.', 'A claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clee', 'n.', 'The redshank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clef', 'n.', 'A character used in musical notation to determine the\n   position and pitch of the scale as represented on the staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', '', 'imp. & p. p. from Cleave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'a.', 'Divided; split; partly divided or split.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'a.', 'Incised nearly to the midrib; as, a cleft leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'n.', 'A space or opening made by splitting; a crack; a crevice;\n   as, the cleft of a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'n.', 'A piece made by splitting; as, a cleft of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft', 'n.', 'A disease in horses; a crack on the band of the pastern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleft-footed', 'a.', 'Having a cloven foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleftgraft', 'v. t.', 'To ingraft by cleaving the stock and inserting a\n   scion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleg', 'n.', 'A small breeze or horsefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleistogamic', 'a.', 'Alt. of Cleistogamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleistogamous', 'a.', 'Having, beside the usual flowers, other minute,\n   closed flowers, without petals or with minute petals; -- said of\n   certain species of plants which possess flowers of two or more kinds,\n   the closed ones being so constituted as to insure self-fertilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clem', 'v. t. & i.', 'To starve; to famish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clematis', 'n.', 'A genus of flowering plants, of many species, mostly\n   climbers, having feathery styles, which greatly enlarge in the fruit;\n   -- called also virgins bower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clemence', 'n.', 'Clemency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clemencies', 'pl. ', 'of Clemency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clemency', 'n.', 'Disposition to forgive and spare, as offenders; mildness\n   of temper; gentleness; tenderness; mercy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clemency', 'n.', 'Mildness or softness of the elements; as, the clemency of\n   the season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clement', 'a.', 'Mild in temper and disposition; merciful; compassionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clementine', 'a.', 'Of or pertaining to Clement, esp. to St. Clement of\n   Rome and the spurious homilies attributed to him, or to Pope Clement V.\n   and his compilations of canon law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clench', 'n. & v. t.', 'See Clinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleped', 'imp. & p. p.', 'of Clepe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleping', 'p. pr. & vb. n.', 'of Clepe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ycleped', '', 'of Clepe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clepe', 'v. t.', 'To call, or name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clepe', 'v. i.', 'To make appeal; to cry out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clepsine', 'n.', 'A genus of fresh-water leeches, furnished with a\n   proboscis. They feed upon mollusks and worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clepsydra', 'n.', 'A water clock; a contrivance for measuring time by the\n   graduated flow of a liquid, as of water, through a small aperture. See\n   Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleptomania', 'n.', 'See Kleptomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerestory', 'n.', 'Same as Clearstory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergeon', 'n.', 'A chorister boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergial', 'a.', 'Learned; erudite; clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergical', 'a.', 'Of or pertaining to the clergy; clerical; clerkily;\n   learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergy', 'n.', 'The body of men set apart, by due ordination, to the\n   service of God, in the Christian church, in distinction from the laity;\n   in England, usually restricted to the ministers of the Established\n   Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergy', 'n.', 'Learning; also, a learned profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergy', 'n.', 'The privilege or benefit of clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergyable', 'a.', 'Entitled to, or admitting, the benefit of clergy; as, a\n   clergyable felony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergymen', 'pl. ', 'of Clergyman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clergyman', 'n.', 'An ordained minister; a man regularly authorized to\n   preach the gospel, and administer its ordinances; in England usually\n   restricted to a minister of the Established Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleric', 'n.', 'A clerk, a clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleric', 'a.', 'Same as Clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerical', 'a.', 'Of or pertaining to the clergy; suitable for the clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerical', 'a.', 'Of or relating to a clerk or copyist, or to writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clericalism', 'n.', 'An excessive devotion to the interests of the\n   sacerdotal order; undue influence of the clergy; sacerdotalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clericity', 'n.', 'The state of being a clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerisy', 'n.', 'The literati, or well educated class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerisy', 'n.', 'The clergy, or their opinions, as opposed to the laity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerk', 'n.', 'A clergyman or ecclesiastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerk', 'n.', 'A man who could read; a scholar; a learned person; a man of\n   letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerk', 'n.', 'A parish officer, being a layman who leads in reading the\n   responses of the Episcopal church service, and otherwise assists in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerk', 'n.', 'One employed to keep records or accounts; a scribe; an\n   accountant; as, the clerk of a court; a town clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerk', 'n.', 'An assistant in a shop or store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerk-ale', 'n.', 'A feast for the benefit of the parish clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerkless', 'a.', 'Unlearned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerklike', 'a.', 'Scholarlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerkliness', 'n.', 'Scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerkly', 'a.', 'Of or pertaining to a clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerkly', 'adv.', 'In a scholarly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerkship', 'n.', 'State, quality, or business of a clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleromancy', 'n.', 'A divination by throwing dice or casting lots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleronomy', 'n.', 'Inheritance; heritage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clerstory', 'n.', 'See Clearstory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clever', 'a.', 'Possessing quickness of intellect, skill, dexterity,\n   talent, or adroitness; expert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clever', 'a.', 'Showing skill or adroitness in the doer or former; as, a\n   clever speech; a clever trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clever', 'a.', 'Having fitness, propriety, or suitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clever', 'a.', 'Well-shaped; handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clever', 'a.', 'Good-natured; obliging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleverish', 'a.', 'Somewhat clever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleverly', 'adv.', 'In a clever manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cleverness', 'n.', 'The quality of being clever; skill; dexterity;\n   adroitness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clevis', 'n.', 'A piece of metal bent in the form of an oxbow, with the two\n   ends perforated to receive a pin, used on the end of the tongue of a\n   plow, wagen, etc., to attach it to a draft chain, whiffletree, etc.; --\n   called also clavel, clevy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clew', 'n.', 'Alt. of Clue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clue', 'n.', 'A ball of thread, yarn, or cord; also, The thread itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clue', 'n.', 'That which guides or directs one in anything of a doubtful or\n   intricate nature; that which gives a hint in the solution of a mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clue', 'n.', 'A lower corner of a square sail, or the after corner of a\n   fore-and-aft sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clue', 'n.', 'A loop and thimbles at the corner of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clue', 'n.', 'A combination of lines or nettles by which a hammock is\n   suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clewing', 'imp. & p. p. & vb. n', 'of Clew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clew', 'n.', 'To direct; to guide, as by a thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clew', 'n.', 'To move of draw (a sail or yard) by means of the clew\n   garnets, clew lines, etc.; esp. to draw up the clews of a square sail\n   to the yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliche', 'n.', 'A stereotype plate or any similar reproduction of ornament,\n   or lettering, in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicked', 'imp. & p. p.', 'of Click');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicking', 'p. pr. & vb. n.', 'of Click');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'v. i.', 'To make a slight, sharp noise (or a succession of such\n   noises); as by gentle striking; to tick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'v. t.', 'To move with the sound of a click.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'v. t.', 'To cause to make a clicking noise, as by striking\n   together, or against something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'n.', 'A slight sharp noise, such as is made by the cocking of a\n   pistol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'n.', 'A kind of articulation used by the natives of Southern\n   Africa, consisting in a sudden withdrawal of the end or some other\n   portion of the tongue from a part of the mouth with which it is in\n   contact, whereby a sharp, clicking sound is produced. The sounds are\n   four in number, and are called cerebral, palatal, dental, and lateral\n   clicks or clucks, the latter being the noise ordinarily used in urging\n   a horse forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'v. t.', 'To snatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'n.', 'A detent, pawl, or ratchet, as that which catches the cogs\n   of a ratchet wheel to prevent backward motion. See Illust. of Ratched\n   wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click', 'n.', 'The latch of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Click beetle', '', 'See Elater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicker', 'n.', 'One who stands before a shop door to invite people to buy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicker', 'n.', 'One who as has charge of the work of a companionship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicket', 'n.', 'The knocker of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicket', 'n.', 'A latch key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clicky', 'a.', 'Resembling a click; abounding in clicks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clidastes', 'n.', 'A genus of extinct marine reptiles, allied to the\n   Mosasaurus. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliency', 'n.', 'State of being a client.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Client', 'n.', 'A citizen who put himself under the protection of a man of\n   distinction and influence, who was called his patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Client', 'n.', 'A dependent; one under the protection of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Client', 'n.', 'One who consults a legal adviser, or submits his cause to\n   his management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientage', 'n.', 'State of being client.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientage', 'n.', 'A body of clients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliental', 'a.', 'Of or pertaining to a client.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliented', 'a.', 'Supplied with clients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientelage', 'n.', 'See Clientele, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientele', 'n.', 'The condition or position of a client; clientship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientele', 'n.', 'The clients or dependents of a nobleman of patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientele', 'n.', 'The persons who make habitual use of the services of\n   another person; ones clients, collectively; as, the clientele of a\n   lawyer, doctor, notary, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clientship', 'n.', 'Condition of a client; state of being under the\n   protection of a patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliff', 'n.', 'A high, steep rock; a precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliff', 'n.', 'See Clef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliff limestone', '', 'A series of limestone strata found in Ohio and\n   farther west, presenting bluffs along the rivers and valleys, formerly\n   supposed to be of one formation, but now known to be partly Silurian\n   and partly Devonian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliffy', 'a.', 'Having cliffs; broken; craggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clift', 'n.', 'A cliff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clift', 'n.', 'A cleft of crack; a narrow opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clift', 'n.', 'The fork of the legs; the crotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clifted', 'a.', 'Broken; fissured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climacter', 'n.', 'See Climacteric, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climacteric', 'a.', 'Relating to a climacteric; critical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climacteric', 'n.', 'A period in human life in which some great change is\n   supposed to take place in the constitution. The critical periods are\n   thought by some to be the years produced by multiplying 7 into the odd\n   numbers 3, 5, 7, and 9; to which others add the 81st year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climacteric', 'n.', 'Any critical period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climacterical', 'a. & n.', 'See Climacteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatal', 'a.', 'Climatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatarchic', 'a.', 'Presiding over, or regulating, climates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climate', 'v. i.', 'One of thirty regions or zones, parallel to the\n   equator, into which the surface of the earth from the equator to the\n   pole was divided, according to the successive increase of the length of\n   the midsummer day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climate', 'v. i.', 'The condition of a place in relation to various\n   phenomena of the atmosphere, as temperature, moisture, etc., especially\n   as they affect animal or vegetable life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climate', 'v. i.', 'To dwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatic', 'a.', 'Of or pertaining to a climate; depending on, or limited\n   by, a climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatical', 'a.', 'Climatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatized', 'imp. & p. p.', 'of Climatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatizing', 'p. pr. & vb. n.', 'of Climatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatize', 'v. t. & i.', 'To acclimate or become acclimated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatography', 'n.', 'A description of climates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatological', 'a.', 'Of or pertaining to climatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatologist', 'n.', 'One versed in, or who studies, climatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climatology', 'n.', 'The science which treats of climates and investigates\n   their phenomena and causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climature', 'n.', 'A climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climax', 'v. i.', 'Upward movement; steady increase; gradation; ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climax', 'v. i.', 'A figure in which the parts of a sentence or paragraph\n   are so arranged that each succeeding one rises above its predecessor in\n   impressiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climax', 'v. i.', 'The highest point; the greatest degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climbed', 'imp. & p. p.', 'of Climb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clomb', '', 'of Climb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climbing', 'p. pr. & vb. n.', 'of Climb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climb', 'v. i.', 'To ascend or mount laboriously, esp. by use of the hands\n   and feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climb', 'v. i.', 'To ascend as if with effort; to rise to a higher point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climb', 'v. i.', 'To ascend or creep upward by twining about a support, or\n   by attaching itself by tendrils, rootlets, etc., to a support or\n   upright surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climb', 'v. t.', 'To ascend, as by means of the hands and feet, or\n   laboriously or slowly; to mount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climb', 'n.', 'The act of one who climbs; ascent by climbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climbable', 'a.', 'Capable of being climbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climber', 'n.', 'One who, or that which, climbs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climber', 'n.', 'A plant that climbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climber', 'n.', 'A bird that climbs, as a woodpecker or a parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climber', 'v. i.', 'To climb; to mount with effort; to clamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Climbing', '', 'p. pr. & vb. n. of Climb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clime', 'n.', 'A climate; a tract or region of the earth. See Climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinanthium', 'n.', 'The receptacle of the flowers in a composite plant; --\n   also called clinium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinched', 'imp. & p. p.', 'of Clinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinching', 'p. pr. & vb. n.', 'of Clinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'v. t.', 'To hold firmly; to hold fast by grasping or embracing\n   tightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'v. t.', 'To set closely together; to close tightly; as, to clinch\n   the teeth or the first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'v. t.', 'To bend or turn over the point of (something that has\n   been driven through an object); so that it will hold fast; as, to\n   clinch a nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'v. t.', 'To make conclusive; to confirm; to establish; as, to\n   clinch an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'v. i.', 'To hold fast; to grasp something firmly; to seize or\n   grasp one another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'n.', 'The act or process of holding fast; that which serves to\n   hold fast; a grip; a grasp; a clamp; a holdfast; as, to get a good\n   clinch of an antagonist, or of a weapon; to secure anything by a\n   clinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'n.', 'A pun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinch', 'n.', 'A hitch or bend by which a rope is made fast to the ring of\n   an anchor, or the breeching of a ships gun to the ringbolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clincher', 'n.', 'One who, or that which, clinches; that which holds fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clincher', 'n.', 'That which ends a dispute or controversy; a decisive\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clincher-built', 'a.', 'See Clinker-built.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clung', 'imp. & p. p.', 'of Cling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clong', '', 'of Cling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinging', 'p. pr. & vb. n.', 'of Cling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cling', 'v. i.', 'To adhere closely; to stick; to hold fast, especially by\n   twining round or embracing; as, the tendril of a vine clings to its\n   support; -- usually followed by to or together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cling', 'v. t.', 'To cause to adhere to, especially by twining round or\n   embracing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cling', 'v. t.', 'To make to dry up or wither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cling', 'n.', 'Adherence; attachment; devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clingstone', 'a.', 'Having the flesh attached closely to the stone, as in\n   some kinds of peaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clingstone', 'n.', 'A fruit, as a peach, whose flesh adheres to the stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clingy', 'a.', 'Apt to cling; adhesive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinic', 'n.', 'One confined to the bed by sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinic', 'n.', 'One who receives baptism on a sick bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinic', 'n.', 'A school, or a session of a school or class, in which\n   medicine or surgery is taught by the examination and treatment of\n   patients in the presence of the pupils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinical', 'v. i.', 'Alt. of Clinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinic', 'v. i.', 'Of or pertaining to a bed, especially, a sick bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinic', 'v. i.', 'Of or pertaining to a clinic, or to the study of disease\n   in the living subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinically', 'adv.', 'In a clinical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinique', 'n.', 'A clinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinium', 'n.', 'See Clinanthium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinked', 'imp. & p. p.', 'of Clink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinking', 'p. pr. & vb. n.', 'of Clink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clink', 'v. i.', 'To cause to give out a slight, sharp, tinkling, sound, as\n   by striking metallic or other sonorous bodies together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clink', 'v. i.', 'To give out a slight, sharp, tinkling sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clink', 'v. i.', 'To rhyme. [Humorous].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clink', 'n.', 'A slight, sharp, tinkling sound, made by the collision of\n   sonorous bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinkant', 'a.', 'See Clinquant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinker', 'n.', 'A mass composed of several bricks run together by the\n   action of the fire in the kiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinker', 'n.', 'Scoria or vitrified incombustible matter, formed in a\n   grate or furnace where anthracite coal in used; vitrified or burnt\n   matter ejected from a volcano; slag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinker', 'n.', 'A scale of oxide of iron, formed in forging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinker', 'n.', 'A kind of brick. See Dutch clinker, under Dutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinker-built', 'a.', 'Having the side planks (af a boat) so arranged that\n   the lower edge of each overlaps the upper edge of the plank next below\n   it like clapboards on a house. See Lapstreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinkstone', 'n.', 'An igneous rock of feldspathic composition, lamellar in\n   structure, and clinking under the hammer. See Phonolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinodiagonal', 'n.', 'That diagonal or lateral axis in a monoclinic\n   crystal which makes an oblique angle with the vertical axis. See\n   Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinodiagonal', 'a.', 'Pertaining to, or the direction of, the\n   clinodiagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinodome', 'n.', 'See under Dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinographic', 'a.', 'Pertaining to that mode of projection in drawing in\n   which the rays of light are supposed to fall obliquely on the plane of\n   projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinoid', 'a.', 'Like a bed; -- applied to several processes on the inner\n   side of the sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinometer', 'n.', 'An instrument for determining the dip of beds or\n   strata, pr the slope of an embankment or cutting; a kind of plumb\n   level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinometric', 'a.', 'Pertaining to, or ascertained by, the clinometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinometric', 'a.', 'Pertaining to the oblique crystalline forms, or to\n   solids which have oblique angles between the axes; as, the clinometric\n   systems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinometry', 'n.', 'That art or operation of measuring the inclination of\n   strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinopinacoid', 'n.', 'The plane in crystals of the monoclinic system which\n   is parallel to the vertical and the inclined lateral (clinidiagonal)\n   axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinorhombic', 'a.', 'Possessing the qualities of a prism, obliquely\n   inclined to a rhombic base; monoclinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinquant', 'a.', 'Glittering; dressed in, or overlaid with, tinsel finery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clinquant', 'n.', 'Tinsel; Dutch gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clio', 'n.', 'The Muse who presided over history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clione', 'n.', 'A genus of naked pteropods. One species (Clione\n   papilonacea); abundant in the Arctic Ocean, constitutes a part of the\n   food of the Greenland whale. It is sometimes incorrectly called Clio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipped', 'imp. & p. p.', 'of Clip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipping', 'p. pr. & vb. n.', 'of Clip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'v. t.', 'To embrace, hence; to encompass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'v. t.', 'To cut off; as with shears or scissors; as, to clip the\n   hair; to clip coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'v. t.', 'To curtail; to cut short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'v. i.', 'To move swiftly; -- usually with indefinite it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'An embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'A cutting; a shearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'The product of a single shearing of sheep; a seasons crop of\n   wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'A clasp or holder for letters, papers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'An embracing strap for holding parts together; the iron\n   strap, with loop, at the ends of a whiffletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'A projecting flange on the upper edge of a horseshoe, turned\n   up so as to embrace the lower part of the hoof; -- called also toe clip\n   and beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clip', 'n.', 'A blow or stroke with the hand; as, he hit him a clip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipper', 'n.', 'One who clips; specifically, one who clips off the edges\n   of coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipper', 'n.', 'A machine for clipping hair, esp. the hair of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipper', 'n.', 'A vessel with a sharp bow, built and rigged for fast\n   sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipping', 'n.', 'The act of embracing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipping', 'n.', 'The act of cutting off, curtailing, or diminishing; the\n   practice of clipping the edges of coins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clipping', 'n.', 'That which is clipped off or out of something; a piece\n   separated by clipping; as, newspaper clippings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clique', 'v. i.', 'A narrow circle of persons associated by common\n   interests or for the accomplishment of a common purpose; -- generally\n   used in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clique', 'v. i.', 'To To associate together in a clannish way; to act with\n   others secretly to gain a desired end; to plot; -- used with together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliquish', 'a.', 'Of or pertaining to a clique; disposed to from cliques;\n   exclusive in spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cliquism', 'n.', 'The tendency to associate in cliques; the spirit of\n   cliques.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clitellus', 'n.', 'A thickened glandular portion of the body of the adult\n   earthworm, consisting of several united segments modified for\n   reproductive purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clitoris', 'n.', 'A small organ at the upper part of the vulva, homologous\n   to the penis in the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clivers', 'n.', 'See Cleavers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clivities', 'pl. ', 'of Clivity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clivity', 'n.', 'Inclination; ascent or descent; a gradient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloacae', 'pl. ', 'of Cloaca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaca', 'n.', 'A sewer; as, the Cloaca Maxima of Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaca', 'n.', 'A privy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaca', 'n.', 'The common chamber into which the intestinal, urinary, and\n   generative canals discharge in birds, reptiles, amphibians, and many\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloacal', 'a.', 'Of or pertaining to a cloaca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloak', 'n.', 'A loose outer garment, extending from the neck downwards,\n   and commonly without sleeves. It is longer than a cape, and is worn\n   both by men and by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloak', 'n.', 'That which conceals; a disguise or pretext; an excuse; a\n   fair pretense; a mask; a cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaked', 'imp. & p. p.', 'of Cloak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaking', 'p. pr. & vb. n.', 'of Cloak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloak', 'v. t.', 'To cover with, or as with, a cloak; hence, to hide or\n   conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloakedly', 'adv.', 'In a concealed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaking', 'n.', 'The act of covering with a cloak; the act of concealing\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloaking', 'n.', 'The material of which of which cloaks are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloakroom', 'n.', 'A room, attached to any place of public resort, where\n   cloaks, overcoats, etc., may be deposited for a time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'n.', 'A machine for measuring time, indicating the hour and other\n   divisions by means of hands moving on a dial plate. Its works are moved\n   by a weight or a spring, and it is often so constructed as to tell the\n   hour by the stroke of a hammer on a bell. It is not adapted, like the\n   watch, to be carried on the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'n.', 'A watch, esp. one that strikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'n.', 'The striking of a clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'n.', 'A figure or figured work on the ankle or side of a stocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'v. t.', 'To ornament with figured work, as the side of a stocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'v. t. & i.', 'To call, as a hen. See Cluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clock', 'n.', 'A large beetle, esp. the European dung beetle (Scarabaeus\n   stercorarius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clocklike', 'a.', 'Like a clock or like clockwork; mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clockwork', 'n.', 'The machinery of a clock, or machinery resembling that\n   of a clock; machinery which produces regularity of movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'n.', 'A lump or mass, especially of earth, turf, or clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'n.', 'The ground; the earth; a spot of earth or turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'n.', 'That which is earthy and of little relative value, as the\n   body of man in comparison with the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'n.', 'A dull, gross, stupid fellow; a dolt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'n.', 'A part of the shoulder of a beef creature, or of the neck\n   piece near the shoulder. See Illust. of Beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'v.i', 'To collect into clods, or into a thick mass; to coagulate;\n   to clot; as, clodded gore. See Clot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'v. t.', 'To pelt with clods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clod', 'v. t.', 'To throw violently; to hurl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloddish', 'a.', 'Resembling clods; gross; low; stupid; boorish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloddy', 'a.', 'Consisting of clods; full of clods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clodhopper', 'n.', 'A rude, rustic fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clodhopping', 'a.', 'Boorish; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clodpate', 'n.', 'A blockhead; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clodpated', 'a.', 'Stupid; dull; doltish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clodpoll', 'n.', 'A stupid fellow; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloff', 'n.', 'Formerly an allowance of two pounds in every three hundred\n   weight after the tare and tret are subtracted; now used only in a\n   general sense, of small deductions from the original weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v.', 'That which hinders or impedes motion; hence, an encumbrance,\n   restraint, or impediment, of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v.', 'A weight, as a log or block of wood, attached to a man or an\n   animal to hinder motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v.', 'A shoe, or sandal, intended to protect the feet from wet, or\n   to increase the apparent stature, and having, therefore, a very thick\n   sole. Cf. Chopine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clogged', 'imp. & p. p.', 'of Clog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clogging', 'p. pr. & vb. n.', 'of Clog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v. t.', 'To encumber or load, especially with something that\n   impedes motion; to hamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v. t.', 'To obstruct so as to hinder motion in or through; to choke\n   up; as, to clog a tube or a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v. t.', 'To burden; to trammel; to embarrass; to perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v. i.', 'To become clogged; to become loaded or encumbered, as with\n   extraneous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clog', 'v. i.', 'To coalesce or adhere; to unite in a mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clogginess', 'n.', 'The state of being clogged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clogging', 'n.', 'Anything which clogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloggy', 'a.', 'Clogging, or having power to clog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloisonne', 'a.', 'Inlaid between partitions: -- said of enamel when the\n   lines which divide the different patches of fields are composed of a\n   kind of metal wire secured to the ground; as distinguished from\n   champleve enamel, in which the ground is engraved or scooped out to\n   receive the enamel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloister', 'v. t.', 'An inclosed place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloister', 'v. t.', 'A covered passage or ambulatory on one side of a\n   court;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloister', 'v. t.', 'the series of such passages on the different sides of\n   any court, esp. that of a monastery or a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloister', 'v. t.', 'A monastic establishment; a place for retirement from\n   the world for religious duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloistered', 'imp. & p. p.', 'of Cloister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloistering', 'p. pr. & vb. n.', 'of Cloister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloister', 'v. t.', 'To confine in, or as in, a cloister; to seclude from\n   the world; to immure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloisteral', 'a.', 'Cloistral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloistered', 'a.', 'Dwelling in cloisters; solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloistered', 'a.', 'Furnished with cloisters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloisterer', 'n.', 'One belonging to, or living in, a cloister; a recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloistral', 'a.', 'Of, pertaining to, or confined in, a cloister; recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloistress', 'n.', 'A nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloke', 'n. & v.', 'See Cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clomb', '', 'Alt. of Clomben');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clomben', '', 'imp. & p. p. of Climb (for climbed).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clomp', 'n.', 'See Clamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clong', '', 'imp. of Cling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clonic', 'a.', 'Having an irregular, convulsive motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloom', 'v. t.', 'To close with glutinous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloop', 'n.', 'The sound made when a cork is forcibly drawn from a bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closed', 'imp. & p. p.', 'of Close');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closing', 'p. pr. & vb. n.', 'of Close');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'To stop, or fill up, as an opening; to shut; as, to close\n   the eyes; to close a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'To bring together the parts of; to consolidate; as, to close\n   the ranks of an army; -- often used with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'To bring to an end or period; to conclude; to complete; to\n   finish; to end; to consummate; as, to close a bargain; to close a\n   course of instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'To come or gather around; to inclose; to encompass; to\n   confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. i.', 'To come together; to unite or coalesce, as the parts of a\n   wound, or parts separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. i.', 'To end, terminate, or come to a period; as, the debate\n   closed at six oclock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. i.', 'To grapple; to engage in hand-to-hand fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'The manner of shutting; the union of parts; junction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'Conclusion; cessation; ending; end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'A grapple in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'The conclusion of a strain of music; cadence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'n.', 'A double bar marking the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'An inclosed place; especially, a small field or piece of\n   land surrounded by a wall, hedge, or fence of any kind; --\n   specifically, the precinct of a cathedral or abbey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'A narrow passage leading from a street to a court, and\n   the houses within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'The interest which one may have in a piece of ground,\n   even though it is not inclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Shut fast; closed; tight; as, a close box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Narrow; confined; as, a close alley; close quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Oppressive; without motion or ventilation; causing a\n   feeling of lassitude; -- said of the air, weather, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Strictly confined; carefully quarded; as, a close\n   prisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Out of the way observation; secluded; secret; hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Disposed to keep secrets; secretive; reticent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Having the parts near each other; dense; solid; compact;\n   as applied to bodies; viscous; tenacious; not volatile, as applied to\n   liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Concise; to the point; as, close reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Adjoining; near; either in space; time, or thought; --\n   often followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Short; as, to cut grass or hair close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Intimate; familiar; confidential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Nearly equal; almost evenly balanced; as, a close vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Difficult to obtain; as, money is close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Parsimonious; stingy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Adhering strictly to a standard or original; exact;\n   strict; as, a close translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Accurate; careful; precise; also, attentive; undeviating;\n   strict; not wandering; as, a close observer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'v. t.', 'Uttered with a relatively contracted opening of the\n   mouth, as certain sounds of e and o in French, Italian, and German; --\n   opposed to open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'adv.', 'In a close manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close', 'adv.', 'Secretly; darkly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close-banded', 'a.', 'Closely united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close-barred', 'a.', 'Firmly barred or closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close-bodied', 'a.', 'Fitting the body exactly; setting close, as a\n   garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close-fights', 'n. pl.', 'Barriers with loopholes, formerly erected on the\n   deck of a vessel to shelter the men in a close engagement with an\n   enemys boarders; -- called also close quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closefisted', 'a.', 'Covetous; niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closehanded', 'a.', 'Covetous; penurious; stingy; closefisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closehauled', 'a.', 'Under way and moving as nearly as possible toward the\n   direction from which the wind blows; -- said of a sailing vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closely', 'adv.', 'In a close manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closely', 'adv.', 'Secretly; privately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closemouthed', 'a.', 'Cautious in speaking; secret; wary; uncommunicative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closen', 'v. t.', 'To make close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closeness', 'n.', 'The state of being close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closer', 'n.', 'One who, or that which, closes; specifically, a boot\n   closer. See under Boot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closer', 'n.', 'A finisher; that which finishes or terminates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closer', 'n.', 'The last stone in a horizontal course, if of a less size\n   than the others, or a piece of brick finishing a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closereefed', 'a.', 'Having all the reefs taken in; -- said of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close-stool', 'n.', 'A utensil to hold a chamber vessel, for the use of the\n   sick and infirm. It is usually in the form of a box, with a seat and\n   tight cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closet', 'n.', 'A small room or apartment for retirement; a room for\n   privacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closet', 'n.', 'A small apartment, or recess in the side of a room, for\n   household utensils, clothing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closeting', 'imp. & p. pr. & vb. ', 'of Closet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closet', 'v. t.', 'To shut up in, or as in, a closet; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closet', 'v. t.', 'To make into a closet for a secret interview.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Close-tongued', 'a.', 'Closemouthed; silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closh', 'n.', 'A disease in the feet of cattle; laminitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closh', 'n.', 'The game of ninepins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closure', 'v. t.', 'The act of shutting; a closing; as, the closure of a\n   chink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closure', 'v. t.', 'That which closes or shuts; that by which separate\n   parts are fastened or closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closure', 'v. t.', 'That which incloses or confines; an inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closure', 'v. t.', 'A conclusion; an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Closure', 'v. t.', 'A method of putting an end to debate and securing an\n   immediate vote upon a measure before a legislative body. It is similar\n   in effect to the previous question. It was first introduced into the\n   British House of Commons in 1882. The French word cloture was\n   originally applied to this proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clot', 'n.', 'A concretion or coagulation; esp. a soft, slimy, coagulated\n   mass, as of blood; a coagulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotted', 'imp. & p. p.', 'of Clot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotting', 'p. pr. & vb. n.', 'of Clot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clot', 'v. i.', 'To concrete, coagulate, or thicken, as soft or fluid\n   matter by evaporation; to become a cot or clod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clot', 'v. t.', 'To form into a slimy mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotbur', 'n.', 'The burdock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotbur', 'n.', 'Same as Cocklebur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clote', 'n.', 'The common burdock; the clotbur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloths', 'pl. ', 'of Cloth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothes', 'pl. ', 'of Cloth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloth', 'n.', 'A fabric made of fibrous material (or sometimes of wire, as\n   in wire cloth); commonly, a woven fabric of cotton, woolen, or linen,\n   adapted to be made into garments; specifically, woolen fabrics, as\n   distinguished from all others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloth', 'n.', 'The dress; raiment. [Obs.] See Clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloth', 'n.', 'The distinctive dress of any profession, especially of the\n   clergy; hence, the clerical profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothed', 'imp. & p. p.', 'of Clothe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clad', '', 'of Clothe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothing', 'p. pr. & vb. n.', 'of Clothe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothe', 'v. t.', 'To put garments on; to cover with clothing; to dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothe', 'v. t.', 'To provide with clothes; as, to feed and clothe a\n   family; to clothe ones self extravagantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothe', 'v. t.', 'Fig.: To cover or invest, as with a garment; as, to\n   clothe one with authority or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothe', 'v. i.', 'To wear clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothes', 'n. pl.', 'Covering for the human body; dress; vestments;\n   vesture; -- a general term for whatever covering is worn, or is made to\n   be worn, for decency or comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothes', 'n. pl.', 'The covering of a bed; bedclothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotheshorse', 'n.', 'A frame to hang clothes on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothesline', 'n.', 'A rope or wire on which clothes are hung to dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothespin', 'n.', 'A forked piece of wood, or a small spring clamp, used\n   for fastening clothes on a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothespress', 'n.', 'A receptacle for clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothier', 'n.', 'One who makes cloths; one who dresses or fulls cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothier', 'n.', 'One who sells cloth or clothes, or who makes and sells\n   clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothing', 'n.', 'Garments in general; clothes; dress; raiment; covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothing', 'n.', 'The art of process of making cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothing', 'n.', 'A covering of non-conducting material on the outside of a\n   boiler, or steam chamber, to prevent radiation of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothing', 'n.', 'See Card clothing, under 3d Card.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clothred', 'p. p.', 'Clottered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotpoll', 'n.', 'See Clodpoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotted', 'a.', 'Composed of clots or clods; having the quality or form of\n   a clot; sticky; slimy; foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotter', 'v. i.', 'To concrete into lumps; to clot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotty', 'n.', 'Full of clots, or clods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloture', 'n.', 'See Closure, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clotweed', 'n.', 'Cocklebur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'n.', 'A collection of visible vapor, or watery particles,\n   suspended in the upper atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'n.', 'A mass or volume of smoke, or flying dust, resembling vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'n.', 'A dark vein or spot on a lighter material, as in marble;\n   hence, a blemish or defect; as, a cloud upon ones reputation; a cloud\n   on a title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'n.', 'That which has a dark, lowering, or threatening aspect; that\n   which temporarily overshadows, obscures, or depresses; as, a cloud of\n   sorrow; a cloud of war; a cloud upon the intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'n.', 'A great crowd or multitude; a vast collection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'n.', 'A large, loosely-knitted scarf, worn by women about the\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouded', 'imp. & p. p.', 'of Cloud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouding', 'p. pr. & vb. n.', 'of Cloud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'v. t.', 'To overspread or hide with a cloud or clouds; as, the sky\n   is clouded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'v. t.', 'To darken or obscure, as if by hiding or enveloping with\n   a cloud; hence, to render gloomy or sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'v. t.', 'To blacken; to sully; to stain; to tarnish; to damage; --\n   esp. used of reputation or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'v. t.', 'To mark with, or darken in, veins or sports; to variegate\n   with colors; as, to cloud yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud', 'v. i.', 'To grow cloudy; to become obscure with clouds; -- often\n   used with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudage', 'n.', 'Mass of clouds; cloudiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudberry', 'n.', 'A species of raspberry (Rubus Chamaemerous) growing in\n   the northern regions, and bearing edible, amber-colored fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud-built', 'a.', 'Built of, or in, the clouds; airy; unsubstantial;\n   imaginary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud-burst', 'n.', 'A sudden copious rainfall, as the whole cloud had been\n   precipitated at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud-capped', 'a.', 'Having clouds resting on the top or head; reaching to\n   the clouds; as, cloud-capped mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloud-compeller', 'n.', 'Cloud-gatherer; -- an epithet applied to Zeus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudily', 'adv.', 'In a cloudy manner; darkly; obscurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudiness', 'n.', 'The state of being cloudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouding', 'n.', 'A mottled appearance given to ribbons and silks in the\n   process of dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouding', 'n.', 'A diversity of colors in yarn, recurring at regular\n   intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudland', 'n.', 'Dreamland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudless', 'a.', 'Without a cloud; clear; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudlet', 'n.', 'A little cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudy', 'n.', 'Overcast or obscured with clouds; clouded; as, a cloudy\n   sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudy', 'n.', 'Consisting of a cloud or clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudy', 'n.', 'Indicating gloom, anxiety, sullenness, or ill-nature; not\n   open or cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudy', 'n.', 'Confused; indistinct; obscure; dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudy', 'n.', 'Lacking clearness, brightness, or luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cloudy', 'n.', 'Marked with veins or sports of dark or various hues, as\n   marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clough', 'n.', 'A cleft in a hill; a ravine; a narrow valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clough', 'n.', 'A sluice used in returning water to a channel after\n   depositing its sediment on the flooded land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clough', 'n.', 'An allowance in weighing. See Cloff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'A cloth; a piece of cloth or leather; a patch; a rag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'A swadding cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'A piece; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'The center of the butt at which archers shoot; -- probably\n   once a piece of white cloth or a nail head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'An iron plate on an axletree or other wood to keep it from\n   wearing; a washer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'A blow with the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouted', 'imp. & p. p.', 'of Clout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouting', 'p. pr. & vb. n.', 'of Clout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'To cover with cloth, leather, or other material; to bandage;\n   patch, or mend, with a clout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'To join or patch clumsily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'To quard with an iron plate, as an axletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'To give a blow to; to strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clout', 'n.', 'To stud with nails, as a timber, or a boot sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clouterly', 'n.', 'Clumsy; awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clove', 'imp.', 'Cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clove', 'v. t.', 'A cleft; a gap; a ravine; -- rarely used except as part\n   of a proper name; as, Kaaterskill Clove; Stone Clove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clove', 'n.', 'A very pungent aromatic spice, the unexpanded flower bud of\n   the clove tree (Eugenia, / Caryophullus, aromatica); a native of the\n   Molucca Isles.');
