  INSERT INTO words (word, word_type, description) VALUES  (    'Coagent', 'n.', 'An associate in an act; a coworker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagment', 'v. t.', 'To join together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagmentation', 'n.', 'The act of joining, or the state of being joined,\n   together; union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulability', 'n.', 'The quality of being coagulable; capacity of being\n   coagulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulable', 'a.', 'Capable of being coagulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulant', 'n.', 'That which produces coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulate', 'a.', 'Coagulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulated', 'imp. & p. p.', 'of Coagulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulating', 'p. pr. & vb. n.', 'of Coagulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulate', 'v. t.', 'To cause (a liquid) to change into a curdlike or\n   semisolid state, not by evaporation but by some kind of chemical\n   reaction; to curdle; as, rennet coagulates milk; heat coagulates the\n   white of an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulate', 'v. i.', 'To undergo coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulated', 'a.', 'Changed into, or contained in, a coagulum or a curdlike\n   mass; curdled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulation', 'n.', 'The change from a liquid to a thickened, curdlike,\n   insoluble state, not by evaporation, but by some kind of chemical\n   reaction; as, the spontaneous coagulation of freshly drawn blood; the\n   coagulation of milk by rennet, or acid, and the coagulation of egg\n   albumin by heat. Coagulation is generally the change of an albuminous\n   body into an insoluble modification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulation', 'n.', 'The substance or body formed by coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulative', 'a.', 'Having the power to cause coagulation; as, a\n   coagulative agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulator', 'n.', 'That which causes coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulatory', 'a.', 'Serving to coagulate; produced by coagulation; as,\n   coagulatory effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagula', 'pl. ', 'of Coagulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coagulum', 'a.', 'The thick, curdy precipitate formed by the coagulation of\n   albuminous matter; any mass of coagulated matter, as a clot of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaita', 'n.', 'The native name of certain South American monkeys of the\n   genus Ateles, esp. A. paniscus. The black-faced coaita is Ateles ater.\n   See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coak', 'n.', 'See Coke, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coak', 'n.', 'A kind of tenon connecting the face of a scarfed timber with\n   the face of another timber, or a dowel or pin of hard wood or iron\n   uniting timbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coak', 'n.', 'A metallic bushing or strengthening piece in the center of a\n   wooden block sheave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coak', 'v. t.', 'To unite, as timbers, by means of tenons or dowels in the\n   edges or faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal', 'n.', 'A thoroughly charred, and extinguished or still ignited,\n   fragment from wood or other combustible substance; charcoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal', 'n.', 'A black, or brownish black, solid, combustible substance, dug\n   from beds or veins in the earth to be used for fuel, and consisting,\n   like charcoal, mainly of carbon, but more compact, and often affording,\n   when heated, a large amount of volatile matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaled', 'imp. & p. p.', 'of Coal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaling', 'p. pr. & vb. n.', 'of Coal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal', 'v. t.', 'To burn to charcoal; to char.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal', 'v. t.', 'To mark or delineate with charcoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal', 'v. t.', 'To supply with coal; as, to coal a steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal', 'v. i.', 'To take in coal; as, the steamer coaled at Southampton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal-black', 'a.', 'As black as coal; jet black; very black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalery', 'n.', 'See Colliery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalesced', 'imp. & p. p.', 'of Coalesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalescing', 'p. pr. & vb. n.', 'of Coalesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalesce', 'n.', 'To grow together; to unite by growth into one body; as,\n   the parts separated by a wound coalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalesce', 'n.', 'To unite in one body or product; to combine into one body\n   or community; as, vapors coalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalescence', 'n.', 'The act or state of growing together, as similar\n   parts; the act of uniting by natural affinity or attraction; the state\n   of being united; union; concretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalescent', 'a.', 'Growing together; cohering, as in the organic cohesion\n   of similar parts; uniting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalfish', 'n.', 'The pollock; -- called also, coalsey, colemie, colmey,\n   coal whiting, etc. See Pollock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalfish', 'n.', 'The beshow or candlefish of Alaska.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalfish', 'n.', 'The cobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalgoose', 'n.', 'The cormorant; -- so called from its black color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalite', 'v. i.', 'To unite or coalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalite', 'v. t.', 'To cause to unite or coalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalition', 'n.', 'The act of coalescing; union into a body or mass, as of\n   separate bodies or parts; as, a coalition of atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalition', 'n.', 'A combination, for temporary purposes, of persons,\n   parties, or states, having different interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalitioner', 'n.', 'A coalitionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalitionist', 'n.', 'One who joins or promotes a coalition; one who\n   advocates coalition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-allies', 'pl. ', 'of Co-ally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-ally', 'n.', 'A joint ally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal-meter', 'n.', 'A licensed or official coal measurer in London. See\n   Meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalmouse', 'n.', 'A small species of titmouse, with a black head; the\n   coletit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalpit', 'n.', 'A pit where coal is dug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coalpit', 'n.', 'A place where charcoal is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal tar', '', 'A thick, black, tarry liquid, obtained by the distillation\n   of bituminous coal in the manufacture of illuminating gas; used for\n   making printers ink, black varnish, etc. It is a complex mixture from\n   which many substances have been obtained, especially hydrocarbons of\n   the benzene or aromatic series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal-whipper', 'n.', 'One who raises coal out of the hold of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coal works', '', 'A place where coal is dug, including the machinery for\n   raising the coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaly', 'n.', 'Pertaining to, or resembling, coal; containing coal; of the\n   nature of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coamings', 'n. pl.', 'Raised pieces of wood of iron around a hatchway,\n   skylight, or other opening in the deck, to prevent water from running\n   bellow; esp. the fore-and-aft pieces of a hatchway frame as\n   distinguished from the transverse head ledges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coannex', 'v. t.', 'To annex with something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaptation', 'n.', 'The adaptation or adjustment of parts to each other, as\n   of a broken bone or dislocated joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarct', 'a.', 'Alt. of Coarctate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarctate', 'a.', 'To press together; to crowd; to straiten; to confine\n   closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarctate', 'a.', 'To restrain; to confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarctate', 'a.', 'Pressed together; closely connected; -- applied to\n   insects having the abdomen separated from the thorax only by a\n   constriction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarctation', 'n.', 'Confinement to a narrow space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarctation', 'n.', 'Pressure; that which presses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarctation', 'n.', 'A stricture or narrowing, as of a canal, cavity, or\n   orifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarse', 'superl.', 'Large in bulk, or composed of large parts or\n   particles; of inferior quality or appearance; not fine in material or\n   close in texture; gross; thick; rough; -- opposed to fine; as, coarse\n   sand; coarse thread; coarse cloth; coarse bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarse', 'superl.', 'Not refined; rough; rude; unpolished; gross;\n   indelicate; as, coarse manners; coarse language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarse-grained', 'a.', 'Having a coarse grain or texture, as wood; hence,\n   wanting in refinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarsely', 'adv.', 'In a coarse manner; roughly; rudely; inelegantly;\n   uncivilly; meanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarsen', 'v. t.', 'To make coarse or vulgar; as, to coarsen ones\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarseness', 'n.', 'The quality or state of being coarse; roughness;\n   inelegance; vulgarity; grossness; as, coarseness of food, texture,\n   manners, or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coarticulation', 'n.', 'The union or articulation of bones to form a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-assessor', 'n.', 'A joint assessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'v. t.', 'The side of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'v. t.', 'The exterior line, limit, or border of a country;\n   frontier border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'v. t.', 'The seashore, or land near it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coasted', 'imp. & p. p.', 'of Coast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coasting', 'p. pr. & vb. n.', 'of Coast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'n.', 'To draw or keep near; to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'n.', 'To sail by or near the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'n.', 'To sail from port to port in the same country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'n.', 'To slide down hill; to slide on a sled, upon snow or ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'v. t.', 'To draw near to; to approach; to keep near, or by the\n   side of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'v. t.', 'To sail by or near; to follow the coast line of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coast', 'v. t.', 'To conduct along a coast or river bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coastal', 'a.', 'Of or pertaining to a coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaster', 'n.', 'A vessel employed in sailing along a coast, or engaged in\n   the coasting trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaster', 'n.', 'One who sails near the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coasting', 'a.', 'Sailing along or near a coast, or running between ports\n   along a coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coasting', 'n.', 'A sailing along a coast, or from port to port; a carrying\n   on a coasting trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coasting', 'n.', 'Sliding down hill; sliding on a sled upon snow or ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coastwise', 'adv.', 'Alt. of Coastways');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coastways', 'adv.', 'By way of, or along, the coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'An outer garment fitting the upper part of the body;\n   especially, such a garment worn by men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'A petticoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'The habit or vesture of an order of men, indicating the order\n   or office; cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'An external covering like a garment, as fur, skin, wool,\n   husk, or bark; as, the horses coats were sleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'A layer of any substance covering another; a cover; a\n   tegument; as, the coats of the eye; the coats of an onion; a coat of\n   tar or varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'Same as Coat of arms. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'n.', 'A coat card. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coated', 'imp. & p. p.', 'of Coat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coating', 'p. pr. & vb. n.', 'of Coat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'v. t.', 'To cover with a coat or outer garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coat', 'v. t.', 'To cover with a layer of any substance; as, to coat a jar\n   with tin foil; to coat a ceiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coatee', 'n.', 'A coat with short flaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coati', 'n.', 'A mammal of tropical America of the genus Nasua, allied to\n   the raccoon, but with a longer body, tail, and nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coating', 'n.', 'A coat or covering; a layer of any substance, as a cover\n   or protection; as, the coating of a retort or vial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coating', 'n.', 'Cloth for coats; as, an assortment of coatings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coatless', 'a.', 'Not wearing a coat; also, not possessing a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaxed', 'imp. & p. p.', 'of Coax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaxing', 'p. pr. & vb. n.', 'of Coax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coax', 'v. t.', 'To persuade by gentle, insinuating courtesy, flattering,\n   or fondling; to wheedle; to soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coax', 'n.', 'A simpleton; a dupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaxation', 'n.', 'The act of croaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaxer', 'n.', 'One who coaxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaxingly', 'adv.', 'In a coaxing manner; by coaxing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'The top or head of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A leader or chief; a conspicuous person, esp. a rich covetous\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'The axis on which the kernels of maize or indian corn grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A spider; perhaps from its shape; it being round like a head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A young herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A fish; -- also called millers thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A short-legged and stout horse, esp. one used for the saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A sea mew or gull; esp., the black-backed gull (Larus\n   marinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A lump or piece of anything, usually of a somewhat large size,\n   as of coal, or stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A cobnut; as, Kentish cobs. See Cobnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'Clay mixed with straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A punishment consisting of blows inflicted on the buttocks\n   with a strap or a flat piece of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'n.', 'A Spanish coin formerly current in Ireland, worth abiut 4s.\n   6d.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbed', 'imp. & p. p.', 'of Cob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbing', 'p. pr. & vb. n.', 'of Cob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'v. t.', 'To strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'v. t.', 'To break into small pieces, as ore, so as to sort out its\n   better portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cob', 'v. t.', 'To punish by striking on the buttocks with a strap, a flat\n   piece of wood, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobaea', 'n.', 'A genus of climbing plants, native of Mexico and South\n   America. C. scandens is a conservatory climber with large bell-shaped\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobalt', 'n.', 'A tough, lustrous, reddish white metal of the iron group,\n   not easily fusible, and somewhat magnetic. Atomic weight 59.1. Symbol\n   Co.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobalt', 'n.', 'A commercial name of a crude arsenic used as fly poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobaltic', 'a.', 'Pertaining to, derived from, or containing, cobalt; --\n   said especially of those compounds in which cobalt has higher valence;\n   as, cobaltic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobaltiferous', 'a.', 'Containing cobalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobaltine', 'n.', 'Alt. of Cobaltite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobaltite', 'n.', 'A mineral of a nearly silver-white color, composed of\n   arsenic, sulphur, and cobalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobaltous', 'a.', 'Pertaining to, derived from, or containing, cobalt; --\n   said esp. of cobalt compounds in which the metal has its lower valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbing', 'a.', 'Haughty; purse-proud. See Cob, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobble', 'n.', 'A fishing boat. See Coble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobble', 'n.', 'A cobblestone.');
