  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbled', 'imp. & p. p.', 'of Cobble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbling', 'p. pr. & vb. n.', 'of Cobble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobble', 'v. t.', 'To make or mend coarsely; to patch; to botch; as, to\n   cobble shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobble', 'v. t.', 'To make clumsily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobble', 'v. t.', 'To pave with cobblestones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbler', 'n.', 'A mender of shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbler', 'n.', 'A clumsy workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobbler', 'n.', 'A beverage. See Sherry cobbler, under Sherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobblestone', 'n.', 'A large pebble; a rounded stone not too large to be\n   handled; a small boulder; -- used for paving streets and for other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobby', 'n.', 'Headstrong; obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobby', 'n.', 'Stout; hearty; lively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobelligerent', 'a.', 'Carrying on war in conjunction with another power.');
