  INSERT INTO words (word, word_type, description) VALUES  (    'Cobia', 'n.', 'An oceanic fish of large size (Elacate canada); the\n   crabeater; -- called also bonito, cubbyyew, coalfish, and sergeant\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobiron', 'n.', 'An andiron with a knob at the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobishop', 'n.', 'A joint or coadjutant bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coble', 'n.', 'A flat-floored fishing boat with a lug sail, and a drop\n   rudder extending from two to four feet below the keel. It was\n   originally used on the stormy coast of Yorkshire, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobnut', 'n.', 'A large roundish variety of the cultivated hazelnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobnut', 'n.', 'A game played by children with nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coboose', 'n.', 'See Caboose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobourg', 'n.', 'A thin worsted fabric for womens dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobra', 'n.', 'See Copra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobra', 'n.', 'The cobra de capello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobra de capello', '', 'The hooded snake (Naia tripudians); a highly\n   venomous serpent inhabiting India.Naja --');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobstone', 'n.', 'Cobblestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobswan', 'n.', 'A large swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobwall', 'n.', 'A wall made of clay mixed with straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobweb', 'n.', 'The network spread by a spider to catch its prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobweb', 'n.', 'A snare of insidious meshes designed to catch the ignorant\n   and unwary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobweb', 'n.', 'That which is thin and unsubstantial, or flimsy and\n   worthless; rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobweb', 'n.', 'The European spotted flycatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobwebbed', 'a.', 'Abounding in cobwebs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobwebby', 'a.', 'Abounding in cobwebs, or any fine web; resembling a\n   cobweb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cobwork', 'a.', 'Built of logs, etc., laid horizontally, with the ends\n   dovetailed together at the corners, as in a log house; in marine work,\n   often surrounding a central space filled with stones; as, a cobwork\n   dock or breakwater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coca', 'n.', 'The dried leaf of a South American shrub (Erythroxylon Coca).\n   In med., called Erythroxylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocagne', 'n.', 'An imaginary country of idleness and luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocagne', 'n.', 'The land of cockneys; cockneydom; -- a term applied to\n   London and its suburbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocaine', 'n.', 'A powerful alkaloid, C17H21NO4, obtained from the leaves\n   of coca. It is a bitter, white, crystalline substance, and is\n   remarkable for producing local insensibility to pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocciferous', 'a.', 'Bearing or producing berries; bacciferous; as,\n   cocciferous trees or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccinella', 'n.', 'A genus of small beetles of many species. They and\n   their larvae feed on aphids or plant lice, and hence are of great\n   benefit to man. Also called ladybirds and ladybugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccobacteria', 'pl. ', 'of Coccobacterium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccobacterium', 'n.', 'One of the round variety of bacteria, a vegetable\n   organism, generally less than a thousandth of a millimeter in diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccolite', 'n.', 'A granular variety of pyroxene, green or white in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccolith', 'n.', 'One of a kind of minute, calcareous bodies, probably\n   vegetable, often abundant in deep-sea mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccosphere', 'n.', 'A small, rounded, marine organism, capable of braking\n   up into coccoliths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccosteus', 'n.', 'An extinct genus of Devonian ganoid fishes, having the\n   broad plates about the head studded with berrylike tubercles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocculus Indicus', 'n.', 'The fruit or berry of the Anamirta Cocculus, a\n   climbing plant of the East Indies. It is a poisonous narcotic and\n   stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocci', 'pl. ', 'of Coccus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccus', 'n.', 'One of the separable carpels of a dry fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccus', 'n.', 'A genus of hemipterous insects, including scale insects,\n   and the cochineal insect (Coccus cacti).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccus', 'n.', 'A form of bacteria, shaped like a globule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccygeal', 'a.', 'Of or pertaining to the coccyx; as, the coccygeal\n   vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccygeous', 'a.', 'Coccygeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccyges', 'pl. ', 'of Coccyx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coccyx', 'n.', 'The end of the vertebral column beyond the sacrum in man\n   and tailless monkeys. It is composed of several vertebrae more or less\n   consolidated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochineal', '', 'A dyestuff consisting of the dried bodies of females of\n   the Coccus cacti, an insect native in Mexico, Central America, etc.,\n   and found on several species of cactus, esp. Opuntia cochinellifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochineal fig', '', 'A plant of Central and Southern America, of the Cactus\n   family, extensively cultivated for the sake of the cochineal insect,\n   which lives on it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochin fowl', '', 'A large variety of the domestic fowl, originally from\n   Cochin China (Anam).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochlea', 'n.', 'An appendage of the labyrinth of the internal ear, which\n   is elongated and coiled into a spiral in mammals. See Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochlear', 'a.', 'Of or pertaining to the cochlea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochleare', 'n.', 'A spoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochleare', 'n.', 'A spoonful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocleariform', 'a.', 'Spoon-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochleary', 'a.', 'Same as Cochleate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochleate', 'a.', 'Alt. of Cochleated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cochleated', 'a.', 'Having the form of a snail shell; spiral; turbinated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The male of birds, particularly of gallinaceous or domestic\n   fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'A vane in the shape of a cock; a weathercock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'A chief man; a leader or master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The crow of a cock, esp. the first crow in the morning;\n   cockcrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'A faucet or valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The style of gnomon of a dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The indicator of a balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The bridge piece which affords a bearing for the pivot of a\n   balance in a clock or watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocked', 'imp. & p. p.', 'of Cock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocking', 'p. pr. & vb. n.', 'of Cock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. t.', 'To set erect; to turn up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. t.', 'To shape, as a hat, by turning up the brim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. t.', 'To set on one side in a pert or jaunty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. t.', 'To turn (the eye) obliquely and partially close its lid,\n   as an expression of derision or insinuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. i.', 'To strut; to swagger; to look big, pert, or menacing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The act of cocking; also, the turn so given; as, a cock of\n   the eyes; to give a hat a saucy cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The notch of an arrow or crossbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'The hammer in the lock of a firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. t.', 'To draw the hammer of (a firearm) fully back and set it\n   for firing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. i.', 'To draw back the hammer of a firearm, and set it for\n   firing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'A small concial pile of hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'v. t.', 'To put into cocks or heaps, as hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'A small boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock', 'n.', 'A corruption or disguise of the word God, used in oaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockade', 'n.', 'A badge, usually in the form of a rosette, or knot, and\n   generally worn upon the hat; -- used as an indication of military or\n   naval service, or party allegiance, and in England as a part of the\n   livery to indicate that the wearer is the servant of a military or\n   naval officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockaded', 'a.', 'Wearing a cockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock-a-hoop', 'a.', 'Boastful; defiant; exulting. Also used adverbially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockal', 'n.', 'A game played with sheeps bones instead of dice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockal', 'n.', 'The bone used in playing the game; -- called also huckle\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockaleekie', 'n.', 'A favorite soup in Scotland, made from a capon highly\n   seasoned, and boiled with leeks and prunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockamaroo', 'n.', 'The Russian variety of bagatelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockateel', 'n.', 'An Australian parrot (Calopsitta Novae-Hollandiae); --\n   so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockatoo', 'n.', 'A bird of the Parrot family, of the subfamily Cacatuinae,\n   having a short, strong, and much curved beak, and the head ornamented\n   with a crest, which can be raised or depressed at will. There are\n   several genera and many species; as the broad-crested (Plictolophus, /\n   Cacatua, cristatus); the sulphur-crested (P. galeritus); etc. The palm\n   or great black cockatoo of Australia is Microglossus aterrimus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockatrice', 'n.', 'A fabulous serpent whose breath and look were said to\n   be fatal. See Basilisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockatrice', 'n.', 'A representation of this serpent. It has the head,\n   wings, and legs of a bird, and tail of a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockatrice', 'n.', 'A venomous serpent which which cannot now be\n   identified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockatrice', 'n.', 'Any venomous or deadly thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockbill', 'v. t.', 'To tilt up one end of so as to make almost vertical;\n   as, to cockbill the yards as a sign of mourning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockboat', 'n.', 'A small boat, esp. one used on rivers or near the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock-brained', 'a.', 'Giddy; rash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockchafer', 'n.', 'A beetle of the genus Melolontha (esp. M. vulgaris) and\n   allied genera; -- called also May bug, chafer, or dorbeetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockcrow', 'n.', 'Alt. of Cockcrowing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockcrowing', 'n.', 'The time at which cocks first crow; the early morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockered', 'imp. & p. p.', 'of Cocker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockering', 'p. pr. & vb. n.', 'of Cocker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocker', 'v. t.', 'To treat with too great tenderness; to fondle; to\n   indulge; to pamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocker', 'n.', 'One given to cockfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocker', 'n.', 'A small dog of the spaniel kind, used for starting up\n   woodcocks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocker', 'n.', 'A rustic high shoe or half-boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockerel', 'n.', 'A young cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocket', 'n.', 'Pert; saucy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocket', 'n.', 'A customhouse seal; a certified document given to a shipper\n   as a warrant that his goods have been duly entered and have paid duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocket', 'n.', 'An office in a customhouse where goods intended for export\n   are entered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocket', 'n.', 'A measure for bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockeye', 'n.', 'A squinting eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockeye', 'n.', 'The socket in the ball of a millstone, which sits on the\n   cockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockfight', 'n.', 'A match or contest of gamecocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockfighting', 'n.', 'The act or practice of pitting gamecocks to fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockfighting', 'a.', 'Addicted to cockfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockhead', 'n.', 'The rounded or pointed top of a grinding mill spindle,\n   forming a pivot on which the stone is balanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockhorse', 'n.', 'A childs rocking-horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockhorse', 'n.', 'A high or tall horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockhorse', 'a.', 'Lifted up, as one is on a tall horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockhorse', 'a.', 'Lofty in feeling; exultant; proud; upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockieleekie', 'n.', 'Same as Cockaleekie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocking', 'n.', 'Cockfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'A bivalve mollusk, with radiating ribs, of the genus\n   Cardium, especially C. edule, used in Europe for food; -- sometimes\n   applied to similar shells of other genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'A cockleshell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'The mineral black tourmaline or schorl; -- so called by the\n   Cornish miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'The fire chamber of a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'A hop-drying kiln; an oast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'The dome of a heating furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockled', 'imp. & p. p.', 'of Cockle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockling', 'p. pr. & vb. n.', 'of Cockle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'v. t.', 'To cause to contract into wrinkles or ridges, as some\n   kinds of cloth after a wetting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'A plant or weed that grows among grain; the corn rose\n   (Luchnis Githage).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockle', 'n.', 'The Lotium, or darnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocklebur', 'n.', 'A coarse, composite weed, having a rough or prickly\n   fruit; one of several species of the genus Xanthium; -- called also\n   clotbur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockled', 'a.', 'Inclosed in a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockled', 'a.', 'Wrinkled; puckered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockler', 'n.', 'One who takes and sells cockles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockleshell', 'n.', 'One of the shells or valves of a cockle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockleshell', 'n.', 'A light boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockloft', 'n.', 'An upper loft; a garret; the highest room in a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockmaster', 'n.', 'One who breeds gamecocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockmatch', 'n.', 'A cockfight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockneys', 'pl. ', 'of Cockney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockney', 'n.', 'An effeminate person; a spoilt child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockney', 'n.', 'A native or resident of the city of London; -- used\n   contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockney', 'a.', 'Of or relating to, or like, cockneys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockneydom', 'n.', 'The region or home of cockneys; cockneys, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockneyfy', 'v. t.', 'To form with the manners or character of a cockney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockneyish', 'a.', 'Characteristic of, or resembling, cockneys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockneyism', 'n.', 'The characteristics, manners, or dialect, of a cockney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cock-padle', 'n.', 'See Lumpfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockpit', 'n.', 'A pit, or inclosed area, for cockfights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockpit', 'n.', 'The Privy Council room at Westminster; -- so called\n   because built on the site of the cockpit of Whitehall palace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockpit', 'n.', 'That part of a war vessel appropriated to the wounded\n   during an engagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockpit', 'n.', 'In yachts and other small vessels, a space lower than the\n   rest of the deck, which affords easy access to the cabin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockroach', 'n.', 'An orthopterous insect of the genus Blatta, and allied\n   genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockscomb', 'n.', 'See Coxcomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockscomb', 'n.', 'A plant (Celosia cristata); of many varieties,\n   cultivated for its broad, fantastic spikes of brilliant flowers; --\n   sometimes called garden cockscomb. Also the Pedicularis, or lousewort,\n   the Rhinanthus Crista-galli, and the Onobrychis Crista-galli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockshead', 'n.', 'A leguminous herb (Onobrychis Caput-galli); having small\n   spiny-crested pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockshut', 'n.', 'A kind of net to catch woodcock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockshy', 'n.', 'A game in which trinkets are set upon sticks, to be thrown\n   at by the players; -- so called from an ancient popular sport which\n   consisted in \"shying\" or throwing cudgels at live cocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockshy', 'n.', 'An object at which stones are flung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockspur', 'n.', 'A variety of Crataegus, or hawthorn (C. Crus-galli);\n   having long, straight thorns; -- called also Cockspur thorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocksure', 'a.', 'Perfectly safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocksure', 'a.', 'Quite certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockswain', 'n.', 'The steersman of a boat; a petty officer who has charge\n   of a boat and its crew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocktail', 'n.', 'A beverage made of brandy, whisky, or gin, iced,\n   flavored, and sweetened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocktail', 'n.', 'A horse, not of pure breed, but having only one eighth or\n   one sixteenth impure blood in his veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocktail', 'n.', 'A mean, half-hearted fellow; a coward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocktail', 'n.', 'A species of rove beetle; -- so called from its habit of\n   elevating the tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockup', 'n.', 'A large, highly esteemed, edible fish of India (Lates\n   calcarifer); -- also called begti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cockweed', 'n.', 'Peppergrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocky', 'a.', 'Pert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coco', '', 'Alt. of Coco palm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coco palm', '', 'See Cocoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoa', '', 'Alt. of Cocoa palm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoa palm', '', 'A palm tree producing the cocoanut (Cocos nucifera). It\n   grows in nearly all tropical countries, attaining a height of sixty or\n   eighty feet. The trunk is without branches, and has a tuft of leaves at\n   the top, each being fifteen or twenty feet in length, and at the base\n   of these the nuts hang in clusters; the cocoanut tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoa', 'n.', 'A preparation made from the seeds of the chocolate tree, and\n   used in making, a beverage; also the beverage made from cocoa or cocoa\n   shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoanut', 'n.', 'The large, hard-shelled nut of the cocoa palm. It yields\n   an agreeable milky liquid and a white meat or albumen much used as food\n   and in making oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocobolo', 'n.', 'Alt. of Cocobolas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocobolas', 'n.', 'A very beautiful and hard wood, obtained in the West\n   India Islands. It is used in cabinetmaking, for the handles of tools,\n   and for various fancy articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoon', 'n.', 'An oblong case in which the silkworm lies in its chrysalis\n   state. It is formed of threads of silk spun by the worm just before\n   leaving the larval state. From these the silk of commerce is prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoon', 'n.', 'The case constructed by any insect to contain its larva or\n   pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoon', 'n.', 'The case of silk made by spiders to protect their eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cocoon', 'n.', 'The egg cases of mucus, etc., made by leeches and other\n   worms.');
