  INSERT INTO words (word, word_type, description) VALUES  (    'Coefficient', 'n.', 'A number or letter put before a letter or quantity,\n   known or unknown, to show how many times the latter is to be taken; as,\n   6x; bx; here 6 and b are coefficients of x.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coefficient', 'n.', 'A number, commonly used in computation as a factor,\n   expressing the amount of some change or effect under certain fixed\n   conditions as to temperature, length, volume, etc.; as, the coefficient\n   of expansion; the coefficient of friction.');
