  INSERT INTO words (word, word_type, description) VALUES  (    'Coenenchyma', 'n.', 'The common tissue which unites the polyps or zooids of\n   a compound anthozoan or coral. It may be soft or more or less ossified.\n   See Coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coenesthesis', 'n.', 'Common sensation or general sensibility, as\n   distinguished from the special sensations which are located in, or\n   ascribed to, separate organs, as the eye and ear. It is supposed to\n   depend on the ganglionic system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coenobite', 'n.', 'See Cenobite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coenoecium', 'n.', 'The common tissue which unites the various zooids of a\n   bryozoan.');
