  INSERT INTO words (word, word_type, description) VALUES  (    'Coerce', 'v. t.', 'To restrain by force, especially by law or authority; to\n   repress; to curb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coerce', 'v. t.', 'To compel or constrain to any action; as, to coerce a\n   man to vote for a certain candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coerce', 'v. t.', 'To compel or enforce; as, to coerce obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coercible', 'a.', 'Capable of being coerced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coercion', 'n.', 'The act or process of coercing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coercion', 'n.', 'The application to another of either physical or moral\n   force. When the force is physical, and cannot be resisted, then the act\n   produced by it is a nullity, so far as concerns the party coerced. When\n   the force is moral, then the act, though voidable, is imputable to the\n   party doing it, unless he be so paralyzed by terror as to act\n   convulsively. At the same time coercion is not negatived by the fact of\n   submission under force. \"Coactus volui\" (I consented under compulsion)\n   is the condition of mind which, when there is volition forced by\n   coercion, annuls the result of such coercion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coercitive', 'a.', 'Coercive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coercive', 'a.', 'Serving or intended to coerce; having power to constrain.');
