  INSERT INTO words (word, word_type, description) VALUES  (    'Cogitate', 'v. t.', 'To think over; to plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cogitation', 'n.', 'The act of thinking; thought; meditation;\n   contemplation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cogitative', 'a.', 'Possessing, or pertaining to, the power of thinking or\n   meditating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cogitative', 'a.', 'Given to thought or contemplation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cogman', 'n.', 'A dealer in cogware or coarse cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognac', 'n.', 'A kind of French brandy, so called from the town of Cognac.');
