  INSERT INTO words (word, word_type, description) VALUES  (    'Cognate', 'a.', 'Of the same or a similar nature; of the same family;\n   proceeding from the same stock or root; allied; kindred; as, a cognate\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognate', 'n.', 'One who is related to another on the female side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognate', 'n.', 'One of a number of things allied in origin or nature; as,\n   certain letters are cognates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognateness', 'n.', 'The state of being cognate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognati', 'n. pl.', 'Relatives by the mothers side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognation', 'n.', 'Relationship by blood; descent from the same original;\n   kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognation', 'n.', 'Participation of the same nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognation', 'n.', 'That tie of consanguinity which exists between persons\n   descended from the same mother; -- used in distinction from agnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognatus', 'n.', 'A person connected through cognation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognisor', 'n.', 'Alt. of Cognisee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognisee', 'n.', 'See Cognizor, Cognizee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognition', 'v. t.', 'The act of knowing; knowledge; perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognition', 'v. t.', 'That which is known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognitive', 'a.', 'Knowing, or apprehending by the understanding; as,\n   cognitive power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizable', 'a.', 'Capable of being known or apprehended; as, cognizable\n   causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizable', 'a.', 'Fitted to be a subject of judicial investigation;\n   capable of being judicially heard and determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizably', 'adv.', 'In a cognizable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'Apprehension by the understanding; perception;\n   observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'Recollection; recognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'Jurisdiction, or the power given by law to hear and\n   decide controversies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'The hearing a matter judicially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'An acknowledgment of a fine of lands and tenements or\n   confession of a thing done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'A form of defense in the action of replevin, by which\n   the defendant insists that the goods were lawfully taken, as a\n   distress, by defendant, acting as servant for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizance', 'n.', 'The distinguishing mark worn by an armed knight,\n   usually upon the helmet, and by his retainers and followers: Hence, in\n   general, a badge worn by a retainer or dependent, to indicate the\n   person or party to which he belonged; a token by which a thing may be\n   known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizant', 'a.', 'Having cognizance or knowledge. (of).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognize', 'v. t.', 'To know or perceive; to recognize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizee', 'n.', 'One to whom a fine of land was acknowledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognizor', 'n.', 'One who acknowledged the right of the plaintiff or\n   cognizee in a fine; the defendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognomen', 'n.', 'The last of the three names of a person among the ancient\n   Romans, denoting his house or family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognomen', 'n.', 'A surname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognominal', 'a.', 'Of or pertaining to a cognomen; of the nature of a\n   surname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognominal', 'n.', 'One bearing the same name; a namesake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognomination', 'n.', 'A cognomen or surname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscence', 'n.', 'Cognizance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscenti', 'pl. ', 'of Cognoscente');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscente', 'n.', 'A connoisseur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscibility', 'n.', 'The quality of being cognoscible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscible', 'a.', 'Capable of being known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscible', 'a.', 'Liable to judicial investigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognoscitive', 'a.', 'Having the power of knowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cognovit', 'n.', 'An instrument in writing whereby a defendant in an action\n   acknowledges a plaintiffs demand to be just.');
