  INSERT INTO words (word, word_type, description) VALUES  (    'Cogue', 'n.', 'A small wooden vessel; a pail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cogware', 'n.', 'A coarse, narrow cloth, like frieze, used by the lower\n   classes in the sixteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cogwheel', 'n.', 'A wheel with cogs or teeth; a gear wheel. See Illust. of\n   Gearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabited', 'imp. & p. p.', 'of Cohabit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabiting', 'p. pr. & vb. n.', 'of Cohabit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabit', 'v.', 'To inhabit or reside in company, or in the same place or\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabit', 'v.', 'To dwell or live together as husband and wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabitant', 'n.', 'One who dwells with another, or in the same place or\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabitation', 'n.', 'The act or state of dwelling together, or in the same\n   place with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabitation', 'n.', 'The living together of a man and woman in supposed\n   sexual relationship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohabiter', 'n.', 'A cohabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coheir', 'n.', 'A joint heir; one of two or more heirs; one of several\n   entitled to an inheritance.');
