  INSERT INTO words (word, word_type, description) VALUES  (    'Coheirship', 'n.', 'The state of being a coheir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherald', 'n.', 'A joint herald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohered', 'imp. & p. p.', 'of Cohere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohering', 'p. pr. & vb. n.', 'of Cohere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohere', 'a.', 'To stick together; to cleave; to be united; to hold fast,\n   as parts of the same mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohere', 'a.', 'To be united or connected together in subordination to one\n   purpose; to follow naturally and logically, as the parts of a\n   discourse, or as arguments in a train of reasoning; to be logically\n   consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohere', 'a.', 'To suit; to agree; to fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherence', 'n.', 'Alt. of Coherency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherency', 'n.', 'A sticking or cleaving together; union of parts of the\n   same body; cohesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherency', 'n.', 'Connection or dependence, proceeding from the\n   subordination of the parts of a thing to one principle or purpose, as\n   in the parts of a discourse, or of a system of philosophy;\n   consecutiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherent', 'a.', 'Sticking together; cleaving; as the parts of bodies;\n   solid or fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherent', 'a.', 'Composed of mutually dependent parts; making a logical\n   whole; consistent; as, a coherent plan, argument, or discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherent', 'a.', 'Logically consistent; -- applied to persons; as, a\n   coherent thinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherent', 'a.', 'Suitable or suited; adapted; accordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coherently', 'adv.', 'In a coherent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesibility', 'n.', 'The state of being cohesible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesible', 'a.', 'Capable of cohesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesion', 'n.', 'The act or state of sticking together; close union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesion', 'n.', 'That from of attraction by which the particles of a body\n   are united throughout the mass, whether like or unlike; --\n   distinguished from adhesion, which unites bodies by their adjacent\n   surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesion', 'n.', 'Logical agreement and dependence; as, the cohesion of\n   ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesive', 'a.', 'Holding the particles of a homogeneous body together; as,\n   cohesive attraction; producing cohesion; as, a cohesive force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cohesive', 'a.', 'Cohering, or sticking together, as in a mass; capable of\n   cohering; tending to cohere; as, cohesive clay.');
