  INSERT INTO words (word, word_type, description) VALUES  (    'Coif', 'n.', 'A cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coif', 'n.', 'A close-fitting cap covering the sides of the head, like a\n   small hood without a cape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coif', 'n.', 'An official headdress, such as that worn by certain judges in\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coif', 'v. t.', 'To cover or dress with, or as with, a coif.');
