  INSERT INTO words (word, word_type, description) VALUES  (    'Coin', 'v. t.', 'To acquire rapidly, as money; to make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coin', 'v. i.', 'To manufacture counterfeit money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coinage', 'v. t.', 'The act or process of converting metal into money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coinage', 'v. t.', 'Coins; the aggregate coin of a time or place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coinage', 'v. t.', 'The cost or expense of coining money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coinage', 'v. t.', 'The act or process of fabricating or inventing;\n   formation; fabrication; that which is fabricated or forged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coincided', 'imp. & p. p.', 'of Coincide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coinciding', 'p. pr. & vb. n.', 'of Coincide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coincide', 'n.', 'To occupy the same place in space, as two equal\n   triangles, when placed one on the other.');
