  INSERT INTO words (word, word_type, description) VALUES  (    'Coincidence', 'n.', 'The condition of occupying the same place in space;\n   as, the coincidence of circles, surfaces, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coincidence', 'n.', 'The condition or fact of happening at the same time;\n   as, the coincidence of the deaths of John Adams and Thomas Jefferson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coincidence', 'n.', 'Exact correspondence in nature, character, result,\n   circumstances, etc.; concurrence; agreement.');
