  INSERT INTO words (word, word_type, description) VALUES  (    'Coincident', 'n.', 'One of two or more coincident events; a coincidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coincidental', 'a.', 'Coincident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coincidently', 'adv.', 'With coincidence.');
