  INSERT INTO words (word, word_type, description) VALUES  (    'Coiner', 'n.', 'An inventor or maker, as of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coinhabitant', 'n.', 'One who dwells with another, or with others.');
