  INSERT INTO words (word, word_type, description) VALUES  (    'Coition', 'n.', 'A coming together; sexual intercourse; copulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cojoin', 'v. t.', 'To join; to conjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cojuror', 'n.', 'One who swears to anothers credibility.');
