  INSERT INTO words (word, word_type, description) VALUES  (    'Coke', 'v. t.', 'To convert into coke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cokenay', 'n.', 'A cockney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cokernut', 'n.', 'The cocoanut.');
