  INSERT INTO words (word, word_type, description) VALUES  (    'Colation', 'n.', 'The act or process of straining or filtering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colatitude', 'n.', 'The complement of the latitude, or the difference\n   between any latitude and ninety degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colature', 'n.', 'The process of straining; the matter strained; a\n   strainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colbertine', 'n.', 'A kind of lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colchicine', 'n.', 'A powerful vegetable alkaloid, C17H19NO5, extracted\n   from the Colchicum autumnale, or meadow saffron, as a white or\n   yellowish amorphous powder, with a harsh, bitter taste; -- called also\n   colchicia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colchicum', 'n.', 'A genus of bulbous-rooted plants found in many parts of\n   Europe, including the meadow saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colcothar', 'n.', 'Polishing rouge; a reddish brown oxide of iron, used in\n   polishing glass, and also as a pigment; -- called also crocus Martis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Deprived of heat, or having a low temperature; not warm or\n   hot; gelid; frigid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Lacking the sensation of warmth; suffering from the absence\n   of heat; chilly; shivering; as, to be cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Not pungent or acrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Wanting in ardor, intensity, warmth, zeal, or passion;\n   spiritless; unconcerned; reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Unwelcome; disagreeable; unsatisfactory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Wanting in power to excite; dull; uninteresting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Affecting the sense of smell (as of hunting dogs) but feebly;\n   having lost its odor; as, a cold scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Not sensitive; not acute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Distant; -- said, in the game of hunting for some object, of\n   a seeker remote from the thing concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'Having a bluish effect. Cf. Warm, 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'The relative absence of heat or warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'The sensation produced by the escape of heat; chilliness or\n   chillness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'n.', 'A morbid state of the animal system produced by exposure to\n   cold or dampness; a catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold', 'v. i.', 'To become cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-blooded', 'a.', 'Having cold blood; -- said of fish or animals whose\n   blood is but little warmer than the water or air about them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-blooded', 'a.', 'Deficient in sensibility or feeling; hard-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-blooded', 'a.', 'Not thoroughbred; -- said of animals, as horses,\n   which are derived from the common stock of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coldfinch', 'n.', 'A British wagtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-hearted', 'a.', 'Wanting passion or feeling; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coldish', 'a.', 'Somewhat cold; cool; chilly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coldly', 'adv.', 'In a cold manner; without warmth, animation, or feeling;\n   with indifference; calmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coldness', 'n.', 'The state or quality of being cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-short', 'a.', 'Brittle when cold; as, cold-short iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-shut', 'a.', 'Closed while too cold to become thoroughly welded; --\n   said of a forging or casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cold-shut', 'n.', 'An imperfection caused by such insufficient welding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cole', 'n.', 'A plant of the Brassica or Cabbage genus; esp. that form of\n   B. oleracea called rape and coleseed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-legatee', 'n.', 'A joint legatee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colegoose', 'n.', 'See Coalgoose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colemanite', 'n.', 'A hydrous borate of lime occurring in transparent\n   colorless or white crystals, also massive, in Southern California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colemouse', 'n.', 'See Coletit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleopter', 'n.', 'One of the Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleoptera', 'n. pl.', 'An order of insects having the anterior pair of\n   wings (elytra) hard and horny, and serving as coverings for the\n   posterior pair, which are membranous, and folded transversely under the\n   others when not in use. The mouth parts form two pairs of jaws\n   (mandibles and maxillae) adapted for chewing. Most of the Coleoptera\n   are known as beetles and weevils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleopteral', 'a.', 'Alt. of Coleopterous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleopterous', 'a.', 'Having wings covered with a case or sheath; belonging\n   to the Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleopteran', 'n.', 'One of the order of Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleopterist', 'n.', 'One versed in the study of the Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleorhiza', 'n.', 'A sheath in the embryo of grasses, inclosing the\n   caulicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleperch', 'n.', 'A kind of small black perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colera', 'n.', 'Bile; choler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleridgian', 'a.', 'Pertaining to Samuel Taylor Coleridge, or to his\n   poetry or metaphysics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleseed', 'n.', 'The common rape or cole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleslaw', 'n.', 'A salad made of sliced cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-lessee', 'n.', 'A partner in a lease taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-lessor', 'n.', 'A partner in giving a lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colestaff', 'n.', 'See Colstaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colet', '', 'Alt. of Collet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collet', '', 'An inferior church servant. [Obs.] See Acolyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coletit', 'n.', 'Alt. of Coaltit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coaltit', 'n.', 'A small European titmouse (Parus ater); so named from its\n   black color; -- called also coalmouse and colemouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coleus', 'n.', 'A plant of several species of the Mint family, cultivated\n   for its bright-colored or variegated leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colewort', 'n.', 'A variety of cabbage in which the leaves never form a\n   compact head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colewort', 'n.', 'Any white cabbage before the head has become firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colfox', 'n.', 'A crafty fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colic', 'n.', 'A severe paroxysmal pain in the abdomen, due to spasm,\n   obstruction, or distention of some one of the hollow viscera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colic', 'a.', 'Of or pertaining to colic; affecting the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colic', 'a.', 'Of or pertaining to the colon; as, the colic arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colical', 'a.', 'Of, pertaining to, or of the nature of, colic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colicky', 'a.', 'Pertaining to, or troubled with, colic; as, a colicky\n   disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colicroot', 'n.', 'A bitter American herb of the Bloodwort family, with the\n   leaves all radical, and the small yellow or white flowers in a long\n   spike (Aletris farinosa and A. aurea). Called sometimes star grass,\n   blackroot, blazing star, and unicorn root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colin', 'n.', 'The American quail or bobwhite. The name is also applied to\n   other related species. See Bobwhite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coliseum', 'n.', 'The amphitheater of Vespasian at Rome, the largest in the\n   world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colitis', 'n.', 'An inflammation of the large intestine, esp. of its mucous\n   membrane; colonitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coll', 'v. t.', 'To embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaborateur', 'n.', 'See Collaborator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaboration', 'n.', 'The act of working together; united labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaborator', 'n.', 'An associate in labor, especially in literary or\n   scientific labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collagen', 'n.', 'The chemical basis of ordinary connective tissue, as of\n   tendons or sinews and of bone. On being boiled in water it becomes\n   gelatin or glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collagenous', 'a.', 'Containing or resembling collagen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapsed', 'imp. & p. p.', 'of Collapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapsing', 'p. pr. & vb. n.', 'of Collapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapse', 'v. i.', 'To fall together suddenly, as the sides of a hollow\n   vessel; to close by falling or shrinking together; to have the sides or\n   parts of (a thing) fall in together, or be crushed in together; as, a\n   flue in the boiler of a steam engine sometimes collapses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapse', 'v. i.', 'To fail suddenly and completely, like something hollow\n   when subject to too much pressure; to undergo a collapse; as,\n   Maximilians government collapsed soon after the French army left\n   Mexico; many financial projects collapse after attaining some success\n   and importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapse', 'n.', 'A falling together suddenly, as of the sides of a hollow\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapse', 'n.', 'A sudden and complete failure; an utter failure of any\n   kind; a breakdown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapse', 'n.', 'Extreme depression or sudden failing of all the vital\n   powers, as the result of disease, injury, or nervous disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collapsion', 'n.', 'Collapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'Something worn round the neck, whether for use, ornament,\n   restraint, or identification; as, the collar of a coat; a ladys\n   collar; the collar of a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'A ring or cincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'A collar beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'The neck or line of junction between the root of a plant\n   and its stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'An ornament worn round the neck by knights, having on it\n   devices to designate their rank or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'A ringlike part of a mollusk in connection with esophagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'A colored ring round the neck of a bird or mammal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'A ring or round flange upon, surrounding, or against an\n   object, and used for restraining motion within given limits, or for\n   holding something to its place, or for hiding an opening around an\n   object; as, a collar on a shaft, used to prevent endwise motion of the\n   shaft; a collar surrounding a stovepipe at the place where it enters a\n   wall. The flanges of a piston and the gland of a stuffing box are\n   sometimes called collars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'An eye formed in the bight or bend of a shroud or stay to\n   go over the masthead; also, a rope to which certain parts of rigging,\n   as dead-eyes, are secured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'n.', 'A curb, or a horizontal timbering, around the mouth of a\n   shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collared', 'imp. & p. p.', 'of Collar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaring', 'p. pr. & vb. n.', 'of Collar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'v. t.', 'To seize by the collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar', 'v. t.', 'To put a collar on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collar bone', '', 'The clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collards', 'n. pl.', 'Young cabbage, used as \"greens\"; esp. a kind\n   cultivated for that purpose; colewort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collared', 'a.', 'Wearing a collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collared', 'a.', 'Wearing a collar; -- said of a man or beast used as a\n   bearing when a collar is represented as worn around the neck or loins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collared', 'a.', 'Rolled up and bound close with a string; as, collared\n   beef. See To collar beef, under Collar, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collatable', 'a.', 'Capable of being collated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collated', 'imp. & p. p.', 'of Collate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collating', 'p. pr. & vb. n.', 'of Collate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collate', 'v. t.', 'To compare critically, as books or manuscripts, in\n   order to note the points of agreement or disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collate', 'v. t.', 'To gather and place in order, as the sheets of a book\n   for binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collate', 'v. t.', 'To present and institute in a benefice, when the person\n   presenting is both the patron and the ordinary; -- followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collate', 'v. t.', 'To bestow or confer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collate', 'v. i.', 'To place in a benefice, when the person placing is both\n   the patron and the ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'a.', 'Coming from, being on, or directed toward, the side;\n   as, collateral pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'a.', 'Acting in an indirect way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'a.', 'Related to, but not strictly a part of, the main thing\n   or matter under consideration; hence, subordinate; not chief or\n   principal; as, collateral interest; collateral issues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'a.', 'Tending toward the same conclusion or result as\n   something else; additional; as, collateral evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'a.', 'Descending from the same stock or ancestor, but not in\n   the same line or branch or one from the other; -- opposed to lineal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'n.', 'A collateral relative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateral', 'n.', 'Collateral security; that which is pledged or deposited\n   as collateral security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaterally', 'adv.', 'Side by side; by the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaterally', 'adv.', 'In an indirect or subordinate manner; indirectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaterally', 'adv.', 'In collateral relation; not lineally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collateralness', 'n.', 'The state of being collateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The act of collating or comparing; a comparison of\n   one copy er thing (as of a book, or manuscript) with another of a like\n   kind; comparison, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The gathering and examination of sheets preparatory\n   to binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The act of conferring or bestowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'A conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The presentation of a clergyman to a benefice by a\n   bishop, who has it in his own gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The act of comparing the copy of any paper with its\n   original to ascertain its conformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The report of the act made by the proper officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'The right which an heir has of throwing the whole\n   heritable and movable estates of the deceased into one mass, and\n   sharing it equally with others who are of the same degree of kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'A collection of the Lives of the Fathers or other\n   devout work read daily in monasteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. t.', 'A light repast or luncheon; as, a cold collation; --\n   first applied to the refreshment on fast days that accompanied the\n   reading of the collation in monasteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collation', 'v. i.', 'To partake of a collation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collationer', 'n.', 'One who examines the sheets of a book that has just\n   been printed, to ascertain whether they are correctly printed, paged,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collatitious', 'a.', 'Brought together; contributed; done by contributions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collative', 'a.', 'Passing or held by collation; -- said of livings of\n   which the bishop and the patron are the same person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collator', 'n.', 'One who collates manuscripts, books, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collator', 'n.', 'One who collates to a benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collator', 'n.', 'One who confers any benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collaud', 'v. t.', 'To join in praising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colleague', 'n.', 'A partner or associate in some civil or ecclesiastical\n   office or employment. It is never used of partners in trade or\n   manufactures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colleague', 'v.t & i.', 'To unite or associate with another or with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colleagueship', 'n.', 'Partnership in office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collected', 'imp. & p. p.', 'of Collect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collecting', 'p. pr. & vb. n.', 'of Collect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collect', 'v. t.', 'To gather into one body or place; to assemble or bring\n   together; to obtain by gathering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collect', 'v. t.', 'To demand and obtain payment of, as an account, or\n   other indebtedness; as, to collect taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collect', 'v. t.', 'To infer from observed facts; to conclude from\n   premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collect', 'v. i.', 'To assemble together; as, the people collected in a\n   crowd; to accumulate; as, snow collects in banks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collect', 'v. i.', 'To infer; to conclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collect', 'v. t.', 'A short, comprehensive prayer, adapted to a particular\n   day, occasion, or condition, and forming part of a liturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectanea', 'v. t.', 'Passages selected from various authors, usually for\n   purposes of instruction; miscellany; anthology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collected', 'a.', 'Gathered together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collected', 'a.', 'Self-possessed; calm; composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectedly', 'adv.', 'Composedly; coolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectedness', 'n.', 'A collected state of the mind; self-possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectible', 'a.', 'Capable of being collected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'The act or process of collecting or of gathering; as,\n   the collection of specimens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'That which is collected');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'A gathering or assemblage of objects or of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'A gathering of money for charitable or other purposes,\n   as by passing a contribution box for freewill offerings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'That which is obtained in payment of demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'An accumulation of any substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'The act of inferring or concluding from premises or\n   observed facts; also, that which is inferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collection', 'n.', 'The jurisdiction of a collector of excise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectional', 'a.', 'Of or pertaining to collecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collective', 'a.', 'Formed by gathering or collecting; gathered into a\n   mass, sum, or body; congregated or aggregated; as, the collective body\n   of a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collective', 'a.', 'Deducing consequences; reasoning; inferring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collective', 'a.', 'Expressing a collection or aggregate of individuals, by\n   a singular form; as, a collective name or noun, like assembly, army,\n   jury, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collective', 'a.', 'Tending to collect; forming a collection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collective', 'a.', 'Having plurality of origin or authority; as, in\n   diplomacy, a note signed by the representatives of several governments\n   is called a collective note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collective', 'n.', 'A collective noun or name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectively', 'adv.', 'In a mass, or body; in a collected state; in the\n   aggregate; unitedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectiveness', 'n.', 'A state of union; mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectivism', 'n.', 'The doctrine that land and capital should be owned by\n   society collectively or as a whole; communism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectivist', 'n.', 'An advocate of collectivism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectivist', 'a.', 'Relating to, or characteristic of, collectivism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collector', 'n.', 'One who collects things which are separate; esp., one\n   who makes a business or practice of collecting works of art, objects in\n   natural history, etc.; as, a collector of coins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collector', 'n.', 'A compiler of books; one who collects scattered passages\n   and puts them together in one book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collector', 'n.', 'An officer appointed and commissioned to collect and\n   receive customs, duties, taxes, or toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collector', 'n.', 'One authorized to collect debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collector', 'n.', 'A bachelor of arts in Oxford, formerly appointed to\n   superintend some scholastic proceedings in Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectorate', 'n.', 'The district of a collector of customs; a\n   collectorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collectorship', 'n.', 'The office of a collector of customs or of taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collegatary', 'n.', 'A joint legatee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'College', 'n.', 'A collection, body, or society of persons engaged in\n   common pursuits, or having common duties and interests, and sometimes,\n   by charter, peculiar rights and privileges; as, a college of heralds; a\n   college of electors; a college of bishops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'College', 'n.', 'A society of scholars or friends of learning, incorporated\n   for study or instruction, esp. in the higher branches of knowledge; as,\n   the colleges of Oxford and Cambridge Universities, and many American\n   colleges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'College', 'n.', 'A building, or number of buildings, used by a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'College', 'n.', 'Fig.: A community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collegial', 'n.', 'Collegiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collegian', 'n.', 'A member of a college, particularly of a literary\n   institution so called; a student in a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collegiate', 'a.', 'Of or pertaining to a college; as, collegiate studies;\n   a collegiate society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collegiate', 'n.', 'A member of a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collembola', 'n. pl.', 'The division of Thysanura which includes Podura,\n   and allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collenchyma', 'n.', 'A tissue of vegetable cells which are thickend at the\n   angles and (usually) elongated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collet', 'n.', 'A small collar or neckband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collet', 'n.', 'A small metal ring; a small collar fastened on an arbor;\n   as, the collet on the balance arbor of a watch; a small socket on a\n   stem, for holding a drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collet', 'n.', 'The part of a ring containing the bezel in which the stone\n   is set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collet', 'n.', 'The flat table at the base of a brilliant. See Illust. of\n   Brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colleterial', 'a.', 'Of or pertaining to the colleterium of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colleterium', 'n.', 'An organ of female insects, containing a cement to\n   unite the ejected ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colletic', 'a.', 'Agglutinant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colletic', 'n.', 'An agglutinant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colley', 'n.', 'See Collie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collide', 'v. i.', 'To strike or dash against each other; to come into\n   collision; to clash; as, the vessels collided; their interests\n   collided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collide', 'v. t.', 'To strike or dash against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collidine', 'n.', 'One of a class of organic bases, C8H11N, usually pungent\n   oily liquids, belonging to the pyridine series, and obtained from bone\n   oil, coal tar, naphtha, and certain alkaloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collie', 'n.', 'The Scotch shepherd dog. There are two breeds, the\n   rough-haired and smooth-haired. It is remarkable for its intelligence,\n   displayed especially in caring for flocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collied', 'p. & a.', 'Darkened. See Colly, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collier', 'n.', 'One engaged in the business of digging mineral coal or\n   making charcoal, or in transporting or dealing in coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collier', 'n.', 'A vessel employed in the coal trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collieries', 'pl. ', 'of Colliery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliery', 'n.', 'The place where coal is dug; a coal mine, and the\n   buildings, etc., belonging to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliery', 'n.', 'The coal trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliflower', 'n.', 'See Cauliflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligated', 'imp. & p. p.', 'of Colligate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligating', 'p. pr. & vb. n.', 'of Colligate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligate', 'v. t.', 'To tie or bind together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligate', 'v. t.', 'To bring together by colligation; to sum up in a\n   single proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligate', 'a.', 'Bound together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligation', 'n.', 'A binding together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colligation', 'n.', 'That process by which a number of isolated facts are\n   brought under one conception, or summed up in a general proposition, as\n   when Kepler discovered that the various observed positions of the\n   planet Mars were points in an ellipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collimated', 'imp. & p. p.', 'of Collimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collimating', 'p. p. & vb. n.', 'of Collimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collimate', 'v. t.', 'To render parallel to a certain line or direction; to\n   bring into the same line, as the axes of telescopes, etc.; to render\n   parallel, as rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collimation', 'n.', 'The act of collimating; the adjustment of the line of\n   the sights, as the axial line of the telescope of an instrument, into\n   its proper position relative to the other parts of the instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collimator', 'n.', 'A telescope arranged and used to determine errors of\n   collimation, both vertical and horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collimator', 'n.', 'A tube having a convex lens at one end and at the other\n   a small opening or slit which is at the principal focus of the lens,\n   used for producing a beam of parallel rays; also, a lens so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collin', 'n.', 'A very pure form of gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colline', 'n.', 'A small hill or mount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collineation', 'n.', 'The act of aiming at, or directing in a line with, a\n   fixed object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colling', 'v. t.', 'An embrace; dalliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collingly', 'adv.', 'With embraces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collingual', 'a.', 'Having, or pertaining to, the same language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquable', 'a.', 'Liable to melt, grow soft, or become fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquament', 'n.', 'The first rudiments of an embryo in generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquated', 'imp. & p. p.', 'of Colliquate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquating', 'p. pr. & vb. n.', 'of Colliquate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquate', 'v. t. & i.', 'To change from solid to fluid; to make or\n   become liquid; to melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquation', 'n.', 'A melting together; the act of melting; fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquation', 'n.', 'A processive wasting or melting away of the solid\n   parts of the animal system with copious excretions of liquids by one or\n   more passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquative', 'a.', 'Causing rapid waste or exhaustion; melting; as,\n   colliquative sweats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colliquefaction', 'n.', 'A melting together; the reduction of different\n   bodies into one mass by fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collish', 'n.', 'A tool to polish the edge of a sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collision', 'n.', 'The act of striking together; a striking together, as of\n   two hard bodies; a violent meeting, as of railroad trains; a clashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collision', 'n.', 'A state of opposition; antagonism; interference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collisive', 'a.', 'Colliding; clashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collitigant', 'a.', 'Disputing or wrangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collitigant', 'n.', 'One who litigates or wrangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocate', 'a.', 'Set; placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocated', 'imp. & p. p.', 'of Collocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocating', 'p. pr. & vb. n.', 'of Collocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocate', 'v. t.', 'To set or place; to set; to station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocation', 'n.', 'The act of placing; the state of being placed with\n   something else; disposition in place; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocution', 'n.', 'A speaking or conversing together; conference; mutual\n   discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collocutor', 'n.', 'One of the speakers in a dialogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collodion', 'n.', 'A solution of pyroxylin (soluble gun cotton) in ether\n   containing a varying proportion of alcohol. It is strongly adhesive,\n   and is used by surgeons as a coating for wounds; but its chief\n   application is as a vehicle for the sensitive film in photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collodionize', 'v. t.', 'To prepare or treat with collodion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collodiotype', 'n.', 'A picture obtained by the collodion process; a\n   melanotype or ambrotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collodium', 'n.', 'See Collodion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collogue', 'v. i.', 'To talk or confer secretly and confidentially; to\n   converse, especially with evil intentions; to plot mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloid', 'a.', 'Resembling glue or jelly; characterized by a jellylike\n   appearance; gelatinous; as, colloid tumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloid', 'n.', 'A substance (as albumin, gum, gelatin, etc.) which is of a\n   gelatinous rather than a crystalline nature, and which diffuses itself\n   through animal membranes or vegetable parchment more slowly than\n   crystalloids do; -- opposed to crystalloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloid', 'n.', 'A gelatinous substance found in colloid degeneration and\n   colloid cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloidal', 'a.', 'Pertaining to, or of the nature of, colloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloidality', 'n.', 'The state or quality of being colloidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collop', 'n.', 'A small slice of meat; a piece of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collop', 'n.', 'A part or piece of anything; a portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloped', 'a.', 'Having ridges or bunches of flesh, like collops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collophore', 'n.', 'A suckerlike organ at the base of the abdomen of\n   insects belonging to the Collembola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collophore', 'n.', 'An adhesive marginal organ of the Lucernariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquial', 'a.', 'Pertaining to, or used in, conversation, esp. common\n   and familiar conversation; conversational; hence, unstudied; informal;\n   as, colloquial intercourse; colloquial phrases; a colloquial style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquialism', 'n.', 'A colloquial expression, not employed in formal\n   discourse or writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquialize', 'v. t.', 'To make colloquial and familiar; as, to\n   colloquialize ones style of writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquist', 'n.', 'A speaker in a colloquy or dialogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquies', 'pl. ', 'of Colloquy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquy', 'n.', 'Mutual discourse of two or more persons; conference;\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colloquy', 'n.', 'In some American colleges, a part in exhibitions,\n   assigned for a certain scholarship rank; a designation of rank in\n   collegiate scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collow', 'n.', 'Soot; smut. See 1st Colly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colluctancy', 'n.', 'A struggling to resist; a striving against;\n   resistance; opposition of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colluctation', 'n.', 'A struggling; a contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colluded', 'imp. & p. p.', 'of Collude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colluding', 'p. pr. & vb. n.', 'of Collude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collude', 'v. i.', 'To have secretly a joint part or share in an action; to\n   play into each others hands; to conspire; to act in concert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colluder', 'n.', 'One who conspires in a fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colla', 'pl. ', 'of Collum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collum', 'n.', 'A neck or cervix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collum', 'n.', 'Same as Collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collusion', 'n.', 'A secret agreement and cooperation for a fraudulent or\n   deceitful purpose; a playing into each others hands; deceit; fraud;\n   cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collusion', 'n.', 'An agreement between two or more persons to defraud a\n   person of his rights, by the forms of law, or to obtain an object\n   forbidden by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collusive', 'a.', 'Characterized by collusion; done or planned in\n   collusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collusive', 'a.', 'Acting in collusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collusory', 'a.', 'Collusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collutory', 'n.', 'A medicated wash for the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colly', 'n.', 'The black grime or soot of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collied', 'imp. & p. p.', 'of Colly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collying', 'p. pr. & vb. n.', 'of Colly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colly', 'v. t.', 'To render black or dark, as of with coal smut; to\n   begrime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colly', 'n.', 'A kind of dog. See Collie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collybist', 'n.', 'A money changer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collyriums', 'pl. ', 'of Collyrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collyria', 'pl. ', 'of Collyrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Collyrium', 'n.', 'An application to the eye, usually an eyewater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colocolo', 'n.', 'A South American wild cat (Felis colocolo); of the size\n   of the ocelot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colocynth', 'n.', 'The light spongy pulp of the fruit of the bitter\n   cucumber (Citrullus, / Cucumis, colocynthis); an Asiatic plant allied\n   to the watermelon; coloquintida. It comes in white balls, is intensely\n   bitter, and a powerful cathartic. Called also bitter apple, bitter\n   cucumber, bitter gourd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colocynthin', 'n.', 'The active medicinal principle of colocynth; a bitter,\n   yellow, crystalline substance, regarded as a glucoside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cologne', 'n.', 'A perfumed liquid, composed of alcohol and certain\n   aromatic oils, used in the toilet; -- called also cologne water and eau\n   de cologne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cologne earth', '', 'An earth of a deep brown color, containing more\n   vegetable than mineral matter; an earthy variety of lignite, or brown\n   coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colombier', 'n.', 'A large size of paper for drawings. See under Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colombin', 'n.', 'See Calumbin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colombo', 'n.', 'See Calumba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colon', 'n.', 'That part of the large intestines which extends from the\n   caecum to the rectum. [See Illust of Digestion.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colon', 'n.', 'A point or character, formed thus [:], used to separate\n   parts of a sentence that are complete in themselves and nearly\n   independent, often taking the place of a conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonel', 'n.', 'The chief officer of a regiment; an officer ranking next\n   above a lieutenant colonel and next below a brigadier general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonelcy', 'n.', 'The office, rank, or commission of a colonel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonelship', 'n.', 'Colonelcy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloner', 'n.', 'A colonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonial', 'a.', 'Of or pertaining to a colony; as, colonial rights,\n   traffic, wars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonical', 'a.', 'Of or pertaining to husbandmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonist', 'n.', 'A member or inhabitant of a colony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonitis', 'n.', 'See Colitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonization', 'n.', 'The act of colonizing, or the state of being\n   colonized; the formation of a colony or colonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonizationist', 'n.', 'A friend to colonization, esp. (U. S. Hist) to the\n   colonization of Africa by emigrants from the colored population of the\n   United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonized', 'imp. & p. p.', 'of Colonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonizing', 'p. pr. & vb. n.', 'of Colonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonize', 'v. t.', 'To plant or establish a colony or colonies in; to\n   people with colonists; to migrate to and settle in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonize', 'v. i.', 'To remove to, and settle in, a distant country; to\n   make a colony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonizer', 'n.', 'One who promotes or establishes a colony; a colonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonnade', 'n.', 'A series or range of columns placed at regular intervals\n   with all the adjuncts, as entablature, stylobate, roof, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colonies', 'pl. ', 'of Colony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colony', 'n.', 'A company of people transplanted from their mother country\n   to a remote province or country, and remaining subject to the\n   jurisdiction of the parent state; as, the British colonies in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colony', 'n.', 'The district or country colonized; a settlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colony', 'n.', 'A company of persons from the same country sojourning in a\n   foreign city or land; as, the American colony in Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colony', 'n.', 'A number of animals or plants living or growing together,\n   beyond their usual range.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colophany', 'n.', 'See Colophony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colophene', 'n.', 'A colorless, oily liquid, formerly obtained by\n   distillation of colophony. It is regarded as a polymeric form of\n   terebenthene. Called also diterebene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colophon', 'n.', 'An inscription, monogram, or cipher, containing the place\n   and date of publication, printers name, etc., formerly placed on the\n   last page of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colophonite', 'n.', 'A coarsely granular variety of garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colophony', 'n.', 'Rosin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloquintida', 'n.', 'See Colocynth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'A property depending on the relations of light to the eye,\n   by which individual and specific differences in the hues and tints of\n   objects are apprehended in vision; as, gay colors; sad colors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'Any hue distinguished from white or black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'The hue or color characteristic of good health and spirits;\n   ruddy complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'That which is used to give color; a paint; a pigment; as,\n   oil colors or water colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'That which covers or hides the real character of anything;\n   semblance; excuse; disguise; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'Shade or variety of character; kind; species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'A distinguishing badge, as a flag or similar symbol (usually\n   in the plural); as, the colors or color of a ship or regiment; the\n   colors of a race horse (that is, of the cap and jacket worn by the\n   jockey).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'n.', 'An apparent right; as where the defendant in trespass gave\n   to the plaintiff an appearance of title, by stating his title\n   specially, thus removing the cause from the jury to the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colored', 'imp. & p. p.', 'of Color');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloring', 'p. pr. & vb. n.', 'of Color');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'v. t.', 'To change or alter the hue or tint of, by dyeing,\n   staining, painting, etc.; to dye; to tinge; to paint; to stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'v. t.', 'To change or alter, as if by dyeing or painting; to give\n   a false appearance to; usually, to give a specious appearance to; to\n   cause to appear attractive; to make plausible; to palliate or excuse;\n   as, the facts were colored by his prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'v. t.', 'To hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color', 'v. i.', 'To acquire color; to turn red, especially in the face; to\n   blush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorable', 'a.', 'Specious; plausible; having an appearance of right or\n   justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorado beetle', '', 'A yellowish beetle (Doryphora decemlineata); with\n   ten longitudinal, black, dorsal stripes. It has migrated eastwards from\n   its original habitat in Colorado, and is very destructive to the potato\n   plant; -- called also potato beetle and potato bug. See Potato beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorado group', '', 'A subdivision of the cretaceous formation of western\n   North America, especially developed in Colorado and the upper Missouri\n   region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloradoite', 'n.', 'Mercury telluride, an iron-black metallic mineral,\n   found in Colorado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorate', 'a.', 'Colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloration', 'n.', 'The act or art of coloring; the state of being colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorature', 'n.', 'Vocal music colored, as it were, by florid ornaments,\n   runs, or rapid passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color-blind', 'a.', 'Affected with color blindness. See Color blindness,\n   under Color, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colored', 'a.', 'Having color; tinged; dyed; painted; stained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colored', 'a.', 'Specious; plausible; adorned so as to appear well; as, a\n   highly colored description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colored', 'a.', 'Of some other color than black or white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colored', 'a.', 'Of some other color than white; specifically applied to\n   negroes or persons having negro blood; as, a colored man; the colored\n   people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colored', 'a.', 'Of some other color than green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorific', 'a.', 'Capable of communicating color or tint to other bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorimeter', 'n.', 'An instrument for measuring the depth of the color of\n   anything, especially of a liquid, by comparison with a standard liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloring', 'n.', 'The act of applying color to; also, that which produces\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coloring', 'n.', 'Change of appearance as by addition of color; appearance;\n   show; disguise; misrepresentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorist', 'n.', 'One who colors; an artist who excels in the use of\n   colors; one to whom coloring is of prime importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorless', 'a.', 'Without color; not distinguished by any hue;\n   transparent; as, colorless water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorless', 'a.', 'Free from any manifestation of partial or peculiar\n   sentiment or feeling; not disclosing likes, dislikes, prejudice, etc.;\n   as, colorless music; a colorless style; definitions should be\n   colorless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colormen', 'pl. ', 'of Colorman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colorman', 'n.', 'A vender of paints, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Color sergeant', '', 'See under Sergeant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossal', 'a.', 'Of enormous size; gigantic; huge; as, a colossal statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossal', 'a.', 'Of a size larger than heroic. See Heroic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossean', 'a.', 'Colossal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colosseum', 'n.', 'The amphitheater of Vespasian in Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossi', 'pl. ', 'of Colossus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossuses', 'pl. ', 'of Colossus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossus', 'n.', 'A statue of gigantic size. The name was especially\n   applied to certain famous statues in antiquity, as the Colossus of Nero\n   in Rome, the Colossus of Apollo at Rhodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colossus', 'n.', 'Any man or beast of gigantic size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colostrum', 'n.', 'The first milk secreted after delivery; biestings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colostrum', 'n.', 'A mixture of turpentine and the yolk of an egg, formerly\n   used as an emulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colotomy', 'n.', 'An operation for opening the colon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colour', 'n.', 'See Color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colp', 'n.', 'See Collop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colportage', 'n.', 'The distribution of religious books, tracts, etc., by\n   colporteurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colporter', 'n.', 'Same as Colporteur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colporteur', 'n.', 'A hawker; specifically, one who travels about selling\n   and distributing religious tracts and books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colstaff', 'n.', 'A staff by means of which a burden is borne by two\n   persons on their shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colt', 'n.', 'The young of the equine genus or horse kind of animals; --\n   sometimes distinctively applied to the male, filly being the female.\n   Cf. Foal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colt', 'n.', 'A young, foolish fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colt', 'n.', 'A short knotted rope formerly used as an instrument of\n   punishment in the navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colt', 'v. i.', 'To frisk or frolic like a colt; to act licentiously or\n   wantonly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colt', 'v. t.', 'To horse; to get with young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colt', 'v. t.', 'To befool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colter', 'n.', 'A knife or cutter, attached to the beam of a plow to cut\n   the sward, in advance of the plowshare and moldboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coltish', 'a.', 'Like a colt; wanton; frisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coltsfoot', 'n.', 'A perennial herb (Tussilago Farfara); whose leaves and\n   rootstock are sometimes employed in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colts tooth', '', 'See under Colt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coluber', 'n.', 'A genus of harmless serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colubrine', 'a.', 'like or related to snakes of the genus Coluber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colubrine', 'a.', 'Like a snake; cunning; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colugo', 'n.', 'A peculiar East Indian mammal (Galleopithecus volans);\n   having along the sides, connecting the fore and hind limbs, a\n   parachutelike membrane, by means of which it is able to make long\n   leaps, like the flying squirrel; -- called also flying lemur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columba', 'n.', 'See Calumba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbae', 'n. pl.', 'An order of birds, including the pigeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbaria', 'pl. ', 'of Columbarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbarium', 'n.', 'A dovecote or pigeon house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbarium', 'n.', 'A sepulchral chamber with niches for holding cinerary\n   urns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbaries', 'pl. ', 'of Columbary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbary', 'n.', 'A dovecote; a pigeon house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbate', 'n.', 'A salt of columbic acid; a niobate. See Columbium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbatz fly', '', 'See Buffalo fly, under Buffalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbella', 'n.', 'A genus of univalve shells, abundant in tropical seas.\n   Some species, as Columbella mercatoria, were formerly used as shell\n   money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbia', 'n.', 'America; the United States; -- a poetical appellation\n   given in honor of Columbus, the discoverer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbiad', 'n.', 'A form of seacoast cannon; a long, chambered gun\n   designed for throwing shot or shells with heavy charges of powder, at\n   high angles of elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbian', 'a.', 'Of or pertaining to the United States, or to America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbic', 'a.', 'Pertaining to, or containing, columbium or niobium;\n   niobic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbic', 'a.', 'Pertaining to, or derived from, the columbo root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbier', 'n.', 'See Colombier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbiferous', 'a.', 'Producing or containing columbium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbin', 'n.', 'A white, crystalline, bitter substance. See Calumbin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbine', 'a.', 'Of or pertaining to a dove; dovelike; dove-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbine', 'n.', 'A plant of several species of the genus Aquilegia; as,\n   A. vulgaris, or the common garden columbine; A. Canadensis, the wild\n   red columbine of North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbine', 'n.', 'The mistress or sweetheart of Harlequin in pantomimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbite', 'n.', 'A mineral of a black color, submetallic luster, and high\n   specific specific gravity. It is a niobate (or columbate) of iron and\n   manganese, containing tantalate of iron; -- first found in New England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbium', 'n.', 'A rare element of the vanadium group, first found in a\n   variety of the mineral columbite occurring in Connecticut, probably at\n   Haddam. Atomic weight 94.2. Symbol Cb or Nb. Now more commonly called\n   niobium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columbo', 'n.', 'See Calumba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columella', 'n.', 'An axis to which a carpel of a compound pistil may be\n   attached, as in the case of the geranium; or which is left when a pod\n   opens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columella', 'n.', 'A columnlike axis in the capsules of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columella', 'n.', 'A term applied to various columnlike parts; as, the\n   columella, or epipterygoid bone, in the skull of many lizards; the\n   columella of the ear, the bony or cartilaginous rod connecting the\n   tympanic membrane with the internal ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columella', 'n.', 'The upright pillar in the axis of most univalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columella', 'n.', 'The central pillar or axis of the calicles of certain\n   corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columelliform', 'a.', 'Shaped like a little column, or columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'A kind of pillar; a cylindrical or polygonal support for a\n   roof, ceiling, statue, etc., somewhat ornamented, and usually composed\n   of base, shaft, and capital. See Order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'Anything resembling, in form or position, a column in\n   architecture; an upright body or mass; a shaft or obelisk; as, a column\n   of air, of water, of mercury, etc.; the Column Vendome; the spinal\n   column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'A body of troops formed in ranks, one behind the other; --\n   contradistinguished from line. Compare Ploy, and Deploy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'A small army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'A number of ships so arranged as to follow one another in\n   single or double file or in squadrons; -- in distinction from \"line\",\n   where they are side by side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'A perpendicular set of lines, not extending across the\n   page, and separated from other matter by a rule or blank space; as, a\n   column in a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'A perpendicular line of figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Column', 'n.', 'The body formed by the union of the stamens in the Mallow\n   family, or of the stamens and pistil in the orchids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columnar', 'a.', 'Formed in columns; having the form of a column or\n   columns; like the shaft of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columnarity', 'n.', 'The state or quality of being columnar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columnated', 'a.', 'Having columns; as, columnated temples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columned', 'a.', 'Having columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Columniation', 'n.', 'The employment or arrangement of columns in a\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colures', 'pl. ', 'of Colure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colure', 'n.', 'One of two great circles intersecting at right angles in\n   the poles of the equator. One of them passes through the equinoctial\n   points, and hence is denominated the equinoctial colure; the other\n   intersects the equator at the distance of 90¡ from the former, and is\n   called the solstitial colure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colies', 'pl. ', 'of Coly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coly', 'n.', 'Any bird of the genus Colius and allied genera. They inhabit\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Colza', 'n.', 'A variety of cabbage (Brassica oleracea); cultivated for its\n   seeds, which yield an oil valued for illuminating and lubricating\n   purposes; summer rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Com-', '', 'A prefix from the Latin preposition cum, signifying with,\n   together, in conjunction, very, etc. It is used in the form com- before\n   b, m, p, and sometimes f, and by assimilation becomes col- before l,\n   cor- before r, and con- before any consonant except b, h, l, m, p, r,\n   and w. Before a vowel com- becomes co-; also before h, w, and sometimes\n   before other consonants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coma', 'n.', 'A state of profound insensibility from which it is difficult\n   or impossible to rouse a person. See Carus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coma', 'n.', 'The envelope of a comet; a nebulous covering, which surrounds\n   the nucleus or body of a comet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coma', 'n.', 'A tuft or bunch, -- as the assemblage of branches forming the\n   head of a tree; or a cluster of bracts when empty and terminating the\n   inflorescence of a plant; or a tuft of long hairs on certain seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comanches', 'n. pl.', 'A warlike, savage, and nomadic tribe of the Shoshone\n   family of Indians, inhabiting Mexico and the adjacent parts of the\n   United States; -- called also Paducahs. They are noted for plundering\n   and cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comart', 'n.', 'A covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comate', 'a.', 'Encompassed with a coma, or bushy appearance, like hair;\n   hairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-mate', 'n.', 'A companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comatose', 'a.', 'Relating to, or resembling, coma; drowsy; lethargic; as,\n   comatose sleep; comatose fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comatous', 'a.', 'Comatose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comatula', 'n.', 'A crinoid of the genus Antedon and related genera. When\n   young they are fixed by a stem. When adult they become detached and\n   cling to seaweeds, etc., by their dorsal cirri; -- called also feather\n   stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comatulid', 'n.', 'Any crinoid of the genus Antedon or allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'An instrument with teeth, for straightening, cleansing, and\n   adjusting the hair, or for keeping it in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'An instrument for currying hairy animals, or cleansing and\n   smoothing their coats; a currycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'A toothed instrument used for separating and cleansing wool,\n   flax, hair, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The serrated vibratory doffing knife of a carding machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'A former, commonly cone-shaped, used in hat manufacturing for\n   hardening the soft fiber into a bat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'A tool with teeth, used for chasing screws on work in a\n   lathe; a chaser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The notched scale of a wire micrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The collector of an electrical machine, usually resembling a\n   comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The naked fleshy crest or caruncle on the upper part of the\n   bill or hood of a cock or other bird. It is usually red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'One of a pair of peculiar organs on the base of the abdomen\n   of scorpions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The curling crest of a wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The waxen framework forming the walls of the cells in which\n   bees store their honey, eggs, etc.; honeycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'The thumbpiece of the hammer of a gunlock, by which it may be\n   cocked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combed', 'imp. & p. p.', 'of Comb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combing', 'p. pr. & vb. n.', 'of Comb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'v. t.', 'To disentangle, cleanse, or adjust, with a comb; to lay\n   smooth and straight with, or as with, a comb; as, to comb hair or wool.\n   See under Combing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'To roll over, as the top or crest of a wave; to break with a\n   white foam, as waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'Alt. of Combe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combe', 'n.', 'That unwatered portion of a valley which forms its\n   continuation beyond and above the most elevated spring that issues into\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comb', 'n.', 'A dry measure. See Coomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combated', 'imp. & p. p.', 'of Combat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combating', 'p. pr. & vb. n.', 'of Combat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combat', 'v. i.', 'To struggle or contend, as with an opposing force; to\n   fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combat', 'v. t.', 'To fight with; to oppose by force, argument, etc.; to\n   contend against; to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combat', 'n.', 'A fight; a contest of violence; a struggle for supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combat', 'n.', 'An engagement of no great magnitude; or one in which the\n   parties engaged are not armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combatable', 'a.', 'Such as can be, or is liable to be, combated; as,\n   combatable foes, evils, or arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combatant', 'a.', 'Contending; disposed to contend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combatant', 'n.', 'One who engages in combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combater', 'n.', 'One who combats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combative', 'a.', 'Disposed to engage in combat; pugnacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combativeness', 'n.', 'The quality of being combative; propensity to\n   contend or to quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combativeness', 'n.', 'A cranial development supposed to indicate a\n   combative disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combattant', 'a.', 'In the position of fighting; -- said of two lions set\n   face to face, each rampant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combbroach', 'n.', 'A tooth of a wool comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combe', 'n.', 'See Comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comber', 'n.', 'One who combs; one whose occupation it is to comb wool,\n   flax, etc. Also, a machine for combing wool, flax, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comber', 'n.', 'A long, curling wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comber', 'v. t.', 'To cumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comber', 'n.', 'Encumbrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comber', 'n.', 'The cabrilla. Also, a name applied to a species of wrasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combinable', 'a.', 'Capable of combining; consistent with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combinate', 'a.', 'United; joined; betrothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combination', 'n.', 'The act or process of combining or uniting persons and\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combination', 'n.', 'The result of combining or uniting; union of persons\n   or things; esp. a union or alliance of persons or states to effect some\n   purpose; -- usually in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combination', 'n.', 'The act or process of uniting by chemical affinity, by\n   which substances unite with each other in definite proportions by\n   weight to form distinct compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combination', 'n.', 'The different arrangements of a number of objects, as\n   letters, into groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combined', 'imp. & p. p.', 'of Combine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Combining', 'p. pr. & vb. n.', 'of Combine');
