  INSERT INTO words (word, word_type, description) VALUES  (    'Commend', 'v. t.', 'To recommend as worthy of confidence or regard; to\n   present as worthy of notice or favorable attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commend', 'v. t.', 'To mention with approbation; to praise; as, to commend\n   a person or an act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commend', 'v. t.', 'To mention by way of courtesy, implying remembrance and\n   good will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commend', 'n.', 'Commendation; praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commend', 'n.', 'Compliments; greetings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendable', 'a.', 'Worthy of being commended or praised; laudable;\n   praiseworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendam', 'n.', 'A vacant living or benefice commended to a cleric\n   (usually a bishop) who enjoyed the revenue until a pastor was provided.\n   A living so held was said to be held in commendam. The practice was\n   abolished by law in 1836.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendatary', 'n.', 'One who holds a living in commendam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendation', 'n.', 'The act of commending; praise; favorable\n   representation in words; recommendation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendation', 'n.', 'That which is the ground of approbation or praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendation', 'n.', 'A message of affection or respect; compliments;\n   greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendator', 'n.', 'One who holds a benefice in commendam; a commendatary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendatory', 'a.', 'Serving to commend; containing praise or\n   commendation; commending; praising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendatory', 'a.', 'Holding a benefice in commendam; as, a commendatory\n   bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commendatory', 'n.', 'A commendation; eulogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commender', 'n.', 'One who commends or praises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensal', 'n.', 'One who eats at the same table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensal', 'n.', 'An animal, not truly parasitic, which lives in, with, or\n   on, another, partaking usually of the same food. Both species may be\n   benefited by the association.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensal', 'a.', 'Having the character of a commensal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensalism', 'n.', 'The act of eating together; table fellowship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensality', 'n.', 'Fellowship at table; the act or practice of eating at\n   the same table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensation', 'n.', 'Commensality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurability', 'n.', 'The quality of being commensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurable', 'a.', 'Having a common measure; capable of being exactly\n   measured by the same number, quantity, or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurably', 'adv.', 'In a commensurable manner; so as to be\n   commensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurated', 'imp. & p. p.', 'of Commensurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurating', 'p. pr. & vb. n.', 'of Commensurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurate', 'v. t.', 'To reduce to a common measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurate', 'v. t.', 'To proportionate; to adjust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurate', 'a.', 'Having a common measure; commensurable; reducible to\n   a common measure; as, commensurate quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurate', 'a.', 'Equal in measure or extent; proportionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurately', 'adv.', 'In a commensurate manner; so as to be equal or\n   proportionate; adequately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurately', 'adv.', 'With equal measure or extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensurateness', 'n.', 'The state or quality of being commensurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commensuration', 'n.', 'The act of commensurating; the state of being\n   commensurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commented', 'imp. & p. p.', 'of Comment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commenting', 'p. pr. & vb. n.', 'of Comment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comment', 'v. i.', 'To make remarks, observations, or criticism;\n   especially, to write notes on the works of an author, with a view to\n   illustrate his meaning, or to explain particular passages; to write\n   annotations; -- often followed by on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comment', 'v. t.', 'To comment on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comment', 'n.', 'A remark, observation, or criticism; gossip; discourse;\n   talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comment', 'n.', 'A note or observation intended to explain, illustrate, or\n   criticise the meaning of a writing, book, etc.; explanation;\n   annotation; exposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentaries', 'pl. ', 'of Commentary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentary', 'v. i.', 'A series of comments or annotations; esp., a book of\n   explanations or expositions on the whole or a part of the Scriptures or\n   of some other work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentary', 'v. i.', 'A brief account of transactions or events written\n   hastily, as if for a memorandum; -- usually in the plural; as, Caesars\n   Commentaries on the Gallic War.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentate', 'v. t. & i.', 'To write comments or notes upon; to make\n   comments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentation', 'n.', 'The act or process of commenting or criticising;\n   exposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentation', 'n.', 'The result of the labors of a commentator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentator', 'n.', 'One who writes a commentary or comments; an expositor;\n   an annotator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentatorial', 'a.', 'Pertaining to the making of commentaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentatorship', 'n.', 'The office or occupation of a commentator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commenter', 'n.', 'One who makes or writes comments; a commentator; an\n   annotator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commentitious', 'a.', 'Fictitious or imaginary; unreal; as, a commentitious\n   system of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerce', 'n.', 'The exchange or buying and selling of commodities; esp.\n   the exchange of merchandise, on a large scale, between different places\n   or communities; extended trade or traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerce', 'n.', 'Social intercourse; the dealings of one person or class\n   in society with another; familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerce', 'n.', 'Sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerce', 'n.', 'A round game at cards, in which the cards are subject to\n   exchange, barter, or trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerced', 'imp. & p. p.', 'of Commerce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commercing', 'p. pr. & vb. n.', 'of Commerce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerce', 'v. i.', 'To carry on trade; to traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commerce', 'v. i.', 'To hold intercourse; to commune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commercial', 'a.', 'Of or pertaining to commerce; carrying on or occupied\n   with commerce or trade; mercantile; as, commercial advantages;\n   commercial relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commercialism', 'n.', 'The commercial spirit or method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commercially', 'adv.', 'In a commercial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commigrate', 'v. i.', 'To migrate together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commigration', 'n.', 'Migration together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commination', 'n.', 'A threat or threatening; a denunciation of punishment\n   or vengeance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commination', 'n.', 'An office in the liturgy of the Church of England,\n   used on Ash Wednesday, containing a recital of Gods anger and\n   judgments against sinners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminatory', 'a.', 'Threatening or denouncing punishment; as, comminatory\n   terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commingled', 'imp. & p. p.', 'of Commingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commingling', 'p. pr. & vb. n.', 'of Commingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commingle', 'v. t. & i.', 'To mingle together; to mix in one mass, or\n   intimately; to blend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminuted', 'imp. & p. p.', 'of Comminute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminuting', 'p. pr. & vb. n.', 'of Comminute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminute', 'v. t.', 'To reduce to minute particles, or to a fine powder;\n   to pulverize; to triturate; to grind; as, to comminute chalk or bones;\n   to comminute food with the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminution', 'n.', 'The act of reducing to a fine powder or to small\n   particles; pulverization; the state of being comminuted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminution', 'n.', 'Fracture (of a bone) into a number of pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comminution', 'n.', 'Gradual diminution by the removal of small particles\n   at a time; a lessening; a wearing away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiserable', 'a.', 'Pitiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiserated', 'imp. & p. p.', 'of Commiserate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiserating', 'p. pr. & vb. n.', 'of Commiserate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiserate', 'v. t.', 'To feel sorrow, pain, or regret for; to pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiseration', 'n.', 'The act of commiserating; sorrow for the wants,\n   afflictions, or distresses of another; pity; compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiserative', 'a.', 'Feeling or expressing commiseration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commiserator', 'n.', 'One who pities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissarial', 'a.', 'Of or pertaining to a commissary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissariat', 'n.', 'The organized system by which armies and military\n   posts are supplied with food and daily necessaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissariat', 'n.', 'The body of officers charged with such service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissaries', 'pl. ', 'of Commissary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissary', 'n.', 'One to whom is committed some charge, duty, or office,\n   by a superior power; a commissioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissary', 'n.', 'An officer of the bishop, who exercises ecclesiastical\n   jurisdiction in parts of the diocese at a distance from the residence\n   of the bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissary', 'n.', 'An officer having charge of a special service; as, the\n   commissary of musters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissary', 'n.', 'An officer whose business is to provide food for a body\n   of troops or a military post; -- officially called commissary of\n   subsistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissaryship', 'n.', 'The office or employment of a commissary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'The act of committing, doing, or performing; the act of\n   perpetrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'The act of intrusting; a charge; instructions as to how\n   a trust shall be executed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'The duty or employment intrusted to any person or\n   persons; a trust; a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'A formal written warrant or authority, granting certain\n   powers or privileges and authorizing or commanding the performance of\n   certain duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'A certificate conferring military or naval rank and\n   authority; as, a colonels commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'A company of persons joined in the performance of some\n   duty or the execution of some trust; as, the interstate commerce\n   commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'The acting under authority of, or on account of,\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'The thing to be done as agent for another; as, I have\n   three commissions for the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'n.', 'The brokerage or allowance made to a factor or agent\n   for transacting business for another; as, a commission of ten per cent\n   on sales. See Del credere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissioned', 'imp. & p. p.', 'of Commission');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissioning', 'p. pr & vb. n.', 'of Commission');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'v. t.', 'To give a commission to; to furnish with a\n   commission; to empower or authorize; as, to commission persons to\n   perform certain acts; to commission an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commission', 'v. t.', 'To send out with a charge or commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissional', 'a.', 'Alt. of Commissionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissionary', 'a.', 'Of, pertaining to, or conferring, a commission;\n   conferred by a commission or warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissionate', 'v. t.', 'To commission');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissioner', 'n.', 'A person who has a commission or warrant to perform\n   some office, or execute some business, for the government, corporation,\n   or person employing him; as, a commissioner to take affidavits or to\n   adjust claims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissioner', 'n.', 'An officer having charge of some department or bureau\n   of the public service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissionnaire', 'n.', 'An agent or factor; a commission merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissionnaire', 'n.', 'One of a class of attendants, in some European\n   cities, who perform miscellaneous services for travelers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissionship', 'n.', 'The office of commissioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissive', 'a.', 'Relating to commission; of the nature of, or involving,\n   commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissural', 'a.', 'Of or pertaining to a commissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissure', 'n.', 'A joint, seam, or closure; the place where two bodies,\n   or parts of a body, meet and unite; an interstice, cleft, or juncture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissure', 'n.', 'The point of union between two parts, as the angles of\n   the lips or eyelids, the mandibles of a bird, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissure', 'n.', 'A collection of fibers connecting parts of the brain or\n   spinal marrow; a chiasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commissure', 'n.', 'The line of junction or cohering face of two carpels,\n   as in the parsnip, caraway, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committed', 'imp. & p. p.', 'of Commit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committing', 'p. pr. & vb. n.', 'of Commit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. t.', 'To give in trust; to put into charge or keeping; to\n   intrust; to consign; -- used with to, unto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. t.', 'To put in charge of a jailor; to imprison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. t.', 'To do; to perpetrate, as a crime, sin, or fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. t.', 'To join for a contest; to match; -- followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. t.', 'To pledge or bind; to compromise, expose, or endanger by\n   some decisive act or preliminary step; -- often used reflexively; as,\n   to commit ones self to a certain course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. t.', 'To confound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commit', 'v. i.', 'To sin; esp., to be incontinent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commitment', 'n.', 'The act of committing, or putting in charge, keeping,\n   or trust; consignment; esp., the act of committing to prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commitment', 'n.', 'A warrant or order for the imprisonment of a person; --\n   more frequently termed a mittimus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commitment', 'n.', 'The act of referring or intrusting to a committee for\n   consideration and report; as, the commitment of a petition or a bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commitment', 'n.', 'A doing, or perpetration, in a bad sense, as of a crime\n   or blunder; commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commitment', 'n.', 'The act of pledging or engaging; the act of exposing,\n   endangering, or compromising; also, the state of being pledged or\n   engaged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committable', 'a.', 'Capable of being committed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committal', 'n.', 'The act of committing, or the state of being committed;\n   commitment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committee', 'n.', 'One or more persons elected or appointed, to whom any\n   matter or business is referred, either by a legislative body, or by a\n   court, or by any collective body of men acting together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committee', 'v. t.', 'One to whom the charge of the person or estate of\n   another, as of a lunatic, is committed by suitable authority; a\n   guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committeeman', 'n.', 'A member of a committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committer', 'n.', 'One who commits; one who does or perpetrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committer', 'n.', 'A fornicator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Committible', 'a.', 'Capable of being committed; liable to be committed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commixed', 'imp. & p. p.', 'of Commix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commixing', 'p. pr. & vb. n.', 'of Commix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commix', 'v. t. & i.', 'To mix or mingle together; to blend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commixion', 'n.', 'Commixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commixtion', 'n.', 'Commixture; mingling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commixture', 'n.', 'The act or process of mixing; the state of being\n   mingled; the blending of ingredients in one mass or compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commixture', 'n.', 'The mass formed by mingling different things; a\n   compound; a mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodate', 'n.', 'A gratuitous loan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commode', 'n.', 'A kind of headdress formerly worn by ladies, raising the\n   hair and fore part of the cap to a great height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commode', 'n.', 'A piece of furniture, so named according to temporary\n   fashion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commode', 'n.', 'A chest of drawers or a bureau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commode', 'n.', 'A night stand with a compartment for holding a chamber\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commode', 'n.', 'A kind of close stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commode', 'n.', 'A movable sink or stand for a wash bowl, with closet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodious', 'a.', 'Adapted to its use or purpose, or to wants and\n   necessities; serviceable; spacious and convenient; roomy and\n   comfortable; as, a commodious house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodiously', 'adv.', 'In a commodious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodiousness', 'n.', 'State of being commodious; suitableness for its\n   purpose; convenience; roominess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodities', 'pl. ', 'of Commodity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodity', 'n.', 'Convenience; accommodation; profit; benefit; advantage;\n   interest; commodiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodity', 'n.', 'That which affords convenience, advantage, or profit,\n   especially in commerce, including everything movable that is bought and\n   sold (except animals); -- goods, wares, merchandise, produce of land\n   and manufactures, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodity', 'n.', 'A parcel or quantity of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodore', 'n.', 'An officer who ranks next above a captain; sometimes, by\n   courtesy, the senior captain of a squadron. The rank of commodore\n   corresponds with that of brigadier general in the army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodore', 'n.', 'A captain commanding a squadron, or a division of a\n   fleet, or having the temporary rank of rear admiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodore', 'n.', 'A title given by courtesy to the senior captain of a\n   line of merchant vessels, and also to the chief officer of a yachting\n   or rowing club.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commodore', 'n.', 'A familiar for the flagship, or for the principal vessel\n   of a squadron or fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v.', 'Belonging or relating equally, or similarly, to more than\n   one; as, you and I have a common interest in the property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v.', 'Belonging to or shared by, affecting or serving, all the\n   members of a class, considered together; general; public; as,\n   properties common to all plants; the common schools; the Book of Common\n   Prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v.', 'Often met with; usual; frequent; customary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v.', 'Not distinguished or exceptional; inconspicuous; ordinary;\n   plebeian; -- often in a depreciatory sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v.', 'Profane; polluted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v.', 'Given to habits of lewdness; prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'n.', 'The people; the community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'n.', 'An inclosed or uninclosed tract of ground for pleasure, for\n   pasturage, etc., the use of which belongs to the public; or to a number\n   of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'n.', 'The right of taking a profit in the land of another, in\n   common either with the owner or with other persons; -- so called from\n   the community of interest which arises between the claimant of the\n   right and the owner of the soil, or between the claimants and other\n   commoners entitled to the same right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v. i.', 'To converse together; to discourse; to confer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v. i.', 'To participate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v. i.', 'To have a joint right with others in common ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common', 'v. i.', 'To board together; to eat at a table in common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonable', 'a.', 'Held in common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonable', 'a.', 'Allowed to pasture on public commons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonage', 'n.', 'The right of pasturing on a common; the right of using\n   anything in common with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonalties', 'pl. ', 'of Commonalty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonalty', 'n.', 'The common people; those classes and conditions of\n   people who are below the rank of nobility; the commons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonalty', 'n.', 'The majority or bulk of mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoner', 'n.', 'One of the common people; one having no rank of nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoner', 'n.', 'A member of the House of Commons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoner', 'n.', 'One who has a joint right in common ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoner', 'n.', 'One sharing with another in anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoner', 'n.', 'A student in the university of Oxford, Eng., who is not\n   dependent on any foundation for support, but pays all university\n   charges; - - at Cambridge called a pensioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoner', 'n.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonish', 'a.', 'Somewhat common; commonplace; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonition', 'n.', 'Advice; warning; instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonitive', 'a.', 'Monitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonitory', 'a.', 'Calling to mind; giving admonition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonly', 'adv.', 'Usually; generally; ordinarily; frequently; for the\n   most part; as, confirmed habits commonly continue through life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonly', 'adv.', 'In common; familiarly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonness', 'n.', 'State or quality of being common or usual; as, the\n   commonness of sunlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonness', 'n.', 'Triteness; meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonplace', 'a.', 'Common; ordinary; trite; as, a commonplace person, or\n   observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonplace', 'n.', 'An idea or expression wanting originality or interest;\n   a trite or customary remark; a platitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonplace', 'n.', 'A memorandum; something to be frequently consulted or\n   referred to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonplace', 'v. t.', 'To enter in a commonplace book, or to reduce to\n   general heads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonplace', 'v. i.', 'To utter commonplaces; to indulge in platitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonplaceness', 'n.', 'The quality of being commonplace; commonness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commons', 'n. pl.', 'The mass of the people, as distinguished from the\n   titled classes or nobility; the commonalty; the common people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commons', 'n. pl.', 'The House of Commons, or lower house of the British\n   Parliament, consisting of representatives elected by the qualified\n   voters of counties, boroughs, and universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commons', 'n. pl.', 'Provisions; food; fare, -- as that provided at a\n   common table in colleges and universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commons', 'n. pl.', 'A club or association for boarding at a common table,\n   as in a college, the members sharing the expenses equally; as, to board\n   in commons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commons', 'n. pl.', 'A common; public pasture ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Common sense', '', 'See Common sense, under Sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonty', 'n.', 'A common; a piece of land in which two or more persons\n   have a common right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonweal', 'n.', 'Commonwealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonwealth', 'n.', 'A state; a body politic consisting of a certain\n   number of men, united, by compact or tacit agreement, under one form of\n   government and system of laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonwealth', 'n.', 'The whole body of people in a state; the public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commonwealth', 'n.', 'Specifically, the form of government established on\n   the death of Charles I., in 1649, which existed under Oliver Cromwell\n   and his son Richard, ending with the abdication of the latter in 1659.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorance', 'n.', 'See Commorancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorancy', 'n.', 'A dwelling or ordinary residence in a place;\n   habitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorancy', 'n.', 'Residence temporarily, or for a short time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorant', 'n.', 'Ordinarily residing; inhabiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorant', 'n.', 'Inhabiting or occupying temporarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorant', 'n.', 'A resident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoration', 'n.', 'The act of staying or residing in a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorient', 'a.', 'Dying together or at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commorse', 'n.', 'Remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commote', 'v. t.', 'To commove; to disturb; to stir up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commotion', 'n.', 'Disturbed or violent motion; agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commotion', 'n.', 'A popular tumult; public disturbance; riot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commotion', 'n.', 'Agitation, perturbation, or disorder, of mind; heat;\n   excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoved', 'imp. & p. p.', 'of Commove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commoving', 'p. pr. & vb. n.', 'of Commove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commove', 'v. t.', 'To urge; to persuade; to incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commove', 'v. t.', 'To put in motion; to disturb; to unsettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communal', 'a.', 'Pertaining to a commune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communalism', 'n.', 'A French theory of government which holds that commune\n   should be a kind of independent state, and the national government a\n   confederation of such states, having only limited powers. It is\n   advocated by advanced French republicans; but it should not be\n   confounded with communism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communalist', 'n.', 'An advocate of communalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communalistic', 'a.', 'Pertaining to communalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communed', 'imp. & p. p.', 'of Commune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communing', 'p. pr. & vb. n.', 'of Commune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commune', 'v. i.', 'To converse together with sympathy and confidence; to\n   interchange sentiments or feelings; to take counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commune', 'v. i.', 'To receive the communion; to partake of the eucharist\n   or Lords supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commune', 'n.', 'Communion; sympathetic intercourse or conversation between\n   friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commune', 'n.', 'The commonalty; the common people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commune', 'n.', 'A small territorial district in France under the\n   government of a mayor and municipal council; also, the inhabitants, or\n   the government, of such a district. See Arrondissement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commune', 'n.', 'Absolute municipal self-government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicability', 'n.', 'The quality of being communicable; capability of\n   being imparted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicable', 'a.', 'Capable of being communicated, or imparted; as, a\n   communicable disease; communicable knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicable', 'a.', 'Communicative; free-speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicant', 'n.', 'One who partakes of, or is entitled to partake of, the\n   sacrament of the Lords supper; a church member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicant', 'n.', 'One who communicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicant', 'a.', 'Communicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicated', 'imp. & p. p.', 'of Communicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicating', 'p. pr. & vb. n.', 'of Communicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To share in common; to participate in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To impart; to bestow; to convey; as, to communicate\n   a disease or a sensation; to communicate motion by means of a crank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To make known; to recount; to give; to impart; as,\n   to communicate information to any one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To administer the communion to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To share or participate; to possess or enjoy in\n   common; to have sympathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To give alms, sympathy, or aid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To have intercourse or to be the means of\n   intercourse; as, to communicate with another on business; to be\n   connected; as, a communicating artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicate', 'v. i.', 'To partake of the Lords supper; to commune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'The act or fact of communicating; as, communication\n   of smallpox; communication of a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'Intercourse by words, letters, or messages;\n   interchange of thoughts or opinions, by conference or other means;\n   conference; correspondence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'Association; company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'Means of communicating; means of passing from place\n   to place; a connecting passage; connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'That which is communicated or imparted;\n   intelligence; news; a verbal or written message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'Participation in the Lords supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communication', 'n.', 'A trope, by which a speaker assumes that his hearer\n   is a partner in his sentiments, and says we, instead of I or you.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicative', 'a.', 'Inclined to communicate; ready to impart to others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicativeness', 'n.', 'The quality of being communicative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicator', 'n.', 'One who communicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communicatory', 'a.', 'Imparting knowledge or information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communion', 'n.', 'The act of sharing; community; participation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communion', 'n.', 'Intercourse between two or more persons; esp., intimate\n   association and intercourse implying sympathy and confidence;\n   interchange of thoughts, purposes, etc.; agreement; fellowship; as, the\n   communion of saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communion', 'n.', 'A body of Christians having one common faith and\n   discipline; as, the Presbyterian communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communion', 'n.', 'The sacrament of the eucharist; the celebration of the\n   Lords supper; the act of partaking of the sacrament; as, to go to\n   communion; to partake of the communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communism', 'n.', 'A scheme of equalizing the social conditions of life;\n   specifically, a scheme which contemplates the abolition of inequalities\n   in the possession of property, as by distributing all wealth equally to\n   all, or by holding all wealth in common for the equal use and advantage\n   of all.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communist', 'n.', 'An advocate for the theory or practice of communism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communist', 'n.', 'A supporter of the commune of Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communistic', 'a.', 'Of or pertaining to communism or communists; as,\n   communistic theories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communistic', 'a.', 'Living or having their nests in common, as certain\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Communities', 'pl. ', 'of Community');
  INSERT INTO words (word, word_type, description) VALUES  (    'Community', 'n.', 'Common possession or enjoyment; participation; as, a\n   community of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Community', 'n.', 'A body of people having common rights, privileges, or\n   interests, or living in the same place under the same laws and\n   regulations; as, a community of monks. Hence a number of animals living\n   in a common home or with some apparent association of interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Community', 'n.', 'Society at large; a commonwealth or state; a body\n   politic; the public, or people in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Community', 'n.', 'Common character; likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Community', 'n.', 'Commonness; frequency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutability', 'n.', 'The quality of being commutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutable', 'a.', 'Capable of being commuted or interchanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutableness', 'n.', 'The quality of being commutable;\n   interchangeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutation', 'n.', 'A passing from one state to another; change;\n   alteration; mutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutation', 'n.', 'The act of giving one thing for another; barter;\n   exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutation', 'n.', 'The change of a penalty or punishment by the pardoning\n   power of the State; as, the commutation of a sentence of death to\n   banishment or imprisonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutation', 'n.', 'A substitution, as of a less thing for a greater, esp.\n   a substitution of one form of payment for another, or one payment for\n   many, or a specific sum of money for conditional payments or\n   allowances; as, commutation of tithes; commutation of fares;\n   commutation of copyright; commutation of rations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutative', 'a.', 'Relative to exchange; interchangeable; reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutator', 'n.', 'A piece of apparatus used for reversing the direction\n   of an electrical current; an attachment to certain electrical machines,\n   by means of which alternating currents are made to be continuous or to\n   have the same direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commuted', 'imp. & p. p.', 'of Commute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commuting', 'p. pr. & vb. n.', 'of Commute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commute', 'v. t.', 'To exchange; to put or substitute something else in\n   place of, as a smaller penalty, obligation, or payment, for a greater,\n   or a single thing for an aggregate; hence, to lessen; to diminish; as,\n   to commute a sentence of death to one of imprisonment for life; to\n   commute tithes; to commute charges for fares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commute', 'v. i.', 'To obtain or bargain for exemption or substitution; to\n   effect a commutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commute', 'v. i.', 'To pay, or arrange to pay, in gross instead of part by\n   part; as, to commute for a years travel over a route.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commuter', 'n.', 'One who commutes; especially, one who commutes in\n   traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Commutual', 'a.', 'Mutual; reciprocal; united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comose', 'a.', 'Bearing a tuft of soft hairs or down, as the seeds of\n   milkweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'p. p. & a', 'Joined or held together; leagued; confederated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'p. p. & a', 'Composed or made; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'p. p. & a', 'Closely or firmly united, as the particles of solid\n   bodies; firm; close; solid; dense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'p. p. & a', 'Brief; close; pithy; not diffuse; not verbose; as,\n   a compact discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compacted', 'imp. & p. p.', 'of Compact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compacting', 'p. pr. & vb. n.', 'of Compact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'v. t.', 'To thrust, drive, or press closely together; to join\n   firmly; to consolidate; to make close; -- as the parts which compose a\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'v. t.', 'To unite or connect firmly, as in a system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compact', 'n.', 'An agreement between parties; a covenant or contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compacted', 'a.', 'Compact; pressed close; concentrated; firmly united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compactedly', 'adv.', 'In a compact manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compactedness', 'n.', 'A state of being compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compacter', 'n.', 'One who makes a compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compactible', 'a.', 'That may be compacted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compaction', 'n.', 'The act of making compact, or the state of being\n   compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compactly', 'adv.', 'In a compact manner; with close union of parts;\n   densely; tersely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compactness', 'n.', 'The state or quality of being compact; close union of\n   parts; density.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compacture', 'n.', 'Close union or connection of parts; manner of joining;\n   construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compages', 'v. t.', 'A system or structure of many parts united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compaginate', 'v. t.', 'To unite or hold together; as, the side pieces\n   compaginate the frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compagination', 'n.', 'Union of parts; structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companable', 'a.', 'Companionable; sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companator', 'n.', 'Same as Impanator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companiable', 'a.', 'Companionable; sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'n.', 'One who accompanies or is in company with another for a\n   longer or shorter period, either from choice or casually; one who is\n   much in the company of, or is associated with, another or others; an\n   associate; a comrade; a consort; a partner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'n.', 'A knight of the lowest rank in certain orders; as, a\n   companion of the Bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'n.', 'A fellow; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'n.', 'A skylight on an upper deck with frames and sashes of\n   various shapes, to admit light to a cabin or lower deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'n.', 'A wooden hood or penthouse covering the companion way; a\n   companion hatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'v. t.', 'To be a companion to; to attend on; to accompany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companion', 'v. t.', 'To qualify as a companion; to make equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companionable', 'a.', 'Fitted to be a companion; fit for good fellowship;\n   agreeable; sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companionless', 'a.', 'Without a companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companionship', 'n.', 'Fellowship; association; the act or fact of keeping\n   company with any one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companies', 'pl. ', 'of Company');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'The state of being a companion or companions; the act of\n   accompanying; fellowship; companionship; society; friendly intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'A companion or companions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'An assemblage or association of persons, either permanent\n   or transient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'Guests or visitors, in distinction from the members of a\n   family; as, to invite company to dine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'Society, in general; people assembled for social\n   intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'An association of persons for the purpose of carrying on\n   some enterprise or business; a corporation; a firm; as, the East India\n   Company; an insurance company; a joint-stock company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'Partners in a firm whose names are not mentioned in its\n   style or title; -- often abbreviated in writing; as, Hottinguer & Co.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'A subdivision of a regiment of troops under the command of\n   a captain, numbering in the United States (full strength) 100 men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'The crew of a ship, including the officers; as, a whole\n   ships company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'n.', 'The body of actors employed in a theater or in the\n   production of a play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companied', 'imp. & p. p.', 'of Company');
  INSERT INTO words (word, word_type, description) VALUES  (    'Companying', 'p. pr. & vb. n.', 'of Company');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'v. t.', 'To accompany or go with; to be companion to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'v. i.', 'To associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'v. i.', 'To be a gay companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Company', 'v. i.', 'To have sexual commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparable', 'a.', 'Capable of being compared; worthy of comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparate', 'n.', 'One of two things compared together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparation', 'n.', 'A making ready; provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'a.', 'Of or pertaining to comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'a.', 'Proceeding from, or by the method of, comparison; as,\n   the comparative sciences; the comparative anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'a.', 'Estimated by comparison; relative; not positive or\n   absolute, as compared with another thing or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'a.', 'Expressing a degree greater or less than the positive\n   degree of the quality denoted by an adjective or adverb. The\n   comparative degree is formed from the positive by the use of -er, more,\n   or less; as, brighter, more bright, or less bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'n.', 'The comparative degree of adjectives and adverbs;\n   also, the form by which the comparative degree is expressed; as,\n   stronger, wiser, weaker, more stormy, less windy, are all comparatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'n.', 'An equal; a rival; a compeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparative', 'n.', 'One who makes comparisons; one who affects wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparatively', 'adv.', 'According to estimate made by comparison;\n   relatively; not positively or absolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparator', 'n.', 'An instrument or machine for comparing anything to be\n   measured with a standard measure; -- applied especially to a machine\n   for comparing standards of length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compared', 'imp. & p. p.', 'of Compare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparing', 'p. pr. & vb. n.', 'of Compare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'v. t.', 'To examine the character or qualities of, as of two or\n   more persons or things, for the purpose of discovering their\n   resemblances or differences; to bring into comparison; to regard with\n   discriminating attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'v. t.', 'To represent as similar, for the purpose of\n   illustration; to liken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'v. t.', 'To inflect according to the degrees of comparison; to\n   state positive, comparative, and superlative forms of; as, most\n   adjectives of one syllable are compared by affixing \"- er\" and \"-est\"\n   to the positive form; as, black, blacker, blackest; those of more than\n   one syllable are usually compared by prefixing \"more\" and \"most\", or\n   \"less\" and \"least\", to the positive; as, beautiful, more beautiful,\n   most beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'v. i.', 'To be like or equal; to admit, or be worthy of,\n   comparison; as, his later work does not compare with his earlier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'v. i.', 'To vie; to assume a likeness or equality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'n.', 'Comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'n.', 'Illustration by comparison; simile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compare', 'v. t.', 'To get; to procure; to obtain; to acquire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparer', 'n.', 'One who compares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'n.', 'The act of comparing; an examination of two or more\n   objects with the view of discovering the resemblances or differences;\n   relative estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'n.', 'The state of being compared; a relative estimate; also,\n   a state, quality, or relation, admitting of being compared; as, to\n   bring a thing into comparison with another; there is no comparison\n   between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'n.', 'That to which, or with which, a thing is compared, as\n   being equal or like; illustration; similitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'n.', 'The modification, by inflection or otherwise, which the\n   adjective and adverb undergo to denote degrees of quality or quantity;\n   as, little, less, least, are examples of comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'n.', 'A figure by which one person or thing is compared to\n   another, or the two are considered with regard to some property or\n   quality, which is common to them both; e.g., the lake sparkled like a\n   jewel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'n.', 'The faculty of the reflective group which is supposed\n   to perceive resemblances and contrasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparison', 'v. t.', 'To compare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparted', 'imp. & p. p.', 'of Compart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comparting', 'p. pr. & vb. n.', 'of Compart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compart', 'v. t.', 'To divide; to mark out into parts or subdivisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compartition', 'n.', 'The act of dividing into parts or compartments;\n   division; also, a division or compartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compartment', 'n.', 'One of the parts into which an inclosed portion of\n   space is divided, as by partitions, or lines; as, the compartments of a\n   cabinet, a house, or a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compartment', 'n.', 'One of the sections into which the hold of a ship is\n   divided by water-tight bulkheads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compartner', 'n.', 'See Copartner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'A passing round; circuit; circuitous course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'An inclosing limit; boundary; circumference; as, within\n   the compass of an encircling wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'An inclosed space; an area; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'Extent; reach; sweep; capacity; sphere; as, the compass of\n   his eye; the compass of imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'Moderate bounds, limits of truth; moderation; due limits;\n   -- used with within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'The range of notes, or tones, within the capacity of a\n   voice or instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'An instrument for determining directions upon the earths\n   surface by means of a magnetized bar or needle turning freely upon a\n   pivot and pointing in a northerly and southerly direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'A pair of compasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'n.', 'A circle; a continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassed', 'imp. & p. p.', 'of Compass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassing', 'p. pr. & vb. n.', 'of Compass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'v. t.', 'To go about or entirely round; to make the circuit of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'v. t.', 'To inclose on all sides; to surround; to encircle; to\n   environ; to invest; to besiege; -- used with about, round, around, and\n   round about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'v. t.', 'To reach round; to circumvent; to get within ones\n   power; to obtain; to accomplish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'v. t.', 'To curve; to bend into a circular form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compass', 'v. t.', 'To purpose; to intend; to imagine; to plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassable', 'a.', 'Capable of being compassed or accomplished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassed', 'a.', 'Rounded; arched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compasses', 'n.', 'An instrument for describing circles, measuring figures,\n   etc., consisting of two, or (rarely) more, pointed branches, or legs,\n   usually joined at the top by a rivet on which they move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassing', 'a.', 'Curved; bent; as, compassing timbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassion', 'n.', 'Literally, suffering with another; a sensation of\n   sorrow excited by the distress or misfortunes of another; pity;\n   commiseration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassion', 'v. t.', 'To pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionable', 'a.', 'Deserving compassion or pity; pitiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionate', 'a.', 'Having a temper or disposition to pity; sympathetic;\n   merciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionate', 'a.', 'Complaining; inviting pity; pitiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionated', 'imp. & p. p.', 'of Compassionate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionating', 'p. pr. & vb. n.', 'of Compassionate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionate', 'v. t.', 'To have compassion for; to pity; to commiserate;\n   to sympathize with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionately', 'adv.', 'In a compassionate manner; mercifully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassionateness', 'n.', 'The quality or state of being compassionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compassless', 'a.', 'Having no compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compaternity', 'n.', 'The relation of a godfather to a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatibility', 'n.', 'The quality or power of being compatible or\n   congruous; congruity; as, a compatibility of tempers; a compatibility\n   of properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatible', 'a.', 'Capable of existing in harmony; congruous; suitable;\n   not repugnant; -- usually followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatibleness', 'n.', 'Compatibility; consistency; fitness; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatibly', 'adv.', 'In a compatible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatient', 'a.', 'Suffering or enduring together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatriot', 'n.', 'One of the same country, and having like interests and\n   feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatriot', 'a.', 'Of the same country; having a common sentiment of\n   patriotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compatriotism', 'n.', 'The condition of being compatriots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compear', 'v. i.', 'To appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compear', 'v. i.', 'To appear in court personally or by attorney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compeer', '', 'An equal, as in rank, age, prowess, etc.; a companion; a\n   comrade; a mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compeer', 'v. t.', 'To be equal with; to match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compeer', 'v. i.', 'Alt. of Compeir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compeir', 'v. i.', 'See Compear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compelled', 'imp. & p. p.', 'of Compel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compelling', 'p. pr. & vb. n', 'of Compel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compel', 'v. t.', 'To drive or urge with force, or irresistibly; to force;\n   to constrain; to oblige; to necessitate, either by physical or moral\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compel', 'v. t.', 'To take by force or violence; to seize; to exact; to\n   extort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compel', 'v. t.', 'To force to yield; to overpower; to subjugate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compel', 'v. t.', 'To gather or unite in a crowd or company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compel', 'v. t.', 'To call forth; to summon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compel', 'v. i.', 'To make one yield or submit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compellable', 'a.', 'Capable of being compelled or constrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compellably', 'adv.', 'By compulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compellation', 'n.', 'Style of address or salutation; an appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compellative', 'n.', 'The name by which a person is addressed; an\n   appellative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compellatory', 'a.', 'Serving to compel; compulsory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compeller', 'n.', 'One who compels or constrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compend', 'n.', 'A compendium; an epitome; a summary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendiarious', 'a.', 'Short; compendious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendiate', 'v. t.', 'To sum or collect together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendious', 'a.', 'Containing the substance or general principles of a\n   subject or work in a narrow compass; abridged; summarized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendiously', 'adv.', 'In a compendious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendiousness', 'n.', 'The state or quality of being compendious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendiums', 'pl. ', 'of Compendium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendia', 'pl. ', 'of Compendium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compendium', 'n.', 'A brief compilation or composition, containing the\n   principal heads, or general principles, of a larger work or system; an\n   abridgment; an epitome; a compend; a condensed summary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensated', 'imp. & p. p.', 'of Compensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensating', 'p. pr. & vb. n.', 'of Compensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensate', 'v. t.', 'To make equal return to; to remunerate; to\n   recompense; to give an equivalent to; to requite suitably; as, to\n   compensate a laborer for his work, or a merchant for his losses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensate', 'v. t.', 'To be equivalent in value or effect to; to\n   counterbalance; to make up for; to make amends for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensate', 'v. i.', 'To make amends; to supply an equivalent; -- followed\n   by for; as, nothing can compensate for the loss of reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensation', 'n.', 'The act or principle of compensating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensation', 'n.', 'That which constitutes, or is regarded as, an\n   equivalent; that which makes good the lack or variation of something\n   else; that which compensates for loss or privation; amends;\n   remuneration; recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensation', 'n.', 'The extinction of debts of which two persons are\n   reciprocally debtors by the credits of which they are reciprocally\n   creditors; the payment of a debt by a credit of equal amount; a\n   set-off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensation', 'n.', 'A recompense or reward for some loss or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensation', 'n.', 'An equivalent stipulated for in contracts for the\n   sale of real estate, in which it is customary to provide that errors in\n   description, etc., shall not avoid, but shall be the subject of\n   compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensative', 'a.', 'Affording compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensative', 'n.', 'Compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensator', 'n.', 'One who, or that which, compensates; -- a name applied\n   to various mechanical devices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensator', 'n.', 'An iron plate or magnet placed near the compass on\n   iron vessels to neutralize the effect of the ships attraction on the\n   needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compensatory', 'a.', 'Serving for compensation; making amends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compense', 'v. t.', 'To compensate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comperendinate', 'v. t.', 'To delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compesce', 'v. t.', 'To hold in check; to restrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competed', 'imp. & p. p.', 'of Compete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competing', 'p. pr. & vb. n.', 'of Compete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compete', 'v. i.', 'To contend emulously; to seek or strive for the same\n   thing, position, or reward for which another is striving; to contend in\n   rivalry, as for a prize or in business; as, tradesmen compete with one\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competence', 'n.', 'Alt. of Competency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competency', 'n.', 'The state of being competent; fitness; ability;\n   adequacy; power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competency', 'n.', 'Property or means sufficient for the necessaries and\n   conveniences of life; sufficiency without excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competency', 'n.', 'Legal capacity or qualifications; fitness; as, the\n   competency of a witness or of a evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competency', 'n.', 'Right or authority; legal power or capacity to take\n   cognizance of a cause; as, the competence of a judge or court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competent', 'a.', 'Answering to all requirements; adequate; sufficient;\n   suitable; capable; legally qualified; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competent', 'a.', 'Rightfully or properly belonging; incident; -- followed\n   by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competently', 'adv.', 'In a competent manner; adequately; suitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competible', 'a.', 'Compatible; suitable; consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competition', 'n.', 'The act of seeking, or endeavoring to gain, what\n   another is endeavoring to gain at the same time; common strife for the\n   same objects; strife for superiority; emulous contest; rivalry, as for\n   approbation, for a prize, or as where two or more persons are engaged\n   in the same business and each seeking patronage; -- followed by for\n   before the object sought, and with before the person or thing competed\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competitive', 'a.', 'Of or pertaining to competition; producing\n   competition; competitory; as, a competitive examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competitor', 'n.', 'One who seeks what another seeks, or claims what\n   another claims; one who competes; a rival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competitor', 'n.', 'An associate; a confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competitory', 'a.', 'Acting in competition; competing; rival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competitress', 'n.', 'A woman who competes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Competitrix', 'n.', 'A competitress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compilation', 'n.', 'The act or process of compiling or gathering together\n   from various sources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compilation', 'n.', 'That which is compiled; especially, a book or document\n   composed of materials gathering from other books or documents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compilator', 'n.', 'Compiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compiled', 'imp. & p. p.', 'of Compile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compiling', 'p. pr. & vb. n.', 'of Compile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compile', 'v. t.', 'To put together; to construct; to build.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compile', 'v. t.', 'To contain or comprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compile', 'v. t.', 'To put together in a new form out of materials already\n   existing; esp., to put together or compose out of materials from other\n   books or documents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compile', 'v. t.', 'To write; to compose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compilement', 'n.', 'Compilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compiler', 'n.', 'One who compiles; esp., one who makes books by\n   compilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compinge', 'v. t.', 'To compress; to shut up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacence', 'n.', 'Alt. of Complacency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacency', 'n.', 'Calm contentment; satisfaction; gratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacency', 'n.', 'The cause of pleasure or joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacency', 'n.', 'The manifestation of contentment or satisfaction; good\n   nature; kindness; civility; affability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacent', 'a.', 'Self-satisfied; contented; kindly; as, a complacent\n   temper; a complacent smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacential', 'a.', 'Marked by, or causing, complacence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complacently', 'adv.', 'In a complacent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complained', 'imp. & p. p.', 'of Complain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complaining', 'p. pr. & vb. n.', 'of Complain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complain', 'v. i.', 'To give utterance to expression of grief, pain,\n   censure, regret. etc.; to lament; to murmur; to find fault; -- commonly\n   used with of. Also, to creak or squeak, as a timber or wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complain', 'v. i.', 'To make a formal accusation; to make a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complain', 'v. t.', 'To lament; to bewail.');
