  INSERT INTO words (word, word_type, description) VALUES  (    'Complaint', 'n.', 'A formal allegation or charge against a party made or\n   presented to the appropriate court or officer, as for a wrong done or a\n   crime committed (in the latter case, generally under oath); an\n   information; accusation; the initial bill in proceedings in equity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complaintful', 'a.', 'Full of complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complaisance', 'n.', 'Disposition to please or oblige; obliging compliance\n   with the wishes of others; a deportment indicative of a desire to\n   please; courtesy; civility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complaisant', 'a.', 'Desirous to please; courteous; obliging; compliant;\n   as, a complaisant gentleman.');
