  INSERT INTO words (word, word_type, description) VALUES  (    'Complement', 'v. t.', 'To compliment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complemental', 'a.', 'Supplying, or tending to supply, a deficiency; fully\n   completing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complemental', 'a.', 'Complimentary; courteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complementary', 'a.', 'Serving to fill out or to complete; as,\n   complementary numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complementary', 'n.', 'One skilled in compliments.');
