  INSERT INTO words (word, word_type, description) VALUES  (    'Complete', 'a.', 'Finished; ended; concluded; completed; as, the edifice is\n   complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complete', 'a.', 'Having all the parts or organs which belong to it or to\n   the typical form; having calyx, corolla, stamens, and pistil.');
