  INSERT INTO words (word, word_type, description) VALUES  (    'Completely', 'adv.', 'In a complete manner; fully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Completement', 'n.', 'Act of completing or perfecting; completion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Completeness', 'n.', 'The state of being complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Completion', 'n.', 'The act or process of making complete; the getting\n   through to the end; as, the completion of an undertaking, an education,\n   a service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Completion', 'n.', 'State of being complete; fulfillment; accomplishment;\n   realization.');
