  INSERT INTO words (word, word_type, description) VALUES  (    'Completory', 'n.', 'Same as Compline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complex', 'n.', 'Composed of two or more parts; composite; not simple; as,\n   a complex being; a complex idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complex', 'n.', 'Involving many parts; complicated; intricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complex', 'n.', 'Assemblage of related things; collection; complication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexed', 'a.', 'Complex, complicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexedness', 'n.', 'The quality or state of being complex or involved;\n   complication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexion', 'n.', 'The state of being complex; complexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexion', 'n.', 'A combination; a complex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexion', 'n.', 'The bodily constitution; the temperament; habitude, or\n   natural disposition; character; nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexion', 'n.', 'The color or hue of the skin, esp. of the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexion', 'n.', 'The general appearance or aspect; as, the complexion of\n   the sky; the complexion of the news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexional', 'a.', 'Of or pertaining to constitutional complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexionally', 'adv.', 'Constitutionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexionary', 'a.', 'Pertaining to the complexion, or to the care of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexioned', 'a.', 'Having (such) a complexion; -- used in composition;\n   as, a dark-complexioned or a ruddy-complexioned person.');
