  INSERT INTO words (word, word_type, description) VALUES  (    'Complexness', 'n.', 'The state of being complex; complexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complexus', 'n.', 'A complex; an aggregate of parts; a complication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compliable', 'a.', 'Capable of bending or yielding; apt to yield;\n   compliant.');
