  INSERT INTO words (word, word_type, description) VALUES  (    'Complier', 'n.', 'One who complies, yields, or obeys; one of an easy,\n   yielding temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compliment', 'n.', 'An expression, by word or act, of approbation, regard,\n   confidence, civility, or admiration; a flattering speech or attention;\n   a ceremonious greeting; as, to send ones compliments to a friend.');
