  INSERT INTO words (word, word_type, description) VALUES  (    'Complimenter', 'n.', 'One who compliments; one given to complimenting; a\n   flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compline', 'n.', 'Alt. of Complin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complin', 'n.', 'The last division of the Roman Catholic breviary; the\n   seventh and last of the canonical hours of the Western church; the last\n   prayer of the day, to be said after sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complot', 'n.', 'A plotting together; a confederacy in some evil design; a\n   conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complotted', 'imp. & p. p.', 'of Complot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Complotting', 'p. pr. & vb. n.', 'of Complot');
