  INSERT INTO words (word, word_type, description) VALUES  (    'Complying', 'p. pr. & vb. n.', 'of Comply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comply', 'v. i.', 'To yield assent; to accord; agree, or acquiesce; to\n   adapt ones self; to consent or conform; -- usually followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comply', 'v. i.', 'To be ceremoniously courteous; to make ones\n   compliments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comply', 'v. i.', 'To fulfill; to accomplish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comply', 'v. i.', 'To infold; to embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compone', 'v. t.', 'To compose; to settle; to arrange.');
