  INSERT INTO words (word, word_type, description) VALUES  (    'Compone', 'a.', 'Divided into squares of alternate tinctures in a single\n   row; -- said of any bearing; or, in the case of a bearing having curved\n   lines, divided into patches of alternate colors following the curve. If\n   there are two rows it is called counter-compony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comported', 'imp. & p. p.', 'of Comport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comporting', 'p. pr. & vb. n.', 'of Comport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comport', 'v. i.', 'To bear or endure; to put up (with); as, to comport\n   with an injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comport', 'v. i.', 'To agree; to accord; to suit; -- sometimes followed by\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comport', 'v. t.', 'To bear; to endure; to brook; to put with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comport', 'v. t.', 'To carry; to conduct; -- with a reflexive pronoun.');
