  INSERT INTO words (word, word_type, description) VALUES  (    'Comportable', 'a.', 'Suitable; consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comportance', 'n.', 'Behavior; comport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comportation', 'n.', 'A bringing together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Comportment', 'n.', 'Manner of acting; behavior; bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composed', 'imp. & p. p.', 'of Compose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composing', 'p. pr. & vb. n.', 'of Compose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. t.', 'To form by putting together two or more things or\n   parts; to put together; to make up; to fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. t.', 'To form the substance of, or part of the substance of;\n   to constitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. t.', 'To construct by mental labor; to design and execute, or\n   put together, in a manner involving the adaptation of forms of\n   expression to ideas, or to the laws of harmony or proportion; as, to\n   compose a sentence, a sermon, a symphony, or a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. t.', 'To dispose in proper form; to reduce to order; to put\n   in proper state or condition; to adjust; to regulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. t.', 'To free from agitation or disturbance; to tranquilize;\n   to soothe; to calm; to quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. t.', 'To arrange (types) in a composing stick in order for\n   printing; to set (type).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compose', 'v. i.', 'To come to terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composed', 'a.', 'Free from agitation; calm; sedate; quiet; tranquil;\n   self-possessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composer', 'n.', 'One who composes; an author. Specifically, an author of a\n   piece of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composer', 'n.', 'One who, or that which, quiets or calms; one who adjusts\n   a difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composing', 'a.', 'Tending to compose or soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composing', 'a.', 'Pertaining to, or used in, composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compositae', 'n. pl.', 'A large family of dicotyledonous plants, having\n   their flowers arranged in dense heads of many small florets and their\n   anthers united in a tube. The daisy, dandelion, and asters, are\n   examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composite', 'v. t.', 'Made up of distinct parts or elements; compounded;\n   as, a composite language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composite', 'v. t.', 'Belonging to a certain order which is composed of the\n   Ionic order grafted upon the Corinthian. It is called also the Roman or\n   the Italic order, and is one of the five orders recognized by the\n   Italian writers of the sixteenth century. See Capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composite', 'v. t.', 'Belonging to the order Compositae; bearing\n   involucrate heads of many small florets, as the daisy, thistle, and\n   dandelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composite', 'n.', 'That which is made up of parts or compounded of several\n   elements; composition; combination; compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The act or art of composing, or forming a whole or\n   integral, by placing together and uniting different things, parts, or\n   ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The invention or combination of the parts of any\n   literary work or discourse, or of a work of art; as, the composition of\n   a poem or a piece of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The art or practice of so combining the different\n   parts of a work of art as to produce a harmonious whole; also, a work\n   of art considered as such. See 4, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The act of writing for practice in a language, as\n   English, Latin, German, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The setting up of type and arranging it for printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The state of being put together or composed;\n   conjunction; combination; adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'A mass or body formed by combining two or more\n   substances; as, a chemical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'A literary, musical, or artistic production,\n   especially one showing study and care in arrangement; -- often used of\n   an elementary essay or translation done as an educational exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'Consistency; accord; congruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'Mutual agreement to terms or conditions for the\n   settlement of a difference or controversy; also, the terms or\n   conditions of settlement; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'The adjustment of a debt, or avoidance of an\n   obligation, by some form of compensation agreed on between the parties;\n   also, the sum or amount of compensation agreed upon in the adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composition', 'n.', 'Synthesis as opposed to analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compositive', 'a.', 'Having the quality of entering into composition;\n   compounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compositor', 'n.', 'One who composes or sets in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compositor', 'n.', 'One who sets type and arranges it for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compositous', 'a.', 'Belonging to the Compositae; composite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compossible', 'a.', 'Able to exist with another thing; consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compost', 'n.', 'A mixture; a compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compost', 'n.', 'A mixture for fertilizing land; esp., a composition of\n   various substances (as muck, mold, lime, and stable manure) thoroughly\n   mingled and decomposed, as in a compost heap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compost', 'v. t.', 'To manure with compost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compost', 'v. t.', 'To mingle, as different fertilizing substances, in a\n   mass where they will decompose and form into a compost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composture', 'n.', 'Manure; compost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composure', 'n.', 'The act of composing, or that which is composed; a\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composure', 'n.', 'Orderly adjustment; disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composure', 'n.', 'Frame; make; temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composure', 'n.', 'A settled state; calmness; sedateness; tranquillity;\n   repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Composure', 'n.', 'A combination; a union; a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compotation', 'n.', 'The act of drinking or tippling together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compotator', 'n.', 'One who drinks with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Compote', 'n.', 'A preparation of fruit in sirup in such a manner as to\n   preserve its form, either whole, halved, or quartered; as, a compote of\n   pears.');
