  INSERT INTO words (word, word_type, description) VALUES  (    'Conceit', 'n.', 'That which is conceived, imagined, or formed in the mind;\n   idea; thought; image; conception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceit', 'n.', 'Faculty of conceiving ideas; mental faculty; apprehension;\n   as, a man of quick conceit.');
