  INSERT INTO words (word, word_type, description) VALUES  (    'Conceit', 'n.', 'A fanciful, odd, or extravagant notion; a quant fancy; an\n   unnatural or affected conception; a witty thought or turn of\n   expression; a fanciful device; a whim; a quip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceit', 'n.', 'An overweening idea of ones self; vanity.');
