  INSERT INTO words (word, word_type, description) VALUES  (    'Conceit', 'v. i.', 'To form an idea; to think.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceited', 'a.', 'Endowed with fancy or imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceited', 'a.', 'Entertaining a flattering opinion of ones self; vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceited', 'a.', 'Curiously contrived or designed; fanciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceitedly', 'adv.', 'In an egotistical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceitedly', 'adv.', 'Fancifully; whimsically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceitedness', 'n.', 'The state of being conceited; conceit; vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceitless', 'a.', 'Without wit; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceivable', 'a.', 'Capable of being conceived, imagined, or understood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceived', 'imp. & p. p.', 'of Conceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceiving', 'p. pr. & vb. n.', 'of Conceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceive', 'v. t.', 'To receive into the womb and begin to breed; to begin\n   the formation of the embryo of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceive', 'v. t.', 'To form in the mind; to plan; to devise; to generate;\n   to originate; as, to conceive a purpose, plan, hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceive', 'v. t.', 'To apprehend by reason or imagination; to take into\n   the mind; to know; to imagine; to comprehend; to understand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceive', 'v. i.', 'To have an embryo or fetus formed in the womb; to\n   breed; to become pregnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceive', 'v. i.', 'To have a conception, idea, or opinion; think; -- with\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceiver', 'n.', 'One who conceives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concelebrate', 'v. t.', 'To celebrate together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concent', 'n.', 'Concert of voices; concord of sounds; harmony; as, a\n   concent of notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concent', 'n.', 'Consistency; accordance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentered', 'imp. & p. p.', 'of Concentre');
