  INSERT INTO words (word, word_type, description) VALUES  (    'Concentre', 'v. i.', 'To come to one point; to meet in, or converge toward,\n   a common center; to have a common center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concenter', 'v. t.', 'Alt. of Concentre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentre', 'v. t.', 'To draw or direct to a common center; to bring\n   together at a focus or point, as two or more lines; to concentrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrated', 'imp. & p. p.', 'of Concentrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrating', 'p. pr. & vb. n.', 'of Concentrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrate', 'v. t.', 'To bring to, or direct toward, a common center; to\n   unite more closely; to gather into one body, mass, or force; to fix;\n   as, to concentrate rays of light into a focus; to concentrate the\n   attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrate', 'v. t.', 'To increase the strength and diminish the bulk of,\n   as of a liquid or an ore; to intensify, by getting rid of useless\n   material; to condense; as, to concentrate acid by evaporation; to\n   concentrate by washing; -- opposed to dilute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrate', 'v. i.', 'To approach or meet in a common center; to\n   consolidate; as, population tends to concentrate in cities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentration', 'n.', 'The act or process of concentrating; the process of\n   becoming concentrated, or the state of being concentrated;\n   concentration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentration', 'n.', 'The act or process of reducing the volume of a\n   liquid, as by evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentration', 'n.', 'The act or process of removing the dress of ore and\n   of reducing the valuable part to smaller compass, as by currents of air\n   or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrative', 'a.', 'Serving or tending to concentrate; characterized by\n   concentration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrativeness', 'n.', 'The quality of concentrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrativeness', 'n.', 'The faculty or propensity which has to do with\n   concentrating the intellectual the intellectual powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrator', 'n.', 'An apparatus for the separation of dry comminuted\n   ore, by exposing it to intermittent puffs of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentric', 'a.', 'Alt. of Concentrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrical', 'a.', 'Having a common center, as circles of different size,\n   one within another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentric', 'n.', 'That which has a common center with something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentrically', 'adv.', 'In a concentric manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentricity', 'n.', 'The state of being concentric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concentual', 'a.', 'Possessing harmony; accordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concept', 'n.', 'An abstract general conception; a notion; a universal.');
