  INSERT INTO words (word, word_type, description) VALUES  (    'Conception', 'n.', 'The power or faculty of apprehending of forming an idea\n   in the mind; the power of recalling a past sensation or perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conception', 'n.', 'The formation in the mind of an image, idea, or notion,\n   apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conception', 'n.', 'The image, idea, or notion of any action or thing which\n   is formed in the mind; a concept; a notion; a universal; the product of\n   a rational belief or judgment. See Concept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conception', 'n.', 'Idea; purpose; design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conception', 'n.', 'Conceit; affected sentiment or thought.');
