  INSERT INTO words (word, word_type, description) VALUES  (    'Conceptive', 'a.', 'Capable of conceiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conceptual', 'a.', 'Pertaining to conception.');
