  INSERT INTO words (word, word_type, description) VALUES  (    'Conceptualist', 'n.', 'One who maintains the theory of conceptualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concerned', 'imp. & p. p.', 'of Concern');
